(window.webpackJsonp=window.webpackJsonp||[]).push([[286],{641:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1234-truoc-mat-moi-nguoi-hanh-hinh"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1234-truoc-mat-moi-nguoi-hanh-hinh"}},[n._v("#")]),n._v(" Chương 1234: Trước mặt mọi người hành hình")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1234: Trước mặt mọi người hành hình"),g("br"),g("br"),n._v("“Bẩm báo Hà đường chủ, tại hạ Thanh Nguyên thành Danh Sư đường phó đường chủ, Ngô Như Phong!”"),g("br"),g("br"),n._v("Ngô sư vội vàng tiến lên."),g("br"),g("br"),n._v("“Ta biết ngươi...” Hà đường chủ nhẹ gật đầu."),g("br"),g("br"),n._v("Thanh Nguyên đế quốc, mặc dù tại liên minh cấp dưới tám cái đế quốc bên trong, được cho rất yếu, nhưng cũng thuộc về độc lập đơn vị, đối một đường người phụ trách, vẫn là biết một chút."),g("br"),g("br"),n._v("“Là như vậy, Điền Thanh phó đường chủ, Cẩu đường chủ cùng rất nhiều danh sư bị giết sự tình, đã tra ra manh mối, cố ý hướng ngươi bẩm báo!”"),g("br"),g("br"),n._v("Ngô sư nói."),g("br"),g("br"),n._v("“A, đã tra ra được? Nói nghe một chút!”"),g("br"),g("br"),n._v("Hà đường chủ thị lực ngưng trọng."),g("br"),g("br"),n._v("Thuộc hạ một cái phân bộ, đường chủ đều bị giết, sự tình coi là là rất lớn, hắn một mực chú ý, chỉ bất quá phái ra Tống sư, đến nay không có tin tức."),g("br"),g("br"),n._v("Bình thường đưa tin ngọc bài, chỉ có thể đưa tin trăm vạn cây số bên trong, càng xa lời nói, chỉ có thể mượn nhờ loại này cỡ lớn đưa tin đại trận."),g("br"),g("br"),n._v("Tống sư đi Thanh Nguyên thành về sau, một mực không mở ra trận pháp, do đó, tình huống cụ thể, hắn cũng không rõ lắm."),g("br"),g("br"),n._v("“Là như vậy... Điền Thanh phó đường chủ, là Dị Linh tộc nhân Thanh Điền nhất mạch Hoàng giả, một đạo ý niệm trùng sinh mà thành...”"),g("br"),g("br"),n._v("Ngô sư đem lời chuẩn bị xong ngữ nói ra."),g("br"),g("br"),n._v("Cơ bản cùng sự thật không chênh lệch quá lớn, nhưng mà tóm tắt liên quan tới Ngoan Nhân sự tình."),g("br"),g("br"),n._v("“Một cái Dị Linh tộc Hoàng giả, tiềm phục tại đại lục hơn hai nghìn năm, tại Danh Sư đường bố trí ra hơn trăm cái nội gian, đều không có bị phát hiện?”"),g("br"),g("br"),n._v("Hà đường chủ hiển nhiên có chút không dám tin tưởng."),g("br"),g("br"),n._v("Chuyện này một khi truyền đi, tuyệt đối so với giết mười vị Cẩu đường chủ, đều muốn oanh động."),g("br"),g("br"),n._v("Đây là Danh Sư đường không làm tròn bổn phận!"),g("br"),g("br"),n._v("“Là... May mắn chúng ta tại Trương sư dẫn đầu xuống, thành công đem hắn giết chết!” Ngô sư cổ tay khẽ đảo, hai cái thi thể rơi trên mặt đất: “Đây là thi thể của hắn!”"),g("br"),g("br"),n._v("Hà đường chủ vội vàng nhìn lại, không lâu sau, sắc mặt nghiêm túc: “Quả thực là dị linh Nhân Hoàng tộc thi thể, nếu không không có khả năng sử dụng loại này phân liệt bí pháp...”"),g("br"),g("br"),n._v("Thân vì liên minh đế quốc Danh Sư đường đường chủ, đối Dị Linh tộc nhân biết đến càng thêm kỹ càng, biết loại này chỉ có Hoàng tộc huyết thống, mới có thể thi triển phân liệt bí pháp."),g("br"),g("br"),n._v("Có thể thi triển đi ra, đã xác định thân phận của đối phương."),g("br"),g("br"),n._v("“Không chỉ có như thế, Trương sư, còn đem toàn bộ Thanh Điền nhất mạch mười đại vương giả, cũng toàn bộ chém giết...” Ngô sư nói tiếp."),g("br"),g("br"),n._v("“Chém giết Hoàng giả, Vương giả... Một thân một mình, tiêu diệt Dị Linh tộc nhân một cái chi mạch... Cứ việc nhưng mà huyết thống đẳng cấp thấp nhất Hoàng tộc, cũng mười phần đáng sợ!”"),g("br"),g("br"),n._v("Hà đường chủ nhẹ gật đầu, càng nói càng cảm thấy kinh ngạc, nhịn không được nói: “Khó trách có thể xông đường thành công, phần này thiên tư cùng hiểu biết, quả thực lợi hại, đã không kém gì một chút Thánh tử! Không biết... Vị này Trương sư hiện tại ở đâu?”"),g("br"),g("br"),n._v("“Thanh Nguyên Hoàng đế Sở Thiên Hành hiệu trung Thanh Điền Hoàng, bắt được học sinh của hắn, bây giờ gấp đuổi về cứu người...” Ngô sư giải thích."),g("br"),g("br"),n._v("“Đáng tiếc...”"),g("br"),g("br"),n._v("Không thể nhìn thấy đối phương, Hà đường chủ có chút than thở, nói tiếp: “Lẻ loi một mình, giết chết Dị Linh tộc Thanh Điền nhất mạch một vị Hoàng giả, mười vị Vương giả, đồng thời phá hủy bọn họ xâm nhập nhân tộc kế hoạch, để vô số danh sư miễn ở tai nạn... Công lao bao lớn, không gì sánh được! Ta hiện tại liền trực tiếp ban cho hắn, liên minh đế quốc Danh Sư đường, đặc biệt mời trưởng lão chức vị...”"),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Ngô sư đám người nắm đấm xiết chặt."),g("br"),g("br"),n._v("Liên minh đế quốc đặc biệt mời trưởng lão, nghe đơn giản, trên thực tế nhưng đại biểu địa vị cực cao cùng tôn nghiêm."),g("br"),g("br"),n._v("Dưới tình huống bình thường, chỉ có chân chính bát tinh danh sư, mới có tư cách chịu trách nhiệm."),g("br"),g("br"),n._v("Trương sư thế mà bị đặc biệt mời, đơn cái này một cái thân phận, đừng nói mình, nhưng mà đại diện đường chủ, liền xem như Tiềm Xung đế quốc Danh Sư đường đường chủ tới, cũng phải tự xưng vãn bối, không dám có chút đi quá giới hạn."),g("br"),g("br"),n._v("“Đặc biệt mời giấy chứng nhận, ta sẽ cho người mau chóng chuẩn bị, chờ các ngươi trở lại Thanh Nguyên thành, kích hoạt đưa tin tường, nên có thể tiếp thu được!”"),g("br"),g("br"),n._v("Hà đường chủ cười cười."),g("br"),g("br"),n._v("“Đa tạ Hà đường chủ!”"),g("br"),g("br"),n._v("Ngô sư vội vàng quỳ gối."),g("br"),g("br"),n._v("“Không cần khách khí, có thể chém giết Dị Linh tộc nhân, phá hư bọn họ hành động, loại này đại công lao, đặc biệt mời trưởng lão mà thôi, không tính là gì! Ngô Như Phong, lần này ngươi hiệp trợ Trương sư, hoàn thành chuyện này, công lao cũng bất khả hạn lượng... Từ hôm nay trở đi, chính thức trở thành Thanh Nguyên đế quốc Danh Sư đường đường chủ, cũng lại đạt được một lần đến liên minh đế quốc bồi dưỡng cơ hội!”"),g("br"),g("br"),n._v("Hà đường chủ tiếp tục nói."),g("br"),g("br"),n._v("“Là...”"),g("br"),g("br"),n._v("Hơi đỏ mặt, Ngô sư kích động toàn thân run rẩy."),g("br"),g("br"),n._v("Trở thành đường chủ, đối với hắn mà nói không tính là gì, trọng yếu nhất chính là một lần bồi dưỡng cơ hội."),g("br"),g("br"),n._v("Loại này bồi dưỡng, sẽ có bát tinh danh sư tự mình giảng bài, đối tu vi của hắn, hiểu biết, đều có tăng lên rất nhiều, là vô số người chỉ có thể ngộ mà không thể cầu."),g("br"),g("br"),n._v("Cẩu đường chủ, sở dĩ nghĩ đến an ổn về hưu, chính là hi vọng, không có chuyện cố về hưu về sau, thu hoạch được như vậy một cơ hội."),g("br"),g("br"),n._v("Đáng tiếc, nghiêm trọng không làm tròn bổn phận, đầu này, liền đã định trước không có cơ hội."),g("br"),g("br"),n._v("“Chư vị chiến sư phối hợp Trương sư, đi sâu địa phương nguy hiểm, vì chém giết Thanh Điền Hoàng, làm ra cống hiến, ta đồng dạng sẽ đem chuyện này, báo lên tới liên minh đế quốc chiến sư đường phân bộ, cho cùng các ngươi ban thưởng!”"),g("br"),g("br"),n._v("Giao phó xong Ngô sư, Hà đường chủ nhìn về phía hình đường chủ đám người."),g("br"),g("br"),n._v("“Đa tạ...”"),g("br"),g("br"),n._v("Hình đường chủ vội vàng khom người."),g("br"),g("br"),n._v("“Được rồi, mau chóng đem thi thể phái người đưa tới, Dị Linh tộc Hoàng giả thi thể, cẩn thận nghiên cứu lời nói, đối với chúng ta chiến thắng Dị Linh tộc nhân, có trợ giúp rất lớn!”"),g("br"),g("br"),n._v("Hà đường chủ khoát tay áo."),g("br"),g("br"),n._v("Dị Linh tộc Hoàng giả, tuỳ tiện không rời đi vực ngoại chiến trường, hơn nữa nguyên một đám thủ đoạn bảo mệnh rất nhiều, muốn muốn chém giết độ khó khăn, có thể tưởng tượng được, liền xem như tổng bộ, đều không có bao nhiêu Hoàng giả thi thể, có thể nghiên cứu."),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Nhẹ gật đầu, Ngô sư an bài một vị trưởng lão, chuyên tiễn thi thể, lúc này mới cung kính cùng Hà đường chủ cáo từ."),g("br"),g("br"),n._v("“Đã thông bẩm xong, chúng ta cũng tận mau trở lại Thanh Nguyên thành đi!”"),g("br"),g("br"),n._v("Đem nên nói sự tình, bẩm báo phía trên Danh Sư đường, Ngô sư nói một tiếng, mọi người theo Tề Bắc thành mượn một đầu Thánh thú, nhanh chóng hướng Thanh Nguyên thành phương hướng bay vút đi."),g("br"),n._v("..."),g("br"),g("br"),n._v("Liên tục phi hành tròn sáu ngày, Trương Huyền rốt cục nhìn thấy Thanh Nguyên thành rất nhiều kiến trúc xuất hiện tại ánh mắt."),g("br"),g("br"),n._v("Trên đường trở về, liền thu vào Tôn Cường đưa tin."),g("br"),g("br"),n._v("Tổng cộng hai cái."),g("br"),g("br"),n._v("Thứ nhất, bọn họ đi hoàng thành cứu Lưu Dương, người sau được cứu ra về sau, dưới cơn nóng giận, đem Sở Thiên Hành giết!"),g("br"),g("br"),n._v("Thứ hai, Lưu Dương rời nhà trốn đi, biến mất không thấy gì nữa."),g("br"),g("br"),n._v("“Dựa theo thời gian suy tính, bọn họ giết Sở Thiên Hành thời gian tại đêm khuya, Ngoan Nhân ý niệm đúng lúc bị ma diệt... Sở Thiên Hành, đem linh hồn hiến tế cho Ngoan Nhân, Ngoan Nhân chết rồi, hắn tự nhiên cũng không sống nổi...”"),g("br"),g("br"),n._v("Nhận được đưa tin, đã biết chuyện gì xảy ra."),g("br"),g("br"),n._v("Thanh Nguyên đế quốc vị hoàng đế bệ hạ này, đem linh hồn hiến tế cho Ngoan Nhân, cam tâm tình nguyện trở thành đối phương khôi lỗi, người sau một khi chết, tự nhiên cũng không sống nổi!"),g("br"),g("br"),n._v("Do đó, rất có thể, không phải là bị Lưu Dương giết, mà là... Linh hồn phai mờ, đưa đến thân thể chết."),g("br"),g("br"),n._v("Thật là lời như vậy, đến không có gì, nhiều nhất nhưng mà lỗ mãng cứu người mà thôi, nhờ vào hắn xông đường lực ảnh hưởng, hẳn là sẽ không ra cái đại sự gì."),g("br"),g("br"),n._v("“Nhưng mà... Vì cái gì Tôn Cường không báo ta tin tức?”"),g("br"),g("br"),n._v("Híp mắt lại, có chút nóng nảy."),g("br"),g("br"),n._v("Mặc dù căn cứ đối phương nói những này suy tính, không tính là gì đại sự, nhưng tiếp thu được tin tức về sau, hắn vẫn đưa tin, liên tục bảy, tám đầu, Tôn Cường đều không có hồi phục... Đến cùng xảy ra chuyện gì?"),g("br"),g("br"),n._v("Còn có, Lưu Dương đi nơi nào, nhiều ngày như vậy, tìm được chưa?"),g("br"),g("br"),n._v("Tràn đầy nghi hoặc, vừa đến Thanh Nguyên trên thành không, lập tức khống chế phi hành Thánh thú, thẳng tắp hướng chiến sư đường nơi ở bay đi."),g("br"),g("br"),n._v("“Viện trưởng, ngươi trở về...”"),g("br"),g("br"),n._v("Vừa dứt xuống, chỉ thấy Nhược Hoan công tử tiến lên đón."),g("br"),g("br"),n._v("“Làm sao vậy?”"),g("br"),g("br"),n._v("Thấy hắn mặt mũi lo lắng, cảm xúc có chút kích động, Trương Huyền nhíu nhíu mày."),g("br"),g("br"),n._v("“Xảy ra chuyện lớn...”"),g("br"),g("br"),n._v("Nhược Hoan công tử vội nói: “Lưu Dương giết chết Sở Thiên Hành bệ hạ, toàn bộ Thanh Nguyên đế quốc đều oanh động... Một vị họ Tống danh sư, làm ra hành hình, trưa hôm nay, tại ngoài hoàng cung quảng trường, chém giết trước mặt mọi người tham dự làm loạn Tôn Cường quản gia cùng rất nhiều độc sư...”"),g("br"),g("br"),n._v("“Chém giết Tôn Cường cùng rất nhiều độc sư?”"),g("br"),g("br"),n._v("Con ngươi co rụt lại, Trương Huyền vẻ mặt trở nên xanh xám."),g("br"),g("br"),n._v("Ánh nắng bên trong huyền, đã lập tức đến buổi trưa!"),g("br"),g("br"),n._v("Nói cách khác, bất cứ lúc nào cũng sẽ đem Tôn Cường đám người chém đầu răn chúng!"),g("br"),g("br"),n._v("“Đúng, Huyền Huyền hội người, toàn bộ đều đi qua, đang định cướp pháp trường... Liền ta ở lại chỗ này, chờ ngươi trở về...”"),g("br"),g("br"),n._v("Nhược Hoan công tử vội nói."),g("br"),g("br"),n._v("“Cướp pháp trường?” Trương Huyền lông mày nhảy một cái."),g("br"),g("br"),n._v("Đám người kia lá gan có thể thật là lớn."),g("br"),g("br"),n._v("Bọn họ mặc dù cùng cấp bậc sức chiến đấu kinh người, có thể thực lực quá thấp, liền cái Bán Thánh đều không có, làm sao kiếp?"),g("br"),g("br"),n._v("Chỉ sợ vừa qua khỏi đi, liền bị đối phương binh sĩ bắt lấy, cũng không còn cách nào trốn."),g("br"),g("br"),n._v("“Đi quảng trường!”"),g("br"),g("br"),n._v("Biết thời gian không đợi người, một khi bọn này không biết trời cao đất rộng gia hỏa, thật làm ầm ĩ ra, sự tình lại khó kết thúc, Trương Huyền bàn tay lớn vồ một cái, đem Nhược Hoan công tử nắm lên, thẳng tắp hướng hoàng cung phương hướng bay vút đi."),g("br"),g("br"),n._v("Một bên phi hành, một bên hỏi thăm."),g("br"),g("br"),n._v("“Các ngươi nói cái kia họ Tống danh sư là ai? Vì sao phải chém giết Tôn Cường?”"),g("br"),g("br"),n._v("Thanh Nguyên đế quốc Danh Sư đường danh sư, hầu như người người đều là học sinh của hắn, nắm giữ nửa sư tình nghĩa, không đi thăm dò Hoàng đế chân chính nguyên nhân cái chết, chém giết Tôn Cường muốn làm gì?"),g("br"),g("br"),n._v("“Nghe nói vị này Tống sư, là Tiềm Xung đế quốc tới chính xác bát tinh danh sư, địa vị cực cao, vừa đến liền tiếp thu Thanh Nguyên thành Danh Sư đường, tất cả mọi người nghe theo mệnh lệnh của hắn...”"),g("br"),g("br"),n._v("Nhược Hoan công tử giải thích."),g("br"),g("br"),n._v("“Tiềm Xung đế quốc người?” Trương Huyền nhíu nhíu mày."),g("br"),g("br"),n._v("“Đúng, Sở Thiên Hành sau khi chết, vô số đại thần, vương công quý tộc, thậm chí binh sĩ, vòng vây tại Danh Sư đường bên ngoài gây rối, đã liên tục mấy ngày, khẳng định cũng là không chịu nổi áp lực, mới làm ra loại này phán quyết!”"),g("br"),g("br"),n._v("Nhược Hoan công tử nói tiếp."),g("br"),g("br"),n._v("Trương Huyền cau mày."),g("br"),g("br"),n._v("Cũng đúng, đường đường một nước Hoàng đế, bị người giết, nếu như không cho cái rõ ràng bàn giao, toàn bộ đế quốc chẳng phải người người cảm thấy bất an?"),g("br"),g("br"),n._v("“Lưu Dương đâu?”"),g("br"),g("br"),n._v("Nhịn không được hỏi."),g("br"),g("br"),n._v("“Lưu Dương, kể từ sau ngày đó, liền mất tích, Danh Sư đường đồn công an có danh sư, đều không tìm được... Chúng ta cũng không biết đi nơi nào! Vị này Tống sư, sở dĩ muốn chém giết trước mặt mọi người Tôn quản gia đám người, mục đích rất đơn giản, cũng là muốn mượn dư luận, bức bách hắn hiện thân, đem vị này hung phạm bắt lấy, cho Thanh Nguyên đế quốc một câu trả lời!”"),g("br"),g("br"),n._v("Nhược Hoan công tử tiếp tục nói."),g("br"),g("br"),n._v("“Ừm!”"),g("br"),g("br"),n._v("Trương Huyền nhẹ gật đầu."),g("br"),g("br"),n._v("Đường đường Hoàng đế bệ hạ, bị người giết chết, nhiều như vậy hộ vệ đều bị người dùng độc độc choáng..."),g("br"),g("br"),n._v("Không hảo hảo xử lý, khẳng định sẽ náo ra càng lớn mâu thuẫn, dẫn xuất càng lớn gợn sóng."),g("br"),g("br"),n._v("“Bất quá, bất kể thế nào xử lý, muốn giết Tôn Cường bọn họ, trừ phi... Giẫm lên thi thể của ta đi qua!”"),g("br"),g("br"),n._v("Lông mày giương lên, Trương Huyền híp mắt lại."),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1242"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1244"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);