(window.webpackJsonp=window.webpackJsonp||[]).push([[288],{643:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1236-noi-hoc-mau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1236-noi-hoc-mau"}},[n._v("#")]),n._v(" Chương 1236: Nói hộc máu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1236: Nói hộc máu"),t("br"),t("br"),n._v("Bóng người thon dài, lông mi như kiếm, không có kinh thiên động địa khí tức, cũng không có rung động lòng người tu vi, lại cho người ta làm người ta ngạt thở giống như áp bức, hình như phản bác một câu, liền sẽ nghênh đón tai hoạ ngập đầu."),t("br"),t("br"),n._v("Dung mạo thoạt nhìn bình thường, trong hai con ngươi, lại mang theo nhật nguyệt tinh thần giống như ánh sáng, tới đối kháng, liền như là miệt thị thiên địa, miệt thị vạn vật muôn dân."),t("br"),t("br"),n._v("“Là Trương sư...”"),t("br"),t("br"),n._v("“Viện trưởng tới...”"),t("br"),t("br"),n._v("“Bọn họ được cứu...”"),t("br"),t("br"),n._v("Tống Siêu đám người, kích động nắm đấm xiết chặt, từng cái hốc mắt thấu đỏ."),t("br"),t("br"),n._v("Mấy ngày nay bọn họ tiếp nhận áp lực thực sự quá lớn, lớn đến đầy đủ sụp đổ."),t("br"),t("br"),n._v("May mắn viện trưởng tới..."),t("br"),t("br"),n._v("Hắn tại, như là trong nháy mắt có chủ kiến, có phương hướng."),t("br"),t("br"),n._v("“Ngươi chính là Trương Huyền?”"),t("br"),t("br"),n._v("Lông mày nhăn lại, Tống sư trên mặt không vui, hừ một tiếng: “Có thể tới tốt nhất, miễn đi không ít phiền phức.”"),t("br"),t("br"),n._v("“Thân là danh sư, mặc kệ đệ tử giỏi, mặc kệ hành hung làm điều ác, tội danh bao lớn, không cần ta nói, ngươi cũng hẳn là rõ ràng. Niệm tình ngươi là một thiên tài, không tại Thanh Nguyên thành, không biết việc này, theo nhẹ xử lý, chỉ cần đem những người này, tự mình xử quyết, vì việc nước quên tình nhà, ta sẽ hướng tổng bộ tự mình xin, cho ngươi mở ra một con đường.”"),t("br"),t("br"),n._v("Lúc đầu trước mặt mọi người xử quyết đám người mục đích, là đưa tới Lưu Dương, không nghĩ tới đem vị này Trương Huyền đưa tới, đã như vậy, vậy liền không còn gì tốt hơn."),t("br"),t("br"),n._v("Nuôi không dạy, lỗi của cha, giáo không nghiêm, sư chi biếng nhác!"),t("br"),t("br"),n._v("Đệ tử hành hung giết người, hơn nữa chém giết chính là một vị phong hào đế quốc Hoàng đế bệ hạ, chỉ bằng vào cái này một cái tội danh, thất tinh danh sư tư cách, liền có khả năng bị tước đoạt, chớ nói chi là đi tới Thanh Nguyên thành gây ra động tĩnh lớn như vậy!"),t("br"),t("br"),n._v("Bất quá, đối phương cũng là thiên tài hiếm thấy, chỉ cần kịp thời tỉnh ngộ, dừng cương trước bờ vực, cùng những này độc sư cùng làm loạn quản gia chia cắt ra đến, cũng không phải không có cứu vãn chỗ trống."),t("br"),t("br"),n._v("“Vì việc nước quên tình nhà? Thật là cao to lời nói!”"),t("br"),t("br"),n._v("Trương Huyền con mắt lập tức híp lại."),t("br"),t("br"),n._v("“Làm sao? Chẳng lẽ giống như bọn họ, cũng muốn cướp pháp trường?”"),t("br"),t("br"),n._v("Nghe hắn giọng nói không đúng, Tống sư mặt mũi sắc trầm xuống: “Ngươi là danh sư, muốn thân phận của chú ý mình cùng cử chỉ! Ta cho ngươi cơ hội, là bởi vì ngươi thiên phú không yếu, hơn nữa đội Danh Sư đường từng có cống hiến, không muốn bản thân lãng phí cơ hội!”"),t("br"),t("br"),n._v("“Cơ hội này, còn là lưu cho chính ngươi đi!”"),t("br"),t("br"),n._v("Thấy đối phương để cho mình chém giết Tôn Cường, giống như cho cực lớn ân đức, Trương Huyền lại lười nhác nói nhảm, mí mắt vừa nhấc, một đạo cuộn trào mãnh liệt khí tức, tuôn trào ra, xông thẳng lên trời."),t("br"),t("br"),n._v("“Nể mặt ngươi, đừng không biết điều!”"),t("br"),t("br"),n._v("Tống sư bàn tay lớn vẫy một cái, lông mi mang theo nộ khí."),t("br"),t("br"),n._v("Một cái thất tinh danh sư mà thôi, may mắn có chút thiên phú, như vậy giúp hắn, thế mà không biết rõ tình hình, cũng không tránh khỏi quá tự đại đi!"),t("br"),t("br"),n._v("“Nể tình? Không cần, ta Trương Huyền, chưa từng cần người khác nể tình!”"),t("br"),t("br"),n._v("Sắc mặt tái xanh, Trương Huyền hướng đài cao từng bước một đi tới."),t("br"),t("br"),n._v("Hắn tức giận phi thường!"),t("br"),t("br"),n._v("Đường đường chuẩn bát tinh danh sư, nếu biết Cẩu đường chủ chết rồi, Điền Thanh phó đường chủ chết rồi, cái này nhiều tên sư chết rồi, nên lập tức điều tra!"),t("br"),t("br"),n._v("Nhiều chuyện như vậy, không đi dò xét đến tột cùng, lại đem người của mình nắm chặt không thả..."),t("br"),t("br"),n._v("Chẳng lẽ là Hứa trưởng lão cùng Tôn Cường những người này, giết nhiều như vậy danh sư?"),t("br"),t("br"),n._v("Tổng bộ làm sao phái ra như vậy một cái không rõ không phải là, không biết mức độ nặng nhẹ gia hỏa?"),t("br"),t("br"),n._v("“Làm càn! Trương Huyền, đừng tưởng rằng ngươi xông Thanh Nguyên Danh Sư đường phân bộ, có chút thiên tư, liền có thể muốn làm gì thì làm! Tại hạ Tống Hiên, chuẩn bát tinh danh sư, dâng tổng bộ mệnh lệnh, đến đây xử lý Thanh Nguyên phân bộ chuyện, ngươi một cái thất tinh danh sư, mở miệng vô lễ, hành động tùy tiện, lễ tiết ở đâu?”"),t("br"),t("br"),n._v("Tống sư hét lớn một tiếng."),t("br"),t("br"),n._v("Đối phương xông đường sự tình, hắn nghe nói, là cái siêu cấp thiên tài, nguyên nhân chính là như vậy, mới đặc thù chăm sóc, cấp cho ưu đãi."),t("br"),t("br"),n._v("Không hiểu cảm kích ngược lại cũng thôi, một điểm lễ tiết cũng không có, còn nói ra lời này, quả thực không có đem hắn để vào mắt."),t("br"),t("br"),n._v("“Lễ tiết? Ta không biết, ta chỉ biết là, bọn họ không đáng chết!”"),t("br"),t("br"),n._v("Tiếp tục hướng phía trước, đã đi tới đài cao lân cận."),t("br"),t("br"),n._v("“Cái kia không đáng chết, không phải ngươi định đoạt, chém giết một nước đế vương, gây nên náo động, chỉ điều này, giết mấy lần đều không đủ! Ngươi thân là danh sư, hẳn phải biết những này, đừng phá hư quy củ, tự hủy tương lai!”"),t("br"),t("br"),n._v("Cười lạnh một tiếng, Tống sư bàn tay lại vồ một cái, trước đó rơi xuống chém đầu đao, lần nữa lơ lửng mà lên, đang muốn rơi vào Tôn Cường đám người trên đầu, liền nghe đến liên tiếp giòn vang."),t("br"),t("br"),n._v("Bành! Bành! Bành! Bành!"),t("br"),t("br"),n._v("Hơn một trăm chuôi trường đao, đồng thời nổ tung, từng cái vỡ thành bột phấn, từ không trung rớt xuống."),t("br"),t("br"),n._v("“Thật sự là thật to gan, ta rốt cuộc biết, Thanh Nguyên đế quốc Danh Sư đường, từ trước tới nay yên lặng, một chút việc đều không có, ngươi vừa đến, liền gây ra như thế vấn đề lớn, một con chuột cứt, hỏng hỗn loạn, quả thực không có quy củ, vô pháp vô thiên...”"),t("br"),t("br"),n._v("Tống sư tức giận sắp nổ tung."),t("br"),t("br"),n._v("Cái này rõ ràng là đối phó với hắn, thật đòi lại cướp pháp trường!"),t("br"),t("br"),n._v("“Câm miệng cho ta!”"),t("br"),t("br"),n._v("Một tiếng sấm rền, Trương Huyền đi tới cách hắn không đủ mười mét địa phương, hai mắt như điện, mang theo làm người ta không cách nào phản bác khí thế cùng lực lượng."),t("br"),n._v(" “Ngươi...”"),t("br"),t("br"),n._v("Nghe nói để hắn im miệng, Tống sư run rẩy, đang nghĩ nói chuyện, liền nghe thanh âm của đối phương pháo vang lên."),t("br"),t("br"),n._v("“Cùng ta nói quy củ? Cầm lễ phép? Tốt, ta Trương Huyền, tra hung phạm, ngăn cản dị linh người âm mưu, chém giết Thanh Điền Hoàng thời điểm, ngươi ở nơi nào?”"),t("br"),t("br"),n._v("“Ta Trương Huyền, đi sâu khô cằn, rơi vào nguy cơ, cứu rất nhiều danh sư thời điểm, ngươi ở nơi nào?”"),t("br"),t("br"),n._v("“Ta Trương Huyền, truyền thụ kiến thức, để rất nhiều danh sư, hóa giải tổn hại, tu vi đột phá thời điểm, ngươi ở nơi nào?”"),t("br"),t("br"),n._v("“Thân là chuẩn bát tinh danh sư, không nghĩ biện pháp truy xét hung phạm, không xử lý Danh Sư đường rất nhiều danh sư bị giết chân tướng, lại tại nơi này, la lối om sòm, cáo mượn oai hùm... Ai cho ngươi lá gan cùng quyền lợi?”"),t("br"),t("br"),n._v("“Ngươi lại tính là thứ gì, đối ta Trương Huyền khoa tay múa chân?”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Thanh âm như lôi đình nổ tung, mang theo sét đánh thanh âm, đinh tai nhức óc, dường như dẫn động thiên địa hợp kêu."),t("br"),t("br"),n._v("Tựa như có một đạo hạo nhiên chính khí, bay thẳng Thiên Đình, mang theo uy thế cường đại, để cho người ta không dám xâm phạm."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Bị kìm nén nói không ra lời, Tống sư vẻ mặt đỏ lên, sắp nổ."),t("br"),t("br"),n._v("Trương Huyền tiếp tục xem tới: “Sở Thiên Hành, đầu nhập vào Dị Linh tộc nhân, ngươi không đi thăm dò! Điền Thanh thân là Dị Linh tộc Hoàng giả phân thân, ngươi không đi thăm dò! Cẩu Thiên Trạch, thân là đường chủ, lại bỏ rơi nhiệm vụ, tín nhiệm gian nịnh, làm toàn bộ Danh Sư đường chướng khí mù mịt... Ngươi không đi thăm dò! Lại tại nơi này, đối ta một cái không hiểu chuyện quản gia, theo đuổi không bỏ, đối mấy cái độc sư, bắt lấy không thả... Ta chỉ muốn hỏi một chút, đây là Danh Sư đường trao cho quyền lợi của ngươi?”"),t("br"),t("br"),n._v("“Đây là chuẩn bát tinh danh sư, cái kia có trách nhiệm?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Tống sư lần nữa muốn nói chuyện, lại bị đánh gãy."),t("br"),t("br"),n._v("“Khổng sư truyền giáo thiên hạ, lập danh sư chức nghiệp, là muốn cho người rõ ràng mà thấy vật, thính giác mà thông lý! Càng là tự thân dạy dỗ, truyền thụ thiên hạ, lúc này mới được phong làm vạn thế chi sư, người người ngưỡng mộ! Danh sư chức nghiệp, mục đích đúng là noi theo Khổng sư, mở thiên hạ chi Linh Hải, mở vạn dân trí tuệ tuệ! Đệ tử ta Lưu Dương, liền Thánh vực đều không có đạt tới, ngươi cảm thấy, có thể chém giết nửa bước Xuất Khiếu cảnh cường giả?”"),t("br"),t("br"),n._v("“Sở Thiên Hành một nước Đế Hoàng, dễ dàng như vậy bị giết, Thanh Nguyên đế quốc còn có tồn tại cần thiết?”"),t("br"),t("br"),n._v("“Không muốn nói với ta, là độc sư dùng độc... Nếu như ta tin tức không sai, bọn họ dùng chính là Thanh Phong tán đi! Hai mươi năm trước, Dị Linh tộc nhân đột kích, toàn bộ Thanh Nguyên đế quốc suýt chút nữa hủy diệt, liền là dùng loại thuốc này vật... Hai mươi năm thất bại một lần, hoàng thất không có một chút chuẩn bị?”"),t("br"),t("br"),n._v("“Thật không có chuẩn bị... Là đầu nhập vào Dị Linh tộc, còn là có vấn đề gì? Loại này phong hào đế quốc, làm sao có thể giữ vững nhân loại cương vực?”"),t("br"),t("br"),n._v("Trương Huyền thanh âm càng ngày càng nghiêm khắc."),t("br"),t("br"),n._v("“Cái này...” Tống sư thân thể run lên."),t("br"),t("br"),n._v("Hất lên ống tay áo, Trương Huyền tiếp tục nói: “Xử sự không rõ, không biết nên xử lý cái gì, nên làm cái gì, lại bắt lấy việc nhỏ không ngại, là vì không rõ! Nghe theo vương công, đại thần mê hoặc, dựa theo bọn họ ý nguyện làm việc, không phân biệt trung gian, không phân phải trái, là vì không khôn ngoan! Thân là chuẩn bát tinh danh sư, không đi xử lý Danh Sư đường vấn đề, đưa rất nhiều đồng nghiệp bất luận thế nào không để ý, huyên náo lòng người bàng hoàng, là vì chủ thứ chẳng phân biệt được! Không để ý Chiến Sư đường phản bác, đem đường chủ, chiến sư vây khốn, tùy ý quyền lợi, lung tung ra lệnh, ảnh hưởng Danh Sư đường danh tiếng cùng danh sư tôn nghiêm... Là vì lạm dụng chức quyền!”"),t("br"),t("br"),n._v("“Không rõ, không khôn ngoan, chủ thứ chẳng phân biệt được, lạm dụng chức quyền...” Lông mày giương lên, Trương Huyền hai tay chắp sau lưng: “Tống Hiên, ta hỏi ngươi, có gì mặt mũi tự xưng chuẩn bát tinh danh sư?”"),t("br"),t("br"),n._v("“Ta... Phốc!”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Tống sư một ngụm máu tươi phun ra, thân thể quơ quơ, từ không trung suýt chút nữa rơi xuống."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("“Một chiêu không động, mấy câu, liền nói Tống sư miệng phun máu tươi, suýt chút nữa rơi xuống?”"),t("br"),t("br"),n._v("Nuốt nước bọt, Tống Siêu đám người từng cái hai mặt nhìn nhau, vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Kế hoạch tốt, cướp pháp trường trước đó, bọn họ liền đủ loại cân nhắc, nhưng cuối cùng bất đắc dĩ phát hiện, cho dù nhiều người như vậy tính gộp lại, cũng không phải là đối thủ của Tống sư, thật muốn động thủ, tất nhiên có đi không về!"),t("br"),t("br"),n._v("Vốn cho rằng mãnh liệt vô cùng, không nghĩ tới Trương sư xuất hiện... Một chiêu đều không có ra, mấy câu liền nói đối phương hộc máu..."),t("br"),t("br"),n._v("“Thiếu gia... Miệng sống quá tốt rồi đi!”"),t("br"),t("br"),n._v("Bị giam giữ Tôn Cường, trợn mắt hốc mồm, một tiếng nói thầm."),t("br"),t("br"),n._v("“Miệng sống? Khụ khụ!” Hứa trưởng lão bị nước bọt sặc một cái: “Ân công, chúng ta làm sao bây giờ?”"),t("br"),t("br"),n._v("“Không cần phải gấp, ta trước đó không liền nói qua ư? Thiếu gia, khẳng định sẽ ra tay, cho dù hắn không ra tay, lão gia cũng sẽ ra tay... Bọn họ sẽ không trơ mắt nhìn ta xảy ra chuyện. Có bọn họ, chúng ta liền khẳng định an toàn!”"),t("br"),t("br"),n._v("Nhếch miệng, Tôn Cường ngáp một cái."),t("br"),t("br"),n._v("Giam giữ mấy ngày nay, hắn chẳng những không có mảy may bị thương, dường như... Lại mập một vòng."),t("br"),t("br"),n._v("Thật giống như, tu luyện quá mệt mỏi, đi Danh Sư đường nhà tù... Nghỉ ngơi mấy ngày, nghỉ phép..."),t("br"),t("br"),n._v("“Ân công tâm tính thật tốt...”"),t("br"),t("br"),n._v("Nghe hắn nói như vậy, nhìn hắn cử động, Hứa trưởng lão tràn đầy im lặng, trong lòng nhịn không được nghi ngờ... Cái này ân công, như thế nào cùng trước đó đã gặp không giống?"),t("br"),t("br"),n._v("Trước đó ân công, anh dũng quả quyết, thông minh dị thường, gặp được việc lớn, bình tĩnh mà không mất đi uy nghiêm, mà vị này..."),t("br"),t("br"),n._v("Nhốt tại Danh Sư đường, ăn một bữa năm người cơm, một giấc có thể ngủ đến ngày thứ hai bình minh... Làm sao cảm giác cũng không quá tương tự."),t("br"),t("br"),n._v("Bất quá, đối phương tâm tính cùng cử chỉ, quả thực là vạn người không được một."),t("br"),t("br"),n._v("Bị Danh Sư đường định tội, tiện tay đều sẽ chém giết, đều không sợ chút nào, vui mừng tự nhiên... Phần này năng lực, liền xem như hắn, đều theo không kịp."),t("br"),t("br"),n._v("Có lẽ đây là ân công được tổ sư vừa ý chỗ hơn người đi!"),t("br"),t("br"),n._v("(Tiến cử lão nhai sách cũ 《 vô tận đan điền 》, Chí Tôn sảng văn, không thể bỏ lỡ.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1244"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1246"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);