(window.webpackJsonp=window.webpackJsonp||[]).push([[291],{646:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1239-cuu-van-dan-tai-dau-soi-lua-bong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1239-cuu-van-dan-tai-dau-soi-lua-bong"}},[n._v("#")]),n._v(" Chương 1239: Cứu vạn dân tại dầu sôi lửa bỏng")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1239: Cứu vạn dân tại dầu sôi lửa bỏng"),t("br"),t("br"),n._v("“Ngươi làm càn!”"),t("br"),t("br"),n._v("Tống Hiên tức giận sắp nổ."),t("br"),t("br"),n._v("“Chúng ta danh sư, tự thân dạy dỗ, thay đổi một cách vô tri vô giác, để cho người ta học tập kiến thức đồng thời, thay đổi tâm tính, hướng tốt hơn phương hướng phát triển! Nếu như theo lời ngươi nói, người người bản tính khó dời, muốn danh sư làm gì dùng? Ngươi là đang phủ định Khổng sư lý niệm, phủ định Danh Sư đường ư?”"),t("br"),t("br"),n._v("Trương Huyền trách mắng."),t("br"),t("br"),n._v("“Ta... Ta không phải ý tứ này!”"),t("br"),t("br"),n._v("Tống Hiên thân thể nhoáng một cái."),t("br"),t("br"),n._v("Phủ định Khổng sư, phủ định Danh Sư đường... Cho hắn mười cái lá gan cũng không dám ah!"),t("br"),t("br"),n._v("“Vậy là ngươi phủ định chiến tử, cùng Chiến Sư đường, cảm thấy quyết định của bọn hắn có sai?” Trương Huyền nói tiếp."),t("br"),t("br"),n._v("“Ta... Cũng không phải ý tứ này!”"),t("br"),t("br"),n._v("Tống Hiên tiếp tục cắn răng."),t("br"),t("br"),n._v("Chiến sư cứ việc thụ Danh Sư đường chỉ huy, nhưng chiến tử, thân phận quá cao, không phải hắn loại tiểu nhân vật này, có thể ngông cuồng thảo luận."),t("br"),t("br"),n._v("“Kia cái gì ý tứ? Trên đời vẩn đục ta duy nhất thanh liêm, mọi người đều say ta duy nhất tỉnh?”"),t("br"),t("br"),n._v("Trương Huyền nhìn tới."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Kìm nén sắc mặt đỏ lên, biết mặc cho đối phương tiếp tục hỏi tiếp, khẳng định rơi vào trong mương, Tống Hiên vội vàng nhìn về phía trước mắt Diêu Mạn Thiên, nắm đấm ôm chặt: “Diêu sư, độc sư hành động, chúng ta danh sư, đều rất rõ ràng, tu luyện công pháp, ác độc không nói, thậm chí sử dụng máu người, mạng người nuôi cổ... Tàn nhẫn đến cực điểm! Những người này, mặc dù không biết loại nguyên nhân nào, bị hợp nhất đi vào Chiến Sư đường, nhưng căn cứ ta phỏng đoán, có lẽ chỉ là biến tướng giam giữ, phòng ngừa làm ác! Cũng không phải là thật thừa nhận thân phận...”"),t("br"),t("br"),n._v("“Công pháp ác độc?”"),t("br"),t("br"),n._v("Lông mày nâng lên, Trương Huyền ngắt lời hắn: “Thân là danh sư, muốn nói đúng sự thật... Mở mắt nói lời bịa đặt, cố ý lừa gạt bát tinh danh sư, không biết ra sao tội danh?”"),t("br"),t("br"),n._v("“Ngươi nói bậy bạ gì đó?”"),t("br"),t("br"),n._v("Tống Hiên cắn răng."),t("br"),t("br"),n._v("“Nói bậy?” Nhìn về phía dưới đài Hứa trưởng lão đám người, Trương Huyền thanh âm vang lên: “Chư vị, còn xin vận chuyển công pháp của các ngươi, để vị này Tống sư nhìn một chút, có phải hay không ác độc chi pháp!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Hứa trưởng lão đám người chân khí trong cơ thể vận chuyển, từng đạo khí tức phóng lên tận trời."),t("br"),t("br"),n._v("Những khí tức này, đường đường chính chính, chẳng những không có mảy may âm tà cùng kỳ lạ, ngược lại cho người ta một loại ấm áp thoải mái dễ chịu cảm giác."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Tống Hiên sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("Thân là chuẩn bát tinh danh sư, độc sư gặp qua không ít, mỗi một cái đều có mang kịch độc, cho người ta một loại âm quỷ cảm giác."),t("br"),t("br"),n._v("Trước mắt những này, không những không có loại cảm giác này, còn tràn đầy ấm áp..."),t("br"),t("br"),n._v("Không để ý tới hắn khiếp sợ, Trương Huyền ôm quyền: “Xin hỏi Diêu sư... Bọn họ tu luyện thế nhưng là ác độc chi pháp?”"),t("br"),t("br"),n._v("Lắc đầu, Diêu Mạn Thiên nói: “Bọn họ chân khí tinh thuần thản nhiên, mang theo quang minh chi ý, không có quan hệ gì với ác độc!”"),t("br"),t("br"),n._v("“Không sai, bọn họ sớm đã vứt bỏ ác hướng thiện... Trở thành Chiến Sư đường một thành viên, Tống sư vẫn còn đem hắn xem như độc sư, mở miệng một tiếng ác độc... Ta muốn hỏi một chút, như thế nào ác độc? Là ngươi chủ quan đi ra sao?”"),t("br"),t("br"),n._v("Nhìn chằm chằm Tống Hiên, Trương Huyền một bước cũng không nhường."),t("br"),t("br"),n._v("“Ta...” Thân thể run lên, Tống Hiên không biết trả lời như thế nào."),t("br"),t("br"),n._v("đọc tｒuyện cùng"),t("br"),n._v("//ngantruyen.com/ Bắt Hứa trưởng lão đám người thời điểm, đám người không có phản kháng, cho nên hắn cũng không biết, độc sư cũng có thể tu luyện ra như vậy đường đường chính chính công pháp."),t("br"),t("br"),n._v("“Cho dù công pháp của bọn họ tu luyện, lại không âm quỷ, vừa ra tay liền đem toàn bộ hoàng cung tất cả mọi người độc choáng, đây là sự thật, ngươi nói nhiều hơn nữa, cũng không cách nào sửa đổi...”"),t("br"),t("br"),n._v("Qua một hồi thật lâu, lúc này mới khôi phục lại."),t("br"),t("br"),n._v("“Đã nói như vậy, vậy ta liền đem chuyện nói rõ chi tiết một lần, cũng tốt để ngươi hết hy vọng!”"),t("br"),t("br"),n._v("Bàn tay lớn vẫy một cái, Trương Huyền nhìn về phía trước mắt bát tinh danh sư: “Diêu sư, ta muốn đem chuyện này chân tướng, cẩn thận kể ra, sau đó ngươi sẽ giúp ta cùng đám người phân xử!”"),t("br"),t("br"),n._v("“Mời nói!” Diêu Mạn Thiên gật đầu."),t("br"),t("br"),n._v("“Là như vậy, Thanh Điền nhất mạch vị này Thanh Điền Hoàng, không chỉ tu vi cao thâm, thực lực phi phàm, càng là một vị lợi hại vu hồn sư! Có một loại, có thể bắt người hồn phách đặc thù bí pháp. Nói cách khác, mặc kệ thực lực ngươi làm sao, chỉ cần không có vượt qua đối phương, hơn nữa tại trong phạm vi nhất định... Linh hồn liền có thể bị nhẹ nhõm bắt giữ, luyện hóa, trở thành tu luyện chất dinh dưỡng!”"),t("br"),t("br"),n._v("“Bắt giữ linh hồn, luyện hóa?”"),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một cái tế đàn xuất hiện ở trước mắt, rơi vào trên mặt đất, Trương Huyền nói: “Đây chính là hắn sử dụng bên trong một cái trận pháp, Diêu sư nếu như không tin, có thể nhìn một chút!”"),t("br"),t("br"),n._v("Chính là dùng để bắt giữ Thanh Điền Hoàng cái kia tế đàn."),t("br"),t("br"),n._v("“Những này bí văn...”"),t("br"),t("br"),n._v("Nhìn kỹ một vòng, Diêu Mạn Thiên vẻ mặt càng ngày càng dày đặc: “Mặc dù ta chưa bao giờ thấy qua loại này tế đàn, nhưng... Vu hồn sư kỳ lạ khó lường, dựa vào thôn phệ người khác linh hồn để cho mình tấn cấp bí pháp, quả thực tồn tại, nguyên nhân chính là như vậy, năm đó, mới bị chính đạo chỗ không được, bị Danh Sư đường gạt bỏ!”"),t("br"),t("br"),n._v("Cứ việc chưa thấy qua thứ này, nhưng lấy nàng bát tinh danh sư ánh mắt, có thể nhẹ nhõm nhìn ra, đối linh hồn có cực lớn tính công kích, một khi phối hợp phương pháp đặc thù vận chuyển, làm không cẩn thận, thật có thể đem linh hồn của con người, theo trong cơ thể rút ra ra ngoài."),t("br"),t("br"),n._v("“Không sai, loại phương pháp này, nói thật, không chỉ Thanh Điền Hoàng có, Sở Thiên Hành hiệu trung với hắn, cũng học được!” Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Sở Thiên Hành cũng có thể là vu hồn sư?” Diêu Mạn Thiên cau mày."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),n._v("“Nhưng có chứng cứ?” Diêu Mạn Thiên nói."),t("br"),t("br"),n._v("Học tập vu hồn thủ đoạn, hiệu trung Dị Linh tộc nhân, tội danh rất lớn, Sở Thiên Hành đã chết, nhưng không có chứng cứ, vẫn như cũ tương đương phỉ báng."),t("br"),t("br"),n._v("“Muốn chứng minh, mười phần đơn giản, Bạch trưởng lão, làm phiền đi đem Trần Triết, Cẩu đường chủ, cùng mấy vị kia thất tinh danh sư thi thể mang tới!”"),t("br"),t("br"),n._v("Vẻ mặt không thay đổi, Trương Huyền quay đầu phân phó."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Dưới đài cao, Danh Sư đường một vị trưởng lão, xoay người đi ra ngoài."),t("br"),t("br"),n._v("Vị này, từng cùng hắn, cùng đi Trần Triết sân nhỏ, bắt Điền Thanh phó đường chủ, đã sớm nhận biết."),t("br"),t("br"),n._v("Thời gian không dài, Bạch trưởng lão lần nữa trở về, cổ tay khẽ đảo, một loạt thi thể rơi trên mặt đất."),t("br"),t("br"),n._v("Biết quan hệ trọng đại, những thi thể này, Danh Sư đường trải qua đặc thù xử lý, cho dù qua nhiều ngày như vậy lấy ra, vẫn như cũ sinh động như thật, cùng vừa mới chết không có khác nhau quá nhiều."),t("br"),t("br"),n._v("“Diêu sư có thể cẩn thận kiểm tra, nhìn một chút những thi thể này điểm giống nhau!”"),t("br"),t("br"),n._v("Trương Huyền vẫy vẫy tay."),t("br"),t("br"),n._v("Đi lên phía trước, Diêu Mạn Thiên cúi đầu nhìn sang, càng xem lông mày nhăn càng chặt."),t("br"),t("br"),n._v("Những thi thể này, tất cả đều hoàn hảo không chút tổn hại, từ bên ngoài nhìn vào không ra bất kỳ vết thương, nhưng linh hồn, cũng đã biến mất, hoàn toàn biến mất."),t("br"),t("br"),n._v("“Đây đều là linh hồn bị giết mà chết...”"),t("br"),t("br"),n._v("Toàn bộ xem hết, Diêu Mạn Thiên nói."),t("br"),t("br"),n._v("“Ừm, đây đều là linh hồn bị câu bắt, gạt bỏ mà chết... Hơn nữa, những này thất tinh danh sư, trước khi chết, đều có một cái đặc thù, cùng Sở Thiên Hành từng có tiếp xúc, điểm ấy, có thể tại Danh Sư đường thẩm tra!”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Sở Thiên Hành từng tới quan sát qua Trung Thanh Vương, cùng những người này cũng đã có tiếp xúc, trước đó, không cảm thấy cái gì... Trong đầm lầy biết hắn hiệu trung Ngoan Nhân về sau, đoán ra vấn đề liền đơn giản."),t("br"),t("br"),n._v("Không có môi giới, cho dù Ngoan Nhân lợi hại hơn nữa, cũng không có khả năng cách xa không biết bao nhiêu vạn dặm, liền đem người gạt bỏ, sở dĩ có thể làm được, hẳn là vị này Sở Thiên Hành bệ hạ, tiếp xúc những danh sư này, đồng thời cho đối phương đưa tới mỹ tửu mỹ thực."),t("br"),t("br"),n._v("“Ta có thể chứng minh, đêm hôm đó, Sở Thiên Hành tới quan sát Trung Thanh Vương, mang theo rất nhiều rượu ngon, cho mọi người uống!”"),t("br"),t("br"),n._v("“Ta cũng có thể chứng minh, nói tới mỗi một câu, đều có thể dùng danh sư thân phận thề...”"),t("br"),t("br"),n._v("Vừa dứt lời, dưới đài không ít danh sư, hô lên."),t("br"),t("br"),n._v("Lúc đó Sở Thiên Hành mười phần niềm nở, không ít người ký ức vẫn còn mới mẻ."),t("br"),t("br"),n._v("“Ngươi ý tứ... Là vị này Sở Thiên Hành, sử dụng bí pháp, giết chết nhiều như vậy danh sư?”"),t("br"),t("br"),n._v("Nghe nhiều người như vậy chứng minh, Diêu Mạn Thiên biết không có khả năng là giả, nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Cho dù không phải hắn làm, cũng tất nhiên cùng hắn có cực lớn quan hệ!”"),t("br"),t("br"),n._v("Trương Huyền nói tiếp: “Ta cùng Ngô sư, mặc dù suy đoán ra, hắn có vấn đề, nhưng cũng không dám động thủ, bởi vì, một khi đem hắn chém giết, núp ở Tề Bắc đầm lầy Thanh Điền Hoàng, tất nhiên sẽ bỏ trốn, lại nghĩ tìm kiếm liền khó khăn! Cho nên, hai người chúng ta lấy bế quan danh nghĩa, lặng lẽ tập hợp chín vị cao thủ, đi tới đầm lầy chém giết đối phương, cứ việc nguy hiểm tầng tầng, nhưng cũng may mắn thành công... Chuyện này, ta trước đó đã nói!”"),t("br"),t("br"),n._v("Diêu Mạn Thiên gật đầu."),t("br"),t("br"),n._v("Chuyện này vừa rồi đã nói tỉ mỉ."),t("br"),t("br"),n._v("“Chúng ta đi mặc dù bí ẩn, nhưng làm một nước Hoàng đế, mấy ngày sau, vẫn như cũ phát hiện không đúng. Đoán chừng cũng suy đoán ra bản thân rò rỉ, ý định làm chó cùng rứt giậu!”"),t("br"),t("br"),n._v("“Chó cùng rứt giậu?”"),t("br"),t("br"),n._v("“Không sai, căn cứ suy đoán của ta, Sở Thiên Hành vô cùng có khả năng, sử dụng cái này bắt giữ linh hồn bí pháp, hiến tế Thanh Nguyên thành toàn bộ sinh linh, giúp đỡ tăng cao tu vi! Một chút giết chết nhiều cường giả như vậy, không thể nào làm được, nhưng chỉ bị hạn chế Thánh vực trở xuống người, đồ sát mấy trăm vạn, mấy ngàn vạn, không khó lắm! Một khi thành công, cả tòa Đế đô, đều đem sinh linh đồ thán, biến thành nhân gian địa ngục!”"),t("br"),t("br"),n._v("Trương Huyền đạo, thanh âm bên trong, mang theo trách trời thương dân."),t("br"),t("br"),n._v("“Trương sư có ý tứ là... Sở Thiên Hành bệ hạ, muốn đem Thanh Nguyên thành tất cả Thánh vực trở xuống người, toàn bộ giết chết?”"),t("br"),t("br"),n._v("“Cái này... Cái này sao có thể? Không có điên cuồng như vậy đi!”"),t("br"),t("br"),n._v("Trương Huyền thanh âm không có che lấp, dưới đài lập tức xôn xao, tất cả mọi người cũng không dám tin tưởng."),t("br"),t("br"),n._v("Tin tức này quả thực nghe nói quá kinh người, vượt ra khỏi tưởng tượng ra."),t("br"),t("br"),n._v("“Phản bội nhân tộc, kết quả gì, chắc hẳn không cần ta nhiều lời, mọi người đều biết! Một khi vạch trần, sẽ trở thành toàn bộ đại lục kẻ địch, sống không bằng chết... Làm ra bất luận cái gì vô cùng sự tình, cũng có thể!” Trương Huyền khẽ nói."),t("br"),t("br"),n._v("“Cái này...” Đám người trầm mặc."),t("br"),t("br"),n._v("Sinh Tử trước mặt, không có người có thể duy trì tâm tính, ai cũng không cách nào xác nhận, sẽ làm ra cái gì."),t("br"),t("br"),n._v("“Nguyên nhân chính là có này lo lắng, xuất phát phía trước, ta đặc biệt dặn dò Tôn Cường, Lưu Dương, cùng Hứa trưởng lão đám người, chỉ cần xuất hiện biến cố, dù là bốc lên bị hiểu lầm thanh danh, cũng muốn cứu sinh mệnh! Đây là chúng ta danh sư, cái kia tận nghĩa vụ, cái kia làm được chức trách!”"),t("br"),t("br"),n._v("“Cho nên... Lúc này mới có, Lưu Dương lẫn vào hoàng cung, làm bộ bị bắt, Tôn Cường cùng Hứa trưởng lão đám người, độc choáng tất cả hộ vệ... Nhìn tới lỗ mãng, lại là giải quyết vấn đề phương pháp tốt nhất, đã có thể giảm bớt ảnh hưởng, liệu có thể cứu người...”"),t("br"),t("br"),n._v("“Bởi vậy, ta cảm thấy bọn họ không những vô tội, còn cứu người có công... Như vậy công thần, bị không phân biệt thanh hồng tạo bạch Tống sư, phán xử chém giết trước mặt mọi người, ta có thể nào khoanh tay đứng nhìn? Thật muốn mặc kệ phát sinh, Danh Sư đường công bằng ở đâu? Danh dự ở đâu?”"),t("br"),t("br"),n._v("Càng nói càng kích động, Trương Huyền nổi giận đùng đùng, thanh âm vang vọng cả tòa Đế đô."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Đem những lời này nghe vào trong tai, Hứa trưởng lão đám người hai mặt nhìn nhau."),t("br"),t("br"),n._v("Cứu vạn dân tại dầu sôi lửa bỏng... Làm sao, đột nhiên, chúng ta cứ như vậy vĩ đại..."),t("br"),t("br"),n._v("Vị này Trương sư nói những thứ này..."),t("br"),t("br"),n._v("Chúng ta, làm sao hoàn toàn không có ấn tượng đây?"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1247"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1249"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);