(window.webpackJsonp=window.webpackJsonp||[]).push([[292],{648:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1240-then-qua-thanh-gian-tong-hien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1240-then-qua-thanh-gian-tong-hien"}},[n._v("#")]),n._v(" Chương 1240: Thẹn quá thành giận Tống Hiên")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1240: Thẹn quá thành giận Tống Hiên"),t("br"),t("br"),n._v("Cầm trong tay độc dược, một đêm đem trọn cái hoàng cung, trên vạn người mê choáng, không có chút nào năng lực phản kháng, mặc dù là vì cứu người, Sở Thiên Hành cũng trừng phạt đúng tội... Nhưng thực sự quá kinh thế hãi tục."),t("br"),t("br"),n._v("Không phải cứu người, mà là giết người... Ai có thể chống cự chủ?"),t("br"),t("br"),n._v("Tin tức truyền ra, tất nhiên người người cảm thấy bất an, cái khác phong hào đế quốc, hoảng sợ không chịu nổi một ngày, hận không thể giết chi cho thống khoái."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Tống Hiên phán xử đám người tử hình, không có quá nhiều chần chừ, ngay cả Diêu Mạn Thiên trong lòng cũng ngầm đồng ý, cảm thấy không thương tổn phong nhã."),t("br"),t("br"),n._v("Trương Huyền tâm cảnh đạt tới 2 5.1, thế sự hiểu rõ đều học vấn, bọn họ lo lắng cùng ý nghĩ, nhìn ở trong mắt, rõ ràng rõ rõ ràng ràng, biết nếu như không có lý do thích hợp, cho dù nói khá hơn nữa, cũng khẳng định khó thoát hình phạt kết cục."),t("br"),t("br"),n._v("Cho nên... Trước cho Hứa trưởng lão đám người một cái chính thức thân phận, sau đó lại cho Sở Thiên Hành an cái “Có lẽ có” tội danh!"),t("br"),t("br"),n._v("Như vậy trải qua, bọn họ cũng không phải là lỗ mãng động thủ, mà là Chiến Sư đường phối hợp danh sư, có mục đích có kế hoạch cứu người hành động..."),t("br"),t("br"),n._v("Cái khác phong hào đế quốc, biết những này, tất nhiên sẽ không nhiều lời, cũng sẽ không đi lo lắng cái gì."),t("br"),t("br"),n._v("Những ý nghĩ này, những người khác không nghĩ ra được, Hứa trưởng lão sửng sốt một chút, lập tức rõ ràng, lần nữa nhìn về phía vị này Trương Huyền, tràn đầy bội phục."),t("br"),t("br"),n._v("Thời gian ngắn như vậy, liền phân tích ra, Tống sư, Diêu sư tâm lý, biết Danh Sư đường cùng toàn bộ Thanh Nguyên thành tất cả tu luyện giả ý nghĩ, đồng thời tìm ra thích hợp nhất lý do, vừa đập vừa cào, một chiêu giải quyết vấn đề... Đơn chiêu này, liền là không biết bao nhiêu người, đều làm không được!"),t("br"),t("br"),n._v("Phản ứng nhanh chóng, suy nghĩ chi nhanh chóng, có thể nói kinh khủng!"),t("br"),t("br"),n._v("“Ta thế nào cảm giác... Vị này mới thật sự là ân công?”"),t("br"),t("br"),n._v("Lần nữa nhìn Trương Huyền một cái, lại nhịn không được nhìn về phía cách đó không xa Tôn Cường, Hứa trưởng lão nói thầm."),t("br"),t("br"),n._v("Cứu bọn họ vị kia ân công, gặp được sự tình chính là như vậy, có được vượt qua người thường phản ứng cùng năng lực, cơ trí thông minh, làm bất cứ chuyện gì đều không chút hoang mang, cùng cùng bọn hắn cùng một chỗ bị bắt cái tên mập mạp kia... Hoàn toàn khác biệt!"),t("br"),t("br"),n._v("Mập mạp này Tôn Cường, hình như loại trừ biết ăn... Tương đối lười, có vẻ như thật không có phát hiện quá nhiều ưu điểm ah!"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ, đang nghĩ thăm dò một chút, chỉ thấy cách đó không xa mập mạp “Ân công”, bỗng nhiên hướng về phía trước mấy bước."),t("br"),t("br"),n._v("“Diêu sư, tại hạ liền là vị kia phải bị chém đầu Tôn Cường!”"),t("br"),t("br"),n._v("Thanh âm vang vang, Tôn Cường hai tay chắp sau lưng, một bộ ngạo nghễ thần thái, đồng thời thanh âm vang vọng toàn bộ quảng trường: “Chuyện này, chúng ta không muốn nói ra, là sợ làm cho náo động, làm cho tất cả mọi người khủng hoảng, đã thiếu gia nói rồi, vậy ta liền thừa nhận đi! Ta cùng Lưu Dương thiếu gia, sở dĩ chém giết Sở Thiên Hành, chính là nhìn thấy hắn đang làm ác, ý định đem trọn cái Đế đô người, tất cả đều tế tự! Chúng ta mặc dù không có thực lực, danh khí cũng không hiện, nhưng nếu như có thể sử dụng mạng của chúng ta, thay đổi cả tòa thành người, có chết cũng vinh dự!”"),t("br"),t("br"),n._v("Nói hết, Tôn Cường đi tới trước mặt mọi người, lưng eo thẳng đứng, một bộ vì nhân tộc, bất cứ lúc nào cũng sẽ anh dũng hy sinh, lại không bị lý giải bộ dáng."),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("“Hắn là vì cứu chúng ta... Buồn cười, chúng ta còn tưởng rằng, hắn là phản thần nghịch tử!”"),t("br"),t("br"),n._v("“Sở Thiên Hành thế mà đầu nhập vào Dị Linh tộc, còn muốn đem chúng ta toàn bộ tế tự, tội không thể tha!”"),t("br"),t("br"),n._v("“Tận mắt nhìn đến, mới biết được Tôn Cường cùng những này độc sư, không, là chiến sư, là vĩ đại dường nào, chúng ta đều trách oan bọn họ...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được giải thích, lại tận mắt thấy vị này Tôn Cường, thà chết chứ không chịu khuất phục sống lưng, tình nguyện bị giết, cũng không nguyện ý để đám người khủng hoảng vĩ đại tình cảm sâu đậm... Đám người từng cái tràn đầy cảm động."),t("br"),t("br"),n._v("Danh Sư đường rất nhiều danh sư càng là một mặt xấu hổ."),t("br"),t("br"),n._v("Mặc dù là danh sư, nhưng cùng những người này so, còn là kém quá nhiều."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hứa trưởng lão khóe miệng lần nữa co lại."),t("br"),t("br"),n._v("Đang tại làm điều ác?"),t("br"),t("br"),n._v("Rõ ràng là trong lúc vô tình đánh chết được không? Hơn nữa mấu chốt nhất là... Ngươi còn muốn cầm đao đem người ta thiến..."),t("br"),t("br"),n._v("Làm ác chính là ngươi, lúc nào biến thành Sở Thiên Hành?"),t("br"),t("br"),n._v("Mấu chốt nhất là... Ngươi trừ ăn ra liền là ngủ, lúc nào anh dũng hy sinh..."),t("br"),t("br"),n._v("“Bất quá... Điểm ấy lại cùng ân nhân có chút tương tự!”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, Hứa trưởng lão cũng chia không rõ."),t("br"),t("br"),n._v("Trước đó hoài nghi, vị này Trương Huyền Trương sư, có thể là chân chính ân nhân, nhưng nhìn thấy Tôn Cường bộ này thần thái... Lại cùng ân nhân có chút tương tự, để hắn cũng lại phân biệt không ra."),t("br"),t("br"),n._v("“Cái tên này... Liền biết trang bức!”"),t("br"),t("br"),n._v("Cùng Hứa trưởng lão ý nghĩ khác biệt, gặp Tôn Cường lao ra, biểu hiện ra bộ dáng này, Trương Huyền nhịn không được xoa xoa mi tâm, không còn gì để nói: “Nếu là có ta một nửa khiêm tốn liền tốt!”"),t("br"),t("br"),n._v("Hắn bất kể làm chuyện gì, đều nghĩ tới là, có thể tiết kiệm phiền phức liền tiết kiệm phiền phức, có thể khiêm tốn liền khiêm tốn... Cái tên này ngược lại tốt, liền dễ ra danh tiếng..."),t("br"),t("br"),n._v("Thật không biết học từ ai vậy!"),t("br"),t("br"),n._v("Bất quá, như vậy cũng tốt."),t("br"),t("br"),n._v("Chỉ cần tạo thành dư luận, chuyện chẳng khác nào hóa giải, cho dù vị này Diêu sư, cảm thấy chuyện không thích hợp, tại dân tâm, dân ý trước mặt, cũng khẳng định sẽ trực tiếp đáp ứng."),t("br"),t("br"),n._v("Mấu chốt nhất là, một cái đế quốc Hoàng đế phản bội, đối Danh Sư đường danh dự, có tổn thương rất nặng, làm như vậy... Chẳng khác nào Danh Sư đường, trí cuốn tại nắm, sớm biết Sở Thiên Hành việc ác, sớm chuẩn bị, không chỉ danh dự không tổn hại, còn có thể lần nữa cất cao một cái cấp bậc, để cho người ta càng thêm kính nể."),t("br"),t("br"),n._v("Loại tình huống này, chỉ cần không ngốc, khẳng định sẽ trực tiếp đáp ứng, không cần nghĩ."),t("br"),t("br"),n._v("Quả nhiên, ý nghĩ còn không có kết thúc, liền thấy cách đó không xa Diêu Mạn Thiên nhẹ gật đầu, một mặt ngưng trọng nhìn qua: “Không nghĩ tới Sở Thiên Hành như vậy việc ác! Đã chuyện tra ra manh mối, ta sẽ hướng tổng bộ xin thưởng, thật tốt hậu đãi chư vị, bù đắp trước đó sai lầm!”"),t("br"),t("br"),n._v("“Diêu sư...”"),t("br"),t("br"),n._v("Nghe được nàng như vậy phán quyết, Tống Hiên sắc mặt trắng nhợt: “Chuyện này, ta cảm thấy còn có kỳ lạ, cần cẩn thận điều tra...”"),t("br"),t("br"),n._v("Như vậy xác định, chẳng khác nào nói hắn, làm việc bất lợi, phủ hãm trung lương... Chỉ điều này tội danh, chỉ sợ về sau lại không cách nào xoay người."),t("br"),t("br"),n._v("“Đủ rồi!”"),t("br"),t("br"),n._v("Sầm mặt lại, Diêu Mạn Thiên vung tay áo một cái: “Còn ngại mất mặt ném không đủ ư? Thân là chuẩn bát tinh danh sư, không hảo hảo điều tra rõ ràng, liền trực tiếp xét xử, suýt chút nữa để trung lương thất vọng đau khổ... Về sau, làm sao xứng đáng Danh Sư đường trao cho quyền lợi của ngươi?”"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Tống Hiên thân thể run lên."),t("br"),t("br"),n._v("“Tốt, lần này ngươi làm việc bất lợi, ta sẽ kỹ càng báo lên tổng bộ, chờ lấy trở về tiếp nhận xử lý đi!”"),t("br"),t("br"),n._v("Diêu Mạn Thiên khoát tay áo."),t("br"),t("br"),n._v("“Đều là ngươi...”"),t("br"),t("br"),n._v("Vốn cho rằng là cái rất nhẹ nhàng, rất đơn giản sống, không nghĩ tới biến thành như vậy, Tống Hiên quay đầu nhìn về phía Trương Huyền, tràn đầy hận ý."),t("br"),t("br"),n._v("Nếu không phải cái tên này... Chỉ cần xử lý tốt chuyện nơi đây, cho dù sẽ không một bước lên mây, cũng tuyệt đối có thể từng bước cao thăng, thậm chí có khả năng đạt được phía trên ban thưởng, thẳng đột phá Xuất Khiếu cảnh ràng buộc, xung kích Lĩnh Vực cảnh thành công..."),t("br"),t("br"),n._v("Đáng tiếc... Hiện tại tất cả đều biến thành bọt nước!"),t("br"),t("br"),n._v("Mấu chốt nhất là, còn bị trước mặt mọi người đánh mặt, đồ chơi kia cũng phế bỏ..."),t("br"),t("br"),n._v("Cừu hận có thể nói đã không đội trời chung!"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, danh sư huy chương rơi vào lòng bàn tay, ngón tay một điểm, một giọt máu tươi lơ lửng ở phía trên."),t("br"),t("br"),n._v("Ngay sau đó một cái âm thanh vang dội, vang vọng mà lên."),t("br"),t("br"),n._v("“Trương Huyền, giật dây nhiều như vậy danh sư, ra tay với ta, nhục ta tôn nghiêm, càng làm cho binh khí của mình đem ta đánh phế... Ta Tống Hiên, lấy thất tinh danh sư danh nghĩa, hướng tên hội đường tổng bộ, xin danh sư khiêu chiến, hiện tại tiến hành... Ngươi có dám đáp ứng?”"),t("br"),t("br"),n._v("Lập tức, huy chương bên trên một đạo quang mang, bỗng nhiên bay về phía bầu trời."),t("br"),t("br"),n._v("“Tống Hiên, ngươi thật to gan!”"),t("br"),t("br"),n._v("Không nghĩ tới, cái tên này không cam tâm bị phạt, thẳng đưa ra danh sư khiêu chiến, Diêu Mạn Thiên vẻ mặt âm trầm xuống."),t("br"),t("br"),n._v("Danh sư ở giữa, có không giải quyết được mâu thuẫn, có thể xin , thông qua huy chương có thể đem loại này ý nguyện báo lên tới tổng bộ, sau đó tổng bộ, căn cứ ngươi muốn khiêu chiến người thực lực cụ thể, tiến hành phán quyết."),t("br"),t("br"),n._v("Đây là giải quyết danh sư ân oán phương pháp tốt nhất, một khi hai bên đồng ý, cho dù nàng là bát tinh danh sư, cũng không có cách nào ngăn cản."),t("br"),t("br"),n._v("“Làm sao không dám, ta đáp ứng!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cưỡng ép từ đối phương trong tay cướp người, càng là nói hộc máu, làm không làm được nam nhân... Mà nên lấy nhiều người như vậy... Nếu như vị này Tống Hiên nuốt được đi, hắn mới gọi giật mình."),t("br"),t("br"),n._v("Xuất hiện loại kết quả này, hoàn toàn ở trong dự liệu."),t("br"),t("br"),n._v("“Trương sư...” Diêu Mạn Thiên vội vàng nhìn tới, muốn khuyên can, có điều, hắn mới vừa đáp ứng, Danh Sư đường bên kia liền có cảm ứng."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Cách đó không xa danh sư tháp bên trên, một đạo quang mang lấp lánh mà ra, thời gian nháy mắt trên không trung tạo thành hai chữ lớn “Đồng ý!”."),t("br"),t("br"),n._v("Tống Hiên, mặc dù là chuẩn bát tinh danh sư, trên thực tế cũng tại thất tinh phạm trù, còn là thất tinh danh sư, Trương Huyền vừa mới xông đường thành công, uy thế chính nồng... Bởi vậy, tổng bộ đi qua phán định, hai người có lực đánh một trận."),t("br"),t("br"),n._v("“Dám đáp ứng liền tốt...”"),t("br"),t("br"),n._v("Thấy đối phương không có chút nào chần chờ đáp ứng, Tống Hiên có chút ngoài ý muốn, cắn răng: “Ta đưa ra khiêu chiến, tỷ thí hạng mục, có thể từ ngươi tới định!”"),t("br"),t("br"),n._v("Hắn đưa ra khiêu chiến, tự nhiên đối phương tới chọn tỷ thí nội dung, mới có vẻ công bằng."),t("br"),t("br"),n._v("“Để ta tới định? Cái kia tốt!”"),t("br"),t("br"),n._v("Biết khiêu chiến quy củ, Trương Huyền nhàn nhạt nhìn qua: “Ta khiêu chiến sư ngôn thiên bẩm!”"),t("br"),t("br"),n._v("Tâm hắn cảnh khắc độ đã sớm đạt đến bát tinh danh sư tiêu chuẩn, lại thêm trời nhận danh sư tự nhiên ưu thế, khiêu chiến sư ngôn thiên bẩm lời nói, đừng nói cái tên này, không cách nào chống lại, ngay cả bình thường bát tinh danh sư, chỉ sợ đều ngăn cản không nổi."),t("br"),t("br"),n._v("Đã cái tên này tìm kích thích, cũng không có gì có thể xoắn xuýt... Nghiền ép chính là."),t("br"),t("br"),n._v("“Sư ngôn thiên bẩm?” Lông mày nhảy một cái."),t("br"),t("br"),n._v("Đối phương xông đường, làm cho cả Thanh Nguyên đế quốc Danh Sư đường tất cả danh sư, cam tâm bái sư... Hắn đã sớm nghe nói, tâm cảnh khắc độ tuyệt đối so với hắn chỉ mạnh không yếu."),t("br"),t("br"),n._v("Lựa chọn cái này lời nói, vô cùng có khả năng bị nghiền ép."),t("br"),t("br"),n._v("Bất quá... Hắn là Xuất Khiếu cảnh cường giả đỉnh phong, trải qua bốn lần lôi kiếp, nguyên thần mạnh mẽ, tâm cảnh trầm ổn, đối phương tu vi Thai Anh cảnh đỉnh phong... Chỉ cần sớm chuẩn bị, lựa chọn kĩ càng trong tỉ thí cho, có lẽ cơ hội chiến thắng rất lớn."),t("br"),t("br"),n._v("“Tốt, ta đáp ứng, có điều, nội dung cụ thể, ta tới định!”"),t("br"),t("br"),n._v("Sư ngôn thiên bẩm tỷ thí, có rất nhiều loại, đối phương xác định tỷ thí hạng mục, nội dung lại có hắn tới quyết định."),t("br"),t("br"),n._v("“Đương nhiên!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo, cũng không nhiều lời."),t("br"),t("br"),n._v("Quản cái gì nội dung, sư ngôn thiên bẩm... Hắn còn không có sợ qua ai."),t("br"),t("br"),n._v("“Cái kia tốt!”"),t("br"),t("br"),n._v("Tống Hiên nhìn về phía trước mắt Diêu sư, hai tay ôm quyền."),t("br"),t("br"),n._v("“Xin hỏi Diêu sư, có thể hay không mượn dùng một chút ngươi Thiên Âm bút?”"),t("br"),t("br"),n._v("“Thiên Âm bút?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Diêu Mạn Thiên lập tức sầm mặt lại: “Ngươi chẳng lẽ muốn khiêu chiến sư ngôn thiên bẩm bên trong khó khăn nhất... Sư chữ Thiên Âm?”"),t("br"),t("br"),n._v("(Cuối tháng, chuyên gia cũng nhìn thấy, lão nhai giống như Trương Huyền, miệng lưỡi vụng về, sẽ không nói cái gì cầu nguyệt phiếu lời nói, nếu như mà có, liền cho điểm đi. Đáng thương đáng thương một cái nghiêm túc đổi mới người thành thật đi! Ngày mai mở đại hội thể dục thể thao, bận bịu một ngày, yên tâm, đêm nay tăng ca đến nửa đêm, cũng sẽ không quịt canh.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1248"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1250"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);