(window.webpackJsonp=window.webpackJsonp||[]).push([[293],{647:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-116-tan-vo-bach-minh-đan-su-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-116-tan-vo-bach-minh-đan-su-thuong"}},[n._v("#")]),n._v(" Chương 116: Tan vỡ Bạch Minh đan sư (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Lợi hại!” "),t("br"),t("br"),n._v(" “Cũng là Bạch Minh đan sư mới có thực lực như vậy, như vậy trong hoàn cảnh đều có thể luyện chế Nhuận Huyệt đan thành công!” "),t("br"),t("br"),n._v(" “Nhuận Huyệt đan rất khó luyện chế, Bạch Minh đan sư thủ pháp càng là phức tạp, ta đều chưa từng thấy, ngược lại muốn xem xem, cái này Trương Huyền có thể nói ra cái gì khuyết điểm đến!” "),t("br"),t("br"),n._v(" “Khà khà, mấy người trò hay xem đi!” "),t("br"),t("br"),n._v(" Nhìn thấy Bạch Minh đan sư luyện chế ra Nhuận Huyệt đan, rất nhiều luyện đan sư tất cả đều nở nụ cười.?"),t("br"),t("br"),n._v(" Đan dược càng cao cấp hơn, thủ pháp luyện đan cũng là càng phức tạp, Nhuận Huyệt đan tại nhất phẩm đan dược bên trong đều được cho không kém, thủ pháp luyện chế càng là rườm rà không gì sánh được, liền ngay cả bọn họ những người này, đều không thể xác nhận thủ pháp bên trong sai lầm, một cái học đồ làm sao có khả năng nhìn ra?"),t("br"),t("br"),n._v(" “Được rồi, đan dược luyện thành, ngươi nói một chút thủ pháp tên gọi cùng khuyết điểm!” "),t("br"),t("br"),n._v(" Cười lạnh, Bạch Minh đan sư vững vàng một hồi hô hấp, bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v(" Ỷ vào không biết từ nơi nào đạt được nhược điểm cùng tin tức, đã nghĩ Luận đan thành công, nằm mơ đi!"),t("br"),t("br"),n._v(" Không có chân tài thực học, chính xác vạch ra luyện đan sai lầm, muốn cho bản thân khâm phục, môn đều không có!"),t("br"),t("br"),n._v(" “Ngươi xác định để ta nói?” Trương Huyền lần thứ hai vẻ mặt quái lạ."),t("br"),t("br"),n._v(" “Đừng ở chỗ này giả thần giả quỷ, không nói ra được, liền chịu thua!” Bạch Minh đan sư hừ lạnh."),t("br"),t("br"),n._v(" “Vậy cũng tốt!” "),t("br"),t("br"),n._v(" Trương Huyền lắc đầu một cái, hai tay chắp ở sau lưng, chậm rãi đi ở trong phòng gian."),t("br"),t("br"),n._v(" “Ngươi dùng thủ pháp luyện đan gọi , truyền thuyết là một vị gọi Phùng Tuyên nhị tinh luyện đan sư sáng tạo ra, đưa lên dược liệu, dựa theo đặc thù trình tự, ngọn núi giống nhau, một tầng liên tiếp một tầng, một làn sóng liên tiếp một làn sóng, khống chế hỏa diễm cũng đồng dạng y theo cái này trình tự, uy lực bỗng lớn bỗng nhỏ, như vậy luyện đan, có thể để cho dược hiệu đầy đủ dung hợp, rất dễ dàng thành đan! Nhưng thiếu hụt cũng hết sức rõ ràng, vậy thì là muốn cho đan dược cấp bậc tăng lên, vô cùng khó khăn!” "),t("br"),t("br"),n._v(" “Vì lẽ đó, ngươi này ba viên Nhuận Huyệt đan cứ việc thành công, cũng chỉ là cấp thấp nhất thành đan, muốn thành tựu no đủ, hầu như không thể. Loại này thủ pháp luyện đan, chỉ thích hợp trình độ thấp luyện đan sư lượng lớn sản xuất. Nói thật, hơi hơi khá một chút luyện đan sư, đều sẽ không học tập, bởi vì giống như ràng buộc tiến lên con đường, bất luận luyện tập bao nhiêu lần, cũng không thể đem phẩm chất đan dược tăng lên một phần một hào, học được cần gì dùng?” "),t("br"),t("br"),n._v(" “Trừ phi... Bản thân cảm thấy bản thân không cái gì tiềm lực có thể khai quật, lúc này mới tự bạo tự bỏ!” Trương Huyền khẽ mỉm cười, ý tứ sâu xa nhìn sang: “Bạch Minh đan sư, không biết ta nói có đúng hay không?” "),t("br"),t("br"),n._v(" “Hừ!” "),t("br"),t("br"),n._v(" Nghe được đối phương lời này, Bạch Minh cắn răng, sắc mặt lúc đỏ lúc trắng."),t("br"),t("br"),n._v(" Thân là Bạch gia người cầm lái, lại là nhất tinh luyện đan sư, địa vị tôn sùng, đến đối phương trong miệng, lại có thể xong rồi... Thấp trình độ luyện đan sư... Không có tiềm lực đào..."),t("br"),t("br"),n._v(" Bất quá, hắn không có cách nào phản bác."),t("br"),t("br"),n._v(" Thiên Phong Thủ khuyết điểm cùng đối phương nói giống như đúc, tuy rằng luyện chế Nhuận Huyệt đan dễ dàng, lại lại không cách nào tiến bộ."),t("br"),t("br"),n._v(" “Ngươi cũng không nên đắc ý, nói ra những thứ vô dụng này, còn muốn nói ra ta vừa nãy luyện đan bên trong sai lầm mới coi như đánh bạc!” Phun ra một hơi, Bạch Minh cố nén lửa giận."),t("br"),t("br"),n._v(" “Được rồi!” "),t("br"),t("br"),n._v(" Trương Huyền cũng không phí lời, đi về phía trước hai bước, đi tới lò luyện đan trước mặt: “Vậy ta trước hết theo trụ cột nhất bắt đầu!” "),t("br"),t("br"),n._v(" “Chỉ cần là luyện đan sư, đều biết muốn luyện chế ra tốt đan dược, nhất định phải đối với hỏa diễm, lò luyện đan có cực cường khống chế mới được, vừa nãy Bạch Minh đan sư, cầm lấy dược liệu liền bắt đầu luyện chế, đối với cái này lô đỉnh biết bao nhiêu? Lò lửa đây?” "),t("br"),n._v(" “Các ngươi không cần phản bác ta nói, những lò luyện đan này quy chế đều không khác mấy, không cần tính toán quá nhiều. Ta có thể rõ ràng nói cho các ngươi, tương tự dược liệu, tương tự lò lửa, tương tự lò luyện đan, luyện được đan dược lại không giống nhau, liền bởi vì có một chút khác biệt, thậm chí hơi bất cẩn một chút, liền có thể có thể làm cho luyện chế ra đến đan dược hạ thấp một đẳng cấp!” "),t("br"),t("br"),n._v(" Ngón tay nhẹ nhàng lướt qua lò luyện đan, Trương Huyền tiếp tục nói: “Không tin, ta hiện tại sẽ nói cho các ngươi.” "),t("br"),t("br"),n._v(" “Cái này lò luyện đan cao bảy thước ba tấc, rộng ba thước hai tấc, vì Thạch Tỉnh phường ba vạch thợ thủ công tại bảy năm ba tháng trước luyện chế, tổng cộng luyện qua 147 lần đan dược, trong đó nhất phẩm đan dược tám mươi tám lần, thấp hơn nhất phẩm viên thuốc năm mươi chín lần! Vận chuyển trong quá trình, chịu đựng qua ba lần va chạm, toàn trên đỉnh xuống tổng cộng mười ba nơi vết nhỏ, luyện chế phổ thông đan dược thời điểm, không ảnh hưởng toàn cục, bất quá, lại sẽ làm dược lực lẻ tẻ dật, nguyên khí tán loạn, do đó dẫn đến thành đan thời điểm, dược lực không đủ, đẳng cấp hạ thấp!” "),t("br"),t("br"),n._v(" “Đỉnh này độ dày một tấc có thừa, nhiệt lượng muốn hoàn toàn thẩm thấu, cần một nén hương lẻ ba mười bốn hô hấp. Hai bên trái phải nắp đỉnh độ dày hơi có sự khác biệt, cách biệt ba cái đầu tơ, chớ xem thường điểm ấy độ dày, tương tự hỏa diễm tình huống, hai bên nhiệt lượng sẽ có năm phần trăm chênh lệch, bởi vậy, chi phối vị trí thả làm thuốc tài, sẽ làm Nhuận Huyệt đan bên trong bồng hoa cỏ, độc *** dược hiệu hoàn toàn khác nhau!” "),t("br"),t("br"),n._v(" “Thật hay giả?” "),t("br"),t("br"),n._v(" “Một cái lô đỉnh đều có nhiều như vậy thuyết pháp?” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Nghe được bên trong Trương Huyền chậm rãi mà nói, phía dưới rất nhiều luyện đan sư đều miệng mở ra, sắp điên rồi."),t("br"),t("br"),n._v(" Bọn họ tuy rằng cũng biết lò luyện đan không giống, luyện chế ra đan dược cấp bậc cũng không giống nhau, có thể làm mộng đều không nghĩ tới, một cái lò luyện đan, có nhiều như vậy thuyết pháp."),t("br"),t("br"),n._v(" Hơn nữa..."),t("br"),t("br"),n._v(" Đây là luyện đan sư công hội vì lần này Luận đan mới vừa chuyển tiến vào lô đỉnh, hắn làm sao biết cặn kẽ như vậy? Thậm chí ở nơi nào luyện chế, lô đỉnh độ dày, to nhỏ, luyện qua bao nhiêu lần đan... Đều rõ ràng?"),t("br"),t("br"),n._v(" Mẹ nó, đây cũng quá nghịch thiên rồi đi!"),t("br"),t("br"),n._v(" Liếc mắt là đã nhìn ra những cái này, ngươi... Là quái vật sao?"),t("br"),t("br"),n._v(" “Nói xong lô đỉnh, ta hãy nói một chút lò lửa!” "),t("br"),t("br"),n._v(" Không để ý tới mọi người kinh ngạc, Trương Huyền chỉ tay lô dưới đỉnh mặt hỏa diễm: “Đây là  thiêu đốt mà ra, nóng nhất địa phương, có thể mang sắt thép đều hòa tan thành trấp, phối hợp với võ giả chân khí, có thể ung dung khống chế hỏa thế to nhỏ, do đó khống chế nhiệt độ cao thấp.” "),t("br"),t("br"),n._v(" “Bất quá... Hỏa diễm cùng dược liệu cũng không phải là trực tiếp tiếp xúc, chúng ta luyện chế thuốc, là tại lô bên trong đỉnh bộ thành đan, bên ngoài hỏa diễm xuyên thấu qua nắp đỉnh, còn có bao nhiêu lưu giữ tại lô bên trong đỉnh, mới là chúng ta nhất định phải nắm giữ.” "),t("br"),t("br"),n._v(" “Thiên Đô than đá, là luyện đan tốt nhất than đá, hỏa diễm nhiệt độ cao không nói, lực xuyên thấu cũng mạnh, phổ thông than đá, xuyên thấu qua khoảng một tấc nắp đỉnh, chỉ chừa có ba mươi bảy phần trăm nhiệt lượng, mà loại này than đá, năng lực bảo lưu bốn mươi sáu phần trăm! Chỉ có biết cái này quy luật, mới có thể xác nhận bên trong dược liệu, chịu đựng cao bao nhiêu nhiệt độ, bao lâu mới có thể dược hiệu tốt nhất dung hợp, thành tựu cao nhất phẩm chất đan dược!” "),t("br"),t("br"),n._v(" “Đương nhiên, những cái này chỉ là luyện đan sư nhất thứ căn bản, không tính là khuyết điểm, ta chỉ là thuận miệng giới thiệu một chút!” "),t("br"),t("br"),n._v(" Trương Huyền nở nụ cười, nhìn về phía một bên: “Bất quá, ta hay là muốn hỏi một chút Bạch Minh đan sư, luyện chế đan dược trước, những thứ này... Ngươi chú ý sao?” "),t("br"),t("br"),n._v(" Bạch Minh đan sư chỉ cảm thấy da đầu tê tê, suýt chút nữa không ngất đi."),t("br"),t("br"),n._v(" Chú ý, chú ý ngươi muội a!"),t("br"),t("br"),n._v(" Ta sẽ theo liền luyện cái đan, những thứ đồ này phức tạp như vậy, ta... Làm sao biết?"),t("br"),t("br"),n._v(" Hơn nữa, coi như muốn chú ý, cũng không có cách nào a, chẳng lẽ trước tìm thước đo đem lô đỉnh đo một lần, sau đó sẽ đem lò lửa nhiệt độ từng điểm kiểm tra?"),t("br"),t("br"),n._v(" Thật muốn như vậy, hai ba ngày xuống, còn luyện cái len sợi đan?"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-124"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-126"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);