(window.webpackJsonp=window.webpackJsonp||[]).push([[294],{649:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1241-su-tu-thien-am"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1241-su-tu-thien-am"}},[n._v("#")]),n._v(" Chương 1241: Sư tự thiên âm")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1241: Sư tự thiên âm"),t("br"),t("br"),n._v("“Không sai!” Ánh mắt phát lạnh, Tống Hiên hàm răng cắn chặt."),t("br"),t("br"),n._v("“Sư tự thiên âm?” Sửng sốt một chút, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Nhìn qua Thanh Nguyên đế quốc Danh Sư đường tất cả thư tịch, đối loại này tỷ thí, đã biết đến rất rõ ràng."),t("br"),t("br"),n._v("Bình thường sư ngôn thiên bẩm, là tìm số lượng nhất định tu luyện giả, hạn định thời gian bên trong, cấp cho giảng bài, dò xét độ thân thiện, càng cao, thành tích càng tốt."),t("br"),t("br"),n._v("Sư tự thiên âm, nguyên lý không kém nhiều, chỉ bất quá mượn một loại gọi Thiên Âm bút đạo cụ mà thôi!"),t("br"),t("br"),n._v("Thiên Âm bút, không phải là Thánh khí, cũng không phải Linh khí, mà là một loại hun đúc danh sư ý niệm đặc thù pháp bảo, có thể phân biệt danh sư ngôn ngữ, một khi phù hợp đại đạo áo nghĩa, hơn nữa sửa cũ thành mới, liền sẽ tự động đem nói thuật ảo diệu, toàn bộ ghi chép lại."),t("br"),t("br"),n._v("Cái khác tu luyện giả, quan sát nó ghi chép lại văn tự, đồng dạng có thể đưa đến tự mình lắng nghe sư ngôn thiên bẩm hiệu quả, từ đó tăng tốc tu luyện."),t("br"),t("br"),n._v("Đương nhiên, loại này quan sát, một lần qua đi, chữ viết liền sẽ hoàn toàn biến mất."),t("br"),t("br"),n._v("Sở dĩ, là sư ngôn thiên bẩm bên trong, khó khăn nhất tỷ thí, có hai cái nguyên nhân."),t("br"),t("br"),n._v("Thứ nhất, Thiên Âm bút, hun đúc danh sư ý niệm, bình thường sư ngôn thiên bẩm, đối với nó tới nói, đã nhìn lắm thành quen, muốn để nó động, truyền thụ kiến thức, tất nhiên muốn suy nghĩ khác người, không giống bình thường."),t("br"),t("br"),n._v("Thứ hai, đối tâm cảnh tiêu hao rất nhiều, vượt xa quá cho danh sư giảng bài, dưới tình huống bình thường, để hắn viết một chữ, liền có thể tiêu hao danh sư một cái tâm cảnh khắc độ."),t("br"),t("br"),n._v("Bởi vậy, đơn hai điểm này, liền không biết làm khó bao nhiêu danh sư, rất nhiều thất tinh đỉnh phong danh sư, một bài giảng qua đi, Thiên Âm bút không động chút nào, thậm chí chuẩn bát tinh danh sư, mười cái chữ, cũng là cực hạn, hầu như không có người có thể phá vỡ."),t("br"),t("br"),n._v("“Sư tự thiên âm, mỗi tiến hành một lần, đối tâm cảnh đã có rất lớn tổn hại, ngươi khẳng định muốn tỷ thí cái này?”"),t("br"),t("br"),n._v("Nghe được tỷ thí lại là cái này, Diêu Mạn Thiên vẻ mặt khó coi để Thiên Âm bút tự động viết, đối tâm cảnh tiêu hao rất nhiều, hơi không cẩn thận, liền có thể dẫn đến tâm cảnh sụp đổ, nhiều năm tu vi hủy hoại chỉ trong chốc lát, bởi vậy, loại này tỷ thí, không chỉ khó, càng thêm nguy hiểm."),t("br"),t("br"),n._v("“Vâng!” Tống Hiên ánh mắt kiên định."),t("br"),t("br"),n._v("“Cái này...” Dừng lại một chút, Diêu Mạn Thiên nhìn về phía Trương Huyền: “Trương sư, ý kiến của ngươi làm sao? Nếu như cảm thấy quá khó, hoàn toàn có thể cự tuyệt!”"),t("br"),t("br"),n._v("Nói hết, lặng lẽ truyền âm."),t("br"),t("br"),n._v("“Tống Hiên chuyên môn học qua sư tự thiên âm, từng sáng chế ‘Tám chữ’ thành tích tốt, ngươi chưa hề tiếp xúc qua Thiên Âm bút, thực sự không được, thẳng cự tuyệt, đổi một cái tỷ thí phương pháp!”"),t("br"),t("br"),n._v("Để Thiên Âm bút động, chưa hề thử qua, không biết quy củ, hầu như không có người có thể thành công."),t("br"),t("br"),n._v("Tống Hiên, dám khiêu chiến, là bởi vì Tiềm Xung đế quốc, nắm giữ Thiên Âm bút, hơn nữa còn sáng chế qua “Tám chữ” thành tích tốt."),t("br"),t("br"),n._v("Mười chữ, là chuẩn bát tinh danh sư cực hạn, có thể đạt tới tám chữ, cho dù tại Tiềm Xung đế quốc, cũng tuyệt đối số một số hai."),t("br"),t("br"),n._v("Có thể nói, khiêu chiến cái này, đối với hắn mười phần có lợi, mà đối vị này Trương sư tới nói, độ khó liền lớn."),t("br"),t("br"),n._v("“Không cần, liền cái này đi!”"),t("br"),t("br"),n._v("Biết đối phương là hảo ý, nhẹ gật đầu, ngỏ ý cảm ơn, Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Sư tự thiên âm mà thôi, mặc dù không tiếp xúc qua, cũng không tính quá khó."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Gặp hắn đáp ứng, một mặt không quan tâm biểu lộ, Diêu Mạn Thiên lắc đầu, cổ tay khẽ đảo, một mực kim sắc bút lông xuất hiện ở trước mặt mọi người, an tĩnh lơ lửng giữa không trung."),t("br"),t("br"),n._v("Bút dài một thước có dư, mặt ngoài không mực, thần thức đảo qua, cho người ta một loại danh sư đặc thù khí tức, sinh ra nhàn nhạt cảm giác áp bách."),t("br"),t("br"),n._v("“Đây là Thiên Âm bút, các ngươi ai tới trước?”"),t("br"),t("br"),n._v("Ngón tay một điểm, bút lông an tĩnh lơ lửng tại trong hai người ở giữa, không nhúc nhích."),t("br"),t("br"),n._v("“Đã trong tỉ thí cho là Tống sư nói ra, liền do hắn bắt đầu đi!”"),t("br"),t("br"),n._v("Trương Huyền một mặt khiêm nhượng."),t("br"),t("br"),n._v("Sư tự thiên âm tỷ thí phương pháp, hắn nhìn qua ghi chép, nhưng chưa hề tự mình thử qua, cụ thể quy tắc, cũng không rõ ràng, làm cho đối phương tới trước, cũng tốt quan sát một chút, trước thời hạn có cái phán đoán."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Cũng không cự tuyệt, Tống Hiên hai bước đi tới trước mặt, lông mày giương lên, miệng bắt đầu chuyển động."),t("br"),t("br"),n._v("Không có phát ra âm thanh, mà là chân khí truyền âm."),t("br"),t("br"),n._v("“Sử dụng truyền âm, sư ngôn thiên bẩm có thể càng thêm đánh trúng, không cần quá mức lãng phí... Lại càng dễ để Thiên Âm bút động...”"),t("br"),t("br"),n._v("Sợ Trương Huyền không hiểu, Diêu Mạn Thiên lặng lẽ truyền âm."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cho một người giảng bài, cùng cho một đám người giảng bài, là hai loại khái niệm."),t("br"),t("br"),n._v("ngantruyen.com"),t("br"),n._v("Cùng khóa ngoại phụ đạo đồng dạng, lão sư một chọi một, phụ đạo hiệu quả liền sẽ càng tốt hơn, một đối nhiều, không cách nào chăm sóc, hiệu quả còn kém."),t("br"),t("br"),n._v("Truyền âm qua, có tính nhắm vào, tránh khỏi lực lượng phân tán, uy lực càng lớn."),t("br"),t("br"),n._v("Miệng lúc mở lúc đóng, Tống Hiên không ngừng nói chuyện, rất nhanh, trên đầu toát ra mồ hôi."),t("br"),t("br"),n._v("Bất quá, trước mắt bút lông, lại không nhúc nhích, hình như đóng ở trên không đồng dạng."),t("br"),t("br"),n._v("Trương Huyền thần thức lan tràn, cảm giác vị này Tống sư nương theo giảng bài, tâm cảnh khắc độ nhanh chóng hạ thấp."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Không biết qua bao lâu, một mực yên lặng không hiểu bút lông, đột nhiên lắc lư một cái, nhẹ nhàng xoay một cái, một cái chữ đại xuất hiện trên không trung."),t("br"),t("br"),n._v("Hư không lâm thư!"),t("br"),t("br"),n._v("Không phải là quen thuộc văn tự, cũng không phải Dị Linh tộc cùng Thú tộc, mà là một loại chưa từng thấy qua kiểu chữ, mặc dù không nhận ra là cái gì, nhưng chỉ nhìn lên một cái, dường như liền có thể rõ ràng muốn biểu đạt ý tứ, chân khí trong cơ thể sẽ tùy theo nhảy lên, dựa theo đối phương phương thức tu luyện."),t("br"),t("br"),n._v("“Thần thức đảo qua đi, liền có thể học tập, có điều, chỉ cần quét một lần, chữ viết liền biến mất...”"),t("br"),t("br"),n._v("Diêu Mạn Thiên tiếp lấy truyền âm."),t("br"),t("br"),n._v("Sư tự thiên âm, là sư ngôn thiên bẩm một loại, có thể khiến người ta tự động vận chuyển chân khí, chỉ cần thần thức tiếp xúc, liền có thể không bị khống chế tu luyện, chỉ là... Thứ này là duy nhất một lần, bất kể công pháp bao nhiêu thần kỳ, cũng chỉ có thể cho một người xem một lần."),t("br"),n._v("Có thể nói, thứ này liền là dự trữ lên sư ngôn thiên bẩm, có thể tùy thân mang theo, thậm chí đưa tặng."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mười phần trân quý, trên cơ bản là có tiền mà không mua được, mua cũng mua không được."),t("br"),t("br"),n._v("Xuất hiện chữ thứ nhất, Tống Hiên mồ hôi lạnh trên đầu càng ngày càng nhiều, ngay sau đó xuất hiện cái thứ hai, cái thứ ba, cái thứ tư..."),t("br"),t("br"),n._v("Nương theo số lượng từ càng ngày càng nhiều, vẻ mặt liền càng ngày càng trắng xám, tựa hồ đối với tâm cảnh hao tổn, đã xuất hiện tiêu hao."),t("br"),t("br"),n._v("Chuẩn bát tinh danh sư tâm cảnh tại 23 trở lên, nhiều như vậy khắc độ, không có khả năng một lần tiêu hao sạch sẽ, thật làm như vậy, sẽ dẫn đến tâm linh sụp đổ, sinh ra vô số tâm ma, lại khó khống chế."),t("br"),t("br"),n._v("Tình huống bình thường, đạt tới 15 trở xuống, sẽ xuất hiện nguy hiểm."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn vận chuyển, Trương Huyền cẩn thận quan sát, nương theo kiểu chữ càng ngày càng nhiều, trước mắt Tống Hiên thân thể lay động đến càng kịch liệt, nguyên thần tựa hồ cũng có xu thế sụp đổ."),t("br"),t("br"),n._v("May mắn là vượt qua bốn lần lôi kiếp, hồn phách mạnh mẽ, nếu không chỉ sợ sớm đã không kiên trì nổi."),t("br"),t("br"),n._v("“Sáu, bảy, tám... Đã tám chữ, đạt đến lúc trước hắn sáng tạo ra ghi chép...”"),t("br"),t("br"),n._v("Mười phút đồng hồ qua đi, trên không đã xuất hiện tám chữ thể, an tĩnh lơ lửng, lóe ra đặc thù chói lọi."),t("br"),t("br"),n._v("“Tám chữ” là Tống Hiên thành tích tốt nhất, có thể làm được điểm ấy đã đến cực hạn, cả người không ngừng lay động, bất cứ lúc nào cũng sẽ ngã sấp xuống, vẻ mặt cũng biến thành giống như giấy trắng, không có một chút huyết sắc."),t("br"),t("br"),n._v("“Tiếp tục nữa có thể sẽ... Đối thân thể có tổn thương cực lớn.”"),t("br"),t("br"),n._v("Nhìn hắn vẫn không có dừng lại ý tứ, tiếp tục đối với bút lông truyền âm, Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Bất kể là sư ngôn thiên bẩm còn là tu luyện loại hình, đều muốn lượng sức mà đi, vượt qua thân thể cực hạn, liền sẽ tạo thành không được vãn hồi thương thế."),t("br"),t("br"),n._v("Hắn là thất tinh đỉnh phong y sư, đối thân thể hiểu rất nhiều, như vậy tiếp tục kiên trì, có lẽ có thể có thể vượt qua cực hạn, đạt tới trước kia làm không được thành tích."),t("br"),t("br"),n._v("Nhưng đối với thân thể tổn thương nhưng cũng là mãi mãi, nhất là trên tâm cảnh, rất có thể dẫn đến về sau lại không cách nào tiến bộ."),t("br"),t("br"),n._v("Đối với danh sư tới nói, tâm cảnh không cách nào tiến bộ, chẳng khác nào cả đời, dừng bước tại chuẩn bát tinh cấp, đừng có lại không cách nào đặt chân cảnh giới cao hơn."),t("br"),t("br"),n._v("Hô! Hô! Hô!"),t("br"),t("br"),n._v("Vẻ mặt cứ việc thay đổi khó coi, nhưng Tống Hiên vẫn không có dừng lại ý tứ, đột nhiên một ngụm máu tươi phun ra, cả người tựa như trở nên già nua mấy phần."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Bút lông trên không trung lắc lư một cái, viết ra một chữ cuối cùng."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Làm xong những này, Tống Hiên cũng rất giống là dùng hết tất cả khí lực, thẳng tắp ngã trên mặt đất, mắt tối sầm lại, ngất đi."),t("br"),t("br"),n._v("“Ai, tội gì khổ như thế chứ? Cần gì chứ?”"),t("br"),t("br"),n._v("Thấy đối phương vì chiến thắng, tiêu hao tất cả lực lượng, tinh thần không chịu nổi, rơi vào hôn mê, Trương Huyền cười khổ lắc đầu."),t("br"),t("br"),n._v("Trả giá như thế đại đánh đổi, chỉ vì giận dỗi... Cái tên này thật là cố chấp."),t("br"),t("br"),n._v("Mấy bước đi tới trước mặt, ngón tay tại đối phương huyệt vị bên trên nắm hai lần, một đạo chân khí lặng lẽ lan tràn ra ngoài, Tống Hiên lúc này mới chậm rãi mở mắt."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, giãy dụa lấy đứng dậy, Tống Hiên khuôn mặt tái mét: “Không cần lấy lòng, ta sẽ không cảm kích...”"),t("br"),t("br"),n._v("“Không cần ngươi cảm kích!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Cứu đối phương chỉ là thân là danh sư đạo nghĩa vị trí, có biết không mời, đối với hắn mà nói không có cái gì ý nghĩa."),t("br"),t("br"),n._v("Tỉnh táo lại, nuốt vào một viên đan dược, Tống Hiên vẻ mặt khôi phục một chút, lúc này mới nhìn về phía cách đó không xa Diêu Mạn Thiên: “Diêu sư, ta đã hoàn thành sư tự thiên âm, mong rằng chứng kiến!”"),t("br"),t("br"),n._v("“Ngươi hết thảy viết ra chín chữ, mặc dù không có phá chuẩn bát tinh danh sư mười chữ cao nhất ghi chép, nhưng cũng trăm năm hiếm thấy!”"),t("br"),t("br"),n._v("Diêu Mạn Thiên gật đầu."),t("br"),t("br"),n._v("Chữ Cửu thành tích, tại bát tinh danh sư bên trong không tính là gì, nhưng ở chuẩn bát tinh danh sư bên trong, tuyệt đối là đứng đầu nhất tồn tại, cho dù Tiềm Xung đế quốc vị đường chủ kia, khả năng cũng có thiếu sót."),t("br"),t("br"),n._v("“Đa tạ Diêu sư làm chứng!”"),t("br"),t("br"),n._v("Thấy đối phương thừa nhận thành tích, Tống Hiên tròng mắt hơi híp, nhìn về phía cách đó không xa Trương Huyền: “Còn xin Trương sư bắt đầu đi!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Vừa rồi đối phương thi triển sư tự thiên âm, toàn bộ nhìn ở trong mắt, đối cái này sư ngôn thiên bẩm khảo hạch phương pháp, đã nắm giữ tại ngực."),t("br"),t("br"),n._v("Rất mau tới đến lơ lửng Thiên Âm bút trước mặt, Trương Huyền hai tay chắp sau lưng, đồng dạng truyền âm qua, miệng há ra hợp lại, ai cũng không biết nói rồi thứ gì."),t("br"),t("br"),n._v("Bút lông vốn đang trên không trung lay động, nương theo hắn truyền âm, cứng ngắc tại nguyên chỗ, hình như đóng ở phía trên đồng dạng."),t("br"),t("br"),n._v("“Cái này đã gần nửa canh giờ, tại sao không có một điểm động tĩnh?”"),t("br"),t("br"),n._v("“Đúng vậy a, sẽ không phải Trương sư nói, không hề có một chữ đạt được công nhận a?”"),t("br"),t("br"),n._v("“Như vậy, thật sự gặp...”"),t("br"),t("br"),n._v("Chờ gần nửa canh giờ, trên không bút lông một điểm động tĩnh đều không có, tất cả mọi người đã hai mặt nhìn nhau."),t("br"),t("br"),n._v("Vừa rồi Tống sư thời gian dài như vậy, đều không khác mấy viết ra bảy, tám chữ."),t("br"),t("br"),n._v("Làm sao Trương sư hiện tại một điểm động tĩnh đều không có?"),t("br"),t("br"),n._v("Chẳng lẽ... Thất bại?"),t("br"),t("br"),n._v("(Cảm ơn thư hữu 【, minh tịch 】 mười vạn khen thưởng, liên tục ba ngày đại hội thể dục thể thao, số một lão nhai đi Thượng Hải tham gia nhà văn rồng cát, số mười mới có thể trở về, tăng thêm tạm thời là không thể nào. Trở về bù đắp đi. Ai, tranh thủ không đứt chương! Không đứt chương, chuyên gia muốn cho nguyệt phiếu ah! Hôm nay hai chương là rạng sáng bốn giờ nửa rời giường gõ, ban ngày còn muốn phơi một ngày, ô ô ô ô ô!)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1249"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1251"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);