(window.webpackJsonp=window.webpackJsonp||[]).push([[301],{656:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1248-hai-vi-du-thinh-sinh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1248-hai-vi-du-thinh-sinh"}},[n._v("#")]),n._v(" Chương 1248: Hai vị dự thính sinh")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1248: Hai vị dự thính sinh"),i("br"),i("br"),n._v("Tại một vị danh sư dẫn đầu bên dưới, rất mau tới đến một ngôi đại điện."),i("br"),i("br"),n._v("Trong phòng, Ngô đường chủ, Diêu Mạn Thiên bọn người tại, quý vị khách quan bên trên, ngồi một vị cẩm y trung niên nhân, phía sau hắn, thì đứng hai vị thanh niên."),i("br"),i("br"),n._v("Lặng lẽ nhìn sang, trung niên nhân khí tức núp ở trong cơ thể, như là lôi đình, sâu không lường được, nhìn không ra thực lực cụ thể."),i("br"),i("br"),n._v("Hai vị thanh niên, nguyên thần chiếm cứ trong cơ thể, lực lượng như biển, vậy mà tất cả đều là Nguyên Thần cảnh trung kỳ cao thủ."),i("br"),i("br"),n._v("Nhìn bọn họ tuổi tác, hẳn là cũng không cao hơn ba mươi tuổi, trẻ tuổi như vậy liền có loại thực lực này, chỉ có thể dùng “Đáng sợ” hai chữ hình dáng, so với Trương Cửu Tiêu, chỉ sợ đều chỉ mạnh không yếu."),i("br"),i("br"),n._v("“Trương sư!”"),i("br"),i("br"),n._v("Gặp hắn tiến đến, Ngô đường chủ đứng dậy, cười giới thiệu: “Vị này là Thánh tử điện tiếp dẫn sứ giả, Triệu Hưng Mặc, Triệu sư! Triệu sư, vị này chính là ta cùng ngươi đã nói Trương Huyền Trương sư, thiên phú vô song, không chỉ Thanh Nguyên đế quốc, ít có thứ nhất, bát đại phong hào đế quốc, chỉ sợ đều có một chỗ cắm dùi!”"),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Nhìn một cái, Triệu Hưng Mặc nhẹ gật đầu, trên mặt không có bất kỳ cái gì biểu lộ."),i("br"),i("br"),n._v("Dường như như vậy giới thiệu, sớm đã nghe quen, không có nửa điểm gợn sóng."),i("br"),i("br"),n._v("Thấy đối phương đối với hắn, không có hứng thú quá lớn, Trương Huyền cũng lười chú ý, hướng bốn phía nhìn lại."),i("br"),i("br"),n._v("Trong phòng không chỉ có mấy vị này, Trương Cửu Tiêu cùng Thanh Nguyên Danh Sư đường hơn mười vị tuổi trẻ thiên tài cũng tại."),i("br"),i("br"),n._v("“Đã người đến đông đủ, vậy thì bắt đầu đi!”"),i("br"),i("br"),n._v("Chờ một hồi, lại có hai người đi tới, Triệu Hưng Mặc tựa hồ tại không có thời gian, nhíu nhíu mày mở miệng."),i("br"),i("br"),n._v("Lên tiếng, Ngô đường chủ nhìn quanh một vòng: “Chư vị, vị này Triệu sư, chắc hẳn không cần cùng chuyên gia làm sao giới thiệu, là lần này tiếp dẫn sứ giả, phụ trách Thánh tử điện tuyển chọn! Phía dưới mời hắn cho chúng ta nói một chút, làm sao khảo hạch, cần chuẩn bị cái gì.”"),i("br"),i("br"),n._v("Đám người đồng loạt nhìn lại."),i("br"),i("br"),n._v("“Không cần chuẩn bị, ta nói thẳng ra yêu cầu, không hợp cách, không cần lãng phí thời gian!”"),i("br"),i("br"),n._v("Mí mắt vừa nhấc, Triệu Hưng Mặc khoát tay áo, mặt không hề cảm xúc: “Có thể tới khảo hạch, chắc hẳn đối Thánh tử điện đã có hiểu biết, muốn tiến vào trong đó, cái thứ nhất cứng nhắc yêu cầu, liền là ba mươi tuổi thất tinh danh sư, tu vi đạt tới Thánh vực tam trọng Thai Anh cảnh, vô cùng trở lên... Ở đây chư vị, thoạt nhìn đều phù hợp, bất quá... Lấy ta đến xem, tu vi càng cao càng tốt. Hiện nay, đạt tới Thánh vực tứ trọng, có hai vị, bọn họ lưu lại, những người khác có thể đi về...”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("“Vậy thì tuyển chọn xong?”"),i("br"),i("br"),n._v("“Có phải hay không quá trò đùa?”"),i("br"),i("br"),n._v("Không nghĩ tới nhìn tu vi, trực tiếp đào thải còn lại hai cái, tất cả mọi người hai mặt nhìn nhau."),i("br"),i("br"),n._v("“Thánh tử điện, không phải danh sư học viện, chỉ cần phù hợp đều sẽ thu, là toàn bộ đại lục cao nhất học phủ, chuyên môn bồi dưỡng Thánh Nhân tồn tại, thực lực không đủ, cũng đừng có ở đây lãng phí thời gian!”"),i("br"),i("br"),n._v("Triệu Hưng Mặc lông mày nhíu một cái: “Ta không có nhiều như vậy không cùng các ngươi chậm trễ!”"),i("br"),i("br"),n._v("“Ây...”"),i("br"),i("br"),n._v("Không nghĩ tới hắn trực tiếp như vậy, Ngô đường chủ có chút xấu hổ: “Lấy tu vi trực tiếp quyết định, có phải hay không quá võ đoán? Triệu sư, có muốn hay không nhìn nhìn lại? Có nhiều người danh sư, tu vi khả năng không mạnh, nhưng phương diện khác không kém.”"),i("br"),i("br"),n._v("“Không cần, ta chỉ là sơ tuyển, đến Tiềm Xung đế quốc, còn cần lại tiến hành một lần lại tuyển, vào Thánh tử thành, càng phải tiến hành cuối cùng khảo hạch, tu vi liền Nguyên Thần cảnh đều không đạt tới, lại tuyển đều qua không được, mang theo cũng là lãng phí thời gian!”"),i("br"),i("br"),n._v("Tùy ý khoát tay áo, Triệu Hưng Mặc nói."),i("br"),i("br"),n._v("“Thôi được...”"),i("br"),i("br"),n._v("Thấy đối phương nói như vậy, Ngô đường chủ đành phải cười khổ lắc đầu, nhìn về phía còn lại đám người: “Chư vị cũng không muốn nhụt chí, thật tốt tu luyện, ở nơi nào, đều sẽ có rất lớn tiến bộ, chưa hẳn nhất định muốn đi Thánh tử điện!”"),i("br"),i("br"),n._v("“Đúng...”"),i("br"),i("br"),n._v("Biết đường chủ lời này mang theo an ủi, hơn mười vị thiên tài, đành phải lắc đầu, xoay người rời khỏi."),i("br"),i("br"),n._v("Trên thực tế, bọn họ cũng có tự mình biết mình, tu vi cùng thiên phú cứ việc không tính quá yếu, có thể cùng Trương Huyền, Trương Cửu Tiêu hai người so, vẫn là kém có chút xa."),i("br"),i("br"),n._v("Thanh Nguyên thành, một cái danh ngạch lời nói, khẳng định tuyển không lên bọn họ."),i("br"),i("br"),n._v("Thấy mọi người rời khỏi, chỉ còn lại có Trương Huyền cùng Trương Cửu Tiêu, Triệu Hưng Mặc ánh mắt tập trung tới."),i("br"),i("br"),n._v("“Thánh tử điện, tuyển chọn đều là nhân tộc đỉnh phong nhất tinh anh, về sau, thống lĩnh một phương, trở thành một phương hào hùng... Muốn người khác thần phục, nói đến nhiều hơn nữa đều không dùng, thực lực mới là trọng yếu nhất! Bởi vậy, thực lực là lớn nhất phán xét tiêu chuẩn.”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Điểm ấy tràn đầy cảm xúc, mưu kế, ngụy trang loại hình cứ việc có khả năng trong thời gian ngắn để cho người ta chấn nhiếp, chân chính làm cho người tin phục, vẫn là thực lực bản thân!"),i("br"),i("br"),n._v("Thật giống như hắn hiện tại, tại Thanh Nguyên đế quốc, không người có thể địch, có này dựa dẫm, cái gì đều không e ngại."),i("br"),i("br"),n._v("Chỉ dựa vào ngụy trang lời nói, lực lượng liền sẽ yếu bớt, sớm muộn gì đều sẽ bị đâm thủng."),i("br"),i("br"),n._v("Bởi vậy, một phương hào hùng, chưa bao giờ là dựa vào mưu kế, mà là đường đường chính chính nắm đấm."),i("br"),i("br"),n._v("Không phục... Nghiền ép chính là, thẳng đánh ngươi phục mới thôi!"),i("br"),i("br"),n._v("Danh Sư đường, sở dĩ canh giữ thiên hạ, lấy lý phục người, làm việc công chính, chỉ là một trong số đó, càng quan trọng hơn là, nắm giữ những nghề nghiệp khác, không dám phản bác tuyệt đối sức chiến đấu."),i("br"),i("br"),n._v("Đã từng có hai cái nhảy nhót chức nghiệp... Vu hồn sư cùng độc sư."),i("br"),i("br"),n._v("Cái trước trực tiếp diệt đi... Đến bây giờ truyền thừa cắt đứt, Quỷ Ảnh cũng không tìm tới; Người sau cũng bị chạy tới góc, không dám lấy thân phận chân thật gặp người."),i("br"),i("br"),n._v("Đây không phải bá đạo, mà là uy quyền."),i("br"),i("br"),n._v("Đạo lý giống vậy, Thánh vực quý tộc để cho người ta sợ hãi, cũng không phải là gia tộc bọn họ trước đó từng ra Thánh Nhân, mà là... Đến bây giờ, đều có để cho người ta kính sợ nội tình cùng lực lượng."),i("br"),i("br"),n._v("Có nắm đấm, mới có nhân từ, lực lượng không đủ, nói đến nhiều hơn nữa, đều là nói nhảm."),i("br"),i("br"),n._v("Trương Cửu Tiêu cũng lên tiếng, tràn đầy cảm xúc."),i("br"),n._v(" Hắn mặc dù là bàng chi, nếu như bây giờ tu vi đã đạt tới Thánh vực lục trọng, thất trọng, gia tộc đồng dạng sẽ chú trọng, không đến mức lưu lạc đến một cái nho nhỏ phong hào đế quốc, tranh đoạt một cái, rất khó chiếm được danh ngạch."),i("br"),i("br"),n._v("Thấy hai người rõ ràng ý tứ, Triệu Hưng Mặc gật gật đầu, chỉ chỉ phía sau mình hai cái thanh niên."),i("br"),i("br"),n._v("“Hai vị này, là Thánh tử điện dự thính học sinh, cứ việc không đột xuất, nhưng cũng không yếu, hơn nữa phối hợp lẫn nhau, có khả năng hình thành đặc thù hợp kích trận pháp. Trong các ngươi, ai có thể tại bọn họ liên hợp bên dưới, chống nổi ba chiêu, coi như thông qua!”"),i("br"),i("br"),n._v("Rầm!"),i("br"),i("br"),n._v("Nương theo lời của hắn, hai cái thanh niên đi ra, trên người lực lượng thả ra ngoài, toàn bộ đại điện trong nháy mắt, lạnh lẽo đi, để cho người ta cảm nhận được nồng đậm áp bức."),i("br"),i("br"),n._v("“Thật mạnh...”"),i("br"),i("br"),n._v("Mặc dù không có giao thủ, nhưng bằng cho mượn khí thế, liền có thể cảm ứng ra đến, hai người này cứ việc giống như hắn, chỉ là Nguyên Thần cảnh trung kỳ, lại đều có Nguyên Thần cảnh đỉnh phong sức chiến đấu, liên thủ hình thành trận pháp, chỉ sợ bình thường Xuất Khiếu cảnh sơ kỳ cường giả, đều có thể một trận chiến."),i("br"),i("br"),n._v("Nói cách khác, hai người liên thủ, Ngô đường chủ loại này vượt qua lôi kiếp Xuất Khiếu cảnh cường giả, cũng chưa chắc có khả năng chiến thắng."),i("br"),i("br"),n._v("Cái này... Chỉ là dự thính?"),i("br"),i("br"),n._v("Cái kia Thánh tử điện chân chính học sinh, cái kia mạnh bao nhiêu?"),i("br"),i("br"),n._v("“Các ngươi ai tới trước?”"),i("br"),i("br"),n._v("Giới thiệu xong, Triệu Hưng Mặc nhìn qua."),i("br"),i("br"),n._v("Trương Cửu Tiêu nhìn kỹ hướng trước mắt hai người, nghiên cứu nửa ngày, cười khổ lắc đầu: “Trương sư, ta không biết ngươi cũng ý định đi vào Thánh tử điện, đã ngươi muốn đi, ta... Vẫn là bỏ quyền đi!”"),i("br"),i("br"),n._v("“Bỏ quyền?”"),i("br"),i("br"),n._v("Trương Huyền sững sờ."),i("br"),i("br"),n._v("Còn muốn lấy, có một hồi tranh đấu, không nghĩ tới vị này Trương gia thiên tài, thế mà trực tiếp nhận thua."),i("br"),i("br"),n._v("“Đúng vậy a, cùng hắn tự rước lấy nhục, còn không bằng chủ động rời khỏi...”"),i("br"),i("br"),n._v("Trương Cửu Tiêu bất đắc dĩ."),i("br"),i("br"),n._v("Theo gia tộc tới đây, tranh thủ duy nhất danh ngạch, đương nhiên đối Thánh tử điện tràn đầy khát vọng, vốn, khẳng định có thể thành công, ai biết... Tới vị này Trương Huyền, bất kể từ chỗ nào một mặt, đều đả kích hắn không có lòng tin."),i("br"),i("br"),n._v("Trước đó, tự nhận có thiên phú, còn cảm thấy có lực đánh một trận, kể từ khi biết đối phương là trời nhận danh sư về sau, liền không dám tiếp tục suy nghĩ nhiều."),i("br"),i("br"),n._v("Loại người này, nhất định bị ghi chép tại sử sách bên trên, căn bản cũng không phải là hắn, có thể chống lại."),i("br"),i("br"),n._v("Huống chi, vừa mới đã tính toán một chút, lấy thực lực của hắn bây giờ, muốn tại trong tay đối phương chống nổi ba chiêu khả năng không lớn."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Gặp hắn biểu lộ, Trương Huyền chần chờ một chút, ngẩng đầu nhìn về phía trước mắt Triệu Hưng Mặc: “Triệu sư, ngươi mới vừa nói, ai có thể tại bọn họ liên hợp bên dưới, nhận lấy ba chiêu, liền có thể thông qua khảo hạch đúng không?”"),i("br"),i("br"),n._v("“Không sai!”"),i("br"),i("br"),n._v("Triệu Hưng Mặc gật đầu."),i("br"),i("br"),n._v("“Vậy thì tốt, nếu như chúng ta hai người, đều có thể nhận lấy, có phải hay không, Thanh Nguyên đế quốc, có thể có hai cái đi vào Thánh tử điện danh ngạch?” Trương Huyền nói tiếp."),i("br"),i("br"),n._v("“Đều có thể nhận lấy?” Cười nhạo một tiếng, Triệu Hưng Mặc mặt không hề cảm xúc: “Cũng có khả năng, một cái đều không có!”"),i("br"),i("br"),n._v("“Cái kia không nhất định!” Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Trước đó, bởi vì Lạc Nhược Hi nguyên nhân, đối Trương gia tràn đầy thành kiến, ở chung được một đoạn thời gian, phát hiện vị này Trương Cửu Tiêu, cũng liền tâm cao khí ngạo chút, đồng thời không có như vậy kinh khủng."),i("br"),i("br"),n._v("Đi Thánh tử điện danh ngạch, vốn là đối phương, vì thế, ly biệt quê hương, tại Thanh Nguyên thành đợi nhiều năm, bản thân trực tiếp cướp đi, thực sự băn khoăn... Nếu là có thể hai người đều đi, liền tất cả đều vui vẻ."),i("br"),i("br"),n._v("“Nếu như hai người các ngươi đều có thể chống nổi ba chiêu, ta sẽ cân nhắc... Bất quá, không được đánh giá cao bản thân, hai người này ba chiêu, không phải dễ dàng như vậy vượt qua!”"),i("br"),i("br"),n._v("Gặp vị này Trương Huyền một mặt tự tin, Triệu Hưng Mặc nhắc nhở một câu."),i("br"),i("br"),n._v("Chỉ là cân nhắc mà thôi, hắn cũng không tin tưởng, một cái nho nhỏ phong hào đế quốc danh sư, đều có thể kiên trì ba chiêu."),i("br"),i("br"),n._v("“Chỉ cần cân nhắc liền tốt, có thể hay không kiên trì ba chiêu, liền xem chúng ta thực lực của mình...” Trương Huyền cười cười."),i("br"),i("br"),n._v("“Có tự tin là chuyện tốt, chỉ mong không phải tự đại!”"),i("br"),i("br"),n._v("Nhíu nhíu mày, Triệu Hưng Mặc nói: “Được rồi, các ngươi đến cùng ai tới trước?”"),i("br"),i("br"),n._v("“Ta tới đi!”"),i("br"),i("br"),n._v("Đi tới trước mặt, Trương Huyền thân thể nhoáng một cái, phóng ra thực lực, chỉ có Nguyên Thần cảnh trung kỳ, tại hai cái thanh niên trước mặt, thoạt nhìn không có ý nghĩa."),i("br"),i("br"),n._v("“Hơi thở không nhỏ, để chúng ta huynh đệ nhìn một chút, có thể có dạng gì thực lực!”"),i("br"),i("br"),n._v("Hai cái thanh niên cười lạnh, hai bên trái phải, lắc người một cái, đi tới Trương Huyền hai bên."),i("br"),i("br"),n._v("Mặc dù còn không có động, lại đem hết thảy chung quanh sinh cơ, toàn bộ đóng kín, cho người ta một loại bị phong tỏa, nhốt vào chiếc lồng cảm giác."),i("br"),i("br"),n._v("“Còn không có ra tay, liền thi triển tâm lý ám chỉ cùng áp lực, có chút thực lực...”"),i("br"),i("br"),n._v("Trương Huyền âm thầm gật đầu."),i("br"),i("br"),n._v("Không hổ là Thánh tử điện tới cao thủ, còn không có động thủ, liền cấp cho tâm lý ám chỉ, tâm cảnh yếu, sợ rằng sẽ lập tức sinh ra bối rối."),i("br"),i("br"),n._v("Loại tâm tình này, một khi lên men ra, đối với chiến đấu ảnh hưởng cực lớn, hơi không cẩn thận, vô cùng có khả năng, còn không có động thủ, liền sẽ trực tiếp nhận thua."),i("br"),i("br"),n._v("“Chiến đấu phương pháp rất tốt, có điều, so với ta tâm cảnh, các ngươi còn yếu chút...”"),i("br"),i("br"),n._v("Cái cằm vừa nhấc, Trương Huyền hai tay chắp sau lưng, chậm rãi đi về phía trước một bước."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1256"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1258"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);