(window.webpackJsonp=window.webpackJsonp||[]).push([[302],{657:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1249-tu-minh-khao-hach"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1249-tu-minh-khao-hach"}},[n._v("#")]),n._v(" Chương 1249: Tự mình khảo hạch")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1249: Tự mình khảo hạch"),t("br"),t("br"),n._v("Đối phương muốn cho mình áp lực, ý định tại cảm giác áp bách nồng nặc nhất thời điểm, một lần ra tay... Chiêu này dùng để đối phó Trương Cửu Tiêu, có lẽ có dùng, đối phó bản thân... Quá yếu!"),t("br"),t("br"),n._v("Không nói trước tâm cảnh của hắn, đã đạt tới bát tinh trung phẩm khoảng chừng, coi như không bằng đối phương, cũng không phải loại này chiêu số liền có thể chèn ép."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Động tác của hắn rất nhỏ, thoạt nhìn không tính là gì, nhưng ở hai vị thanh niên trong mắt, lại giống có một tòa cự Sơn Lăng không đè xuống, để cho người ta luống cuống tay chân, không cách nào ứng phó."),t("br"),t("br"),n._v("Ngay sau đó, trong đầu một hồi nổ vang, vừa rồi cho đối phương áp lực, lập tức phản phệ mà đến, hai người đồng thời cảm thấy chóng mặt, bất cứ lúc nào cũng sẽ ngã trên mặt đất."),t("br"),t("br"),n._v("“Kinh hồng sư chức nghiệp?”"),t("br"),t("br"),n._v("Triệu Hưng Mặc lông mày nâng lên."),t("br"),t("br"),n._v("Trước mắt vị này Trương Huyền, cứ việc hai tay chắp sau lưng, lại tại trong nháy mắt, thi triển kinh hồng chức nghiệp bên trong công kích linh hồn, không chỉ đem hai cái thanh niên ý niệm công kích, hóa giải không còn một mảnh, còn phản kích tới, để hắn trong lòng sinh ra khủng hoảng."),t("br"),t("br"),n._v("Chỉ một chút, liền nhìn ra công kích mục đích, vừa đập vừa cào, để Thánh tử điện dự thính ăn sống thua thiệt... Cái tên này, quả nhiên không kém."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Bàn tay lớn vẫy một cái, thanh âm thản nhiên."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Ngoại nhân nghe, thanh âm của hắn không coi là nhiều vang, thực tế hai người nghe vào trong tai, lại cùng sét đánh, lập tức đem hắn theo chóng mặt trạng thái bên trong kéo ra ngoài."),t("br"),t("br"),n._v("đọcngantruyen.com"),t("br"),n._v("“Đáng ghét!”"),t("br"),t("br"),n._v("Hai vị thanh niên lúc này mới phát hiện trúng chiêu, vẻ mặt đỏ lên, từng cái híp mắt lại."),t("br"),t("br"),n._v("Vốn dạy bảo đối phương, kết quả một cái nháy mắt liền bị đối phương dạy bảo, mất đi bản thân... Mãnh liệt lửa giận sắp đem hắn thiêu đốt."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Chân khí theo huyệt đạo lao ra, Trương Huyền không gian bốn phía, bị phong tỏa lên, hai người lăng không một trảo, hai cái bàn tay, một trước một sau, đồng thời bổ xuống."),t("br"),t("br"),n._v("Một chiêu này phối hợp vô cùng tinh diệu, bất kể chân khí vẫn là lực lượng, đều nắm tới được đỉnh phong, tránh né một cái, liền né tránh không được cái thứ hai, liền xem như Xuất Khiếu cảnh sơ kỳ cường giả bị nhốt trong đó, đều khó mà chạy trốn."),t("br"),t("br"),n._v("Đáng tiếc... Trương Huyền không phải Xuất Khiếu cảnh sơ kỳ, mà là sức chiến đấu có thể so với Xuất Khiếu cảnh hậu kỳ cường giả."),t("br"),t("br"),n._v("Trong mắt mang theo mỉm cười, miệng nhẹ nhàng mở ra."),t("br"),t("br"),n._v("“Các ngươi đánh nhầm...”"),t("br"),t("br"),n._v("Thanh âm bên trong mang theo đặc thù ý vị, tựa như tình nhân thì thầm, rơi vào hai người trong tai, để bọn hắn kìm lòng không được sắc mặt đỏ lên."),t("br"),t("br"),n._v("Trong nháy mắt, nguyên bản rơi vào Trương Huyền trên người bàn tay, cùng hắn nói đồng dạng, đánh trật phương hướng, mỗi người rơi vào đối phương ngực."),t("br"),t("br"),n._v("Bành! Bành!"),t("br"),t("br"),n._v("Hai tiếng kêu rên, đồng thời bay ngược ra ngoài."),t("br"),t("br"),n._v("Vốn tiến công Trương Huyền, kết quả biến thành... Hai người đánh nhau!"),t("br"),t("br"),n._v("“Ma âm?”"),t("br"),t("br"),n._v("Triệu Hưng Mặc sững sờ tại nguyên chỗ: “Cái tên này là cố ý hướng ta thị uy?”"),t("br"),t("br"),n._v("Vừa rồi hắn dùng tiếng la, đem hai người theo bị kinh hồng công kích trong hôn mê tỉnh táo lại, mặc dù làm được bí ẩn, nhưng đối phương khẳng định đã nhìn ra, cho nên, mới dùng đồng dạng ma âm thủ đoạn, để hắn tự giết lẫn nhau!"),t("br"),t("br"),n._v("Mục đích rất đơn giản... Ma âm, ta cũng biết!"),t("br"),t("br"),n._v("Hiển nhiên là tại hướng hắn thị uy, cũng là cảnh cáo, trách cứ hắn, can thiệp tỷ thí."),t("br"),t("br"),n._v("“Có ý tứ gia hỏa!”"),t("br"),t("br"),n._v("Rõ ràng điểm ấy, Triệu Hưng Mặc cũng không tức giận, mà là đối vị này Trương Huyền mang theo tò mò."),t("br"),t("br"),n._v("Có thể đi vào Thánh tử điện, không phải đại gia tộc hậu nhân, liền là tuyệt thế thiên tài, về sau, tất nhiên canh giữ một phương, trở thành đỉnh phong tồn tại... Nếu như gặp phải không công bằng đãi ngộ, không dám phản kháng, còn nói cái gì chấn nhiếp một phương?"),t("br"),t("br"),n._v("Bởi vậy, nhắc nhở chiến đấu hai người, thật ra thì cũng là khảo hạch một loại, muốn nhìn một chút phản ứng của đối phương."),t("br"),t("br"),n._v("Không nghĩ tới, lập tức lấy đạo của người, trả lại cho người... Không có chút nào áp chế cùng oan ức."),t("br"),t("br"),n._v("Đơn phần này tâm tính, liền sẽ không chịu thiệt."),t("br"),t("br"),n._v("“Có thể trở thành Thánh tử điện dự thính sinh, không có một cái là đèn đã cạn dầu, ăn một lần thua thiệt, chẳng khác nào bại lộ ma âm cùng kinh hồng sư át chủ bài, lại nghĩ kiên trì còn lại hai chiêu, liền không dễ dàng như vậy...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Triệu Hưng Mặc không tại nhiều nói, mà là tiếp tục nhìn sang."),t("br"),t("br"),n._v("Hai cái này phụ trách khảo hạch thanh niên, cứ việc tuổi không lớn lắm, nhưng có thể trở thành Thánh tử điện dự thính sinh, tất nhiên trải qua khảo hạch, chỉ dựa dẫm ma âm cùng kinh hồng loại này phụ tu, liền có thể đánh bại, thật sự quá không đem đối phương, để ở trong mắt."),t("br"),t("br"),n._v("Quả nhiên, hai người bay ngược mà ra, người trên không trung cũng đã tỉnh táo, con mắt đồng thời nheo lại, sát khí sôi trào."),t("br"),t("br"),n._v("Bọn họ là tới khảo hạch, không phải tới chơi gánh xiếc."),t("br"),t("br"),n._v("Một cái thâm sơn cùng cốc gia hỏa, thế mà liên tục đánh lén hai lần, để bọn hắn xấu mặt, tức giận đến sắp nổ."),t("br"),t("br"),n._v("“Vững chắc linh hồn, phòng ngừa của hắn linh hồn công kích!”"),t("br"),t("br"),n._v("Bên trái thanh niên hét lớn một tiếng, bàn tay tại sau lưng nhẹ nhàng đè ép, đã ngừng lại, nắm đấm bỗng nhiên bóp một cái, không gian chung quanh lập tức ngưng kết."),t("br"),t("br"),n._v("Nguyên Thần cảnh trung kỳ... Thế mà thi triển ra Xuất Khiếu cảnh cường giả mới có thể thi triển không khí ngưng trệ."),t("br"),t("br"),n._v('"Yên tâm!'),t("br"),t("br"),n._v("Bên phải thanh niên, giờ phút này cũng phản ứng lại, đồng dạng hướng về phía trước đẩy một cái."),t("br"),t("br"),n._v("Hai người không khí ngưng trệ, lập tức chồng chất lên nhau, để bốn phía trở nên càng thêm ngưng thực, để cho người ta như là trong nháy mắt rơi vào bùn nhão bên trong, tiến lên lui về phía sau, đều hứng chịu tới ảnh hưởng cực lớn."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Trương Huyền âm thầm gật đầu, dưới tình huống bình thường, không khí ngưng trệ hai người đồng thời thi triển lời nói, rất dễ dàng xung đột, từ đó làm cho không gian hỗn loạn, uy lực ngược lại không bằng một cái."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Xuất Khiếu cảnh cường giả liên hợp, cũng không phải là một cộng một bằng hai, mà là một cộng một bằng không điểm mấy."),t("br"),t("br"),n._v("Hai người trước mắt, thi triển ra hoàn toàn khác biệt, vậy mà hoàn mỹ dung hợp, liền xem như hắn, đều lần thứ nhất nhìn thấy."),t("br"),t("br"),n._v("Không hổ là Thánh tử điện người, riêng này phần ý thức chiến đấu cùng phản ứng, cũng không phải là bình thường người có thể so sánh được."),t("br"),t("br"),n._v("Bất quá... Loại này ràng buộc, tại người khác nhìn tới, rất khó chạy trốn, đối với hắn mà nói, đơn giản đến cực điểm, dưới chân vạch một cái, bước ra hai bước, ngón tay hướng về phía trước một điểm."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),n._v("Tựa như có đồ vật tại nguyên chỗ nổ tung, ngưng trệ không gian giống như là đâm thủng bóng bay, mấy đạo khí lưu, lập tức hung mãnh hướng bốn phía cuồng dũng tới."),t("br"),t("br"),n._v("Bành! Bành!"),t("br"),t("br"),n._v("Hai bên trái phải hai cái thanh niên, không nghĩ tới bọn họ sắp đặt đi ra không khí ngưng trệ, sẽ bị dễ dàng như thế phá giải, thậm chí lực lượng phản phệ, đồng thời lui về sau hai bước, từng cái vẻ mặt trở nên trắng."),t("br"),t("br"),n._v("“Hai chiêu... Đều là các ngươi công kích ta, cũng nên ta xuất thủ!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Chỉ cần có thể nhận lấy bọn họ ba chiêu, coi như thông qua, hai chiêu trước, đều là mình bị động chịu đòn, đã một chiêu cuối cùng, vậy liền chủ động xuất kích!"),t("br"),t("br"),n._v("“Tới đi!”"),t("br"),t("br"),n._v("Hai người lông mày nâng lên, bốn cái bàn tay điệp gia, một cỗ vô địch khí tức, lập tức lan tràn đi ra, nhìn tới bọn họ cũng ý thức được trước mắt vị này thực lực cực mạnh, không dám một mình đón đỡ."),t("br"),t("br"),n._v("“Làm phiền các ngươi bị thương, ta có chút băn khoăn, trước thời hạn nói xin lỗi!”"),t("br"),t("br"),n._v("Thấy hai người điệp gia lực lượng, Xuất Khiếu cảnh trung kỳ cường giả đều muốn tạm thời tránh mũi nhọn, Trương Huyền ôm quyền, khắp khuôn mặt là áy náy."),t("br"),t("br"),n._v("“Bị thương?”"),t("br"),t("br"),n._v("“Ngươi tự tìm cái chết!”"),t("br"),t("br"),n._v("Hai cái thanh niên suýt chút nữa tức điên, một chiêu cuối cùng còn không có thi triển, liền xin lỗi... Ôm em gái ngươi!"),t("br"),t("br"),n._v("Thắng chúng ta rồi nói sau!"),t("br"),t("br"),n._v("Trong lòng giận dữ, lực lượng lại không giữ lại, điệp gia lực lượng lập tức thả ra ngoài, như là xé rách không khí đạn pháo, hình như muốn đem toàn bộ đại điện đánh nát."),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, không khí liền bị áp súc nổ tung, bốn phía tiếng gió phần phật, thổi đến làn da sắp nổ tung."),t("br"),t("br"),n._v("“Thật mạnh...”"),t("br"),t("br"),n._v("Trương Cửu Tiêu sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Lúc này, hắn rốt cuộc biết vì cái gì Triệu Hưng Mặc tự tin như vậy, bọn họ rất khó đồng thời nhận lấy ba chiêu... Không nói cái khác, chỉ nói một chiêu này, coi như hắn đem trước kia học qua tất cả bí pháp thi triển đi ra, đều không thể ngăn cản!"),t("br"),t("br"),n._v("Tràn đầy lo lắng hướng Trương Huyền nhìn lại, chỉ thấy hắn đối mặt hung mãnh công kích, vẻ mặt thản nhiên, cũng không né tránh, lật bàn tay một cái, không dùng võ kỹ, cũng vô dụng chiêu số, đập ruồi đồng dạng đánh ra."),t("br"),t("br"),n._v("“Kết thúc đi!”"),t("br"),t("br"),n._v("Lạch cạch! Lạch cạch!"),t("br"),t("br"),n._v("Nương theo một cái nhẹ nhàng lời nói, hai cái thanh niên liên hợp hình thành chân khí đạn pháo, chớp mắt liền tan thành mây khói, giống như chưa hề xuất hiện qua, sau một khắc, hai người như là bị quất bay bóng bàn, “Hô!” Hai tiếng, bay ngược mà ra, dính sát vào trên vách tường, khuôn mặt trắng bệch, từng ngụm từng ngụm hộc máu."),t("br"),t("br"),n._v("Hai đại Thánh tử điện dự thính, liên thủ tích góp lực lượng, thế mà liền hắn tiện tay mà ra một bàn tay đều không có chặn lại!"),t("br"),t("br"),n._v("“Ta tính xong qua ư?”"),t("br"),t("br"),n._v("Đánh bay hai người, Trương Huyền giống như là làm một cái không có ý nghĩa sự tình, mí mắt vừa nhấc, nhìn về phía trước mắt Triệu Hưng Mặc."),t("br"),t("br"),n._v("“Cùng cấp bậc dưới tình huống, một chiêu loại bỏ hai người liên thủ, hiển nhiên còn có chỗ trống... Thông qua!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Triệu Hưng Mặc lên tiếng, trong lòng đồng thời hơi nghi hoặc một chút."),t("br"),t("br"),n._v("Hai chiêu trước, trước mắt vị này rõ ràng hạ thủ lưu tình, nhưng vì sao... Một chiêu cuối cùng, đem bọn hắn đánh thành đả thương?"),t("br"),t("br"),n._v("Chẳng lẽ hai người liên hợp lực lượng quá mạnh, có chút không khống chế được?"),t("br"),t("br"),n._v("“Đa tạ! Cửu Tiêu, ngươi lên đi, hẳn là có thể đủ kiên trì ba chiêu!”"),t("br"),t("br"),n._v("Gặp hắn trả lời, Trương Huyền quay đầu nói."),t("br"),t("br"),n._v("“Ta lên? Hiện tại?” Trương Cửu Tiêu sững sờ."),t("br"),t("br"),n._v("Hai người kia trọng thương dán tại trên tường rào, liền như thế đi lên, có chút không tốt lắm đâu."),t("br"),t("br"),n._v("“Ừm, hai người bọn họ, chúng ta cũng là hai người, ta chiến đấu một hồi, ngươi đi lên cũng không tính không công bằng!” Trương Huyền cười cười."),t("br"),t("br"),n._v("“Đúng...”"),t("br"),t("br"),n._v("Nghe nói như thế, Trương Cửu Tiêu lập tức hiểu được..."),t("br"),t("br"),n._v("Đối phương đây là vì giúp hắn, mới cố ý đem hai người đả thương!"),t("br"),t("br"),n._v("Như vậy hắn liền có chống nổi ba chiêu khả năng."),t("br"),t("br"),n._v("Tràn đầy cảm động, biết giờ phút này không phải rầu rĩ những này thời điểm, hướng về phía trước mấy bước, đi tới dán tại trên vách tường hai cái thanh niên trước mặt, hai tay ôm quyền: “Còn xin chỉ giáo!”"),t("br"),t("br"),n._v("“Đáng ghét...”"),t("br"),t("br"),n._v("Nghe được hai người đối thoại, gặp cái tên này quả thật không biết xấu hổ xông lại, tiếp tục khiêu chiến... Hai cái thanh niên chỉ cảm thấy ngực buồn bực, sắp nổ tung."),t("br"),t("br"),n._v("Gặp qua không biết xấu hổ, chưa thấy qua không biết xấu hổ như vậy!"),t("br"),t("br"),n._v("Chúng ta đều bị thương thành như vậy, đứng lên cũng không nổi... Làm sao đánh với ngươi?"),t("br"),t("br"),n._v("“Triệu sư chính miệng nói rồi, chỉ cần có thể chống nổi hai vị ba chiêu, liền sẽ cân nhắc, cho thêm chúng ta Thanh Nguyên đế quốc thêm một cái danh ngạch... Các ngươi nếu như không cách nào chiến thắng, chẳng khác nào nhận thua, Trương Cửu Tiêu chiến thắng!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Chiến thắng? Nghĩ hay lắm!”"),t("br"),t("br"),n._v("“Coi như liều mạng, cũng đừng hòng tại trong tay chúng ta chống nổi ba chiêu!”"),t("br"),t("br"),n._v("Cắn răng một cái, hai cái thanh niên giãy dụa lấy theo trên tường nhảy xuống, liền muốn xông lại."),t("br"),t("br"),n._v("“Được rồi, các ngươi hai cái nghỉ ngơi tại chỗ!”"),t("br"),t("br"),n._v("Khoát tay áo, Triệu Hưng Mặc nói."),t("br"),t("br"),n._v("“Triệu sư...”"),t("br"),t("br"),n._v("Hai cái thanh niên, tràn đầy cuống cuồng."),t("br"),t("br"),n._v("Đều là bọn họ không tốt, mới khảo hạch xong một cái, liền bị đánh tổn thương... Triệu sư không cho động thủ, hiển nhiên là tức giận."),t("br"),t("br"),n._v("“Không trách các ngươi, là vị này Trương sư, đã sớm tính toán được rồi.”"),t("br"),t("br"),n._v("Khoát tay áo, Triệu Hưng Mặc đứng dậy, nhẹ nhàng cười một tiếng: “Vị này Trương Cửu Tiêu, ta tự mình khảo hạch!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1257"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1259"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);