(window.webpackJsonp=window.webpackJsonp||[]).push([[305],{660:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1251-truong-huyen-truyen-thu-cho-tuyet-chieu-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1251-truong-huyen-truyen-thu-cho-tuyet-chieu-ha"}},[n._v("#")]),n._v(" Chương 1251: Trương Huyền truyền thụ cho tuyệt chiêu (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1251: Trương Huyền truyền thụ cho tuyệt chiêu (hạ)"),t("br"),t("br"),n._v("Triệu Hưng Mặc tràn đầy không vui, mặc dù tức giận, giờ phút này nhưng cũng hết cách rồi, đành phải thân thể nhoáng một cái, hướng một bên tránh né."),t("br"),t("br"),n._v("Mặc kệ lực lượng của đối phương làm sao, có thể hay không chịu được, cũng không thể tuỳ tiện mạo hiểm đón đỡ, không phải sợ phế, mà là gánh không nổi người này."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Thân thể vừa tới đến một bên, chỉ thấy đối phương hai ngón tay hướng về phía dưới nách của hắn điểm tới, vừa rồi tiến công dưới hông hung mãnh chiêu số, lại là hư chiêu, mục đích thực sự ở đây!"),t("br"),t("br"),n._v("“Tiến công dưới hông, suy đoán ra ta trốn tránh, thân pháp vận chuyển thời điểm, dưới nách sẽ xuất hiện kẽ hở...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Triệu Hưng Mặc tràn đầy khó tin."),t("br"),t("br"),n._v("Đối phương ngón tay chỉ tới địa phương, công bằng, đúng là hắn công pháp bên trong, dễ dàng nhất công phá mệnh môn, một khi bị đánh trúng, toàn thân sẽ lập tức tê liệt, từ đó mất đi sức chiến đấu!"),t("br"),t("br"),n._v("Hai bên chiến đấu, không phải tin tưởng trùng hợp mới điểm hướng vị trí này, nhất định là trước thời hạn liền làm ra dự phán."),t("br"),t("br"),n._v("Một chiêu tiến công, liền để bản thân rơi vào đối phương tiết tấu, hơn nữa suy tính xuất chiêu đếm được thiếu hụt... Đối với chiến đấu lĩnh ngộ, có phần quá kinh khủng đi!"),t("br"),t("br"),n._v("“Thật là một cái tên giảo hoạt, ngay từ đầu liền đối ta thi triển tâm lý chiến...”"),t("br"),t("br"),n._v("Hắn là Thánh vực lục trọng trở lên danh sư, tư duy kỹ càng, nhìn ra đối phương chiêu số, chuyện lúc trước, trong nháy mắt đã nghĩ thông suốt."),t("br"),t("br"),n._v("Ngô đường chủ chạy tới, cố ý nói Tống sư bị phế, trong lòng hắn lưu lại một cái tiềm thức ý nghĩ, ngay sau đó Trương Cửu Tiêu vừa đến, liền tiến công đối ứng vị trí, bản thân một cách tự nhiên liền cảm giác, khẳng định sẽ ra tay, từ đó không nghĩ, chỉ là cái hư chiêu."),t("br"),t("br"),n._v("Còn chưa bắt đầu, liền trúng phải tâm lý đối phương chiến..."),t("br"),t("br"),n._v("Lợi hại!"),t("br"),t("br"),n._v("“Đổi lại bình thường Nguyên Thần cảnh, rơi vào bị động, mười chiêu bên trong muốn chuyển về cũng khó khăn, nhưng ta không giống!”"),t("br"),t("br"),n._v("Lông mày giương lên, Triệu Hưng Mặc thân thể lần nữa xoay một cái, tránh thoát công kích, lăng không lật tay, điểm tới."),t("br"),t("br"),n._v("Đầu ngón tay đối đầu ngón tay, hắn muốn dùng võ kỹ của mình cùng lực lượng, phá giải công kích của đối phương, đổi bị động làm chủ động."),t("br"),t("br"),n._v("Lực lượng hung mãnh, mặc dù là tương đồng cấp bậc, nhưng hắn chỉ lực, muốn so Trương Cửu Tiêu hùng hậu nhiều, còn chưa tới đến trước mặt, liền phát ra nghẹn ngào thanh âm, mang theo phong lôi thanh âm, hình như muốn đem không khí xé rách."),t("br"),t("br"),n._v("Nhìn thấy một chỉ này uy lực, Trương Cửu Tiêu dường như giật nảy mình, biết không dám đón đỡ, vội vàng thu tay lại chỉ, bởi vì khẩn trương thái quá, thân thể nghiêng một cái, không có phòng bị, thế mà đầu hướng về phía ngón tay liền tiến lên đón."),t("br"),t("br"),n._v("Triệu Hưng Mặc cau mày."),t("br"),t("br"),n._v("Lấy hắn chỉ lực, một khi rơi vào đối phương trên đầu, đầu tất nhiên tại chỗ nổ nát vụn, có thể nói... Hẳn phải chết không nghi ngờ!"),t("br"),t("br"),n._v("Đối tiểu bối ra tay bản thân liền bị người chỉ trích, lại đem người tham gia khảo hạch giết... Hắn vị này tiếp dẫn sứ giả, cũng không cần trở thành."),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Cuộn trào mãnh liệt chân khí chảy ngược, để hắn kìm lòng không được rên lên một tiếng, vẻ mặt có chút ửng hồng."),t("br"),t("br"),n._v("Không áp chế tu vi, điểm ấy lực lượng phản phệ, không tính là gì, nhưng bây giờ chỉ có Nguyên Thần cảnh trung kỳ, lại dùng hết toàn lực, chỉ một chút, liền bị thương."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Hắn thu hồi ngón tay, không muốn đem đối phương chém giết, lại thấy trước mắt Trương Cửu Tiêu, đã dự liệu được phản ứng của hắn, chẳng biết lúc nào đã đi tới trước mặt, bả vai hướng về phía ngực đánh tới."),t("br"),t("br"),n._v("Lần này lực lượng hung mãnh, một khi bị đánh trúng, coi như không bị thương, khẳng định cũng không dễ chịu, tràn đầy buồn bực, Triệu Hưng Mặc lật bàn tay một cái, giữa trời hạ xuống."),t("br"),t("br"),n._v("Đè thấp tu vi cùng đối phương đối chiến, lui về sau nữa, về sau liền mặt mũi đặt ở chỗ đó?"),t("br"),t("br"),n._v("Bàn tay hạ xuống, giống như mặt trời rơi xuống đất, mang theo không thể tránh né lực lượng, dựa theo thời gian suy tính, Trương Cửu Tiêu còn chưa tới đến trước mặt, liền sẽ bị một chưởng đánh trúng, bản thân bị trọng thương."),t("br"),t("br"),n._v("“Thật xin lỗi...”"),t("br"),t("br"),n._v("Đang suy đoán đối phương tránh về phía sau, nên dùng cái gì chiêu số thừa cơ kiếm lấy ưu thế, liền nghe đến một cái thanh âm nhàn nhạt vang lên, ngay sau đó dưới nách tê rần, chẳng biết lúc nào, Trương Cửu Tiêu ngón tay, đã xuất hiện tại mệnh môn của hắn bên trên."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Lực lượng toàn thân trong nháy mắt như thủy triều lui đi, thời gian nháy mắt trở nên rã rời vô lực."),t("br"),t("br"),n._v("t r"),t("br"),n._v("u y e n c u a t u i. v n  “Ngươi...”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một chiêu đến tay, Trương Cửu Tiêu không có tiếp tục công kích, mà là vội vàng lui về sau một bước, khắp khuôn mặt là áy náy: “Nhờ có Triệu sư hạ thủ lưu tình, ba chiêu đã qua...”"),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Vận chuyển chân khí, lần nữa khôi phục tới, Triệu Hưng Mặc tràn đầy buồn bực, lại nói không ra lời nói tới."),t("br"),t("br"),n._v("Vừa rồi đối phương nắm mệnh môn của hắn, thừa cơ công kích, khẳng định bị cuồng đánh một trận... Thật muốn như thế, mất mặt cũng ném chết rồi."),t("br"),t("br"),n._v("“Triệu sư thật muốn giết ta, chiêu thứ hai liền chết, thật ra thì ta dùng vô lại phương pháp...”"),t("br"),t("br"),n._v("Thấy hắn không vui, Trương Cửu Tiêu tiếp tục nói xin lỗi."),t("br"),t("br"),n._v("Chiêu thứ hai thời điểm, tay của đối phương chỉ tiếp tục hướng phía trước, hắn khẳng định còn không có cận thân, đầu liền bạo, cưỡng ép thu hồi lực lượng, bị thương, mới có thể thừa cơ đánh lén thành công."),t("br"),t("br"),n._v("Có thể nói, là mượn đối phương lòng trắc ẩn, nếu không, cho dù có tâm lý chiến, cũng sống không qua chiêu thứ hai."),t("br"),t("br"),n._v("“Thắng liền là thắng, không có lý do gì!”"),t("br"),t("br"),n._v("Mặt không hề cảm xúc, Triệu Hưng Mặc khoát tay áo: “Ta có thể cho các ngươi Thanh Nguyên đế quốc hai cái danh ngạch, có điều, đây chỉ là lần đầu thử nghiệm, thi vòng hai thời điểm, hy vọng có thể có thể đem cái này danh ngạch bảo vệ, nếu không, vẫn như cũ không dùng!”"),t("br"),t("br"),n._v("Cứ việc đối mới dùng mưu kế, nhưng mà kết quả cuối cùng... Không chỉ kiên trì ba chiêu, còn đem bản thân chế phục..."),t("br"),t("br"),n._v("Chỉ bằng vào điểm này, là thực sự chiến thắng, không có bất kỳ cái gì giả dối."),t("br"),t("br"),n._v("Đã như vậy, cho thêm một cái danh ngạch cũng không có gì, bất quá... Cái này danh ngạch, có thể hay không cầm tới, liền nhìn bọn họ tại Tiềm Xung đế quốc biểu hiện."),t("br"),t("br"),n._v("“Đây đều là ngươi dạy kinh nghiệm?”"),t("br"),t("br"),n._v("Lại không rầu rĩ chuyện này, quay đầu nhìn về phía cách đó không xa Trương Huyền."),t("br"),t("br"),n._v("Vị này Trương Cửu Tiêu, mặc kệ chân khí lực lượng, vẫn là tốc độ phản ứng, đều khẳng định làm không được điểm ấy, khả năng duy nhất tính liền là trước mắt vị thanh niên này, vừa rồi lâm trận mới mài gươm."),t("br"),t("br"),n._v("“Tùy tiện suy nghĩ ba chiêu, để Triệu sư chê cười...”"),t("br"),n._v(" Biết không thể gạt được đối phương, cũng không có gì có thể giấu diếm, Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Tùy tiện?”"),t("br"),t("br"),n._v("Triệu Hưng Mặc cười nhạo: “Tùy tiện nghĩ, liền suy tính ra ta vừa rồi thân pháp, sẽ dùng , thiếu hụt tại dưới nách, càng suy tính ra, ta sẽ không xuất thủ giết người, thu lực qua đi, lui về phía sau nửa bước, sau đó để Trương Cửu Tiêu, đánh trúng ta mệnh cửa... Tính toán không sai chút nào?”"),t("br"),t("br"),n._v("Chiến đấu không may mắn."),t("br"),t("br"),n._v("Vừa rồi Trương Cửu Tiêu ba chiêu, chiêu thứ nhất, dụng tâm lý chiến, chiêu thứ hai liều mạng, chiêu thứ ba lấy xảo... Có thể làm được điểm ấy, nhất định là kỹ càng cân nhắc chiến thuật của mình, rõ như lòng bàn tay, mới làm ra như vậy thiết kế..."),t("br"),t("br"),n._v("Tùy tiện nghĩ, liền có thể làm được, nói đùa cái gì?"),t("br"),t("br"),n._v("“Có thể là vận khí ta tốt đi!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Vừa rồi cố ý để Kim Nguyên đỉnh đánh lén vị này Triệu sư, mục đích đúng là để hắn thi triển võ kỹ, trong đầu hình thành thư tịch."),t("br"),t("br"),n._v("Có thư tịch, đối phương tuyệt chiêu, thiếu hụt rõ rõ ràng ràng, lại nhằm vào những này, thiết kế đối chiến chiêu số, đương nhiên cũng liền đơn giản."),t("br"),t("br"),n._v("Bất quá, thư viện sự tình, không thể nói tỉ mỉ, đành phải giả bộ ngớ ngẩn."),t("br"),t("br"),n._v("“Vận may?”"),t("br"),t("br"),n._v("Biết trước mắt vị này Trương sư, không nguyện ý nhiều lời, Triệu Hưng Mặc lười nhác tiếp tục truy vấn, mí mắt vừa nhấc, nhìn lại: “Tùy tiện chỉ điểm, liền có thể để Trương Cửu Tiêu trong vòng ba chiêu, đem ta đánh bại... Như vậy, ta vẫn như cũ đè thấp tu vi, có dám cùng ta chiến đấu một hồi?”"),t("br"),t("br"),n._v("“Cùng ngươi chiến đấu? Ngươi vẫn như cũ áp chế tu vi đến Nguyên Thần cảnh trung kỳ?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Không sai!” Triệu Hưng Mặc gật đầu."),t("br"),t("br"),n._v("Hắn đối trước mắt vị này, có rất hưng thịnh thú."),t("br"),t("br"),n._v("Trong vòng ba chiêu, đem hai cái Thánh tử điện dự thính sinh đánh thành trọng thương... Tùy tiện chỉ điểm một người, để cho mình bị thiệt thòi..."),t("br"),t("br"),n._v("Tới giao đấu, chân chính thi triển ra toàn lực, cái kia mạnh bao nhiêu?"),t("br"),t("br"),n._v("“Ây... Thôi được rồi, ta sợ thu lại không được khí lực, sẽ có tổn thương!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Yên tâm đi, một khi ta cảm thấy sẽ làm bị thương đến ngươi, sẽ lập tức mở ra phong ấn, bảo đảm ngươi bình yên vô sự!”"),t("br"),t("br"),n._v("Nghe đối phương có chút kiêng kị, Triệu Hưng Mặc nói."),t("br"),t("br"),n._v("Thực lực tương đương người đối chiến, bởi vì không cách nào khống chế tinh chuẩn lực lượng, quả thực rất dễ dàng bị thương, nhưng hắn là Thánh vực lục trọng trở lên cường giả, thật nếu gặp phải nguy hiểm, cũng có thể nhẹ nhõm mở ra phong ấn, đem lực lượng khống chế lại."),t("br"),t("br"),n._v("“Không phải nói ta sẽ bị thương... Ta sợ ngươi gánh không được...” Thấy đối phương hiểu lầm, Trương Huyền mang theo xấu hổ."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Triệu Hưng Mặc suýt chút nữa tức chết."),t("br"),t("br"),n._v("Thánh tử điện tới sứ giả, muốn cùng ngươi tỷ thí, không cảm thấy sợ hãi, sợ hãi ngược lại cũng thôi, thế mà còn sợ đem ta đả thương... Quả thực là có thể nhịn không thể nhẫn nhục."),t("br"),t("br"),n._v("“Không cần lo lắng cho ta bị thương, ta loại thực lực này, muốn bị thương cũng rất khó!”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, vẻ mặt trầm thấp, nhịn không được khẽ nói."),t("br"),t("br"),n._v("“Cái này...” Trương Huyền chần chừ."),t("br"),t("br"),n._v("“Ngươi cứ việc yên tâm ra tay chính là! Coi như thực lực ngươi không yếu, thiên phú cũng rất mạnh, nhưng ở Thánh tử điện chân chính cao thủ nhãn lực, không tính là gì...”"),t("br"),t("br"),n._v("Hai tay một lưng, Triệu Hưng Mặc mang theo cao thủ đặc thù khí tức."),t("br"),t("br"),n._v("Thánh tử điện, toàn bộ danh sư đại lục học phủ cao nhất, Thánh Nhân quý tộc, đều đem hậu bối đưa tới học tập... Đủ thấy nội tình, há có thể là ngươi một cái nông thôn đến tiểu gia hỏa, nói đánh bại liền đánh bại?"),t("br"),t("br"),n._v("Vừa rồi sở dĩ trúng chiêu, là bởi vì nhất thời không tra thôi, bây giờ chuẩn bị đầy đủ, lại nghĩ vượt qua, nào có dễ dàng như vậy!"),t("br"),t("br"),n._v("“Đã như vậy... Ta liền thử một chút!”"),t("br"),t("br"),n._v("Thấy đối phương tự tin như vậy, Trương Huyền mắt sáng rực lên."),t("br"),t("br"),n._v("Vừa mới đột phá, cũng muốn tìm người thử một chút lực lượng, đã đối phương nói lời thề son sắt, đương nhiên muốn thử một chút."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Hít sâu một hơi, Triệu Hưng Mặc bàn tay xiết chặt, lực lượng truyền vào toàn thân."),t("br"),t("br"),n._v("Hắn thấy, đối phương thiên phú là không sai, nhưng cũng có chút quá cuồng vọng, vừa vặn nhờ vào đó bỏ đi khí diễm, để hắn biết, nhân ngoại hữu nhân, thiên ngoại hữu thiên."),t("br"),t("br"),n._v("“Tốt, ta bắt đầu...”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, nhắc nhở đối phương một câu."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Cười lạnh một tiếng, Triệu Hưng Mặc thân thể bỗng nhiên vọt tới, đang nghĩ ra tay, lập tức nhìn thấy một cái chưởng ấn thẳng tắp rơi xuống."),t("br"),t("br"),n._v("Như thiên địa sụp đổ, hồng lưu cuốn ngược."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Tất cả chiêu số lập tức bị đè ép trở về, hô hấp cũng không kịp, liền bị đánh trúng ngực, cả người trong nháy mắt bay ngược, một ngụm máu tươi phun mạnh mà ra."),t("br"),t("br"),n._v("Ầm ầm ầm ầm!"),t("br"),t("br"),n._v("Vận chuyển chân khí, vừa định hóa giải lực lượng, con ngươi lần nữa thu hẹp, ngay sau đó lần nữa nhìn thấy vô số bàn tay lần nữa rơi xuống, đối phương tiến công thế mà không phải một chưởng, mà là trăm ngàn chưởng!"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Sống lưng đâm vào đại điện trên vách tường, đập ra một cái to lớn lỗ thủng."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, vội vàng đem phong ấn giải khai, thật vất vả chặn lại công kích, mới vừa ngừng lại, thở phào nhẹ nhõm, liền nghe đến phía trước Trương Huyền thanh âm vang lên."),t("br"),t("br"),n._v("Tràn đầy tịch mịch như tuyết."),t("br"),t("br"),n._v("“Còn tưởng rằng thật rất lợi hại... Mới dùng không đến một phần năm lực lượng liền bay... Cái gì khoác lác ah...”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1259"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1261"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);