(window.webpackJsonp=window.webpackJsonp||[]).push([[308],{664:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1254-gap-lua-bo-tay-nguoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1254-gap-lua-bo-tay-nguoi"}},[n._v("#")]),n._v(" Chương 1254: Gắp lửa bỏ tay người")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1254: Gắp lửa bỏ tay người"),t("br"),t("br"),n._v("“Sau đó hỏi thăm xuống!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trương Cửu Tiêu cứ việc sức chiến đấu không được, thiên phú cũng không đáng nhắc tới... Nhưng làm việc vẫn là rất đáng tin cậy, đã nói lời thề son sắt, hẳn không phải là không có lửa thì sao có khói."),t("br"),t("br"),n._v("Lại nói, mặc kệ Tiềm Xung đế quốc có hay không linh khoáng, thân là đệ nhất đế quốc, nhiều như vậy Xuất Khiếu cảnh cường giả, tinh nguyên linh thạch thượng phẩm số lượng, tất nhiên không ít, có khả năng tìm người hối đoái, kiếm lấy lời nói, có lẽ liền có thể ở đây, một lần đột phá đến Nguyên Thần cảnh đỉnh phong."),t("br"),t("br"),n._v("Trong lòng suy tư, mọi người đã đi tới một cái rộng lớn đại điện, phân chủ khách ngồi xuống, Nhạc đường chủ cười cười, vung tay lên: “Cho mời thông qua lần đầu thử nghiệm tất cả tài tuấn!”"),t("br"),t("br"),n._v("Một vị danh sư lui ra ngoài, thời gian không dài, một đám thanh niên đi đến."),t("br"),t("br"),n._v("Có nam có nữ, đều mặc danh sư trang phục, ngực thêu lên bảy viên sao."),t("br"),t("br"),n._v("Nhìn một vòng, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cùng Triệu Hưng Mặc nói đồng dạng, những người này thực lực không thấp, kém nhất đều giống như bọn họ, đạt đến Nguyên Thần cảnh trung kỳ, cao cùng trước đó Hình đường chủ đồng dạng, đạt đến ngụy xuất khiếu cảnh giới."),t("br"),t("br"),n._v("Không đến ba mươi, không chỉ trở thành thất tinh danh sư, tu vi càng đạt tới loại tình trạng này, thiên tài quả nhiên khắp nơi đều có, so với mình không kém bao nhiêu."),t("br"),t("br"),n._v("“Triệu sư, mỗi bên đại đế quốc vừa đi vừa về, thực sự vất vả, ngày hôm nay đặc biệt chuẩn bị yến hội, cho ngươi đón tiếp, coi như nghỉ ngơi một ngày, ngày mai lại tiến hành thi vòng hai, ngươi xem coi thế nào?”"),t("br"),t("br"),n._v("Nhạc đường chủ cười nói."),t("br"),t("br"),n._v("“Cũng tốt!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Triệu Hưng Mặc nhẹ gật đầu."),t("br"),t("br"),n._v("Mặc dù không có thời gian, nhưng cũng không quan tâm chờ lâu buổi sáng."),t("br"),t("br"),n._v("Vừa vặn cũng có thể để đến đây khảo hạch tất cả thiên tài, trước thời hạn thư giãn một tí, nghênh đón tàn khốc hơn khảo hạch."),t("br"),t("br"),n._v("“Đưa rượu lên, mang thức ăn lên!”"),t("br"),t("br"),n._v("Thấy hắn đồng ý, Nhạc đường chủ nhẹ gật đầu, vẫy bàn tay lớn một cái, một đám học đồ đi ra ngoài, thời gian không dài, đủ loại sơn hào hải vị mỹ vị liền bưng tới."),t("br"),t("br"),n._v("Nhìn tới đối phương đoán chắc Triệu sư tới thời gian, trước thời hạn liền chuẩn bị được rồi."),t("br"),t("br"),n._v("Không hổ là đứng hàng thứ nhất phong hào đế quốc, rất nhanh trên bàn liền bày đầy mỹ tửu mỹ thực, đều là một chút đại bổ Thánh thú thịt cùng một chút trân quý dược liệu, ra tay nấu nướng, đoán chừng cũng là cấp bậc rất cao mỹ thực sư, không chỉ hương vị ngon, còn đem trong đó linh tính phát huy đầy đủ, ăn vào trong miệng, chân khí một hồi sôi trào, đối nguyên thần tiến bộ, đều có cực lớn trợ giúp."),t("br"),t("br"),n._v("“Một bàn này giá trị, chỉ sợ không thua kém một ngàn linh thạch thượng phẩm!”"),t("br"),t("br"),n._v("Trương Cửu Tiêu cảm khái."),t("br"),t("br"),n._v("“Đắt như thế?” Trương Huyền sửng sốt một chút."),t("br"),t("br"),n._v("Lúc trước vì kiếm một ngàn linh thạch thượng phẩm, hắn mất không biết bao nhiêu tâm huyết, mà bây giờ, một bữa cơm liền đáng giá nhiều như vậy... Có phần quá kinh khủng đi."),t("br"),t("br"),n._v("“Ngươi nhìn cái kia Kim Hổ thú gan, không chỉ muốn săn giết một đầu Thánh vực nhất trọng Kim Hổ thú, trong đó phối đồ ăn, Thánh vực cấp bậc dược liệu liền có trọn vẹn bốn vị... Xuyên Tức thảo, Tỳ Bà căn, Thanh Linh hoa, Ngũ Vị Ngưu diệp! Cái này bốn loại, cơ bản đều tại khoảng 50 năm, mỗi một cây, giá trị hơn trăm linh thạch thượng phẩm, xen lẫn trong cùng một chỗ, lại phối hợp mỹ thực sư tài năng như thần thủ pháp, một ngàn linh thạch, khả năng nói đều thiếu đi!”"),t("br"),t("br"),n._v("Trương Cửu Tiêu êm tai nói."),t("br"),t("br"),n._v("“Ngươi... Đối thức ăn ngon có nghiên cứu?” Nghe hắn nói đến đạo lý rõ ràng, Trương Huyền lúc này mới phát hiện, đúng là như thế, có chút hiếu kỳ."),t("br"),t("br"),n._v("Mỹ thực sư, hạ cửu lưu chức nghiệp, không cách nào trở thành phụ tu, chỉ cần là danh sư, cơ bản không có người đi học tập, không nghĩ tới hắn lại biết nhiều như vậy."),t("br"),t("br"),n._v("“Ta thích thức ăn ngon, cho nên... Nghiên cứu không ít, Trương gia cũng có chuyên môn mỹ thực sư, cung cấp nguyên liệu nấu ăn, bình thường thời điểm, cũng đi theo học qua, cho nên... Tính toán giải một chút!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Trương Cửu Tiêu nói."),t("br"),t("br"),n._v("Thức ăn ngon tại bình thường danh sư trong mắt, là tiểu đạo, nhưng nếu như phối hợp được rồi, có khả năng hình thành cơm thuốc, công hiệu không thể so với đan dược yếu bao nhiêu."),t("br"),t("br"),n._v("Hắn có thể tại hai mươi bảy tuổi, thân là bàng chi, liền có thực lực như thế, loại thức ăn này ở giữa phối hợp, tuyệt đối lên tác dụng không nhỏ."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Trương Huyền khen ngợi."),t("br"),t("br"),n._v("Đối với thức ăn ngon, cứ việc cũng biết một chút, chẳng qua chỉ giới hạn ở giám bảo, luyện đan loại hình tổng hợp kiến thức, chân chính đối với cái nghề nghiệp này, thư tịch đều không có nhìn qua, không tính là tinh thông."),t("br"),t("br"),n._v("“Có đồ ăn, có thể nào không rượu!”"),t("br"),t("br"),n._v("Hai người đang tại trò chuyện, liền nghe đến trung tâm Nhạc đường chủ cười cười, cổ tay khẽ đảo, lấy ra một cái hồ lô rượu."),t("br"),t("br"),n._v("“Đây là Thiên Thần lão nhân ủ chế Thiên Thần túy, trân quý mức độ, hẳn là không cần nhiều lời đi! Ta may mắn đạt được một hồ lô, cất vào hầm trọn ba trăm năm, ngày hôm nay Triệu sư ở đây, vừa vặn lấy ra, cùng quân cùng một chỗ nhấm nháp, chư vị, có muốn hay không phẩm một ly?”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Nhạc đường chủ mở ra nắp bình, nhẹ nhàng đổ đầy một ly."),t("br"),t("br"),n._v("Trong suốt rượu ngon, như là hổ phách, lại hình như là thủy tinh, treo ở ly trên vách, tản mát ra mùi thơm nồng nặc, toàn bộ đại điện lập tức bị mùi rượu phủ kín, ngửi bên trên một ngụm, cũng làm người ta thèm ăn nhỏ dãi."),t("br"),t("br"),n._v("“Rượu ngon...”"),t("br"),t("br"),n._v("Trương Huyền cũng âm thầm gật đầu."),t("br"),t("br"),n._v("Cứ việc không uống đến, nhưng khoảng cách xa như vậy, liền có như thế hương khí, cái này cái gọi là Thiên Thần túy, độ tinh khiết cao, hương vị tốt, tuyệt đối không phải trước đó uống những cái kia, có thể so sánh được."),t("br"),t("br"),n._v("“Thiên Thần lão nhân là liên minh đế quốc nổi danh tửu thánh, ủ ra rượu ngon, nghe nói liền Thánh vực thất trọng cường giả, đều có thể say ngã... Thiên Thần túy, là hắn sở trường nhất rượu ngon, số lượng thưa thớt, vô cùng trân quý. Vô số người vì cầu lấy một ly, tiêu tốn không biết giá lớn bao nhiêu, lại không thể được... Nhạc đường chủ vậy mà lấy ra, để đám người nhấm nháp?”"),t("br"),t("br"),n._v("Hô hấp dồn dập, Trương Cửu Tiêu nhãn tình kích động tỏa ánh sáng."),t("br"),t("br"),n._v("“Rượu này rất nổi danh?”"),t("br"),t("br"),n._v("“Đương nhiên là có danh... Tuyệt đối có tiền mà không mua được! Liền một chén này, mười cái tinh nguyên linh thạch thượng phẩm, cũng mua không được!” Thấy hắn không hiểu, Trương Cửu Tiêu nói."),t("br"),t("br"),n._v("“Mười cái... Mua một chén rượu?” Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Thanh Nguyên đế quốc Chiến Sư đường, cộng lại mới có mười cái loại cấp bậc này linh thạch... Mới có thể mua một chén rượu này..."),t("br"),t("br"),n._v("Ai điên rồi, có thể như vậy làm?"),t("br"),n._v("“Ngươi đừng không tin! Tinh nguyên linh thạch thượng phẩm, cứ việc thưa thớt, chỉ cần có khoáng mạch, còn có thể lấy được, nhưng Thiên Thần lão nhân Thiên Thần túy, không phải có tiền liền có thể mua được, còn cần vận may, vận may không tốt, khả năng thấy đều không thấy được...”"),t("br"),t("br"),n._v("Trương Cửu Tiêu lắc đầu."),t("br"),t("br"),n._v("Thấy hắn như thế hưng phấn, nói như thế chém đinh chặt sắt, Trương Huyền mặt mũi cười khổ."),t("br"),t("br"),n._v("Đối với rượu ngon, hắn không có gì đặc biệt khát vọng, ngược lại cảm thấy uống nhiều hỏng việc, lúc không có chuyện gì làm, cơ bản sẽ không đi nếm."),t("br"),t("br"),n._v("Thấy đối phương bộ dáng này, rất hiển nhiên, mười cái tinh nguyên linh thạch thượng phẩm có khả năng mua được lời nói, khẳng định sẽ không chậm trễ chút nào bỏ tiền... Bộ dáng này, liền để hắn có chút bất đắc dĩ."),t("br"),t("br"),n._v("Cái này rượu, thật sự tốt như vậy uống, có thể khiến người ta cam tâm tình nguyện tốn tiền nhiều như vậy mua sắm?"),t("br"),t("br"),n._v("“Trương sư không thích uống rượu, cũng không phải mỹ thực sư, biết đến không nhiều, cũng rất bình thường, ngươi xem một chút phản ứng của bọn hắn liền biết...”"),t("br"),t("br"),n._v("Nhìn hắn xem thường, Trương Cửu Tiêu biết, có người ưa thích, liền có người không thích, nói."),t("br"),t("br"),n._v("“Bọn họ?”"),t("br"),t("br"),n._v("Trương Huyền ngẩng đầu, lúc này mới phát hiện, nương theo Nhạc đường chủ đem rượu ngon đổ đầy, toàn bộ đại điện, đều sôi trào, đám người từng cái tròng mắt đỏ hoe, nhìn chằm chằm chén rượu, hận không thể trực tiếp ra tay cướp đoạt."),t("br"),t("br"),n._v("“Nhạc đường chủ, chúng ta nhiều người như vậy, rượu ngon chỉ có một ly, cho ai không cho ai, cũng không tốt đi... Không bằng, xác định cái điều lệ, ai có thể uống đến, đều bằng bản sự!”"),t("br"),t("br"),n._v("Huyên náo một hồi, một thanh niên mở miệng."),t("br"),t("br"),n._v("“Nói sư nói không sai, Thiên Thần lão nhân rượu ngon, đương nhiên muốn cho có tư cách người uống, nếu không, liền là trâu gặm mẫu đơn, uổng phí hết!”"),t("br"),t("br"),n._v("“Không có tác dụng cái gì điều lệ, chén rượu này ta uống định!”"),t("br"),t("br"),n._v("“Đừng khoác lác, ai có thể cầm tới, còn chưa nhất định đây...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Cái thứ nhất thanh niên mở miệng, đám người lập tức đáp lời."),t("br"),t("br"),n._v("Thiên Thần túy, quý giá như thế, đương nhiên không có khả năng mỗi người một ly, ai muốn uống đến, khẳng định muốn lấy ra, khiến người khác đều tin phục bản lĩnh mới được."),t("br"),t("br"),n._v("“Yên tĩnh!”"),t("br"),t("br"),n._v("Đem mọi người thảo luận nghe vào trong tai, Nhạc đường chủ cười cười, nhìn quanh một vòng: “Chư vị nói không sai, rượu chỉ có một ly, tự nhiên là cho có thực lực nhất người uống mới được... Đương nhiên, điều lệ gì gì đó không cần định, cũng không cần tỷ thí, chén rượu này, cho ai uống, trong lòng ta đã có đáp án, hắn trong lòng ta, liền là thực lực mạnh nhất, cực kỳ có bản lĩnh.”"),t("br"),t("br"),n._v("“Ồ?”"),t("br"),t("br"),n._v("“Thực lực mạnh nhất? Không biết vị này là ai?”"),t("br"),t("br"),n._v("“Có thể để cho đường chủ như vậy khen ngợi, ta cũng muốn nhìn một chút, có hay không tư cách này...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được hắn, đám người đồng loạt nhìn qua."),t("br"),t("br"),n._v("Bọn họ đều là thiên tài trong thiên tài, cường giả bên trong cường giả, không có đi qua tỷ thí, liền nói có người là trong bọn họ mạnh nhất, làm sao có thể bội phục!"),t("br"),t("br"),n._v("“Người này danh khí không tính quá hiện ra, nhưng là từ vương quốc một đường mà đến, liên tục xông đường hai lần, đều thuận lợi thành công, càng làm cho Thanh Nguyên đế quốc, thay đổi triều đại, thu vô số danh sư làm đồ đệ... Nổi danh thời gian không dài, lại làm mỗi một sự kiện, đều tên tuổi lẫy lừng, để cho người ta kính nể!”"),t("br"),t("br"),n._v("Nhạc đường chủ nói."),t("br"),t("br"),n._v("“Liên tục hai lần xông đường thành công?”"),t("br"),t("br"),n._v("“Để Thanh Nguyên đế quốc thay đổi triều đại?”"),t("br"),t("br"),n._v("“Theo vương quốc mà tới?”"),t("br"),t("br"),n._v("Đám người tất cả đều sững sờ, đồng loạt nhìn về phía Trương Huyền cùng Trương Cửu Tiêu bên này."),t("br"),t("br"),n._v("Hai vị này là cuối cùng mới tới, dĩ nhiên chính là Thanh Nguyên đế quốc thiên tài."),t("br"),t("br"),n._v("“Không sai, hắn liền là Trương Huyền Trương sư!”"),t("br"),t("br"),n._v("Nhạc đường chủ một chỉ."),t("br"),t("br"),n._v("“Cái tên này, thật biết mượn đao giết người...”"),t("br"),t("br"),n._v("Trương Huyền nhướng mày, càng thêm không vui."),t("br"),t("br"),n._v("Cố ý lấy ra rượu ngon, thu hút chú ý của mọi người, đợi quần tình xúc động về sau, nói muốn tặng cho bản thân..."),t("br"),t("br"),n._v("Bên ngoài là tán dương, cố ý nâng lên bản thân, trên thực tế lại kéo những người khác cừu hận, đưa tới đám người phản cảm."),t("br"),t("br"),n._v("Một chén rượu, liền cho mình đưa tới vô số kẻ địch."),t("br"),t("br"),n._v("Thật là âm hiểm."),t("br"),t("br"),n._v("“Trương sư, Tống Hiên là ta em vợ, vì người có chút lỗ mãng, tiến đến Thanh Nguyên đế quốc xử lý chuyện thời điểm, khả năng làm sai một ít chuyện, nhờ có ngươi giúp hắn sửa chữa, mới tránh khỏi ủ thành đại họa... Điểm ấy, ta mười phần cảm kích. Ngắn như vậy thời gian bên trong, liền phát hiện vấn đề, gọn gàng ngăn nắp xử lý tốt, để Thanh Nguyên đế quốc xóa bỏ rung chuyển, Danh Sư đường uy tín lần nữa thiết lập... Ta đều làm không được, mười phần bội phục, chén rượu này, kính ngươi!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, bàn tay vạch một cái, trên bàn vừa mới đổ đầy rượu ngon lập tức lơ lửng."),t("br"),t("br"),n._v("Nghe đối phương nói ra những này, Trương Huyền vẻ mặt trở nên có chút khó coi."),t("br"),t("br"),n._v("Trước thừa nhận Tống Hiên quan hệ với hắn, sau đó nói ra bội phục... Bên ngoài rất thẳng thắn, để cho người ta tìm không ra khuyết điểm, trên thực tế, lại đem họa thủy đông dẫn, đem tất cả mọi người cừu hận đều kéo đến đây."),t("br"),t("br"),n._v("Cũng không để người mượn cớ, lại có thể bản thân khó xử."),t("br"),t("br"),n._v("Không hổ là thứ nhất phong hào đế quốc Danh Sư đường đường chủ, thật là độc ác."),t("br"),t("br"),n._v("Quả nhiên, danh sư tiểu nhân lên, thật sự tiểu nhân còn đáng sợ hơn!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1262"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1264"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);