(window.webpackJsonp=window.webpackJsonp||[]).push([[309],{663:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1255-truong-su-lam-sao-yeu-nhu-vay"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1255-truong-su-lam-sao-yeu-nhu-vay"}},[n._v("#")]),n._v(" Chương 1255: Trương sư làm sao yếu như vậy?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Cự tuyệt, khí thế bên trên yếu đi, về sau ở trước mặt mọi người, khẳng định không ngẩng đầu được lên, tiếp nhận, tự nhiên sẽ có người không phục."),t("br"),t("br"),n._v("Suy nghĩ một chút cũng nhức đầu."),t("br"),t("br"),n._v("Đang lo lắng, làm sao hóa giải cái này đột nhiên tới hỏi khó, chỉ thấy Nhạc đường chủ nhẹ nhàng cười một tiếng, ngón tay một điểm, trước mắt lơ lửng chén rượu và rượu ngon, bút thẳng bay tới."),t("br"),t("br"),n._v("Nhìn tới đối phương căn bản không cho mình cơ hội cự tuyệt."),t("br"),t("br"),n._v("“Mong rằng Trương sư không muốn từ chối, đây là của ta một điểm tâm ý!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Chén rượu đi tới trước mặt."),t("br"),t("br"),n._v("Trong nháy mắt, một cỗ nồng đậm áp bức theo chén rượu truyền tới, tựa hồ muốn thân thể của hắn ép sụp đổ."),t("br"),t("br"),n._v("Cảm nhận được cỗ lực lượng này, Trương Huyền lông mày lần nữa nhăn lại."),t("br"),t("br"),n._v("Đối phương đem chén rượu đưa tới, thoạt nhìn tốc độ không nhanh, hơn nữa mang theo thân thiện hương vị, trên thực tế, lại vận dụng thủ đoạn, có lẽ cái ra oai phủ đầu."),t("br"),t("br"),n._v("Một khi không có nhận được, để trong đó rượu ngon vẩy ra, chỉ sợ không riêng gì không tôn trọng hắn cái chủ nhân này, càng là không biết điều! Không cần hắn nói chuyện, chung quanh vốn là không phục đám người, liền sẽ nhao nhao ra tay, đem bản thân đánh bẹt, đập dẹp."),t("br"),t("br"),n._v("Cái tên này trở thành người tốt, hiển lộ rõ ràng rộng lượng, còn thuận tiện đem bản thân âm... Lợi hại!"),t("br"),t("br"),n._v("“Đa tạ Nhạc đường chủ ưu ái!”"),t("br"),t("br"),n._v("Đổi lại những người khác, thủ đoạn của đối phương, tới đột nhiên như vậy, khẳng định không có cách nào ứng phó, trực tiếp xấu mặt, nhưng hắn khác biệt, Minh Lý Chi Nhãn vận chuyển, lập tức đem ẩn chứa trong đó chân khí quỹ tích, nhìn rõ rõ ràng ràng."),t("br"),t("br"),n._v("Không có quá nhiều động tác, đôi đũa trong tay nhẹ nhàng nâng lên, bỗng nhiên hướng về phía trước nhấn tới."),t("br"),t("br"),n._v("Đũa tốc độ không nhanh, lại mang theo một cỗ vô địch thương ý, thời gian nháy mắt đi tới chén rượu trước mặt, “Ba!” Một tiếng, liền đem vây quanh tại bốn phía chân khí ghim cái lỗ thủng."),t("br"),t("br"),n._v("Chân khí phân tán bốn phía, giống như là đâm rách bóng bay, lại không còn chống đỡ, thẳng tắp rơi xuống dưới."),t("br"),t("br"),n._v("Đũa lắc một cái, nhẹ nhàng kẹp lấy, rượu ngon đã bị kẹp lấy."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Cái này liên tiếp động tác, nước chảy mây trôi, thật giống như Nhạc Nhất Toàn đem rượu ngon yên tĩnh đưa tới, hắn dùng đũa tiện tay nhận lấy đồng dạng... Ngoại nhân nhìn không ra cái gì, Nhạc đường chủ lại là vẻ mặt, kìm lòng không được trầm xuống."),t("br"),t("br"),n._v("Hắn vừa rồi tại chén rượu chung quanh lưu lại lực lượng, coi như Nguyên Thần cảnh đỉnh phong, đều không chống đỡ được, trực tiếp xấu mặt, trước mắt vị này, dễ như trở bàn tay nhận lấy... Đối lực lượng đem khống, đã đạt đến mức làm người nghe kinh hãi."),t("br"),t("br"),n._v("Căn cứ tin tức, vị này Trương Huyền, sở dĩ mạnh, không phải là bởi vì cái kia lô đỉnh ư?"),t("br"),t("br"),n._v("Lúc nào thực lực bản thân, cũng lợi hại như vậy?"),t("br"),t("br"),n._v("Triệu Hưng Mặc khảo hạch, cùng cấp bậc bị đánh thảm sự tình, việc quan hệ mặt mũi, chắc chắn sẽ không nói ra, ở đây Ngô đường chủ, Diêu Mạn Thiên, Trương Cửu Tiêu đám người, càng chưa hề nói cần thiết."),t("br"),t("br"),n._v("Bởi vậy, Trương Huyền tu vi bạo tăng, cùng cấp bậc vô địch sự tình, coi như hắn vị này Tiềm Xung đế quốc Danh Sư đường đường chủ, đều đồng thời không biết rõ tình hình."),t("br"),t("br"),n._v("Lấy được tin tức, chỉ là theo Tống Hiên nơi đó biết được."),t("br"),t("br"),n._v("Mà Tống Hiên, chịu thiệt tại Trương Huyền trong tay, đương nhiên đem hắn nói khuôn mặt đáng ghét, sứ dùng binh khí đánh lén... Cái khác, cũng không tiện nhiều lời, như vậy trải qua, tin tức không ngang nhau, cũng liền liền không có thuận lợi như vậy."),t("br"),t("br"),n._v("Bất quá, mặc dù khiếp sợ, dù sao cũng là một nhà chi chủ, trên mặt không có chút nào biểu lộ, nhẹ nhàng cười một tiếng: “Trương sư, mời!”"),t("br"),t("br"),n._v("Nói hết nâng lên chén rượu của mình uống một hơi cạn sạch."),t("br"),t("br"),n._v("Trương Huyền cười cười, bưng chén rượu lên, đang muốn nói chuyện, vừa rồi vị kia gọi là Vân sư thanh niên, đột nhiên đứng dậy: “Chậm đã!”"),t("br"),t("br"),n._v("Đám người đồng loạt nhìn sang."),t("br"),t("br"),n._v("“Vân sư, làm sao vậy?”"),t("br"),t("br"),n._v("Nhạc Nhất Toàn nhìn qua."),t("br"),t("br"),n._v("“Nhạc đường chủ, vừa rồi tất cả mọi người đã nói rồi, chén rượu này, người có thực lực đạt được... Ngươi nói vị này Trương sư, là trong chúng ta, mạnh nhất, ta có chút không quá bội phục, muốn muốn khiêu chiến một chút, không biết có thể?”"),t("br"),t("br"),n._v("Liền ôm quyền, quay đầu nhìn về phía Trương Huyền, Vân sư trong mắt tràn đầy khiêu khích."),t("br"),t("br"),n._v("Thiên Thần lão nhân ủ chế Thiên Thần túy, không có người sẽ bỏ lỡ, vô duyên vô cớ cho Thanh Nguyên đế quốc một cái gia hỏa uống, không chỉ hắn không phục, những người khác cũng không phục."),t("br"),t("br"),n._v("“Không sai, ta cũng cảm thấy vẫn là tỷ thí một chút!”"),t("br"),t("br"),n._v("“Thanh Nguyên đế quốc tình huống, chúng ta đều lòng dạ biết rõ, vị này Trương sư, nếu như ta không nhìn lầm, cũng chỉ có Thánh vực tứ trọng Nguyên Thần cảnh trung kỳ đi, loại thực lực này... Cùng ta một cái học sinh gần như, có tư cách uống rượu, ta cũng cảm thấy không quá ổn thoả!”"),t("br"),t("br"),n._v("“Người có năng lực đến, Trương sư, dù sao tất cả mọi người muốn thi vòng hai, không bằng trước để chúng ta nhìn nhìn thực lực của ngươi đến cùng làm sao!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lao nhao, đám người từng cái nhìn qua, tràn đầy địch ý."),t("br"),t("br"),n._v("“Xem ra ta vẫn là đánh giá thấp chén rượu này uy lực...”"),t("br"),t("br"),n._v("Nhìn thấy bọn họ bộ dáng này, Trương Huyền có chút bất đắc dĩ."),t("br"),t("br"),n._v("Vốn nghĩ rượu mà thôi, ai uống cũng không đáng kể, làm sao đều không nghĩ tới, uy lực như thế lớn."),t("br"),t("br"),n._v("“Rượu ngon ta đã cho Trương sư, xử lý như thế nào là chuyện của hắn, đừng tổn thương hòa khí liền tốt...”"),t("br"),t("br"),n._v("Nhạc Nhất Toàn nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Lửa đã bị khều lên tới, còn lại tọa sơn quan hổ đấu là đủ."),t("br"),t("br"),n._v("“Cái kia tốt!”"),t("br"),t("br"),n._v("Nghe hắn nói đừng tổn thương hòa khí, rõ ràng đã ngầm đồng ý, Vân sư mắt sáng rực lên, cười lạnh một tiếng, nhìn lại: “Trương sư, có dám hay không cùng ta so sánh với một hồi, thắng, rượu ngon về ta!”"),t("br"),t("br"),n._v("“A, thua đây?”"),t("br"),t("br"),n._v("Nắm chén rượu, Trương Huyền nói."),t("br"),t("br"),n._v("“Thua?” Vân sư khóe miệng nâng lên: “Yên tâm đi, ta liền sẽ không thua!”"),t("br"),t("br"),n._v("“Có thể hay không thua, không phải từ ngươi định đoạt, ta coi như thực lực yếu, cũng có chiến thắng tỷ lệ... Thắng rượu ngon cho ngươi, thua cái gì đều không lấy ra, có thể hay không quá chiếm tiện nghi?”"),t("br"),t("br"),n._v("Mí mắt vừa nhấc, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Vậy ngươi nói làm sao bây giờ?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Vân sư khẽ nói."),t("br"),t("br"),n._v("Chén rượu này, là Nhạc đường chủ cho đối phương, thua cho mình, tương đương với mất đi rượu ngon, thắng còn không có cá cược, là có chút không công bằng."),t("br"),n._v(" “Rất đơn giản, ngươi ta đều biết Thiên Thần túy giá trị, một chén này, chừng mười cái tinh nguyên linh thạch thượng phẩm... Như vậy, ta cũng không cá cược mười cái, ngươi lấy ra năm cái làm tiền đặt cược, ta thua, rượu ngon lấy đi, thắng... Linh thạch về ta!”"),t("br"),t("br"),n._v("Trương Huyền cười cười."),t("br"),t("br"),n._v("Cái này Nhạc Nhất Toàn, mục đích là để cho mình xấu mặt, nhưng mà... Tài tình lợi dụng một chút, ngược lại là có thể đem đám này “Thiên tài” vơ vét một chút!"),t("br"),t("br"),n._v("Mười cái tinh nguyên linh thạch thượng phẩm, những người này khả năng không bỏ ra nổi đến, nhưng mà năm cái... Lấy thân phận của bọn hắn, không khó lắm."),t("br"),t("br"),n._v("“Năm cái tinh nguyên linh thạch thượng phẩm?”"),t("br"),t("br"),n._v("Không nghĩ tới dưới cá cuộc như thế lớn, Vân sư nhíu nhíu mày, có chút chần chờ."),t("br"),t("br"),n._v("“Coi như không dám, cái kia thật không tiện, ta trước đem rượu này uống...”"),t("br"),t("br"),n._v("Lười nhác nhiều lời, bưng chén rượu lên, Trương Huyền liền muốn một uống mà xuống."),t("br"),t("br"),n._v("“Chậm đã!”"),t("br"),t("br"),n._v("Chén rượu mới giơ lên, lại một cái hét lớn vang lên, ngay sau đó lại một cái danh sư đứng dậy: “Lão Vân, ngươi không có can đảm, chén rượu này liền thuộc về ta...”"),t("br"),t("br"),n._v("Nói hết, một tiếng cười khẽ, bàn tay một trảo, một cái hộp ngọc xuất hiện ở trước mặt mọi người, nhẹ nhàng mở ra, bên trong năm cái linh thạch chiếu lấp lánh."),t("br"),t("br"),n._v("“Đây là năm cái tinh nguyên linh thạch thượng phẩm, ngươi kiểm tra một chút!”"),t("br"),t("br"),n._v("Bàn tay lắc một cái, linh thạch bay tới."),t("br"),t("br"),n._v("Tiện tay nhận lấy, Trương Huyền nhìn một cái, nhẹ gật đầu."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("“Vậy thì tốt, bắt đầu đi! Lần đầu thử nghiệm thời điểm, khảo hạch là thực lực, ta liền cùng ngươi tỷ thí... Có dám hay không ứng chiến?”"),t("br"),t("br"),n._v("Thấy hắn nhận lấy, cũng không lo lắng sẽ giấu bên dưới, vị danh sư này nhẹ nhàng cười một tiếng, hai bước tới đến đại điện trung tâm."),t("br"),t("br"),n._v("Điện đường rộng lớn, mặc dù hai bên ngồi hơn ba mươi vị tài tuấn, trung tâm vẫn như cũ chỗ trống mấy trăm mét vuông địa phương, luận võ loại hình, hoàn toàn không bị ảnh hưởng."),t("br"),t("br"),n._v("“Luận võ?” Trương Huyền dường như không còn trước đó tự tin, thận trọng nhìn lại: “Không biết, tu vi của ngươi cao bao nhiêu?”"),t("br"),t("br"),n._v("“Tại hạ Nguyên Thần cảnh đỉnh phong... Nếu như sợ, có thể áp chế giống như ngươi, Nguyên Thần cảnh trung kỳ!”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, vị danh sư này mặt mũi cười nhạo."),t("br"),t("br"),n._v("Còn không có chiến đấu, cũng đã yếu đi khí thế, không cần so, đều biết ai muốn thắng."),t("br"),t("br"),n._v("“Cái kia không còn gì tốt hơn!”"),t("br"),t("br"),n._v("Thở phào nhẹ nhõm, Trương Huyền đem chén rượu để xuống, cũng đứng dậy, đi vào trung tâm trống trải địa phương."),t("br"),t("br"),n._v("“Tại hạ Bắc Nghiệp đế quốc Lưu Sùng Tân!”"),t("br"),t("br"),n._v("Thấy hắn đi tới, Lưu Sùng Tân lông mày giương lên, lực lượng toàn thân, áp chế lại, thời gian nháy mắt, liền cùng Trương Huyền đồng dạng, đạt đến Nguyên Thần cảnh trung kỳ."),t("br"),t("br"),n._v("“Thanh Nguyên đế quốc, Trương Huyền!”"),t("br"),t("br"),n._v("Gật gật đầu, Trương Huyền trù trừ một chút, ôm quyền: “Mong rằng hạ thủ lưu tình!”"),t("br"),t("br"),n._v("“Ha ha, ta biết!”"),t("br"),t("br"),n._v("Thấy cái tên này, mới vừa rồi còn khí thế hung hăng muốn cược tiền đánh bạc, thời gian nháy mắt liền biến thành như vậy, Lưu Sùng Tân lộ ra khinh thường, lật bàn tay một cái, lòng bàn tay bắt đầu ửng hồng: “Bắt đầu đi!”"),t("br"),t("br"),n._v("Tiến về phía trước một bước bước ra, lăng không bắt đến."),t("br"),t("br"),n._v("“Lưu sư thoạt nhìn lỗ mãng, trên thực tế lại hết sức cẩn thận, vừa ra tay liền đem bản thân Thiết Tâm chưởng phát huy ra!”"),t("br"),t("br"),n._v("“Có thể thông qua Triệu sư tuyển chọn, nói rõ tại hai vị kia dự thính trong tay, chống nổi ba chiêu, làm sao có thể đơn giản, Lưu sư lỗ mãng, chỉ là cố ý biểu hiện làm cho đối phương chủ quan, trên thực tế, chân chính chiến đấu, tất nhiên khắp nơi cẩn thận!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Cái khác quen thuộc Lưu Sùng Tân tu luyện giả, lặng lẽ thảo luận."),t("br"),t("br"),n._v("Có thể theo phong hào đế quốc, trổ hết tài năng, bị tuyển chọn bên trên, cái nào lại là nhân vật đơn giản, Trương Huyền mặc dù biểu hiện tạm được, đối phương vẫn như cũ sẽ không xem thường."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Bàn tay rơi xuống, không gian chung quanh bắt đầu ửng hồng, hình như bị lửa than thiêu đốt đồng dạng."),t("br"),t("br"),n._v("Thiết Tâm chưởng, Bắc Nghiệp đế quốc Danh Sư đường tuyệt kỹ, Thánh vực trung kỳ võ kỹ, toàn lực thi triển, uy lực cực mạnh, so với Đại Tinh La Chỉ Lực, đều không kém chút nào."),t("br"),t("br"),n._v("“Đến hay lắm!”"),t("br"),t("br"),n._v("Trương Huyền dường như cũng khơi dậy hào hùng, hét lớn một tiếng, cổ tay khẽ đảo, tiến lên đón."),t("br"),t("br"),n._v("Hắn chiêu này, đồng dạng là một cái Thánh vực trung kỳ võ kỹ, Hạc Vân chưởng!"),t("br"),t("br"),n._v("Thoạt nhìn hùng hồn, trên thực tế linh động, rõ ràng không dám đón đỡ đối phương Thiết Tâm chưởng, ý định lấy xảo phá chi."),t("br"),t("br"),n._v("“Muốn lấy xảo? Nằm mơ!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Lưu Sùng Tân lòng bàn tay đột nhiên đè ép, không gian bốn phía giống như là bị trực tiếp bao phủ, Hạc Vân chưởng tài tình liền rốt cuộc không thi triển ra được, chỉ có thể dọc theo lộ tuyến chào đón."),t("br"),t("br"),n._v("Nhất lực hàng thập hội!"),t("br"),t("br"),n._v("Hắn dùng chân khí cường đại tu vi, trực tiếp đem Trương Huyền biến chiêu áp chế."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Song chưởng đối đầu, Trương Huyền sắc mặt trắng nhợt, liên tục lui về phía sau mấy bước, giống như là không chịu nổi đối phương lực lượng cuồng mãnh, bàn tay xuất hiện vặn vẹo, giấu ở ống tay áo bên dưới, kìm lòng không được run rẩy."),t("br"),t("br"),n._v("“Ta còn tưởng rằng bao nhiêu lợi hại, liền điểm ấy công phu, cũng muốn uống rượu?”"),t("br"),t("br"),n._v("Một chiêu đến tay, Lưu Sùng Tân lông mày giương lên, cười to một tiếng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nháy nháy mắt, Trương Cửu Tiêu khóe miệng giật một cái: “Trương sư... Làm sao trở nên yếu như vậy? Chỉ mong vị này Lưu sư may mắn...”"),t("br"),t("br"),n._v("Trong mắt đầy là đồng tình."),t("br"),t("br"),n._v("Mỗi lần Trương sư thời điểm như vậy, liền có người xui xẻo, hắn kinh nghiệm nhiều lần, xem như có kinh nghiệm."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1263"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1265"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);