(window.webpackJsonp=window.webpackJsonp||[]).push([[31],{386:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1004-bai-kien-tien-to"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1004-bai-kien-tien-to"}},[n._v("#")]),n._v(" Chương 1004: Bái kiến Tiên Tổ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Tiên Thiên độc hồn thể?”"),t("br"),t("br"),n._v("Dừng lại một chút, điện chủ nhẹ gật đầu."),t("br"),t("br"),n._v("Tiên Thiên độc hồn thể, tên như ý nghĩa, hồn phách đối độc dược không sợ hãi chút nào, bách độc bất xâm, loại này thể chất đặc thù, vài vạn năm đến, loại trừ vị kia Độc điện người sáng lập, chưa hề xuất hiện qua, cũng chưa từng nghe nói qua, nếu như có thể xác định, tàn hồn là loại thể chất này, hầu như đã có thể chứng minh, đối phương chính là khai phái tổ sư!"),t("br"),t("br"),n._v("Cho dù không phải, nắm giữ loại này năng lực đặc thù, chỉ cần tiếp tục nghiên cứu độc dược, cũng tất nhiên có thể càng đi càng xa, danh dương thiên hạ, đạt tới cửu tinh độc sư cấp bậc, đều là dễ như trở bàn tay."),t("br"),t("br"),n._v("Nghĩ tới những thứ này, điện chủ không có trầm tư quá lâu, cổ tay khẽ đảo, lòng bàn tay nhiều ra một cái bình ngọc."),t("br"),t("br"),n._v("Cùng cái khác chứa đựng thuốc cái bình, không quá tương đồng, bàn tay lớn nhỏ, bề ngoài dùng đặc thù văn tự điêu khắc một chút kỳ quái phong ấn."),t("br"),t("br"),n._v("Nhắm mắt lại “Tôn Cường” thân thể cứng đờ, giống như là phát hiện cái gì khó có thể tin chuyện, ngay sau đó lần nữa khôi phục không hề bận tâm biểu lộ."),t("br"),t("br"),n._v("“Đây là có thể độc chết linh hồn đồ vật, ! Lấy Dị Linh tộc nhân sát khí là nguyên liệu, phối hợp vô số trân quý dược liệu, cô đọng mà thành, là Hồng Viễn Độc điện một vị Tiên Tổ lưu lại, Thánh vực tứ trọng cường giả hồn phách, đều có thể dễ dàng độc chết! Hiện tại ta, cũng phối trí không ra.”"),t("br"),t("br"),n._v("Điện chủ giải thích một tiếng, nhìn lại: “Nếu như ngươi thật sự là Tiên Thiên độc hồn thể, tất nhiên có thể chặn lại, sẽ không nhận tổn hại.”"),t("br"),t("br"),n._v("Đo lường Tiên Thiên độc hồn thể, không có những biện pháp khác, chỉ có một cái... Lấy độc kiểm tra."),t("br"),t("br"),n._v("Chỉ cần không có việc gì, tương đương với không nói lời nói dối."),t("br"),t("br"),n._v("Nếu là chết rồi, cũng coi như thay tất cả trưởng lão báo thù."),t("br"),t("br"),n._v("“Tốt?”"),t("br"),t("br"),n._v("“Tôn Cường” nhẹ gật đầu, ngón tay nhất câu, bình ngọc chậm rãi bay tới."),t("br"),t("br"),n._v("Không chần chờ chút nào, trực tiếp mở ra nắp bình, lập tức, một cỗ đặc thù sát khí cuộn trào mãnh liệt mà ra, đâm vào trong óc, tựa như gió lớn sóng lớn."),t("br"),t("br"),n._v("Điện chủ đám người vội vàng nhìn sang, phát hiện bị trong bình khí độc công kích, trước mắt “Tôn Cường”, cũng chưa từng xuất hiện suy yếu vẻ, ngược lại vẻ mặt càng ngày càng hồng nhuận, dường như ăn cái gì vật đại bổ."),t("br"),t("br"),n._v("Thật giống như đây không phải là độc vật, mà là một loại nào đó thuốc bổ đồng dạng."),t("br"),t("br"),n._v("“Truyền thuyết Tiên Thiên độc thể, có thể ăn độc mà tiến, chỉ cần có đầy đủ độc dược, tu vi cùng lực lượng đều có thể nhanh chóng gia tăng, chẳng lẽ... Là thật?”"),t("br"),t("br"),n._v("Một vị trưởng lão tự lẩm bẩm."),t("br"),t("br"),n._v("Tiên Thiên độc thể tồn tại trong truyền thuyết, bọn họ đều không có gặp qua, nghe nói, người nắm giữ loại thể chất này, thích hợp nhất tu luyện độc sư cái nghề nghiệp này, người khác nuốt độc dược sẽ bị độc chết, mà loại người này, lại có thể đem loại này gạt bỏ sinh cơ thuốc chuyển hóa làm lực lượng, từ đó tu vi tăng nhiều."),t("br"),t("br"),n._v("[ngantruyen.com ]"),t("br"),n._v("Chính là bởi vì có loại năng lực này, mới bị vô số độc sư hâm mộ và ghen ghét."),t("br"),t("br"),n._v("Thanh Thiền sát độc là một loại chủ công linh hồn thuốc, đối thân thể không có ảnh hưởng, đối phương sau khi thôn phệ, linh hồn không những không bị thương, còn giống như đại bổ, đủ để chứng minh vấn đề..."),t("br"),t("br"),n._v("Chỉ sợ thật sự là... Tiên Thiên độc hồn thể!"),t("br"),t("br"),n._v("Nắm giữ loại thể chất này, lại có thể một cái xem thấu Độc điện huyền bí của đại trận, lại thêm vượt cấp khiêu chiến năng lực, trước mắt vị này coi như không phải khai phái tổ sư, cũng tất nhiên cùng hắn có quan hệ lớn lao."),t("br"),t("br"),n._v("“Hiện tại có thể hay không xác nhận?”"),t("br"),t("br"),n._v("Trong lòng đang suy tư, đối diện Tôn Cường thanh âm vang lên lần nữa."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Điện chủ chần chừ."),t("br"),t("br"),n._v("Mặc dù xác định đối phương là Tiên Thiên độc hồn thể, nhưng muốn nói là Độc điện khai phái Tiên Tổ, vẫn cảm thấy có chút phóng đãng."),t("br"),t("br"),n._v("“Làm sao? Còn chưa tin? Nếu không phải Tôn Cường khổ sở cầu khẩn, để cho ta đừng chém giết, chỉ bằng vào loại này nghi ngờ, liền sẽ đem bọn ngươi toàn bộ giết sạch!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nương theo một tiếng tức giận, một cỗ cường đại tới cực điểm khí tức, từ nơi không xa “Tôn Cường” trong cơ thể bộc phát ra, thật giống như nhân ảnh trước mắt, giống như cửu thiên mà đến thần chi, mang theo không thể khinh nhờn uy nghiêm."),t("br"),t("br"),n._v("“Hồng Viễn Độc điện điện chủ, Nhược Thanh Viễn gặp qua tổ sư!”"),t("br"),t("br"),n._v("Cảm nhận được cỗ khí tức này, điện chủ lại không hoài nghi, trực tiếp quỳ rạp xuống đất."),t("br"),t("br"),n._v("Đối phương không sợ kịch độc, nắm trong tay toàn bộ Độc điện đại trận, tới lui tự nhiên, lực lượng càng là vô cùng vô tận, tất cả mọi người không phải là đối thủ... Như vậy ưu thế bên dưới, hoàn toàn có thể đem nhóm người mình toàn bộ đồ sát sạch sẽ, không cần thiết nói nhảm để giải thích."),t("br"),t("br"),n._v("Có lẽ, Độc điện tổ sư, thật ẩn thân tại Địa Ngục Thất Tâm liên bên trong, một sợi tàn hồn còn sống!"),t("br"),t("br"),n._v("Nghĩ thông suốt những này, kính sợ đồng thời cũng đầy là kích động."),t("br"),t("br"),n._v("Khai phái tổ sư, thế mà xuất hiện ở đây, không nói cái khác, chỉ cần hơi chỉ điểm một ít, liền có thể cam đoan thực lực của bọn hắn toàn bộ tăng nhiều, toàn bộ Hồng Viễn Độc điện lực lượng tăng lên một đoạn dài, đều ở trong tầm tay!"),t("br"),t("br"),n._v("Có thể nói đối với hắn mà nói, tuyệt đối là một lần cực lớn cơ hội."),t("br"),t("br"),n._v("“Coi như có chút nhãn lực! Ta sở dĩ có thể còn sống xuống, nhờ có vị này Tôn Cường!”"),t("br"),t("br"),n._v("Thấy đối phương thừa nhận, Trương Huyền thở phào nhẹ nhõm, duỗi ra ngón tay, hướng mình chĩa sang, con mắt vẫn không có mở ra, khuôn mặt nhưng trở nên nghiêm túc: “Hắn là ân nhân cứu mạng của ta, không có hắn, ta chỉ sợ sớm đã tan thành mây khói, hoàn toàn tử vong! Bởi vậy, về sau muốn đối hắn nói gì nghe nấy, phân phó của hắn, chính là ta ý tứ, có dám vi phạm... Đừng trách ta, hạ thủ không lưu tình!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nhược Thanh Viễn điện chủ cùng còn thừa lại tất cả trưởng lão đồng thời gật đầu."),t("br"),t("br"),n._v("Tổ sư khai báo, sao dám không theo?"),t("br"),t("br"),n._v("“Mặt khác, những trưởng lão kia cùng vị kia Minh phó điện chủ là ta giết, không có quan hệ gì với hắn, nếu như muốn báo thù, tới tìm ta chính là...” Trương Huyền nói tiếp."),t("br"),t("br"),n._v("“Chúng ta không dám...”"),t("br"),t("br"),n._v("Nhược Thanh Viễn liền vội vàng gật đầu: “Minh phó điện chủ đám người, đắc tội tổ sư, bị giết bị thương, đều là trừng phạt đúng tội, hậu bối không dám báo thù!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu, thanh âm yên tĩnh lại, lập tức chậm rãi mở mắt, tràn đầy mê man cùng đơn thuần, tựa hồ đối với chuyện lúc trước hoàn toàn không biết gì cả: “Vừa rồi chuyện gì xảy ra? Điện chủ... Còn xin không nên làm khó Hứa trưởng lão, hắn thật sự là vô tội...”"),t("br"),n._v("Nhìn hắn một mặt không rõ ràng cho lắm bộ dạng, biết “Tổ sư” hồn phách, lại trầm tịch đi xuống, Nhược Thanh Viễn vội vàng ôm quyền: “Tôn độc sư nói đùa, tổ sư đã cùng chúng ta nói rõ, Hoàng trưởng lão mấy người cũng là gieo gió gặt bão, Hứa trưởng lão chẳng những không có sai lầm, ngược lại công lao rất cao, cần ngợi khen, từ hôm nay trở đi, liền thay thế Minh Chân, trở thành Độc điện phó điện chủ...”"),t("br"),t("br"),n._v("“Trở thành phó điện chủ?”"),t("br"),t("br"),n._v("Hứa trưởng lão ngẩn ngơ: “Điện chủ, ta tư lịch còn chưa đủ...”"),t("br"),t("br"),n._v("Hắn chỉ là lục tinh đỉnh phong, muốn trở thành phó điện chủ, ít nhất cũng phải đạt tới thất tinh cấp bậc, tùy tiện đề bạt, chỉ sợ những người khác hội nghị luận nhao nhao."),t("br"),t("br"),n._v("“Ta nói ngươi đủ là đủ rồi, không cần từ chối!”"),t("br"),t("br"),n._v("Nhược Thanh Viễn khoát tay áo."),t("br"),t("br"),n._v("Đem tổ sư mời đến, công lao bao lớn, không thể đo lường, tương lai còn trông cậy vào đối phương, nói tốt vài câu, để tổ sư cho thêm bọn họ nói một chút khóa đây."),t("br"),t("br"),n._v("“Tốt a...”"),t("br"),t("br"),n._v("Thấy không cách nào chối từ, Hứa trưởng lão đành phải nhẹ gật đầu, lần nữa nhìn về phía cách đó không xa Tôn Cường, trong mắt tràn đầy bái phục."),t("br"),t("br"),n._v("Khó trách hắn bách độc bất xâm, đối độc lý giải sâu sắc như vậy, nghe một hồi, để hắn đều hiểu ra... Nguyên lai tổ sư tàn hồn đi vào trong cơ thể, loại cơ duyên này, cũng không phải người người đều có thể hưởng thụ được!"),t("br"),t("br"),n._v("“Xem ra lừa gạt đi qua...”"),t("br"),t("br"),n._v("Thấy mọi người đối với hắn thân phận mới tin tưởng không nghi ngờ, Trương Huyền lúc này mới thở phào nhẹ nhõm, lần nữa nhìn về phía lòng bàn tay cái bình bên trên văn tự, lông mày nhảy một cái, nhịn không được hướng cách đó không xa điện chủ nhìn sang."),t("br"),t("br"),n._v("“Điện chủ, ta muốn hỏi một chút, phía trên này, là văn tự gì... Ngươi nhưng có biết?”"),t("br"),t("br"),n._v("Trên bình ngọc phong ấn Thanh Thiền sát độc văn tự, lúc trước hắn gặp qua... Cùng Ngô Dương Tử lưu lại tấm bản đồ kia bên trên giống nhau như đúc!"),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, tinh thần quét đến thời điểm, tràn đầy kích động, giờ phút này, cũng nhịn không được nữa, hỏi lên."),t("br"),t("br"),n._v("“Cái này? Ta cũng không biết... Hai ngàn năm trước, chúng ta Độc điện một vị điện chủ, theo một chỗ di tích nhận được một cái sách cổ, trong đó chính là dùng loại này văn tự viết, bởi vì không biết, cũng không có nghiên cứu ra cái gì, có điều, một lần thỉnh thoảng cơ hội phát hiện, loại này văn tự, đối phong ấn sát khí loại hình, có hiệu quả, cho nên... Liền sao chép tới...”"),t("br"),t("br"),n._v("Cẩn thận suy nghĩ một chút, Nhược Thanh Viễn nói."),t("br"),t("br"),n._v("“Sách cổ? Phong ấn sát khí?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Hắn là nhìn trên bản đồ đến những văn tự này, làm sao kéo tới phong ấn sát khí lên?"),t("br"),t("br"),n._v("“Quyển kia sách cổ... Còn tại? Không biết... Có thể hay không cho ta mượn nhìn xem?”"),t("br"),t("br"),n._v("Suy nghĩ một chút, không hiểu chuyện gì xảy ra, Trương Huyền hỏi."),t("br"),t("br"),n._v("Địa đồ ghi chú chính là bình thường địa danh, không có sai lầm, thư viện phân tích không ra cái gì, nhưng nếu có một bản hoàn chỉnh thư tịch liền tốt, hoàn toàn có thể suy đoán ra văn tự ý tứ."),t("br"),t("br"),n._v("Lại nói, hai ngàn năm trước... Dường như cùng Tử Diệp vương nói địa đồ xuất hiện thời gian tương đương, cả hai sẽ có hay không có cái gì liên hệ?"),t("br"),t("br"),n._v("“Sách cổ... Lúc trước để quyển sách này, thương vong Độc điện không ít tinh anh, vị điện chủ kia cảm thấy không rõ, trước khi chết, liền bồi hắn cùng một chỗ mai táng...”"),t("br"),t("br"),n._v("Nhược Thanh Viễn lắc đầu."),t("br"),t("br"),n._v("“Mai táng?”"),t("br"),t("br"),n._v("Trương Huyền cau mày: “Không biết chôn ở nơi nào?”"),t("br"),t("br"),n._v("“Tại điện chủ lăng...”"),t("br"),t("br"),n._v("Nhược Thanh Viễn nói."),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Nghe được ở đây, Trương Huyền không hỏi thêm nữa."),t("br"),t("br"),n._v("Điện chủ lăng thuộc về cấm địa một trong, loại trừ điện chủ không người có thể đi vào, hỏi quá nhiều, tất nhiên gây nên đối phương hoài nghi."),t("br"),t("br"),n._v("Xác nhận trước mắt vị này “Tôn Cường” thân phận, Nhược Thanh Viễn không dám nhiều lời, lần nữa an bài chỗ ở mới."),t("br"),t("br"),n._v("Còn khảo hạch độc sư, đối phương liền độc trùng của hắn đều có thể giết chết, tiếp tục khảo hạch cũng mất ý nghĩa, trực tiếp hỗ trợ xin thất tinh đẳng cấp huy chương."),t("br"),t("br"),n._v("“Ta đi tàng thư điện nhìn một chút thư...”"),t("br"),t("br"),n._v("Có thất tinh huy chương, lại thêm “Tổ sư” thân phận, Trương Huyền đi tàng thư điện đọc sách, đã không cần lén lút, hoàn toàn có thể quang minh chính đại."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nhược Thanh Viễn đám người, không nói thêm lời, đem hắn đưa đến tàng thư cửa đại điện, thấy hắn đi vào, lúc này mới nguyên một đám lẫn nhau nhìn lại."),t("br"),t("br"),n._v("“Hứa phó điện chủ, chư vị, đều đi chỗ ở của ta!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Mọi người nhẹ gật đầu, đi theo điện chủ sau lưng, quay trở về, thời gian không dài, đến hắn sân nhỏ, khởi động cắt đứt khí tức trận pháp, Nhược Thanh Viễn lần nữa nhìn về phía mọi người."),t("br"),t("br"),n._v("“Chư vị, đối với chuyện ngày hôm nay, các ngươi thấy thế nào?”"),t("br"),t("br"),n._v("“Đã xác định vị này Tôn Cường trong cơ thể hồn phách, là Tiên Thiên độc hồn thể, liền có thể xác nhận, hắn cùng khai phái tổ sư có vô cùng quan hệ mật thiết!”"),t("br"),t("br"),n._v("“Đúng vậy a! Tiên Thiên độc hồn thể... Loại trừ năm đó tổ sư, thật đúng là chưa từng nghe những người khác từng có!”"),t("br"),t("br"),n._v("Mấy vị trưởng lão đồng thời gật đầu."),t("br"),t("br"),n._v("Bọn họ đối “Tôn Cường” tổ sư thân phận, đã tin tưởng không nghi ngờ."),t("br"),t("br"),n._v("“Từ phó điện chủ ngươi đây? Có thể hay không đem tại vị này ‘Tôn độc sư’ gặp mặt tình cảnh, lại kỹ càng cùng mọi người nói một lần?”"),t("br"),t("br"),n._v("Nhược Thanh Viễn nhìn qua."),t("br"),t("br"),n._v("Canh thứ nhất rạng sáng liền đổi mới, tại chỉ dấu ấn phía trước! Đầu tháng tất cả mọi người có giữ gốc nguyệt phiếu, đầu cho lão nhai Ha! Đều rơi ra mười vị trí đầu..."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1012"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1014"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);