(window.webpackJsonp=window.webpackJsonp||[]).push([[312],{667:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1258-mong-rang-chi-giao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1258-mong-rang-chi-giao"}},[n._v("#")]),n._v(" Chương 1258: Mong rằng chỉ giáo")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1258: Mong rằng chỉ giáo"),t("br"),t("br"),n._v("Người bình thường xoay quanh nhiều, cũng sẽ không chịu nổi, từ đó làm cho buồn nôn nôn mửa."),t("br"),t("br"),n._v("Vị này Tiết tam công tử, vì thi triển Kim Ti trảm, xoay vòng lại nhiều, lại nhanh... Có thể hay không giống như người bình thường, cũng cảm thấy không chịu nổi?"),t("br"),t("br"),n._v("“Dĩ nhiên không phải...”"),t("br"),t("br"),n._v("Vị thứ nhất ngụy Xuất Khiếu mang theo im lặng, lắc đầu: “Nếu như không nhìn lầm, hẳn là vị này Trương Huyền thi triển kinh hồng sư năng lực, Tiết Ngưng nhất thời không tra, không có kháng trụ!”"),t("br"),t("br"),n._v("Không nói trước tu vi đạt tới Nguyên Thần cảnh đỉnh phong, đã không còn người bình thường phản ứng, cho dù có... Có thể thi triển chiêu này, khẳng định cũng là đi qua cường hóa huấn luyện, nếu không, người khác đánh rắm không có, bản thân trước nôn... Còn đánh cái cái rắm a?"),t("br"),t("br"),n._v("“Kinh hồng sư? Vừa rồi hắn giơ tay nhấc chân quả thực rất quái dị, chỉ là... Không có mặc hoa phục, không có khoa trương tứ chi động tác, liền có thể để Tiết Ngưng trúng chiêu?”"),t("br"),t("br"),n._v("Vị thứ hai ngụy Xuất Khiếu có chút không dám tin tưởng."),t("br"),t("br"),n._v("Tiết Ngưng thực lực, tại trong bọn họ, không tính là đỉnh phong, nhưng cũng tuyệt đối gần phía trước, nhân vật như vậy, bất kể ý chí vẫn là tu vi, đều không có kẽ hở, một cái nam nhân, không có mặc quần áo đẹp, càng không có tốt đẹp ngôn ngữ tay chân... Làm sao lại trúng chiêu?"),t("br"),t("br"),n._v("Kinh hồng sư không đều là vui tai vui mắt mỹ nữ, mới có thể để cho người say mê ư?"),t("br"),t("br"),n._v("Chẳng lẽ Tiết Ngưng... Thích nam phong?"),t("br"),t("br"),n._v("“Lợi hại kinh hồng sư, nhất cử nhất động, đều có thể kèm theo chuẩn mực, khiến người ta say mê... Dựa theo tình huống bình thường, muốn dùng loại thủ đoạn này công kích Tiết Ngưng, quả thực rất khó, nhưng đối phương phòng ngự thời gian dài như vậy, đánh lâu không xong, tâm tình vội vàng xao động phía dưới, xuất hiện sơ hở... Mới bị phát giác, một lần trúng chiêu!”"),t("br"),t("br"),n._v("Nhìn đối phương biểu lộ, vị thứ nhất ngụy Xuất Khiếu mặt mũi bất đắc dĩ, nói."),t("br"),t("br"),n._v("Thực lực của bọn hắn cứ việc tương đương, nhưng mình đối kinh hồng sư chức nghiệp biết rõ nhiều, đối phương am hiểu hơn phương diện khác."),t("br"),t("br"),n._v("“Bị động phòng ngự, tiêu hao lực lượng, để tâm cảnh xuất hiện biến hóa, chờ thời cơ, một lần ra tay... Khó trách Nhạc đường chủ, nói vị này Trương Huyền, là trong chúng ta mạnh nhất, quả thực không có đơn giản như vậy!”"),t("br"),t("br"),n._v("Nghe xong giải thích, vị thứ hai ngụy Xuất Khiếu lần nữa nhìn về phía mừng khấp khởi đem linh thạch thu hồi thanh niên, vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Vừa mới bắt đầu, cùng đám người đồng dạng, cảm thấy đối phương dựa vào là vận may, mới thắng Lưu Sùng Tân cùng Vân sư đám người, nhìn thấy giờ mới hiểu được, có thể bị Triệu Hưng Mặc tuyển chọn tới, làm sao có thể đơn giản?"),t("br"),t("br"),n._v("“Loại này cường giả, ta có hứng thú, Tôn huynh, để cho ta đi lên thử một chút, không được, ngươi tới!”"),t("br"),t("br"),n._v("Vị thứ hai ngụy Xuất Khiếu, gật đầu nói."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, Thiên Thần túy cứ việc để cho người ta điên cuồng, nhưng bọn hắn thực lực ở đây bày biện, còn có thể khống chế được, không cần thiết chạy tới tranh đoạt, nhưng... Vị này Trương Huyền quái dị như vậy, gây nên bọn họ hứng thú."),t("br"),t("br"),n._v("“Hai ta thực lực tương đương, ngươi không được, ta khẳng định cũng không được, không cần thiết lại đi mất mặt...”"),t("br"),t("br"),n._v("Vị thứ nhất ngụy Xuất Khiếu lắc đầu, nhắc nhở một câu: “Một lát nữa thời điểm chiến đấu, đừng dùng vũ khí, đóng chặt nguyên thần, phòng ngừa hắn kinh hồng vũ...”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Vị thứ hai gật đầu, đứng lên, hai tay ôm quyền: “Trương sư, tại hạ Tiềm Xung đế quốc danh sư, Mã Minh Hải, cầu chỉ giáo!”"),t("br"),t("br"),n._v("“Tiềm Xung đế quốc?”"),t("br"),t("br"),n._v("Đem linh thạch thu hồi, Trương Huyền đang cảm khái Nhạc đường chủ người tốt, tùy tiện cho một chén rượu, liền kiếm lời nhiều như vậy, lập tức nghe được tiếng la, nhìn lại."),t("br"),t("br"),n._v("“Không sai! Có dám nghênh chiến?” Mã Minh Hải nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Không dám!”"),t("br"),t("br"),n._v("Trương Huyền ngồi xuống."),t("br"),t("br"),n._v("“...” Mã Minh Hải thân thể nhoáng một cái."),t("br"),t("br"),n._v("Cái này lời thoại... Làm sao không theo lẽ thường ra bài đâu?"),t("br"),t("br"),n._v("Không dám... Có thể hay không đừng như thế hùng hồn?"),t("br"),t("br"),n._v("“Ta đồng dạng nguyện ra mười lăm viên tinh nguyên linh thạch thượng phẩm, xem như cá cược!”"),t("br"),t("br"),n._v("Tràn đầy buồn bực, cắn răng nói."),t("br"),t("br"),n._v("“Vừa rồi hao tổn quá lớn, đã không còn khí lực tái chiến!”"),t("br"),t("br"),n._v("“Hai mươi viên, nhiều hơn nữa không còn...”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Mã Minh Hải nói còn chưa dứt lời, chỉ thấy thanh niên chẳng biết lúc nào đã đi tới trong đại sảnh ở giữa, vung tay áo một cái, một bộ cao nhân bộ dáng: “Nói sớm ah, ta đã khôi phục, chúng ta bắt đầu đi!”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Mã Minh Hải kìm nén có chút không thở nổi."),t("br"),t("br"),n._v("Gặp qua không biết xấu hổ, chưa thấy qua không biết xấu hổ như vậy!"),t("br"),t("br"),n._v("Rõ ràng suy nghĩ để cho mình dùng linh thạch đánh cược, không phải nói mình không còn khí lực... Có thể muốn chút tiết tháo không?"),t("br"),t("br"),n._v("Tràn đầy buồn bực, đang nghĩ nói chuyện, bên tai truyền âm vang lên."),t("br"),t("br"),n._v("“Đừng bị hắn ảnh hưởng, hắn là cố ý chọc giận ngươi, để ngươi trước loạn tâm trí!”"),t("br"),t("br"),n._v("Chính là vừa rồi vị kia “Tôn huynh”."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Ánh mắt run lên, Mã Minh Hải gật đầu."),t("br"),t("br"),n._v("Mới vừa rồi bị đối phương tức giận, tâm tình quả thực có chút táo bạo, nhìn tới còn chưa bắt đầu chiến đấu, giao chiến đã bắt đầu."),t("br"),t("br"),n._v("Hít sâu một hơi, bình phục tâm cảnh, cổ tay khẽ đảo, lấy ra hai mươi viên tinh nguyên linh thạch thượng phẩm, nhẹ nhàng bắn ra, rơi vào đối phương trên bàn, vừa tung người đi tới trong đại sảnh ở giữa, cánh tay duỗi ra."),t("br"),t("br"),n._v("“Mời!”"),t("br"),t("br"),n._v("Đã theo vừa rồi phẫn nộ bên trong, khôi phục lại."),t("br"),t("br"),n._v("“Mời!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Oanh!"),t("br"),t("br"),n._v("Chưa nói nói nhảm, Mã Minh Hải đã áp chế được rồi tu vi, chân khí trong cơ thể phát ra nổ giống như vang lên, thẳng tắp lao đến."),t("br"),t("br"),n._v("Động tác của hắn, cùng trước đó mấy vị khác biệt, không có quá nhiều cuốn hút, có chỉ là cuồng bạo cùng hung mãnh, để cho người ta không thể không phòng, không thể không phòng."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Trương Huyền âm thầm khen ngợi."),t("br"),t("br"),n._v("Không hổ là thiên tài, không thể khinh thường."),t("br"),t("br"),n._v("Chiến đấu mấy trận, chính mình cũng không có thi triển toàn lực, đối phương nhất định là nhìn ra cái gì, lúc này mới từ bỏ cuốn hút, trực tiếp dùng hung mãnh nhất lực lượng công kích!"),t("br"),t("br"),n._v("Như vậy trải qua, coi như mình muốn thi triển những phương pháp khác, cũng bị chèn ép không có cách nào."),t("br"),t("br"),n._v("Bàn tay vạch một cái, chân khí sôi trào, giữa hai người lập tức giống như là nhiều hơn một cái sông lớn."),t("br"),n._v(" Hắn không có thi triển không khí ngưng trệ, mà là vận dụng chân khí của mình."),t("br"),t("br"),n._v("Cứ việc chỉ dùng một phần mười không đến uy lực, nhưng cũng không phải người bình thường có thể ứng phó."),t("br"),t("br"),n._v("Khí tức sôi trào, giống như là đại điện phía trong, nhiều hơn một đạo dải lụa màu bạc, để cho người ta hoa mắt, khó mà phân biệt."),t("br"),t("br"),n._v("“Cái này...” Đã tỉnh lại Lưu Sùng Tân, nuốt ngụm nước bọt, yết hầu phát khô."),t("br"),t("br"),n._v("Trước đó, hắn vẫn cảm thấy, là đối phương vận khí cứt chó, mới đưa bản thân đánh bại, tràn đầy không phục... Nhìn thấy giờ mới hiểu được, đối phương vừa rồi muốn động thủ lời nói, chỉ một chiêu này, liền chống lại không được!"),t("br"),t("br"),n._v("“Đến hay lắm!”"),t("br"),t("br"),n._v("Hưng phấn một tiếng hí dài, Mã Minh Hải hai tay xoa một cái, chập ngón tay làm kiếm, điểm tới."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Một đạo kiếm mang gào thét mà ra, thời gian nháy mắt liền đem chân khí sông dài xé thành hai nửa."),t("br"),t("br"),n._v("“Thật mạnh kiếm ý!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Giao chiến cao thủ, có không ít, chưa bao giờ thấy qua như vậy thuần túy kiếm ý, đều có thể đuổi kịp hắn một phần năm!"),t("br"),t("br"),n._v("Không hổ là Tiềm Xung đế quốc cao thủ, vừa ra tay, liền không tầm thường."),t("br"),t("br"),n._v("Ngón trỏ, ngón giữa cùng nổi lên, nhẹ nhàng bóp một cái, liền đem kiếm khí kẹp lấy, ngón tay xoay một cái, hướng về bắn ra."),t("br"),t("br"),n._v("“Đem hung mãnh như vậy kiếm khí nắm?”"),t("br"),t("br"),n._v("Vừa rồi vị kia “Tôn huynh” con ngươi co rụt lại."),t("br"),t("br"),n._v("Đối phương chiêu này cử trọng nhược khinh, cứ việc Mã Minh Hải kiếm khí, đâm thủng chân khí sông lớn, uy lực yếu bớt, cũng không phải bình thường người có thể nắm, vừa ý lực, lực lượng, thậm chí kiếm pháp, cũng phải có cực mạnh trình độ mới được!"),t("br"),t("br"),n._v("Liền xem như hắn, đều làm không được."),t("br"),t("br"),n._v("Vị này Trương Huyền, chẳng lẽ... Thật sự là giả heo ăn thịt hổ, thoạt nhìn rất bình thường, trên thực tế lại là cao thủ trong cao thủ?"),t("br"),t("br"),n._v("“Lợi hại, nhìn ngươi có thể hay không tiếp được ta chiêu này...”"),t("br"),t("br"),n._v("Thấy mình kiếm khí bị đối phương tiếp được, thậm chí phản kích tới, Mã Minh Hải không có sợ hãi, ngược lại hưng phấn mà con mắt tỏa ánh sáng, lần nữa thét dài, kiếm khí lan tràn, chung quanh thân thể giống như là lập tức trôi lơ lửng vô số thanh kiếm, chừng mấy trăm chuôi nhiều, lít nha lít nhít, che khuất bầu trời."),t("br"),t("br"),n._v("Đây đều là có kiếm khí hội tụ mà thành, cũng không phải là thực chất, nhưng mà nếu là mặc cho đâm vào trên thân, đồng dạng có khả năng đâm thủng làn da, để cho người ta bị thương nặng."),t("br"),t("br"),n._v("“Là Thiên Phong Kiếm Hải!”"),t("br"),t("br"),n._v("“Không sử dụng kiếm, liền ngưng tụ ra nhiều như vậy kiếm ý, sử dụng vũ khí, cái kia mạnh bao nhiêu?”"),t("br"),t("br"),n._v("“Kiếm khí tung hoành, uy hiếp bát phương, ta khẳng định là ngăn không được...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ở đây tất cả tài tuấn, tất cả đều vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Tiềm Xung đế quốc, nắm giữ Kiếm Trì, bởi vậy, đi ra cao thủ, đối kiếm đều có đặc thù lý giải."),t("br"),t("br"),n._v("Kiếm Trì bên trong, truyền thừa kiếm thuật rất nhiều, lợi hại nhất kiếm chiêu một trong, chính là cái này Thiên Phong Kiếm Hải, một chiêu thi triển, kiếm khí tràn ngập, căn bản không có cách nào phòng ngự."),t("br"),t("br"),n._v("Cùng đám người khiếp sợ đồng dạng, Trương Huyền cũng có chút kinh ngạc."),t("br"),t("br"),n._v("Thời gian nháy mắt, thi triển ra nhiều như vậy kiếm khí, thực lực quả nhiên mạnh mẽ, mặc dù so với bản thân, còn kém rất xa, nhưng mà Trịnh Dương, Vương Dĩnh, Lưu Dương... Ách, cùng cấp bậc, Tôn Cường khẳng định là chiến thắng chẳng qua!"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, cũng không tránh né hắn, đột nhiên miệng há mở, bỗng nhiên khẽ hấp."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Bay lên đầy trời kiếm khí, lập tức bị tụ lại, thẳng tắp hướng trong miệng tụ tập mà đi."),t("br"),t("br"),n._v("“Kẽo kẹt! Kẽo kẹt!”"),t("br"),t("br"),n._v("Thời gian nháy mắt, kiếm khí liền bị nuốt vào trong bụng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người chung quanh, tất cả đều dọa đến sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Nhiều như vậy kiếm khí, trốn cũng không kịp, cái tên này thế mà cái miệng nuốt..."),t("br"),t("br"),n._v("“Trả lại cho ngươi!”"),t("br"),t("br"),n._v("Khiếp sợ còn không có kết thúc, nuốt xong kiếm khí Trương Huyền, một tiếng hô khẽ, vừa rồi vô số đạo kiếm khí, vậy mà tại hắn trong bụng, tạo thành một chuôi hoàn toàn do chân khí hình thành đoản kiếm, nương theo la lên, thẳng tắp hướng Mã Minh Hải đâm tới."),t("br"),t("br"),n._v("Đoản kiếm đón gió liền dài, thay đổi một trượng có dư, còn chưa tới đến trước mặt, liền xé rách không khí, tựa hồ muốn người nghiền nát."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Không nghĩ tới trước mắt vị này còn có chiêu này, Mã Minh Hải sắc mặt trắng nhợt, thời gian nháy mắt, lực lượng trong cơ thể liên tục tăng lên, cũng không còn cách nào áp chế."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Liên tục lui về sau vài chục bước, ống tay áo tại Kiếm Phong bên dưới, vỡ vụn thành từng mảnh từng mảnh vải rách."),t("br"),t("br"),n._v("Khôi phục lại ngụy Xuất Khiếu cảnh giới, mới ngăn lại công kích, dù vậy, thân thể cũng đau sắp xé rách ra đến, quần áo cùng bị cởi hết đồng dạng, mất hết mặt mũi."),t("br"),t("br"),n._v("“Ngươi rất mạnh...”"),t("br"),t("br"),n._v("Hít sâu một hơi, đang nghĩ tiếp tục đem tu vi đè xuống, thi triển ra tuyệt chiêu lợi hại nhất, tiếp tục chiến đấu, liền nghe đến một thanh âm vang lên: “Được rồi, ngươi không phải Trương sư đối thủ!”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy một bên Nhạc đường chủ khoát tay áo."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thở ra một hơi, mặc dù trong lòng không cam lòng, nhưng cũng biết, đường chủ nói không sai, hắn cùng cấp bậc chỉ sợ thật không cách nào chiến thắng trước mắt vị thanh niên này."),t("br"),t("br"),n._v("“Trương sư thực lực mạnh mẽ, chuyên gia rõ như ban ngày, được rồi, tỷ thí như vậy coi như thôi...”"),t("br"),t("br"),n._v("Khoát tay áo, Nhạc Nhất Toàn tràn đầy buồn bực."),t("br"),t("br"),n._v("Vốn nghĩ đến, mượn nhờ đám người tay, thật tốt dạy bảo một chút cái tên này, nằm mơ đều không có nghĩ đến, không những không thể toại nguyện, ngược lại để to lớn làm náo động..."),t("br"),t("br"),n._v("“Như vậy coi như thôi? Bắt nạt xong ta liền không sao? Làm gì có chuyện ngon ăn như thế!”"),t("br"),t("br"),n._v("Mí mắt vừa nhấc, Trương Huyền ngắt lời hắn, thanh âm càng ngày càng vang, chấn toàn bộ đại điện “Ong ong!” Vang vọng."),t("br"),t("br"),n._v("“Thanh Nguyên đế quốc Trương Huyền, muốn khiêu chiến một chút, Tiềm Xung đế quốc Nhạc đường chủ, mong rằng chỉ giáo!”"),t("br"),t("br"),n._v("(Ngày mai thi đại học, chúc chuyên gia thi đại học thắng ngay từ trận đầu, thi đậu ưa thích trường học. Lão nhai, năm nay mới 18 tuổi, cũng tại chuẩn bị kiểm tra, chuẩn bị tham gia thi đại học, hi vọng các vị ca ca tỷ tỷ cho thêm điểm nguyệt phiếu làm trống lệ. Cám ơn)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1266"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1268"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);