(window.webpackJsonp=window.webpackJsonp||[]).push([[317],{672:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1262-thap-ngon-co-thu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1262-thap-ngon-co-thu"}},[n._v("#")]),n._v(" Chương 1262: Tháp ngôn cổ thư")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1262: Tháp ngôn cổ thư"),i("br"),i("br"),n._v("Người bán hàng này, chỉ có chừng ba mươi tuổi, hết sức trẻ tuổi, tu vi cũng chỉ Bán Thánh cấp bậc, vốn đối hối đoái linh thạch, cũng không thèm để ý, khi thấy trên đất vô số đồ vật, con mắt lập tức thẳng."),i("br"),i("br"),n._v("Chợ đen giá cả đắt đỏ, bởi vậy, loại trừ cần dùng gấp, có rất ít người lựa chọn ở đây hối đoái, lúc bình thường, hầu như không có người, hiện tại tùy tiện tới khách hộ, một chút ném ra nhiều đồ như vậy, quả thực không thể tưởng tượng nổi."),i("br"),i("br"),n._v("“Vị công tử này, những này đều hối đoái?”"),i("br"),i("br"),n._v("Chậm một hồi, mới khôi phục tới, phục vụ viên nhịn không được nói."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("Những vật này, có theo Thanh Điền nhất mạch giành được, cũng có theo Thanh Nguyên hoàng thất bắt chẹt tới, đối với trước kia hắn tới nói, xem như vô thượng bảo vật, nhưng tu vi đạt tới Nguyên Thần cảnh hậu kỳ, đã không còn tác dụng."),i("br"),i("br"),n._v("Cùng hắn giữ lại chiếm không gian, còn không bằng toàn bộ hối đoái, đối tăng thực lực lên cũng có trợ giúp."),i("br"),i("br"),n._v("“Tốt, ta hiện tại liền tiến hành tính toán...”"),i("br"),i("br"),n._v("Biết tới cái khách hàng lớn, phục vụ viên không dám thất lễ, vội vã đi vào trong, thời gian không dài đem một cái lão giả mời tới."),i("br"),i("br"),n._v("Đem đồ vật toàn bộ kiểm lại một lần, lão giả nhìn lại: “Vật phẩm của ngươi toàn bộ cộng lại, đại khái có thể hối đoái một trăm mười ba viên tinh nguyên linh thạch thượng phẩm, đây là danh sách...”"),i("br"),i("br"),n._v("Trương Huyền khoát tay áo: “Không cần, đem linh thạch lấy ra đi!”"),i("br"),i("br"),n._v("Kiểm kê vật phẩm thời điểm, đã hạch toán qua, cùng đối phương tính toán không kém nhiều."),i("br"),i("br"),n._v("“Tốt! Đi lấy linh thạch tới!” Lão giả nhẹ gật đầu."),i("br"),i("br"),n._v("Phục vụ viên đi vào, thời gian không dài, đem một viên trữ vật giới chỉ đưa tới."),i("br"),i("br"),n._v("Tiện tay nhận lấy, Trương Huyền nhìn một chút, một trăm mười ba viên linh thạch, không nhiều không ít."),i("br"),i("br"),n._v("“Trương công tử, ngươi trực tiếp hối đoái hơn một trăm viên linh thạch, đã tự động tấn cấp làm chúng ta thị trường khách quý, đêm nay vừa vặn có cái chỉ đối khách quý mở ra đấu giá hội, có muốn hay không tham gia?”"),i("br"),i("br"),n._v("Lần nữa cho một cái đại biểu khách quý thân phận lệnh bài, lão giả cười nhìn qua."),i("br"),i("br"),n._v("Một chút hối đoái nhiều linh thạch như vậy, coi như tại chợ đen cũng coi là khách hàng lớn, có đầy đủ tư cách trở thành khách quý."),i("br"),i("br"),n._v("“Đấu giá hội?” Trương Huyền lắc đầu: “Tạm thời không hứng thú!”"),i("br"),i("br"),n._v("Hắn hiện tại thiếu hụt là linh thạch, cái khác có muốn hay không đều không quan trọng."),i("br"),i("br"),n._v("“Công tử không nên gấp gáp cự tuyệt, hãy nghe ta nói hết, có lẽ sẽ thay đổi ý nghĩ!” Lão giả cười nói."),i("br"),i("br"),n._v("“Ồ?”"),i("br"),i("br"),n._v("“Lần đấu giá này vật phẩm, có hai cái Xuất Khiếu cảnh trung kỳ trung phẩm Thánh khí, còn có một viên chuẩn cấp tám đan dược, có thể tại ba cái hô hấp phía trong, khôi phục Xuất Khiếu cảnh cường giả trong cơ thể tất cả lực lượng, là độ Xuất Khiếu kiếp đồ tốt nhất!”"),i("br"),i("br"),n._v("Lão giả nói."),i("br"),i("br"),n._v("Xuất Khiếu kiếp, đối kháng thiên kiếp, một khi lực lượng kém, rất dễ dàng được giết, hình hồn câu diệt."),i("br"),i("br"),n._v("Vì phòng ngừa ngoài ý muốn, rất nhiều người sẽ tiêu phí vô số đại giới, mua sắm chuẩn cấp tám đan dược, có thể trong thời gian ngắn khôi phục nguyên khí, để thực lực một mực nằm ở đỉnh phong."),i("br"),i("br"),n._v("Có loại vật này, xác suất thành công liền sẽ lớn hơn nhiều."),i("br"),i("br"),n._v("Đương nhiên, thứ này giá cả, cũng mười phần đắt đỏ, không phải người bình thường có khả năng chịu đựng nổi."),i("br"),i("br"),n._v("“Được rồi...” Trương Huyền tiếp tục lắc đầu."),i("br"),i("br"),n._v("Tu luyện Phong Thánh giải, chân khí trong cơ thể hắn, vô cùng vô tận, loại đan dược này đối với hắn mà nói, tác dụng không lớn, còn Thánh khí, có Kim Nguyên đỉnh, cùng phần đông khôi lỗi, cái khác có muốn hay không đều được."),i("br"),i("br"),n._v("“Nếu như công tử đối với mấy cái này, không hứng thú lời nói, cuối cùng đồng dạng, khẳng định sẽ thích!”"),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, lão giả tiếp tục xem tới."),i("br"),i("br"),n._v("“Ồ?” Trương Huyền nghi ngờ."),i("br"),i("br"),n._v("“Tháp ngôn cổ thư!”"),i("br"),i("br"),n._v("Lão giả nói."),i("br"),i("br"),n._v("“Tháp ngôn cổ thư?” Trương Huyền cau mày."),i("br"),i("br"),n._v("Cái tên này, chưa từng nghe nói qua."),i("br"),i("br"),n._v("“Nghe nói là trước đây Lạc Thánh, dọc đường nơi đây, đương thời Diêm đường chủ, tới tiếp kiến, thấy hắn nghỉ ngơi, đứng tại chỗ ba ngày ba đêm chưa từng động đậy, đến mức tuyết lớn đầy người, tựa như một tòa tuyết điêu. Niệm hắn chân thành, Lạc Thánh ngồi ở trên giường, truyền thụ chương trình học, thiên hoa loạn trụy, mặt đất nở sen vàng, nguyên khí vì đó rơi vãi, thiên địa vì đó dao động... Phần này sư ngôn, được Diêm đường chủ ghi xuống, lưu ra hậu thế, cũng chính là bản này tháp ngôn cổ thư!”"),i("br"),i("br"),n._v("Lão giả giải thích."),i("br"),i("br"),n._v("“Lạc Thánh?” Trương Huyền cau mày."),i("br"),i("br"),n._v("“Ừm, Thánh Nhân Lạc gia lão tổ, Lạc Thiên Vân!”"),i("br"),i("br"),n._v("Lão giả nhẹ gật đầu: “Nghe nói cái này trong sách xưa, liên lụy đến Lạc gia công pháp một chút bí ẩn, nghiêm túc học tập lời nói, đối không gian sẽ có cực lớn lý giải, chỉ bất quá, nhiều năm qua, một mực không có người tìm tới... Cũng là đoạn thời gian trước, dưới cơ duyên xảo hợp, bị người phát hiện, gián tiếp lưu lạc đến nơi này.”"),i("br"),i("br"),n._v("Trương Huyền lông mày giương lên."),i("br"),i("br"),n._v("Đối với cổ thư loại hình, hắn hứng thú không tính quá lớn, nhưng liên lụy đến Lạc gia, cũng có chút ý động."),i("br"),i("br"),n._v("Nếu như có thể bởi vậy, rõ ràng Lạc gia công pháp một chút bí ẩn, về sau đi tìm Lạc Nhược Hi, khẳng định sẽ có trợ giúp rất lớn."),i("br"),i("br"),n._v("“Không biết cái này cổ thư, muốn bán đi giá cả bao nhiêu?”"),i("br"),i("br"),n._v("Dừng lại một chút, Trương Huyền nhìn qua."),i("br"),i("br"),n._v("Trước thời hạn biết giá cả, cũng tốt nghĩ biện pháp kiếm lấy."),i("br"),i("br"),n._v("Mặc dù hắn đổi hơn một trăm viên linh thạch, nhưng mà muốn mua loại này trân quý đồ vật, hẳn là còn xa xa không đủ."),i("br"),i("br"),n._v("“Tháp ngôn cổ thư, cứ việc không phải Lạc Thánh thân viết, nhưng cũng là hắn truyền thụ đạo lý, dù là chỉ lưu giữ một phần mười, hai, đối với Nguyên Thần cảnh, Xuất Khiếu cảnh cường giả, cũng là chí bảo! Cho nên, giá cả không ít, giá khởi đầu liền vượt qua năm trăm tinh nguyên linh thạch thượng phẩm, kết quả cuối cùng, chỉ sợ muốn vượt qua một ngàn, cho nên... Công tử muốn mua lời nói, có thể sớm chuẩn bị linh thạch!”"),i("br"),i("br"),n._v("Lão giả nói."),i("br"),i("br"),n._v("Hắn thấy, trước mắt vị này tùy tiện có thể lấy ra như thế đồ vật hối đoái, nhất định là người có tiền công tử, mấy trăm, hơn ngàn tinh nguyên linh thạch, không khó lắm."),i("br"),i("br"),n._v("“Tốt!”"),i("br"),i("br"),n._v("Dừng lại một chút, Trương Huyền nhẹ gật đầu: “Ngươi giúp ta lưu cái vị trí, ta đi trước chuẩn bị một chút, đấu giá hội trước đó, sẽ chạy tới!”"),i("br"),i("br"),n._v("Bây giờ cách trời tối, còn có một đoạn thời gian, vừa vặn đi sinh tử lôi đài nhìn một chút, có thể hay không kiếm được một ngàn số lượng."),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Lão giả nhẹ gật đầu, phái người an bài."),i("br"),n._v(" “Đi thôi!”"),i("br"),i("br"),n._v("Bên này xử lý xong, Trương Huyền không nói thêm lời, gọi Trương Cửu Tiêu một tiếng, để hắn dẫn đường, hướng lôi đài phương hướng đi đến."),i("br"),i("br"),n._v("Lôi đài tại toàn bộ lòng đất chợ đen tận cùng bên trong, tiến đến trước đó, trước kỹ càng kiểm tra một lần thực lực của bọn hắn, cấp cho đại biểu cấp bậc lệnh bài, lúc này mới tiến vào bên trong."),i("br"),i("br"),n._v("Nhìn thấy Trương Huyền đã đạt đến Nguyên Thần cảnh hậu kỳ, Trương Cửu Tiêu tràn đầy im lặng."),i("br"),i("br"),n._v("Cái tên này tốc độ tiến bộ, nói thật để hắn có chút phát điên."),i("br"),i("br"),n._v("Lần thứ nhất gặp phải thời điểm, vẫn chỉ là Thần Thức cảnh, thời gian vài ngày đạt đến Nguyên Thần cảnh trung kỳ, cùng hắn tương đương... Một canh giờ trước cùng Nhạc đường chủ đám người thời điểm chiến đấu, cấp bậc không biến, hiện tại đã đột phá..."),i("br"),i("br"),n._v("Một mực đến nay, đều cảm thấy mình là một thiên tài, nhìn thấy đối phương tiến bộ, mới hiểu được... Kém thực sự quá nhiều."),i("br"),i("br"),n._v("Căn bản không tại một cái cấp bậc."),i("br"),i("br"),n._v("“Ta cũng là tích lũy đủ rồi, vừa mới đột phá...”"),i("br"),i("br"),n._v("Thấy hắn một mặt bị đả kích bộ dáng, Trương Huyền an ủi một câu."),i("br"),i("br"),n._v("“Tích lũy đủ...”"),i("br"),i("br"),n._v("Nghe nói như thế, Trương Cửu Tiêu càng thụ đả kích."),i("br"),i("br"),n._v("Người khác tích lũy đều là mười năm, hai mươi năm, ba mươi năm... Lấy mười năm làm đơn vị, cái tên này lại là... Một ngày, hai ngày..."),i("br"),i("br"),n._v("Không cách nào so sánh được."),i("br"),i("br"),n._v("“Được rồi, hắn là trời nhận danh sư...”"),i("br"),i("br"),n._v("Rầu rĩ một hồi, cũng liền thoải mái."),i("br"),i("br"),n._v("Nếu là trời nhận danh sư cũng giống như hắn, dần dần từng bước, làm sao có thể thu hoạch được ông trời công nhận?"),i("br"),i("br"),n._v("“Hai vị, hai người các ngươi ai tham gia lôi đài thi đấu?”"),i("br"),i("br"),n._v("Đang tại cảm khái, một cái quản gia thanh niên đi tới."),i("br"),i("br"),n._v("“Ta...”"),i("br"),i("br"),n._v("Trương Huyền gật gật đầu."),i("br"),i("br"),n._v("“Ừm, vậy thì tốt, ta hiện tại cùng ngươi nói rõ chi tiết một chút, lôi đài thi đấu quy tắc cùng yêu cầu, muốn tham gia, trước muốn giao nộp hai cái tinh nguyên linh thạch thượng phẩm làm phí báo danh! Tỷ thí thời điểm, vũ khí, ám khí loại hình, nhất định phải từ đấu trường cung cấp, không cho phép bản thân mang theo... Đây cũng là vì công bằng!”"),i("br"),i("br"),n._v("Thanh niên giải thích."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("Vũ khí không cho phép mang theo, trước đó hắn liền đoán được, nếu không, mang cái lợi hại vũ khí, căn bản không có cách nào chống lại, tới bao nhiêu chết bao nhiêu, cái gọi là cược lệnh, cũng liền không còn ý nghĩa."),i("br"),i("br"),n._v("“Mặt khác, thắng liền năm tràng về sau, liền sẽ có người bắt đầu phiên giao dịch, bởi vì dính dáng tiền đặt cược, trừ phi đánh gần chết hoặc là tàn phế, không cho phép chủ động nhận thua, điểm ấy muốn trước thời hạn có chuẩn bị tâm lý!”"),i("br"),i("br"),n._v("Thanh niên tiếp tục nói."),i("br"),i("br"),n._v("“Gần chết hoặc là tàn phế?” Trương Huyền nhíu nhíu mày."),i("br"),i("br"),n._v("Điểm ấy trước đó Trương Cửu Tiêu chưa nói."),i("br"),i("br"),n._v("“Vâng, bắt đầu phiên giao dịch đánh cược, dính dáng rất lớn, nếu như rõ ràng có thể chiến thắng, lại cố ý nhận thua, đối với chúng ta thị trường uy tín cũng là một đại xung kích! Cho nên, một khi phát hiện chủ động nhận thua, hoặc là cố ý nhận thua, sẽ nghiêm trị không tha!”"),i("br"),i("br"),n._v("Thanh niên nói."),i("br"),i("br"),n._v("“Rõ ràng!”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Mặc dù đối cái quy củ này không quá đồng ý, nhưng cũng biết, không như vậy chuẩn mực lời nói, người người đánh giả quyền, cái lôi đài này cũng liền không có tiếp tục nữa ý nghĩa."),i("br"),i("br"),n._v("Khẳng định cũng không có khả năng dẫn tới vô số người điên cuồng đặt cược."),i("br"),i("br"),n._v("“Được rồi, cứ như vậy nhiều, ngươi tiếp tục tỷ thí lời nói, ta hiện tại cho ngươi báo danh, không tỷ thí người, mời đến khán đài!”"),i("br"),i("br"),n._v("Đem nên nói nói hết, thanh niên dặn dò."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền nhẹ gật đầu, đem vừa rồi linh thạch lấy ra hai cái, giao phí báo danh, sau đó đem trọn cái trữ vật giới chỉ, đưa cho Trương Cửu Tiêu."),i("br"),i("br"),n._v("“Giúp ta đặt cược, mỗi một lần đều toàn bộ áp lên, áp ta thắng...”"),i("br"),i("br"),n._v("“Tốt!”"),i("br"),i("br"),n._v("Trương Cửu Tiêu gật đầu."),i("br"),i("br"),n._v("Đối phương tới, chính là vì kiếm lấy linh thạch, làm sao đặt cược đương nhiên biết đến rất rõ ràng."),i("br"),i("br"),n._v("Giao phó xong, hắn được thanh niên đưa đến khán đài, Trương Huyền thì được đưa tới kẻ giật dây chuẩn bị."),i("br"),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Đi ngang qua khán đài, Trương Huyền hướng phía trong nhìn một cái, trên đài vừa vặn có người tại chiến đấu, chân khí xao động, phát ra kịch liệt nổ vang, vô số người hưng phấn mà gào thét, để cho người ta hormone tăng vọt."),i("br"),i("br"),n._v("“Xé hắn, xé hắn!”"),i("br"),i("br"),n._v("Từng tiếng gào thét vang lên."),i("br"),i("br"),n._v("Xuyên thấu qua cửa ra vào hướng trên lôi đài nhìn lại, lập tức nhìn thấy một người mặc thiết giáp trung niên nhân, gào lên một tiếng, đem một cái người khiêu chiến giơ lên."),i("br"),i("br"),n._v("Người trung niên này cái đầu rất lớn, chừng hai mét, người khiêu chiến được giơ lên, như là bắt một cái gà con."),i("br"),i("br"),n._v("Tê lạp!"),i("br"),i("br"),n._v("Gào lên một tiếng, người tại chỗ được xé thành hai nửa, máu tươi rơi vãi đầy đất."),i("br"),i("br"),n._v("Lông mày nhảy một cái, Trương Huyền nắm đấm căng thẳng."),i("br"),i("br"),n._v("Cùng người chiến đấu qua vô số trận, còn là lần đầu tiên nhìn thấy như vậy cuồng bạo, quả thực coi nhân mạng là thành trò đùa."),i("br"),i("br"),n._v("Trước đó, nhìn thấy đủ loại thương phẩm bán ra, còn cảm giác nơi này, cùng cái khác thị trường không có khác nhau quá nhiều, nhìn thấy một màn này mới biết được, chợ đen liền là chợ đen... Mạng người ở đây không đáng tiền, trừ phi... Nắm giữ vượt qua những người khác thực lực."),i("br"),i("br"),n._v("Nếu không, sớm tối đều sẽ chết trên đài."),i("br"),i("br"),n._v("Khó trách Trương Cửu Tiêu chỉ chiến đấu năm tràng liền lui, tâm lý lực trùng kích thực sự quá lớn."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1270"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1272"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);