(window.webpackJsonp=window.webpackJsonp||[]).push([[318],{673:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1263-hong-duong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1263-hong-duong"}},[n._v("#")]),n._v(" Chương 1263: Hồng Dương")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1263: Hồng Dương"),t("br"),t("br"),n._v("“Thế nào? Hiện tại rời khỏi còn kịp!”"),t("br"),t("br"),n._v("Một bên thanh niên thấy hắn vẻ mặt thay đổi, cười nhạo một tiếng."),t("br"),t("br"),n._v("Hắn gặp qua không ít thế gia công tử, trước khi đến, đủ loại lời thề son sắt, đủ loại ngang ngược càn rỡ, nhưng chân chính nhìn thấy lôi đài tàn khốc, sẽ trực tiếp dọa mềm, đường đều đi không được."),t("br"),t("br"),n._v("“Không cần!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn khiếp sợ, chỉ là nhân tộc nội bộ tranh đấu, cũng không phải là máu tanh cùng sợ hãi."),t("br"),t("br"),n._v("Thật muốn nói kinh khủng, ai có thể có Dị Linh tộc nhân đáng sợ?"),t("br"),t("br"),n._v("Ăn người, khát máu mang theo sát lục chi khí... Có đôi khi tính cả tộc nhân đều không buông tha, loại người này, lần thứ nhất nhìn thấy thời điểm, liền được nói tự sát, làm sao lại sợ hãi một ít nhân loại chiến đấu."),t("br"),t("br"),n._v("“Không cần liền tốt, đi vào đi!”"),t("br"),t("br"),n._v("Thấy hắn nhanh như vậy vẻ mặt như thường, thanh niên có chút ngoài ý muốn, không nói thêm lời."),t("br"),t("br"),n._v("Dọc theo cầu thang hướng phía dưới, thời gian không dài đi tới một cái phòng, bốn phía trên vách tường, bày đầy đủ loại vũ khí, đều là Thánh vực hạ phẩm cấp bậc, hơn nữa đều không có linh tính, thuận lợi điều khiển cùng nắm giữ."),t("br"),t("br"),n._v("“Những binh khí này, ngươi có thể tùy tiện tuyển... Một dạng cũng được, mười dạng cũng được, không qua, làm hỏng, phải bồi thường, đánh xong, liền muốn thả lại tới!”"),t("br"),t("br"),n._v("Thanh niên tiện tay một chỉ."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu, nhìn quanh một vòng, mấy bước đi tới một cái giá đao trước mặt."),t("br"),t("br"),n._v("Nơi này xếp đặt chừng trọn trên trăm loại đao, đơn đao, yêu đao, quỷ đầu đao, trường đao, đoản đao, phá phong đao, minh hồng đao, cửu hoàn đao, Thanh Long yển nguyệt đao... Lít nha lít nhít, để cho người ta hoa mắt."),t("br"),t("br"),n._v("Ngón tay nhẹ nhàng xẹt qua mỗi một kiện vũ khí, tiện tay cầm lấy một cái thoạt nhìn có chút nặng nề, vung vẩy mấy lần, lắc đầu, ngay sau đó đổi một cái, như thế liên tục, cuối cùng để xuống tất cả vũ khí."),t("br"),t("br"),n._v("“Được rồi, không cần!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Không dùng binh khí?” Thanh niên có chút kỳ quái."),t("br"),t("br"),n._v(" Từng trải qua lôi đài tàn khốc, rất nhiều đến đây tham gia trận đấu người, đều hận không thể đem tất cả vũ khí lắp đi, chuẩn bị bất cứ tình huống nào, cái tên này, tùy tiện sờ lên, một cái đều không chọn... Trái tim thật là quá lớn đi!"),t("br"),t("br"),n._v("“Nếu như chiến thắng không qua, vũ khí nhiều hơn nữa cũng vô dụng!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Cái kia tốt!”"),t("br"),t("br"),n._v("Không nói thêm lời, thanh niên đẩy ra gian phòng một cái cửa khác, theo ở phía sau, đi vào, chỉ thấy mấy chục người đang mỗi người ngồi ở một bên khoanh chân tu luyện."),t("br"),t("br"),n._v("Những người này mỗi một cái trên thân đều mang mùi máu tanh, vừa nhìn liền biết, trên người có không ít người lệnh."),t("br"),t("br"),n._v("“Ngươi chờ ở tại đây, xếp tới ngươi, trực tiếp ra sân chính là!”"),t("br"),t("br"),n._v("Nói hết xoay người rời khỏi."),t("br"),t("br"),n._v("Trương Huyền nhìn quanh một vòng."),t("br"),t("br"),n._v("Gian phòng mấy chục người, mặc dù tại tu luyện, lại từng cái thời khắc chú ý bốn phía, âm thầm cảnh giác, dường như chỉ cần có người tới trước mặt, liền sẽ nổi lên hại người."),t("br"),t("br"),n._v("“Ngươi là vừa tới? Trước kia chưa hề tham gia qua?”"),t("br"),t("br"),n._v("Tìm cái địa phương ngồi xuống, một cái thanh âm nhàn nhạt ở bên tai vang lên."),t("br"),t("br"),n._v("Quay đầu nhìn lại, là cái chừng ba mươi tuổi thanh niên, trên mặt một đạo vết đao, máu tươi âm u tĩnh mịch, hẳn là vừa mới bị thương, còn chưa kịp khôi phục."),t("br"),t("br"),n._v("Bốn phía đám người, đều có chút lạnh lùng, đối phương thế mà chủ động tìm chính mình nói chuyện, để Trương Huyền có chút ngoài ý muốn."),t("br"),t("br"),n._v("“Ngươi có thể nhìn ra?”"),t("br"),t("br"),n._v("“Tham gia qua lôi đài thi đấu, ánh mắt đều cùng như sói, tràn đầy hung ác, nếu không, đi lên chỉ có chết... Ngươi giống như là một cái cừu non, không thấy như vậy?”"),t("br"),t("br"),n._v("Vết đao thanh niên cười nhạo."),t("br"),t("br"),n._v("Sinh tử lôi đài, thực lực kiên cố nhiên một bộ phận nhân tố, càng quan trọng hơn là “Hung ác”!"),t("br"),t("br"),n._v("Đối với địch nhân hung ác, đối với mình hung ác!"),t("br"),t("br"),n._v("Chỉ có như vậy, mới có thể để cho người ta sợ hãi, trước mắt vị này, ánh mắt ngây thơ, một bộ ngốc manh biểu lộ, vừa nhìn liền biết là cái gì đều không có trải qua quý tộc công tử."),t("br"),t("br"),n._v("“Cừu non?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Bản thân đi tới chỗ nào, đều huyên náo long trời lở đất, bị vô số danh sư đau đầu, trong mắt đối phương, thế mà chỉ là cái cừu non?"),t("br"),t("br"),n._v("Nhìn tới vẫn là tính tình quá tốt rồi."),t("br"),t("br"),n._v("“Đương nhiên, không có tham gia qua tỷ thí, không có đi qua máu tanh tẩy lễ, liền là cừu non! Nói thật cho ngươi biết, ta nhiều nhất liên tục chiến thắng sáu tràng, đáng tiếc... Vị thứ bảy quá mạnh, tiêu hao chân khí quá nhiều, nếu không, hiện tại cũng vô cùng có khả năng cầm tới thập quan vương xưng hào!”"),t("br"),t("br"),n._v("Vết đao thanh niên một mặt kiêu ngạo."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu, chân tâm thật ý."),t("br"),t("br"),n._v("Trương Cửu Tiêu loại thiên tài này, thứ sáu tràng đều không có tham gia, đối phương có thể liên tục chiến thắng sáu tràng, đã rất lợi hại."),t("br"),t("br"),n._v("“Kẹt kẹt!”"),t("br"),t("br"),n._v("Đang tại nói chuyện phiếm, chỉ thấy phía trước cửa phòng mở ra, ngay sau đó đám người liền cảm thấy một cỗ sát lục chi khí đập vào mặt, làm cho người ta cảm thấy nồng đậm áp bức cảm giác."),t("br"),t("br"),n._v("“Là hắn?”"),t("br"),t("br"),n._v("Trương Huyền nhướng mày."),t("br"),t("br"),n._v("Không phải người khác, chính là vừa rồi đứng tại trên lôi đài, đem người xé thành mảnh nhỏ gia hỏa."),t("br"),t("br"),n._v("Cái tên này dáng người cực cao, tựa như một cái tháp sắt, toàn thân mặc nặng nề khôi giáp, đỏ tươi như máu, thoạt nhìn dị thường quỷ dị."),t("br"),t("br"),n._v("“Vị này là chợ đen hiện nay cường đại nhất tồn tại, Hồng Dương!”"),t("br"),t("br"),n._v("Thấy hắn không nhận ra, vết đao thanh niên nhẹ gật đầu, tràn đầy bội phục: “Cũng bị người coi là... Bách chiến vương!”"),t("br"),t("br"),n._v("“Bách chiến vương?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ: “Chiến thắng một trăm tràng?”"),t("br"),t("br"),n._v("“Không phải chiến thắng một trăm tràng, mà là liên tục chiến thắng mười vị thập chiến vương! Hơn nữa, khiêu chiến hắn gia hỏa, đều bị xé vỡ nát, chết không thể chết lại!”"),t("br"),t("br"),n._v("Vết đao thanh niên lộ ra vẻ sợ hãi."),t("br"),t("br"),n._v("Trước mắt vị này thực lực, hắn rất bội phục, nhưng đối hắn tàn bạo, cũng dị thường sợ hãi."),t("br"),t("br"),n._v("Không chỉ có là hắn, những người khác thấy vị này đi ra, cũng tất cả đều tràn đầy cảnh giác."),t("br"),n._v("“Cút!”"),t("br"),t("br"),n._v("Mấy bước đi tới góc tường một vị trí, nhìn về phía ngồi dưới đất tu luyện hai cái thanh niên, Hồng Dương một tiếng trách mắng."),t("br"),t("br"),n._v("Hai người kia, thở mạnh cũng không dám nói, xoay người rời đi."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Tháp cao giống như Hồng Dương ngồi xuống, lấy ra một viên đan dược, nuốt xuống, khí tức trên thân tựa như sôi trào."),t("br"),t("br"),n._v("Hắn ngồi xuống, đám người đồng thời thở phào nhẹ nhõm, người xung quanh lặng lẽ lui về phía sau, trống ra một mảnh lớn địa phương."),t("br"),t("br"),n._v("“Vậy mà cũng là Nguyên Thần cảnh hậu kỳ...”"),t("br"),t("br"),n._v("Đối phương tu luyện, Trương Huyền nhìn ra tu vi."),t("br"),t("br"),n._v("Cũng giống như mình, đều là Nguyên Thần cảnh hậu kỳ."),t("br"),t("br"),n._v("Bất quá, đồng dạng cấp bậc, trên người đối phương sát lục chi khí, nặng nề mạnh mẽ, còn không có chiến đấu, liền cho người ta một loại nồng đậm áp bức."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn vận chuyển, đang định nhìn kỹ một chút thực lực của đối phương, đến cùng cao bao nhiêu, đối diện Hồng Dương liền ngẩng đầu lên, mắt sáng lên, nhìn lại."),t("br"),t("br"),n._v("“Thật mạnh cảm ứng...”"),t("br"),t("br"),n._v("Ánh mắt run lên."),t("br"),t("br"),n._v("Không hổ là liên tục chiến thắng mười vị thập quan vương tồn tại, loại cảm ứng này lực, cường đại đến đáng sợ."),t("br"),t("br"),n._v("Chăm chú nhìn thêm, liền có thể theo nhiều người như vậy bên trong, nhận ra phương hướng, cũng xác định vị trí, loại này giác quan thứ sáu, muốn đánh lén đem hắn chém giết, có thể nói đã không có khả năng."),t("br"),t("br"),n._v("“Sống chết trước mắt, đối với người có rất lớn rèn luyện tác dụng, vị này mặc dù là Nguyên Thần cảnh hậu kỳ, nhưng nửa bước Xuất Khiếu cảnh danh sư, chỉ sợ cũng chưa chắc có khả năng chống lại. Thậm chí... Cùng cùng cấp bậc chiến sư nhốt tại trong một cái lồng, sống sót, cũng nhất định là hắn!”"),t("br"),t("br"),n._v("Âm thầm gật đầu."),t("br"),t("br"),n._v("Một người mạnh mẽ hay không, cũng không chỉ có thực lực, đối nguy hiểm lĩnh ngộ cùng vận dụng, cũng cực kỳ trọng yếu."),t("br"),t("br"),n._v("Thật giống như ngưu lực lượng so sói chỉ mạnh không yếu, nhưng đặt ở cùng một cái gian phòng, chết khẳng định là cái trước."),t("br"),t("br"),n._v("Vị này Hồng Dương liền là một con sói, thậm chí một đầu đói bụng không biết giết nhiều trời mãnh hổ, một khi gặp gỡ, sẽ lập tức phóng thích răng nanh sắc bén, để cho người ta khó mà ngăn cản."),t("br"),t("br"),n._v("“Mới tới, cút sang một bên... Nơi này có ngươi ngồi địa phương?”"),t("br"),t("br"),n._v("Đang tại âm thầm phỏng đoán, một cái thanh âm lạnh lùng vang lên."),t("br"),t("br"),n._v("Giương mắt nhìn lại, chính là mới vừa rồi bị Hồng Dương đuổi đi hai cái thanh niên một trong, không có địa phương ngồi, đem khí hất tới trên đầu mình."),t("br"),t("br"),n._v("“Nơi này có khắc tên của ngươi?”"),t("br"),t("br"),n._v("Cũng không đứng dậy, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Hồng Dương một câu dọa đến không dám dừng lại, nhìn thấy bản thân là cái nhân vật mới, liền xông lại... Điển hình lấn yếu sợ mạnh!"),t("br"),t("br"),n._v("“Ồ? Hiện tại nhân vật mới rất ngưu ah!”"),t("br"),t("br"),n._v("“Tiễn Húc, hình như ngươi không có gì lực uy hiếp!”"),t("br"),t("br"),n._v("Thấy vừa tới gia hỏa không những bất động, còn oán giận một câu, vốn tu luyện đám người, tất cả đều mở mắt, tràn đầy xem náo nhiệt giống như trêu tức."),t("br"),t("br"),n._v("“Khắc tên của ta? Hắc hắc, đã ngươi muốn, ta hiện tại liền cho ngươi khắc lên!”"),t("br"),t("br"),n._v("Gọi Tiễn Húc vị thanh niên này, cũng không nghĩ tới một cái người mới tới, sẽ cùng hắn đối mặt đối lập, híp mắt lại, nhếch miệng hừ một cái, lời còn chưa dứt, lòng bàn tay chẳng biết lúc nào đã nhiều ra một cây chủy thủ, hướng về phía Trương Huyền cái trán liền ghim tới."),t("br"),t("br"),n._v("Lần này tốc độ cực nhanh, không có chút nào do dự, thật giống như đâm không phải người, mà là một cái nhánh cây, một viên cây cối."),t("br"),t("br"),n._v("Một khi bị đâm trúng, đầu tất nhiên nứt toác, tại chỗ tử vong."),t("br"),t("br"),n._v("“Một lời không hợp liền giết người?”"),t("br"),t("br"),n._v("Trương Huyền có chút không vui."),t("br"),t("br"),n._v("Nếu như là sinh tử đại thù, tàn nhẫn như vậy ngược lại cũng thôi, cũng bởi vì bản thân không từ bỏ đưa, trực tiếp động thủ, xem mạng người như trò đùa... Có chút quá độc ác đi!"),t("br"),t("br"),n._v("Ngón tay nâng lên, nghênh đón tiếp lấy."),t("br"),t("br"),n._v("Đinh!"),t("br"),t("br"),n._v("Gảy tại dao găm sống lưng bên trên, “Ô” một tiếng, hạ phẩm Thánh khí, cắt thành hai đoạn."),t("br"),t("br"),n._v("“Nguyên lai có chút thực lực, khó trách dám lớn lối như vậy!”"),t("br"),t("br"),n._v("Được một chỉ đạn đoạn vũ khí, sửng sốt một chút, Tiễn Húc lập tức đầu lưỡi vỗ vỗ miệng, lộ ra vẻ hưng phấn."),t("br"),t("br"),n._v("Còn tưởng rằng là cái newbie, có thể tùy ý nắm, hiện tại xem ra, cũng không có đơn giản như vậy."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Cũng không thấy có là cái gì dư thừa động tác, lòng bàn tay chẳng biết lúc nào lần nữa nhiều ra mấy cây chủy thủ, thượng trung hạ ba đường bỗng nhiên bay tới."),t("br"),t("br"),n._v("Lên đường con mắt, trung lộ trái tim, ven đường, hướng về phía Trương Huyền ngồi dưới đất đùi."),t("br"),t("br"),n._v("Một chiêu này, lại hung ác lại nhanh, đổi lại bình thường Nguyên Thần cảnh hậu kỳ, dù là Trương Cửu Tiêu cái loại này thiên tài, chỉ sợ cũng rất khó né tránh, bị ám sát chết ngay tại chỗ."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Thấy đâm ra nhiều như vậy công kích, trước đó không có bất kỳ cái gì báo hiệu, Trương Huyền nhịn không được gật đầu."),t("br"),t("br"),n._v("Không hổ là trải qua sinh tử gia hỏa, kỹ thuật giết người có thể, tu luyện lô hỏa thuần thanh, thượng, trung, hạ ba đường, mặc kệ chặn lại nơi nào, đều tất nhiên phải bị mặt khác hai nơi công kích, nguy hiểm vô cùng."),t("br"),t("br"),n._v("Bất quá..."),t("br"),t("br"),n._v("Gặp Trương Huyền, loại công kích này đồng dạng không còn hiệu quả."),t("br"),t("br"),n._v("Bàn tay nhẹ nhàng vạch một cái, giữa không trung vạch ra nửa vòng tròn, ba thanh dao găm, trong nháy mắt xuất hiện tại lòng bàn tay, không ngừng nhảy, giống như là bị bắt lại sống ngư."),t("br"),t("br"),n._v("“Ngươi để cho ta có hứng thú hơn...”"),t("br"),t("br"),n._v("Thấy lần công kích thứ hai, vẫn như cũ được đối phương chặn lại, hơn nữa như vậy hời hợt, Tiễn Húc con mắt hiện lên một đạo sát cơ, đang nghĩ động thủ, liền nghe đến đại sảnh bên trong một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Nguyên Thần cảnh hậu kỳ Trương Huyền, lôi đài thi đấu bắt đầu, đối chiến cùng cấp bậc, Mạnh Phục Hưng!”"),t("br"),t("br"),n._v("“Đến ta...”"),t("br"),t("br"),n._v("Không tiếp tục để ý đối phương, Trương Huyền đứng dậy, nhanh chân hướng lôi đài phương hướng đi tới."),t("br"),t("br"),n._v("“Ha ha, chọc ta muốn đi? Nào có dễ dàng như vậy!”"),t("br"),t("br"),n._v("Thấy hắn muốn đi, Tiễn Húc bàn tay lần nữa lắc một cái, lại hai thanh dao găm thẳng tắp đối Trương Huyền giữa lưng bắn tới, tiếng gió rít gào, hàn khí đâm thẳng làn da."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1271"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1273"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);