(window.webpackJsonp=window.webpackJsonp||[]).push([[319],{674:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1264-loi-đai-thi-đau-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1264-loi-đai-thi-đau-thuong"}},[n._v("#")]),n._v(" Chương 1264: Lôi đài thi đấu (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1264: Lôi đài thi đấu (thượng)"),t("br"),t("br"),n._v("“Là Tiễn Húc đoạt mệnh tam thức!”"),t("br"),t("br"),n._v("“Hàng năm chết tại hắn chiêu này bên trên, không có một trăm cũng có tám mươi đi!”"),t("br"),t("br"),n._v("“Quá kinh khủng, tốc độ vừa nhanh vừa độc, căn bản ngăn cản không nổi, cùng cấp bậc có thể nói vô địch!”"),t("br"),t("br"),n._v("“Người mới này, chết chắc!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy cái này hai thanh đâm nhanh mà ra dao găm, đám người tất cả đều vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Tiễn Húc thực lực, cùng bách chiến vương Hồng Dương so, mặc dù kém xa tít tắp, nhưng ở toàn bộ lòng đất chợ đen, cũng là tên tuổi lẫy lừng."),t("br"),t("br"),n._v("Nhất là trên thân lấy mãi không hết, dùng mãi không cạn dao găm, cùng lợi hại nhất đoạt mệnh tam thức, bởi vậy bị giết, nhiều vô số kể."),t("br"),t("br"),n._v("Vị này mới tới, không biết trời cao đất rộng, ở trước mặt khiêu khích, dẫn tới sử dụng ra sát chiêu, cơ bản liền phán quyết tử hình."),t("br"),t("br"),n._v("Đồng loạt đi về phía trước thanh niên nhìn sang, chỉ thấy đầu hắn cũng không xoay, không biết sau lưng có dao găm, vết đao thanh niên, có chút nóng nảy, đang nghĩ nhắc nhở một câu, trước mắt trở nên hoảng hốt, lập tức hình như đối phương trên chân giày, bỗng nhúc nhích, đánh vào dao găm phía trên."),t("br"),t("br"),n._v("Không biết nhìn lầm hay là thật có động tác này, còn không có xác định, chỉ thấy dao găm “Ô!” Một tiếng gào thét, bay ngược mà ra, gần đây tốc độ nhanh gấp bội không biết."),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("Sắc mặt tái xanh, Tiễn Húc còn chưa kịp phản ứng, liền nghe “Phốc!” Hai tiếng, dao găm liền đem hắn hai cái cổ tay đâm thủng."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("To lớn lực trùng kích, mang theo hắn bay lùi ra ngoài, thẳng tắp đóng ở sau lưng trên vách tường, như là một cái thánh giá, vô luận như thế nào giãy dụa, đều không tránh thoát."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("“Giày tùy tiện đụng một cái, không chỉ có phá giải Tiễn Húc đoạt mệnh tam thức, còn đem hắn đóng ở trên tường... Cái này thực lực gì?”"),t("br"),t("br"),n._v("“Người mới này không đơn giản, chẳng lẽ lại muốn xuất hiện một vị mới thập quan vương?”"),t("br"),t("br"),n._v("“Không biết, không qua, loại thực lực này, liên tục bảy, tám tràng cũng không có vấn đề!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy tràn đầy dữ tợn, bị treo trên tường, kêu thảm không ngớt Tiễn Húc, đám người tất cả đều âm thầm cảnh giác."),t("br"),t("br"),n._v("Cứ việc vị này Trương Huyền thực lực, đến cùng từng có cao, không nhìn ra, nhưng có thể như vậy hời hợt đem vị này đóng ở trên tường, lực lượng mạnh mẽ, không thể khinh thường."),t("br"),t("br"),n._v("Làm không cẩn thận, sẽ trở thành mới thập quan vương."),t("br"),t("br"),n._v("“Có ý tứ!”"),t("br"),t("br"),n._v("Một bên Hồng Dương nhìn một cái, lập tức lần nữa đóng lại."),t("br"),t("br"),n._v("Vừa rồi thủ đoạn kia, thoạt nhìn kinh diễm, đối với hắn mà nói, không tính là gì."),t("br"),t("br"),n._v("Một quyền đánh không chết, cùng lắm thì liền lại đánh một quyền."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lôi đài không lớn, bốn phía sắp đặt có trận pháp đặc biệt, như là chiếc lồng, một khi tiến vào bên trong, muốn chạy trốn đều làm không được."),t("br"),t("br"),n._v("“Ký một bản giấy sinh tử!”"),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, chỉ thấy trước lôi đài một vị phụ trách tỷ thí trung niên nhân, đưa qua một phần khế ước."),t("br"),t("br"),n._v("Cúi đầu nhìn một cái, một giọt máu tươi bay đi, lập tức nghe được bốn phía vang lên vô số như sấm sét gào thét."),t("br"),t("br"),n._v("Ngẩng đầu nhìn lại, chung quanh lôi đài nhìn trên đài, nam hay nữ vậy, già có trẻ có, chừng năm, sáu trăm người, mỗi một cái đều con mắt đỏ thẫm, hưng phấn mà khó tự kiềm chế."),t("br"),t("br"),n._v("“Quan sát sinh tử chiến đấu, có khả năng hưởng thụ cái loại này kích thích, càng quan trọng hơn là, có thể theo thời khắc sống còn chiến đấu bên trong, thu hoạch được không ít kinh nghiệm...”"),t("br"),t("br"),n._v("Rất nhanh, rõ ràng bọn hắn ý nghĩ."),t("br"),t("br"),n._v("Sinh tử chiến, không phải tùy tiện liền có thể nhìn thấy, đối tu luyện giả có rất lớn kích thích, có khả năng học được không ít thứ."),t("br"),t("br"),n._v("Có lẽ, đây cũng là lòng đất chợ đen hưng thịnh nguyên nhân lớn nhất."),t("br"),t("br"),n._v("Nhấc chân đi đến lôi đài, phía trên đã đứng ngay ngắn một thanh niên, hai tay ôm ở trước ngực, một mặt cao ngạo cùng lạnh lùng, dường như trận đấu này, liền là đi một chút đi ngang qua sân khấu."),t("br"),t("br"),n._v("Hẳn là vừa rồi thanh âm bên trong nói tới vị kia, cùng mình chiến đấu Mạnh Phục Hưng."),t("br"),t("br"),n._v("“Tốt a, sớm một chút kết thúc, sớm một chút kiếm lấy linh thạch, thời gian không thể bị dở dang!”"),t("br"),t("br"),n._v("Tính toán một chút thời gian, khoảng cách đấu giá hội bắt đầu không bao lâu, lười nhác tiếp tục vết mực, Trương Huyền duỗi lưng một cái, cũng không nói nhảm, cúi đầu đến cùng: “Đắc tội!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một cái chớp mắt, người liền xuất hiện tại thanh niên trước mặt, bàn tay bỗng nhiên hướng về phía trước tìm tòi, bắt tới."),t("br"),t("br"),n._v("Chiêu này uy lực không tính là quá mạnh, lại tại trong nháy mắt bao phủ bốn phía, như là một cái to lớn lưới đánh cá, vô luận như thế nào chạy trốn, đều làm không được."),t("br"),t("br"),n._v("“Ngươi... Ta không...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn tại sau khi cúi người chào, trực tiếp ra tay, thanh niên Mạnh Phục Hưng một mặt kinh ngạc, thân thể nhoáng một cái, muốn tránh né, lại phát hiện giống như là lâm vào đầm lầy."),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("Trương Huyền bàn tay đi tới trên mặt, một tiếng vang giòn, thanh niên còn chưa kịp nói chuyện, liền bị đập ngã trên mặt đất, mắt tối sầm lại, hôn mê bất tỉnh."),t("br"),t("br"),n._v("Lên đài phía trước, liền muốn được rồi, đã không thể thả nước, vậy liền đem đối thủ đánh ngất xỉu."),t("br"),t("br"),n._v("Choáng, không có năng lực phản kháng, đương nhiên cũng sẽ bị phán định nhận thua."),t("br"),t("br"),n._v("“Trận đầu xem như chiến thắng đi!”"),t("br"),t("br"),n._v("Chờ nửa ngày, thấy không có người tuyên đọc thành tích, hai tay chắp sau lưng, Trương Huyền đầu lâu nâng lên."),t("br"),t("br"),n._v("Vẫn như cũ không một người nói chuyện."),t("br"),t("br"),n._v("Ngẩng đầu hướng bốn phía nhìn lại, chỉ thấy xem náo nhiệt đám người, chẳng những không có kinh hô, ngược lại từng cái gặp quỷ một dạng biểu lộ."),t("br"),t("br"),n._v("Trương Cửu Tiêu cũng tại mọi người bên trong, che cái trán, không ngừng hô hào cái gì, chỉ bất quá bởi vì có trận pháp ngăn trở, một câu cũng không nghe thấy."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Thấy mọi người phản ứng cùng dự liệu khác biệt, Trương Huyền có chút kỳ quái, đang nghĩ nói chuyện, chỉ thấy lôi đài trận pháp mở ra, lại một thanh niên đi đến, nhìn thấy nằm dưới đất Mạnh Phục Hưng, con mắt trợn tròn."),t("br"),t("br"),n._v("“Ngươi là vị thứ hai so với ta võ?”"),t("br"),t("br"),n._v("Thập quan vương, cần liên tục đối chiến mười vị, mới vừa đập bay một cái, lại tới một cái, nói rõ đây đã là cái thứ hai cùng mình khảo hạch gia hỏa."),t("br"),t("br"),n._v("“Ta là... Mạnh Phục Hưng!”"),t("br"),t("br"),n._v("“Ngươi là? Vậy cái này...” Trương Huyền ngẩn ngơ."),t("br"),t("br"),n._v("Cái tên này là cùng bản thân vị thứ nhất tỷ thí, cái kia... Mới vừa bị đánh ngất xỉu chính là ai?"),t("br"),n._v(" “... Trọng tài!”"),t("br"),t("br"),n._v("Mạnh Phục Hưng khóe miệng lắc một cái."),t("br"),t("br"),n._v("Trương Huyền nheo mắt: “Trận đấu này... Còn có trọng tài?”"),t("br"),t("br"),n._v("“Nhiều mới mẻ, cái gì tranh tài không có trọng tài?” Mạnh Phục Hưng nói."),t("br"),t("br"),n._v("“...” Trương Huyền thân thể lay động."),t("br"),t("br"),n._v("Cái này gọi chuyện gì, người ta tranh tài trọng tài, bình thường đều là đứng tại dưới đài, cái tên này tuổi không lớn lắm, lại đứng tại trên đài, bản thân nơi nào dự đoán được?"),t("br"),t("br"),n._v("Đội viên còn không có gặp, một bàn tay đem trọng tài quất choáng..."),t("br"),t("br"),n._v("Cái này lôi đài đánh..."),t("br"),t("br"),n._v("“Ta sẽ không bị... Hủy bỏ tư cách đi!”"),t("br"),t("br"),n._v("Rốt cuộc biết Trương Cửu Tiêu hô những thứ gì, Trương Huyền im lặng."),t("br"),t("br"),n._v("Thật vất vả nghĩ đến tiên hạ thủ vi cường, lần này được rồi, đem người ta trọng tài đánh thành như vậy... Sẽ không bởi vậy, hủy bỏ tư cách, không có cách nào tiếp tục tỷ thí đi!"),t("br"),t("br"),n._v("“Hẳn là sẽ không, nơi này không có quy tắc...”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Mạnh Phục Hưng theo trong lúc khiếp sợ khôi phục lại: “Chúng ta bắt đầu đi!”"),t("br"),t("br"),n._v("Lòng đất chợ đen, bản thân liền không có quy tắc, đem trọng tài đánh ngất xỉu, tại cái khác địa phương không hợp quy củ, nhưng ở nơi này, hẳn là ảnh hưởng không lớn."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Thanh âm kết thúc, Mạnh Phục Hưng hét dài một tiếng, thẳng tắp lao đến."),t("br"),t("br"),n._v("Người trên không trung, tay ảnh liên tục, thời gian nháy mắt hoa sen nở rộ."),t("br"),t("br"),n._v("Thánh vực trung phẩm võ kỹ, lá sen ngàn phật thủ!"),t("br"),t("br"),n._v("Không thể không nói, vị này Mạnh Phục Hưng thực lực không yếu, coi như không bằng vừa rồi vị kia Tiễn Húc, hẳn là cũng không kém nhiều, vừa ra tay liền đem không khí bốn phía toàn bộ bao vây, khí lưu bốn phía lăn mình, tùy thời đều muốn nổ tung."),t("br"),t("br"),n._v("“Tới tốt lắm!”"),t("br"),t("br"),n._v("Thấy vị thứ nhất đối thủ cứ như vậy lợi hại, có vẻ như so trọng tài đều mạnh, Trương Huyền năm ngón tay mở ra, lần nữa bắt đến."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Cùng vừa rồi chiêu số giống nhau như đúc, đổ ập xuống, giống như bầu trời rơi xuống, muốn đem người óc đánh ra tới."),t("br"),t("br"),n._v("Hắn chiêu này, không phải là võ kỹ, cũng không phải cái gì tuyệt thế công pháp, mà là đơn giản nhất bắt."),t("br"),t("br"),n._v("Nhưng mà, chẳng biết tại sao, bàn tay hạ xuống, liền cho người ta một loại khó mà tránh né ảo giác."),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("Bàn tay cùng bàn tay đối đầu, Mạnh Phục Hưng sắc mặt trắng nhợt, lùi về phía sau mấy bước, thân thể nhoáng một cái, bại liệt trên mặt đất, mắt tối sầm lại hôn mê bất tỉnh."),t("br"),t("br"),n._v("“Thật mạnh!”"),t("br"),t("br"),n._v("“Đây là cái chiêu số gì?”"),t("br"),t("br"),n._v("“Đoán chừng là cái gì chưởng công, không qua, có thể đem Mạnh Phục Hưng một chưởng vỗ choáng, nói rõ ít nhất có thể thắng được năm người!”"),t("br"),t("br"),n._v("“Cái này không nhất định, uy lực càng lớn tuyệt chiêu, đối chân khí hao tổn cũng liền càng lớn, chiêu này rất lợi hại, ngộ nhỡ chân khí tiêu hao quá nhiều, có thể hay không chống đến, cũng không tốt nói!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Dưới đài xôn xao."),t("br"),t("br"),n._v("Vị này Trương Huyền, cứ việc có chút lỗ mãng, vừa lên đài liền đem trọng tài quất choáng, nhưng cũng nói thực lực!"),t("br"),t("br"),n._v("Bất quá, có thực lực vô dụng, còn muốn chịu đựng mới được."),t("br"),t("br"),n._v("Hàng năm, tới tranh đoạt thập quan vương nhiều vô số kể, nhưng có khả năng thông qua cũng thành công, số lượng không tính quá nhiều."),t("br"),t("br"),n._v("“Tại hạ, Đàm Thế Hữu, xin chỉ giáo!”"),t("br"),t("br"),n._v("Đem Mạnh Phục Hưng đập choáng, thời gian không dài lại tới cái thanh niên."),t("br"),t("br"),n._v("Thực lực của người này, so vừa rồi vị kia hơi mạnh một tia, vẫn như cũ không phải là đối thủ, một chiêu qua đi, đập vào trên mặt, đồng dạng nằm ở trên mặt đất."),t("br"),t("br"),n._v("Liên tục năm người, đều là một bàn tay kết thúc, Trương Huyền lộ ra mệt mỏi chi ý, trên đầu mồ hôi xuất hiện, cánh tay đều đang không ngừng run rẩy."),t("br"),t("br"),n._v("“Đã tiến hành năm tràng, có muốn hay không tiếp tục khiêu chiến đi?”"),t("br"),t("br"),n._v("Người thứ năm bị đánh bại, một thanh âm vang lên."),t("br"),t("br"),n._v("Dưới tình huống bình thường, năm tràng là cái cực hạn, đằng sau dính dáng tiền đặt cược, tham dự tỷ thí, vô luận tu vi hay là thực lực, sẽ so trước mặt, gia tăng thật lớn."),t("br"),t("br"),n._v("“Tiếp tục!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Tới này mục đích đúng là kiếm tiền, hiện tại liền tiền đặt cược đều không có áp, đương nhiên không có khả năng rời khỏi."),t("br"),t("br"),n._v("“Tốt, cùng ngươi tiến hành thứ sáu tràng đối chiến cao thủ, là Khuê Thanh, đã từng khiêu chiến qua năm tràng quyết đấu, hơn nữa chiến thắng!”"),t("br"),t("br"),n._v("Thấy hắn đáp ứng, thanh âm nói."),t("br"),t("br"),n._v("Năm vị trí đầu tràng, không dính dáng tiền đặt cược, cơ bản đều là nhân vật mới đối chiến, theo thứ sáu tràng bắt đầu, liền muốn lên trải qua chiến đấu người ra sân."),t("br"),t("br"),n._v("Tình huống bình thường, sáu tràng từng có sáu tràng chiến tích người, bảy tràng đối bảy tràng chiến tích... Cứ thế mà suy ra."),t("br"),t("br"),n._v("Bởi vậy, hắn đối diện vị này Khuê Thanh, trên thực tế trước đó liền tham gia qua thi đấu sự tình, hơn nữa thu được liên tục năm tràng thắng lợi."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Đem quy tắc tại trong óc qua một lần, Trương Huyền giương mắt nhìn lại, chỉ thấy một thanh niên đi tới."),t("br"),t("br"),n._v("Rõ ràng cùng trước mấy vị khác biệt, trên thân mang theo nồng đậm sát lục chi khí, hiển nhiên trên tay có hơn người lệnh, sức chiến đấu không thể khinh thường."),t("br"),t("br"),n._v("“Ngươi tranh tài ta xem, cùng mỗi người chiến đấu, đều chỉ có một chiêu, thực lực không tệ!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Khuê Thanh tròng mắt hơi híp: “Bất quá, vừa rồi dùng chiêu kia, đối chân khí tiêu hao rất lớn đi! Ta muốn nhìn, ngươi còn có thể kiên trì sử dụng mấy lần!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Cổ tay rung lên, một chuôi trường thương xuất hiện tại lòng bàn tay, thẳng tắp đâm tới."),t("br"),t("br"),n._v("Không hổ là kẻ già đời, thế mà đang nói chuyện công phu, trực tiếp đánh lén... Còn không có kịp phản ứng, mũi thương liền đã đi tới trước mắt."),t("br"),t("br"),n._v("“Cùng ta chơi thương?”"),t("br"),t("br"),n._v("Thấy hắn động tác, Trương Huyền lắc đầu, tràn đầy bất đắc dĩ: “Ta mới là lão tổ tông...”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1272"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1274"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);