(window.webpackJsonp=window.webpackJsonp||[]).push([[32],{387:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1005-đoc-đien-tong-bo-nguoi-toi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1005-đoc-đien-tong-bo-nguoi-toi"}},[n._v("#")]),n._v(" Chương 1005: Độc điện tổng bộ người tới")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Hứa Du trưởng lão nhẹ gật đầu, đem cùng Trương Huyền gặp gỡ tất cả mọi chuyện, nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Ngươi nói... Tĩnh Viễn thành xuất hiện một vị, Thánh vực tứ trọng Dị Linh tộc nhân? Hơn nữa muốn giết ngươi, là Tôn độc sư cứu ngươi ra?”"),t("br"),t("br"),n._v("Mọi người tất cả đều giật nảy mình."),t("br"),t("br"),n._v("Dị Linh tộc nhân bản thân liền mạnh mẽ, Thánh vực tứ trọng càng là đáng sợ, đối phương mạo như thế lớn nguy hiểm đem hắn cứu, phần này can đảm, cũng không phải là bọn họ có thể so sánh."),t("br"),t("br"),n._v("“Không chỉ có như vậy, Tôn độc sư trên đường tới còn cho ta giảng giải dùng độc cơ sở, bởi vì quá mức thâm ảo, không có nhớ kỹ quá nhiều, có điều, liền ta nhớ những này, chỉ cần lĩnh ngộ, đều có thể được lợi một đời...”"),t("br"),t("br"),n._v("Hứa Du nhịn không được nói."),t("br"),t("br"),n._v("“Nói nghe một chút...”"),t("br"),t("br"),n._v("Nghe được “Tổ sư” vì hắn giảng bài, mọi người tất cả đều lộ ra vẻ hâm mộ, nguyên một đám nhìn lại."),t("br"),t("br"),n._v("Hứa Du không có giấu diếm, đem trong đầu có thể nhớ chỉ là toàn bộ nói một lần."),t("br"),t("br"),n._v("Nghe xong hắn giảng giải, mọi người tất cả đều kích động vẻ mặt trắng bệch, khó tự kiềm chế."),t("br"),t("br"),n._v("Cùng đối phương nói đồng dạng, mặc dù chỉ là cơ sở, nhưng nhắm thẳng vào đại đạo, để cho người ta nghe qua về sau, có loại hiểu ra cảm giác."),t("br"),t("br"),n._v("“Không cần đoán, cái kia hồn phách khẳng định là khai phái tổ sư... Loại trừ hắn, thực sự nghĩ không ra, còn có người nào, có thể đối dùng độc, nắm giữ sâu sắc như vậy lý giải...”"),t("br"),t("br"),n._v("Thảo luận một lát, Nhược Thanh Viễn nhịn không được nói."),t("br"),t("br"),n._v("Trước đó đối với đối phương thân phận, mặc dù tin tưởng đại bộ phận, vẫn còn có chút nghi ngờ, giờ phút này nghe được Hứa trưởng lão giải thích, lại không hoài nghi."),t("br"),t("br"),n._v("Cũng chỉ có Độc điện chân chính người sáng lập, mới có thể đối độc lĩnh ngộ sâu như vậy, đơn giản nhất pháp quyết tu luyện, đều có thể giảng giải không kém chút nào, nhắm thẳng vào đại đạo thâm ý."),t("br"),t("br"),n._v("“Được rồi, phái người nắm chặt chữa trị hư hại kiến trúc, Đoàn Nhân, Minh Chân đắc tội tổ sư, chịu độc chết chi hình!”"),t("br"),t("br"),n._v("Trong lòng xác nhận, Nhược Thanh Viễn phân phó."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người gật đầu đáp ứng."),t("br"),t("br"),n._v("Đoàn Nhân, Minh Chân đám người tự rước lấy nhục không nói, còn liên lụy nhiều người như vậy, tội không thể tha."),t("br"),t("br"),n._v("“Hôm nay liền đến nơi này, Hứa phó điện chủ, ngươi đi tàng thư điện bên ngoài chờ đợi, một khi Tôn độc sư nhìn xong thư, liền gọi ta...” Nhược Thanh Viễn nhẹ gật đầu."),t("br"),t("br"),n._v("t r u y e n c u a t u i . v Ｎ"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Hứa Du đáp một tiếng, đang muốn đứng dậy, chỉ thấy điện chủ lông mày nhíu một cái, cổ tay khẽ đảo, một cái đưa tin ngọc bài xuất hiện."),t("br"),t("br"),n._v("Ngọc bài phóng xuất ra ôn nhuận ánh sáng, có người đưa tin tới."),t("br"),t("br"),n._v("Nhìn một cái, điện chủ vội vàng đứng lên, ánh mắt sáng lên: “Tổng bộ... Người đến!”"),t("br"),t("br"),n._v("“Tổng bộ người tới?”"),t("br"),t("br"),n._v("Mọi người sững sờ."),t("br"),t("br"),n._v("“Nói là hai vị thất tinh đỉnh phong cấp bậc độc sư, lập tức liền muốn tới, muốn chúng ta nghênh đón!”"),t("br"),t("br"),n._v("Nói xong, điện chủ vội vã đi ra ngoài."),t("br"),t("br"),n._v("Mọi người theo sát trên đó."),t("br"),t("br"),n._v("Thời gian không dài, liền đến đến trước đó Hứa trưởng lão cùng Trương Huyền ngồi phi hành Thánh thú hạ xuống mặt đất phương, không đợi bao lâu, liền thấy phong ấn mở ra, một cái cực lớn Thánh thú chậm rãi bay tới."),t("br"),t("br"),n._v("Thánh thú sống lưng bên trên, hai cái lão giả ngạo nghễ đứng thẳng, còn chưa tới đến trước mặt, liền cho người ta một loại kịch liệt uy áp."),t("br"),t("br"),n._v("Thất tinh đỉnh phong độc sư, Thánh vực tứ trọng cường giả!"),t("br"),t("br"),n._v("“Bạch độc sư, Hải độc sư...”"),t("br"),t("br"),n._v("Thấy rõ ràng dung mạo của đối phương, Nhược Thanh Viễn lông mày nhảy một cái, vội vàng ôm quyền khom người."),t("br"),t("br"),n._v("Mặc dù Hồng Viễn Độc điện rất là bí ẩn, nhưng là cùng phía trên tổng bộ, liên hệ vẫn là rất mật thiết, Bạch độc sư Bạch Đình, Hải độc sư Hải Minh Kim, ở trên loài phân bộ bên trong, đều tiếng tăm lừng lẫy, cực kỳ lợi hại."),t("br"),t("br"),n._v("Bạch độc sư khoát tay áo, Tòng Thánh lưng thú bên trên rơi xuống, nhìn một chút trước mắt vị này, lông mày nhíu một cái: “Không biết các ngươi Nhược điện chủ người ở chỗ nào? Làm sao không có tới nghênh đón?”"),t("br"),t("br"),n._v("“Khụ khụ... Ta chính là điện chủ Nhược Thanh Viễn!” Nhược Thanh Viễn tràn đầy xấu hổ."),t("br"),t("br"),n._v("Hắn bị Trương Huyền đánh mặt mũi bầm dập, đừng nói hai người bọn họ nhận không ra, coi như vợ hắn, cha mẹ có thể hay không nhận ra cũng còn không nhất định."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Bạch độc sư nhìn Hải độc sư một cái, đồng thời khóe miệng giật một cái: “Nhược điện chủ, ngươi làm sao lại biến thành như vậy?”"),t("br"),t("br"),n._v("Nhược Thanh Viễn thế nhưng là cùng bọn hắn cùng cấp bậc độc sư, mặc dù thực lực không bằng, nhưng cũng chênh lệch không tính là quá xa, mạnh như thế người tại nơi ở của mình phía trong, bị đánh thành bộ này đức hạnh, đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("Trong lòng khiếp sợ, hướng mấy vị trưởng lão khác nhìn lại, nhìn một cái, mí mắt lần nữa xoay loạn."),t("br"),t("br"),n._v("Chỉ thấy những trưởng lão này, hoặc nhiều hoặc ít trên thân đều có tổn thương, chỉ có một vị, coi như hoàn hảo không chút tổn hại."),t("br"),t("br"),n._v("“Cái này... Nói rất dài dòng, hai vị tự mình đến đây, không biết tổng bộ có gì khai báo?”"),t("br"),t("br"),n._v("Cười khổ lắc đầu, Nhược Thanh Viễn nhìn lại."),t("br"),t("br"),n._v("Không phải là không muốn nói, mà là thực sự quá mất mặt."),t("br"),t("br"),n._v("“Hơn hai ngàn năm trước, các ngươi phân bộ theo Hỏa Nguyên thành một chỗ trong di tích, có hay không từng chiếm được một quyển sách?” Thấy đối phương không muốn nhiều lời, Bạch độc sư cũng thức thời không còn hỏi thăm, nói."),t("br"),t("br"),n._v("“Vâng!” Nhược Thanh Viễn nhẹ gật đầu."),t("br"),t("br"),n._v("Quyển sách này trước đó mới cùng Tôn độc sư nói một lần, nhớ vô cùng rõ ràng."),t("br"),n._v("“Có là tốt rồi, lần này tổng bộ phái chúng ta tới, chính là muốn đem quyển sách này thu hồi.”"),t("br"),t("br"),n._v("Nghe được hắn không có phủ nhận, Bạch độc sư thở dài một hơi, nói."),t("br"),t("br"),n._v("“Thu hồi?”"),t("br"),t("br"),n._v("Không nghĩ tới tổng bộ phái hai vị thất tinh đỉnh phong độc sư tới, như thế lớn phí trắc trở, chỉ vì một quyển sách, dừng lại một chút, Nhược Thanh Viễn nói “. Quyển sách này đã bị đặt ở điện chủ lăng, nếu như hai vị không vội vã, có thể tới trước khách điện chờ đợi...”"),t("br"),t("br"),n._v("Quyển sách này ở lại chỗ này, cũng không có bất kỳ chỗ dùng nào, đã tổng bộ phái người tới lấy, cho bọn hắn chính là."),t("br"),t("br"),n._v("Lời còn chưa dứt, chỉ thấy đứng tại cách đó không xa Hứa trưởng lão đám người, vẻ mặt có chút xấu hổ, trong đó một vị trưởng lão cũng nhịn không được nữa, đè thấp thanh âm truyền tới."),t("br"),t("br"),n._v("“Điện chủ... Khách điện đã bị phá hủy...”"),t("br"),t("br"),n._v("Trước đó xung đột sụp đổ rất nhiều kiến trúc bên trong, khách điện thình lình xuất hiện."),t("br"),t("br"),n._v("Hiện tại một vùng phế tích, kéo hai vị tổng bộ khách tới đi qua lời nói, thực sự quá mức mất mặt."),t("br"),t("br"),n._v("“Ây...” Đối phương nói chuyện, Nhược Thanh Viễn lúc này mới nghĩ tới, vội nói: “Ta kéo hai vị tới trước chỗ ở của ta nghỉ ngơi.”"),t("br"),t("br"),n._v("“Chỗ ở của ngươi?” Trắng, hải hai vị độc sư cau mày."),t("br"),t("br"),n._v("Nếu như bọn họ là lấy tư nhân thân phận, tới du ngoạn, đi đối phương nơi ở, không gì đáng trách. Nhưng bây giờ đại biểu tổng bộ mà đến, tự nhiên muốn lấy khách quý lễ tiết đối đãi, không đi khách điện, trực tiếp đưa đến trong nhà tính là gì sự tình?"),t("br"),t("br"),n._v("“Hai vị có chỗ không biết...”"),t("br"),t("br"),n._v("Xem bọn hắn biểu lộ không đúng, Nhược Thanh Viễn cười khổ lắc đầu: “Trên đường các ngươi liền biết...”"),t("br"),t("br"),n._v("Nói thật, cũng không biết nói như thế nào lối ra."),t("br"),t("br"),n._v("Một đường bay về phía trước, rất nhanh, hai vị tổng bộ tới độc sư, liền thấy đầy đất phế tích, cùng không sai biệt lắm đổ sụp nửa cái thành thị, liếc mắt nhìn nhau, từng người choáng váng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hải độc sư nhịn không được chỉ đi qua."),t("br"),t("br"),n._v("Nơi này là Độc điện, đâu đâu cũng có người một nhà, làm sao làm thành bộ này mãnh liệt bộ dáng?"),t("br"),t("br"),n._v("“Ai, chuyện này thực sự là... Chúng ta Hồng Viễn Độc điện sỉ nhục...” Nhược Thanh Viễn nói."),t("br"),t("br"),n._v("Hai vị này mặc dù thực lực mạnh mẽ, hắn cũng là thất tinh độc sư, cấp bậc không hề thấp bao nhiêu, chuyện này xử lý như vậy thất bại, thực sự có chút mất mặt."),t("br"),t("br"),n._v("Lúc trước nếu không phải như thế lỗ mãng, hỏi rõ ràng đối phương đến cùng là ai, cũng không trở thành, đánh đâu đâu cũng có phế tích, để tổng bộ người tới chê cười."),t("br"),t("br"),n._v("“Đến cùng xảy ra chuyện gì?”"),t("br"),t("br"),n._v("Nhìn trước mắt nguyên một đám sưng mặt sưng mũi trưởng lão, điện chủ, lại nhìn xem đầy đất phế tích, Bạch độc sư cũng nhịn không được nữa."),t("br"),t("br"),n._v("“Là dạng này...”"),t("br"),t("br"),n._v("Biết không nói ra, đối phương khẳng định còn có thể hỏi thăm, Nhược Thanh Viễn chần chờ một chút, đem trước chuyện phát sinh, nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Ngươi nói cái gì? Khai phái Tiên Tổ hồn phách gửi ở một cái gọi Tôn Cường trong thân thể?”"),t("br"),t("br"),n._v("Nghe xong lời nói, Bạch độc sư cau mày: “Điều đó không có khả năng! Chúng ta Độc điện khai phái Tiên Tổ, ngoại giới mệnh danh , trên thực tế, tại tổng bộ trong thư tịch từng có ghi chép, là cái thân nữ nhi, tự xưng Kịch Độc Tiên tử, làm sao có thể phát ra giọng nam? Huống chi, năm đó cùng người xung đột, hồn phi phách tán, cái gì đều không có lưu lại, làm sao có thể gửi ở Địa Ngục Thất Tâm liên bên trong sống sót? Tuyệt đối là giả!”"),t("br"),t("br"),n._v("“Khai phái Tiên Tổ... Là nữ?”"),t("br"),t("br"),n._v("Nhược Thanh Viễn sững sờ."),t("br"),t("br"),n._v("Mặc dù nửa trang in phái tổ sư rất nhiều chuyện xưa biết đến không ít, nhưng mà... Vị này tổ tiên, đến cùng là nam hay là nữ, thật đúng là không rõ ràng."),t("br"),t("br"),n._v("“Không sai, liên quan tới Tiên Tổ thư tịch, ta mặc dù chưa có xem qua, nhưng nghe một vị bát tinh độc sư, chuyên môn nói qua, nghe nói tổng bộ bên kia còn có nàng đệ tử lưu lại pho tượng... Đoạt xá nam tử, đồng thời phát ra giọng nam, không cần nghĩ, là giả!”"),t("br"),t("br"),n._v("Hải độc sư cũng lắc đầu."),t("br"),t("br"),n._v("“Nhưng... Thanh Thiền sát độc đối với hắn thật không dùng! Hơn nữa bản thân cũng là bách độc bất xâm...”"),t("br"),t("br"),n._v("Một trưởng lão nhịn không được nói."),t("br"),t("br"),n._v("“Thanh Thiền sát độc mặc dù lợi hại, nhưng vẫn là có rất nhiều thủ đoạn có thể hóa giải! Ta chỗ này có một viên  là một vị bát tinh độc sư luyện chế mà thành, chỉ cần nuốt vào, cho dù là thất tinh đỉnh phong kịch độc, cũng không thể làm bị thương chút nào...”"),t("br"),t("br"),n._v("Thấy hắn có này nghi vấn, Bạch độc sư nói: “Coi như không phải những này giải độc Thánh dược, một chút đặc thù thiên tài địa bảo cũng có giải độc năng lực, không nói cái khác, Hồng Viễn đế quốc hoàng thất có một gốc Thánh vực Bồ Đề thụ, nếu như loại thực vật này, nhận được Thánh vực tứ trọng nguyên thần tẩm bổ, kết ra Bồ Đề quả, đồng dạng có thể giải bách độc, thất tinh cấp bậc độc, đồng dạng vô dụng, thậm chí đối có thể độc chết linh hồn thuốc, cũng không tính là cái gì!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhược Thanh Viễn sững sờ."),t("br"),t("br"),n._v("Mặc dù cùng đối phương cùng thuộc thất tinh độc sư, nhưng hai vị này dù sao cũng là theo tổng bộ tới, bất kể tầm mắt vẫn là kiến thức, đều không phải là hắn có thể so sánh."),t("br"),t("br"),n._v("“Ngươi ý tứ... Hắn có thể là cái lừa gạt?”"),t("br"),t("br"),n._v("Trước đó tra hỏi trưởng lão, nhướng mày."),t("br"),t("br"),n._v("“Đây cũng không phải, chẳng qua là cảm thấy có chút kỳ quái thôi! Đối phương Tòng Thánh liền nắm giữ ngươi đều không cách nào chiến thắng thực lực, ngươi giáp trùng cắn lấy trên người đối phương, không những không có việc gì, ngược lại bị độc chết... Ta đều không thể làm được, trừ phi... Đạt tới bát tinh cấp bậc! Nhưng loại thực lực này người, không cần thiết mở cái này trò đùa...”"),t("br"),t("br"),n._v("Bạch độc sư lắc đầu."),t("br"),t("br"),n._v("Mặc dù cảm thấy “Tôn Cường” nói tới khai phái tổ sư phụ thể không đúng, nhưng tình huống khác, cũng nói không ra như thế về sau."),t("br"),t("br"),n._v("Để cho người ta kỳ quái nhất chính là, đối phương cũng không có yêu cầu khác, nếu quả thật có mưu đồ lời nói, dùng thân phận giả hù dọa mất mật đối phương, khẳng định sẽ đưa ra yêu cầu, tuyệt không chỉ là đọc sách đơn giản như vậy."),t("br"),t("br"),n._v("“Như vậy đi, ngươi nói vị này Tôn Cường hiện còn ở tàng thư điện ư?”"),t("br"),t("br"),n._v("Suy tư một hồi, thực sự nghĩ không ra như thế về sau, Bạch độc sư nhìn qua, mắt sáng lên: “Nếu không như vậy đi, ta thay các ngươi thăm dò một chút, nhìn một chút người này trình độ đến cùng cao bao nhiêu, nếu như là giả, sẽ đích thân ra tay, đem hắn đánh giết...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Do dự một chút, Nhược Thanh Viễn nhẹ gật đầu: “Tốt!”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1013"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1015"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);