(window.webpackJsonp=window.webpackJsonp||[]).push([[323],{679:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1268-đanh-phuong-chu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1268-đanh-phuong-chu"}},[n._v("#")]),n._v(" Chương 1268: Đánh phường chủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1268: Đánh phường chủ"),t("br"),t("br"),n._v("“Không tính?”"),t("br"),t("br"),n._v("Trương Cửu Tiêu sững sờ, lông mày lập tức nhăn lại: “Ngươi có ý tứ gì?”"),t("br"),t("br"),n._v("“Không có gì!”"),t("br"),t("br"),n._v("Trung niên nhân hừ một tiếng: “Chúng ta nơi này đặt cược, đều là xây dựng ở công bằng trên cơ sở, ngươi cùng vị kia Trương Huyền, một cái chứa yếu mê hoặc đám người, một cái cố ý đặt cược... Thuộc về gian lận hành động, đã vi phạm với quy tắc, ngươi tất cả tiền đặt cược không chỉ muốn tạm giam, còn muốn tiến hành đền bù!”"),t("br"),t("br"),n._v("“Đền bù?”"),t("br"),t("br"),n._v("“Nhiễu loạn thị trường trật tự, ác ý đặt cược... Người tới, đem cái tên này bắt lại cho ta!”"),t("br"),t("br"),n._v("Bàn tay lớn vẫy một cái, trung niên nhân một tiếng gọi."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Thời gian nháy mắt, theo bốn phía vọt tới hơn mười vị người mặc khôi giáp hộ vệ, mỗi một cái đều có Nguyên Thần cảnh đỉnh phong thực lực, đem Trương Cửu Tiêu vây quanh ở trung tâm."),t("br"),t("br"),n._v("“Các ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới đường đường lòng đất chợ đen, thua không thừa nhận, còn muốn đem bản thân bắt lấy, Trương Cửu Tiêu cáu sắp nổ, ngón tay một điểm, một chuôi trung phẩm Thánh khí lơ lửng ở trước mắt, tùy thời đều muốn động thủ."),t("br"),t("br"),n._v("Hắn mặc dù có vượt cấp năng lực chiến đấu, nhưng người trước mắt quá nhiều, vị này trung niên nhân càng là Xuất Khiếu cảnh cường giả đỉnh phong, căn bản không phải hắn loại thực lực này, có thể chống lại."),t("br"),t("br"),n._v("“Như thế lớn thương hội, thua đền không nổi, lại muốn bắt người... Đây là tác phong của các ngươi?”"),t("br"),t("br"),n._v("Còn không có động thủ, một cái thanh âm nhàn nhạt vang lên, chỉ thấy trên đài vừa mới chiến thắng Hồng Dương thanh niên, chẳng biết lúc nào xuất hiện tại trước mặt, tràn đầy không vui."),t("br"),t("br"),n._v("Vốn nghĩ, cầm tới thắng tới tiền, tham gia đấu giá hội, mua cái kia tháp ngôn cổ thư... Không nghĩ tới đối phương chẳng những không trả tiền, còn muốn đem Trương Cửu Tiêu vây quanh, cũng lại kìm nén không được, theo trên lôi đài bay tới."),t("br"),t("br"),n._v("“Là các ngươi liên thủ bày cục, vi phạm với quy tắc, không trừng phạt, người người đều như vậy, thương hội về sau, còn làm sao làm ăn?”"),t("br"),t("br"),n._v("Trung niên nhân vung tay áo một cái."),t("br"),t("br"),n._v("Chính là vừa rồi vị kia phường chủ."),t("br"),t("br"),n._v("Hai vạn tinh nguyên linh thạch thượng phẩm, toàn bộ chợ đen cộng lại, cũng chỉ có thế, cho bọn hắn, thị trường cũng không cần mở."),t("br"),t("br"),n._v("“Quy tắc? Chợ đen cũng chú ý quy tắc?”"),t("br"),t("br"),n._v("Trương Huyền cười nhạo."),t("br"),t("br"),n._v("Đều nói nơi này là không coi trọng quy tắc, quy củ địa phương... Hiện tại tới cùng ta nói những này?"),t("br"),t("br"),n._v("“Không quy củ không thành phương viên, liền xem như chợ đen cũng giống vậy...” Phường chủ nói."),t("br"),t("br"),n._v("“Đã ngươi tạm quy tắc, ta liền cùng ngươi nói một cái...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Danh Sư đường chú ý như thế quy củ địa phương, đều không làm gì được hắn, một cái chợ đen phường chủ, cùng hắn nói... Quả thực liền là ngân hàng trước mặt bán cắt bánh ngọt!"),t("br"),t("br"),n._v("“Lôi đài thi đấu đặt cược, người người đều có thể, Trương Cửu Tiêu là bạn ta, đối ta mang theo kỳ vọng, áp ta chiến thắng không có cái gì vấn đề đi!” Trương Huyền nói."),t("br"),t("br"),n._v("“Không có vấn đề!”"),t("br"),t("br"),n._v("Phường chủ gật đầu."),t("br"),t("br"),n._v("Chợ đen đặt cược, rất nhiều tuyển thủ áp bản thân chiến thắng, không có loại kích thích này, ai sẽ ở đây liều sống liều chết?"),t("br"),t("br"),n._v("Để bằng hữu áp bản thân chiến thắng, thuộc về bình thường kỹ thuật."),t("br"),t("br"),n._v("“Đã không có vấn đề, tỉ lệ đặt cược lại là các ngươi định, áp bao nhiêu, các ngươi đều tiếp nhận... Dựa vào cái gì nói chúng ta vi phạm với quy củ?” Trương Huyền khẽ nói."),t("br"),t("br"),n._v("“Rõ ràng có thể nhẹ nhõm chiến thắng, lại ngụy trang rất yếu... Thuộc về lừa gạt...”"),t("br"),t("br"),n._v("“Lừa gạt? Cái gọi là lừa gạt, là lừa gạt cùng lừa dối, dùng không đứng đắn thủ đoạn thu lợi... Ngươi hỏi một chút tất cả mọi người ở đây, ta cùng Hồng Dương chiến đấu, một mực nói không kiên trì nổi, không phải là đối thủ, để hắn không cần tiếp tục, là hắn không nghe khuyên ngăn, nhất định muốn cùng ta chiến đấu, lúc này mới được bức bất đắc dĩ, làm ra phản kích... Đây cũng là lừa gạt?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua: “Nếu quả thật muốn dùng cái này thu lợi, trực tiếp đáp ứng cùng hắn chiến đấu liền tốt, không cần thiết liên tiếp cự tuyệt!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trung niên nhân lông mày nhảy một cái."),t("br"),t("br"),n._v("Đối phương lời này, hắn vừa rồi cũng nghe đến."),t("br"),t("br"),n._v("Quả thực là làm cho đối phương không nên đánh, còn càng muốn đánh... Cẩn thận nói đến, thật không trách được đối phương."),t("br"),t("br"),n._v("“Không cần cùng ta ở đây nghiền ngẫm từng chữ một, nơi này là chợ đen, ta là phường chủ, lời nói của ta, liền là quy tắc! Người tới, đem hai cái này làm loạn gia hỏa cũng cho ta ấn xuống đi!”"),t("br"),t("br"),n._v("Biết nói tiếp, đối phương lý do càng nhiều, hất lên ống tay áo, trung niên nhân vạch mặt."),t("br"),t("br"),n._v("“Tìm không thấy lý do, liền muốn trực tiếp bắt người? Ta ngược lại muốn xem xem, ai dám động đến chúng ta một cọng tóc gáy...”"),t("br"),t("br"),n._v("Lông mày giương lên, Trương Huyền nhìn quanh một vòng."),t("br"),t("br"),n._v("“Lên!”"),t("br"),t("br"),n._v("Một cái hộ vệ lớn tiếng trách mắng, đi đầu vọt lên."),t("br"),t("br"),n._v("Coi như cái tên này thực lực mạnh mẽ, nhưng mà bọn họ nhiều người, càng tất cả đều là Nguyên Thần cảnh cường giả đỉnh phong, hẳn là nhẹ nhõm có thể bắt lại."),t("br"),t("br"),n._v("Trường kiếm lăng không, một đạo kiếm mang bắn tới."),t("br"),t("br"),n._v("Có thể tại chợ đen làm hộ vệ, trấn áp không tuân quy củ người, thực lực không thể khinh thường, kiếm mang còn chưa tới đến trước mặt, liền lôi kéo không khí phát ra nứt toác, dường như tiện tay đều sẽ đem người chém thành hai khúc."),t("br"),t("br"),n._v("Thấy đối phương kiếm khí cương mãnh bên trong mang theo âm nhu, Trương Huyền cũng không tránh né, thân thể đột nhiên nhoáng một cái, ngón tay chỉ tới."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Kiếm khí vỡ vụn, chân khí đi tới hộ vệ trước mặt, chỉ một chút, người sau liền cảm thấy đan điền một hồi kịch liệt rung động, “Đùng!” Vỡ vụn."),t("br"),t("br"),n._v("“Ngươi... Phế bỏ thực lực của ta?”"),t("br"),t("br"),n._v("Thân thể mềm nhũn, hộ vệ ngã trên mặt đất, trong mắt tràn đầy hoảng sợ."),t("br"),t("br"),n._v("Đan điền vỡ vụn, tương đương với tu vi hủy hết, tu luyện không biết bao nhiêu năm, triệt để phế bỏ."),t("br"),t("br"),n._v("Lười nhác trả lời, Trương Huyền dưới chân tiếp tục lay động, liên tiếp như là pháo thanh âm, trước đó vây quanh Trương Cửu Tiêu mười cái hộ vệ, mấy hơi thở qua đi, liền tất cả đều nằm trên mặt đất, cảm nhận được bị phế sạch đan điền, từng cái từ nội tâm chỗ sâu sinh ra sợ hãi."),t("br"),t("br"),n._v("Đám người này làm mưa làm gió, trợ Trụ vi ngược, coi như giết, đều không quá phận, huỷ bỏ đan điền, xem như hạ thủ lưu tình."),t("br"),t("br"),n._v("Thấy một cái nho nhỏ Nguyên Thần cảnh hậu kỳ, vậy mà ở ngay trước mặt hắn, đem thuộc hạ của mình phế bỏ, phường chủ khí tròng mắt đỏ hoe, bàn tay một trảo, lực lượng hùng hồn phun ra ngoài, lăng không bắt đến."),t("br"),t("br"),n._v("Híp mắt lại, Trương Huyền cũng không tránh né, đạp chân xuống, tới đón."),t("br"),n._v(" Đem tu vi tấn cấp đến Nguyên Thần cảnh hậu kỳ, còn không người luyện tập, đã cái tên này tìm phiền toái, không ngại đánh bên trên một chầu!"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nắm đấm cùng bàn tay của đối phương đối đầu cùng một chỗ, hai đạo lực lượng chạm vào nhau, bốn phía phát ra một hồi nổ thật to, khán đài tại lực lượng dư âm trùng kích vào, lập tức sụp đổ."),t("br"),t("br"),n._v("Trước đó xem náo nhiệt đám người, thấy như thế lực lượng cường đại xung kích, tất cả đều dọa đến nhao nhao phân tán bốn phía, Trương Cửu Tiêu cũng lùi về mấy chục mét có hơn."),t("br"),t("br"),n._v("“Ngươi... Làm sao lại mạnh như vậy?”"),t("br"),t("br"),n._v("Phường chủ con ngươi co rụt lại."),t("br"),t("br"),n._v("Vốn cho rằng đối phương chỉ là vượt cấp chiến đấu, thực lực so với hắn, kém rất nhiều, nằm mơ đều không có nghĩ đến, quyền chưởng tấn công, thế mà... Thế lực ngang nhau!"),t("br"),t("br"),n._v("Một cái Nguyên Thần cảnh hậu kỳ, cùng hắn Xuất Khiếu cảnh đỉnh phong sánh ngang lực lượng..."),t("br"),t("br"),n._v("Tận mắt nhìn thấy, đều cảm thấy khó có thể tin."),t("br"),t("br"),n._v("“Ngươi đến cùng là ai?”"),t("br"),t("br"),n._v("Loại này vượt cấp năng lực chiến đấu, lai lịch tất nhiên sẽ không đơn giản, phường chủ tuyệt không tin tưởng, chỉ là cái bình thường người khiêu chiến."),t("br"),t("br"),n._v("“Trương Huyền!”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, lật bàn tay một cái, Trương Huyền một chiêu đánh xuống."),t("br"),t("br"),n._v("Thoạt nhìn không có bất kỳ lực lượng nào, lại cho người ta một loại, khó mà chống lại cảm giác, phường chủ còn muốn nói nhiều cái gì, thấy chiêu này bổ tới, biết không ngăn cản không được, lần nữa nghênh tiếp."),t("br"),t("br"),n._v("Bất quá, nắm đấm cùng bàn tay của đối phương còn không có tiếp xúc, chỉ thấy nhân ảnh trước mắt chợt lóe, chẳng biết lúc nào đã đi tới trước mặt, nhẹ nhàng nhấn một cái."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Máu tươi phun ra, phường chủ bay ngược mà ra, tầng tầng ngã xuống đất, vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Trương Huyền Nguyên Thần cảnh trung kỳ thời điểm, còn kém không nhiều có thể cùng Xuất Khiếu cảnh đỉnh phong chiến đấu, hiện tại càng là đạt đến hậu kỳ, đối phương coi như rất mạnh, vẫn như cũ chống lại không được."),t("br"),t("br"),n._v("“Đi, bắt hắn cho ta đánh cho tàn phế!”"),t("br"),t("br"),n._v("Hai chiêu qua đi, biết đối phương căn bản không phải đối thủ, lười nhác tiếp tục động thủ, Trương Huyền một tiếng gọi."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Phía dưới lôi đài trong một cái phòng, lập tức bay ra hơn một trăm chuôi nhiều loại trường đao, thẳng tắp hướng trước mắt phường chủ bổ tới."),t("br"),t("br"),n._v("Những này đại đao, cứ việc đều chỉ là hạ phẩm Thánh khí, nhưng số lượng thực sự quá nhiều, hơn trăm cái đồng thời tiến công, trong nháy mắt đao khí tung hoành, phường chủ con ngươi thu hẹp, đánh trả cũng không kịp, liền bị đánh máu me đầm đìa."),t("br"),t("br"),n._v("“Những thứ này...”"),t("br"),t("br"),n._v("Bẩm báo phường chủ lão giả, nhìn thấy nhiều như vậy đao, lông mày nhảy loạn."),t("br"),t("br"),n._v("Không phải cái khác, chính là lôi đài thi đấu, dùng để cung cấp cho tuyển thủ dao kéo... Chỉ là, những vật này, không đều là không có linh tính ư? Làm sao như thế nghe lời, đối phường chủ tiến công?"),t("br"),t("br"),n._v("“Hẳn là bị khải linh...”"),t("br"),t("br"),n._v("Nắm đấm căng thẳng, rất nhanh hiểu được."),t("br"),t("br"),n._v("Rất hiển nhiên, những binh khí này, đều bị đối phương khải linh, nguyên nhân chính là như vậy, mới dễ dàng như vậy nghe lời, đối phường chủ tiến công..."),t("br"),t("br"),n._v("Cái tên này, từ khi đi vào tuyển vũ khí, đến đi ra không có kinh nghiệm bao lâu thời gian... Nhanh như vậy liền khải linh thành công, hơn nữa nhiều như vậy binh khí... Đến cùng làm sao làm được?"),t("br"),t("br"),n._v("Không chỉ hắn biểu tình không dám tin tưởng, Trương Cửu Tiêu cũng là khóe miệng giật một cái."),t("br"),t("br"),n._v("Sớm biết Trương sư khải linh thủ đoạn, đương thời có một không hai, không nghĩ tới trước thời hạn liền có chuẩn bị, đem người ta trường đao đều thu phục."),t("br"),t("br"),n._v("Nhìn tới, vừa mới đi tới, liền đoán ra muốn lấy đi tiền đặt cược không dễ dàng như vậy... Đối phương chính diện bức bách, vừa khéo cho cái cớ thôi."),t("br"),t("br"),n._v("Ô ô ô ô!"),t("br"),t("br"),n._v("Hơn một trăm chuôi hạ phẩm Thánh khí, hình như đi qua huấn luyện đặc thù, mỗi một lần tiến công, đều là phường chủ chỗ yếu nhất, thời gian không dài, vị này mới vừa rồi còn khí thế hung hăng Xuất Khiếu cảnh cường giả đỉnh phong, trên thân liền máu tươi chảy xuôi, không biết bị chặt bao nhiêu đao, bất cứ lúc nào cũng sẽ tử vong."),t("br"),t("br"),n._v("“Dừng tay... Ta bằng lòng đưa ngươi thắng linh thạch trả lại...”"),t("br"),t("br"),n._v("Biết tiếp tục nữa, sớm tối đều sẽ bị tươi sống chém chết, phường chủ vội vàng la lên."),t("br"),t("br"),n._v("Mặc dù cảm thấy uất ức, nhưng cũng không có cách nào."),t("br"),t("br"),n._v("Bản thân không phải là đối thủ, càng ngăn cản không nổi những này đại đao."),t("br"),t("br"),n._v("“Trả lại?”"),t("br"),t("br"),n._v("Vẫy tay một cái, tất cả trường đao bay trở về, đồng loạt lơ lửng tại Trương Huyền sau lưng, tựa như khổng tước xòe đuôi."),t("br"),t("br"),n._v("“Đúng...”"),t("br"),t("br"),n._v("Giãy dụa lấy đứng dậy, phường chủ lấy ra một chiếc nhẫn đưa tới: “Đây là một vạn chín ngàn tám trăm linh thạch thượng phẩm...”"),t("br"),t("br"),n._v("Đây là lòng đất chợ đen, không biết bao nhiêu năm lợi nhuận, một khi cho, toàn bộ chợ đen mắt xích tài chính, sẽ gián đoạn."),t("br"),t("br"),n._v("Tiện tay nhận lấy, Trương Huyền nhìn một cái, nhẹ gật đầu."),t("br"),t("br"),n._v("Về số lượng đầy đủ, nhìn tới đối phương không đùa mánh khóe."),t("br"),t("br"),n._v("“Những này tiền đánh cược là ta chiến đấu thắng tới... Ngươi đối ta cùng bằng hữu của ta ra tay, nhìn một chút làm sao bồi thường đi!”"),t("br"),t("br"),n._v("Đem linh thạch cất kỹ, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("“Đền bù?”"),t("br"),t("br"),n._v("Phường chủ khuôn mặt tái đi."),t("br"),t("br"),n._v("Đều táng gia bại sản, còn muốn đền bù?"),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cái này chợ đen, đem nhân mạng không xem ra gì, Danh Sư đường cùng Tiềm Xung thành mặc kệ, đã gặp gỡ, tự nhiên muốn quản một cái, nếu không, người người đều như vậy, danh sư lập xuống quy củ ở đâu?"),t("br"),t("br"),n._v("“Không biết... Làm sao đền bù?” Phường chủ cắn răng."),t("br"),t("br"),n._v("“Ta nghe nói, các ngươi chợ đen, vừa mới đạt được một bản, tháp ngôn cổ thư?”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1276"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1278"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);