(window.webpackJsonp=window.webpackJsonp||[]).push([[326],{681:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-119-chan-chinh-luan-đan-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-119-chan-chinh-luan-đan-ha"}},[n._v("#")]),n._v(" Chương 119: Chân chính Luận đan (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Hiển nhiên, thể chất thuyết pháp, không bảo vệ được!” "),t("br"),t("br"),n._v(" “Bởi vậy, Chương Kiến đan sư cũng nghiêm túc nghiên cứu, cuối cùng đem chính mình kết luận ở lại 《 Thối Đan luận 》 bên trong, này bản thư tịch tường tận viết quan điểm của hắn, cho rằng Thối Thể đan hiệu quả không giống, cũng không phải là thể chất, mà là luyện chế đan dược thời điểm, dược lực phân tán không đều đều dẫn đến! Dù cho đồng nhất lô, tương tự cấp bậc viên thuốc, cũng có dược hiệu không giống tồn tại!” "),t("br"),t("br"),n._v(" “Cái này lý luận tiếp diễn mấy năm, mãi đến tận mười hai năm trước, lại bị người nghi vấn cùng lật đổ! Nghi vấn không phải luyện đan sư mà là một vị nhất tinh danh sư, hắn nói, nếu như luyện đan thời điểm, dược lực phân tán không đều, cái kia vì sao chỉ có Thối Thể đan mới phải xuất hiện tình huống như thế? Mà những đan dược khác không có?” "),t("br"),t("br"),n._v(" “Vì lẽ đó, hắn đưa ra một cái lớn mật ý tưởng, ghi lại ở 《 Ninh Hàn trích lời 》 bên trong, quyển sách này trên thị trường lượng tiêu thụ rất nhỏ, cũng chỉ có công hội sơ đẳng tàng thư khố có một quyển, mọi người nếu như chưa từng xem, rảnh rỗi có thể đi nhìn, ngay ở thứ 19 xếp hàng giá sách nhất bên phải xó xỉnh!” "),t("br"),t("br"),n._v(" Trương Huyền nở nụ cười một tiếng, tiếp tục nói: “Vị này gọi là Ninh Hàn danh sư, đưa ra một cái lớn mật suy đoán, cho rằng Thối Thể đan sở dĩ xuất hiện tình huống như thế, là bởi vì vị thuốc chính tài hoặc là thủ pháp luyện chế cùng với những cái khác không giống mà tạo thành! Đương nhiên, hắn chỉ là đưa ra lý luận, hợp lại không có thực tiễn!” "),t("br"),t("br"),n._v(" “Tám năm trước, Hồng Hiên Vương quốc luyện đan sư Triệu Nhạc, theo trong quyển sách này đạt được mở, chuyên môn làm thí nghiệm, thay đổi đầy đủ mười tám loại vị thuốc chính, cùng bốn mươi hai loại luyện đan phương pháp, cuối cùng được có kết luận! Hắn cũng đem kết quả nghiên cứu viết đi, hợp lại bộ sáng tác, gọi 《 tôi thể nói tỉ mỉ 》, rất khéo, chúng ta công hội sơ đẳng tàng thư khố thì có, tại thứ hai mươi bảy xếp hàng trái bên trên bên cạnh, cùng 《 Dương Chân luyện đan thuyết 》, 《 Bích Huyết đan phương pháp luyện chế 》 hai bản thư cùng nhau! Các ngươi không tin, có thể tìm người lấy tới xem một chút!” "),t("br"),t("br"),n._v(" “Ta cho rằng, trong quyển sách này đáp án, là chính xác, đủ có thể trả lời vấn đề của ngươi!” "),t("br"),t("br"),n._v(" Nói đến đây, Trương Huyền ngừng lại, bàn tay lớn vẫy một cái, ra hiệu Âu Dương Thành phái người đi lấy thư.?"),t("br"),t("br"),n._v(" “Không cần...” "),t("br"),t("br"),n._v(" Mới vừa rồi còn dương dương tự đắc Kim Thần đan sư, nghe xong những cái này sắc mặt trở nên giống như nước lẩu, liên tiếp lui về phía sau hai bước, phảng phất bỗng chốc già rồi vài tuổi."),t("br"),t("br"),n._v(" “Làm sao? Không đem thư tịch mang tới, ta sợ ta trả lời đi ra, Kim Đan sư không thừa nhận!” Trương Huyền nói."),t("br"),t("br"),n._v(" “Của ngươi trả lời là chính xác, quyển sách này ta xem qua, cùng ngươi nói giống như đúc!” Kim Thần đan sư gật đầu."),t("br"),t("br"),n._v(" “Chính xác?” "),t("br"),t("br"),n._v(" “Giống như đúc?” "),t("br"),t("br"),n._v(" “Cái này không thể nào đi... Kim Thần đan sư tuỳ tiện nhắc tới ra một vấn đề, hắn tin miệng trả lời, còn nói ra nhiều như vậy thư tịch, thậm chí nhớ kỹ vị trí cụ thể... Sao có thể có chuyện đó?” "),t("br"),t("br"),n._v(" “Hay là người sao? Thật hay giả?” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Nghe hắn thừa nhận, gian phòng lần thứ hai vỡ tổ."),t("br"),t("br"),n._v(" Vừa nãy mọi người tự cho là đúng cho rằng, Trương Huyền bị giới hạn tuổi, sẽ không có xem qua bao nhiêu thư, chính tai nghe được thế mới biết, cùng hắn so sánh... Bọn họ mới thật sự là chưa từng xem thư!"),t("br"),t("br"),n._v(" Người ta xem xong, chẳng những có thể rõ ràng nhớ kỹ trong đó nội dung cùng thời gian, lại có thể nói ra thư tên gọi, bày ra tại thư khố vị trí..."),t("br"),t("br"),n._v(" Mà bọn họ... Có thể nhớ kỹ nội dung bên trong là tốt lắm rồi!"),t("br"),t("br"),n._v(" Xem ra giữa người và người, thật hắn mẹ là có khoảng cách!"),t("br"),t("br"),n._v(" “Nếu Kim Đan sư thừa nhận thức ta trả lời chính xác, vậy xin hỏi vấn đề thứ hai đi!” "),t("br"),t("br"),n._v(" Trương Huyền cười cợt."),t("br"),t("br"),n._v(" So ký ức?"),t("br"),t("br"),n._v(" Đùa giỡn, trong đầu nắm giữ Thiên Đạo thư viện, tinh thần hơi động, thư tịch liền bay đến, bản thân chiếu niệm là được, ngươi ký ức cho dù tốt, chẳng lẽ còn có thể so sánh ta niệm chuẩn xác?"),t("br"),t("br"),n._v(" “Vấn đề thứ hai luyện chế nhất phẩm  thời điểm, vì sao lò luyện đan sẽ xuất hiện vết rách, không đáng kể?” "),t("br"),t("br"),n._v(" Kim Thần đan sư do dự chốc lát, lần này băn khoăn có tới mười phút, mới mở miệng."),t("br"),n._v(" “Lợi hại!” "),t("br"),t("br"),n._v(" “Vấn đề này thật nhọn sắc bén!” "),t("br"),t("br"),n._v(" “Đúng đấy, luyện chế nhất phẩm Tăng Lực đan thời điểm, thường thường sẽ xuất hiện lô đỉnh vỡ vụn tình huống, cái này ở bề ngoài là luyện đan sư vấn đề, trên thực tế cùng luyện khí sư cũng có quan hệ!” "),t("br"),t("br"),n._v(" “Đúng đấy, xem hắn làm sao trả lời!” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Nghe được Kim Thần đan sư vấn đề thứ hai, mọi người tinh thần lần thứ hai chấn động."),t("br"),t("br"),n._v(" Luyện đan sư lò luyện đan do luyện khí sư rèn đúc, luyện đan thời gian vỡ vụn, đã không trọn vẹn thuộc về luyện đan phạm trù, có chút qua giới."),t("br"),t("br"),n._v(" Đương nhiên nếu như cẩn thận cân nhắc, vẫn như cũ thuộc về luyện đan cái nghề này, chính là bởi vì loại này ba phải cái nào cũng được, không ít luyện đan sư căn bản sẽ không chú ý loại này vấn đề, chớ nói chi là trả lời."),t("br"),t("br"),n._v(" Tất cả mọi người nghe ra ý của hắn, Trương Huyền làm sao không biết, khóe miệng vung lên: “Vấn đề này, Ngô Hiểu đan sư 《 lò luyện đan bảo dưỡng 》, Triệu Khiêm đan sư 《 luyện đan chú trọng công việc 》, Ngưu Tuyền đan sư 《 việc vặt vãnh nói tỉ mỉ 》, Bàng Bác đan sư 《 mới lô đỉnh bảo dưỡng 》...” "),t("br"),t("br"),n._v(" Không có một chút nào dừng lại, vừa mở miệng liền nói mười mấy bản thư tịch tên gọi, cuối cùng mới nói: “... Những sách này tịch bên trong, đều có ghi chép, kỳ thực nói trắng ra cũng không là vấn đề, các ngươi sử dụng lò luyện đan, là phổ thông cửu luyện thiết, loại này thiết tuy rằng cứng rắn, lại không chịu được nóng lạnh xung kích, Tăng Lực đan vị thuốc chính tài, là hàn băng man thú huyết dịch, có chứa chí âm chí hàn chi khí, cùng nóng lô đỉnh đụng nhau, tự nhiên khó có thể chịu đựng!” "),t("br"),t("br"),n._v(" “Nếu như muốn giải quyết, tốt nhất sử dụng Huyền Thiết lô đỉnh! Hoặc là mỗi luyện chế một lò Tăng Lực đan, liền thay đổi một cái lô đỉnh!” "),t("br"),t("br"),n._v(" Trương Huyền cười khanh khách nói."),t("br"),t("br"),n._v(" Vấn đề này nhìn như xảo quyệt, trên thực tế sơ đẳng tàng thư khố đều có ghi chép, chỉ có điều bên trong tàng thư thực sự quá nhiều, công hội hết thảy học đồ, đều muốn nghiên cứu một ít thành luyện đan phương pháp, tốt sát hạch chính thức luyện đan sư, như thế nào sẽ cẩn thận nghiên cứu, một ít vô dụng thư tịch!"),t("br"),t("br"),n._v(" Lại nói, sát hạch tới chính thức luyện đan sư, địa vị không giống, có càng nhiều xã giao cùng quyền lợi, đọc sách thời gian thì càng thiếu, rất nhiều tri thức, tại tàng thư khố liền có thể tìm tới, bọn họ nhưng từ không đi tìm, thậm chí cũng không biết hiểu."),t("br"),t("br"),n._v(" Bởi vậy, những vấn đề này, cái khác đan sư đều không đáp lại được, đối với trong đầu phục chế toàn bộ sơ đẳng tàng thư khố Trương Huyền tới nói, không có bất luận cái gì độ khó."),t("br"),t("br"),n._v(" “Không biết câu trả lời này, Kim Thần đan sư thoả mãn hay không? Không hài lòng, ta còn có thể nói tiếp...” "),t("br"),t("br"),n._v(" “Không cần, của ngươi trả lời rất chính xác!” "),t("br"),t("br"),n._v(" Kim Thần đan sư trên đầu bốc lên mồ hôi lạnh."),t("br"),t("br"),n._v(" Hắn vừa mới bắt đầu luyện chế Tăng Lực đan thời điểm, gặp phải vấn đề này, tìm khắp nơi người hỏi dò, cuối cùng là tại một quyển sách bên trên đạt được đáp án, chính là Trương Huyền vừa nãy trong miệng nói Bàng Bác đan sư 《 mới lô đỉnh bảo dưỡng 》."),t("br"),t("br"),n._v(" Hắn vốn tưởng rằng chỉ có này một quyển sách bên trên ghi chép chuyện này, nhân cơ hội làm tốt khó đối phương, không nghĩ tới, trước mắt cái tên này, một hơi nói rồi mười mấy bản."),t("br"),t("br"),n._v(" May mà không lỗ mãng đem tên sách nói ra, bằng không, đối phương một hơi nói ra nhiều như vậy, bản thân khẳng định mất mặt xấu hổ."),t("br"),t("br"),n._v(" Mẹ nó, cái tên này đầu óc đến cùng cái gì làm?"),t("br"),t("br"),n._v(" Làm sao sẽ nhớ tới rõ ràng như thế?"),t("br"),t("br"),n._v(" Nhìn thấy của hắn dáng vẻ, Âu Dương Thành cùng Đỗ Mãn liếc mắt nhìn nhau, đồng thời cười khổ, lúc này mới cái nào đến cái nào, nếu như cho các ngươi biết, trước mắt cái tên này, liền câu nói kia xuất từ trong thư tịch cái kia tờ, đều có thể rõ ràng, chỉ sợ cũng sẽ không tự tìm kích động, không phải muốn tiến hành loại này Luận đan!"),t("br"),t("br"),n._v(" Nói thật, người ta nói muốn luyện đan vạch ra thiếu hụt, xác thực là gia tăng rồi độ khó, theo người ta so tri thức còn muốn thắng lợi..."),t("br"),t("br"),n._v(" E sợ cũng chỉ có thể nằm mơ ngẫm lại..."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-127"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-129"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);