(window.webpackJsonp=window.webpackJsonp||[]).push([[327],{682:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1271-trong-long-đau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1271-trong-long-đau"}},[n._v("#")]),n._v(" Chương 1271: Trong lồng đấu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1271: Trong lồng đấu"),t("br"),t("br"),n._v("“Trong lồng đấu?”"),t("br"),t("br"),n._v("Đám người xôn xao."),t("br"),t("br"),n._v("Loại phương thức tranh tài này, bọn họ biết được."),t("br"),t("br"),n._v("Thời kỳ Thượng Cổ, Dị Linh tộc nhân khống chế nhân tộc lúc, liền thường xuyên lấy loại phương thức này tìm niềm vui."),t("br"),t("br"),n._v("Là đem một đám người nhốt tại cùng một cái lồng bên trong, tự giết lẫn nhau, cuối cùng thắng được, có thể sống sót, nếu không, chỉ có bị ăn một con đường có thể đi, vô cùng tàn nhẫn, thậm chí đối với tình người, đều là cực lớn khảo nghiệm."),t("br"),t("br"),n._v("Chí thân hảo hữu, vì mình sống sót, đều sẽ đối cốt nhục ra tay... Loại phương pháp này, tà ma ngoại đạo, từ trước tới nay bị lấy người vì vốn Danh Sư đường vứt bỏ... Làm sao Thánh tử điện thi vòng hai sẽ dùng?"),t("br"),t("br"),n._v("“Mọi người không cần khẩn trương, cái này cái gọi là trong lồng đấu, cũng không phải là máu tanh chém giết, mà là đào thải!”"),t("br"),t("br"),n._v("Thấy mọi người biểu lộ không đúng, Triệu Hưng Mặc khoát tay áo, giải thích nói: “Là đem bọn ngươi đồng thời trục xuất tại một cái đảo hoang bên trên, trong vòng ba ngày, nghĩ biện pháp tìm tới những người khác, hơn nữa đào thải trong đó mười ba vị, mới có thể thông qua! Đương nhiên, đào thải là nghĩ biện pháp bóp nát trên người hắn mang theo ngọc phù, mà không phải chém giết! Nếu như phát hiện, tàn sát đồng bạn, trực tiếp hủy bỏ tư cách!”"),t("br"),t("br"),n._v("“Không thể chém giết, bóp nát ngọc phù?”"),t("br"),t("br"),n._v("“Cái này rất khó...”"),t("br"),t("br"),n._v("Đám người tất cả đều vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Tất cả mọi người là thiên tài, thực lực cho dù có chút chênh lệch, nhưng chạy trốn năng lực, cùng thủ đoạn bảo mệnh, không kém bao nhiêu, đánh lén chém giết, có lẽ còn có thể làm được, chỉ bóp nát ngọc phù, không thương tổn cùng tính mạng, độ khó bao lớn, có thể tưởng tượng được!"),t("br"),t("br"),n._v("Có đôi khi, làm không cẩn thận, liền bị người giết ngược lại!"),t("br"),t("br"),n._v("“Đương nhiên, cái này đảo hoang, cũng không phải là chỉ có các ngươi những người này, còn có Thánh thú, sớm đã bố trí tốt bẫy rập, cùng đặc thù nơi hiểm yếu... Muốn đào thải người khác đồng thời, còn muốn bảo hộ bản thân!”"),t("br"),t("br"),n._v("Triệu Hưng Mặc con mắt chợt lóe: “Nếu như, ngươi chỉ ôm tại chỗ bất động, khiến người khác đào thải người khác dự định, có thể nói cho các ngươi biết, tất nhiên cũng sẽ gặp phải đào thải! Thánh tử điện, sẽ không thu nhận giúp đỡ kẻ hèn nhát cùng trốn tránh người. Mặt khác, trong vòng ba ngày, đào thải không được mười ba cái, tất cả mọi người... Đều sẽ mất đi tư cách!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("“Nói cách khác, nhất định phải đào thải, nếu không chính mình cũng không cách nào đi vào Thánh tử điện?”"),t("br"),t("br"),n._v("“Cái này có chút quá tàn khốc đi!”"),t("br"),t("br"),n._v("“Đại lục đệ nhất học phủ, nếu như dễ dàng như vậy đi vào, hàng năm tân sinh đều chỉ sợ muốn chen bể!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đám người xôn xao về sau, mỗi người trầm mặc."),t("br"),t("br"),n._v("Cái này trong lồng đấu, nghe đơn giản, trên thực tế so chân chính đặt ở lồng bên trong, còn tàn khốc hơn."),t("br"),t("br"),n._v("Đối một người thống trù, thực lực, mưu kế rất nhiều thủ đoạn, đều có tổng hợp kiểm tra đánh giá, vượt xa quá đơn thuần chiến đấu."),t("br"),t("br"),n._v("Không hổ là Thánh tử điện khảo hạch, vừa ra tay cũng làm người ta cảm thụ độ khó."),t("br"),t("br"),n._v("“Đây là ngọc phù, đại biểu mỗi người các ngươi thân phận, có thể đặt ở trên thân, cũng có thể giấu đi, đặt ở bất kỳ địa phương nào, nhưng mà điều kiện tiên quyết là chỉ có thể ở hòn đảo bên trên, chỉ cần không bị bóp nát, liền đại biểu không có bị đào thải!”"),t("br"),t("br"),n._v("Bàn tay lắc một cái, hơn ba mươi quả ngọc phù thẳng tắp hướng đám người bay tới, mỗi người một cái."),t("br"),t("br"),n._v("“Có thể giấu đi?”"),t("br"),t("br"),n._v("“Nói như vậy... Coi như người bị bắt, cũng vô dụng?”"),t("br"),t("br"),n._v("“Không có ngọc phù, liền không cách nào đào thải, giết hắn cũng vô dụng, cái này... Làm sao bây giờ?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lần nữa ngẩn ngơ, đám người từng cái khó tin."),t("br"),t("br"),n._v("Ngọc phù ở trên người, đem hắn đánh bại, ép đối phương lấy ra, thuận tay bóp nát, mười phần đơn giản, có thể... Thứ này có thể giấu đi, một khi đặt ở hòn đảo một bên, coi như đem người bắt lấy, cũng vô dụng thôi!"),t("br"),t("br"),n._v("Vốn là khó, quy định này mới ra, độ khó trong nháy mắt, lần nữa tăng lên không chỉ gấp mười lần!"),t("br"),t("br"),n._v("“Đây cũng là một loại tâm tính khảo nghiệm!”"),t("br"),t("br"),n._v("Cùng đám người kinh ngạc khác biệt, Trương Huyền mắt sáng lên."),t("br"),t("br"),n._v("Tâm hắn cảnh cao, đối Thánh tử điện cũng không có như vậy khẩn cấp yêu cầu, ngoài cuộc tỉnh táo trong cuộc u mê, rất dễ dàng nhìn ra vấn đề."),t("br"),t("br"),n._v("Đối phương cố ý nói, có thể đem ngọc phù giấu đi, trên thực tế là đối đám người tâm tính một cái khảo hạch."),t("br"),t("br"),n._v("Nói thật, muốn nói an toàn, nơi nào có thể so sánh trên người mình an toàn?"),t("br"),t("br"),n._v("Giấu đi, rõ ràng là không tin mình, thật muốn làm như thế, tất nhiên sẽ ở trước mặt đối phương thật to mất phân."),t("br"),t("br"),n._v("Đương nhiên, việc này, hắn cũng không biết nói đi ra, xòe bàn tay ra, nhẹ nhàng bóp một cái, bay tới ngọc phù liền rơi vào lòng bàn tay."),t("br"),t("br"),n._v("Là một loại đặc thù mặc ngọc, mặt trái viết tên của mình, mà chính diện, thì điêu khắc văn lộ kỳ quái, dường như có đưa tin công năng, chỉ cần bóp nát, liền bày tỏ, thân phận của ngươi biến mất, bị triệt để đào thải."),t("br"),t("br"),n._v("“Triệu sư, nếu có người sử dụng lợi hại vũ khí, chúng ta chẳng phải liền đánh trả lực lượng đều không có, liền sẽ bị trực tiếp đào thải?”"),t("br"),t("br"),n._v("Tiềm Xung đế quốc thiên tài Mã Minh Hải hỏi lên."),t("br"),t("br"),n._v("Nghe được hắn, đám người đồng loạt đem ánh mắt tập trung ở Trương Huyền trên thân."),t("br"),t("br"),n._v("Cái tên này trước đây cùng Nhạc đường chủ chiến đấu, một cái ném ra hơn trăm cái khôi lỗi, nửa bước Lĩnh Vực cảnh đều bị đánh thành đầu heo, thật muốn dùng để đối phó bọn hắn... Căn bản là chống lại không được."),t("br"),t("br"),n._v("“Trong lồng đấu khảo hạch, công bằng công chính, không cho phép sử dụng vũ khí, cướp đoạt ngọc phù, làm ra đối với mình đào thải đối thủ có lợi sự tình, như có vi phạm, trực tiếp đào thải!” Triệu Hưng Mặc nói."),t("br"),t("br"),n._v("“Vậy là tốt rồi...”"),t("br"),t("br"),n._v("Đám người thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Chỉ cần không cho phép sử dụng vũ khí, vị này Trương Huyền, coi như sức chiến đấu không yếu, cũng không có đáng sợ như vậy!"),t("br"),t("br"),n._v("Đánh không lại, bọn họ thủ đoạn bảo mệnh vẫn phải có, chạy trốn cũng không thành vấn đề."),t("br"),t("br"),n._v("“Còn có cái gì vấn đề ư?”"),t("br"),n._v(" “Không còn... Đúng rồi, Triệu sư, không biết chúng ta vị trí đảo hoang ở nơi nào? Phạm vi lớn bao nhiêu? Ngươi nói Thánh thú cùng bẫy rập, đều là cấp bậc gì?”"),t("br"),t("br"),n._v("Một vị danh sư nói."),t("br"),t("br"),n._v("“Đây đều là bí mật, chính các ngươi đổ địa phương, kỹ càng tìm tòi mới có thể biết, được rồi, nếu như không có vấn đề, hiện tại liền xuất phát! Còn có, liền là giữa các ngươi không cho phép đưa tin, một khi phát hiện, đồng dạng đào thải!”"),t("br"),t("br"),n._v("Triệu Hưng Mặc nhẹ gật đầu: “Đừng nghĩ đến gian lận, chui quy tắc sơ hở, ta đã dám nói như vậy, toàn bộ đảo tình huống, tự nhiên là rõ rõ ràng ràng!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Đám người đồng thời gật đầu."),t("br"),t("br"),n._v("“Nhạc đường chủ, để bọn hắn lên đường đi!”"),t("br"),t("br"),n._v("Gặp tất cả mọi người rõ ràng quy tắc, Triệu Hưng Mặc không nói thêm lời, quay đầu nhìn về phía một bên Nhạc Nhất Toàn."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Nhạc đường chủ vẫy bàn tay lớn một cái, trên bầu trời bay tới hơn ba mươi đầu phi hành Thánh thú, phía trên đều có một cái võ giả, đưa tới che mắt đồ vật, đem bọn hắn từng cái mang theo lưng thú."),t("br"),t("br"),n._v("Cái này che mắt đồ vật, cùng lòng đất chợ đen gần như, một khi bịt kín, không chỉ con mắt không nhìn thấy, thần thức cũng không cách nào phóng ra ngoài, tại đối phương dẫn đầu bên dưới, lập tức nghe được hét dài một tiếng, Thánh thú lăng không bay lên."),t("br"),t("br"),n._v("Không biết bay bao lâu, thân thể nhoáng một cái, Trương Huyền bàn chân lần nữa rơi trên mặt đất, đem che mắt đồ vật xé, lập tức phát hiện, đã đi tới trong một khu rừng rậm rạp, bốn phía trống trải, không có bất kỳ ai."),t("br"),t("br"),n._v("Nhìn tới, che lên con mắt, không chỉ để cho mình đám người không biết đi nơi nào, càng tìm không thấy đồng bạn, không cách nào hợp tung liên hoành."),t("br"),t("br"),n._v("Bởi vậy, muốn thông qua khảo hạch, chỉ có thể dựa vào bản thân."),t("br"),t("br"),n._v("“Trương Cửu Tiêu thực lực tiến bộ, đạt đến Nguyên Thần cảnh hậu kỳ, lại thêm đoạn đường này chỉ điểm của ta, đối với chiến đấu lĩnh ngộ cùng thực lực, đều có rõ ràng tiến bộ... Chỉ cần không gặp được Mã Minh Hải đám người, nên vấn đề không lớn!”"),t("br"),t("br"),n._v("Đấu vòng loại nghe tàn khốc, trên thực tế cũng là có tính nhắm vào."),t("br"),t("br"),n._v("Thực lực quá yếu tự nhiên không kiên trì nổi, nhưng đạt tới hắn loại tu vi này, căn bản không cần sợ hãi bất luận kẻ nào."),t("br"),t("br"),n._v("Trương Cửu Tiêu cứ việc thực lực cùng hắn so kém rất nhiều, bất kể nói thế nào, đều là con em đại gia tộc, càng tiếp nhận chỉ điểm của mình... Chỉ cần không gặp quá biến thái, hẳn là nhẹ nhõm ứng đối, chí ít chạy trốn, không thành vấn đề, không cần lo lắng."),t("br"),t("br"),n._v("Không suy nghĩ nhiều, thân thể nhoáng một cái, rơi vào ngọn cây, nhìn chung quanh một vòng, phát hiện cây cối lít nha lít nhít, một cái không nhìn thấy phần cuối, không biết bao xa, thật giống không phải một cái hòn đảo, mà là một cái không biết bao sâu sơn mạch."),t("br"),t("br"),n._v("“Đây là đảo hoang? Thật là quá lớn đi...”"),t("br"),t("br"),n._v("Trong lòng hoảng sợ."),t("br"),t("br"),n._v("Thực lực của hắn bây giờ, một cái đều không nhìn thấy phần cuối, đến cùng lớn bao nhiêu?"),t("br"),t("br"),n._v("Năm trăm cây số? Một ngàn cây số? Thậm chí... Ba ngàn cây số?"),t("br"),t("br"),n._v("Như thế lớn đảo, để lên chừng ba mươi người, trong vòng ba ngày đem hắn bắt lấy, hơn nữa đào thải..."),t("br"),t("br"),n._v("Quả nhiên không có đơn giản như vậy."),t("br"),t("br"),n._v("Nhìn tới như thế nào tìm đến đối thủ, cũng là một loại khảo nghiệm."),t("br"),t("br"),n._v("“Như thế lớn đảo, rõ rõ ràng ràng, làm sao làm được?”"),t("br"),t("br"),n._v("Theo ngọn cây rơi trên mặt đất, Trương Huyền đồng thời nghi ngờ."),t("br"),t("br"),n._v("Thần trí của hắn, bởi vì tu luyện Thiên Đạo công pháp, đạt đến 2 500 mét phạm vi, vượt xa quá người bình thường 400, hòn đảo này như thế lớn, coi như Triệu Hưng Mặc thực lực rất mạnh, cũng không có khả năng nhìn tới đi!"),t("br"),t("br"),n._v("“Trước mặc kệ, gặp được tu luyện giả, trực tiếp đào thải...”"),t("br"),t("br"),n._v("Nghĩ một lát không nghĩ ra, Trương Huyền không nghĩ nhiều nữa."),t("br"),t("br"),n._v("Trong khoảng thời gian này liên tục tiến bộ, coi như không sử dụng vũ khí, sức chiến đấu cũng có thể so với Lĩnh Vực cảnh sơ kỳ cường giả, gặp được khảo hạch danh sư, nhìn xem không vừa mắt, trực tiếp đào thải, không cần lo lắng cái gì, trái lại hẳn là những người khác sợ hãi cùng hắn chạm mặt."),t("br"),t("br"),n._v("Dọc theo sơn mạch đi một hồi, biết như vậy chẳng có mục đích, khả năng chuyển lên một ngày đều không thấy được người, cổ tay khẽ đảo, một cái la bàn xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Thân là trận pháp sư, thứ này là thiết yếu, cứ việc chưa hề dùng qua, trên thân nhưng cũng chuẩn bị mấy khối, cấp bậc cũng không tính là quá thấp."),t("br"),t("br"),n._v("“Cái hướng kia linh khí nồng đậm... Nhìn tới hòn đảo trung tâm, hẳn là ở nơi đó, đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Tìm đúng phương hướng, Trương Huyền mũi chân điểm một cái, nhanh chóng vọt lên phía trước đi."),t("br"),t("br"),n._v("Nơi này bốn phía đều là Thánh thú, một khi bay lên, rất dễ dàng bị người xem như bia ngắm, hắn coi như thực lực mạnh, cũng không nguyện ý ra cái này danh tiếng."),t("br"),t("br"),n._v("Rống!"),t("br"),t("br"),n._v("Đi tới không bao xa, một tiếng tức giận gào thét, vang lên, lập tức nhìn thấy một đầu to lớn Thánh vực cấp bậc hổ cốt thú lao đến."),t("br"),t("br"),n._v("Cái tên này cũng giống như mình, cũng là Nguyên Thần cảnh thực lực, hắn còn chưa tới đến trước mặt, liền cho người ta một loại trên linh hồn uy áp, dường như tuỳ tiện liền có thể đem tu luyện giả xé nát."),t("br"),t("br"),n._v("“Cái này đều là dược liệu...”"),t("br"),t("br"),n._v("Ánh mắt sáng lên."),t("br"),t("br"),n._v("Hắn không đi tìm đám thánh thú này phiền phức thì thôi, cái tên này lại dám xông lại, Trương Huyền bàn tay hướng phía dưới đè ép."),t("br"),t("br"),n._v("Đùng chít chít!"),t("br"),t("br"),n._v("Xông tới hổ cốt thú, lập tức thân thể cứng ngắc, tựa như bị giam cầm, động một cái cũng không thể động, con mắt thật to bên trong tràn đầy hoảng sợ."),t("br"),t("br"),n._v("“Trách ngươi số mệnh không tốt, gặp ta...”"),t("br"),t("br"),n._v("Ngón tay uốn lượn, đang nghĩ đem hắn đàn hồi chết, lột da đi xương, ngưng luyện ra một chút dược liệu, bầu trời đột nhiên chấn động, một thanh âm truyền tới."),t("br"),t("br"),n._v("“Trương Cửu Tiêu, ngọc phù vỡ vụn, cái thứ nhất đào thải, còn lại... Ba mươi hai người!”"),t("br"),t("br"),n._v("“Cái gì? Trương Cửu Tiêu... Bị đào thải?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, trên mặt tất cả đều là khó tin."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1279"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1281"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);