(window.webpackJsonp=window.webpackJsonp||[]).push([[329],{684:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1273-thien-đao-kiem-phap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1273-thien-đao-kiem-phap"}},[n._v("#")]),n._v(" Chương 1273: Thiên Đạo kiếm pháp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1273: Thiên Đạo kiếm pháp"),t("br"),t("br"),n._v("Kiếm cứ việc chỉ là một loại vũ khí, nhưng ngàn vạn năm đến, không biết bao nhiêu tu luyện giả chìm đắm trong đó, sớm đã diễn sinh ra vô số loại giống như, kiếm ý, phương thức tu luyện... Tất cả đều khác biệt."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, truyền thừa rất trọng yếu."),t("br"),t("br"),n._v("Nơi này kiếm ý, kiếm chiêu quá nhiều, tu luyện lý niệm bên trên xuất hiện bất đồng, ở lâu rồi, học tập càng nhiều, càng dễ dàng tẩu hỏa nhập ma, thậm chí bị người chém giết."),t("br"),t("br"),n._v("Đây cũng là Danh Sư đường, vì cái gì đem công pháp, đuổi tầng chia cắt nguyên nhân."),t("br"),t("br"),n._v("Mỗi một cái danh sư đều đối công pháp có đặc biệt nhận xét, một, hai cái còn tốt, số lượng nhiều, nhận xét nhiều, cũng sẽ xuất hiện vô số loại phương thức tu luyện, thậm chí không ít hoàn toàn trái lại, không phân tầng lời nói, lung tung tu luyện, đừng nói nhân loại tiến bộ, làm không cẩn thận, đâu đâu cũng có tẩu hỏa nhập ma."),t("br"),t("br"),n._v("Cho nên, danh sư đọc sách cũng có lựa chọn, không thể đi ngụy tích trữ thật, nhìn càng nhiều, càng cảm thấy hỗn loạn hoang mang, tâm cảnh liền loạn."),t("br"),t("br"),n._v("Nơi này nắm giữ nhiều loại kiếm ý cùng kiếm chiêu, đến đây người tu luyện, không phân biệt thật giả, đều muốn thử một lần, dần dà, tai hoạ ngầm sẽ càng ngày càng nặng, thực lực không những sẽ không tăng thêm, làm không cẩn thận, còn có thể bởi vì học tập quá nhiều, từ đó chết tại nơi này."),t("br"),t("br"),n._v("“Dẫn ta nhìn một chút!”"),t("br"),t("br"),n._v("Rõ ràng những này, Trương Huyền nói."),t("br"),t("br"),n._v("“Tốt!” Lắc lư hai lần, Mộ Vân kiếm phía trước dẫn đường."),t("br"),t("br"),n._v("Khoảng cách hơn 100 km, không tính là gì, thời gian không dài, liền đến đến trước mặt."),t("br"),t("br"),n._v("Là cái to lớn vách đá, phía trên điêu khắc một cái chữ lớn “Kiếm!”, rồng bay phượng múa, còn chưa tới đến trước mặt liền cho người ta một loại kiếm khí phá không, bất cứ lúc nào cũng sẽ đem hắn xé rách cảm giác."),t("br"),t("br"),n._v("“Thật mạnh kiếm ý!”"),t("br"),t("br"),n._v("Vẻ mặt nghiêm túc, Trương Huyền ngừng lại."),t("br"),t("br"),n._v("Cái chữ này, không biết là mấy cảnh thư pháp, nhưng từ xa nhìn lại, thật giống như có người cầm trường kiếm, lăng không đâm tới, vô luận như thế nào trốn tránh, đều thẳng đối mi tâm, tựa như linh hồn bị đối phương khóa lại, không cách nào chạy trốn."),t("br"),t("br"),n._v("“Đây là Kiếm lão nhân lưu lại?”"),t("br"),t("br"),n._v("Rung động trong lòng, nhịn không được hỏi."),t("br"),t("br"),n._v("“Vâng!” Mộ Vân kiếm trả lời."),t("br"),t("br"),n._v("“Thật là kiếm đạo đại tông sư!”"),t("br"),t("br"),n._v("Trương Huyền cảm khái."),t("br"),t("br"),n._v("Tu luyện tiếp cận một năm, hắn liên quan tới kiếm đạo thư tịch, cũng học qua không ít, càng là đạt đến cảnh giới cực cao."),t("br"),t("br"),n._v("Một mực đến nay, đều cảm thấy đối kiếm đạo lý giải, coi như không có đạt tới đỉnh phong, tuyệt đối cũng không yếu tại người, nhìn thấy cái chữ này, mới biết được, cùng vị này Kiếm lão nhân, còn kém rất lớn một đoạn."),t("br"),t("br"),n._v("Đối phương đã đem “Kiếm” hòa vào cốt tủy, thậm chí đem bản thân đều luyện hóa thành trường kiếm... Lúc này mới có thể tùy tiện viết một chữ, đều khí tức tung hoành, để cho người ta khó mà tránh né."),t("br"),t("br"),n._v("“Những cái kia đều là đến đây cảm ngộ tu luyện giả...”"),t("br"),t("br"),n._v("Mũi kiếm chỉ về phía trước, Mộ Vân kiếm nói."),t("br"),t("br"),n._v("Trương Huyền nhìn lại, lúc này mới phát hiện, bên dưới vách đá phương, ngồi ngay thẳng không biết bao nhiêu người, chừng mấy trăm, từng cái nhìn chằm chằm phía trên kiểu chữ, trong tay không ngừng ra dấu, như si như say, tựa như điên."),t("br"),t("br"),n._v("“Ngươi nói là sai, ta chiêu này mới là đúng, ngươi nhìn!”"),t("br"),t("br"),n._v("Thanh âm truyền đến, một người trung niên trường kiếm trong tay lắc một cái, vạch ra một đạo cầu vồng, hướng về phía trước đâm ra ngoài, lan tràn vài trăm mét, mới nhẹ nhàng tiêu tán, không khí đều bị xé nứt."),t("br"),t("br"),n._v("“Đây là Kiếm Tâm cảnh bên trong thượng kiếm tâm?”"),t("br"),t("br"),n._v("Trương Huyền vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Kiếm pháp, chia làm kiếm ý cùng kiếm tâm hai loại cảnh giới."),t("br"),t("br"),n._v("Bình thường Hóa Phàm cảnh cường giả, liền có thể lĩnh ngộ kiếm ý, thiên phú cao chút, có thể lĩnh ngộ kiếm tâm, từ đó “Vạn kiếm tề minh, long ngâm khiếu thiên!”"),t("br"),t("br"),n._v("Kiếm tâm, lấy tâm làm kiếm, lấy ý xu thế, chỗ đến, đều là kiếm chiêu, cũng chính là cái gọi là nhân kiếm hợp nhất... Đạt tới loại cảnh giới này, đại biểu kiếm có linh, kiếm có ý!"),t("br"),t("br"),n._v("Chia làm ba cái cảnh giới, theo thứ tự là hạ kiếm tâm, trung kiếm tâm cùng thượng kiếm tâm!"),t("br"),t("br"),n._v("Cường giả Thánh vực, đối kiếm lĩnh ngộ, có thể đạt tới trung kiếm tâm, chính là cao thủ bên trong cao thủ, trước mắt vị này, vừa ra tay thế mà dùng ra thượng kiếm tâm lĩnh ngộ... Đối kiếm lý giải, so với hắn, thế mà đều không hề yếu!"),t("br"),t("br"),n._v("Cho đến trước mắt, hắn cũng chỉ là thượng kiếm tâm cảnh giới."),t("br"),t("br"),n._v("“Không đúng, chiêu này dạ chiến bát phương, chú ý chính là kiếm khí tung hoành, khí tức bay thẳng bát phương, kiếm khí của ngươi tập trung, rõ ràng không đúng, xem ta...”"),t("br"),t("br"),n._v("Đang tại khiếp sợ, chỉ thấy vị này trung niên nhân bên cạnh một cái lão giả, lắc đầu, cũng lấy ra trường kiếm, run lên cái kiếm hoa."),t("br"),t("br"),n._v("Thời gian nháy mắt, đầy trời mưa kiếm, lạnh lùng khí tức, bao phủ khắp nơi, lan tràn khoảng cách mấy chục mét, dường như chỉ cần đi vào trong đó, người lợi hại hơn nữa, đều sẽ bị vây khốn, lại khó chạy trốn."),t("br"),t("br"),n._v("“Thế mà cũng là thượng kiếm tâm?”"),t("br"),t("br"),n._v("Trương Huyền lần nữa kinh ngạc."),t("br"),t("br"),n._v("Một cái lĩnh ngộ thượng kiếm tâm, liền rất khủng bố, không nghĩ tới vị này cũng đúng, thậm chí trên kiếm đạo lý giải, giống như thắng trước đó vị kia."),t("br"),t("br"),n._v("“Các ngươi hai cái đều không đúng, ta cảm thấy là như thế này...”"),t("br"),t("br"),n._v("Người thứ ba gia nhập, đồng dạng thi triển kiếm pháp, kiếm khí như sương, di tán bốn phía."),t("br"),t("br"),n._v("Đồng dạng đạt đến thượng kiếm tâm chi cảnh."),t("br"),t("br"),n._v("Trương Huyền trợn mắt hốc mồm, hướng những người khác nhìn lại, mỗi một cái đều kiếm khí mơ hồ, tựa như biến thành từng chuôi trường kiếm, thực lực kinh người."),t("br"),t("br"),n._v("“Cái này mấy trăm người... Đều đạt đến thượng kiếm tâm cảnh giới?”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Cùng nhau đi tới, còn không có gặp qua kiếm thuật vượt qua bản thân, một cái nháy mắt, liền thấy nhiều như vậy, không hổ là Kiếm Trì, thật là đáng sợ!"),t("br"),t("br"),n._v("“Bọn họ đều là các đại đế quốc, thậm chí liên minh, am hiểu nhất kiếm pháp danh sư, chiến sư, tán tu! Tại lúc này ở giữa dài nhất, đều vượt qua chín trăm năm... Hơn 900 năm tích lũy, mới có nhiều như vậy, không tính là quá nhiều...”"),t("br"),t("br"),n._v("Mộ Vân kiếm nói."),t("br"),t("br"),n._v("“Hơn 900 năm?”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Bản thân mặc dù thiên tài, thời gian tu luyện cũng không ngắn, đều nhanh một năm... Nhưng, chân chính dùng tại kiếm pháp bên trên thời gian, đoán chừng liền một ngày cũng chưa tới!"),t("br"),t("br"),n._v("Bát đại phong hào đế quốc phạm vi bên trong, vô số cao thủ, hơn 900 năm, xuất hiện nhiều như vậy cùng mình không kém bao nhiêu, cũng rất bình thường."),t("br"),t("br"),n._v("Dù sao, hắn cũng không tính được chân chính tuyệt đỉnh thiên tài, không nói cái khác, so với phân thân, liền kém xa."),t("br"),t("br"),n._v("“Những người này, một mực không đi?”"),t("br"),n._v("“Cái chữ này, đầy đủ bọn họ tu luyện một đời, những người này si mê trong đó, từ khi đi tới, liền không có rời đi...” Mộ Vân kiếm nói."),t("br"),t("br"),n._v("“Ai!”"),t("br"),t("br"),n._v("Trương Huyền than thở."),t("br"),t("br"),n._v("Hắn nhớ tới kiếp trước nhìn qua một quyển sách, trong đó một tòa ở trên đảo, có tuyệt thế bí tịch, hàng năm ngày mồng tám tháng chạp, đều sẽ có người đi tới, tất cả mọi người là một đi không trở lại... Cùng cảnh tượng trước mắt có chút tương tự."),t("br"),t("br"),n._v("Cứ việc không có người hạn chế, nhưng vì lĩnh ngộ cao hơn võ học, từng cái si mê trong đó, không biết cảnh xuân tươi đẹp đã qua, hồng nhan đã già."),t("br"),t("br"),n._v("“Ta cảm thấy của ta đúng!”"),t("br"),t("br"),n._v("“Ta mới là đúng...”"),t("br"),t("br"),n._v("“Các ngươi tất cả đều là sai... Không phục so sánh với một hồi!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Ba người cãi lộn nửa ngày, dù ai cũng không cách nào thuyết phục ai, cuối cùng rút kiếm đối mặt, đánh hừng hực khí thế."),t("br"),t("br"),n._v("“Thật ra thì... Bọn họ đã tẩu hỏa nhập ma!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Mặc kệ cái này “Kiếm” chữ như thế nào cao thâm, lưu lại kiểu chữ Kiếm lão nhân như thế nào cao minh, những người này, trầm mê trong đó không cách nào tự kềm chế, thậm chí ra tay đối mặt, đã mê loạn tâm trí, mất đi bản thân, xem như tẩu hỏa nhập ma."),t("br"),t("br"),n._v("Cảm khái âm thanh bên trong, ba đại cao thủ đã phân ra được thắng bại, hai vị bị trọng thương, duy nhất người thắng trận, ánh mắt ngược lại có chút mê man, cảm thấy mình có phải hay không lĩnh ngộ thác, nếu không, vì cái gì hai người khác uy lực cũng mạnh như vậy."),t("br"),t("br"),n._v("Trương Huyền biết, đối phương quanh năm đắm chìm trong kiếm pháp bên trong, đã mất đi sức phán đoán, loại tình huống này, khuyên can cũng vô dụng, không thèm để ý, ngẩng đầu nhìn về phía trên vách đá chữ lớn."),t("br"),t("br"),n._v("Cái chữ này, mặc dù là dùng kiếm khí điêu khắc mà thành, lại ẩn chứa điêu khắc người ý niệm, ngân câu tranh sắt, khí tức kinh người, mỗi một bút, đều giống như là một loại không cùng loại giống như kiếm pháp, nhiều loại dung hợp lại cùng nhau, đại khí bàng bạc, để cho người ta càng xem càng cảm thấy sâu không lường được."),t("br"),t("br"),n._v("“Khó trách những người này lưu luyến quên về...”"),t("br"),t("br"),n._v("Chỉ nhìn một hồi, Trương Huyền cũng cảm thấy tâm thần xao động, kìm lòng không được muốn tu luyện."),t("br"),t("br"),n._v("Chậm rãi nhắm mắt lại."),t("br"),t("br"),n._v("Không phải tâm hắn trí kiên định, có thể chống cự lại những người khác không thể chống đối mê hoặc, mà là... Từ nơi này trong chữ lĩnh ngộ kiếm pháp, thực sự sơ hở quá nhiều, thực sự học không đi vào."),t("br"),t("br"),n._v("“Những cái kia là cái gì?”"),t("br"),t("br"),n._v("Thở ra một hơi, đem tạp niệm trong lòng dứt bỏ, Trương Huyền mở mắt lần nữa, lập tức nhìn thấy vách đá bốn phía, cũng viết đầy chữ viết, lít nha lít nhít, không biết bao nhiêu."),t("br"),t("br"),n._v("“Những này là lịch đại tu luyện giả lĩnh ngộ kiếm ý, kiếm chiêu, nếu như đối trên vách đá ‘Kiếm’ chữ lý giải không sâu, những này có thể tham khảo!”"),t("br"),t("br"),n._v("Mộ Vân kiếm nói."),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Trương Huyền nhìn sang."),t("br"),t("br"),n._v("Quả nhiên phía trên viết đầy tu luyện giả mỗi người nhận xét, từng trang từng trang sách nối liền cùng một chỗ, số lượng vượt qua một ngàn."),t("br"),t("br"),n._v("“Cùng trước đây Phong Thánh đài có chút tương tự!”"),t("br"),t("br"),n._v("Âm thầm gật đầu."),t("br"),t("br"),n._v("Phong Thánh đài cũng nhìn thấy không hiếm thấy giải, mỗi một cái đều tự nhận là lĩnh ngộ ý tứ chân chính, là thiên chi kiêu tử, trên thực tế... Bọn họ lĩnh ngộ... Trăm ngàn chỗ hở, không biết chiếu một cái!"),t("br"),t("br"),n._v("Đoán chừng cái này cũng gần như."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Đảo qua vách đá, từng quyển từng quyển thư tịch xuất hiện tại trong óc."),t("br"),t("br"),n._v("Những này nhận xét, cũng là một loại bí tịch, con mắt đảo qua, liền có thể thu vào."),t("br"),t("br"),n._v("“Chính xác!”"),t("br"),t("br"),n._v("Thư tịch xuất hiện tại thức hải, lần nữa hô khẽ, hơn một ngàn vốn kiếm pháp bí tịch, lập tức ngưng tụ thành một bản, hiện lên ở trong óc."),t("br"),t("br"),n._v("Ngón tay nhẹ nhàng điểm một cái, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("“Lại là Thiên Đạo kiếm pháp!”"),t("br"),t("br"),n._v("Vốn chỉ là tùy tiện nhìn một chút, không có ý định học thứ gì, không nghĩ tới, đem những năm này đám người lĩnh ngộ, tập trung ở cùng một chỗ, thế mà tạo thành Thiên Đạo công pháp!"),t("br"),t("br"),n._v("Kiếm pháp trong đầu chảy xuôi, rất nhanh nghiên cứu một lần."),t("br"),t("br"),n._v("Không thể không nói, Kiếm lão nhân lưu lại kiếm đạo, quả thực tinh thâm, bản thân dựa theo tu luyện, đoán chừng đối kiếm thuật lý giải, có thể lần nữa tăng lên một đoạn dài."),t("br"),t("br"),n._v("Đang định nhắm mắt lại tu luyện một cái, để thực lực lần nữa tăng lên, đột nhiên cảm thấy linh khí bốn phía, bỗng nhiên hướng về phía trước hội tụ, tựa như gió lốc."),t("br"),t("br"),n._v("Vội vàng nhìn lại, chỉ thấy vách đá phía trước, đang chìm thấm tu luyện đám người, cũng tất cả đều đồng loạt ngẩng đầu lên."),t("br"),t("br"),n._v("“Chủ nhân...”"),t("br"),t("br"),n._v("Đang nghĩ nhìn một chút chuyện gì xảy ra, chỉ thấy trước mắt Mộ Vân kiếm không ngừng lay động, hình như đã không khống chế được thân thể của mình."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, Trương Huyền nghi ngờ."),t("br"),t("br"),n._v("“Chủ nhân, có người lĩnh ngộ kiếm đạo... Ta thân là kiếm linh, tự động thần phục!”"),t("br"),t("br"),n._v("“Lĩnh ngộ kiếm đạo?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, chỉ thấy trước mọi người phương, một thanh niên khoanh chân ngồi dưới đất, trên đầu kiếm khí tung hoành, tựa như một chuôi tuyệt thế lợi kiếm, rút ra vỏ kiếm, muốn đem thiên địa đều xé thành hai nửa."),t("br"),t("br"),n._v("“Là Mã Minh Hải?”"),t("br"),t("br"),n._v("Thấy rõ bóng người này bộ dáng, Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Không phải người khác, chính là trước đó tại Danh Sư đường, cùng hắn chiến đấu qua, Tiềm Xung đế quốc thiên tài, Mã Minh Hải!"),t("br"),t("br"),n._v("“Hắn tại sao lại ở chỗ này?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1281"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1283"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);