(window.webpackJsonp=window.webpackJsonp||[]).push([[33],{388:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1006-bach-đoc-su-toi-choi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1006-bach-đoc-su-toi-choi"}},[n._v("#")]),n._v(" Chương 1006: Bạch độc sư tới chơi")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1006: Bạch độc sư tới chơi"),t("br"),t("br"),n._v("Đi vào tàng thư điện, Trương Huyền bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Không phải hắn muốn đi giả mạo Độc điện khai phái tổ sư, mà là tình huống lúc đó, bây giờ không có những biện pháp khác."),t("br"),t("br"),n._v("Không nói như vậy, Hứa trưởng lão vô cùng có khả năng tự sát thân vong, cùng Độc điện cũng không đội trời chung, không thể quay lại chỗ trống, như vậy mặc dù có chút kéo cờ lớn, dù sao có thể đem gặp phải vấn đề toàn bộ giải quyết, còn không chậm trễ đọc sách, một lần tốt hai."),t("br"),t("br"),n._v("Đương nhiên, có thể ngụy trang thành công, vẫn là muốn may mắn mà có Ngoan Nhân."),t("br"),t("br"),n._v("Mượn Tử Diệp vương ngụy trang cúc áo, đem trong cơ thể sát lục khí tức che lấp, lúc này mới thành công tản mát ra nhân loại cường giả khí tức, để Nhược Thanh Viễn đám người không dám hoài nghi."),t("br"),t("br"),n._v("Nếu không Ngoan Nhân khí tức một khi phóng thích, ngu nữa đều biết là Dị Linh tộc nhân, đừng nói chấn nhiếp, khả năng lời nói đều nói không đi xuống, liền bị vạch trần."),t("br"),t("br"),n._v("Bất quá, ngụy trang cúc áo hiệu quả vẫn là kém xa Khổng sư lưu lại sách, giấu diếm độc sư còn có thể, danh sư lời nói, liền không dễ dàng như vậy."),t("br"),t("br"),n._v("“Không biết có thể giấu diếm bao lâu, vẫn là trước đọc sách đi!”"),t("br"),t("br"),n._v("Mặc dù trong thời gian ngắn hù dọa đối phương, nhưng mà rất nhiều chuyện là khó mà cân nhắc được, kế trước mắt, là mau chóng đọc sách, chỉ cần có thể tìm tới giải quyết Ngụy Như Yên phương pháp, liền có thể trực tiếp rời khỏi, không cần thiết cùng hắn quần nhau."),t("br"),t("br"),n._v("Nghĩ đến cái này, không có quá nhiều chần chừ, bước nhanh đi lên tầng thứ tư."),t("br"),t("br"),n._v("Tầng này thư tịch, rõ ràng so ba tầng trước muốn ít đi rất nhiều, chẳng qua nhưng càng cao thâm hơn, lưu lại những sách này sách, cơ bản đều đạt đến tứ tinh đỉnh phong, ngũ tinh, thậm chí cao hơn."),t("br"),t("br"),n._v("Dọc theo từng dãy giá sách quét tới, đem thư tịch khắc sâu vào trong óc, biến thành thuộc về mình tri thức, nương theo nhìn càng ngày càng nhiều, đối độc sư nghề nghiệp lý giải cũng càng ngày càng khắc sâu."),t("br"),t("br"),n._v("Chưa tới một canh giờ, liền đem tầng này toàn bộ nhìn xong, tiếp tục hướng tầng thứ năm đi tới."),t("br"),t("br"),n._v("Nơi này thư tịch chỉ nhằm vào ngũ tinh độc sư mở ra, số lượng tinh giản không ít, hao tốn không đến nửa canh giờ, liền thu sạch ghi chép sạch sẽ."),t("br"),t("br"),n._v("“Vẫn là không có?”"),t("br"),t("br"),n._v("Năm tầng thư tịch toàn bộ nhìn xong, vẫn không có liên quan tới Tiên Thiên độc thể quá nhiều tin tức, Trương Huyền nhịn không được có chút lo lắng."),t("br"),t("br"),n._v("Cái này tàng thư điện, chỉ có bảy tầng, nội dung đã vượt qua hơn phân nửa, nhưng không có chút nào manh mối, chẳng lẽ Tiên Thiên độc thể phương pháp giải quyết, liền nơi này đều không có?"),t("br"),t("br"),n._v("Thật muốn như vậy, liền nguy rồi!"),t("br"),t("br"),n._v("Ngụy Như Yên tình huống, mặc dù còn không có hoàn toàn chuyển biến xấu, nhưng cũng không có khả năng kiên trì tìm kiếm cấp bậc cao hơn Độc điện."),t("br"),t("br"),n._v("“Nhìn xong cuối cùng hai tầng, mới quyết định đi...”"),t("br"),t("br"),n._v("Biết lo lắng cũng không dùng, xoa xoa mi tâm, Trương Huyền tiếp tục hướng bên trên đi đến."),t("br"),t("br"),n._v("Tầng này thư tịch, chỉ có không đến một vạn bản, dọc theo giá sách chậm rãi tiến lên, mới vừa cái không sai biệt lắm, muốn đi sau cùng một tầng quan sát, liền nghe đến cửa phòng “Kẹt kẹt!” Mở ra, một bóng người theo đi đến."),t("br"),t("br"),n._v("Là cái 60 tới tuổi lão giả, trắng như tuyết sợi râu, ánh mắt sáng ngời có thần, quần áo rất là bình thường, cũng không đeo độc sư huy chương, nhìn không ra cụ thể đẳng cấp."),t("br"),t("br"),n._v("Lắc đầu, cũng không thèm để ý, đang định đi lên, trong óc truyền đến Ngoan Nhân thanh âm."),t("br"),t("br"),n._v("“Chủ nhân, người này thực lực so sánh với thanh xa còn cao hơn, chỉ sợ đã đạt đến Thánh vực tứ trọng...”"),t("br"),t("br"),n._v("“Thánh vực tứ trọng, thất tinh độc sư?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Hồng Viễn Độc điện thực lực mạnh nhất không phải Nhược Thanh Viễn điện chủ ư? Cũng chỉ có tam trọng đỉnh phong mà thôi, làm sao đột nhiên toát ra một vị tứ trọng?"),t("br"),t("br"),n._v("Loại thực lực này, coi như so với Tử Diệp vương, đều không kém chút nào."),t("br"),t("br"),n._v("“Vâng, hắn mặc dù nấp rất kỹ, nhưng mà không thể gạt được ta!” Ngoan Nhân lặng lẽ truyền âm."),t("br"),t("br"),n._v("Thôn phệ Thủy Diệp vương nguyên thần, thực lực của hắn lần nữa khôi phục một chút, lúc này quan sát, coi như cách đó không xa lão giả này cũng không phát hiện."),t("br"),t("br"),n._v("“Thánh vực tứ trọng cường giả lặng yên không một tiếng động tới, muốn làm gì?”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Trương Huyền cảnh giác chi ý tăng nhiều."),t("br"),t("br"),n._v("Đang muốn đem Tử Diệp vương cùng rất nhiều khôi lỗi ném ra, tiên hạ thủ vi cường, đem hắn phóng giương lại nói, nghĩ đến cái gì, ngừng lại."),t("br"),t("br"),n._v("Cao như vậy cấp bậc cường giả, xuất hiện ở đây, khẳng định không phải là vì đọc sách, càng không khả năng đi dạo, khẳng định là tìm đến mình."),t("br"),t("br"),n._v("Không có trực tiếp ra tay, mà là đẩy cửa đi vào, đoán chừng không phải đến tìm phiền phức, mà là sinh ra hoài nghi, muốn thăm dò!"),t("br"),t("br"),n._v("Chỉ thăm dò không động thủ lời nói, cũng không cần phải để Tử Diệp vương đi ra, nếu không, động tĩnh làm lớn chuyện, trước đó ngụy trang không có chút tác dụng chỗ không nói, làm hỏng tầng thứ bảy thư tịch, chậm trễ sự tình sẽ chỉ càng nhiều."),t("br"),t("br"),n._v("“Thánh vực tứ trọng cường giả, nắm giữ nguyên thần, không có trước đó dễ gạt như vậy...”"),t("br"),t("br"),n._v("Có thể không động thủ tốt nhất, nhưng muốn lừa gạt loại này cường giả, khẳng định so trước đó muốn khó."),t("br"),t("br"),n._v("Nói mình là Tiên Thiên độc hồn thể, chỉ cần độc không chết, Nhược Thanh Viễn đám người cũng không dám hoài nghi, nhưng đối với Nguyên Thần cường giả tới nói, chiêu này tác dụng liền không lớn."),t("br"),t("br"),n._v("Nắm giữ nguyên thần, hoàn toàn có thể đo lường hồn phách... Hắn là cùng Ngụy Như Yên linh hồn giao hòa mới có loại năng lực này, cũng không phải là chân chính độc hồn thể, kiểm tra, rất dễ dàng bị phát hiện sơ hở."),t("br"),t("br"),n._v("Trừ phi... Làm cho đối phương không cần nguyên thần dò xét!"),t("br"),t("br"),n._v("Nên làm cái gì?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hắn bên này suy tư, sau lưng tới lão giả cũng ánh mắt tập trung quan sát trong phòng vị này."),t("br"),t("br"),n._v("Tên trước mắt, thực lực chân thật quả thực chỉ là Tòng Thánh cấp bậc, nhìn không ra đặc thù, bộ dáng cũng có chút bình thường, thậm chí còn có chút bỉ ổi."),t("br"),t("br"),n._v("Thật không biết Nhược Thanh Viễn đám người vì cái gì dễ dàng như vậy mắc lừa, nhận định trong cơ thể hắn nắm giữ khai phái tổ sư hồn phách."),t("br"),t("br"),n._v("Đang muốn nói chuyện, hỏi thăm một chút, chỉ thấy cách đó không xa gia hỏa, bỗng nhiên vọt lên phía trước ra ngoài, một đầu hướng một loạt giá sách đụng tới."),t("br"),n._v(" “Ừm!”"),t("br"),t("br"),n._v("Nhướng mày."),t("br"),t("br"),n._v("Chẳng lẽ nhìn ra hắn đặc thù, sợ tới mức muốn chạy trốn?"),t("br"),t("br"),n._v("Là như vậy lời nói, liền nguy rồi!"),t("br"),t("br"),n._v("Hơn nữa, nhìn tốc độ, thật muốn đụng vào, toàn bộ giá sách tàng thư, cũng có thể bị hủy bởi tay hắn!"),t("br"),t("br"),n._v("Đây chính là Độc điện vô số độc sư tâm huyết lấy được thư tịch, làm sao có thể tại trước mắt mình, bị người hủy đi!"),t("br"),t("br"),n._v("Sầm mặt lại, chân khí trong cơ thể vận chuyển, bàn tay đưa tay về phía trước, liền muốn đem cái tên này bắt lấy, lại thấy hắn đột nhiên ngừng lại, khoanh chân ngồi dưới đất, dường như đi vào trạng thái tu luyện."),t("br"),t("br"),n._v("Tu luyện..."),t("br"),t("br"),n._v("Lão giả không hiểu ra sao."),t("br"),t("br"),n._v("Mới vừa rồi còn phát lực lao nhanh, tính toán đụng vào trên giá sách, sau một khắc an vị xuống tu luyện... Cái này cũng chênh lệch quá lớn, quá không đáng tin cậy đi!"),t("br"),t("br"),n._v("Vị lão giả này chính là mới từ tổng bộ tới Bạch độc sư Bạch Đình, biết cái này giả thần giả quỷ gia hỏa tại Tàng Thư các về sau, để Nhược Thanh Viễn đám người canh giữ ở bên ngoài, tự mình đi đến."),t("br"),t("br"),n._v("Đang suy nghĩ như thế nào mở miệng, cùng đối phương nói chuyện, liền nghe đến ngồi tại cách đó không xa gia hỏa, thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Ngươi thấy được cái gì?”"),t("br"),t("br"),n._v("Nhìn quanh hai bên một tuần, phát hiện không có những người khác, Bạch độc sư đầu óc mơ hồ chỉ mình: “Ta?”"),t("br"),t("br"),n._v("Phía trước gia hỏa, không hề trả lời, mà là nhẹ gật đầu."),t("br"),t("br"),n._v("“Loại trừ sách, hình như cũng không có gì đi!” Không biết đối phương ý tứ, Bạch độc sư chần chờ một chút, nói."),t("br"),t("br"),n._v("Đây là tàng thư điện, đập vào mắt chỗ đều là giá sách cùng sách, loại trừ hai thứ này, còn có thể có cái gì?"),t("br"),t("br"),n._v("“Ai!”"),t("br"),t("br"),n._v("Trước mặt gia hỏa, than thở một tiếng, trong giọng nói mang theo tiêu điều, dường như tràn đầy thất vọng: “Vốn cho rằng thất tinh đỉnh phong độc sư, Thánh vực tứ trọng cường giả, có thể có khác biệt nhận xét, hiện tại xem ra... Cũng giống như những người khác.”"),t("br"),t("br"),n._v("“Ngươi, ngươi... Biết thực lực của ta?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Bạch độc sư giật nảy mình."),t("br"),t("br"),n._v("Hắn che giấu thực lực, lại không kéo đẳng cấp huy chương, ở bề ngoài nhìn, cùng bình thường lục tinh độc sư không có gì khác nhau, đối phương đưa lưng về phía bản thân, một cái nói ra... Để hắn có chút không dám tin tưởng."),t("br"),t("br"),n._v("“Thánh vực tứ trọng mà thôi, có cái gì không nhìn ra?”"),t("br"),t("br"),n._v("Hừ một tiếng, phía trước trung niên nhân, hai mắt nhắm chặt, “Nhìn” hướng cách đó không xa giá sách: “Đây không phải sách, mà là tri thức, chỉ tiếc... Hậu nhân nghe nhầm đồn bậy, đem truyền thừa của ta, đều làm cho giống thật mà là giả!”"),t("br"),t("br"),n._v("“Truyền thừa của ngươi?”"),t("br"),t("br"),n._v("Bạch độc sư cau mày."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, trung niên nhân lần nữa than thở: “Ngươi có biết, vì cái gì tu luyện đến thất tinh đỉnh phong, từ đầu đến cuối vô pháp đột phá? Tằm Tinh công, mặc dù là không kém pháp môn, nhưng mà đáng tiếc, tới phối hợp thuốc xảy ra vấn đề, nguyên thần huyệt phong bế như sắt, từ đầu đến cuối gõ đánh không ra, thực sự đáng tiếc!”"),t("br"),t("br"),n._v("“Ngươi, ngươi... Biết ta tu luyện chính là Tằm Tinh công?”"),t("br"),t("br"),n._v("Thân thể cứng đờ, Bạch độc sư khó tin."),t("br"),t("br"),n._v("Vốn muốn thăm dò đối phương, kết quả, còn chưa lên tiếng, liền nghe đến câu này, sợ tới mức con mắt trợn tròn."),t("br"),t("br"),n._v("Hắn tu luyện Tằm Tinh công, là một vị bát tinh độc sư sáng tạo, tuổi trẻ thí luyện lúc, trong lúc vô tình nhận được, vốn cho rằng có này lợi hại pháp quyết, xung kích bát tinh độc sư, ở trong tầm tay, nằm mơ đều không có nghĩ đến, vây ở thất tinh đỉnh phong, đã mấy trăm năm nhiều."),t("br"),t("br"),n._v("Còn bị nhốt lý do, cùng đối phương nói giống nhau như đúc, lúc trước lúc tu luyện, tới phối hợp độc dược xuất hiện sai lầm, tổn thương cơ sở, nguyên thần huyệt đóng chặt, đã dùng hết toàn lực đều không thể gõ mở."),t("br"),t("br"),n._v("Lúc tuổi còn trẻ đều làm không được, lúc này khoảng cách tuổi thọ đại nạn cũng chỉ hơn trăm năm, kiếp này chỉ sợ lại không đột phá hi vọng!"),t("br"),t("br"),n._v("Đây là hắn chỗ sâu nhất việc riêng tư, ngay cả cùng đi Hải độc sư đều không rõ ràng, người trước mắt này, nhìn cũng chưa từng nhìn liền một câu nói toạc ra... Chỉ cảm thấy lạnh cả người, sợ tới mức trái tim đều nhanh theo cổ họng nhảy ra."),t("br"),t("br"),n._v("Một cái nhìn ra những này, chẳng lẽ... Thật sự là khai phái tổ sư?"),t("br"),t("br"),n._v("Nếu không, liền xem như bát tinh độc sư, cũng không có loại này nhãn lực đi!"),t("br"),t("br"),n._v("“Tằm Tinh công, phun ra nuốt vào như tằm, chiếu rọi tinh không! Lấy độc tẩm bổ, lấy sao trời làm môi giới, chủ ý là thổ khí thành tơ, chạy toàn thân, mà ngươi nhưng đem tơ tằm trải rộng toàn thân, hoàn toàn phong bế nguyên thần huyệt... Có thể giải khai mới là lạ!”"),t("br"),t("br"),n._v("Lắc đầu, trung niên nhân tràn đầy thất vọng, tựa hồ tại là đi nhầm vào lạc lối vãn bối, cảm thấy tiếc hận."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Trên đầu mồ hôi lạnh ứa ra, Bạch độc sư nói không ra lời."),t("br"),t("br"),n._v("Liếc mắt nhìn, không chỉ đem tu vi nói ra, liền trong vấn đề tu luyện mấu chốt, toàn bộ một câu nói toạc ra, càng nói ra Tằm Tinh công thiếu hụt, cùng hắn trên việc tu luyện vấn đề xuất hiện..."),t("br"),t("br"),n._v("Đây cũng không phải là nhãn lực liền có thể giải thích... Còn nhất định phải đối độc sư chức nghiệp, hiểu rõ cực sâu, nghiên cứu cực sâu mới có thể làm đến!"),t("br"),t("br"),n._v("Coi như hắn vị trí phân bộ điện chủ... Muốn đạt tới điểm ấy, đều khó có khả năng!"),t("br"),t("br"),n._v("“Không cần khẩn trương, dùng độc sai lầm, thật ra thì... Cũng không thể thương tới căn bản, để ngươi xuất hiện cục diện bây giờ, tu luyện ra hiện như thế lớn chỗ nhầm lẫn, hẳn là có người phản bội đi!”"),t("br"),t("br"),n._v("Không để ý tới hắn khẩn trương, trung niên nhân nói tiếp."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Da đầu một chút nổ tung, Bạch độc sư con mắt trừng đến sắp rơi trên mặt đất, không còn có trước đó bình tĩnh cùng thất tinh độc sư kềm chế, nhịn không được mở miệng hỏi đi ra: “Ngươi, làm sao ngươi biết?”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1014"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1016"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);