(window.webpackJsonp=window.webpackJsonp||[]).push([[330],{685:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1274-thuc-luc-manh-lien-co-the-muon-lam-gi-thi-lam"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1274-thuc-luc-manh-lien-co-the-muon-lam-gi-thi-lam"}},[n._v("#")]),n._v(" Chương 1274: Thực lực mạnh liền có thể muốn làm gì thì làm!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1274: Thực lực mạnh liền có thể muốn làm gì thì làm!"),t("br"),t("br"),n._v("Mọi người đi vào nơi này, đều lo lắng bị đào thải, hoặc là đi đào thải người khác, cái tên này, lại trực tiếp ngồi ở chỗ này tu luyện, hơn nữa nhìn bộ dáng, đã tới thời gian rất lâu, khó trách để hắn nghi ngờ."),t("br"),t("br"),n._v("“Hắn là Tiềm Xung đế quốc thiên tài, cũng am hiểu kiếm thuật... Ta bắt lấy Mộ Vân kiếm mới biết được đây là địa phương nào, hắn khả năng vừa tiến vào, liền hiểu!”"),t("br"),t("br"),n._v("Rất nhanh, kịp phản ứng."),t("br"),t("br"),n._v("Đối phương là Tiềm Xung đế quốc người, cái này Kiếm Trì, khẳng định đã sớm tới qua, tìm tới nơi này cũng không khó."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Cảm khái âm thanh bên trong, chỉ thấy bốn phía kiếm ý, kiếm khí không ngừng hướng trên người đối phương hội tụ mà đi, thời gian nháy mắt, khí tức cả người càng ngày càng mạnh."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Bên người Mộ Vân kiếm không thể kiên trì được nữa, nằm sấp trên mặt đất không ngừng run rẩy, dường như bị trong cơ thể hắn kiếm khí áp chế, không dám phản kháng."),t("br"),t("br"),n._v("“Là... Kiếm đạo chân giải! Bảy trăm năm, không nghĩ tới lần nữa thấy được một vị!”"),t("br"),t("br"),n._v("Nhìn xem đầy trời kiếm khí, cùng trường kiếm trong tay thần phục, một cái lão giả tràn đầy kích động."),t("br"),t("br"),n._v("“Không phải kiếm đạo chân giải, chân chính kiếm đạo chân giải, là Vạn Kiếm Quy Tông, hiện tại chỉ là thần phục, hẳn là... Nửa bước chân giải!”"),t("br"),t("br"),n._v("Bên cạnh hắn một vị lão giả nói."),t("br"),t("br"),n._v("“Không sai, là nửa bước chân giải! Thế nhân chỉ biết là kiếm pháp chia làm kiếm ý, kiếm tâm hai cái cấp độ, lại không biết mặt trên còn có một cái kiếm đạo chân giải! Lĩnh ngộ cảnh giới này, Vạn Kiếm Quy Tông, chỉ cần kiếm thực lực, không cao hơn bản thân, liền vô điều kiện nghe lời...”"),t("br"),t("br"),n._v("Lại một vị lão giả cảm khái: “Trước mắt cái này, chỉ là để tu vi thấp hơn hắn thần phục, còn không có đạt tới vô điều kiện nghe theo mệnh lệnh... Hẳn là chỉ là nửa bước!”"),t("br"),t("br"),n._v("“Coi như nửa bước, cũng rất lợi hại, chúng ta nghiên cứu lâu như vậy, không phải là vì cái này ư?”"),t("br"),t("br"),n._v("“Đúng vậy a!”"),t("br"),t("br"),n._v("“Bảy trăm năm trước, Khinh Minh kiếm Kỷ Linh Chân, đột phá cảnh giới này thời điểm, ta vừa vặn ở bên cạnh, chính mắt thấy kỳ tích này phát sinh... Không nghĩ tới, thọ nguyên gần tới, lại thấy được vị thứ hai!”"),t("br"),t("br"),n._v("“Kỷ Linh Chân, sau khi đột phá, dường như không có rời khỏi Kiếm Trì a?”"),t("br"),t("br"),n._v("“Không có, đi Kiếm Trì trung tâm, muốn có được Kiếm lão nhân truyền thừa... Chỉ tiếc, một mực không thành công thôi, nhiều năm như vậy không thấy, cũng không biết còn sống không có!”"),t("br"),t("br"),n._v("Đám người nghị luận ầm ĩ."),t("br"),t("br"),n._v("“Nửa bước chân giải?”"),t("br"),t("br"),n._v("Đem mọi người đối thoại nghe vào trong tai, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Bàn tay khẽ vỗ, nằm sấp trên mặt đất Mộ Vân kiếm lập tức cảm thấy áp lực biến mất, giãy dụa lấy lần nữa bay lên, dường như tràn đầy hoảng sợ cùng sợ hãi."),t("br"),t("br"),n._v("Kiếm đạo chân giải, đối kiếm lĩnh ngộ, vượt qua Kiếm Tâm cảnh, đối kiếm linh, mang theo trời sinh áp bức, vị này Mã Minh Hải, trẻ tuổi như vậy liền có thể lĩnh ngộ, không hổ là có cơ hội đi vào Thánh tử điện người, thiên tư vô song!"),t("br"),t("br"),n._v("Trương Cửu Tiêu tới so sánh, đều kém rất lớn một đoạn."),t("br"),t("br"),n._v("Hô hô!"),t("br"),t("br"),n._v("Đầy trời kiếm khí bị hấp thu trong cơ thể, Mã Minh Hải chậm rãi mở mắt."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Trong mắt dường như có kiếm khí tung hoành, bất cứ lúc nào cũng sẽ bắn ra."),t("br"),t("br"),n._v("Tu vi của hắn, cứ việc vẫn như cũ là ngụy Xuất Khiếu, không có trải qua lôi kiếp, nhưng so trước đó hùng hồn không biết bao nhiêu, dường như coi như thật lôi kiếp tới, cũng có thể một kiếm đâm thủng."),t("br"),t("br"),n._v("“Trương Huyền?”"),t("br"),t("br"),n._v("Đứng dậy, hoạt động một chút, Mã Minh Hải đang cảm thấy Thần thoả mãn đủ, lập tức thấy được cách đó không xa Trương Huyền, con mắt lập tức híp lại."),t("br"),t("br"),n._v("Cái tên này, cùng hắn giao thủ qua, lúc ấy thi triển ra Thiên Phong Kiếm Hải, đều bị một cái nuốt vào, thực lực cực mạnh."),t("br"),t("br"),n._v("Vốn định thi triển ra tuyệt chiêu, đem hắn đánh bại, kết quả bị Nhạc đường chủ ngăn cản, chỉ có thể không giải quyết được gì."),t("br"),t("br"),n._v("Về sau, đối phương khiêu chiến Nhạc đường chủ, sử dụng khôi lỗi... Cứ việc đại hoạch toàn thắng, lại không có nghĩa là thực lực."),t("br"),t("br"),n._v("Nếu như mình không áp chế tu vi, lại thêm vừa mới đột phá kiếm đạo... Đừng nói đánh bại đối phương, đem hắn chém giết, hẳn là cũng sẽ không quá khó!"),t("br"),t("br"),n._v("Mắt sáng lên, lộ ra cười lạnh: “Coi như ngươi xui xẻo, ta mới vừa đột phá, liền gặp gỡ, nhìn tới, chỉ có thể trước đem ngươi đào thải...”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Nhẹ nhàng nhoáng một cái, đi tới Trương Huyền trước mặt, nhìn lại: “Trương sư ngươi tốt, chúng ta lại gặp mặt!”"),t("br"),t("br"),n._v("“Đúng, lại gặp mặt!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Mặc dù ta sợ hãi thán phục thực lực của ngươi, nhưng thực sự thật không tiện, chúng ta đang tiến hành thi vòng hai, dính dáng đi vào Thánh tử điện danh ngạch, cho nên... Là chính ngươi đem ngọc phù lấy ra, vẫn là ta tự mình ra tay?”"),t("br"),t("br"),n._v("Mí mắt vừa nhấc, Mã Minh Hải nhàn nhạt nhìn qua, mang theo làm người ta không cho cự tuyệt giọng nói: “Bản thân lấy ra, ta có thể không ra tay, để ngươi ít chịu thống khổ, xuất thủ... Coi như quy tắc không thể giết người, chịu chút da ngoại thương, thời gian ngắn không thể động đậy, vẫn là có thể làm được! Ở đây không cách nào động đậy... Có thể hay không bị Thánh thú giết chết, ta liền không cách nào bảo đảm!”"),t("br"),t("br"),n._v("“Ngươi uy hiếp ta?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Ngươi có thể làm thành uy hiếp, cũng có thể làm thành... Thực lực so với ngươi còn mạnh hơn, đối ngươi áp bức!”"),t("br"),t("br"),n._v("Mã Minh Hải cười lạnh."),t("br"),t("br"),n._v("Trương Huyền cau mày: “Chẳng lẽ... Thực lực mạnh liền có thể muốn làm gì thì làm?”"),t("br"),t("br"),n._v("“Không sai, thực lực mạnh, chính là có thể muốn làm gì thì làm!”"),t("br"),t("br"),n._v("Mã Minh Hải nhếch miệng lên đường cong."),t("br"),t("br"),n._v("Trước đó bị đối phương đánh bại, đã sớm ngực nhẫn nhịn khẩu khí, bây giờ tại nơi này, không có những người khác nhìn thấy, không cần chú ý danh sư quy tắc, hoàn toàn có thể nhẹ nhõm nghiền ép, báo thù rửa hận."),t("br"),t("br"),n._v("Thuận tiện giúp Nhạc đường chủ hả giận!"),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Thấy đối phương nói trực tiếp như vậy, Trương Huyền than thở, lật bàn tay một cái, chân khí chảy xuôi, đẩy về phía trước tới: “Đã ngươi quyết định như vậy, ta liền thật không tiện!”"),t("br"),t("br"),n._v("“Làm sao? Còn muốn động thủ với ta?”"),t("br"),t("br"),n._v("Gặp cái tên này, không lấy ra ngọc phù, còn làm vùng vẫy giãy chết, một tiếng cười nhạo, Mã Minh Hải ngón tay một điểm, một đạo kiếm khí tiến lên đón."),t("br"),t("br"),n._v("Đối kiếm lĩnh ngộ đạt đến nửa bước chân giải, coi như không cần trường kiếm, trong cơ thể kiếm khí, cũng có thể đánh bại dễ dàng cùng cấp bậc cường giả!"),t("br"),t("br"),n._v("Huống chi một cái... Nguyên Thần cảnh trung kỳ tiểu nhân vật..."),t("br"),t("br"),n._v("Hả?"),t("br"),n._v(" Trong lòng tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Hôm qua cùng đối phương thời điểm chiến đấu, vẫn chỉ là Nguyên Thần cảnh trung kỳ, làm sao bây giờ nhìn lại, giống như là đỉnh phong?"),t("br"),t("br"),n._v("Trong vòng một đêm, tiến bộ hai cái cấp độ?"),t("br"),t("br"),n._v("“Mặc kệ là trung kỳ vẫn là đỉnh phong, đều không trọng yếu! Gặp được hiện tại ta... Chỉ có một con đường chết!”"),t("br"),t("br"),n._v("Lông mày giương lên, đầu ngón tay huy hoàng kiếm khí, phá không mà tới, ngay tại hắn cho rằng, tuỳ tiện đâm thủng đối phương bàn tay thời điểm, kiếm khí cùng chưởng lực đụng nhau, tựa như là đậu phụ đụng phải sắt thép, lập tức tiêu tán."),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Lĩnh ngộ kiếm đạo nửa bước chân giải, biết một chỉ này uy lực, liền xem như trước đó vị kia “Tôn huynh” cũng không dám tuỳ tiện đối đầu, cái tên này, tùy tiện một chưởng liền đem lực lượng đánh tan... Làm sao làm được?"),t("br"),t("br"),n._v("Bất quá, nghi ngờ còn không có kết thúc, chỉ thấy đánh tan kiếm khí chưởng lực, tiếp tục hướng phía trước, thời gian nháy mắt đi tới trước mắt."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Áp lực cực lớn đánh tới, hơi đỏ mặt, Mã Minh Hải muốn ngăn cản, không chút nào làm không được, trực tiếp đâm vào một cái nham thạch to lớn bên trên, ngực khó chịu, một ngụm máu tươi phun tới."),t("br"),t("br"),n._v("“Tại sao có thể như vậy... Thực lực của ngươi, làm sao lại mạnh như vậy?”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, tràn đầy khó tin."),t("br"),t("br"),n._v("Hôm qua bại bởi đối phương, là bởi vì giảm thấp xuống tu vi, hiện tại tu vi không có áp chế, càng là lĩnh ngộ nửa bước kiếm đạo chân giải... Vẫn như cũ bị đánh bại, thế giới quan trực tiếp lật đổ."),t("br"),t("br"),n._v("“Nhất định là trùng hợp, ta không tin...”"),t("br"),t("br"),n._v("Bỗng nhiên đứng lên, chập ngón tay làm kiếm, ở trước ngực quẹt nửa tròn, chân khí bắn, hình thành một vòng tròn."),t("br"),t("br"),n._v("“Ngàn vạn kiếm khí, nghe ta hiệu lệnh!”"),t("br"),t("br"),n._v("Tiếng gầm gừ bên trong, chung quanh dạo chơi kiếm khí, tụ lại, ở trước ngực tạo thành một cái to lớn chân khí trường kiếm, chừng mười mét."),t("br"),t("br"),n._v("Lĩnh ngộ kiếm đạo nửa bước chân giải, cứ việc còn làm không được trường kiếm nghe theo hiệu lệnh, khống chế trên không kiếm khí, còn có thể làm được."),t("br"),t("br"),n._v("Kiếm Trì, đâu đâu cũng có kiếm tu, kiếm khí ngưng tụ không tan, có thể điều động kiếm khí, chẳng khác nào, có nhiều như vậy cao thủ trợ trận, sức chiến đấu chợt tăng không chỉ một lần!"),t("br"),t("br"),n._v("Hắn giờ phút này, liền xem như chân chính Xuất Khiếu cảnh cường giả gặp gỡ, cũng có thể nhẹ nhõm chém giết!"),t("br"),t("br"),n._v("“Đây là nửa bước kiếm đạo chân giải thực lực?”"),t("br"),t("br"),n._v("“Thật là khủng khiếp...”"),t("br"),t("br"),n._v("“Khống chế kiếm khí, tại Kiếm Trì phía trong, có thể nói là vô địch...”"),t("br"),t("br"),n._v("“Đúng vậy a, đối diện tên kia, đoán chừng sắp thua...”"),t("br"),t("br"),n._v("Mọi người chung quanh, tất cả đều sắc mặt nghiêm túc."),t("br"),t("br"),n._v("Bọn họ đều là kiếm tu, biết nửa bước chân giải đáng sợ, kiếm khí tung hoành, muôn hình vạn trạng, cùng cấp bậc tuyệt đối vô địch tồn tại."),t("br"),t("br"),n._v("Đối diện thanh niên, chẳng qua Nguyên Thần cảnh đỉnh phong, như thế nào ngăn cản được."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Ánh mắt kinh ngạc bên trong, toàn bộ do kiếm khí hội tụ mà thành trường kiếm, hướng Trương Huyền đâm tới."),t("br"),t("br"),n._v("Tựa như đột phá vận tốc âm thanh, thời gian nháy mắt xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Nhìn uy thế, một khi bị đâm trúng, lại cứng rắn thân thể đoán chừng cũng sẽ phút chốc bị đánh thành hai nửa, tại chỗ bỏ mình."),t("br"),t("br"),n._v("“Hội tụ kiếm khí, uy lực cứ việc rất mạnh, nhưng... Dù sao không phải là của mình!”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền không khẩn trương chút nào, ngược lại lộ ra vẻ thất vọng, đồng dạng ngón tay một điểm."),t("br"),t("br"),n._v("Đầu ngón tay một đạo đặc thù ánh sáng lấp lánh, đâm tới trường kiếm tới đụng một cái, lập tức tán loạn."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Nhẹ nhàng bắn ra."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Mã Minh Hải lần nữa bay ngược mà ra, sống lưng kề vách đá, đập ra một cái hố to, giống như là bị người đóng ở phía trên, vô luận như thế nào giãy dụa, đều không thể động đậy."),t("br"),t("br"),n._v("“Ngươi, ngươi...”"),t("br"),t("br"),n._v("Vẻ mặt trở nên trắng bệch, nhìn về phía cách đó không xa thanh niên, Mã Minh Hải trong mắt tràn đầy khủng hoảng."),t("br"),t("br"),n._v("Vốn cho rằng, không cần áp chế tu vi, nghiền ép đối phương, dễ như trở bàn tay, nằm mơ đều không có nghĩ đến, đối phương chỉ xuất một ngón tay, liền đánh hắn căn bản không có cách nào đánh trả!"),t("br"),t("br"),n._v("Cho dù nhờ vào ngàn vạn kiếm khí, đều không được!"),t("br"),t("br"),n._v("Đang khiếp sợ khó tin, chỉ thấy thanh niên một mặt ưu nhã đi tới, mí mắt vừa nhấc, mang trên mặt vui vẻ."),t("br"),t("br"),n._v("“Mặc dù ta sợ hãi thán phục thực lực của ngươi, nhưng thực sự thật không tiện, chúng ta đang tiến hành thi vòng hai, dính dáng đi vào Thánh tử điện danh ngạch, cho nên... Là chính ngươi đem ngọc phù lấy ra, vẫn là ta tự mình ra tay?”"),t("br"),t("br"),n._v("Toàn thân căng thẳng, Mã Minh Hải kìm nén sắc mặt thấu đỏ."),t("br"),t("br"),n._v("Câu nói này, chính là vừa rồi hắn cùng đối phương nói, không nghĩ tới, một chữ không kém trả lại."),t("br"),t("br"),n._v("“Muốn cầm ta ngọc phù? Trừ phi giết ta...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt."),t("br"),t("br"),n._v("Ngọc phù mất đi, liền đại biểu lại chưa đi đến nhập Thánh con điện tư cách, một phen cố gắng phó mặc."),t("br"),t("br"),n._v("“Giết ngươi? Không đến mức...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền ngón tay búng một cái."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Mã Minh Hải lập tức cảm thấy như là núi lớn đè xuống, đem hắn ép tại trên vách đá, tùy thời đều muốn cắt đứt hô hấp."),t("br"),t("br"),n._v("“Dừng tay...”"),t("br"),t("br"),n._v("Thân thể bị ép biến hình, Mã Minh Hải muốn nói chuyện, lại không phát ra được thanh âm nào."),t("br"),t("br"),n._v("“Thật không tiện, đối thủ không khuất phục, ta còn không có chủ động thu tay lại thói quen!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng: “A, đúng rồi, ngươi mới vừa nói rất đúng...”"),t("br"),t("br"),n._v("“Thực lực mạnh, chính là có thể... Muốn làm gì thì làm!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1282"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1284"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);