(window.webpackJsonp=window.webpackJsonp||[]).push([[335],{690:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1279-tu-phuong-van-đong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1279-tu-phuong-van-đong"}},[n._v("#")]),n._v(" Chương 1279: Tứ phương vân động")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1279: Tứ phương vân động"),t("br"),t("br"),n._v("Cảnh tượng trước mắt thực sự thật là đáng sợ."),t("br"),t("br"),n._v("Loạn cung mê trận, liền xem như hắn, cũng chỉ có thể căn cứ thiếu hụt, đem phá giải, mà bây giờ, vậy mà sắp sụp đổ, khe suối phía dưới sức mạnh, đã vượt ra khỏi tưởng tượng ra."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Đang tại khiếp sợ, một đạo kiếm ý thẳng tắp xông lên Thiên Đình, hình như muốn đem bầu trời chém thành hai khúc, thủ hộ ở chung quanh trận pháp, tại đây loại sức mạnh trùng kích vào, lập tức bị đâm xuyên một cái động lớn."),t("br"),t("br"),n._v("Xao động kiếm ý, theo cửa hang, xông lên phía trên đi, tản mát ra hào quang chói sáng, giống như là tạo thành một cái kết nối thiên địa cầu vồng."),t("br"),t("br"),n._v("Két! Két!"),t("br"),t("br"),n._v("Nhìn thấy đạo này cầu vồng, “Trương Huyền” lập tức cảm thấy trong cơ thể tu luyện kiếm ý, bị triệt để áp chế, có loại không thi triển ra được cảm giác."),t("br"),t("br"),n._v("Ngay sau đó, mặt đất lần nữa cuồn cuộn, một cái màu đỏ tươi bệ đá theo khe suối trong cái khe chậm rãi nổi lên, phía trên cắm một thanh trường kiếm, khoảng cách còn xa, liền cảm thấy kiếm khí sắc bén, không ngừng lôi kéo, tựa hồ muốn người nghiền thành bột phấn."),t("br"),t("br"),n._v("“Đây là... Thượng phẩm Thánh khí?”"),t("br"),t("br"),n._v("Nắm đấm căng thẳng, “Trương Huyền” vẻ mặt lập tức thay đổi!"),t("br"),t("br"),n._v("Cường đại như thế khí tức, liền loạn cung mê trận đều không có chặn lại, càng là thả ra uy lực như thế kiếm ý, chỉ có một khả năng..."),t("br"),t("br"),n._v("Thanh trường kiếm này, đã vượt qua trung phẩm Thánh khí ràng buộc, đạt đến thượng phẩm cấp bậc!"),t("br"),t("br"),n._v(" Cũng chỉ có loại này cấp bậc, mới có uy lực như thế, liền cấp tám trận pháp, đều ngăn cản không nổi, thậm chí hắn, đều sinh ra lòng kính sợ, không dám chống lại."),t("br"),t("br"),n._v("“Là Kiếm lão nhân chuôi này bội kiếm!”"),t("br"),t("br"),n._v("Đang tại khó hiểu, một cái giống nhau như đúc “Trương Huyền” xuất hiện, vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Vừa rồi cùng Kỷ Linh Chân chiến đấu, cũng đem hắn đánh tàn bạo, tự nhiên là phân thân, hiện tại đi ra, mới thật sự là Trương Huyền."),t("br"),t("br"),n._v("Trước đó, không phải là đối thủ, tế ra trận bàn thời điểm, liền để phân thân đi ra."),t("br"),t("br"),n._v("Quả nhiên không phụ kỳ vọng, nhẹ nhõm liền đem nó nghiền ép... Chỉ là nằm mơ đều không có nghĩ đến, cái tên này thế mà còn có át chủ bài!"),t("br"),t("br"),n._v("Thanh trường kiếm này... Hẳn là lưu lại Kiếm Trì vị kia Kiếm lão nhân bội kiếm..."),t("br"),t("br"),n._v("Chẳng lẽ đã bị vị này Kỷ Linh Chân luyện hóa?"),t("br"),t("br"),n._v("“Ta nói qua, chờ ngươi đạt tới chân chính kiếm đạo chân giải, mới có tư cách để cho ta thần phục, hơn nữa đạt được Kiếm lão nhân truyền thừa... Làm sao, lời nói của ta, không tính toán gì hết?”"),t("br"),t("br"),n._v("Tiếng nổ bên trong, trường kiếm lắc lư một cái, thanh âm như là chuông lớn, đâm thủng tâm linh."),t("br"),t("br"),n._v("Đạt tới thượng phẩm Thánh khí cấp bậc, không chỉ có thể chém giết nguyên thần, để cho người ta tinh thần thất thủ, một kiếm tự nhiên, tâm cảnh đều vô cùng có khả năng không chống đỡ được, chịu ảnh hưởng."),t("br"),t("br"),n._v("“Bẩm báo tiền bối, ta cầu ngươi xuất thế, chỉ là muốn giúp ta lui địch... Chém giết vị kia, vị kia... Trương Huyền!”"),t("br"),t("br"),n._v("Cắn chặt răng răng, Kỷ Linh Chân hướng thanh niên một chỉ, lập tức vẻ mặt cứng đờ."),t("br"),t("br"),n._v("Vừa rồi chỉ có một cái, làm sao thời gian nháy mắt, biến thành hai cái?"),t("br"),t("br"),n._v("“Giết người?”"),t("br"),t("br"),n._v("Trên bệ đá trường kiếm, dường như nhìn lại, Trương Huyền lập tức cảm thấy bị một đạo kiếm ý phong tỏa, vô luận như thế nào đều khó mà chạy trốn, lập tức toàn thân mồ hôi lạnh chảy ròng."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Kỷ Linh Chân nói."),t("br"),t("br"),n._v("“Ừm, những năm này, cho ta ăn không ít đồ vật, nếu không khẳng định còn một mực ngủ say, ân tình, ta ghi xuống. Một người mà thôi, giúp ngươi giết chính là, bất quá... Muốn cho thần phục với ngươi, còn kém chút hỏa hầu, chờ chân chính lý giải kiếm đạo chân giải, lại cùng ta thương nghị!”"),t("br"),t("br"),n._v("Trên bệ đá trường kiếm, lắc lư một cái, kiếm ý như biển, đem hai cái Trương Huyền tất cả đều bao phủ ở bên trong, dường như chỉ cần hơi chuyển động ý nghĩ một chút, liền có thể tuỳ tiện chém giết."),t("br"),t("br"),n._v("“Đa tạ tiền bối!”"),t("br"),t("br"),n._v("Kỷ Linh Chân thở phào nhẹ nhõm, tràn đầy dữ tợn nhìn về phía cách đó không xa thanh niên, mang theo nồng đậm hận ý."),t("br"),t("br"),n._v("Nếu như có thể tuỳ tiện đem cái tên này chém giết, nhờ vào hiến tế Khinh Ngữ kiếm, Khinh Minh kiếm, liền có thể tới lôi kéo quan hệ, thu hoạch được hảo cảm... Đạt được truyền thừa cũng là có khả năng!"),t("br"),t("br"),n._v("Mà bây giờ, chỉ có thể hiện đem hắn giết chết lại nói."),t("br"),t("br"),n._v("“Hai cái này, giống nhau như đúc, muốn ta giết cái nào?”"),t("br"),t("br"),n._v("Đang cảm giác trường kiếm lập tức liền muốn động thủ, liền nghe đến nó giọng nghi ngờ vang lên."),t("br"),t("br"),n._v("“Cái nào?”"),t("br"),t("br"),n._v("Kỷ Linh Chân cũng nhíu nhíu mày lông mày."),t("br"),t("br"),n._v("Hắn cũng rất kỳ quái, vị này Trương Huyền danh sư, không phải chỉ có một cái ư? Làm sao đột nhiên biến ra hai cái đến, hơn nữa bất kể dung mạo vẫn là khí chất, đều giống nhau như đúc, không sai chút nào?"),t("br"),t("br"),n._v("“Ta là giả, hắn là thật!”"),t("br"),t("br"),n._v("“Ta là giả, hắn là thật!”"),t("br"),t("br"),n._v("Hai cái Trương Huyền đồng thời chỉ hướng đối phương, từng cái tràn đầy thở phì phò, trừng mắt nhìn."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy hai tên gia hỏa, như là gương, làm sao đều phân biệt không được, Kỷ Linh Chân sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bên này Kiếm lão nhân bội kiếm đâm thủng trận pháp, theo khe suối bên trong đi ra, bên ngoài lúc này cũng nổ thành hỗn loạn."),t("br"),t("br"),n._v("Vách đá phía sau tất cả kiếm tu, từng cái dừng lại nghiên cứu, nhìn về phía đâm về trên bầu trời kiếm khí, kích động thân thể run rẩy."),t("br"),t("br"),n._v("“Đây là Kiếm lão nhân bội kiếm... Nói cách khác, truyền thừa sắp xuất thế!”"),t("br"),t("br"),n._v("“Chờ nhiều năm như vậy, liền là chờ lấy trời này, mau qua tới nhìn một chút!”"),t("br"),t("br"),n._v("“Mặc kệ ai đạt được, đều muốn đoạt tới...”"),t("br"),t("br"),n._v("“Ai giành với ta, ta liền giết hắn...”"),t("br"),t("br"),n._v("Tất cả mọi người con mắt tất cả đều đỏ lên."),t("br"),t("br"),n._v("Bọn họ đóng giữ ở đây mấy trăm năm, một là vì để cho bản thân đối kiếm lĩnh ngộ càng ngày càng cao, càng quan trọng hơn liền là chờ đợi Kiếm lão nhân bội kiếm xuất thế, tốt thu hoạch được truyền thừa!"),t("br"),t("br"),n._v("Nếu không, khẳng định đã sớm rời đi, không đến mức một mực thủ hộ, không oán không hối."),t("br"),n._v(" Kiếm lão nhân, năm đó tung hoành đế quốc phạm vi, không người có thể địch, đạt được truyền thừa của hắn, thực lực tất nhiên bạo tăng, đột phá Xuất Khiếu cảnh, thậm chí Lĩnh Vực cảnh, cũng không quá khó khăn!"),t("br"),t("br"),n._v("“Nhất định là vừa rồi đi vào người thanh niên kia, đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Không biết ai hô lên, đám người lại không để ý tới nghiên cứu, vội vã hướng Kiếm Trì mê trận phương hướng lao đến."),t("br"),t("br"),n._v("Vừa rồi vị kia Trương Huyền, nhẹ nhõm đem lĩnh ngộ nửa bước chân giải Mã Minh Hải đánh bại, đi vào không đến mười phút đồng hồ, kiếm khí liền phá không mà tới, nói rõ... Khẳng định là hắn để trường kiếm thoát ly phong ấn!"),t("br"),t("br"),n._v("Bảo vật, truyền thừa xuất thế, ai cũng muốn được, đối phương coi như thực lực mạnh, đám người cũng sẽ không từ bỏ."),t("br"),t("br"),n._v("Rất nhanh, đi tới trận pháp trước mặt."),t("br"),t("br"),n._v("Lúc này loạn cung mê trận, bị trường kiếm đâm thủng, đã bị phá hư chấm dứt cấu thành, tràn ngập nguy hiểm, bất cứ lúc nào cũng sẽ vỡ vụn."),t("br"),t("br"),n._v("Trước kia, muốn đi vào, rất khó làm được, mà bây giờ, nhẹ nhõm liền có thể hoàn thành."),t("br"),t("br"),n._v("“Trước không vội, tên kia thực lực chúng ta cũng nhìn thấy, không tầm thường, một mình đối kháng lời nói, khẳng định không phải là đối thủ... Không bằng liên thủ chống lại!”"),t("br"),t("br"),n._v("Một cái lão giả ngăn lại đám người, nói."),t("br"),t("br"),n._v("“Không sai, mọi người liên thủ mới có cơ hội!” Có người hùa theo."),t("br"),t("br"),n._v("“Tốt, chúng ta có thể liên thủ, đạt được truyền thừa làm sao bây giờ?”"),t("br"),t("br"),n._v("Có người hỏi."),t("br"),t("br"),n._v("“Chúng ta ở chung được lâu như vậy, mọi người hiểu rõ, trước liên thủ, đem truyền thừa đạt được lại nói, đến lúc đó, mọi người cùng nhau nghiên cứu!”"),t("br"),t("br"),n._v("Lão giả nói."),t("br"),t("br"),n._v("“Tốt!” Đám người đồng loạt lên tiếng."),t("br"),t("br"),n._v("Kiếm lão nhân truyền thừa, uy lực vô tận, nếu như bọn họ người có khả năng nghiên cứu được, cũng không trở thành một mực quanh quẩn một chỗ tại vách đá trước mặt, nhiều năm không có đột phá."),t("br"),t("br"),n._v("Lại nói, thật muốn nuốt riêng, bọn họ cũng biết, khẳng định không cách nào còn sống ra ngoài."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Đạt được mục đích nhất trí, đồng loạt hướng trận pháp đi vào."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Kiếm Trì vị trí ở trên đảo, mấy cái thanh niên đối diện chiến lập, từng cái ánh mắt cảnh giác."),t("br"),t("br"),n._v("“Tôn Càn, chúng ta xem như quen biết đã lâu, sớm đã hiểu rõ, ngươi coi như có khả năng cướp đi của ta ngọc phù, khẳng định cũng sẽ nguyên khí tổn hao nhiều, đến lúc đó gặp được người khác đồng dạng sẽ bị đào thải, ta nhìn không bằng hai chúng ta liên thủ...”"),t("br"),t("br"),n._v("Một thanh niên mở miệng nói, hắn đối diện, chính thức Mã Minh Hải quen thuộc vị kia “Tôn huynh”."),t("br"),t("br"),n._v("“Hồ huynh nói không sai, cùng ngươi đối chiến, ta quả thực sẽ nguyên khí đại thương, dù sao chỉ đào thải 1 3 người, hiện tại chỉ còn lại có mười cái, chúng ta hoàn toàn có thể liên thủ đối phó những người khác, không cần thiết tranh một mất một còn.”"),t("br"),t("br"),n._v("“Tôn huynh” Tôn Càn mỉm cười."),t("br"),t("br"),n._v("“Vậy thì tốt, chúng ta liền làm một cái danh sư ước định, lẫn nhau liên thủ, chung đào thải người khác.”"),t("br"),t("br"),n._v("Thanh niên nhẹ gật đầu."),t("br"),t("br"),n._v("Danh sư chú ý đến thân phận, có danh sư ước định, cơ bản sẽ không vi phạm."),t("br"),t("br"),n._v("“Được...”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Tôn Càn nhẹ nhàng cười một tiếng, đang nghĩ tiếp tục nói chuyện, liền cảm thấy linh khí chung quanh kịch liệt lay động, ngay sau đó liền thấy một cỗ kiếm ý, bay thẳng Thiên Đình."),t("br"),t("br"),n._v("“Có bảo vật xuất thế...”"),t("br"),t("br"),n._v("Mấy người đồng thời con mắt co rụt lại."),t("br"),t("br"),n._v("Bọn họ đều là đế quốc phạm vi bên trong thiên tài, kiến thức rộng rãi, biết bảo vật khi xuất hiện trên đời, sẽ đưa tới dị tượng."),t("br"),t("br"),n._v("“Nghe nói Tiềm Xung đế quốc có một cái Kiếm Trì, trong đó cất giấu không biết tên Thánh khí, cấp bậc không biết, từng có cao thủ, đến đây dò xét, kết quả bị Danh Sư đường ra tay đuổi đi, nói là Tiềm Xung đế quốc cơ duyên, do bọn họ tự mình giải quyết... Vẫn cho là chỉ là truyền thuyết, chẳng lẽ là thật? Chúng ta vị trí hòn đảo, lại là Kiếm Trì vị trí địa?”"),t("br"),t("br"),n._v("Nắm đấm đột nhiên xiết chặt, Tôn Càn nhớ ra cái gì đó, tràn đầy kích động."),t("br"),t("br"),n._v("Tiềm Xung đế quốc Kiếm Trì sự tình bọn họ đều có nghe qua, không nghĩ tới thi vòng hai thế mà thiết trí ở đây, mấu chốt nhất là bảo vật xuất thế khí tức, mãnh liệt như thế, nói cách khác... Một mực không ai thấy qua bảo vật, đã xuất thế!"),t("br"),t("br"),n._v("Truyền thuyết là có thật lời nói, có khả năng đem hắn đạt được, thực lực tất nhiên đại tăng, coi như tại Thánh tử điện, loại địa phương này khẳng định cũng có thể trở nên nổi bật, bộc lộ tài năng."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Hồ huynh cũng khó có thể an nại hưng phấn."),t("br"),t("br"),n._v("“Trước không vội, như thế lớn dị động, khẳng định cả đảo người đều phát hiện, đến lúc đó tất cả mọi người sẽ ra tay, tranh đấu tất nhiên lợi hại, không bằng chúng ta trước thương nghị một cái hiệp định, nếu không... Chúng ta so với những người khác cũng không chiếm ưu thế, nhất là vị kia Trương Huyền!”"),t("br"),t("br"),n._v("Ngăn lại muốn đi qua đám người, Tôn Càn suy nghĩ một chút, nói."),t("br"),t("br"),n._v("“Trương Huyền? Nơi này không cho phép sử dụng vũ khí, không dùng khôi lỗi, chẳng qua cùng cấp bậc mạnh mẽ thôi, chỉ cần chúng ta không áp chế tu vi, còn không thoải mái nghiền ép?”"),t("br"),t("br"),n._v("Hồ huynh xem thường."),t("br"),t("br"),n._v("Cái tên này, chỉ là cùng cấp bậc vô địch, thực lực chân chính cùng bọn hắn so, vẫn là kém một đoạn dài."),t("br"),t("br"),n._v("Không tính là gì!"),t("br"),t("br"),n._v("“Triệu sư yêu cầu là không dùng khôi lỗi, nhưng mà bảo vật phía trước, ai có thể khống chế nổi sao? Lại nói, hắn chỉ cần dùng khôi lỗi đem chúng ta ngăn tại bên ngoài, bản thân độc chiếm bảo vật là được, không cướp đoạt ngọc phù, hơn nữa không phá hư, cũng không tính vi phạm quy định!”"),t("br"),t("br"),n._v("Tôn Càn nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hồ huynh sửng sốt."),t("br"),t("br"),n._v("Triệu Hưng Mặc Triệu sư chỉ nói, không cho phép nhờ vào vũ khí, cướp đoạt ngọc phù... Khôi lỗi chỉ là ngăn cản bọn họ, quả thực không tính không tuân theo quy định."),t("br"),t("br"),n._v("“Vậy ngươi nói làm sao bây giờ?”"),t("br"),t("br"),n._v("Dừng lại một chút, biết chỉ bằng thực lực của bọn hắn, căn bản không phải những khôi lỗi kia đối thủ, Hồ huynh hỏi."),t("br"),t("br"),n._v("“Rất đơn giản... Nếu như nhìn thấy Trương Huyền, ý của ta là... Chúng ta trực tiếp liên thủ xông đi lên, không đợi hắn phóng thích khôi lỗi, trước hết đem hắn đánh bại, hơn nữa đả thương! Như vậy, nhất định có thể tránh khỏi không ít phiền phức!”"),t("br"),t("br"),n._v("Mắt sáng lên, Tôn Càn nói."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1287"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1289"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);