(window.webpackJsonp=window.webpackJsonp||[]).push([[338],{693:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1281-kiem-đao-chan-giai"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1281-kiem-đao-chan-giai"}},[n._v("#")]),n._v(" Chương 1281: Kiếm đạo chân giải")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1281: Kiếm đạo chân giải"),t("br"),t("br"),n._v("“Năm phút đồng hồ?”"),t("br"),t("br"),n._v("Trên bệ đá trường kiếm nhịn không được sững sờ."),t("br"),t("br"),n._v("Kiếm đạo chân giải, là đối kiếm lĩnh ngộ cảnh giới tối cao, không phải dễ dàng như vậy đi đến, không có mấy chục, mấy trăm năm tích lũy, gần như không có khả năng hoàn thành."),t("br"),t("br"),n._v("Coi như năm đó Kiếm lão nhân, cũng tích lũy không xuống một trăm năm, mới lấy thành công!"),t("br"),t("br"),n._v("Trước mắt vị này, thoạt nhìn chẳng qua hai mươi tuổi, lĩnh ngộ thượng kiếm tâm đã rất đáng sợ..."),t("br"),t("br"),n._v("Lại còn nói, chờ năm phút đồng hồ, liền có thể đột phá đến kiếm đạo chân giải..."),t("br"),t("br"),n._v("Cái này sao có thể?"),t("br"),t("br"),n._v("“Tiền bối, đừng nghe hắn nói bậy, hắn khẳng định là đang trì hoãn thời gian, không động thủ, ta sợ xuất hiện biến cố gì...”"),t("br"),t("br"),n._v("Thấy sự tình phát triển phương hướng, đã bắt đầu không nhận khống, Kỷ Linh Chân tràn đầy cuống cuồng."),t("br"),t("br"),n._v("Ta đưa ngươi triệu hoán đi ra, không phải để ngươi cùng kẻ địch hàn huyên... Thân là vũ khí, có thể hay không hơi chuyên nghiệp một chút?"),t("br"),t("br"),n._v("“Im miệng!”"),t("br"),t("br"),n._v("Trường kiếm hét lớn."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Kỷ Linh Chân còn không có kịp phản ứng, liền cảm thấy một đạo kiếm khí bén nhọn, thẳng tắp bay tới."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Hơi đỏ mặt, một ngụm máu tươi phun tới, cả người bay ngược mà ra, ngực xuất hiện một đạo vết máu, dữ tợn đáng sợ."),t("br"),t("br"),n._v("Giãy dụa lấy đứng dậy, thân thể cứng ngắc, Kỷ Linh Chân vẻ mặt trở nên trắng."),t("br"),t("br"),n._v("Một mực đến nay, hắn chỉ biết là thanh trường kiếm này, là thượng phẩm Thánh khí, cụ thể đạt đến cấp bậc gì, cũng không hiểu rõ tình hình, cảm nhận được kiếm khí đánh tới, mới hiểu được, thực lực so với hắn tưởng tượng còn muốn đáng sợ nhiều lắm!"),t("br"),t("br"),n._v("Một đạo kiếm ý liền để hắn không có chút nào sức đánh trả, thật muốn giết người... Khẳng định ngăn cản không nổi!"),t("br"),t("br"),n._v("“Còn dám nói nhảm, tin hay không lão tử hiện tại liền giết chết ngươi?”"),t("br"),t("br"),n._v("Trường kiếm hừ lạnh."),t("br"),t("br"),n._v("Cũng khó trách nó tức giận."),t("br"),t("br"),n._v("Kiếm lão nhân vẫn lạc, đem hắn ở lại chỗ này, muốn thoát ly, có hai cái điều kiện."),t("br"),t("br"),n._v("Thứ nhất, triệt để lĩnh ngộ bên ngoài cái kia “Kiếm” chữ đại biểu ý nghĩa; Thứ hai, đối kiếm đạo lý giải đạt tới chân giải tình trạng."),t("br"),t("br"),n._v("Chỉ có thỏa mãn hai cái này, mới có thể thoát ly bệ đá, cá liệng biển cả, những năm này, gặp qua không biết bao nhiêu thiên tài, đáng tiếc... Không có một cái thỏa mãn điều kiện."),t("br"),t("br"),n._v("Cách đó không xa vị này Kỷ Linh Chân, có thể nói là mấy ngàn năm nay, gần nhất, nhưng mà, vẫn như cũ không cách nào đột phá chân giải..."),t("br"),t("br"),n._v("Nếu là vị này Trương sư, có thể làm được, cũng không cần tiếp tục chờ đi xuống, tự nhiên không muốn để cho những người khác chậm trễ cùng phá hư!"),t("br"),t("br"),n._v("“Tiền bối...”"),t("br"),t("br"),n._v("Nghĩ đến mấy trăm năm tình cảm, thế mà vẫn còn so sánh không lên đối phương mấy câu, Kỷ Linh Chân khí run rẩy, nhịn không được nói: “Hắn đối ngươi sử dụng sư ngôn thiên bẩm cùng ma âm, cố ý mê hoặc, có chủ tâm không tốt...”"),t("br"),t("br"),n._v("“Mê hoặc?”"),t("br"),t("br"),n._v("“Đúng, tiền bối ngươi nghĩ một hồi, kiếm đạo chân giải, sao mà khó khăn, bao nhiêu thiên tài, đều vây ở cửa ải này, khó mà làm được, liền xem như Kiếm lão nhân, cũng không có dễ dàng như vậy đã đột phá đi!”"),t("br"),t("br"),n._v("Kỷ Linh Chân tận tình khuyên bảo: “Hắn chẳng qua chỉ là thượng kiếm tâm, liền nửa bước chân giải đều không đạt tới, làm sao có thể vài phút, đã đột phá đến chân giải tình trạng? Rõ ràng là cố ý kéo dài, muốn lừa ngươi, dùng phương pháp đặc thù, đưa ngươi luyện hóa...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trường kiếm do dự."),t("br"),t("br"),n._v("Đối phương nói không sai, kiếm đạo chân giải, dễ dàng như vậy liền đạt tới, cũng không trở thành mệnh danh kiếm thuật cao nhất ngưỡng cửa!"),t("br"),t("br"),n._v("Năm đó, Kiếm lão nhân tung hoành liên minh đế quốc, không biết bao nhiêu dùng kiếm cao thủ, đều thua ở thủ hạ, cũng bởi vì đối dùng kiếm lý giải, đạt đến chân giải tình trạng!"),t("br"),t("br"),n._v("Loại này lý giải, vô số bát tinh danh sư, đều làm không được."),t("br"),t("br"),n._v("Một cái hai mươi tuổi tiểu gia hỏa, thật sự có thể đột phá?"),t("br"),t("br"),n._v("“Cái tên này quỷ kế đa đoan, khẳng định còn muốn cái gì tổn hại chiêu, không bằng hiện tại liền giết...”"),t("br"),t("br"),n._v("Thấy nó dừng lại, Kỷ Linh Chân biết có chút ý động, vội vàng hô, lời nói còn không có kết thúc, đột nhiên toàn thân mát lạnh, ngay sau đó liền cảm thấy một đạo to lớn vô cùng kiếm khí, bay thẳng bầu trời."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Toàn bộ Kiếm Trì tựa như sôi trào, một cái nháy mắt, vô số kiếm ý đồng loạt từ đằng xa bay tới, hướng bên này hội tụ, tựa như tạo thành gió lốc."),t("br"),t("br"),n._v("“Đây là...”"),t("br"),t("br"),n._v("Cảm nhận được trong cơ thể mình lĩnh ngộ kiếm ý, bị áp chế không cách nào động đậy, cả người đều có chút cứng ngắc, Kỷ Linh Chân khuôn mặt trắng bệch, khống chế mang theo người cứng ngắc, hướng kiếm ý truyền đến phương hướng nhìn sang."),t("br"),t("br"),n._v("Lập tức liền thấy lời mới vừa nói thanh niên, cứ việc hai mắt nhắm nghiền, cả người lại giống như là biến thành một thanh kiếm sắc, bất cứ lúc nào cũng sẽ phá không mà tới, đem người giết chết!"),t("br"),t("br"),n._v("Coong coong coong coong!"),t("br"),t("br"),n._v("Vô số thanh trường kiếm, theo trận pháp bên ngoài phá không bay tới, lơ lửng tại bốn phía, nằm sấp trên mặt đất, tựa như thấy được Quân Vương."),t("br"),t("br"),n._v("“Vạn kiếm triều tông... Đây là kiếm đạo chân giải?”"),t("br"),t("br"),n._v("Khuôn mặt tái đi, Kỷ Linh Chân lắc lư hai lần."),t("br"),t("br"),n._v("Mới vừa nói hết, đối phương là đang tính tính một thứ gì đó, khẳng định không cách nào đột phá, liền xuất hiện loại tình huống này, cả người đều cảm thấy sắp bối rối."),t("br"),t("br"),n._v("Bảy trăm năm trước, hắn liền đạt đến nửa bước chân giải, thời gian khá dài như vậy, một mực nghiên cứu, đều không thể đột phá, đối phương chỉ nhắm mắt lại tu luyện vài phút, liền đạt tới..."),t("br"),t("br"),n._v("Cường đại chênh lệch, để hắn sắp sụp đổ."),t("br"),t("br"),n._v("Tận mắt nhìn thấy, đều cảm thấy không thể tưởng tượng nổi."),t("br"),t("br"),n._v("“Hắn, hắn lĩnh ngộ kiếm đạo chân giải? Nguy rồi...”"),t("br"),t("br"),n._v("Kỷ Linh Phong thì con ngươi thu hẹp, trong mắt tràn đầy khủng hoảng."),t("br"),t("br"),n._v("Vốn cho rằng Kiếm lão nhân chuôi này bội kiếm, sẽ trực tiếp ra tay, đem Trương Huyền gạt bỏ, không nghĩ tới, thái độ mập mờ, hiện tại người sau càng là trực tiếp đột phá... Làm không cẩn thận, không biết giết, còn có thể nhận chủ!"),t("br"),t("br"),n._v("Nói cách khác, bọn họ sẽ rơi vào nguy hiểm!"),t("br"),t("br"),n._v("“Trốn!”"),t("br"),n._v("Ý thức được điểm ấy, không dám tiếp tục dừng lại, cố nén đau đớn trên người, cắn răng một cái, xoay người liền hướng sương mù dày đặc bên ngoài phóng đi."),t("br"),t("br"),n._v("Hắn tới thời điểm, mặc dù là bị Kỷ Linh Chân tiếp đón, nhưng cụ thể lộ tuyến ghi vào trong lòng, lại thêm trận pháp vỡ vụn, muốn chạy ra, không tính quá khó."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Mới vừa chạy ra hai bước, còn chưa đi xa, thân thể đột nhiên phát lạnh, ngay sau đó liền cảm thấy nửa người dưới tại trước mắt mình vọt ra ngoài."),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy Kỷ Linh Chân thu tay lại chỉ, đâm thủng kiếm khí của mình, chính là từ đầu ngón tay hắn bắn ra."),t("br"),t("br"),n._v("Làm xong những này, hắn vội vàng xoay người, nhìn về phía bệ đá: “Tiền bối, ta là bị cái tên này mê hoặc, mới đối Trương sư động thủ, trên thực tế ngươi cũng biết, ta nhiều năm chưa đi ra ngoài, cùng hắn vốn không che mặt...”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Nửa người trên rơi trên mặt đất, máu tươi tuôn ra, Kỷ Linh Phong hai mắt trợn tròn xoe, thẳng đến trước khi chết, cũng không dám tin tưởng, huynh trưởng của mình, tự mình ra tay đem hắn chém giết."),t("br"),t("br"),n._v("“Im miệng, không nên quấy rầy Trương sư đột phá!”"),t("br"),t("br"),n._v("Dường như thường thấy giết người, trường kiếm không có chút nào tâm tình chập chờn, quát lạnh một tiếng."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Không dám nói lời nào, Kỷ Linh Chân yên tĩnh đợi tại nguyên chỗ."),t("br"),t("br"),n._v("Kỷ Linh Phong cái này vừa tới người, đều có thể nhìn ra trường kiếm thái độ, hắn bồi bạn đối phương mấy trăm năm, làm sao không rõ ràng?"),t("br"),t("br"),n._v("Muốn luyện hóa chuôi kiếm này yêu cầu, liền là lĩnh ngộ kiếm đạo chân giải, đối phương đã đạt tới, vừa rồi càng là một cái nói ra Kiếm lão nhân để thư lại ý tứ, đem luyện hóa, chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("Một khi hoàn thành, mạng của hắn liền không có, cho nên... Quyết định thật nhanh, chém giết Kỷ Linh Phong, phủi sạch quan hệ."),t("br"),t("br"),n._v("Sinh tử trước mặt, hắn liền danh sư đều có thể giết, huống chi huynh đệ!"),t("br"),t("br"),n._v("Chuyện bên này, Trương Huyền cũng không hiểu, có phân thân thủ hộ, giờ phút này đã toàn thân tâm, đắm chìm trong trước đó sưu tập Thiên Đạo kiếm pháp bên trong."),t("br"),t("br"),n._v("Bộ kiếm pháp kia, có thể nói là tập hợp phong hào đế quốc phạm vi bên trong, thậm chí liên minh đế quốc bên trong sở hữu cao thủ sử dụng kiếm trí tuệ, một khi học tập, cả người nguyên thần, dường như biến thành một thanh trường kiếm, sắc bén vô cùng, bất cứ lúc nào cũng sẽ trảm thiên diệt địa, phát huy ra sợ hãi thán phục thế nhân uy lực."),t("br"),t("br"),n._v("Nguyên thần trường kiếm hình thành về sau, tựa hồ đối với thực lực so với hắn thấp kiếm giống như vũ khí, có cực mạnh năng lực chưởng khống, hơi chuyển động ý nghĩ một chút, liền có thể đem hắn khống chế."),t("br"),t("br"),n._v("Đương nhiên, chỉ là nông cạn nhất khống chế, để hắn linh tính thần phục, không dám đối phó bản thân... Muốn triệt để khống chế, còn cần luyện hóa."),t("br"),t("br"),n._v("Dù vậy, cũng rất đáng sợ!"),t("br"),t("br"),n._v("Nắm giữ loại năng lực này, gặp gỡ tương đồng thực lực kiếm tu, đối phương căn bản không có cách nào chiến thắng."),t("br"),t("br"),n._v("“Đối kiếm lĩnh ngộ càng sâu, tiện tay một chiêu, đều là kiếm ý... Dùng kiếm lời nói, sức chiến đấu so quyền pháp, chưởng pháp loại hình mạnh mẽ tiếp cận gấp đôi!”"),t("br"),t("br"),n._v("Con mắt đóng chặt, cảm nhận được kiếm ý ở trong người chảy xuôi, đối với mình thực lực, Trương Huyền có rõ ràng phán đoán."),t("br"),t("br"),n._v("“Không hổ là kiếm đạo chân giải, lợi hại... Đúng rồi, có loại thực lực này, có phải hay không có thể đánh bại phân thân?”"),t("br"),t("br"),n._v("Một cái ý nghĩ đột nhiên xông ra."),t("br"),t("br"),n._v("“Được rồi... Vẫn là không tìm ngược!”"),t("br"),t("br"),n._v("Lắc đầu."),t("br"),t("br"),n._v("Hắn cùng phân thân, cứ việc xuất từ nhất thể, nhưng đối phương nhờ vào Cửu Thiên Liên Thai, thực lực vượt xa quá hắn, mỗi lần cảm thấy tiến bộ, muốn đi khiêu chiến, liền phát hiện đối phương trở nên càng thêm cường đại..."),t("br"),t("br"),n._v("Lần này cũng giống vậy."),t("br"),t("br"),n._v("Bản thân lĩnh ngộ kiếm đạo chân giải, tinh thần một câu thông, trên thực tế chẳng khác nào đối phương cũng hiểu, như thế nào đánh thắng được?"),t("br"),t("br"),n._v("Thật muốn ra tay, có thể tưởng tượng, khẳng định sẽ bị lần nữa đánh thành đầu heo, thê thảm vô cùng."),t("br"),t("br"),n._v("Chậm rãi mở mắt."),t("br"),t("br"),n._v("Lập tức nhìn thấy hơn một trăm chuôi trung phẩm Thánh khí cấp bậc trường kiếm, nằm sấp tại cách đó không xa, từng cái tràn đầy run rẩy."),t("br"),t("br"),n._v("“Khó trách trước đó Mộ Vân kiếm, như vậy kính sợ Mã Minh Hải, thì ra là thế...”"),t("br"),t("br"),n._v("Lĩnh ngộ chân giải, mới hiểu được vì cái gì Mộ Vân kiếm, vì cái gì run rẩy."),t("br"),t("br"),n._v("Có loại năng lực này, có thể tuỳ tiện gạt bỏ đối phương linh tính, làm sao không sợ hãi?"),t("br"),t("br"),n._v("“Trương sư, chúc mừng đột phá... Ta cũng là chịu Kỷ Linh Phong cái này gian nịnh tiểu nhân mê hoặc, mới làm ra sai lầm quyết định, mong rằng đạt được tha thứ!”"),t("br"),t("br"),n._v("Ngay sau đó nghe được một thanh âm, quay đầu nhìn lại, chỉ thấy Kỷ Linh Chân quỳ gối cách đó không xa, trong mắt tràn đầy khủng hoảng."),t("br"),t("br"),n._v("Trước đó hắn cũng không phải là đối thủ, hiện tại trên bệ đá trường kiếm, rõ ràng bất công, lại thêm nắm trong tay nhiều như vậy chuôi Thánh khí, thật muốn giết hắn, một ý niệm thôi."),t("br"),t("br"),n._v("“Tha thứ?”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Trương Huyền lúc này mới phát hiện, Kỷ Linh Phong đã bị giết, quay đầu nhìn phân thân một cái, linh hồn trao đổi, rõ ràng chuyện gì xảy ra, lông mày nhíu một cái."),t("br"),t("br"),n._v("Liền huynh đệ đều có thể tàn sát, đã không có nhân tính, không còn luân lý đạo đức."),t("br"),t("br"),n._v("Dặn dò phân thân một câu, không tiếp tục để ý cái tên này, Trương Huyền nhìn về phía cách đó không xa bệ đá."),t("br"),t("br"),n._v("“Trương sư!”"),t("br"),t("br"),n._v("Trường kiếm tràn đầy kích động."),t("br"),t("br"),n._v("Kiếm lão nhân sau khi chết, nó vẫn bị phong ấn ở nơi này, chừng trên vạn năm, lâu như vậy, cứ việc phần lớn đều đang ngủ say, có khả năng rời khỏi, khẳng định cũng không nguyện ý lưu lại."),t("br"),t("br"),n._v("“Trương sư, trên người của ta có lưu lại Kiếm lão nhân truyền thừa, cần ngươi đem ta theo trên bệ đá rút ra, mới có thể hiện ra...”"),t("br"),t("br"),n._v("Một đạo ý niệm truyền tới."),t("br"),t("br"),n._v("“Rút ra?”"),t("br"),t("br"),n._v("Trương Huyền lúc này mới phát hiện, thanh trường kiếm này chăm chú cắm ở bệ đá chính giữa, phảng phất bị thứ gì cầm cố lại, khẽ động cũng không thể động."),t("br"),t("br"),n._v("Coi như thực lực của nó mạnh như vậy, đều không thể tránh thoát."),t("br"),t("br"),n._v("“Ngươi đã không tránh thoát được, ta... Như thế nào rút ra?”"),t("br"),t("br"),n._v("Trong lòng nghi ngờ, nhịn không được hỏi."),t("br"),t("br"),n._v("(Mọi người tiết đoan ngọ vui vẻ!)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1289"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1291"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);