(window.webpackJsonp=window.webpackJsonp||[]).push([[339],{694:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1282-ta-khong-hoc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1282-ta-khong-hoc"}},[n._v("#")]),n._v(" Chương 1282: Ta không học")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1282: Ta không học"),i("br"),i("br"),n._v("“Đây là Kiếm lão nhân lưu lại, chỉ có lĩnh ngộ kiếm đạo chân giải kiếm tu, mới có tư cách tiếp xúc, hơn nữa phá vỡ phong ấn, đem ta rút ra! Nhưng cụ thể như thế nào mới có thể làm được, ta cũng không hiểu rõ tình hình...”"),i("br"),i("br"),n._v("Trường kiếm mang theo xấu hổ."),i("br"),i("br"),n._v("Kiếm lão nhân đi về cõi tiên, đem hắn phong ấn tại cái này về sau, vẫn ngủ say, chỉ biết là muốn thoát ly bệ đá, nhất định phải phù hợp kiếm đạo chân giải cùng lĩnh ngộ “Kiếm” chữ ý nghĩa, tìm tới cái gọi là truyền nhân, còn như thế nào đem hắn rút ra, cũng không hiểu rõ tình hình."),i("br"),i("br"),n._v("“Không biết rõ tình hình?”"),i("br"),i("br"),n._v("Trương Huyền lắc đầu: “Ta thử một chút đi!”"),i("br"),i("br"),n._v("Thanh trường kiếm này, ít nhất đạt đến thượng phẩm Thánh khí, đối với hắn có rất lớn trợ giúp, có khả năng biến thành của mình, tự nhiên sẽ không cự tuyệt."),i("br"),i("br"),n._v("Thân thể nhoáng một cái, đi tới trước mặt, nắm chặt trường kiếm, cánh tay dùng sức."),i("br"),i("br"),n._v("Trường kiếm đóng ở phía trên, không nhúc nhích tí nào."),i("br"),i("br"),n._v("Nhíu nhíu mày, Trương Huyền cúi đầu nhìn xuống."),i("br"),i("br"),n._v("“Lại là cái Thiên Công vật phẩm, khó trách...”"),i("br"),i("br"),n._v("Lấy lực lượng của hắn, liền xem như một tòa núi lớn, cũng có thể tuỳ tiện rút lên, giờ phút này lại ngay cả một cái bình thường bệ đá đều không có biện pháp, nguyên lai trường kiếm bốn phía, không chỉ có đặc thù phù văn phong ấn, quan trọng hơn vẫn là một cái Thiên Công cơ quan, một khi cắn vào, không có phương pháp đặc thù, căn bản không có khả năng mở ra."),i("br"),i("br"),n._v("“Đã Kiếm lão nhân muốn lĩnh ngộ kiếm đạo chân giải mới có thể có đến truyền thừa, muốn rút kiếm, đoán chừng cũng có tương đồng yêu cầu...”"),i("br"),i("br"),n._v("Suy đoán một chút, hai tay nắm chắc chuôi kiếm, Trương Huyền con mắt đóng lại, vừa mới lĩnh ngộ kiếm đạo chân giải, lập tức hình thành từng đạo kiếm ý, ở trong người chậm chạp vận chuyển."),i("br"),i("br"),n._v("Ầm ầm!"),i("br"),i("br"),n._v("Giống như là vật gì đó bị kích hoạt, một tiếng nổ vang, một cỗ đặc thù sức mạnh theo bệ đá bốn phía, bừng lên, ngay sau đó một đạo kiếm khí bén nhọn, đâm nhanh mà ra, đem chung quanh bao phủ."),i("br"),i("br"),n._v("Như là phong ấn, Trương Huyền cảm giác thần trí của mình lập tức bị kiếm khí chặt đứt, cũng lại không nhìn thấy bên ngoài, đồng dạng, người bên ngoài, cũng không nhìn thấy tình huống của mình."),i("br"),i("br"),n._v("Nhướng mày, đang nghĩ buông ra chuôi kiếm, liền thấy kiếm khí, chậm rãi ở trước mắt hội tụ, dần dần biến thành một cái lão giả."),i("br"),i("br"),n._v("Bộ dáng có chút gầy yếu, nhưng cả người như là một thanh trường kiếm, mang theo sắc bén chi ý, bất cứ lúc nào cũng sẽ đâm thủng bầu trời."),i("br"),i("br"),n._v("“Kiếm lão nhân?”"),i("br"),i("br"),n._v("Trương Huyền âm thầm cảnh giác."),i("br"),i("br"),n._v("Cứ việc đối phương do kiếm khí hội tụ, nhưng trong đó ẩn chứa khi còn sống ý niệm, thập phần cường đại, coi như trước mắt hắn thực lực, chỉ sợ cũng khó mà chống lại."),i("br"),i("br"),n._v("“Lão phu Kiếm Chân, người xưng Kiếm lão nhân! Có thể đem ta lưu lại ý niệm kích hoạt, nhìn tới đã lĩnh ngộ kiếm đạo chân giải, có tư cách tiếp nhận truyền thừa của ta...”"),i("br"),i("br"),n._v("Hai tay chắp sau lưng, lão giả đen nhánh hai con ngươi ở giữa, kiếm ý chảy xuôi, dường như có thể chặt đứt tất cả."),i("br"),i("br"),n._v("“Quỳ xuống, bái ta làm thầy, truyền cho ngươi Linh Hư tam kiếm, cùng phá giải bệ đá phong ấn phương pháp!”"),i("br"),i("br"),n._v("“Bái sư?” Trương Huyền nhíu nhíu mày."),i("br"),i("br"),n._v("“Được ta truyền thừa, chịu ta ân huệ, tự nhiên muốn bái ta làm thầy!”"),i("br"),i("br"),n._v("Kiếm lão nhân thanh âm bên trong mang theo không thể nghi ngờ giọng nói: “Lão phu Linh Hư tam kiếm, một kiếm phá hải, một kiếm Uông Dương, một kiếm Lăng Thiên, đều là kinh thiên động địa võ kỹ, liên minh đế quốc, vô số người tranh đoạt, đều không thể được! Nếu không phải trọng thương ở đây, lại thế nào khả năng, tùy tiện truyền cho ngoại nhân!”"),i("br"),i("br"),n._v("Bình thường cao thủ, muốn lưu lại truyền thừa, đầu tiên muốn khảo nghiệm đệ tử nhân phẩm, bản tính, Kiếm lão nhân lúc đến nơi này, đã không kiên trì nổi, lại không cách nào thiết trí tất cả cửa ải, chỉ để lại chữ viết cùng cái này bệ đá, đem trường kiếm phong ấn trong đó."),i("br"),i("br"),n._v("Bất quá, mặc dù không cách nào khảo nghiệm hậu nhân, nhưng muốn học công pháp của hắn, bái sư là nhất định."),i("br"),i("br"),n._v("Không thầy trò danh phận, như thế nào yên tâm truyền thừa?"),i("br"),i("br"),n._v("“Ngươi tiêu tốn tâm huyết, xem hiểu của ta lưu chữ, càng tuổi còn trẻ, lĩnh ngộ kiếm đạo chân giải, chắc hẳn, đối ta truyền thừa tâm động đã lâu đi! Bái sư về sau, không chỉ truyền cho ngươi tam kiếm, còn có thể đem cả đời đối với kiếm pháp lý giải, dốc túi truyền cho, chỉ cần thật tốt học, trong vòng ba trăm năm, trở thành liên minh đế quốc đỉnh tiêm kiếm đạo tông sư, không thành vấn đề!”"),i("br"),i("br"),n._v("Kiếm lão nhân trong giọng nói mang theo tự ngạo."),i("br"),i("br"),n._v("Hắn đối với hắn kiếm pháp nắm giữ tuyệt đối tự tin."),i("br"),i("br"),n._v("Đối phương trăm cay nghìn đắng chạy tới, bỏ bao công sức nghiên cứu bản thân “Kiếm” chữ, tự nhiên là dự đoán được truyền thừa."),i("br"),i("br"),n._v("Đã như vậy, vị này làm lão sư, cũng muốn nắm giữ quyền uy tuyệt đối cùng thân phận."),i("br"),i("br"),n._v("Giá đỡ muốn bày đủ."),i("br"),i("br"),n._v("“Trong vòng ba trăm năm, mới trở thành liên minh đế quốc đỉnh tiêm kiếm đạo tông sư? Quá lâu, ta sợ đợi không được!”"),i("br"),i("br"),n._v("Vẻ mặt làm khó, Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Hắn nhất định phải đuổi tại ba mươi tuổi trước trở thành cửu tinh danh sư, mới có thể miễn trừ trong cơ thể Tiên Thiên thai độc bạo phát."),i("br"),i("br"),n._v("Càng cần hơn sang năm tháng tư phía trước, liền nắm giữ bát tinh đỉnh phong danh sư thậm chí cao hơn thực lực, mới có thể đem Lạc Nhược Hi theo nàng thất bại “Hôn nhân” bên trong giải cứu ra."),i("br"),i("br"),n._v("Ba trăm năm, chỉ là liên minh đế quốc đỉnh tiêm, cùng Thánh Nhân quý tộc còn có nhất định chênh lệch... Quá chậm."),i("br"),i("br"),n._v("“Chờ không đến? Có ý tứ gì?”"),i("br"),i("br"),n._v("Kiếm lão nhân híp mắt lại, trên không kiếm khí lập tức xao động mà ra, đâm vào không khí phát ra nghẹn ngào thanh âm: “Là cảm giác kiếm pháp của ta quá mức thâm ảo, ngươi không có tự tin thành công? Không đúng! Có thể lĩnh ngộ ta ‘Kiếm’ chữ ý nghĩa, nói rõ ngộ tính không yếu, Linh Hư tam kiếm cứ việc rườm rà phức tạp, chỉ cần nghiêm túc, vẫn là có ba thành trở lên cơ hội tu luyện thành công... Đương nhiên, đối ta lưu chữ, lĩnh ngộ thời gian càng ngắn, ngộ tính liền càng cao, luyện thành tỷ lệ cũng liền càng lớn!”"),i("br"),i("br"),n._v("Hắn lưu chữ mục đích rất đơn giản, khảo nghiệm một người ngộ tính."),i("br"),i("br"),n._v("Không có ngộ tính, coi như cho bí tịch, cũng không cách nào thành công."),i("br"),i("br"),n._v("Bởi vậy, xem hiểu hàm nghĩa thời gian càng ngắn, nói rõ ngộ tính cũng liền càng nghịch thiên."),i("br"),i("br"),n._v("“Ta nhìn ngươi tuổi tác không lớn, hẳn là tìm hiểu ta ‘Kiếm’ chữ thời gian không dài đi!” Kiếm lão nhân nói: “Không cao hơn mười năm... Luyện thành tam kiếm cơ hội sẽ đạt tới sáu thành trở lên, có thể hay không nói một chút... Tìm hiểu bao lâu?”"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Trương Huyền gãi đầu một cái, cẩn thận nhớ lại một chút: “Hẳn là có cái mấy... Chục giây đi!”"),i("br"),i("br"),n._v("Trên vách đá kiểu chữ, hắn chỉ nhìn lướt qua, liền thu vào đám người chú giải, cẩn thận tính toán, toàn bộ thời gian tính gộp lại, hẳn là có cái vài giây đồng hồ thời gian..."),i("br"),i("br"),n._v("Bất quá, nói thẳng ra, quá mức dọa người rồi, lời đến khóe miệng, tăng thêm gấp mười lần."),i("br"),i("br"),n._v("“Mấy chục... Giây?”"),i("br"),i("br"),n._v("Kiếm lão nhân sững sờ, đầy trời kiếm ý lay động, tựa như lúc nào cũng sẽ sụp đổ: “Ngươi nói chỉ nhìn mấy chục giây, liền nhìn ra ta lưu chữ mục đích cùng ý nghĩa?”"),i("br"),i("br"),n._v("“Đúng vậy a, ngươi lưu viết, cứ việc chỉ là một chữ, lại ẩn chứa một trăm linh bảy loại kiếm pháp, một trăm linh bảy loại khác biệt kiếm ý, nếu như đoán không sai, hẳn là lưu lại một loại kiếm trận truyền thừa! Thuận lợi tốt hơn dung hợp nhiều như vậy kiếm ý.”"),i("br"),i("br"),n._v("Trương Huyền cười cười: “Loại này tư duy, vẫn là rất dễ phỏng đoán!”"),i("br"),i("br"),n._v("“Ngươi... Mấy chục giây, cũng có thể thấy được ta lưu lại nhiều như vậy kiếm pháp?”"),i("br"),i("br"),n._v("Dường như không có theo trong lúc kinh ngạc đi ra, Kiếm lão nhân tràn đầy khó tin."),i("br"),i("br"),n._v("Một lát sau, nhớ tới cái gì, nhướng mày, lộ ra một tia không vui: “Tuổi còn nhỏ, thế mà đầu cơ trục lợi... Ngươi nói ngươi nhìn ra ta lưu chữ ý nghĩa, vậy ta liền kiểm tra một chút ngươi! Nếu như còn có thể nhìn ra, liền tin tưởng, lời nói không ngoa!”"),i("br"),i("br"),n._v("Hắn lưu lại kiếm pháp, kiếm ý, mười phần mịt mờ, coi như cùng hắn cùng cấp bậc kiếm đạo đại tông sư, lần thứ nhất tiếp xúc, cũng sẽ choáng váng, không biết rõ đến cùng chuyện gì xảy ra, trước mắt vị này nói, mấy chục giây liền nhìn ra, chỉ sợ là đầu cơ trục lợi, căn bản cũng không phải là lĩnh ngộ của mình."),i("br"),i("br"),n._v("“Kiểm tra ta? Tốt!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("“Ta hiện tại thi triển kiếm chiêu, ngươi xem một chút ẩn chứa trong đó bao nhiêu loại kiếm ý kiếm pháp!”"),i("br"),i("br"),n._v("Hừ một câu, cũng không thấy Kiếm lão nhân có động tác gì, Trương Huyền lập tức cảm thấy một cỗ sát khí tuôn ra mà tới."),i("br"),i("br"),n._v("Ngẩng đầu hướng ánh mắt của hắn nhìn lại, lập tức thấy được một mảnh kiếm khí biển, tựa như một cái thế giới mới, nước chảy biến hóa, thay đổi khôn lường, không chỗ không kiếm, khắp nơi đều kiếm."),i("br"),i("br"),n._v("“Có thể nhìn ra ta vừa rồi chiêu số, ẩn chứa bao nhiêu loại kiếm pháp?”"),i("br"),i("br"),n._v("Làm xong những này, Kiếm lão nhân lần nữa nhìn tới tới."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Trương Huyền lắc đầu: “Không nhìn ra!”"),i("br"),i("br"),n._v("“Rõ ràng như thế kiếm ý, cũng nhìn không ra, còn không phải đầu cơ trục lợi?” Kiếm lão nhân tràn đầy không vui."),i("br"),i("br"),n._v("Vừa rồi thi triển kiếm ý, so với hắn để thư lại, đơn giản nhiều, như vậy đều không có nhìn ra, đối phương nói mấy chục giây liền hiểu rõ hắn ý nghĩa, tương đương với lập tức vạch trần."),i("br"),i("br"),n._v("“Ta không nhìn ra ẩn chứa bao nhiêu loại kiếm pháp, nhưng nhìn ra trọn mười bảy cái sơ hở!”"),i("br"),i("br"),n._v("Trương Huyền cười nhạt một tiếng."),i("br"),i("br"),n._v("Hắn có khả năng lý giải “Kiếm” chữ, là bởi vì vô số tiền bối chú giải, để hắn tự mình lĩnh ngộ lời nói, không có thời gian mấy năm, khẳng định cũng làm không được... Bởi vậy, kiếm ý của đối phương lóe lên nhanh như vậy, coi như hắn đạt đến kiếm đạo chân giải, cũng là không có khả năng chỉ nhìn một cái, liền đếm được xong."),i("br"),i("br"),n._v("Bất quá... Vì sao muốn đếm?"),i("br"),i("br"),n._v("Biết thiếu hụt liền tốt."),i("br"),i("br"),n._v("“Mười bảy chỗ sơ hở? Ăn nói bừa bãi!”"),i("br"),i("br"),n._v("Kiếm lão nhân khuôn mặt biến thành màu đen."),i("br"),i("br"),n._v("“Chỗ thứ nhất, vừa rồi kiếm ý, cứ việc hùng hồn, lại mang theo hỗn tạp chi ý, nếu như không nhìn lầm, hẳn là một loại khốn người tuyệt chiêu, muốn đem người chết, rất khó! Cũng liền nói, thanh thế rất tốt, uy lực không đủ!”"),i("br"),i("br"),n._v("“Thứ hai chỗ, chiêu số bên trong, mang theo công kích nguyên thần kiếm ý, quả thực có thể để cho uy lực tăng thêm không ít, nhưng chân chính thi triển đi ra, đối tu luyện giả linh hồn cùng tâm cảnh yêu cầu rất lớn, một khi không đủ, không những không thể tiến công người khác, còn có thể sẽ bị đối thủ thừa lúc, tại chỗ đánh tan...”"),i("br"),i("br"),n._v("“Thứ ba...”"),i("br"),i("br"),n._v("“Thứ tư...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("“Thứ mười bảy, chiêu số lớn mà tán, khẽ mà không, rõ ràng một chiêu có thể giết người, lại diễn biến ra mười mấy loại biến hóa, vẽ rắn thêm chân, bất quá cũng chỉ như vậy...”"),i("br"),i("br"),n._v("Lắc đầu, Trương Huyền nhàn nhạt nhìn qua: “Không biết ta nói những này, có thể hay không chính xác?”"),i("br"),i("br"),n._v("“Ngươi, ngươi...”"),i("br"),i("br"),n._v("Đối phương vừa mới bắt đầu lúc nói, hắn còn xem thường, nương theo nói đến càng nhiều, Kiếm lão nhân sắc mặt càng trắng, nên nói đến một câu cuối cùng lúc, đã toàn thân run rẩy, hoàn toàn nói không ra lời."),i("br"),i("br"),n._v("Thân là kiếm đạo đại tông sư, bản thân thi triển kiếm chiêu, có gì vấn đề, vẫn là biết một chút... Đối phương chỉ nhìn một cái, liền tất cả đều đào ra, thậm chí nói so với hắn vị này tu luyện giả đều kỹ càng..."),i("br"),i("br"),n._v("Đến cùng làm sao làm được?"),i("br"),i("br"),n._v("“Ngộ tính như vậy, nhãn lực như thế, học tập Linh Hư tam kiếm, tất nhiên rất nhanh liền có thể thành công...”"),i("br"),i("br"),n._v("Sau khi hết khiếp sợ, là nồng đậm cuồng hỉ, Kiếm lão nhân hai mắt tỏa ánh sáng, cũng nhịn không được nữa, đang nghĩ để hắn bái sư, truyền thụ kiếm pháp, liền nghe đến đối diện thanh niên tiếng thở dài vang lên."),i("br"),i("br"),n._v("“Tùy tiện thi triển kiếm pháp, đều nhiều như vậy sơ hở, Linh Hư tam kiếm khẳng định cũng chỉ là hư danh... Được rồi, kiếm pháp chính ngươi giữ đi, ta không học!”"),i("br"),i("br"),n._v("“Hả?”"),i("br"),i("br"),n._v("Kiếm lão nhân ngốc tại chỗ."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1290"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1292"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);