(window.webpackJsonp=window.webpackJsonp||[]).push([[343],{698:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1286-nhuoc-hi-ta-đen-roi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1286-nhuoc-hi-ta-đen-roi"}},[n._v("#")]),n._v(" Chương 1286: Nhược Hi, ta đến rồi!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1286: Nhược Hi, ta đến rồi!"),t("br"),t("br"),n._v("Gió táp gào thét, Thánh thú ở trên trời phi hành, tốc độ quá nhanh, vạch ra một đạo trắng tinh sóng khí."),t("br"),t("br"),n._v("Trương Huyền ngồi tại lưng thú bên trên trong phòng, nhìn về phía thanh niên trước mắt, thoả mãn nhẹ gật đầu."),t("br"),t("br"),n._v("Thi vòng hai thành tích không tính, đám người liền tại Triệu Hưng Mặc dẫn đầu bên dưới, hướng Thánh tử điện phương hướng bay đi."),t("br"),t("br"),n._v("Một đường vô sự, đem Linh Hư tam kiếm, cùng đối kiếm đạo lý giải, kỹ càng truyền cho Trương Cửu Tiêu."),t("br"),t("br"),n._v("Không hổ là Trương gia thiên tài, năng lực học tập kinh người, ngắn ngủi thời gian nửa tháng, đối kiếm đạo lý giải, liền đạt đến nửa bước chân giải tình trạng, cứ việc cùng hắn so, còn kém rất lớn một đoạn, nhưng mà thi triển kiếm pháp, đến đây người tham gia khảo hạch bên trong, đã không người có thể địch!"),t("br"),t("br"),n._v("Không chỉ có như vậy, tu vi cũng thuận lợi đột phá Nguyên Thần cảnh đỉnh phong, loại thực lực này, phối hợp thêm kiếm ý, coi như cùng Tôn Càn, Mã Minh Hải đám người liều, đều không hề yếu."),t("br"),t("br"),n._v("Nếu như nói, trước kia Trương Cửu Tiêu, miễn cưỡng có thể thông qua Thánh tử điện khảo hạch, mà bây giờ, tất cả thiên tài bên trong, tuyệt đối có thể xếp bên trên thứ tự, được cho số một nhân vật."),t("br"),t("br"),n._v("Người thực lực, đạt tới nhất định cực hạn về sau, tiến bộ sẽ càng ngày càng chậm, thật giống như trăm mét, theo mười ba giây, tiến bộ đến mười hai giây, rất dễ dàng, nhưng theo mười giây ba, đến mười giây hai, độ khó tăng lên gấp mấy lần không thôi."),t("br"),t("br"),n._v("Trương Cửu Tiêu liền là loại tình huống này, hắn thiên phú vốn là không sai, có thuộc về mình phương pháp tu luyện, tiến bộ một điểm, đều rất khó, để hắn trong nửa tháng, có to lớn như vậy đột phá, truyền đi, tuyệt đối có thể không biết nhiều ít người dọa đến nói không ra lời."),t("br"),t("br"),n._v("Có thể nói là quỷ phủ thần công, cửu tinh danh sư mới có thủ đoạn."),t("br"),t("br"),n._v("“Hành Giả Vô Cương, rốt cục đạt tới đệ nhị trọng!”"),t("br"),t("br"),n._v("Chỉ điểm Trương Cửu Tiêu, Trương Huyền cũng không có nhàn rỗi, thời gian nửa tháng, đem sở học thân pháp, cùng Diễn Không thiên thư dung hợp lại cùng nhau, rốt cục tạo thành Hành Giả Vô Cương đệ nhị trọng Thiên Đạo công pháp, cũng thuận lợi học được."),t("br"),t("br"),n._v("Chỉ là lưng thú bên trên, không cách nào thí nghiệm, cũng không biết chiêu này, cụ thể công hiệu như thế nào."),t("br"),t("br"),n._v("Không chỉ có Hành Giả Vô Cương đạt tới đệ nhị trọng, Nguyên Thần cảnh đỉnh phong tu vi, cũng triệt để củng cố, trong cơ thể nguyên thần, tựa như lúc nào cũng sẽ nhảy ra, xung kích nửa bước Xuất Khiếu."),t("br"),t("br"),n._v("Đương nhiên, hắn tiến bộ lớn nhất, vẫn là kiếm pháp."),t("br"),t("br"),n._v("Lĩnh ngộ kiếm đạo chân giải, kết hợp Linh Hư tam kiếm bên trong kiếm chiêu, để hắn đối kiếm lĩnh ngộ, vượt xa quá những binh khí khác, không nói Linh Hư kiếm, coi như trước đó Băng Vũ kiếm trong tay hắn, cũng có thể phát huy ra vượt qua người thường uy lực!"),t("br"),t("br"),n._v("Trước kia nhiều nhất cùng Lĩnh Vực cảnh sơ kỳ, chiến cái ngang tay, hiện tại chỉ cần dùng kiếm, hoàn toàn có thể tuỳ tiện nghiền ép."),t("br"),t("br"),n._v("“Hiện tại trọng yếu nhất chính là, sưu tập càng nhiều kiếm pháp, bổ sung Linh Hư tam kiếm bên trong thiếu hụt, một khi hình thành Thiên Đạo công pháp, sức chiến đấu tất nhiên còn có thể lần nữa bạo tăng!”"),t("br"),t("br"),n._v("Than thở một tiếng, đứng dậy."),t("br"),t("br"),n._v("Linh Hư tam kiếm uy lực quả thực cường đại đến đáng sợ, nhưng thiếu hụt quá nhiều, đối với hắn loại này hoàn mỹ chủ nghĩa người mà nói, thực sự luyện không đi xuống."),t("br"),t("br"),n._v("Nhìn tới chỉ có thể tiến vào Thánh tử điện, tìm kiếm càng nhiều bí tịch, để hắn hình thành Thiên Đạo công pháp, lại cẩn thận học tập."),t("br"),t("br"),n._v("Trên đường đi hắn cùng Trương Cửu Tiêu, không ngừng tu luyện, Mã Minh Hải, Tôn Càn mấy người cũng không có nhàn rỗi, dường như nhận lấy đả kích, tu luyện càng thêm khắc khổ."),t("br"),t("br"),n._v("Mặc dù bọn hắn thực lực, không có quá đại biến hóa, khí thế cùng tâm tính lại rõ ràng so trước đó lăng lệ không ít."),t("br"),t("br"),n._v("“Ngọc không mài không nên thân, những người này, đều là một phương thế lực thiên tài, bị Trương sư đả kích một lần, biết chênh lệch, đối bọn hắn tới nói, là chuyện tốt...”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Triệu Hưng Mặc thoả mãn gật đầu."),t("br"),t("br"),n._v("Có thể bị hắn tuyển chọn, hầu như đều là một phương thế lực nhân vật thiên tài nhất, kiêu ngạo vô cùng, bị Trương Huyền mạnh mẽ đả kích, tâm lý không phục, nửa tháng đến, tất cả đều cắn răng tu luyện, tranh thủ sớm ngày đem hắn vượt qua, rửa sạch nhục nhã."),t("br"),t("br"),n._v("“Triệu sư, Thánh tử điện cuối cùng khảo hạch là cái gì? Sẽ không cùng dạng là chiến đấu a?”"),t("br"),t("br"),n._v("Trong lòng đang cảm khái, một vị danh sư hỏi lên."),t("br"),t("br"),n._v("Quay đầu nhìn lại, chính là Tôn Càn."),t("br"),t("br"),n._v("Nghe nói như thế, những người khác cũng đình chỉ tu luyện, tò mò nhìn qua."),t("br"),t("br"),n._v("Lập tức liền muốn tới Thánh tử điện, bọn họ cũng đối cái cuối cùng khảo hạch, tràn ngập tò mò."),t("br"),t("br"),n._v("“Cái khảo hạch này, so trước đó lần đầu thử nghiệm, thi vòng hai đều muốn khó khăn nhiều, cũng không phải là đơn giản chiến đấu!”"),t("br"),t("br"),n._v("Triệu Hưng Mặc chần chờ một chút, nói: “Mà là tổng hợp đánh giá!”"),t("br"),t("br"),n._v("“Tổng hợp đánh giá?”"),t("br"),t("br"),n._v("Đám người nghi ngờ."),t("br"),t("br"),n._v("“Thánh tử điện là danh sư đại lục đệ nhất học viện, muốn đi vào trong đó, thực lực, trí tuệ, đối chuyện ứng biến, lòng dạ, quyết đoán... Đều sẽ tính toán tại thành tích bên trong, độ khó so thi vòng hai lớn!”"),t("br"),t("br"),n._v("Triệu Hưng Mặc nói: “Dựa theo trước kia trải qua, không có gì bất ngờ xảy ra, hẳn là... Xông sơn môn!”"),t("br"),t("br"),n._v("“Xông sơn môn?”"),t("br"),t("br"),n._v("“Hả, sơn môn, là Danh Sư đường tiêu tốn vô số tâm huyết sắp đặt đi ra khảo hạch tràng chỗ. Đối tu luyện giả tâm tính, thực lực, đều có cực lớn khảo nghiệm, muốn trở thành Thánh tử điện một thành viên, cần tại trong vòng thời gian quy định, hoàn thành một loạt khảo hạch, thời gian càng ngắn, điểm số càng cao... Vượt qua quy định thời gian, không cách nào thông qua, chỉ có thể bị đào thải!”"),t("br"),t("br"),n._v("Vẻ mặt nghiêm túc, Triệu Hưng Mặc nói."),t("br"),t("br"),n._v("Phi hành nửa tháng, đã khoảng cách Thánh tử điện không xa, trước thời hạn nói ra quy tắc, không tính trái lệ."),t("br"),t("br"),n._v("“Hai người bọn họ, liền là xông sơn môn không có thông qua, nhưng thành tích coi như không tệ, lúc này mới có cơ hội trở thành dự thính, nếu không, liền dự thính tư cách đều không có!”"),t("br"),t("br"),n._v("Nói, Triệu Hưng Mặc chỉ hướng trước đó phụ trách lần đầu thử nghiệm hai vị thanh niên."),t("br"),t("br"),n._v("“Nói như vậy lên... Bọn họ đều là thông qua thi vòng hai thiên tài?”"),t("br"),n._v(" Đám người tất cả đều giật nảy mình."),t("br"),t("br"),n._v("Có thể xông sơn môn, nói rõ thông qua được thi vòng hai... Bọn họ cứ việc cũng có cơ hội xông sơn môn, nhưng dựa theo tình huống bình thường, hẳn là ít nhất có hơn mười sắp bị đào thải."),t("br"),t("br"),n._v("Nói cách khác, trong mọi người, còn có gần một nửa, không có thông qua thi vòng hai."),t("br"),t("br"),n._v("Chỉ là thi vòng hai xuất hiện biến cố, mới có may mắn đi theo tới."),t("br"),t("br"),n._v("“Hả, bọn họ đều thông qua được thi vòng hai, đang xông sơn môn bên trong bị đào thải!” Triệu Hưng Mặc gật đầu."),t("br"),t("br"),n._v("“Triệu sư, dựa theo kinh nghiệm của ngươi, chúng ta những người này, đại khái nhiều ít có thể vượt qua?” Mã Minh Hải nhịn không được nói."),t("br"),t("br"),n._v("“Thiên phú của các ngươi cùng thực lực, cũng không tệ, có điều, xông sơn môn cần cùng thế lực lớn thiên tài, cao thủ quyết đấu, độ khó cùng trước đó trong lồng đấu, không thể so sánh nổi, dựa theo của ta suy tính, vận khí tốt, hẳn là có mười người thông qua, vận may không tốt... Nhiều nhất một, hai cái, thậm chí toàn quân bị diệt!”"),t("br"),t("br"),n._v("Triệu Hưng Mặc nói."),t("br"),t("br"),n._v("Xông sơn môn khảo hạch, không chỉ có là cái này hơn ba mươi người nội bộ khảo hạch, trong đó còn có liên minh, Thánh Nhân quý tộc, cùng tất cả thế lực lớn vô thượng thiên tài..."),t("br"),t("br"),n._v("Thực lực là một bộ phận, vận may cũng là một bộ phận, cái này ba mươi ba người, thực lực, thiên phú cứ việc không yếu, nhưng chân chính có khả năng thông qua, nhiều nhất mười cái, vận may không tốt, khả năng một cái đều không có trúng tuyển."),t("br"),t("br"),n._v("Loại tình huống này, trước kia cũng không phải không có xuất hiện qua."),t("br"),t("br"),n._v("“Cửu Tiêu là Thánh Nhân Trương gia người, hẳn là đối loại này khảo hạch biết rất nhiều đi!”"),t("br"),t("br"),n._v("Thấy mọi người khó mà tiếp nhận, Triệu Hưng Mặc quay đầu."),t("br"),t("br"),n._v("“Đúng, quả thực rất khó, coi như Thánh Nhân Trương gia hàng năm phái ra danh ngạch, cũng đều sẽ có một nửa không cách nào thông qua...”"),t("br"),t("br"),n._v("Trương Cửu Tiêu gật đầu."),t("br"),t("br"),n._v("Mặc dù là Trương gia bàng chi, nhưng Thánh tử điện tin tức, nên cũng biết không ít, biết xông sơn môn độ khó."),t("br"),t("br"),n._v("Trương gia hạch tâm đệ tử vô số, thiên tư mạnh hơn hắn, thực lực cao hơn hắn, chỗ nào cũng có... Dù vậy, hàng năm đều có rất nhiều bị đào thải, độ khó bao lớn, có thể tưởng tượng được."),t("br"),t("br"),n._v("“Ta cùng chi mạch một vị thiên tài, Trương Vân Kỳ, năm ngoái hai mươi tám tuổi, tu vi đạt đến Xuất Khiếu cảnh sơ kỳ, vượt qua một lần lôi kiếp, loại thực lực này, tại trong chúng ta, đã không người có thể địch, đáng tiếc, đồng dạng khảo hạch thất bại, không có thông qua...”"),t("br"),t("br"),n._v("Trương Cửu Tiêu nói tiếp."),t("br"),t("br"),n._v("“Xuất Khiếu cảnh sơ kỳ, đều thất bại?”"),t("br"),t("br"),n._v("Đám người ngẩn ngơ."),t("br"),t("br"),n._v("Trong bọn họ mạnh nhất, chỉ bất quá ngụy Xuất Khiếu, đi qua lôi kiếp Xuất Khiếu cảnh cường giả đều không thông qua được, chẳng phải đại biểu bọn họ, không có người có khả năng thành công?"),t("br"),t("br"),n._v("“Đúng vậy a, có điều, cái khảo hạch này, cũng không hoàn toàn dựa theo thực lực tới xác định, Trương Vân Kỳ đệ đệ, tu vi chẳng qua Nguyên Thần cảnh hậu kỳ, đồng dạng xông sơn môn, lại thông qua được... Cụ thể khảo hạch là cái gì, tiêu chuẩn gì, ta cũng không rõ lắm!”"),t("br"),t("br"),n._v("Trương Cửu Tiêu cười khổ."),t("br"),t("br"),n._v("Trương gia rất nhiều thiên tài, hàng năm đều tới xông một lần, có chút liên tục hơn mười năm đều không có biện pháp thành công, cũng có chút, một lần liền thu được không sai thành tích... Cụ thể chuyện gì xảy ra, không tự mình trải qua, chỉ sợ căn bản không hiểu."),t("br"),t("br"),n._v("“Xông sơn môn khảo hạch đa dạng, đồng dạng cùng một chỗ khảo hạch, gặp phải tình cảnh, cũng không hoàn toàn giống nhau, chỉ có thể nhờ vào năng lực cá nhân tùy cơ ứng biến, không có cách nào huấn luyện, cũng không có cách nào trước thời hạn báo cho! Cụ thể như thế nào, liền nhìn chính các ngươi...”"),t("br"),t("br"),n._v("Triệu Hưng Mặc gật đầu."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Đám người trầm mặc."),t("br"),t("br"),n._v("Vốn nghĩ, trước thời hạn biết được, có thể mau chóng chuẩn bị, nghe được giờ mới hiểu được, không có đơn giản như vậy."),t("br"),t("br"),n._v("Cũng đúng, đường đường đại lục đệ nhất học viện, nếu như dễ dàng như vậy liền thông qua, chẳng phải đã sớm kín người hết chỗ?"),t("br"),t("br"),n._v("“Thật ra thì, mọi người cũng không cần lo lắng quá mức, tiến nhập sơn môn về sau, chỉ cần phát huy ra cao nhất thực lực cùng trình độ, coi như không cách nào thông qua, trở thành dự thính sinh, vẫn là có rất lớn cơ hội! Chỉ cần có thể dự thính, thời cơ chín muồi, đồng dạng có thể lần nữa tiến hành khảo hạch... Cũng không phải là một lần đào thải, liền lại không có cơ hội!”"),t("br"),t("br"),n._v("Thấy nói một hồi, tất cả mọi người có chút sợ hãi cùng khẩn trương, Triệu Hưng Mặc cười cười."),t("br"),t("br"),n._v("Thánh tử điện khảo hạch cứ việc rất nghiêm, nhưng cũng không phải một lần khảo hạch xong, liền lại không có cơ hội, chỉ cần thành tựu không tính quá kém, hỗn thành dự thính, hoàn toàn có thể đợi năm thứ hai một lần nữa."),t("br"),t("br"),n._v("Đương nhiên, lần thứ hai khảo hạch, khẳng định muốn so lần thứ nhất càng khó... Bất quá, vì phòng ngừa đám người nhụt chí, chỉ có thể đem loại lời này để ở trong lòng, nếu không đả kích quá ác, không còn lòng tin, thật muốn toàn quân bị diệt, hắn vị này tiếp dẫn sứ giả, trên mặt cũng không có bất luận cái gì hào quang."),t("br"),t("br"),n._v("“Không phải trực tiếp đào thải liền tốt...”"),t("br"),t("br"),n._v("Nghe được chỉ cần biểu hiện tốt một chút, còn có cơ hội, đám người tất cả đều thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Thánh tử điện đến, mọi người chuẩn bị một chút, không có gì bất ngờ xảy ra, xông sơn môn khảo hạch, hẳn là sẽ trong vòng ba ngày tiến hành!”"),t("br"),t("br"),n._v("Lại trò chuyện một hồi, Triệu Hưng Mặc đột nhiên chỉ về phía trước."),t("br"),t("br"),n._v("Đám người vội vàng nhìn lại, lập tức nhìn thấy một tòa cự đại thành thị, đứng sừng sững ở bên trong dãy núi, bốn phía dường như có to lớn Tụ Linh trận, đem phạm vi mấy chục vạn dặm linh khí, tụ lại, còn chưa tới đến trước mặt, liền cảm thấy khí tức bức người, để cho người ta sảng khoái tinh thần."),t("br"),t("br"),n._v("“Đây là Thánh tử điện?”"),t("br"),t("br"),n._v("Đột nhiên đứng dậy, Trương Huyền ống tay áo hạ nắm đấm nhịn không được xiết chặt: “Nhược Hi... Ta đến rồi!”"),t("br"),t("br"),n._v("(Cảm ơn bạn đọc [Nickkkl 】 sáu vạn khen thưởng. Đến Thánh tử điện, một sóng lớn cao trào, sắp đến, nguyệt phiếu đâu?)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1294"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1296"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);