(window.webpackJsonp=window.webpackJsonp||[]).push([[345],{700:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1288-truong-khiem"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1288-truong-khiem"}},[n._v("#")]),n._v(" Chương 1288: Trương Khiêm")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1288: Trương Khiêm"),t("br"),t("br"),n._v("Cách đó không xa, đứng mấy cái tuổi trẻ nữ tử, tất cả đều một bộ áo trắng, cùng Băng Nguyên cung người, hoàn toàn tương đồng."),t("br"),t("br"),n._v("Triệu Nhã đi rồi, một mực không có tin tức, không biết đến cùng thế nào, đã gặp phải các nàng, vừa vặn có thể hỏi một chút."),t("br"),t("br"),n._v("Có thể bị tuyển ra, tham gia Thánh tử điện khảo hạch, mấy người kia bất kể địa vị vẫn là thân phận, hẳn là cũng sẽ không quá yếu, có lẽ có khả năng biết Triệu Nhã tình huống."),t("br"),t("br"),n._v("“Triệu sư, ta qua bên kia nhìn một chút!”"),t("br"),t("br"),n._v("Nói một tiếng, Trương Huyền nhấc chân đã sắp qua đi."),t("br"),t("br"),n._v("“Bên kia?”"),t("br"),t("br"),n._v("Triệu Hưng Mặc nhìn một cái: “Đó là Băng Nguyên cung người, tốt nhất đừng trêu chọc!”"),t("br"),t("br"),n._v("“Ồ?” Trương Huyền nghi ngờ: “Băng Nguyên cung người, không nói đạo lý?”"),t("br"),t("br"),n._v("“Thế thì không có, Băng Nguyên cung người, hầu như đều là nữ tử, hơn nữa từng cái dung mạo bất phàm, bởi vậy, tại Thánh tử điện rất được hoan nghênh, không ít người đều lấy có khả năng cưới làm vợ mà tự hào, mỗi ngày đều sẽ có không ít người, tiến đến bắt chuyện... Tùy tiện tiến đến, ta sợ sẽ gặp phải mặt đen, rất dễ dàng chịu thiệt!”"),t("br"),t("br"),n._v("Triệu Hưng Mặc lắc đầu."),t("br"),t("br"),n._v("Trước mắt vị này Trương Huyền, thiên phú rất tốt, nhãn lực, kiến thức cũng còn không sai, nhưng... Cũng quá sắc đi!"),t("br"),t("br"),n._v("Vừa thấy mặt, liền hỏi thăm Thánh tử điện đệ nhất mỹ nhân, Lạc gia tiểu công chúa, hiện tại nhìn thấy Băng Nguyên cung người, lại đi không được..."),t("br"),t("br"),n._v("Thánh tử điện, cũng không phải Thanh Nguyên đế quốc loại địa phương kia, coi như ngươi thiên phú không yếu, thực lực cũng không tệ, vừa thấy được nữ tử liền như thế... Khẳng định muốn thua thiệt!"),t("br"),t("br"),n._v("“Ây... Ta là hướng các nàng hỏi thăm sự tình, cũng không phải là bắt chuyện...”"),t("br"),t("br"),n._v("Nghe nói như thế, thấy hắn biểu lộ, Trương Huyền nơi nào không hiểu vị này Triệu sư trong lòng nghĩ như thế nào, trên đầu tràn đầy hắc tuyến."),t("br"),t("br"),n._v("Hắn từ trước tới nay gò bó theo khuôn phép, ngay thẳng vô tư, chưa bao giờ có vượt rào hành động, thế mà bị người muốn trở thành nôn nóng... Cũng là say."),t("br"),t("br"),n._v("“Ta chỉ là khuyên ngươi cẩn thận một chút... Có thể đi vào Thánh tử điện, không có một cái nào người yếu, có thể khiêm tốn tận lực khiêm tốn, tuyệt đối không nên gây chuyện!” Triệu Hưng Mặc dặn dò."),t("br"),t("br"),n._v("Lần đầu thử nghiệm khảo hạch thất bại, mang theo hai người đi vào thi vòng hai, thi vòng hai khảo hạch thất bại, tất cả mọi người đều mang tới... Tất cả đều bởi vì cái tên này."),t("br"),t("br"),n._v("Hiện tại vừa đến nơi này, liền muốn đi tìm Băng Nguyên cung người, ngươi có thể hay không bớt lo một chút?"),t("br"),t("br"),n._v("“Yên tâm đi, ta từ trước tới nay khiêm tốn, sẽ không gây chuyện!”"),t("br"),t("br"),n._v("Cười cười, Trương Huyền bảo đảm."),t("br"),t("br"),n._v("Lần này đi qua, chỉ là hỏi thăm một số việc mà thôi, chắc chắn sẽ không xảy ra vấn đề."),t("br"),t("br"),n._v("“Biết liền tốt, ta chỉ là các ngươi tiếp dẫn sứ giả, đi tới Thánh tử điện về sau, bất kể xảy ra chuyện gì, đều mặc kệ chuyện của ta, nhưng vẫn là muốn xin khuyên một câu... Băng Nguyên cung là không kém gì Trương gia, Lạc gia siêu cường tồn tại, cho dù những năm này không có cung chủ tọa trấn, có chút suy bại, nhưng cũng không thể khinh thường.”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Triệu Hưng Mặc nhắc nhở."),t("br"),t("br"),n._v("Hắn chỉ phụ trách đem nhân tuyển rút ra, cũng đưa đến nơi này, còn có thể hay không thông qua khảo hạch, chọc chuyện gì, cơ bản cùng hắn liên quan đến không lớn."),t("br"),t("br"),n._v("Bất quá, mọi người ở chung được lâu như vậy, cũng coi như có tình cảm, vẫn là không hy vọng vị này gây ra tai họa."),t("br"),t("br"),n._v("Nhất là Băng Nguyên cung loại này thế lực lớn."),t("br"),t("br"),n._v("“Ta hiểu!”"),t("br"),t("br"),n._v("Biết đối phương là hảo ý, Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Hả!”"),t("br"),t("br"),n._v("Khoát tay áo, Triệu Hưng Mặc không nói thêm lời."),t("br"),t("br"),n._v("Nhấc chân đi đến, thời gian không dài, đi tới trước mặt, Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Xin hỏi mấy vị cô nương, thế nhưng là Băng Nguyên cung người?”"),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Xoay đầu lại, mấy vị nữ tử, trên mặt không có bất kỳ cái gì biểu lộ, cho người ta một loại từ chối chi ngàn dặm hương vị."),t("br"),t("br"),n._v("Băng Nguyên cung, tu luyện thuộc tính âm hàn công pháp, bản thân cũng có chút lạnh lùng."),t("br"),t("br"),n._v("“Tại hạ Thanh Nguyên đế quốc tới tu luyện giả, muốn hướng mấy vị cô nương hỏi thăm một chút tin tức...” Trương Huyền tràn đầy thành khẩn."),t("br"),t("br"),n._v("“Bắt chuyện dùng một bộ này, có chút bài cũ đi!”"),t("br"),t("br"),n._v("Một nữ tử cười lạnh, trong mắt mang theo xem thường."),t("br"),t("br"),n._v("Băng Nguyên cung, là đại lục đứng đầu nhất thế lực, các nàng lại tất cả đều quốc sắc thiên hương, thiên phú bất phàm, hàng năm đến đây bắt chuyện người, không biết bao nhiêu, đủ loại thủ đoạn cũng thấy cũng nhiều, trước mắt vị này, nói thẳng hỏi thăm tin tức... Đã là nhiều năm trước bài cũ đường."),t("br"),t("br"),n._v("Hoàn toàn không có cái mới xuất hiện cảm giác."),t("br"),t("br"),n._v("“Cô nương hiểu lầm, tại hạ có một vị người quen, đi vào Băng Nguyên cung, một mực không có tin tức, nhìn thấy các ngươi, lúc này mới muốn tới đây hỏi thăm một chút!”"),t("br"),t("br"),n._v("Thấy bị hiểu lầm, Trương Huyền cười khổ lắc đầu, nói rõ mục đích đến."),t("br"),t("br"),n._v("“Người quen? Ngươi sẽ không nói, là chúng ta trong đó nào đó một vị đi!”"),t("br"),t("br"),n._v("Mới vừa nói chuyện nữ tử, giễu cợt hương vị càng đậm."),t("br"),t("br"),n._v("Loại thủ đoạn này, đã gặp quá nhiều."),t("br"),t("br"),n._v("Không nói cái khác, liền các nàng đi tới quảng trường này, không đến một ngày công phu, liền có bốn, năm làn sóng người, dùng loại thủ đoạn này làm quen."),t("br"),t("br"),n._v("Cái gì tiểu tỷ tỷ, chúng ta kiếp trước gặp qua, ta biết ngươi... Ngươi trong mắt ta, ta nghĩ đi vào trong lòng ngươi... Loại hình sáo lộ, đã sớm nghe lỗ tai đều ra vết chai."),t("br"),t("br"),n._v("“Dĩ nhiên không phải, ta muốn hỏi chính là một cái gọi Triệu Nhã người, tuổi không lớn lắm, là cái tiểu nha đầu, bị một vị gọi Liễu Huyên người mang đi...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Láo xược, thiếu cung chủ tục danh cũng là ngươi tùy tiện gọi?”"),t("br"),t("br"),n._v("Mới vừa nói chuyện nữ tử, con mắt lập tức híp lại, khí thế hùng hổ, vẻ mặt tức giận."),t("br"),n._v(" “Thiếu cung chủ?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Bớt ở chỗ này giả vờ giả vịt!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, trên người nữ tử tản mát ra mãnh liệt khí tức, tựa như lúc nào cũng sẽ động thủ: “Lập tức rời đi, ta có thể không so đo, nếu không, đừng trách ta không khách khí...”"),t("br"),t("br"),n._v("Một phóng thích sức mạnh, tu vi lập tức hiển lộ ra, nữ tử này, tuổi không lớn lắm, lại đã đạt tới Xuất Khiếu cảnh đỉnh phong, tại quảng trường trong mọi người, đều xem như cao thủ, cực kỳ không kém."),t("br"),t("br"),n._v("“Đừng kích động, ta thật chỉ muốn hỏi một chút...” Trương Huyền lắc đầu, muốn giải thích cặn kẽ, đối diện nữ tử cũng lại kìm nén không được, tay ngọc khẽ đảo, bỗng nhiên đập tới."),t("br"),t("br"),n._v("Nàng chưởng lực băng hàn, còn chưa tới đến trước mặt, liền cho người ta một loại đông tận xương tuỷ cảm giác, tựa hồ muốn hết thảy chung quanh, đóng băng lên."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Không nghĩ tới nghiêm túc hỏi thăm, dẫn đến đối phương ra tay, Trương Huyền vẻ mặt bất đắc dĩ, chập ngón tay làm kiếm, đang nghĩ dạy bảo đối phương một chầu, để các nàng trả lời vấn đề, chỉ thấy trước mắt thân ảnh nhoáng một cái, đầy trời sức mạnh tiêu tán, Triệu Hưng Mặc xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Băng Nguyên cung bằng hữu, tại hạ Thánh tử điện tiếp dẫn sứ giả Triệu Hưng Mặc, hắn là ta mang tới người, như có đắc tội, mong được tha thứ...”"),t("br"),t("br"),n._v("Mới vừa cùng mình bảo đảm, không đi gây chuyện, chưa nói vài câu liền đánh nhau, cũng nhịn không được nữa, đi tới trước mặt tiến hành khuyên can."),t("br"),t("br"),n._v("“Triệu sư xuất mặt, mặt mũi tự nhiên muốn cho.”"),t("br"),t("br"),n._v("Thấy một vị tiếp dẫn sứ giả ra tay, nữ tử ngừng lại, lạnh lùng nhìn Trương Huyền một cái, hất lên ống tay áo, không tiếp tục để ý."),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Thở phào nhẹ nhõm, Triệu Hưng Mặc xoay người nhìn về phía Trương Huyền, nhướng mày: “Trở về đi!”"),t("br"),t("br"),n._v("“Ta là thật có chuyện xảy ra hỏi thăm...” Trương Huyền vò đầu."),t("br"),t("br"),n._v("Hắn chỉ muốn biết Triệu Nhã qua được không mà thôi, đối phương không những không nói, còn muốn động thủ, đây là cái gì đạo lý?"),t("br"),t("br"),n._v("“Mới mở miệng liền hỏi người ta thiếu cung chủ, hơn nữa gọi đối phương tiểu nha đầu... Ngươi đây coi là khiêu khích đi!”"),t("br"),t("br"),n._v("Triệu Hưng Mặc lông mày nhảy một cái."),t("br"),t("br"),n._v("Hắn thực lực kinh người, vừa rồi Trương Huyền đối thoại lại không cố ý che lấp, tất cả đều nghe lọt vào trong tai."),t("br"),t("br"),n._v("“Ta...” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Triệu Nhã là học sinh của hắn, gọi “Tiểu nha đầu” loại hình không có cái gì vấn đề, bất quá... Dựa theo đối phương thuyết pháp, là Băng Nguyên cung thiếu cung chủ lời nói, xưng hô như vậy, quả thực có chút thiếu thỏa đáng."),t("br"),t("br"),n._v("Băng Nguyên cung, là có thể so với Trương gia thế lực lớn siêu cấp, tại toàn bộ đại lục đều tên tuổi lẫy lừng, thiếu cung chủ địa vị, so với cửu tinh danh sư, đều không hề yếu... Bản thân một cái chừng hai mươi tuổi gia hỏa, xưng hô như vậy, khó trách đối phương tức giận."),t("br"),t("br"),n._v("Thật giống như gọi thẳng Hoàng đế bệ hạ họ tên, hơn nữa xưng hô hắn là tiểu gia hỏa đồng dạng."),t("br"),t("br"),n._v("“Tốt, trở về đi... Băng Nguyên cung vị này thiếu cung chủ, ta nghe nói qua, Thuần Âm thể chất, mấy tháng trước mới tìm trở về, nghe nói dung mạo so với Lạc gia tiểu công chúa, đều không kém chút nào...”"),t("br"),t("br"),n._v("Nói một tiếng, Triệu Hưng Mặc tràn đầy im lặng nhìn về phía thanh niên trước mắt."),t("br"),t("br"),n._v("Cái tên này đã không phải là “Sắc”, mà là sắc đảm bao thiên!"),t("br"),t("br"),n._v("Không có tới Thánh tử viện, liền hỏi thăm Lạc gia tiểu công chúa, hiện tại nhìn thấy Băng Nguyên cung người, lập tức đụng lên tới hỏi thăm người gia thiếu cung chủ..."),t("br"),t("br"),n._v("Ngươi có phải hay không nghe được cô gái xinh đẹp liền đi không được đường?"),t("br"),t("br"),n._v("Hai cái này, mặc dù tuổi trẻ, lại mỗi một cái đều là dậm chân một cái đại lục đều chấn động siêu cấp nhân vật, ngươi đến cùng mấy cái lá gan, để ý các nàng..."),t("br"),t("br"),n._v("“Triệu Nhã là của ta... Cùng ta liên quan đến không ít, ta thật chỉ là muốn nghe được một chút nàng tin tức xác thật...”"),t("br"),t("br"),n._v("Chỉ gọi tiểu nha đầu, đối phương thiếu chút nữa bạo, nói là đồ đệ của mình, còn không nháo đến long trời lở đất?"),t("br"),t("br"),n._v("Suy nghĩ một chút, Trương Huyền đành phải đổi giọng."),t("br"),t("br"),n._v("“Liên quan đến không ít? Ta Băng Nguyên cung thiếu cung chủ, băng thanh ngọc khiết, há lại cho như ngươi vậy sỉ nhục? Triệu sư, còn xin tránh ra, ta muốn giáo huấn một chút cái miệng này không che đậy gia hỏa, hi vọng không muốn ngăn cản!”"),t("br"),t("br"),n._v("Lời mới vừa nói nữ tử, nghe nói như thế, suýt chút nữa tức điên, đôi mi thanh tú nâng lên, cũng lại kìm nén không được."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Triệu Hưng Mặc phát điên."),t("br"),t("br"),n._v("Trước đó nói Lạc gia tiểu công chúa thời điểm, cái tên này liền nói nhận biết, trong mắt mang theo đặc thù ánh sáng, bây giờ nói Băng Nguyên cung thiếu cung chủ, lại được xưng liên quan đến không ít... Ngươi cho rằng ngươi là ai?"),t("br"),t("br"),n._v("Trương gia vị kia thiên tài thiếu chủ ư?"),t("br"),t("br"),n._v("Thật sự cho rằng, người người đều biết ngươi?"),t("br"),t("br"),n._v("“Trong lúc này khẳng định có hiểu lầm...” Cố nén tức giận, Triệu Hưng Mặc cắn răng, vẫn là quyết định điều hòa một chút liên quan đến."),t("br"),t("br"),n._v("Trước mắt vị này, hắn thấy, thế nhưng là trăm phần trăm có khả năng thông qua khảo hạch, ngàn vạn không thể lại không có trước khảo hạch, liền bị người đả thương."),t("br"),t("br"),n._v("“Không có gì hiểu lầm, chúng ta Băng Nguyên cung, cho dù những năm này thâm cư không ra ngoài, nhưng cũng không sợ phiền phức! Sỉ nhục ta thiếu cung chủ, phá hư nàng thanh danh, không dạy dỗ một chầu, ta Băng Nguyên cung uy hiếp ở đâu?”"),t("br"),t("br"),n._v("Nữ tử hừ lạnh, hai bước đi tới Trương Huyền trước mặt: “Bằng hữu, ngươi dám ăn nói bừa bãi, tổng sẽ không một mực trốn ở Triệu sư sau lưng, không dám nghênh chiến a?”"),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Thấy đối phương khí thế hùng hổ, chỉ đích danh muốn động thủ, Trương Huyền đành phải lắc đầu: “Có phải hay không đưa ngươi đánh bại, ngươi liền sẽ nói cho ta biết Triệu Nhã tình hình hiện tại!”"),t("br"),t("br"),n._v("“Tự tìm cái chết...”"),t("br"),t("br"),n._v("Thấy hắn vẫn như cũ mở miệng “Triệu Nhã”, gọi thẳng thiếu cung chủ tục danh, nữ tử sắc mặt tái xanh, gào lên một tiếng liền muốn ra tay."),t("br"),t("br"),n._v("“Vị tiên tử này không cần thiết tức giận, loại này không biết trời cao đất rộng gia hỏa, giao cho tiểu sinh là có thể, chớ nên dơ bẩn bàn tay của ngươi!”"),t("br"),t("br"),n._v("Còn không có ra tay, một cái thanh âm nhàn nhạt vang lên, ngay sau đó liền thấy một cái cầm trong tay quạt xếp thanh niên áo trắng, chậm rãi đi tới trước mặt."),t("br"),t("br"),n._v("Cổ tay rung lên, quạt xếp thu về, thanh niên trên thân mang theo tiêu sái xuất trần hương vị, nhẹ nhàng cười một tiếng, tràn đầy u nhã."),t("br"),t("br"),n._v("“Trương gia Trương Khiêm gặp qua Băng Nguyên cung mấy vị tiên tử, tiểu sinh cái này xin kính chào!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1296"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1298"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);