(window.webpackJsonp=window.webpackJsonp||[]).push([[35],{390:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1008-nguy-nhu-yen-phuong-phap-tri-lieu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1008-nguy-nhu-yen-phuong-phap-tri-lieu"}},[n._v("#")]),n._v(" Chương 1008: Ngụy Như Yên phương pháp trị liệu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1008: Ngụy Như Yên phương pháp trị liệu"),t("br"),t("br"),n._v("Tại tàng thư điện liên tục nhìn sáu tầng, đều không có tìm tới phương pháp giải quyết, căn cứ thư viện giới thiệu, hiểu vị này là tổng bộ tới Thất Tinh độc sư, kiến thức rộng rãi, có lẽ có thể biết một chút bí ẩn."),t("br"),t("br"),n._v("Lại nói, linh hồn sống lâu như vậy, mất đi ký ức cũng là rất bình thường, không nói cái khác, Ngoan Nhân chính là như vậy."),t("br"),t("br"),n._v("Nói ra càng thêm chân thực, làm cho đối phương lại càng dễ tin tưởng."),t("br"),t("br"),n._v("Quả nhiên, nghe nói như thế, Bạch độc sư có một loại bừng tỉnh hiểu ra cảm giác, nhẹ gật đầu, suy tư một lát, nói: “Tiên Thiên độc thể, không khống chế được trong cơ thể năng lượng, hạ xuống tai nạn chuyện, ta cũng đã được nghe nói... Chẳng qua cụ thể như thế nào chặn chế, không rõ lắm, ngược lại là Hải độc sư xem qua liên quan thư tịch, có lẽ có thể biết!”"),t("br"),t("br"),n._v("“Hải độc sư?” Trương Huyền sững sờ, chẳng lẽ tổng bộ không chỉ tới hắn một cái?"),t("br"),t("br"),n._v("Thư viện tiếp xúc ai, chỉ có thể nhìn ra ai thiếu hụt cùng sơ hở, Hải độc sư cũng không đi vào tàng thư điện, hắn không hề hiểu rõ tình hình."),t("br"),t("br"),n._v("“Hải độc sư cùng ta cùng đi đến nơi đây, cũng là Thất Tinh đỉnh phong độc sư, xem qua rất nhiều liên quan tới tổ sư ngươi thư tịch, có lẽ hẳn là có thể đủ biết đến càng nhiều hơn một chút!”"),t("br"),t("br"),n._v("Bạch độc sư giải thích nói."),t("br"),t("br"),n._v("“Cùng ngươi cùng một chỗ theo tổng bộ tới, tổng cộng có mấy vị?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("“Tổng cộng cũng chỉ có hai chúng ta!” Bạch độc sư không dám giấu diếm."),t("br"),t("br"),n._v("“Hai vị?” Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Chỉ là hai vị lời nói, vẫn còn dễ làm, đã lừa dối một cái, một cái khác tiến hành chỉ điểm, nên có thể tin tưởng."),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền lần nữa lấy ra một bình rượu ngon, đưa tới, nói: “Ngươi để hắn tới, còn rượu này, liền nói là lão tổ ta mời hắn!”"),t("br"),t("br"),n._v("“Mời hắn uống rượu?”"),t("br"),t("br"),n._v("Bạch độc sư kỳ quái."),t("br"),t("br"),n._v("“Không sai, trong rượu này ẩn chứa đặc thù thuốc, có thể đối với các ngươi tu vi có tác dụng cực lớn.” Nhẹ gật đầu, Trương Huyền một bộ cao nhân thương cảm hậu bối bộ dáng."),t("br"),t("br"),n._v("“Đối tu vi hữu dụng, ta làm sao...”"),t("br"),t("br"),n._v("Bạch độc sư sững sờ."),t("br"),t("br"),n._v("“Là không phải cảm thấy rượu này ngươi cũng uống, không có gì trợ giúp?”"),t("br"),t("br"),n._v("Dường như nhìn ra trong lòng của hắn ý nghĩ, Trương Huyền nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Bạch độc sư mặt mũi xấu hổ."),t("br"),t("br"),n._v("Đối phương nói không sai, vừa rồi chính là nghĩ như vậy."),t("br"),t("br"),n._v("Rượu hắn cũng uống, cũng không có cảm giác đến đối tu vi có thập trợ giúp, chỉ là thân là hậu bối, chất vấn lời nói không tiện nói ra miệng thôi."),t("br"),t("br"),n._v("“Bất luận cái gì thuốc đều có nhất định liều lượng, ngươi uống hai ba ngụm, hiệu quả quá mức bé nhỏ, uống nhiều mấy ngụm, hiệu quả tự nhiên là hiểu!”"),t("br"),t("br"),n._v("Cười cười, Trương Huyền một bộ cao thâm khó dò hình ảnh."),t("br"),t("br"),n._v("“Uống nhiều một chút?”"),t("br"),t("br"),n._v("Bạch độc sư nhẹ gật đầu."),t("br"),t("br"),n._v("Cũng đúng, chỉ cần là thuốc, đều là có liều lượng, uống một hai ngụm, hiệu quả xác thực không rõ ràng."),t("br"),t("br"),n._v("Nghĩ đến cái này không có quá nhiều do dự, đem vừa rồi cho hắn hồ lô lần nữa giơ lên, “Ừng ực! Ừng ực!” Uống liền mười mấy miệng."),t("br"),t("br"),n._v("Tu vi đạt tới loại cảnh giới này, không muốn say lời nói, bình thường rượu, đã rất khó có cái gì hiệu quả."),t("br"),t("br"),n._v("Uống rượu xong, không có suy nghĩ nhiều, Bạch độc sư nhắm mắt lại tinh tế cảm thụ, quả nhiên cảm thấy một tia dòng nước ấm theo ổ bụng bên trong chậm rãi tuôn ra, hướng mình nguyên môn phóng đi."),t("br"),t("br"),n._v("Nguyên môn đóng chặt, đã không biết mấy trăm năm, dùng qua không biết bao nhiêu loại phương pháp, toàn bộ không công mà lui, giờ phút này, đạo này dòng nước ấm tuôn đi qua, phối hợp trong cơ thể hắn lực lượng, nhẹ nhàng va chạm, cái trước thế mà phát ra “Kẹt kẹt!” Thanh âm, lắc lư một cái."),t("br"),t("br"),n._v("“Cái này...” Kích động suýt chút nữa nhảy lên, Bạch độc sư hô hấp dồn dập."),t("br"),t("br"),n._v("Tu vi đạt tới hắn loại cảnh giới này, biết lay động một chút đại biểu ý nghĩa."),t("br"),t("br"),n._v("Xuất hiện lay động, đã nói lên đã có dãn ra, về sau chỉ cần cố gắng, đem hắn giải khai, cũng chưa biết chừng!"),t("br"),t("br"),n._v("Tu luyện nhiều năm như vậy cũng không làm được, uống một hồ lô rượu ngon, tựa hồ thấy được ánh sáng lúc rạng đông, rượu này công hiệu thật là quá lớn."),t("br"),t("br"),n._v("“Nhưng có hiệu quả?” Nhẹ nhàng cười một tiếng, Trương Huyền mây trôi nước chảy, sâu không lường được."),t("br"),t("br"),n._v("“Đa tạ tổ sư!”"),t("br"),t("br"),n._v("Tự mình cảm thụ, lại đối rượu ngon không có chút nào hoài nghi, Bạch độc sư bội phục đầu rạp xuống đất."),t("br"),t("br"),n._v("“Đi xuống đi, ta hiện tại muốn đi tầng thứ bảy, để vị kia Hải độc sư uống rượu xong, tới tìm ta liền có thể.”"),t("br"),t("br"),n._v("//ngantruyen.com/"),t("br"),n._v("Thấy đối phương tin tưởng không nghi ngờ, không nói thêm lời, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Vâng!” Vội vàng gật đầu, Bạch độc sư tràn đầy hưng phấn đi ra ngoài."),t("br"),t("br"),n._v("Đối với hắn mà nói, lần này đi lên, thu hoạch cực lớn, không chỉ xác định vị này Tôn Cường chân chính thân phận, càng là lại cháy lên tự tin, nhận được tổ sư thuốc, đối tu luyện về sau có trợ giúp thật lớn."),t("br"),t("br"),n._v("Thấy hắn rời khỏi, Trương Huyền lúc này mới mở mắt ra, đứng lên."),t("br"),t("br"),n._v("Lừa dối một vị Thất Tinh đỉnh phong cấp bậc độc sư, cũng không dễ dàng, may mắn chuyện tiến triển thuận lợi."),t("br"),t("br"),n._v("Đương nhiên, cũng không phải là đối phương quá ngu, mà là Thiên Đạo thư viện cùng Thiên Đạo chân khí năng lực quá mức nghịch thiên, để cho người ta khó lòng phòng bị, căn bản nghĩ không ra."),t("br"),t("br"),n._v("Lại thêm hắn cùng nhau đi tới, tinh xảo thâm hậu ngụy trang, liền thất tinh danh sư cũng nhìn không ra, lừa dối một cái Thất Tinh độc sư, không tính là gì!"),t("br"),n._v("“Nhanh lên đi...”"),t("br"),t("br"),n._v("Biết làm cho đối phương uống hết rượu ngon đi lên nữa, còn cần một đoạn thời gian, Trương Huyền không có quá mức chậm trễ, nhấc chân hướng tầng thứ bảy đi tới."),t("br"),t("br"),n._v("Tầng thứ bảy thư tịch số lượng càng ít, chỉ có mấy ngàn bản, liếc mắt qua, liền thu sạch vào trong óc."),t("br"),t("br"),n._v("Ngồi dưới đất, đem vừa rồi nhìn thấy tất cả thư tịch toàn bộ lật xem một lần, thời gian không dài, lắc đầu, trên mặt lộ ra vẻ thất vọng."),t("br"),t("br"),n._v("Hồng Viễn Độc điện liên quan tới độc thư tịch rất nhiều, ghi chép Tiên Thiên độc thể, cũng có mười mấy bản, nhưng mà cơ bản đều nói chi không rõ, càng nhiều hơn chính là tại tán dương loại thể chất này mạnh mẽ, cũng không có liên quan tới như thế nào khống chế độc tính, không toả ra phương pháp."),t("br"),t("br"),n._v("Nói cách khác, nhìn nhiều sách như vậy, vẫn không có tìm tới giải quyết Ngụy Như Yên hiện nay tình huống phương pháp."),t("br"),t("br"),n._v("“Nhìn tới trực tiếp ghi lại phương pháp, khẳng định là không có, chỉ có thể dựa vào bản thân nghiên cứu...”"),t("br"),t("br"),n._v("Mặc dù thất vọng, nhưng cũng không nhụt chí."),t("br"),t("br"),n._v("Nhìn nhiều như vậy thư tịch, chỉ cần toàn bộ học tập xong, đối dùng độc lý giải, tất nhiên có thể đạt tới Thất Tinh đỉnh phong, so trước đó Bạch độc sư đều cường đại hơn."),t("br"),t("br"),n._v("Nắm giữ loại năng lực này, coi như không có ghi chép, cũng có thể tự mình tìm tòi."),t("br"),t("br"),n._v("Đương nhiên, hắn hiện tại chỉ có nhị tinh độc sư trình độ, học tập đến Thất Tinh, đem tất cả tri thức dung hội quán thông, khả năng cần tiêu tốn mấy canh giờ thời gian, trước đó, vẫn là trước nghe một chút Hải độc sư phương pháp lại nói."),t("br"),t("br"),n._v("Ngồi tại nguyên chỗ, thời gian không dài, Bạch độc sư lần nữa đi tới, sau lưng quả nhiên đi theo một cái lão giả."),t("br"),t("br"),n._v("Lão giả này so Bạch độc sư còn cao lớn hơn, khí tức bên trên không hề yếu, cũng là một vị đạt đến Thánh vực tứ trọng đỉnh phong siêu cấp cường giả."),t("br"),t("br"),n._v("Hắn giờ phút này, hẳn là nghe Bạch độc sư lời nói, không có quá nhiều hoài nghi, nhìn qua ánh mắt bên trong, mang theo kính sợ."),t("br"),t("br"),n._v("Nhìn tới hắn đối Bạch độc sư phán đoán, mười phần tin phục, như vậy tốt nhất, cũng không cần chậm trễ nữa công phu, chuyên môn dẫn phát Thiên Đạo chân khí, lừa dối đối phương trúng độc."),t("br"),t("br"),n._v("“Tiên Thiên độc thể sẽ phóng thích tai nạn, ta trước đó liền nghe nói qua, cũng loáng thoáng tại một quyển sách bên trên, thấy qua phương pháp giải quyết!”"),t("br"),t("br"),n._v("Đi tới trước mặt, Hải độc sư không có quá nhiều nói nhảm, khom người đến cùng, suy nghĩ một chút, nói: “Hình như cần rất nhiều độc sư liên thủ, bố trí ra trận pháp đặc biệt, đem Tiên Thiên độc thể phát ra kịch độc, chuyển hóa thành tấn thăng lực lượng... Một khi thành công, không chỉ có thể giải quyết tai nạn thể chất, còn có thể để tu vi tăng nhiều!”"),t("br"),t("br"),n._v("“Đặc thù trận pháp?”"),t("br"),t("br"),n._v("Trương Huyền cau mày: “Ngươi có biết là trận pháp gì?”"),t("br"),t("br"),n._v("“Ta không rõ lắm, có điều, người của tổng bộ khẳng định biết, hơn nữa khả năng cần bát tinh độc sư, thậm chí cửu tinh độc sư phối hợp mới có thể làm đến!”"),t("br"),t("br"),n._v("Hải độc sư cười khổ nói."),t("br"),t("br"),n._v("“Bát tinh, cửu tinh phối hợp...”"),t("br"),t("br"),n._v("Vẻ mặt trở nên khó coi, Trương Huyền biến ảo không ngừng."),t("br"),t("br"),n._v("Không nói trước loại cấp bậc này độc sư, tìm tới rất khó, coi như tìm tới, lấy thực lực của hắn, khẳng định cũng lừa dối không được ah!"),t("br"),t("br"),n._v("Cái gì khai phái Tiên Tổ loại hình, đều là nói bậy, mượn nhờ thư viện thiếu hụt, để Bạch độc sư, Hải độc sư tin tưởng ngược lại cũng thôi, muốn lừa gạt loại này cường giả, coi như hắn ngụy trang giống như cũng tuyệt đối vô dụng!"),t("br"),t("br"),n._v("“Vâng!” Hải độc sư gật đầu: “Chỉ có loại cấp bậc này độc sư, mới có thể đem Tiên Thiên độc thể toả ra khí độc, tụ lại, phối hợp đủ loại độc vật, phát huy càng đại công hơn hiệu, nhanh chóng tăng lên độc thể chủ nhân thực lực! Một khi thành công, đột phá như là uống nước, tổ sư trở lại đỉnh phong, hẳn là không bao lâu!”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Mặc dù còn không có học tập nhị tinh sau này dùng độc phương pháp, nhưng nhìn nhiều như vậy, cũng đối Tiên Thiên độc thể hiểu rõ càng nhiều."),t("br"),t("br"),n._v("Loại thể chất này, nuốt độc dược càng nhiều, tai nạn lực lượng liền càng không khống chế được, nếu có đối ứng độc sư, ở chung quanh chăm sóc, đem khí độc hội tụ, cũng phối hợp thuốc tiến hành tinh luyện lời nói, tốc độ tiến bộ sẽ hết sức kinh người!"),t("br"),t("br"),n._v("“Tổ sư... Định dùng thân thể này, khôi phục thực lực?”"),t("br"),t("br"),n._v("Chần chờ một chút, Hải độc sư nhìn lại."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Nghe hắn giọng nói có chút kỳ lạ, Trương Huyền cau mày."),t("br"),t("br"),n._v("“Không có gì... Chỉ là, Tiên Tổ không phải thân nữ nhi ư? Làm sao dùng nam tử thân thể...” Hải độc sư tràn đầy xấu hổ."),t("br"),t("br"),n._v("“Thân nữ nhi?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Hắn biết Độc điện khai phái tổ sư cũng là Tiên Thiên độc thể, nhưng đến cùng là nam hay là nữ, không hề rõ ràng, nguyên lai là nữ tử... Khó trách Bạch độc sư sẽ hoài nghi!"),t("br"),t("br"),n._v("“Đúng rồi, nếu như là thân nữ nhi, vậy thì càng dễ xử lí...”"),t("br"),t("br"),n._v("Nghĩ đến một việc, Trương Huyền trở nên kích động."),t("br"),t("br"),n._v("Trước đó vẫn nghĩ như thế nào giải quyết Ngụy Như Yên sự tình, không cho đối phương có hoài nghi, đã cái này Tiên Tổ là nữ tử, vậy thì dễ làm rồi."),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền nhìn lại, mặc dù nhắm chặt hai mắt, nhưng Hải độc sư nhưng như cũ có loại bị để mắt tới cảm giác: “Không sai, ta là thân nữ nhi, sở dĩ đi vào cái này Tôn Cường trên thân, là bây giờ không có phương pháp! Bất quá... Ta đã tìm được một cái thích hợp bản thân thể chất, nhưng vừa tiến vào trong, liền sẽ hôn mê, hơn nữa không khống chế được lực lượng trong cơ thể... Cho nên, mới đến Độc điện, tìm kiếm phương pháp giải quyết!”"),t("br"),t("br"),n._v("“Tìm được thích hợp thể chất?”"),t("br"),t("br"),n._v("Trắng, hải hai vị độc sư liếc mắt nhìn nhau, đồng thời sửng sốt."),t("br"),t("br"),n._v("“Không sai, không chỉ có như vậy, ta hiện tại hồn lực hết sức yếu ớt, một khi đoạt xá, vô cùng có khả năng cho nên ký ức đều đánh mất, trừ phi... Lần nữa trở lại đỉnh phong, mới có thể gọi về ý thức, nếu không... Chỉ có thể là đối phương ý niệm chủ đạo, bởi vậy, một mực do dự, không dám tùy tiện động thủ!”"),t("br"),t("br"),n._v("Trương Huyền than thở."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hai đại độc sư cau mày."),t("br"),t("br"),n._v("Điểm ấy đến lúc đó bọn họ không nghĩ tới."),t("br"),t("br"),n._v("Bất quá, linh hồn bí ẩn vô cùng, tổ sư lại từ mấy vạn năm trước còn sống sót, xuất hiện bất kỳ biến cố, đều là có khả năng."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1016"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1018"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);