(window.webpackJsonp=window.webpackJsonp||[]).push([[350],{705:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1291-sup-đo-trieu-hung-mac"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1291-sup-đo-trieu-hung-mac"}},[n._v("#")]),n._v(" Chương 1291: Sụp đổ Triệu Hưng Mặc")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1291: Sụp đổ Triệu Hưng Mặc"),t("br"),t("br"),n._v("“Cùng ta trở mặt?”"),t("br"),t("br"),n._v("Sửng sốt một cái, Trương Khiêm khó tin."),t("br"),t("br"),n._v("Tốn sức vất vả, liền là muốn đòi đối phương vui lòng, lôi kéo Băng Nguyên cung quan hệ, làm sao không những không có tiến thêm một bước, ngược lại nói ra những lời này?"),t("br"),t("br"),n._v("Ngươi vừa rồi không trả khí muốn động thủ ư? Làm sao thời gian nháy mắt, bảo vệ đi lên?"),t("br"),t("br"),n._v("“Không sai, Trương Khiêm, mới vừa mới động thủ, chỉ là đối ngươi một cái cảnh cáo, hi vọng tự giải quyết cho tốt!”"),t("br"),t("br"),n._v("Trần Nhạc Dao khoát tay áo."),t("br"),t("br"),n._v("“Ngươi... Ta muốn biết vì cái gì!”"),t("br"),t("br"),n._v("Khí sắp nổ tung, Trương Khiêm nhịn không được nói."),t("br"),t("br"),n._v("Thực sự không nghĩ ra, vì cái gì trước mắt vị này, sẽ thái độ đại biến."),t("br"),t("br"),n._v("“Ngươi không cần biết vì cái gì, chỉ ghi nhớ, dám tìm Trương sư phiền phức, liền là cùng ta Trần Nhạc Dao đối nghịch!”"),t("br"),t("br"),n._v("Nữ hài toàn thân mang theo thanh lãnh chi ý, tựa hồ đối phương thực có can đảm vi phạm lời nói, liền sẽ trực tiếp ra tay, không lưu tình chút nào!"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, tức giận đến thân thể run rẩy, Trương Khiêm muốn nổi giận hơn, lại lại không dám."),t("br"),t("br"),n._v("Không nói trước thực lực của đối phương, so với hắn không hề yếu, chỉ nói Băng Nguyên cung thế lực, cũng không phải hắn có thể tuỳ tiện đắc tội."),t("br"),t("br"),n._v("“Chúng ta đi...”"),t("br"),t("br"),n._v("Hất lên ống tay áo, đem Trương Dụ đỡ dậy, Trương Khiêm xoay người hướng một bên đi đến."),t("br"),t("br"),n._v("Vất vả chạy tới vuốt mông ngựa, bị người đánh đấm một trận túi bụi không nói, đối phương còn không cảm kích... Càng nghĩ càng thấy đến buồn bực, cả người đều muốn nổ."),t("br"),t("br"),n._v("“Trương Huyền, đều là cái này Trương Huyền... Không cho ngươi sống không bằng chết, ta liền không họ Trương!”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, một bồn lửa giận, tất cả đều chuyển dời đến vừa rồi để hắn mặt mũi mất hết gia hỏa trên thân."),t("br"),t("br"),n._v("Nếu không phải người này, hắn làm sao lại bị này sỉ nhục? Lại làm sao có thể không có giữ gìn mối quan hệ, còn biến thành bộ dáng này?"),t("br"),t("br"),n._v("Đi đến nơi xa, quay đầu hướng Trần Nhạc Dao nhìn lại, chỉ thấy nàng nhìn về phía cách đó không xa Trương Huyền, ánh mắt phức tạp, không biết nghĩ cái gì."),t("br"),t("br"),n._v("“Đáng ghét...”"),t("br"),t("br"),n._v("Nhìn đến nơi này, càng là giận không chỗ phát tiết, đồng thời cũng đầy là không hiểu."),t("br"),t("br"),n._v("Vị này Trần Nhạc Dao, thoạt nhìn rất bình thường, đối với những khác người cũng không nể mặt mũi, làm sao đột nhiên đối cái tên này loại thái độ này?"),t("br"),t("br"),n._v("Bị ma quỷ ám ảnh sao?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Nhạc Dao sư tỷ, vị này Trương Khiêm bất kể thân thế vẫn là thiên phú, đều cũng không tệ lắm, làm sao đột nhiên xích mích?”"),t("br"),t("br"),n._v("Băng Nguyên cung một nữ tử, đi tới Trần Nhạc Dao trước mặt."),t("br"),t("br"),n._v("Vừa rồi Trương Huyền ra tay, nàng cũng không theo tới, cũng không nghe thấy ở giữa đối thoại, giờ phút này nhìn thấy Nhạc Dao sư tỷ động thủ, tràn đầy khó hiểu."),t("br"),t("br"),n._v("Vị này Trương Khiêm, bất kể thân thế vẫn là tướng mạo, đều là nhất đẳng, lại nho nhã lễ độ, làm sao động thủ?"),t("br"),t("br"),n._v("“Ngươi có biết vừa rồi vị kia tới hỏi thăm thiếu cung chủ người là ai?”"),t("br"),t("br"),n._v("Trần Nhạc Dao nói."),t("br"),t("br"),n._v("“Không biết...” Nữ tử lắc đầu."),t("br"),t("br"),n._v("“Là Trương Huyền Trương sư!” Trần Nhạc Dao gật đầu."),t("br"),t("br"),n._v("“Trương Huyền? Trương... Sư tỷ nói chẳng lẽ là... Giải quyết chúng ta tu luyện tai họa ngầm vị kia ân nhân?” Nữ tử thân thể cứng đờ."),t("br"),t("br"),n._v("“Không sai, liền là hắn! Đoán không sai, cũng là thiếu cung chủ lão sư...”"),t("br"),t("br"),n._v("Trần Nhạc Dao gật đầu."),t("br"),t("br"),n._v("“Cái này sao có thể...”"),t("br"),t("br"),n._v("Con mắt trợn tròn, nữ tử khó tin."),t("br"),t("br"),n._v("Các nàng Băng Nguyên cung âm quyết cùng dương quyết, có tự nhiên tai hoạ ngầm, tu luyện giả, cơ bản đều sẽ chết yểu, tuổi thọ không lâu! Dù là đạt đến Thánh vực, cũng không cao hơn ba trăm năm, cùng cái khác tu luyện giả một ngàn năm, chênh lệch cực lớn."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Băng Nguyên cung nữ tử, mặc dù rất nhiều, lại đều cùng tươi như hoa, tại rực rỡ nhất thời điểm tàn lụi, mang theo thê mỹ."),t("br"),t("br"),n._v("Vốn cho rằng, các nàng đồng dạng muốn đi đầu này con đường cũ, nằm mơ đều không nghĩ tới... Tám tháng trước, Liễu Huyên trưởng lão mang theo một thiếu nữ trở về, bởi vì là Thuần Âm thể chất, trực tiếp trở thành thiếu cung chủ, không chỉ có như vậy, còn truyền thụ một bộ sửa chữa sau pháp quyết!"),t("br"),t("br"),n._v("Bộ này pháp quyết, có thể nhẹ nhõm bù đắp âm quyết, dương quyết bên trong mang tới tai hoạ ngầm, để tu luyện giả tuổi thọ khôi phục lại người bình thường trình độ!"),t("br"),t("br"),n._v("Mặc dù công pháp lai lịch, bị phía trên ngăn chặn tin tức, nhưng thân là Băng Nguyên cung hạch tâm nhất thiên tài, vẫn là biết một chút."),t("br"),t("br"),n._v("Chính là thiếu cung chủ lão sư, một vị gọi “Trương Huyền” danh sư sáng tạo..."),t("br"),t("br"),n._v("Vốn cho rằng vị này ân nhân, là cái lão giả, ít nhất cũng là bát tinh trở lên danh sư, nằm mơ đều không nghĩ tới, trẻ tuổi như vậy, cùng các nàng tuổi tác tương đương!"),t("br"),t("br"),n._v("“Sửa chữa công pháp, giải quyết chúng ta tai hoạ ngầm, hắn chính là chúng ta tái sinh phụ mẫu...”"),t("br"),t("br"),n._v("Rõ ràng điểm ấy, nữ tử kích động vẻ mặt đỏ lên."),t("br"),t("br"),n._v("“Đúng vậy a, như vậy ân nhân, vừa rồi ta lại ý định động thủ, thật sự là tội không thể tha... Không bằng, chúng ta đi qua bái kiến một chút đi...”"),t("br"),t("br"),n._v("Trần Nhạc Dao mặt mũi cười khổ, nói."),t("br"),t("br"),n._v("“Tốt!” Nữ tử gật đầu, vội vàng gọi còn lại mấy vị, giải thích một chút, mấy người tất cả đều tràn đầy khiếp sợ, đồng thời hướng Trương Huyền phương hướng đi tới."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Ngươi làm sao như thế lỗ mãng!”"),t("br"),t("br"),n._v("Triệu Hưng Mặc giận không chỗ phát tiết, nhìn về phía trước mắt Trương Huyền, nhịn không được trách mắng."),t("br"),t("br"),n._v("Đối phương là Trương gia thiên tài, một bàn tay đánh nằm rạp trên mặt đất không đứng dậy được, ném vào mặt mũi... Tương đương đem Trương gia triệt để đắc tội, về sau tại Thánh tử điện, còn thế nào ở lại xuống dưới?"),t("br"),t("br"),n._v("“Ta chỉ là muốn hỏi một số chuyện mà thôi, là vị kia Trương Khiêm ra tay...”"),t("br"),t("br"),n._v("Trương Huyền xem thường."),t("br"),t("br"),n._v("Trương gia cứ việc mạnh mẽ, dù sao đây là danh sư đại lục, là Thánh tử điện, làm chuyện gì, còn cần chú ý quy tắc!"),t("br"),t("br"),n._v("Chỉ cần tại quy củ phạm vi bên trong... Thật đúng là không có gì đáng sợ."),t("br"),n._v(" Thật muốn ép, cùng lắm thì cùng xông đường đồng dạng, xông một lần Trương gia!"),t("br"),t("br"),n._v("“Ngươi vừa tới, không biết Trương gia tại Thánh tử điện thế lực, xem thường, chờ triệt để biết, liền tuyệt sẽ không như thế suy nghĩ...”"),t("br"),t("br"),n._v("Thấy hắn không thèm quan tâm, Triệu Hưng Mặc lắc đầu nói: “Như vậy cùng ngươi nói đi, Thánh tử điện, mặc dù là cái siêu thoát tại Thánh Nhân quý tộc học phủ, nhưng... Trương gia thân là đệ nhất gia tộc, hàng năm lấy được danh ngạch, cùng có thể qua khảo hạch người thực sự quá nhiều... Toàn bộ học phủ, mỗi một cái niên cấp, đều có bọn hắn người, hơn nữa, hầu như đều chiếm cứ trọng yếu nhất vị trí, hoặc là nắm giữ đỉnh phong nhất thực lực... Đắc tội một cái, chẳng khác nào đắc tội một mảng lớn, bất kể làm chuyện gì, đều đem nửa bước khó đi!”"),t("br"),t("br"),n._v("Không sợ quan, liền sợ quản, Diêm Vương dễ chịu, tiểu quỷ khó chơi, đắc tội một chút tiểu nhân vật, có đôi khi so đại nhân vật càng thêm phiền phức."),t("br"),t("br"),n._v("“Liền coi như bọn họ nhiều người, cũng không thể làm loạn đi!”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("“Coi như không loạn tới, dựa theo quy củ làm việc, cũng đủ nhức đầu, không nói cái khác, nhiều người như vậy, mỗi một cái đều tới khiêu chiến, ngươi còn có thời gian tu luyện?”"),t("br"),t("br"),n._v("Triệu Hưng Mặc nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền trầm mặc."),t("br"),t("br"),n._v("Quả thực."),t("br"),t("br"),n._v("Thánh tử điện không phản đối học sinh ở giữa tranh đấu, đối phương người đông thế mạnh, mỗi ngày đều tới mấy cái khiêu chiến, coi như thực lực đối phương không đủ, không phải là đối thủ, bản thân cũng phiền muộn không thôi ah."),t("br"),t("br"),n._v("“Hơn nữa, mới vừa rồi còn không chỉ có đắc tội Trương gia, liền Băng Nguyên cung đều đắc tội! Băng Nguyên cung, tại Thánh tử điện, cứ việc cùng Trương gia không thể so sánh, nhưng cũng là nhất đẳng thế lực, càng quan trọng hơn là... Đều là nữ tử, lực hiệu triệu cực mạnh! Không nói nhất hô bách ứng, cũng không xê xích gì nhiều, chỉ cần các nàng công khai tuyên bố giáo huấn ai, thậm chí đều không cần tự mình ra tay, liền sẽ có vô số thiên tài xông lại, tìm làm phiền ngươi.”"),t("br"),t("br"),n._v("Triệu Hưng Mặc nói tiếp."),t("br"),t("br"),n._v("Chỉ là nữ tử ngược lại cũng thôi, mấu chốt chính là, đều rất đẹp, lại thêm, thân là đại lục chúa tể một trong, bởi vậy, mỗi một cái Băng Nguyên cung người, đều có thể dẫn được vô số người điên cuồng."),t("br"),t("br"),n._v("Cho nên, tại Thánh tử điện, hầu như không ai dám đắc tội, đắc tội một cái, chẳng khác nào đắc tội không biết bao nhiêu cường giả."),t("br"),t("br"),n._v("Cái tên này ngược lại tốt, dặn dò nhiều lần, thế mà còn chạy qua tìm người ta, cuối cùng huyên náo không quá vui vẻ..."),t("br"),t("br"),n._v("Ngươi liền không thể an an ổn ổn đợi ở chỗ này, an ổn khảo hạch, không gây chuyện ư?"),t("br"),t("br"),n._v("Khiêm tốn..."),t("br"),t("br"),n._v("Liền như ngươi vậy cũng khiêm tốn?"),t("br"),t("br"),n._v("Cái này mẹ nó cũng gọi khiêm tốn, liền không có cao điệu người!"),t("br"),t("br"),n._v("“Thực sự không được, ngươi đi tìm Băng Nguyên cung người xin lỗi, miễn trừ tai hoạ, nếu không, hai thế lực lớn liên hợp, cho dù có mười cái đầu, cũng không đủ chém!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Triệu Hưng Mặc nói."),t("br"),t("br"),n._v("Mặc dù hắn chỉ phụ trách tiếp đón, nhưng một đường ở chung, sớm đã có tình cảm, không hy vọng vị thiên tài này, mới đi đến Thánh tử điện, cũng bởi vì lỗ mãng mà bị người vây công."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền gãi đầu một cái, đang không biết trả lời như thế nào, chỉ thấy một bên Trương Cửu Tiêu biến sắc, tràn đầy khẩn trương tiếng âm vang lên: “Băng Nguyên cung người đến đây...”"),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Triệu Hưng Mặc, Trương Huyền tất cả đều nhìn lại, lập tức nhìn thấy Trần Nhạc Dao cùng mấy vị Băng Nguyên cung nữ tử, chậm rãi đi tới."),t("br"),t("br"),n._v("“Các nàng cùng một chỗ tới, khẳng định là cùng Trương Khiêm có một loại nào đó hiệp định, tới hưng sư vấn tội...”"),t("br"),t("br"),n._v("Triệu Hưng Mặc cau mày."),t("br"),t("br"),n._v("Vừa rồi Trần Nhạc Dao giáo huấn Trương Dụ, cũng không nhìn thấy, giờ phút này Băng Nguyên cung người đồng loạt đi tới, không phải tìm phiền toái là làm gì?"),t("br"),t("br"),n._v("“Như vậy đi, các nàng đi tới, ngươi trước xin lỗi, cùng lắm thì đền ít đồ... Chỉ cần có thể miễn trừ phiền phức liền tốt!”"),t("br"),t("br"),n._v("Chần chờ một chút, Triệu Hưng Mặc vội vàng truyền âm."),t("br"),t("br"),n._v("“Xin lỗi?” Trương Huyền tràn đầy rầu rĩ: “Nói thế nào?”"),t("br"),t("br"),n._v("Xin lỗi cũng không phải là không thể được, nhưng... Rõ ràng không sai, thực sự nói không nên lời ah."),t("br"),t("br"),n._v("“Nói thế nào?”"),t("br"),t("br"),n._v("Không nghĩ tới gặp được EQ thấp như vậy gia hỏa, Triệu Hưng Mặc tràn đầy bất đắc dĩ, nói: “Như vậy đi, ta dạy cho ngươi, ngươi một chữ không kém thuật lại liền tốt!”"),t("br"),t("br"),n._v("“Ừm! Các nàng đi tới, ngươi nói như vậy: Nhạc Dao tiên tử, mới vừa rồi là ta lỗ mãng, ở đây thành khẩn xin lỗi ngươi, mong rằng có thể có được tha thứ...”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Biết đối phương là vì tốt cho hắn, Trương Huyền vừa đem lời nói ghi lại, chỉ thấy Băng Nguyên cung mấy vị nữ tử, đã đi tới trước mặt."),t("br"),t("br"),n._v("“Nhạc Dao...”"),t("br"),t("br"),n._v("Hắng giọng một cái, đang nghĩ đem Triệu sư đối lời hắn nói, thuật lại một lần, Trương Huyền chỉ thấy cô bé trước mắt doanh doanh cúi đầu: “Trương sư, mới vừa rồi là ta lỗ mãng, ở đây thành khẩn xin lỗi ngươi, mong rằng có thể có được tha thứ...”"),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Khụ khụ...”"),t("br"),t("br"),n._v("Triệu Hưng Mặc cũng mở to hai mắt nhìn, thân thể nhoáng một cái suýt chút nữa không có ngã sấp xuống."),t("br"),t("br"),n._v("Không phải tới tìm phiền toái ư?"),t("br"),t("br"),n._v("Xin lỗi thế nào?"),t("br"),t("br"),n._v("Mấu chốt nhất là, cùng hắn nói một chữ không kém..."),t("br"),t("br"),n._v("“Đây là chúng ta Băng Nguyên cung đặc sản, hàn băng linh dịch... Đối Xuất Khiếu cảnh có cực mạnh công hiệu, Trương sư nếu như không chê, xin hãy nhận lấy, coi như ta vừa rồi lỗ mãng đền bù...”"),t("br"),t("br"),n._v("Khiếp sợ còn không có kết thúc, Trần Nhạc Dao cổ tay khẽ đảo, lấy ra một cái bình ngọc đưa tới."),t("br"),t("br"),n._v("“Hàn băng linh dịch? Cái này... Thế nhưng là Băng Nguyên cung vật trân quý nhất, một bình giá trị ít nhất một ngàn tinh nguyên linh thạch thượng phẩm...”"),t("br"),t("br"),n._v("Con mắt trợn tròn, Triệu Hưng Mặc nuốt ngụm nước bọt."),t("br"),t("br"),n._v("Hàn băng linh dịch, tại Băng Nguyên cung đều là rất khan hiếm bảo vật, cơ bản không có người bán ra... Trần Nhạc Dao trực tiếp lấy ra đưa người..."),t("br"),t("br"),n._v("Đến cùng xảy ra chuyện gì?"),t("br"),t("br"),n._v("Không phải là tức giận, tới hưng sư vấn tội ư?"),t("br"),t("br"),n._v("Làm sao cẩn thận như vậy cẩn thận, không chỉ xin lỗi, còn đưa đồ?"),t("br"),t("br"),n._v("Hắn thực sự không nghĩ ra, có chút choáng."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1299"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1301"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);