(window.webpackJsonp=window.webpackJsonp||[]).push([[354],{709:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1295-muon-lam-đien-chu-u"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1295-muon-lam-đien-chu-u"}},[n._v("#")]),n._v(" Chương 1295: Muốn làm điện chủ ư?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1295: Muốn làm điện chủ ư?"),t("br"),t("br"),n._v("“Ngươi tại truyền tống lúc, sử dụng Minh Lý Chi Nhãn, đưa tới trận pháp không ổn định, mới được đưa đến nơi này!”"),t("br"),t("br"),n._v("Thấy hắn không hiểu, lão giả nói."),t("br"),t("br"),n._v("Thánh tử điện truyền tống trận, có thể căn cứ người am hiểu, truyền tống đến khác biệt vị trí, tiến hành chuyên hạng khảo hạch, trước mắt vị thanh niên này, vừa tiến vào trận pháp, liền vận chuyển Minh Lý Chi Nhãn, một cách tự nhiên liền bị truyền tống đến nơi này."),t("br"),t("br"),n._v("Cửa thứ nhất huyễn trận, là khảo nghiệm hắn loại thiên phú này năng lực trình độ vận dụng, loại bỏ về sau, mới có thể gặp được hắn."),t("br"),t("br"),n._v("Trương Huyền bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Có thể sắp đặt ra truyền tống trận lợi hại như vậy trận pháp, đo lường ra đang tại sử dụng, hơn nữa không có chút nào che lấp Minh Lý Chi Nhãn, cũng không khó hiểu."),t("br"),t("br"),n._v("“Ngươi mới vừa nói của ta Minh Lý Chi Nhãn đạt đến ‘Tỉ mỉ nhập vi, chút xíu không sai’ cảnh giới... Có ý tứ gì?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Từ khi có Minh Lý Chi Nhãn, hắn một mực vùi đầu tu luyện, chưa hề cùng những người khác nói qua, cũng không chút chủ động khoe khoang, cũng không biết, thứ này còn điểm cảnh giới."),t("br"),t("br"),n._v("“Minh Lý Chi Nhãn, là một chút thiên phú người, đạt tới lục tinh danh sư, tự động mở ra, mười phần hiếm thấy, toàn bộ đại lục nhìn chung lịch sử, cũng không quá nhiều. Tiêu diệt triệt để có khả năng phân biệt mức độ khác biệt, uy lực khác biệt, chia làm đẳng cấp khác nhau!”"),t("br"),t("br"),n._v("Lão giả vuốt râu một cái: “Đệ nhất trọng là  có khả năng nhìn thấu tất cả loại trừ dùng bảo vật cố ý che lấp ngụy trang!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn vừa mới lĩnh ngộ Minh Lý Chi Nhãn thời điểm, tất cả ngụy trang tại trước mặt, quả thực không có một chút tác dụng nào."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mới có thể nhìn thấy Lạc Nhược Hi hình dáng, vì đó kinh diễm."),t("br"),t("br"),n._v("“Đệ nhị trọng, chính là ta mới vừa nói, ! Đạt tới loại cảnh giới này, chỉ cần tùy tiện quan sát đồng dạng vật phẩm, liền có thể phân tích ra trọng lượng, lớn nhỏ, chất liệu... Không sai chút nào, đối võ công, chiêu số, cũng có thể rất nhẹ nhàng nhìn ra tốt thiếu sót!”"),t("br"),t("br"),n._v("Lão giả nói tiếp."),t("br"),t("br"),n._v("Trương Huyền hiểu được, trong mắt tràn đầy khó hiểu: “Nhưng còn có đệ tam trọng? Đó là cái gì?”"),t("br"),t("br"),n._v("Hắn giờ phút này, coi như không cần Thiên Đạo thư viện, đơn thuần sử dụng Minh Lý Chi Nhãn, quả thực có thể làm được, tỉ mỉ nhập vi, chút xíu không sai."),t("br"),t("br"),n._v("Nhìn tới hẳn là đạt tới loại cảnh giới này."),t("br"),t("br"),n._v("Chỉ là... Hắn làm sao cảm giác, Minh Lý Chi Nhãn vừa mới xuất hiện thời điểm, liền có loại năng lực này?"),t("br"),t("br"),n._v("“Đệ tam trọng, , tìm kiếm bản nguyên, ngược dòng bản chất! Đạt tới loại cảnh giới này, đó có thể thấy được bảo vật, võ kỹ bản chất nhất đồ vật, thậm chí một chút biến mất dấu vết, cũng có thể nhẹ nhõm phân biệt ra được. Thật giống như có người chạy trốn, dù là che giấu khá hơn nữa, cũng sẽ có dấu vết lưu lại, đạt tới loại cảnh giới này, Minh Lý Chi Nhãn, có thể nhẹ nhõm nhìn ra!”"),t("br"),t("br"),n._v("Lão giả nói."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Trương Huyền chớp mắt."),t("br"),t("br"),n._v("Mới vừa rồi còn có chút kỳ quái, hiện tại đã hoàn toàn rõ ràng... Loại năng lực này, mới vừa có cái Minh Lý Chi Nhãn liền tồn tại, không tính là gì a?"),t("br"),t("br"),n._v("Đây là cái gọi là cảnh giới thứ ba?"),t("br"),t("br"),n._v("Làm sao bản thân vừa mở ra, liền nắm giữ?"),t("br"),t("br"),n._v("“Đạt tới loại cảnh giới này, cùng cấp bậc hầu như đã không có bí mật có thể giấu diếm, liếc mắt qua, cũng có thể thấy được đối phương có hay không nói thật ra, có hay không cố ý giấu diếm, càng có thể biết công pháp ưu khuyết, loại cảnh giới này, chỉ cần không vẫn lạc, xung kích cửu tinh danh sư, cũng sẽ không quá khó!”"),t("br"),t("br"),n._v("Lão giả gật đầu."),t("br"),t("br"),n._v("Trương Huyền không nói."),t("br"),t("br"),n._v("Đối phương nói không sai, hắn sở dĩ tiến bộ nhanh, Thiên Đạo công pháp, là một nguyên nhân, Minh Lý Chi Nhãn cũng không thể bỏ qua."),t("br"),t("br"),n._v("Không có loại năng lực này, tuyệt không có khả năng, một đường hướng về phía trước, đạt tới bây giờ tình trạng."),t("br"),t("br"),n._v("“Cái kia... Có hay không đệ tứ trọng?”"),t("br"),t("br"),n._v("Tiếp tục hỏi."),t("br"),t("br"),n._v("Tiền tam trọng, hắn đã đạt đến, có hay không cái gọi là đệ tứ trọng?"),t("br"),t("br"),n._v("Thật có lời nói, cân nhắc để Minh Lý Chi Nhãn tấn cấp, cũng là không sai."),t("br"),t("br"),n._v("“Đương nhiên là có, đạt tới loại cảnh giới này, liền có thể tu luyện đồng kỹ, trong mắt hình thành từng đạo trận văn, từ đó thi triển ra có thể so với thậm chí vượt qua võ kỹ lực lượng! Căn cứ lão sư ta lý giải, cảnh giới này gọi là , một khi luyện thành, ánh mắt chiếu tới chỗ, bầu trời đều có thể xé rách, uy lực vô tận, so với Khưu Ngô cổ thánh Diễn Không thiên thư, đều không kém chút nào!”"),t("br"),t("br"),n._v("Lão giả vẻ mặt nghiêm túc, trong ánh mắt mang theo tự hào."),t("br"),t("br"),n._v("“Có thể so với Diễn Không thiên thư?” Trương Huyền kinh ngạc."),t("br"),t("br"),n._v("Cái khác không biết, Diễn Không thiên thư thế nhưng là tu luyện qua, năm cái cảnh giới một cái so một cái cao thâm, toàn bộ học được, tương đương với nắm giữ không gian, thậm chí có thể qua lại trong đó, không cần truyền tống trận, cũng có thể khoảng cách dài di động."),t("br"),t("br"),n._v("Loại này tuyệt học, là trước mắt hắn gặp qua tinh diệu nhất, so với Phong Thánh giải đều cường đại hơn không biết bao nhiêu, nếu như có thể bổ sung suốt ngày đạo công pháp, hoàn toàn lĩnh ngộ, coi như chỉ có Nguyên Thần cảnh đỉnh phong, Thánh vực sáu, thất trọng cường giả cũng dễ như trở bàn tay ngược sát."),t("br"),t("br"),n._v("Năm đó Khưu Ngô cổ thánh liền là nhờ vào cái này tuyệt học, phong ấn Dị Linh tộc nhân vài vạn năm, không cách nào đi vào đại lục, nhân loại cũng được lấy bình yên."),t("br"),t("br"),n._v("Có thể so với thứ tuyệt kỹ này, đối phương cái gọi là đồng kỹ, cũng không tránh khỏi quá cường đại đi!"),t("br"),t("br"),n._v("“Đồng kỹ... Không biết tiền bối lão sư là...”"),t("br"),t("br"),n._v("Thực sự nhịn không được, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("Hắn nghe nói qua, Khổng sư thân truyền, Bốc Thương cổ thánh liền nắm giữ Minh Lý Chi Nhãn, hơn nữa sáng chế ra đồng kỹ, một cái nhìn qua, ngàn vạn Dị Linh tộc nhân, biến thành tro bụi."),t("br"),t("br"),n._v("Trước mắt vị lão giả này, còn nói đồng kỹ, sẽ có hay không có cái gì liên quan?"),t("br"),t("br"),n._v("“Tại hạ ân sư, chính là Khổng sư dưới trướng, mười đại hiền giả một trong Bốc Thương cổ thánh! Lão sư lưu lại Thánh tử điện liền rời đi, ta là cái này học phủ đời thứ nhất điện chủ, xông sơn môn quy củ, chính là ta xác định tới!”"),t("br"),t("br"),n._v("Thấy hắn kịp phản ứng, lão giả cười cười."),t("br"),t("br"),n._v("“Đời thứ nhất điện chủ?”"),t("br"),t("br"),n._v("Trương Huyền con mắt trợn tròn."),t("br"),t("br"),n._v("Còn tưởng rằng là cái đánh xì dầu tàn hồn, không nghĩ tới lại là đời thứ nhất điện chủ!"),t("br"),t("br"),n._v("“Ta nghe nói, muốn trở thành Thánh tử điện điện chủ, nhất định phải nắm giữ Minh Lý Chi Nhãn... Nói như vậy, tiền bối, cũng có loại thiên phú này?”"),t("br"),t("br"),n._v("Nhịn không được hỏi."),t("br"),n._v(" “Không sai, ta cũng nắm giữ Minh Lý Chi Nhãn, toàn thịnh kỳ, từng tu luyện tới cảnh giới thứ tư, đạt đến kim cương trừng mắt, phục hổ hàng ma cảnh giới! Có Minh Lý Chi Nhãn mới có thể trở thành điện chủ, cũng chính là ta chỗ quy định. Mục đích rất đơn giản, ân sư đem Khổng sư tư tưởng lưu lại, truyền thừa thiên hạ, ta cũng muốn đem lão sư truyền thừa lưu lại, để thế nhân biết như vậy một vị đại công vô tư cổ thánh!”"),t("br"),t("br"),n._v("Lão giả gật đầu, vẻ mặt lạnh nhạt nhìn qua: “Bởi vậy... Lịch đại nhập môn học sinh, chỉ cần có Minh Lý Chi Nhãn, đều sẽ đi vào nơi này, tiếp nhận khảo hạch của ta!”"),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Khó trách một người đều không có nhìn thấy, hẳn là hắn khảo hạch cùng những người khác hoàn toàn khác biệt."),t("br"),t("br"),n._v("“Còn không biết tiền bối họ tên...”"),t("br"),t("br"),n._v("Hiểu được, Trương Huyền thần thái cung kính, ôm quyền nói."),t("br"),t("br"),n._v("Trước mắt vị này, bàn về tới là Khổng sư đồ tôn, chân chính vượt qua lịch sử siêu cấp nhân vật, càng là Thánh tử điện đời thứ nhất điện chủ, đem hắn phát dương quang đại... Bất kể từ chỗ nào một chút, đều đáng giá tôn kính, không thể có mảy may khinh thường."),t("br"),t("br"),n._v("“Tại hạ họ tên, đã xa xưa, không đáng giá nhắc tới, lúc ấy thế nhân, đều tôn xưng ta vì !”"),t("br"),t("br"),n._v("Lão giả gật đầu."),t("br"),t("br"),n._v("“Vãn bối Trương Huyền, gặp qua Khôi Thánh!” Trương Huyền ôm quyền."),t("br"),t("br"),n._v("Đối với loại này tiên thánh cổ hiền, hắn từ trước tới nay ôm sùng bái chi tâm."),t("br"),t("br"),n._v("Thật giống như đối mặt Khưu Ngô cổ thánh, tới nghiêm túc nghiên cứu thảo luận công pháp tốt thiếu, không có chút nào giấu giếm."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Khôi Thánh ý niệm nói tiếp: “Ngươi tuỳ tiện nhìn ra ta lưu lại huyễn cảnh, đồng thời đem phá giải, nói rõ Minh Lý Chi Nhãn, đã đạt đến thấy rõ thật giả, loại bỏ hư ảo cảnh giới. Vừa chuẩn xác thực nhìn ra chày sắt trọng lượng, hơn nữa chế biến ra hòa tan dược vật, nói rõ đạt đến đệ nhị cảnh... Bất quá, muốn trở thành điện chủ người ứng cử, ít nhất cũng phải đạt tới đệ tam cảnh, phản phác quy chân, có thể phá ẩn nấp cảnh giới!”"),t("br"),t("br"),n._v("“Điện chủ người ứng cử?” Trương Huyền nháy con mắt."),t("br"),t("br"),n._v("Hắn chỉ là tới khảo hạch học viên, nói thế nào nói, biến thành khảo hạch điện chủ?"),t("br"),t("br"),n._v("“Không sai, Thánh tử điện lịch đại điện chủ, đều cần nắm giữ Minh Lý Chi Nhãn, nguyên nhân chính là như vậy, vài vạn năm đến, chân chính điện chủ, xuất hiện nhiều lần gián đoạn, bản thân về sau, toàn bộ cộng lại, cũng chỉ tám vị mà thôi, ngươi nắm giữ loại thiên phú này, có tư cách trở thành người ứng cử... Đúng rồi, còn không có hỏi thăm, ngươi có muốn hay không trở thành Thánh tử điện thứ mười Nhâm điện chủ?”"),t("br"),t("br"),n._v("Khôi Thánh ý niệm nhìn qua."),t("br"),t("br"),n._v("“Đương nhiên!” Trương Huyền liền vội vàng gật đầu."),t("br"),t("br"),n._v("Một khi thành điện chủ, bất kể thân phận vẫn là địa vị, đều đủ để xứng được với Lạc Nhược Hi, lại đi cầu hôn, Lạc gia cũng muốn suy nghĩ thật kỹ."),t("br"),t("br"),n._v("“Vậy là tốt rồi, có điều, điện chủ cũng không phải là chỉ có Minh Lý Chi Nhãn liền có thể làm, còn cần đối ngươi thực lực, tâm tính, ngộ tính tiến hành nhiều phương diện khảo hạch! Chỉ có thông qua, mới có tư cách.”"),t("br"),t("br"),n._v("Khôi Thánh ý niệm nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trước đó hắn nghe nói qua, nắm giữ Minh Lý Chi Nhãn danh sư mười phần thưa thớt, chỉ là không nghĩ tới ít như vậy."),t("br"),t("br"),n._v("Tự đối phương về sau, có tám vị... Mỗi cái Thánh Nhân, sống một ngàn năm... Nói cách khác, Thánh tử điện phần lớn thời gian, đều là không có chân chính điện chủ."),t("br"),t("br"),n._v("Khó trách, bản thân vừa tiến đến liền truyền tống đến nơi này, tiếp nhận khảo nghiệm, nhìn tới thật vất vả phát hiện một vị Minh Lý Chi Nhãn danh sư, đương nhiên muốn làm Thành điện chủ bồi dưỡng."),t("br"),t("br"),n._v("“Không biết làm sao khảo hạch?”"),t("br"),t("br"),n._v("Nhịn không được nói."),t("br"),t("br"),n._v("“Ta mới vừa nói, muốn trở thành điện chủ người ứng cử, Minh Lý Chi Nhãn cần đạt tới đệ tam cảnh, hiện tại liền đối ngươi khảo hạch, có khả năng đạt tới, mới có thể đi vào đi xuống một cái khảo hạch!”"),t("br"),t("br"),n._v("Khôi Thánh ý niệm cười cười: “Minh Lý Chi Nhãn đệ tam cảnh, là phản phác quy chân, có thể phá ẩn nấp, tìm kiếm bản nguyên, ngược dòng bản chất! Ta chỗ này, có một dạng đồ vật, cho ngươi xem dâng một nén nhang thời gian, nếu như có thể đem bản chất nói ra, không sai chút nào, coi như thông qua.”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Phân biệt vật phẩm là hắn cường hạng, theo Thiên Huyền vương quốc đi ra đến bây giờ, còn chưa hề thất thủ qua."),t("br"),t("br"),n._v("“Rất tốt!”"),t("br"),t("br"),n._v("Khôi Thánh ý niệm nhẹ gật đầu, bàn tay nhẹ nhàng vồ một cái, một cái không lớn vò gốm xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Ngươi chỉ cần nói cho ta biết, cái này vò gốm bên trong chứa đựng cái gì liền tốt!”"),t("br"),t("br"),n._v("“Chứa đựng cái gì?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Cái này vò gốm thoạt nhìn hết sức bình thường, toàn thân gốm sứ nung, không có bất kỳ cái gì đặc thù, nhưng phía trên có đặc thù lá bùa dán sát vào, thần thức không cách nào dò xét."),t("br"),t("br"),n._v("Hít sâu một hơi, vô số hoa văn tại trong mắt lăn mình, Trương Huyền nhịn không được hướng vò miệng nhìn sang."),t("br"),t("br"),n._v("Nương theo Minh Lý Chi Nhãn vận chuyển, trước mắt vò gốm, giống như là sống đồng dạng, tản mát ra khí tức kinh người, như là một cái to lớn mặt trời, chiếu sáng ánh sáng, để cho người ta căn bản thấy không rõ lắm, chớ nói chi là cất giấu trong đó cái gì."),t("br"),t("br"),n._v("“Này làm sao nhìn?”"),t("br"),t("br"),n._v("Mày nhăn lại."),t("br"),t("br"),n._v("Như vậy ánh sáng, con mắt đều bị lắc có chút đau nhức, căn bản nhìn không ra ah!"),t("br"),t("br"),n._v("Thậm chí, coi như muốn tìm kiếm dấu vết để lại, tiến hành suy đoán, cũng làm không được."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Chần chờ một chút, trong đầu hô khẽ."),t("br"),t("br"),n._v("(, tiêu đề cùng nội dung có chút không hợp. Nhìn thấy tất cả mọi người tại chửi bậy, lão nhai ở đây giải thích một chút, thật ra thì đây là Bạch Cư Dịch 《 Trường Hận ca 》 bên trên một câu thi từ, hoàn chỉnh câu là: Hậu cung giai nhân ba ngàn người, chày sắt mài thành kim may. Trẫm cùng Quân Vương giải chiến bào, phù dung trướng ấm độ đêm xuân. Đêm xuân khổ ngắn ngày càng cao lên, xương sống thắt lưng run chân khó tảo triều."),t("br"),t("br"),n._v("Lần thứ nhất nhìn thấy câu này thời điểm, lão nhai kinh động như gặp thiên nhân, trên thế giới lại có tuyệt vời như vậy câu thơ... Bởi vậy ký ức sâu hơn, lúc này mới có thể tại sáng tác thời điểm vừa đúng dùng tới. Xin mọi người đừng vì lão nhai bác học cảm thấy bội phục, ta là rất biết điều, chưa bao giờ khoe khoang."),t("br"),t("br"),n._v("Về sau nhìn cũng khuyên mọi người nhiều xem đọc sách, nhất là thơ từ, nhiều đọc thuộc lòng một chút, liền sẽ không xuất hiện không hiểu, gây ra xấu hổ."),t("br"),t("br"),n._v("Mặt khác, lão nhai tại công chúng hào chuẩn bị như vậy câu thơ rất nhiều, mọi người có thể chú ý nhìn một chút. Wechat lục soát “Hoành Tảo Thiên Nhai” tăng thêm chú ý là đủ. Mặt khác, cũng cầu một chút Weibo Fan hâm mộ, Weibo lục soát “Điểm xuất phát Hoành Tảo Thiên Nhai” chú ý là đủ.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1303"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1305"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);