(window.webpackJsonp=window.webpackJsonp||[]).push([[356],{711:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1297-nguoi-la-benh-mu-mau-u"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1297-nguoi-la-benh-mu-mau-u"}},[n._v("#")]),n._v(" Chương 1297: Ngươi là bệnh mù màu ư?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1297: Ngươi là bệnh mù màu ư?"),t("br"),t("br"),n._v("Trương Huyền theo thói quen động tác, là nhìn thấy thư tịch lần đầu tiên, thu vào đến Thiên Đạo thư viện, cho nên, hắn bây giờ nhìn cũng không phải là trên không hiện lên nội dung, mà là thức hải bên trong thư tịch."),t("br"),t("br"),n._v("Giới thiệu vắn tắt còn tốt, chân chính pháp quyết chảy qua trong óc thời điểm, lập tức phát giác không đúng."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Khôi Thánh nhìn qua."),t("br"),t("br"),n._v("Bộ này pháp quyết, là lão sư hắn tự mình sáng tạo, cao thâm vô cùng, kỳ trước nắm giữ Minh Lý Chi Nhãn thiên tài nhìn thấy, đều chìm đắm trong đó, không cách nào tự kềm chế, trước mắt vị này, trực tiếp cau mày, còn nói “Có vấn đề”, có ý tứ gì?"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền xoay đầu lại, nhìn về phía trước mắt Khôi Thánh ý niệm: “Tiền bối, ta muốn hỏi một chút, lịch đại điện chủ bên trong, nhưng có người hai mắt mù?”"),t("br"),t("br"),n._v("“Hai mắt mù?”"),t("br"),t("br"),n._v("Không biết hắn hỏi như vậy là dụng ý gì, Khôi Thánh nhíu nhíu mày: “Ta đạo ý niệm này, chỉ phụ trách tiếp đón, khảo nghiệm điện chủ nhân tuyển, cũng không biết hậu bối làm sao.”"),t("br"),t("br"),n._v("Hắn chết thời gian, sớm tại mấy vạn năm trước, lịch đại điện chủ đều là hậu bối, trừ phi có Minh Lý Chi Nhãn thiên tài đi vào, mới có thể tỉnh táo, còn những này hậu bối có hay không mù, cũng không rõ ràng."),t("br"),t("br"),n._v("“Cái kia... Mạo muội hỏi một câu, Bốc Thương cổ thánh con mắt tốt chứ?” Thấy hắn không biết, Trương Huyền mang theo chần chừ, hỏi lần nữa."),t("br"),t("br"),n._v("“Lão sư sáng lập Thánh tử điện không lâu, liền trực tiếp rời khỏi... Con mắt quả thực là mù!”"),t("br"),t("br"),n._v("Khôi Thánh dừng lại một chút,: “Bất quá, đi tìm khôi phục phương pháp, hẳn là có khả năng khôi phục...”"),t("br"),t("br"),n._v("“Khôi phục?” Trương Huyền cười khổ một tiếng: “Ta cảm thấy rất khó, thậm chí không có khả năng!”"),t("br"),t("br"),n._v("“Vì cái gì?” Khôi Thánh nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Thánh tử trong điện có rất nhiều liên quan tới Bốc Thương cổ thánh sự tích cùng thư tịch, nhưng đều là mười phần quang minh, hai mắt mù sự tình, có rất ít người biết được, bởi vậy không có truyền ra ngoài, trước mắt vị này, làm sao biết được?"),t("br"),t("br"),n._v("Hơn nữa còn nói thẳng, không thể nào khôi phục?"),t("br"),t("br"),n._v("“Nguyên nhân ngay tại bộ này Cửu U Thần đồng bên trong!” Trương Huyền than thở."),t("br"),t("br"),n._v("“Bộ này đồng kỹ, là phát huy Minh Lý Chi Nhãn lực lượng, đồng thời đem tiềm lực của con người đào ra, thuộc về nhất đẳng tuyệt đỉnh công pháp, nhưng... Mỗi một lần vận chuyển trận văn, đều sẽ đối với con mắt tạo thành rất lớn tổn thương, dần dà, sẽ cho người hai mắt mù, dược thạch nan y!”"),t("br"),t("br"),n._v("Thư viện phản hồi tới thiếu hụt, đầu thứ nhất liền là sẽ cho người hai mắt sứ mệnh, không cách nào cứu chữa."),t("br"),t("br"),n._v("Bất kể là người bình thường còn là tu luyện người, tiềm lực lúc nào cũng cố định, sử dụng trận văn, kích phát trong ánh mắt lực lượng, thậm chí diễn sinh ra giết người võ kỹ... Đối với con mắt tổn thương cực lớn, thậm chí có một ít là vĩnh cửu."),t("br"),t("br"),n._v("Dù là cao minh đến đâu đan dược, cao minh đến đâu thủ đoạn, cũng không có bất cứ tác dụng gì."),t("br"),t("br"),n._v("Liền cùng kiếp trước một chút đỉnh phong vận động viên đồng dạng, tuổi thọ bình thường đều không quá quá lâu dài, bởi vì, bọn họ tại lúc còn trẻ, đem thân thể tiềm lực toàn bộ tiêu hao đi ra, sau này già rồi, các loại tổn thương bệnh, sẽ càng ngày càng nhiều, cuối cùng khó hưởng người thường chi thọ."),t("br"),t("br"),n._v("Cửu Tiêu Thần đồng, không chỉ tiêu hao con mắt, liền trong đầu thị giác thần kinh, đều tiêu hao hết, con mắt có thể hoán, có thể dược vật trị liệu, thần kinh một khi chết héo, thật giống như tuổi thọ kết thúc, lại khó có những phương pháp khác."),t("br"),t("br"),n._v("Chính là bởi vì thấy được cái này, hắn mới phát giác được, bộ công pháp này, mặc dù rất tốt, lại có rất lớn vấn đề."),t("br"),t("br"),n._v("Thậm chí có thể nói là thiếu hụt trí mệnh!"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn, vốn là lên trời trọng thưởng, nắm giữ cực mạnh công hiệu, một khi mù, tương đương với thủ đoạn mạnh nhất hao tổn, đừng nói thực lực, liền xem như tâm tính, chỉ sợ đều sẽ gặp phải trọng tỏa, khó khôi phục."),t("br"),t("br"),n._v("Nghe nói như thế, nhìn thấy thanh niên không thể làm gì khác hơn than thở, Khôi Thánh nhíu nhíu mày, mặt lộ vẻ không vui: “Bộ công pháp này, ta tu luyện mấy trăm năm, cũng không cảm thấy được đối với con mắt tạo thành tổn thương a? Đây là lão sư lưu lại, không cần thiết bắn tiếng đe doạ!”"),t("br"),t("br"),n._v("Không phải không tin, mà là hắn cũng tu luyện thời gian không ngắn, con mắt vẫn như cũ hoàn hảo không chút tổn hại, không có xuất hiện bất kỳ khó chịu."),t("br"),t("br"),n._v("Có thể hay không quá mức bắn tiếng đe doạ, quá khoa trương?"),t("br"),t("br"),n._v("Lại nói, lão sư là ai?"),t("br"),t("br"),n._v("Khổng sư thân truyền đệ tử, mười đại hiền giả một trong, chìm đắm cả đời võ kỹ, làm sao có thể có vấn đề, sẽ cho người con mắt mù?"),t("br"),t("br"),n._v("Thật muốn nghĩ như vậy, chẳng phải là đang hoài nghi lão sư?"),t("br"),t("br"),n._v("Coi như hoài nghi, cũng không phải ngươi một cái nho nhỏ Nguyên Thần cảnh gia hỏa, nên đi làm a!"),t("br"),t("br"),n._v("“Có phải hay không bắn tiếng đe doạ, ta cẩn thận nói một lần, ngươi sẽ biết... Tiền bối tu luyện mấy trăm năm, nếu như không nhìn lầm, bộ này Cửu U Thần đồng, hẳn là còn không có đạt tới đệ tam trọng a?”"),t("br"),t("br"),n._v("Biết đối phương sẽ nói ra lời này, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Khôi Thánh gật đầu."),t("br"),t("br"),n._v("Tu vi của hắn, kém xa lão sư, đừng nói đệ tam trọng, coi như đệ nhị trọng, đều không có đạt tới đỉnh phong nhất."),t("br"),t("br"),n._v("“Nếu như không nhìn lầm, ngươi lưu lại đạo ý niệm này thời điểm, hẳn là vừa đem Cửu U Thần đồng tu luyện tới đệ nhị trọng sơ kỳ, sử dụng hỏa diễm luyện mắt, hàn băng tôi đồng! Loại cảnh giới này, nhãn lực kinh người, trong đêm tối như là đèn sáng, chiếu rọi bốn phương, bất quá... Chân chính chiến đấu, còn kém một chút hỏa hầu, đệ nhị trọng kim cương trừng mắt, hẳn là cũng không cách nào thi triển hoàn chỉnh đi!”"),t("br"),n._v("Mí mắt vừa nhấc, Trương Huyền nói."),t("br"),t("br"),n._v("Cửu U Thần đồng, đệ nhất trọng Mục Sinh Thần Ma, đệ nhị trọng, kim cương trừng mắt, nhìn một cái, hỏa diễm thiêu đốt, phần thiên diệt địa, uy lực toàn bộ thi triển, có thể đem địa ngục đều trong nháy mắt đốt xuyên."),t("br"),t("br"),n._v("Nếu như nói đệ nhất trọng là tinh thần công kích, đệ nhị trọng thì là chân chính vật lý công kích."),t("br"),t("br"),n._v("Thực lực không mạnh, căn bản ngăn cản không nổi, sẽ bị phút chốc gạt bỏ, liền xem như vượt qua bản thân, cũng sẽ chật vật không chịu nổi, lại khó phát huy lực lượng."),t("br"),t("br"),n._v("Mấu chốt nhất là, công kích tùy tâm mà phát, không có bất kỳ cái gì chiêu số, ánh mắt quét qua, tức là công kích... Khó lòng phòng bị, căn bản không có cách nào ngăn cản."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, bộ này đồng kỹ mới như vậy nổi danh, Thánh tử điện điện chủ, cũng danh tiếng vang xa, địa vị so với Trương gia loại hình gia chủ, đều không hề yếu."),t("br"),t("br"),n._v("Bất quá, bộ võ kỹ này uy lực rất lớn, nhưng tu luyện cũng vô cùng phiền phức, trước muốn lấy hỏa diễm luyện mắt, hàn băng tôi con ngươi, sau đó lại kinh nghiệm vô số tôi luyện, hơi không cẩn thận, liền có thể công lao đổ biển, thực lực hủy hết."),t("br"),t("br"),n._v("Trước mắt vị này, cứ việc không có thi triển đồng kỹ, nhưng lấy hắn đối nhãn thuật lý giải, hẳn là đạt đến đệ nhị trọng cảnh giới."),t("br"),t("br"),n._v("“Ngươi... Làm thế nào biết?”"),t("br"),t("br"),n._v("Khôi Thánh tràn đầy kinh ngạc."),t("br"),t("br"),n._v("Đối phương nói không sai chút nào, hắn lưu lại ý niệm thời điểm, vừa đem Cửu U Thần đồng tu luyện tới đệ nhị trọng, khoảng cách đệ tam trọng, còn không biết bao xa."),t("br"),t("br"),n._v("“Ngươi bây giờ mặc dù chỉ là một đạo ý niệm, nhưng trong con mắt có hỏa diễm tại thiêu đốt, mặc dù không thịnh, lại mang theo đốt diệt tất cả uy năng, cùng đệ nhị trọng kim cương trừng mắt tương đồng, đương nhiên có thể nhẹ nhõm nhìn ra!”"),t("br"),t("br"),n._v("Tiến về phía trước một bước, Trương Huyền dọc theo đối phương lượn quanh một vòng, thản nhiên nói."),t("br"),t("br"),n._v("Đem trên không bí tịch thu nhập thư viện, còn kém không nhiều, dung hội quán thông tất cả kiến thức, đối phương cứ việc chỉ là một đạo ý niệm, nhưng bằng mượn hắn nhãn lực, muốn xem ra những này, vẫn là không tính quá mức khó khăn."),t("br"),t("br"),n._v("“Ngươi ý tứ... Đã đem đệ nhị trọng công pháp, toàn bộ xem hết?”"),t("br"),t("br"),n._v("Khôi Thánh thân thể chấn động, con mắt trợn tròn."),t("br"),t("br"),n._v("Hắn vừa đem Cửu U Thần đồng bí tịch lấy ra, từ đối phương bắt đầu nhìn thấy kết thúc, chẳng qua mấy hơi thở công phu... Hiện tại nói thẳng ra đệ nhị trọng đủ loại đặc điểm, chẳng lẽ đã toàn bộ xem hết cũng nhớ kỹ?"),t("br"),t("br"),n._v("Bộ công pháp này, chừng mấy chục vạn chữ, ánh sáng đọc thuộc lòng liền muốn tiêu tốn cực lớn công phu, năm đó hắn dốc lòng học tập, ngày đêm không ngủ, cũng hao tốn trọn hai ngày, mới hoàn chỉnh dưới lưng."),t("br"),t("br"),n._v("Bởi vậy còn được đến lão sư khen ngợi, nói hắn thiên tư thông minh, trí nhớ kinh người..."),t("br"),t("br"),n._v("Cái tên này chỉ nhìn mấy hơi thở liền nhớ kỹ, cái này sao có thể?"),t("br"),t("br"),n._v("“Bộ công pháp này, rất dễ dàng đọc thuộc lòng, nhớ kỹ không có cái gì, chủ yếu nhất là như thế nào lý giải cùng tu luyện.”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu, cũng không vì đối phương khiếp sợ, mà cảm thấy vui vẻ, ngược lại mang theo tiếc hận: “Ngươi tu luyện tới đệ nhị trọng kim cương trừng mắt, nếu như không có đoán sai, trong mắt sẽ thỉnh thoảng lại xuất hiện điểm đỏ, mang theo gãi ngứa cảm giác, gặp được gió táp còn có thể xuất hiện rơi lệ tình huống, mấu chốt nhất là, vận chuyển hỏa diễm về sau, sẽ có ngắn ngủi là mù, thời gian không dài, có chừng ba cái hô hấp...”"),t("br"),t("br"),n._v("“Không biết phải chăng là chính xác?”"),t("br"),t("br"),n._v("“Ngươi...” Khôi Thánh tràn đầy khó tin."),t("br"),t("br"),n._v("Hắn tại lưu lại ý niệm thời điểm thật có loại tình huống này, chỉ bất quá lúc ấy đang tu luyện, tưởng rằng bị ngọn lửa hun sấy gây nên, cũng không để tâm, chẳng lẽ thật sự là tu luyện công pháp bên trên xuất hiện vấn đề gì?"),t("br"),t("br"),n._v("Hắn đạt tới đệ nhị trọng thời điểm, lão sư đã rời khỏi, không cách nào tìm người hỏi thăm, bởi vậy, bộ này pháp quyết, căn bản là chính hắn lục lọi tu luyện."),t("br"),t("br"),n._v("Vừa mới luyện thành, cùng người chiến đấu, quả thực từng có thời gian ngắn mù, đại khái ba cái hô hấp, lúc ấy cảm thấy có thể là thoát lực gây nên, chẳng lẽ, thật cùng đối phương nói đồng dạng... Công pháp bên trong tồn tại vấn đề?"),t("br"),t("br"),n._v("“Kim cương trừng mắt, con mắt hóa thành Hồng Liên biển lửa, đại thành thời điểm, nghiệp hỏa đầy trời, đốt cháy tất cả dơ bẩn đồ vật, uy lực quả thực mạnh mẽ, nhưng... Đối tinh thần tiêu hao cũng rất lớn, sử dụng qua đi, chủ điều khiển thị giác thần kinh, sẽ trở nên già yếu, tuổi thọ tổn hao nhiều! Thật giống như trận bàn, có khả năng sử dụng mười lần, mỗi dùng một lần, liền tiêu hao một chút, mười lần qua đi, mạnh hơn trận pháp sư cũng không cách nào tu bổ, chỉ có thể hết hiệu lực!”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền mang theo tiếc hận: “Ngươi chú ý đọc thời điểm, cứ việc vừa mới đạt tới loại cảnh giới này, nhưng cũng xuất hiện tai hoạ ngầm, trong mắt tơ máu biến nhiều, nhìn kỹ có chút rất nhỏ ửng hồng, chủ yếu nhất là, sẽ thường xuyên tính mất ngủ!”"),t("br"),t("br"),n._v("“Ngươi biết... Ta sẽ mất ngủ?”"),t("br"),t("br"),n._v("Khôi Thánh gặp quỷ đồng dạng biểu lộ."),t("br"),t("br"),n._v("Tu vi đạt tới hắn loại cảnh giới này, cứ việc có thể dùng tu luyện thay thế ngủ, nhưng thỉnh thoảng ngủ lấy một lần, đối tinh thần cùng thân thể, đều sẽ có cực lớn thả lỏng."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, ban đêm đã tới, bọn họ thỉnh thoảng cũng là sẽ ngủ."),t("br"),t("br"),n._v("Năm đó, lão sư vừa đi, bản thân quản lý một cái học viện, áp lực quá lớn, một mực không chút nghỉ ngơi, muốn ngủ cũng ngủ không được, tưởng rằng tinh thần căng đến thật chặt nguyên nhân, chẳng lẽ cùng cái này Cửu U Thần đồng liên quan đến?"),t("br"),t("br"),n._v("Mơ hồ nhớ... Lão sư dường như cũng thường xuyên mất ngủ, suốt đêm không ngừng."),t("br"),t("br"),n._v("“Thần kinh bị hao tổn, thức hải chịu đến va chạm, mất ngủ là rất bình thường, nương theo tu vi càng sâu, còn có thể gây nên suy nhược tinh thần, từ đó làm cho lãng quên, trí nhớ suy giảm, mấu chốt nhất là... Đối màu sắc sẽ càng ngày càng không mẫn cảm, cuối cùng trong thiên địa thất thải chi sắc, tại trong mắt, biến thành hắc bạch hai loại, cái khác sẽ không đi hiện lên...”"),t("br"),t("br"),n._v("Nói đến đây, Trương Huyền dừng lại một chút, thở dài một tiếng, khắp khuôn mặt là đồng tình: “Cũng chính là trong truyền thuyết... Bệnh mù màu!”"),t("br"),t("br"),n._v("“Không nhìn lầm, ngươi hẳn là đi!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1305"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1307"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);