(window.webpackJsonp=window.webpackJsonp||[]).push([[357],{712:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1298-khoi-thanh-rau-ri"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1298-khoi-thanh-rau-ri"}},[n._v("#")]),n._v(" Chương 1298: Khôi Thánh rầu rĩ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1298: Khôi Thánh rầu rĩ"),t("br"),t("br"),n._v("Cửu U Thần đồng, đem hỏa diễm tại trong ánh mắt thiêu đốt, lực lượng hội tụ tại con mắt bên trong, dần dà, đối sắc thái cảm thụ, sẽ càng lúc càng mờ nhạt, cuối cùng toàn bộ thiên địa, đều biến thành hắc bạch chi sắc."),t("br"),t("br"),n._v("Lại không có cách nào khôi phục."),t("br"),t("br"),n._v("Điểm ấy, thư viện thiếu hụt bên trên, cứ việc không có nhắc, nhưng Trương Huyền đối y đạo tinh thông, đối các loại tu luyện như lòng bàn tay, nghịch hướng đẩy ngược, rất dễ dàng đoán ra được."),t("br"),t("br"),n._v("“Bệnh mù màu? Đó là cái gì?”"),t("br"),t("br"),n._v("Khôi Thánh ngây người."),t("br"),t("br"),n._v("Cái danh từ này, chưa từng nghe người nói lên."),t("br"),t("br"),n._v("“Ngươi không biết?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trương Huyền lập tức giật mình."),t("br"),t("br"),n._v("Thế giới này cùng kiếp trước khác biệt, không có “Bệnh mù màu” cái từ này cũng rất bình thường."),t("br"),t("br"),n._v("Chần chờ một chút, giải thích nói: “Bệnh mù màu liền là đối màu sắc không mẫn cảm, không cách nào phân biệt màu sắc chủng loại, thật giống như ta bộ y phục này, ngươi nhìn là màu gì?”"),t("br"),t("br"),n._v("“Xám!” Khôi Thánh nói."),t("br"),t("br"),n._v("“Là có chút mang theo lam nhạt, cũng không phải là màu xám!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Nhìn tới trước mắt vị này, đã đối màu sắc mất đi quan sát năng lực, bản thân trường bào, mang theo màu lam nhạt, cùng xám hoàn toàn là hai khái niệm."),t("br"),t("br"),n._v("“Màu lam nhạt?”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Khôi Thánh trong lòng nghi ngờ... Đây là lam nhạt?"),t("br"),t("br"),n._v("Làm sao cảm giác... Không giống đâu?"),t("br"),t("br"),n._v("“Nếu như cái này nhìn không ra, cầu vồng đây, có thể nhìn ra mấy loại màu sắc?”"),t("br"),t("br"),n._v("Trương Huyền bàn tay một tranh, chân khí trên không trung xao động tạo thành một đạo bảy màu cầu vồng."),t("br"),t("br"),n._v("“Có thể nhìn ra mấy loại màu sắc, bất quá... Không đủ bảy loại, hơn nữa rất nhiều đều rất tương tự, phân biệt không ra...”"),t("br"),t("br"),n._v("Nhìn kỹ một hồi, càng xem Khôi Thánh càng cảm thấy kinh hãi."),t("br"),t("br"),n._v("Hắn còn nhỏ thời điểm, nhìn cầu vồng, có thể rõ ràng phân biệt ra được bảy loại màu sắc, bây giờ lại chỉ có ba loại, hơn nữa mười phần mơ hồ... Chẳng lẽ, thật thành đối phương trong miệng nói tới sắc lang... Bệnh mù màu?"),t("br"),t("br"),n._v("Sẽ không thật cùng lão sư lưu lại Cửu U Thần đồng liên quan đến a?"),t("br"),t("br"),n._v("Nhiều năm như vậy, hắn một mực tin tưởng vững chắc lão sư sáng lập ra công pháp, thanh niên trước mắt liên tục nói nhiều chứng cớ như vậy, để hắn kìm lòng không được sinh ra hoài nghi."),t("br"),t("br"),n._v("“Nhưng có phương pháp bổ cứu?”"),t("br"),t("br"),n._v("Dừng lại nửa ngày, Khôi Thánh nhịn không được hỏi."),t("br"),t("br"),n._v("“Ta mới nhìn ra thiếu hụt, còn không có tìm tới phương pháp bổ cứu...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Thư viện chỉ cho khuyết chức hãm, hắn còn không có tiếp xúc qua, tương tự đồng kỹ phương pháp tu luyện, bởi vậy, không cách nào bổ sung."),t("br"),t("br"),n._v("“Bất quá, từ từ nghiên cứu, rồi sẽ tìm được!”"),t("br"),t("br"),n._v("Chỉ cần cấp cho đầy đủ bí tịch, bổ sung suốt ngày đạo cấp bậc, hẳn là có thể giải quyết vấn đề này."),t("br"),t("br"),n._v("Lúc trước Khưu Ngô cổ thánh Đại Bi Thiên Ma chưởng, cũng là sai lầm, cũng sẽ để cho người ta tu luyện mắt mù, bản thân cũng mượn nhờ vô số công pháp, đem chỉ tu đổi thành công, hiện tại thi triển, không có bất kỳ cái gì quá đáng lo."),t("br"),t("br"),n._v("“Nha...”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Khôi Thánh nhìn qua: “Bộ này Cửu U Thần đồng, ngươi tu luyện không tu luyện?”"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn mạnh mẽ, hắn biết đến rất rõ ràng, nếu như mù, thật sự cái gì cũng không có."),t("br"),t("br"),n._v("“Ta trước đem công pháp triệt để lý giải, trở về nghiên cứu một chút, lại tiến hành tu luyện!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Lợi hại như vậy võ kỹ, tu luyện là tất nhiên, chỉ bất quá, hiện nay công pháp, có thể khiến người ta mắt mù, chắc chắn sẽ không tiếp xúc, chờ có cơ hội, xem nhiều sách tịch, đem thiếu hụt bổ sung hoàn chỉnh, rồi nói sau."),t("br"),t("br"),n._v("Dù sao hiện nay đối với hắn mà nói, trọng yếu nhất không phải võ kỹ, mà là tu vi."),t("br"),t("br"),n._v("Tu vi quá thấp, coi như gặp được Lạc Nhược Hi, cũng không có biện pháp gì."),t("br"),t("br"),n._v("“Ừm, không sai, nhất định phải thật tốt nghiên cứu, ba ngày này, ngươi có thể cùng ta học, có cái gì không hiểu, cẩn thận hỏi thăm, ba ngày sau, rời khỏi nơi đây, liền lại không có cơ hội!”"),t("br"),t("br"),n._v("Khôi Thánh nhẹ gật đầu."),t("br"),t("br"),n._v("“Đa tạ tiền bối!”"),t("br"),t("br"),n._v("Lên tiếng, Trương Huyền khoanh chân ngồi dưới đất, con mắt nhìn xem trên không văn tự, trên thực tế tinh thần lại thức hải dạo chơi, tiêu hóa cùng lý giải Cửu U Thần đồng kiến thức."),t("br"),t("br"),n._v("“Trọn bộ công pháp, hết thảy hơn ba mươi chỗ thiếu hụt, mỗi một chỗ, đều đối thân thể có tổn hại cực lớn...”"),t("br"),t("br"),n._v("Nghiên cứu không biết dài đến đâu thời gian, toàn bộ hiểu một lần, xoa xoa mi tâm, Trương Huyền một mặt bất đắc dĩ."),t("br"),t("br"),n._v("Không phải là không muốn học, mà là sơ hở thực sự quá nhiều, để hắn một cái có hoàn mỹ ép buộc chứng người, thực sự không thể đi xuống miệng."),t("br"),t("br"),n._v("Gượng ép tu luyện lời nói, thực biết càng luyện càng mệt, cuối cùng buồn nôn phun ra."),t("br"),t("br"),n._v("“Làm gì, nhưng có cái gì không hiểu chỗ, ta có thể vì ngươi giải đáp!”"),t("br"),t("br"),n._v("Một bên Khôi Thánh thấy hắn ngừng quan sát, cười khanh khách nhìn qua."),t("br"),t("br"),n._v("“Là có chút không hiểu, bất quá... Ngươi cũng giải đáp không được, thôi được rồi!”"),t("br"),t("br"),n._v("Biết đối phương là hảo ý, chần chờ một chút, Trương Huyền cuối cùng lắc đầu."),t("br"),n._v(" Không phải hắn xem thường đối phương, bản thân nghi vấn khó xử lý, liền xem như Bốc Thương cổ thánh cũng không giải quyết được, nếu không, cũng sẽ không tu luyện tới mắt mù."),t("br"),t("br"),n._v("“Không giải quyết được?” Nhíu nhíu mày, Khôi Thánh mang theo không vui: “Bộ công pháp này, ta chìm đắm mấy trăm năm, càng là tu luyện tới đệ nhị trọng, chỉ cần trong vấn đề tu luyện, khẳng định đều có thể giải quyết!”"),t("br"),t("br"),n._v("Trước mắt vị này, cứ việc nhãn lực rất mạnh, một cái nhìn ra Cửu U Thần đồng lớn nhất thiếu hụt, nhưng bộ công pháp này, hắn nghiên cứu nhiều năm như vậy, lại thêm bản thân tu vi cao thâm, khẳng định không phải đối phương có thể so sánh."),t("br"),t("br"),n._v("“Đều có thể giải quyết?”"),t("br"),t("br"),n._v("Thấy hắn một mặt tự tin, Trương Huyền nhìn qua, nhẹ nhàng cười một tiếng: “Vậy thì tốt, ta vừa vặn có mấy cái vấn đề, không có hiểu rõ, mong rằng tiền bối cho giải đáp! Trong mắt Thần Ma, có khả năng cho người ta chấn nhiếp, Thần, Ma loại nào hình thái? Cụ thể bộ dáng nhưng có? Nếu như chỉ là tùy ý tưởng tượng ra phỏng đoán, làm sao có thể sinh ra hiệu quả?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Khôi Thánh sửng sốt."),t("br"),t("br"),n._v("Cái gọi là Thần Ma, quả thực không ai thấy qua."),t("br"),t("br"),n._v("Hắn lúc trước quan tưởng, chỉ là ở trong lòng tận lực tưởng tượng ra sát lục chi địa bộ dáng, lấy sát khí để thay thế Thần Ma, cho người ta chấn nhiếp."),t("br"),t("br"),n._v("Đang nghĩ nói ra bản thân phương pháp tu luyện, liền nghe đến thanh niên tiếp tục mở miệng: “Nếu như chỉ là quan tưởng chém giết, sát lục chi khí đi vào thức hải, trong mắt mang theo sát cơ, cùng Dị Linh tộc nhân có gì khác biệt? Thần Ma, là uy nghiêm, mà không phải chém giết, thật muốn làm như vậy, chẳng khác nào đi lệch con đường tu luyện!”"),t("br"),t("br"),n._v("Thân thể chấn động, Khôi Thánh khuôn mặt trắng bệch."),t("br"),t("br"),n._v("Quan tưởng chém giết thời điểm, mình đích thật có một đoạn thời gian, khó mà khống chế trong cơ thể sát ý, từng chuyên môn chạy đến Địa quật, chém giết Dị Linh tộc nhân."),t("br"),t("br"),n._v("Đối phương vừa nói như vậy, quả thực vi phạm danh sư thân phận cùng đạo lý làm người."),t("br"),t("br"),n._v("“Vấn đề thứ hai!”"),t("br"),t("br"),n._v("Thấy hắn không đáp lại được, Trương Huyền tiếp tục nói: “Lấy mắt tiến công người khác, dựa vào là linh hồn xung kích, trong đó dính dáng không ít hoa văn, ta vừa rồi nhìn một chút, trong đó thứ tám mươi bảy đạo, có khả năng hấp thu đối phương lực lượng tinh thần, mục đích là vì khóa chặt nguyên thần, tốt hơn tiến công, nhưng... Nếu có người trước thời hạn dự báo, thừa cơ tiến hành tiến công, chiêu này có thể hay không trực tiếp sụp đổ, thậm chí gặp phải lực lượng phản phệ? Cho dù không có phát giác, nguyên thần vượt xa ngươi, cưỡng ép tiến công, cũng sẽ thụ tổn hại đi!”"),t("br"),t("br"),n._v("Khôi Thánh yên lặng, nói không ra lời."),t("br"),t("br"),n._v("Đối phương nói chính là  một cái khác thiếu hụt vị trí, lúc trước lão sư cùng hắn nói qua, cho dù Bốc Thương cổ thánh loại này cường giả, cũng không cách nào bù đắp."),t("br"),t("br"),n._v("Bất quá, cái này mặc dù là thiếu hụt, thời điểm chiến đấu, giao chiến thoáng qua liền qua, có rất ít người có khả năng phát hiện, hơn nữa tiến hành phản kích."),t("br"),t("br"),n._v("Liền xem như thiếu hụt, cũng không quá quan trọng."),t("br"),t("br"),n._v("Chỉ là... Đột nhiên hỏi một chút, thật không biết trả lời như thế nào."),t("br"),t("br"),n._v("“Cái thứ ba, kim cương trừng mắt, phục hổ hàng ma, sử dụng hỏa diễm rèn luyện ánh mắt, hàn băng rèn luyện con ngươi, nóng lạnh luân phiên, băng hàn tương dung, trong mắt sinh ra Thái Cực, phù hợp thiên địa đương nhiên, nhưng mà... Thiên Đạo vì một, nhất sinh vạn vật, hai loại trái lại lực lượng, tại trong mắt lưu chuyển thời gian dài, cứ việc có thể làm cho phù văn lực lượng đại tăng, Minh Lý Chi Nhãn bên trong hoa văn, càng thêm sáng chói, nhưng cũng sẽ đè thấp bản thân cái khác lực lượng linh hồn, như là phong ấn, để cho mình nguyên bản nguyên thần công kích, trở nên yếu kém...”"),t("br"),t("br"),n._v("“Vấn đề thứ tư...”"),t("br"),t("br"),n._v("Trương Huyền từng cái hỏi lên."),t("br"),t("br"),n._v("Mỗi hỏi một cái, Khôi Thánh vẻ mặt liền trắng hơn mấy phần, liên tục hơn mười, thân ảnh càng ngày càng phai mờ, bất cứ lúc nào cũng sẽ ngã sấp xuống."),t("br"),t("br"),n._v("Đã không phải là kinh ngạc, mà là sợ hãi."),t("br"),t("br"),n._v("Cửu U Thần đồng, mặc dù là lão sư sáng tạo, nhưng cũng là nắm giữ thiếu hụt, dựa theo tình huống bình thường, khuyết điểm không che lấp được ưu điểm, chỉ cần nghiêm túc thi triển, sẽ không xuất hiện vấn đề quá lớn, thậm chí liền xem như hắn, đều sẽ xem nhẹ những thứ này."),t("br"),t("br"),n._v("Trước mắt vị này, chỉ nhìn như thế một hồi, thế mà đem bên trong xuất hiện sơ hở, một cái không kém nói ra, nếu như không phải học tập, mà là kẻ địch, chỉ bằng vào biết những này, liền có thể nhẹ nhõm phá vỡ tuyệt kỹ của hắn, chỉ trong một chiêu đem con mắt chọc mù!"),t("br"),t("br"),n._v("Mồ hôi lạnh theo cổ, chảy xuống."),t("br"),t("br"),n._v("Cửu U Thần đồng, từ khi học tập về sau, vẫn cảm thấy là vô địch thiên hạ tuyệt kỹ, cùng người đối chiến, chỉ cần thi triển, bách chiến bách thắng!"),t("br"),t("br"),n._v("Nghe được đối phương nói như vậy, mới hiểu được, là không có gặp được cao thủ chân chính, thật muốn đụng tới, hắn đôi mắt này, có thể hay không giữ được, đều vẫn là không biết chi trời."),t("br"),t("br"),n._v("Thiện bơi người chìm, dùng con mắt tiến công, sớm tối đều sẽ chết ở trên đây."),t("br"),t("br"),n._v("“Chỉ một hồi, nhìn ra nhiều như vậy thiếu hụt... Ta còn nghĩ đến cho hắn chỉ điểm...”"),t("br"),t("br"),n._v("Thân thể cứng ngắc, kìm nén nói không ra lời, Khôi Thánh buồn bực suy nghĩ muốn nổ."),t("br"),t("br"),n._v("Vừa rồi lời thề son sắt nói muốn cho người ta chỉ điểm, kết quả đối phương hỏi ra vấn đề, hắn một cái đều đáp không được..."),t("br"),t("br"),n._v("Chỉ cảm thấy da mặt đau rát đau, bất cứ lúc nào cũng sẽ không chịu nổi."),t("br"),t("br"),n._v("“Đây là thứ mười ba cái... Như vậy đi, ngày hôm nay trước nói đến đây, tiền bối, có thể nghĩ được rồi trả lời như thế nào?”"),t("br"),t("br"),n._v("Thanh niên thanh âm tiếp tục vang lên, trong giọng nói mang theo an ủi: “Không cần khẩn trương, từ từ suy nghĩ, có nhiều thời gian, dù sao còn có hơn hai ngày... Thực sự không được, ta ngủ trước một hồi, chờ ngươi nghĩ kỹ gọi ta, giúp ta giải đáp xong xuôi, ta lại tiếp tục hỏi thăm phía sau vấn đề!”"),t("br"),t("br"),n._v("“Khụ khụ...”"),t("br"),t("br"),n._v("Khóe miệng co giật, Khôi Thánh lung lay hai lần."),t("br"),t("br"),n._v("Trước kia đều là hắn khảo hạch hậu bối, cho đối phương giải đáp, làm sao... Hiện tại đồng dạng là giải đáp, làm lại cùng đối phương khảo nghiệm bản thân đồng dạng?"),t("br"),t("br"),n._v("Mấu chốt nhất là... Những vấn đề này có khả năng trả lời đi ra ngược lại cũng thôi, mà là căn bản giải đáp không được!"),t("br"),t("br"),n._v("Không nói là hắn, đem coi như năm lão sư, nếu như có thể giải đáp, cũng khẳng định lần nữa sửa chữa pháp quyết..."),t("br"),t("br"),n._v("Lão sư đều giải đáp không được, để hắn nhớ lại đáp... Đừng nói hai ngày, hai năm cũng làm không được!"),t("br"),t("br"),n._v("Lưu lại ý niệm, khảo hạch hậu nhân, bây giờ lại bị hậu nhân làm khó..."),t("br"),t("br"),n._v("Từ nơi nào chạy tới quái thai? Lợi hại như vậy?"),t("br"),t("br"),n._v("Suy tư không biết bao lâu, phát hiện đối với mấy cái này vấn đề, một cái đều trả lời không được, đành phải bất đắc dĩ hướng thanh niên nhìn lại, lại thấy cái tên này, quả nhiên nằm trên mặt đất đã ngủ, khóe miệng không ngừng lại lấy nước bọt, không biết mơ tới thứ gì, khắp khuôn mặt là nụ cười."),t("br"),t("br"),n._v("“Cái này... Ngươi là đời thứ nhất điện chủ, hay ta là?”"),t("br"),t("br"),n._v("Khóe miệng co quắp một lần, Khôi Thánh trước mắt biến thành màu đen, chỉ cảm thấy một hồi buồn bực, toàn bộ ý niệm đều không ngừng lay động, dường như không chịu nổi."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1306"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1308"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);