(window.webpackJsonp=window.webpackJsonp||[]).push([[36],{391:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1009-an-bai-nguy-nhu-yen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1009-an-bai-nguy-nhu-yen"}},[n._v("#")]),n._v(" Chương 1009: An bài Ngụy Như Yên")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1009: An bài Ngụy Như Yên"),t("br"),t("br"),n._v("“Thật ra thì tổ sư cho dù không thể khôi phục thân nữ nhi, cũng không sao...”"),t("br"),t("br"),n._v("Bạch độc sư an ủi nói."),t("br"),t("br"),n._v("Chỉ cần có thể sống sót, nam, nữ không tính là gì."),t("br"),t("br"),n._v("Độc điện liền có không ít độc sư, bởi vì dùng độc quá nhiều, giới tính đều không phân."),t("br"),t("br"),n._v("“Mới đầu ta cũng nghĩ như vậy, nhưng mà thân thể này cùng ta Tiên Thiên độc hồn thể không phù hợp, tiếp tục tiếp tục chờ đợi, sẽ càng ngày càng yếu, không bao lâu đồng dạng sẽ chết... Cho nên, mặc kệ đoạt xá có thể hay không mất đi ký ức, ta sẽ làm tất cả. Đây là... Ta cơ hội duy nhất!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Thông qua Hải độc sư giới thiệu, hắn biết chỉ có Độc điện mới có thể giải quyết triệt để Ngụy Như Yên tình huống, đồng thời để hắn nhanh chóng thăng cấp."),t("br"),t("br"),n._v("Nhưng... Nếu như cứ như vậy đưa qua lời nói, Độc điện không giống Danh Sư đường, khó tránh khỏi sẽ xuất hiện đủ loại biến cố."),t("br"),t("br"),n._v("Dù sao Tiên Thiên độc thể thật là đáng sợ, một khi xuất hiện, trở thành Độc điện điện chủ chỉ là vấn đề thời gian, mà đương nhiệm điện chủ, làm sao có thể cam tâm nhường chỗ ngồi?"),t("br"),t("br"),n._v("Để phòng ngừa ngoài ý muốn, không bằng trước cho nàng một cái càng thêm lợi hại thân phận... Khai phái tổ sư!"),t("br"),t("br"),n._v("Có cái thân phận này, đương nhiệm điện chủ cho dù muốn động nàng, chỉ sợ cũng không dám!"),t("br"),t("br"),n._v("Đương nhiên, Ngụy Như Yên một khi tỉnh lại, không giống bản thân như vậy sẽ lừa dối, làm không cẩn thận liền sẽ lộ ra chân ngựa... Bởi vậy, trước thời hạn khai báo sẽ đánh mất ký ức, còn cái gọi là trở lại đỉnh phong khôi phục..."),t("br"),t("br"),n._v("Thật muốn nắm giữ loại thực lực này, khôi phục không khôi phục... Ai còn dám nhiều lời nói nhảm?"),t("br"),t("br"),n._v("“Tổ sư có thể cần chúng ta làm cái gì, chỉ cần có thể làm được, ta hai người muôn lần chết không chối từ!”"),t("br"),t("br"),n._v("Biết tổ sư cùng bọn hắn nói cặn kẽ như vậy, tất nhiên có chuyện muốn hắn đi làm, Bạch độc sư ngẩng đầu nhìn tới, ánh mắt kiên định."),t("br"),t("br"),n._v("Hải độc sư cũng đồng thời gật đầu."),t("br"),t("br"),n._v("Đối phương tại trong lòng hai người địa vị, như là danh sư trong suy nghĩ Khổng sư, có thể vì nàng làm việc, cho dù chết, cũng sẽ không cự tuyệt."),t("br"),t("br"),n._v("“Không cần các ngươi muôn lần chết không chối từ... Bất quá, cũng quả thực có chuyện xảy ra, để các ngươi không đi làm!”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền nhìn qua: “Ta hôm nay liền sẽ đem linh hồn, chuyển dời đến sớm đã chuẩn bị xong cô gái trên thân, đến lúc đó, sẽ rơi vào hôn mê, nếu như... Có biện pháp, hi vọng các ngươi có thể đem ta, mang về Độc điện tổng bộ, để cho ta mau chóng khôi phục thực lực!”"),t("br"),t("br"),n._v("“Cái này... Tổ sư yên tâm, chúng ta nhất định hoàn thành!”"),t("br"),t("br"),t("span",[t("br"),n._v("Tr"),t("br"),n._v("uyện Của Tui chấm vn ")]),n._v(" Còn tưởng rằng là cái gì, nghe được là yêu cầu này, hai vị độc sư đồng thời gật đầu."),t("br"),t("br"),n._v("Nếu như cho tổng bộ biết, bọn họ sắp mở phái Tiên Tổ tìm về, ngợi khen cũng không kịp, có thể nói chuyện này không những không khó khăn, ngược lại là một đại mỹ chuyện."),t("br"),t("br"),n._v("“Đừng tưởng rằng là chuyện tốt!”"),t("br"),t("br"),n._v("Dường như nhìn ra bọn hắn ý nghĩ, Trương Huyền lắc đầu: “Ta đoạt xá sau khi thành công, hồn phách sẽ cùng đối phương hoàn toàn dung hợp, làm cho đối phương thể chất, cũng tiến hóa thành Tiên Thiên độc thể... Nói cách khác, sẽ không ngừng toả ra khí độc, các ngươi bảo vệ ở bên người... Vô cùng có khả năng trúng độc!”"),t("br"),t("br"),n._v("“Chỉ cần có thể hoàn thành tổ sư nhắc nhở, chúng ta bỏ mình cũng không tính là gì!”"),t("br"),t("br"),n._v("Vẻ mặt nghiêm túc, hai người đồng thời đứng thẳng người, mang theo coi thường cái chết biểu lộ."),t("br"),t("br"),n._v("Độc sư mặc dù không giống danh sư như thế, coi trọng quy củ nguyên tắc, nhưng cũng có truyền thừa cùng bảo vệ tôn nghiêm, ranh giới cuối cùng."),t("br"),t("br"),n._v("“Hạ độc chết ngược lại không đến nỗi, như vậy, ta hôm nay ban đêm sẽ luyện chế một chút thuốc, dùng để chống cự khí độc, một khi phòng ngự không được, nuốt chửng một hạt liền có thể hóa giải!”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền nói tiếp: “Mặt khác, ta sẽ truyền thụ cho các ngươi cao thâm hơn độc công, cùng dùng độc phương pháp, đồng thời nghĩ biện pháp giúp các ngươi đột phá đến Thánh vực ngũ trọng...”"),t("br"),t("br"),n._v("“Đa tạ tổ sư...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Bạch, Hải hai vị độc sư đồng thời hô hấp dồn dập."),t("br"),t("br"),n._v("Trước mắt vị này là ai?"),t("br"),t("br"),n._v("Khai phái tổ sư!"),t("br"),t("br"),n._v("Tự tay sáng lập độc sư cái nghề nghiệp này, lưu lại độc sư truyền thừa... Nhân vật như vậy, tự mình chỉ điểm, cũng muốn giúp bọn hắn đột phá... Đây tuyệt đối là không dám tưởng tượng!"),t("br"),t("br"),n._v("“Không cần cao hứng quá sớm, tiếp nhận ta truyền thừa, cần bái ta làm thầy! Mặt khác, để phòng ngừa ngoài ý muốn, ta sẽ để vị này Tôn Cường khống chế hồn phách của các ngươi, một khi sinh ra dị tâm, sẽ lập tức tử vong!”"),t("br"),t("br"),n._v("Trương Huyền nói tiếp."),t("br"),t("br"),n._v("Không phải hắn không tin đối phương, mà là muốn bảo đảm Ngụy Như Yên không thể có tổn thương chút nào!"),t("br"),t("br"),n._v("Lòng người khó dò, huống chi Tiên Thiên độc thể loại thể chất hiếm thấy này."),t("br"),t("br"),n._v("Cho nên, trước cho bọn hắn điểm ngon ngọt, sau đó dùng linh hồn khống chế lại."),t("br"),t("br"),n._v("“Tổ sư yên tâm, chúng ta tuyệt sẽ không sinh ra hai lòng...” Biết Đạo Tổ sư lo lắng cái gì, Bạch, Hải hai vị độc sư đồng thời ôm quyền."),t("br"),t("br"),n._v("Có thể trở thành tổ sư đệ tử, liền cùng Khổng sư môn sinh đồng dạng, địa vị tuyệt đối bao trùm tại vô số độc sư phía trên, về sau cho dù cửu tinh độc sư gặp gỡ, chỉ sợ đều muốn ngoan ngoãn nghe lời, không dám vi phạm."),t("br"),t("br"),n._v("“Không cần sớm như vậy tỏ thái độ, ta cho các ngươi thời gian cân nhắc, suy nghĩ kỹ, hôm nay nửa đêm tới tìm ta...”"),t("br"),t("br"),n._v("Thấy đối phương lập tức đáp ứng, Trương Huyền cũng không làm ra quyết định, mà là khoát tay áo."),t("br"),t("br"),n._v("“Cái này... Vâng!”"),t("br"),t("br"),n._v("Hai người nhìn nhau, nhẹ gật đầu."),t("br"),t("br"),n._v("“Được rồi, chuyện của ta, tạm thời không muốn để cho người biết được, nhất định muốn giữ miệng giữ mồm, nếu không, các ngươi trên thân trúng độc, vô pháp giải hết, sống không quá ngày mai!”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Trương Huyền thanh âm bên trong mang theo uy hiếp."),t("br"),n._v("“Tổ sư yên tâm, hai người chúng ta nhất định giữ miệng giữ mồm!”"),t("br"),t("br"),n._v("Hai vị độc sư ôm quyền."),t("br"),t("br"),n._v("“Ừm, đi xuống đi!”"),t("br"),t("br"),n._v("Giao phó xong, Trương Huyền không nói thêm lời."),t("br"),t("br"),n._v("Bạch, Hải hai vị độc sư, xoay người lui xuống."),t("br"),t("br"),n._v("Bọn họ rời khỏi, Trương Huyền chậm rãi mở mắt."),t("br"),t("br"),n._v("Bọn họ có thể đột phá, chính là bát tinh độc sư, dùng để bảo hộ Ngụy Như Yên, sẽ an toàn không ít."),t("br"),t("br"),n._v("Đem trong đầu tri thức sửa sang lại một chút, hình thành Thiên Đạo độc công, Trương Huyền lúc này mới chậm rãi đi ra Tàng Thư các."),t("br"),t("br"),n._v("Lúc này Hứa trưởng lão, Nhược Thanh Viễn bọn người canh giữ ở bên ngoài, thấy hắn đi ra, lập tức chuẩn bị tiệc rượu khoản đãi."),t("br"),t("br"),n._v("Ăn uống xong, trở lại mới an bài chỗ ở, lúc này mới thân thể nhoáng một cái, đi vào Thiên Nghĩ Phong sào, đem chỉnh lý tốt độc công thong thả mở ra."),t("br"),t("br"),n._v("Sở dĩ cùng đối phương ước định nửa đêm tới, là bởi vì hắn còn không có học tập những kiến thức này, cho dù muốn chỉ điểm, cũng không thể nào làm lên."),t("br"),t("br"),n._v("Thiên Đạo độc công tam tinh, tứ tinh, ngũ tinh..."),t("br"),t("br"),n._v("Một bên đọc sách, một bên tu luyện, đối độc lý giải càng ngày càng sâu, cũng biết càng ngày càng nhiều."),t("br"),t("br"),n._v("Không biết qua bao lâu, một ngụm trọc khí phun ra, Trương Huyền trong mắt gợn sóng chớp động, một đạo làm người ta kỳ lạ màu sắc, lóe lên liền biến mất."),t("br"),t("br"),n._v("Theo tam tinh đến thất tinh, vô số dùng độc tri thức, giờ phút này đã toàn bộ ghi vào trong óc, hòa tan vào thân thể, thành hắn bản năng."),t("br"),t("br"),n._v("Hiện tại muốn nói đối độc lý giải, Bạch độc sư cũng theo không kịp, chỉ sợ đã đuổi kịp một chút bát tinh cấp bậc cường giả!"),t("br"),t("br"),n._v("Đương nhiên, hắn tu vi hiện tại còn chưa đủ, muốn khảo hạch bát tinh độc sư, khẳng định là làm không được, thậm chí cho dù Nhược Thanh Viễn hỗ trợ xin thất tinh huy chương, cũng chỉ là “Chuẩn thất tinh”."),t("br"),t("br"),n._v("Độc sư, mặc dù thuộc về hạ cửu lưu, nhưng cùng danh sư, thuần thú sư các loại thượng cửu lưu chức nghiệp đồng dạng, đối tu vi yêu cầu vô cùng nghiêm khắc."),t("br"),t("br"),n._v("Muốn trở thành chân chính thất tinh độc sư, ít nhất đều muốn tu vi đạt tới Thánh vực nhị trọng Thần Thức cảnh mới được, mà muốn trở thành bát tinh, nhất định phải đạt tới ngũ trọng, đây là cứng nhắc tiêu chuẩn, ai cũng không cải biến được."),t("br"),t("br"),n._v("Bất quá, đối với độc sư đẳng cấp, Trương Huyền cũng không để ý."),t("br"),t("br"),n._v("Độc điện yêu cầu đẳng cấp, là bởi vì thực lực không đủ, phối trí đẳng cấp cao độc dược, rất dễ dàng làm bị thương bản thân, thậm chí sẽ bị độc chết."),t("br"),t("br"),n._v("Hắn nắm giữ Thiên Đạo chân khí, bách độc bất xâm, căn bản không dính dáng những thứ này."),t("br"),t("br"),n._v("Đương nhiên, thực lực cũng rất trọng yếu, nếu như thực lực không đủ, cho dù phối trí được rồi độc dược, thi triển không đi ra, cũng là vô dụng."),t("br"),t("br"),n._v("Tựa như hắn hiện tại, Thiên Đạo chân khí mặc dù đáng sợ, có thể nghĩ muốn độc chết Thánh vực tứ trọng cường giả, nhất định phải lừa dối đủ kiểu, đủ loại ngụy trang, bằng vào thực lực, là không thể nào để chân khí, đánh vào đối phương trong cơ thể!"),t("br"),t("br"),n._v("Bởi vì, lấy thực lực của hắn, vô pháp tới gần Thánh vực tứ trọng, thật muốn tới gần, cũng sẽ vô cùng nguy hiểm."),t("br"),t("br"),n._v("Cho dù có thể luyện chế ra độc dược, cũng vô dụng, có thể thi triển đi ra, cũng để đối thủ bên trong độc, mới coi như cường giả chân chính."),t("br"),t("br"),n._v("“Mặc dù học nhiều như vậy dùng độc kỹ xảo cùng tri thức, còn không cách nào làm cho Ngụy Như Yên tỉnh táo, nhưng mà để nàng lại kiên trì nửa năm, thậm chí một năm, vấn đề không lớn! Thời gian dài như vậy, đầy đủ đi Độc điện tổng bộ.”"),t("br"),t("br"),n._v("Thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Hồng Viễn Độc điện thư tịch sửa sang lại Thiên Đạo pháp quyết, mặc dù để hắn nắm giữ vô số dùng độc tri thức, nhưng muốn giải quyết triệt để Tiên Thiên độc thể tai hoạ ngầm, vẫn là làm không được."),t("br"),t("br"),n._v("Bất quá, vô pháp trị tận gốc, để hắn trì hoãn phát tác, vấn đề cũng không lớn."),t("br"),t("br"),n._v("Đứng dậy, mấy bước đi tới Ngụy Như Yên trước mặt, trong cơ thể Thiên Đạo vận chuyển chân khí, lập tức biến thành kịch độc."),t("br"),t("br"),n._v("Tiên Thiên độc thể, muốn cứu chữa, không thể dùng thuốc bổ cùng cố bản bồi nguyên thuốc, chỉ có thể dùng độc!"),t("br"),t("br"),n._v("Lúc trước Ngụy Trường Phong không biết trong đó chi tiết, lại thêm thể chất của nàng còn không có kích hoạt, không có người có thể nhận ra, từ nhỏ đã bổ sung đủ loại thuốc, lúc này mới dẫn đến chứng bệnh không ngừng nhiều lần, nếu không phải gặp được hắn, chỉ sợ sớm đã tử vong."),t("br"),t("br"),n._v("Hít sâu một hơi, ánh mắt ngưng trọng, mười ngón tay tựa như bắn lấy tì bà, đem chuyển biến thành kịch độc chân khí, đánh vào cô gái toàn thân mỗi một chỗ huyệt đạo."),t("br"),t("br"),n._v("Những này khí độc vừa tiến vào trong cơ thể, Ngụy Như Yên nguyên bản thoạt nhìn có chút tái nhợt sắc mặt, dần dần trở nên hồng nhuận."),t("br"),t("br"),n._v("Mặc dù còn vô pháp tỉnh táo, cũng đã không có trở ngại."),t("br"),t("br"),n._v("“Vu hồn ly thể!”"),t("br"),t("br"),n._v("Biết đối phương tình huống, lại kiên trì một năm nửa năm cũng sẽ không có chuyện xảy ra, Trương Huyền lúc này mới thở phào nhẹ nhõm, tinh thần khẽ động, Vu hồn nhảy ra ngoài."),t("br"),t("br"),n._v("Cực lớn hồn phách, kết lấy thủ ấn, rất nhanh chia ra một cái thật nhỏ ý niệm, trực tiếp hướng Ngụy Như Yên mi tâm chui vào."),t("br"),t("br"),n._v("Hắn người học sinh này từ nhỏ đã nhiễm bệnh, tại cha dưới cánh chim sống sót, đối với người không có bất kỳ cái gì tâm cơ, thật muốn đi để danh sư đều kiêng kỵ Độc điện, không có người bảo vệ lời nói, tất nhiên sẽ bị nuốt lấy một lớp da!"),t("br"),t("br"),n._v("Cho nên, lưu lại một đạo ý niệm để phòng bất trắc."),t("br"),t("br"),n._v("Làm xong những này, dừng lại một chút, lại đem duy nhất một phần Thiên Đạo chi sách lấy ra ngoài, ngón tay điểm một cái, đưa vào đối phương mi tâm."),t("br"),t("br"),n._v("Đây là trước đó thu Tưởng Thanh Cầm viện trưởng là học sinh thời điểm hình thành, một mực không có cam lòng sử dụng, vì học sinh, cũng coi như không là cái gì."),t("br"),t("br"),n._v("Làm xong những này, Trương Huyền lúc này mới hoàn toàn thở phào nhẹ nhõm, Vu hồn trở lại thân thể."),t("br"),t("br"),n._v("Hắn lưu lại chính là hồn phách ý niệm, hơn nữa có linh hồn giao hòa tiền lệ, Ngụy Như Yên không hề bài xích, núp ở trong thức hải, người lợi hại hơn nữa cũng không cách nào phát giác, thậm chí chỉ cần không chủ động đi ra, chính mình cái này xếp hạng cuối cùng học sinh, cũng sẽ không biết."),t("br"),t("br"),n._v("Lão sư quan tâm học sinh, chưa hẳn muốn biểu hiện ra ngoài, để hắn biết, có đôi khi nhất cử nhất động, đều mang thật lòng tình cảm... Dù là đối phương hiểu lầm, đều sẽ đi kiên trì, chỉ cần người sau đi cao hơn càng xa, truyền thụ cho tri thức, dùng đến thực tế chỗ, liền sẽ cảm thấy vui mừng, theo đáy lòng cao hứng."),t("br"),t("br"),n._v("Đây không phải ngốc, cũng không phải mong muốn đơn phương, mà là bởi vì bọn hắn trong lòng đều có một chữ nặng càng Thái Sơn... Sư!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1017"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1019"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);