(window.webpackJsonp=window.webpackJsonp||[]).push([[361],{716:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1301-nguoi-muon-co-the-ngu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1301-nguoi-muon-co-the-ngu"}},[n._v("#")]),n._v(" Chương 1301: Ngươi muốn có thể ngủ!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1301: Ngươi muốn có thể ngủ!"),t("br"),t("br"),n._v("“Đáng ghét, đáng ghét!”"),t("br"),t("br"),n._v("Toàn thân run rẩy, Trương Khiêm vẻ mặt biến thành màu đen."),t("br"),t("br"),n._v("Vốn trông cậy vào có khả năng vượt xa bình thường phát huy, nhất phi trùng thiên, không nghĩ tới, cuối cùng vẫn là không được tuyển, mà tên kia, vậy mà vượt qua hắn một cái thứ tự, trực tiếp thông qua!"),t("br"),t("br"),n._v("Trước đó nghĩ đến một khi trở thành lớp tinh anh học viện, địa vị quyền hạn tăng thêm, giáo huấn đối phương, dễ như trở bàn tay, làm sao đều ngờ tới là kết quả này."),t("br"),t("br"),n._v("“Vân ca, chính là cái này gọi Trương Huyền gia hỏa, ra tay với ta, danh ngạch của ta cũng là bị hắn dồn xuống đi!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, ngẩng đầu nhìn về phía cách đó không xa một thanh niên."),t("br"),t("br"),n._v("Trương gia đi vào lớp tinh anh một cái khác thiên tài, Trương Vân Phong, bài danh thứ bốn mươi sáu."),t("br"),t("br"),n._v("“Ngươi sự tình ta biết rồi, còn không phải chính ngươi kiếm chuyện?” Mí mắt vừa nhấc, Trương Vân Phong hừ một tiếng: “Băng Nguyên cung người, ta cũng không dám đi trêu chọc, ngươi vừa đến liền muốn biểu hiện, bị người giáo huấn cũng là đáng đời!”"),t("br"),t("br"),n._v("“Đúng, ta tài nghệ không bằng người, tự rước lấy nhục, ta nhận, nhưng cái tên này ngang nhiên miệt thị Trương gia, nói Trương gia người, chỉ cần nhìn thấy cô gái xinh đẹp, liền ưa thích chặn ngang một gậy... Trong giọng nói mang theo ngạo mạn... Ta thực sự giận!”"),t("br"),t("br"),n._v("Trương Khiêm nói."),t("br"),t("br"),n._v("“Không cần phép khích tướng! Mặc kệ hắn coi thường không có coi thường Trương gia, ngươi biểu lộ thân phận, còn muốn động thủ, quả thực có chút cuồng vọng, yên tâm đi, chờ một hồi, ta sẽ giáo huấn một lần, cho hắn biết, điểm này thiên tư, tại Thánh tử điện còn không có ngông cuồng tư cách!”"),t("br"),t("br"),n._v("Trương Vân Phong thản nhiên nói."),t("br"),t("br"),n._v("“Đa tạ Vân ca...”"),t("br"),t("br"),n._v("Nghe được đáp ứng, Trương Khiêm ánh mắt sáng lên, hưng phấn mà nắm đấm xiết chặt."),t("br"),t("br"),n._v("Vị này Trương Vân Phong, huyết mạch so với hắn tinh thuần không ít, đã đạt đến nhất đẳng, không chỉ có như vậy, tu vi cũng mạnh mẽ hơn hắn, đạt tới Lĩnh Vực cảnh sơ kỳ, thật muốn ra tay, có thể đoán được, cái kia gọi Trương Huyền gia hỏa tất nhiên sẽ bị đánh bại, thê thảm vô cùng."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Vân Phong không nói thêm gì nữa."),t("br"),t("br"),n._v("Đối với hắn mà nói, loại địa phương nhỏ này xuất hiện gia hỏa, chẳng qua trong tu luyện nhạc đệm mà thôi, không đáng để ở trong lòng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Phía dưới là ban phổ thông học sinh!”"),t("br"),t("br"),n._v("Không để ý tới đám người kinh ngạc, Hạo Vũ trưởng lão lần nữa vung tay lên."),t("br"),t("br"),n._v("Bầu trời như là ngân quang lóng lánh, cái này đến cái khác tên, lóe lên mà ra."),t("br"),t("br"),n._v("Rất nhanh tất cả tên đều hiện ra, nhìn thấy phía trên quả nhiên có Trương Cửu Tiêu, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Cứ việc bài danh không cao, đạt đến hơn 460, lại thật thông qua được khảo hạch, thành Thánh tử điện một thành viên."),t("br"),t("br"),n._v("Cùng bọn hắn cùng đi, Mã Minh Hải mấy người cũng thành công tấn cấp, hết thảy bảy cái, mặc dù không tính là thật tốt, nhưng dựa theo tỉ lệ, phải rất khá."),t("br"),t("br"),n._v("“Phía dưới là dự thính học viên!”"),t("br"),t("br"),n._v("Tuyên bố xong bình thường học sinh, Hạo Vũ trưởng lão thanh âm tiếp tục vang lên, ngay sau đó lại xuất hiện năm trăm người có tên đơn."),t("br"),t("br"),n._v("Thánh tử điện chính thức tuyển nhận học viên, tinh anh cộng thêm bình thường, hết thảy khoảng năm trăm người, dự thính còn có năm trăm."),t("br"),t("br"),n._v("Nương theo cái này năm trăm người có tên đơn xuất hiện, hưng phấn cùng thất vọng thanh âm liên tiếp."),t("br"),t("br"),n._v("Trương Huyền nhìn một chút."),t("br"),t("br"),n._v("Bọn họ theo Tiềm Xung đế quốc phương hướng tới ba mươi ba người, tấn cấp bảy cái, còn có bốn cái thành dự thính, vượt qua một phần ba, thành tích xem như không sai."),t("br"),t("br"),n._v("“Trong danh sách người lưu lại, những người khác mời rời khỏi đi!”"),t("br"),t("br"),n._v("Danh sách đã xuất, Hạo Vũ trưởng lão lại không khách khí, bàn tay lớn vẫy một cái, mang theo trục khách chi ý."),t("br"),t("br"),n._v("Bình thường, dự thính đều không có trúng tuyển tài tuấn, đành phải buồn bã rời khỏi."),t("br"),t("br"),n._v("Khảo hạch liền là khảo hạch, bất kể ở nơi nào, đều là đồng dạng tàn khốc."),t("br"),t("br"),n._v("Một khi thất bại, muốn xoay người, nhất định phải trả giá vượt qua trước kia càng đa số hơn lần đánh đổi."),t("br"),t("br"),n._v("Không đến mười phút đồng hồ, trên quảng trường chỉ còn lại có vừa mới bị lưu lại một ngàn người."),t("br"),t("br"),n._v("“Hoan nghênh chư vị, trở thành Thánh tử điện một thành viên!”"),t("br"),t("br"),n._v("Hạo Vũ trưởng lão chỉ vào không trung, vô số đời biểu thân phận lệnh bài lập tức xuất hiện tại mỗi người trong tay: “Đây là thân phận của các ngươi ngọc bài, phía trên sẽ tự động biểu hiện, làm học viên đối ứng chức trách!”"),t("br"),t("br"),n._v("Trương Huyền nhìn về phía hạ xuống ngọc bài."),t("br"),t("br"),n._v("Dự thính là màu lam nhạt, bình thường học viên là màu vàng nhạt, mà hắn thân là lớp tinh anh học viên, thì là màu đen."),t("br"),t("br"),n._v("Cúi đầu nhìn lại, trên ngọc bài điêu khắc một cái triện viết “Thánh” chữ, mang theo cổ điển mênh mông hương vị, bốn phía từng đạo hoa văn, như cùng người ánh mắt, tầng tầng lớp lớp, không nhìn thấy phần cuối."),t("br"),t("br"),n._v("Ngón tay búng một cái, một giọt máu, hòa vào trong đó, lập tức cảm giác cùng cái ngọc bài này dung hòa cùng một chỗ, lại đi vào Thánh tử điện, sẽ không đi bị trong đó trận pháp ảnh hưởng, có thể tự do thông suốt."),t("br"),t("br"),n._v("Trương Cửu Tiêu lúc này cũng luyện hóa ở trong tay ngọc bài, một mặt thoả mãn."),t("br"),t("br"),n._v("Thân là bàng chi con cháu, vốn không được coi trọng, nhưng trở thành Thánh tử điện một thành viên, địa vị liền rõ ràng khác biệt."),t("br"),t("br"),n._v("“Năm nay... Có lẽ có tư cách tham gia niên hội!”"),t("br"),t("br"),n._v("Con mắt sáng lên."),t("br"),t("br"),n._v("Trương gia hàng năm niên kỉ hội, đều là hạch tâm con cháu, cùng có thành tựu bàng chi mới có thể đi tới, hắn là không thể nào đi, mà bây giờ, trở thành Thánh tử điện năm trăm người một thành viên, địa vị nhanh chóng dâng lên, lại trở lại gia tộc, loại cấp bậc này hội nghị, đã có tư cách."),t("br"),t("br"),n._v("“Đây đều là Trương sư cho ta!”"),t("br"),t("br"),n._v("Biết có thể có thành tích bây giờ, cùng trước mắt vị này trợ giúp có cửa ải cực kỳ lớn hệ, Trương Cửu Tiêu thở ra một hơi, tràn đầy cảm kích."),t("br"),t("br"),n._v("Hắn mặc dù là Trương gia con cháu, nhưng ở Trương gia cũng không được cái gì tính thực chất trợ giúp, ngược lại vị này Trương sư, trên đường đi chăm chỉ không ngừng, chưa hề bởi vì ban đầu là đối thủ, mà trong lòng còn có khúc mắc, bất kể là nhân phẩm vẫn là đối kiến thức lý giải, đều để hắn phục sát đất."),t("br"),t("br"),n._v("Đang tại cảm khái, trên bầu trời Hạo Vũ trưởng lão thanh âm vang lên lần nữa: “Khảo hạch kết thúc, thân phận của chư vị xác định, ta suy đoán, nhất định còn có không ít người trong lòng còn có không cam lòng, mang theo lẫn nhau tương đối ý nghĩ, yên tâm, Thánh tử điện cho các ngươi cơ hội này, phía dưới, là một hồi khiêu chiến thi đấu!”"),t("br"),t("br"),n._v("“Quá tốt rồi, ta liền đợi đến như vậy cơ hội...”"),t("br"),t("br"),n._v("“Khiêu chiến thi đấu đồng dạng rất ít mở, không nghĩ tới để chúng ta đuổi kịp, lần này nhất định phải trở thành Thánh tử điện học viên!”"),t("br"),t("br"),n._v("Một hồi hưng phấn, đám người từng cái con mắt sáng lên."),t("br"),n._v("Có thể tới Thánh tử điện, bình thường đều trước thời hạn làm bài học, khiêu chiến thi đấu, cứ việc không phải mỗi lần nhất định phải, nhưng cũng xuất hiện qua không biết bao nhiêu lần, có thể thẩm tra đến."),t("br"),t("br"),n._v("“Cái gọi là khiêu chiến, mười phần đơn giản, dự thính có thể khiêu chiến ban phổ thông thành viên, một khi thành công, có thể lấy mà thay vào! Ban phổ thông người, cũng có thể khiêu chiến lớp tinh anh thành viên, mỗi người chỉ có một lần cơ hội khiêu chiến, thất bại lại nhận đối ứng trừng phạt.”"),t("br"),t("br"),n._v("Hạo Vũ trưởng lão nói: “Những này, chắc hẳn chư vị đã có hiểu biết, thực sự không hiểu, thân phận ngọc bài bên trong cũng có ghi chép!”"),t("br"),t("br"),n._v("“Có ghi chép?”"),t("br"),t("br"),n._v("Trương Huyền thần thức mở rộng, hướng lòng bàn tay ngọc bài nhìn sang, quả nhiên thấy được Thánh tử điện rất nhiều quy định, trong đó có liên quan tới khiêu chiến thi đấu."),t("br"),t("br"),n._v("Cuộc thi đấu này, là vì công bằng, cường giả bên trên, người yếu bên dưới, khôn sống mống chết, đồng dạng, lấy cấp thấp thân phận, khiêu chiến cao cấp, đồng dạng là đại bất kính, thất bại, thì là thấy không rõ thực lực bản thân, nhất định phải chịu đến trừng phạt."),t("br"),t("br"),n._v("Có rất nhiều hình phạt, có thì là giao nộp đền bù, còn có chính là xin lỗi... Cũng không thống nhất."),t("br"),t("br"),n._v("Bất quá, đều cần người thắng trận thoả mãn mới được."),t("br"),t("br"),n._v("Nói cách khác, khiêu chiến có thể, nhưng cũng muốn lượng sức mà đi."),t("br"),t("br"),n._v("Nghi ngờ Thánh tử điện xông sơn môn khảo hạch kết quả, đương nhiên cũng muốn chịu đến đối ứng trừng phạt."),t("br"),t("br"),n._v("“Bắt đầu!”"),t("br"),t("br"),n._v("Hét lớn một tiếng, Hạo Vũ trưởng lão bàn tay lớn vẫy một cái, trong sân rộng gian, một cái hình tròn lôi đài chậm rãi từ dưới đất hiện lên."),t("br"),t("br"),n._v("Hàn Trúc trưởng lão nói quy tắc, đám người vây quanh ở bốn phía, từng cái nhớ lại trên danh sách giới thiệu cùng tên người ý định khiêu chiến."),t("br"),t("br"),n._v("“Ta khiêu chiến bình thường học viên bên trong người thứ 500 Tiết Ngưng!”"),t("br"),t("br"),n._v("Một cái tiếng la vang lên."),t("br"),t("br"),n._v("Chính là dự thính học viên bên trong một cái."),t("br"),t("br"),n._v("“Vị này Triệu Kỳ, là dự thính đệ tử đệ nhất, nói cách khác... Vừa vặn bài danh thứ 501, không có cam lòng, khiêu chiến người thứ 500!”"),t("br"),t("br"),n._v("“Cũng khó trách, hai người này chênh lệch điểm số cũng không nhiều!”"),t("br"),t("br"),n._v("“Đúng vậy a, đổi lại ai chịu định cũng muốn phấn đấu một hồi!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trong đám người có người đi ra, tất cả đều nhẹ gật đầu."),t("br"),t("br"),n._v("Loại này khiêu chiến, cũng không phải là nghi ngờ Thánh tử điện công bằng, mà là cảm thấy không cam tâm."),t("br"),t("br"),n._v("Hai người không kém nhiều, người sau chỉ là dự thính, cái trước lại thành Thánh tử điện một thành viên, đổi lại ai, đều sẽ có chút không phục, muốn mượn cơ hội này, chứng minh bản thân."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Nói hết khiêu chiến, Triệu Kỳ nhảy lên đài cao."),t("br"),t("br"),n._v("“Như ngươi mong muốn!”"),t("br"),t("br"),n._v("Thân thể nhảy lên, ban phổ thông học sinh bên trong, một thanh niên cũng tới đến phía trên."),t("br"),t("br"),n._v("Trương Huyền nhìn một cái."),t("br"),t("br"),n._v("Không phải người khác, chính là tại Tiềm Xung đế quốc, vì thưởng thiên thần say cùng hắn ra tay đánh nhau, hơn nữa hao tổn mười lăm viên tinh nguyên linh thạch thượng phẩm Thánh Nhân Tiết gia tiết Tam công tử."),t("br"),t("br"),n._v("Lúc trước chiến đấu, cái tên này xoay chuyển quá nhanh, bản thân chuyển nôn, bị không biết bao nhiêu người chế giễu."),t("br"),t("br"),n._v("Không nghĩ tới ngắn ngủi hơn mười ngày, biết hổ thẹn sau dũng, tu vi tiến thêm một bước, đã đạt đến ngụy xuất khiếu cảnh giới."),t("br"),t("br"),n._v("Loại thực lực này, tại khảo hạch Thánh tử điện tất cả thiên tài bên trong, không tính là gì, nhưng cũng tuyệt đối là thiên tư không yếu."),t("br"),t("br"),n._v("“Tiết Ngưng, xông sơn môn ngươi khảo hạch cái gì? Phương diện nào đi nữa tiếp nhận đối phương chiến đấu?”"),t("br"),t("br"),n._v("Hàn Trúc trưởng lão nói."),t("br"),t("br"),n._v("Thánh tử điện tuyển chọn nhân tài, cũng không phải là chỉ đơn thuần thực lực, còn có rất nhiều tổng hợp suy nghĩ, đã tiếp nhận khiêu chiến, tự nhiên muốn lấy bản thân am hiểu nhất bộ phận nghênh địch."),t("br"),t("br"),n._v("“Ta sở dĩ thông qua tuyển chọn, là bởi vì tốc độ cùng lực công kích, đối phương chỉ cần đem tu vi áp chế cùng ta tương đồng, chiến đấu bên trong có khả năng vượt qua, liền coi như ta thua!”"),t("br"),t("br"),n._v("Đầu lâu nâng lên, Tiết Ngưng trong mắt mang theo tràn đầy tự tin."),t("br"),t("br"),n._v("Thánh Nhân Tiết gia, cứ việc chỉ là cái tam lưu quý tộc, nhưng trong đó tuyệt chiêu , không thể khinh thường."),t("br"),t("br"),n._v("Nếu không phải gặp được Trương Huyền tên biến thái này, cùng cấp bậc, tuyệt đối được xưng tụng vô địch."),t("br"),t("br"),n._v("Nếu không, cũng sẽ không thông qua xông sơn môn khảo hạch, trở thành Thánh tử điện một thành viên."),t("br"),t("br"),n._v("“Áp chế tu vi cùng ngươi chiến đấu, có thể!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Triệu Kỳ rống to một tiếng, tu vi nhanh chóng hạ thấp, rất nhanh liền cùng đối phương tương đương, đạt đến ngụy xuất khiếu tình trạng."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Hai người lập tức chiến đấu cùng một chỗ."),t("br"),t("br"),n._v("Không hổ là thiên tài, hai người chiêu số, đều mười phần tinh diệu, một chiêu so một chiêu mạnh, các loại lực lượng lăn mình, trong thời gian ngắn khó mà phân ra thắng bại."),t("br"),t("br"),n._v("“Trương sư... Ngươi nói bọn họ ai có thể thắng?”"),t("br"),t("br"),n._v("Trương Cửu Tiêu nhìn qua."),t("br"),t("br"),n._v("“Tiết Ngưng hẳn là cao hơn một bậc!” Trương Huyền nói."),t("br"),t("br"),n._v("“Ta cũng cảm thấy hắn sẽ chiến thắng!”"),t("br"),t("br"),n._v("Cảm khái một câu, Trương Cửu Tiêu nhớ tới một việc, nhịn không được nhìn qua: “Đúng rồi, Trương sư, ngươi là như thế nào xông sơn môn thành công, trở thành lớp tinh anh học viên? Có thể nói một lần thành công của ngươi kinh nghiệm ư?”"),t("br"),t("br"),n._v("Trước mắt vị này, tu vi, danh sư, giám định, thư hoạ... Không có chỗ nào không biết, không gì không giỏi, hắn thực sự nghĩ không ra đến cùng dựa vào cái gì, khảo hạch thành công, trong lòng tràn ngập tò mò."),t("br"),t("br"),n._v("“Kinh nghiệm?”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền một mặt cảm khái: “Đầu tiên... Ngươi muốn có thể ngủ!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1309"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1311"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);