(window.webpackJsonp=window.webpackJsonp||[]).push([[368],{723:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1308-cho-o-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1308-cho-o-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1308: Chỗ ở 【 Canh [3], 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1308: Chỗ ở "),t("br"),t("br"),n._v("Linh Hư kiếm, vẫn luôn khóa tại Thiên Công bệ đá bên trên, người sau cũng bị hắn xem như vỏ kiếm."),t("br"),t("br"),n._v("Bệ đá là Thiên Công vật phẩm, không có pháp bảo cấp bậc, thật muốn nói đẳng cấp lời nói, quả thực không đến thượng phẩm, bởi vậy, sử dụng đi ra, không tính không tuân theo quy định."),t("br"),t("br"),n._v("Chỉ là... Thứ này ngoài dự đoán nặng vô cùng, Lĩnh Vực cảnh cường giả, đều không cầm lên được, bằng không thì cũng không có khả năng khóa lại trường kiếm, không cách nào tránh thoát."),t("br"),t("br"),n._v("Giờ phút này, đột nhiên tế ra, cứ thế mà đập ở trên thân, không có tại chỗ tử vong, coi như mạng lớn."),t("br"),t("br"),n._v("“Đây là vỏ kiếm của hắn?”"),t("br"),t("br"),n._v("“Phía trên còn giống như thật cắm một chuôi kiếm!”"),t("br"),t("br"),n._v("“Tại sao có thể có như thế đại?”"),t("br"),t("br"),n._v("“Trương Vân Phong xong...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Dưới đài yên lặng, không ít người đều nuốt ngụm nước bọt, mí mắt nhảy loạn."),t("br"),t("br"),n._v("Quá vô sỉ đi!"),t("br"),t("br"),n._v("Người ta chỉ là bình thường đồ vật, ngươi dùng lớn như vậy... Ai chịu được?"),t("br"),t("br"),n._v("Không ít người âm thầm vui mừng."),t("br"),t("br"),n._v("May mắn không có đi qua khiêu chiến, nếu không, tuyệt đối sẽ bị đánh chết tươi!"),t("br"),t("br"),n._v("Bất quá, quy củ là Trương Vân Phong định, hắn chỉ là dựa theo thi hành, cũng trước thời hạn chào hỏi... Bị đánh thành như vậy, thật đúng là không trách được người khác!"),t("br"),t("br"),n._v("Không để ý tới đám người khiếp sợ, Trương Huyền bàn tay một trảo, đem “Vỏ kiếm” thu hồi trữ vật giới chỉ, mấy bước đi tới Trương Vân Phong trước mặt, chỉ thấy hắn đầu lưỡi duỗi ra, mí mắt trở nên trắng, thân thể vặn thành bánh quai chèo, trên mặt còn mang theo vẻ mặt sợ hãi."),t("br"),t("br"),n._v("Bộ dáng không nói ra được thê thảm."),t("br"),t("br"),n._v("Ngón tay một điểm, đang định đem Thiên Đạo chân khí rót vào trong cơ thể giúp một tay chữa thương, lần nữa tiếng gió rít gào, một thanh niên lần nữa xông lại."),t("br"),t("br"),n._v("“Dừng tay!”"),t("br"),t("br"),n._v("Khuôn mặt trầm thấp, thanh niên tại Trương Vân Phong mạch môn sờ một lần, biết chỉ là ngắn ngủi hôn mê, cũng không lo ngại, lúc này mới đưa khẩu khí, này thêm một viên tiếp theo đan dược, bàn tay một trảo, người sau chậm rãi bay lên, hướng đám người dưới đài rơi xuống đi qua."),t("br"),t("br"),n._v("“Đa tạ hạ thủ lưu tình, tại hạ Trương Trác, Trương Vân Phong là ta đường đệ, trước đó có nhiều lỗ mãng, xin hãy tha lỗi!”"),t("br"),t("br"),n._v("Làm xong những này, Trương Trác ôm quyền."),t("br"),t("br"),n._v("Trên mặt nhìn không ra là thật tâm thực lòng, vẫn là tức giận."),t("br"),t("br"),n._v("“Khách khí!” Trương Huyền đáp lễ."),t("br"),t("br"),n._v("Vị này Trương Trác, vừa rồi lớp tinh anh trên danh sách thấy qua, bài danh thứ ba mười một vị, cũng là Trương gia người."),t("br"),t("br"),n._v("“Trương huynh theo Thanh Nguyên đế quốc mà đến, lại có thể tại bằng chừng ấy tuổi, nắm giữ loại thực lực này, tại hạ bội phục, hi vọng có ngày cũng có thể lĩnh giáo cao chiêu, có điều, hôm nay là nhập môn tốt đẹp ngày, liền không chậm trễ mọi người thời gian, cáo từ!”"),t("br"),t("br"),n._v("Mắt sáng lên, Trương Trác không nói thêm lời, thân thể nhoáng một cái, theo đài cao bay xuống, động tác nhẹ nhàng, thân pháp tinh diệu."),t("br"),t("br"),n._v("Thấy hắn xoay người rời đi, Trương Huyền cũng biết, tiếp tục vướng víu, cũng không phải là chuyện tốt, cũng trở về đến vị trí của mình."),t("br"),t("br"),n._v("Chiến đấu kết thúc, Hạo Vũ trưởng lão tiếp tục hỏi thăm."),t("br"),t("br"),n._v("Ngay sau đó khiêu chiến thi đấu, lại chiến đấu mấy trận."),t("br"),t("br"),n._v("Vừa rồi Trương Huyền cứ việc dùng tương tự gian lận thủ đoạn, đem Trương Vân Phong đánh ngất xỉu, nhưng thực lực chân chính, những người khác cũng nhìn thấy, lại không có người không có mắt, đến tìm phiền phức."),t("br"),t("br"),n._v("Một canh giờ sau, lại không người khiêu chiến, Hạo Vũ trưởng lão lúc này mới nhẹ nhàng cười một tiếng: “Được rồi, các vị nơi ở, đã an bài xong xuôi, có thể đang đại biểu thân phận trên ngọc bài thẩm tra, ngày hôm nay nghỉ ngơi, ngày mai đi mỗi người lớp đưa tin, chính thức lên lớp!”"),t("br"),t("br"),n._v("Đám người xác nhận, Trương Huyền đem ngọc bài lấy ra, quả nhiên thấy phía trên hiện lên chỗ mình ở lộ tuyến."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Gọi Trương Cửu Tiêu một tiếng, đi đầu hướng Thánh tử điện phương hướng đi đến."),t("br"),t("br"),n._v("Xuyên qua quảng trường, là cái đồ sộ cửa, phía trên ba chữ to “Thánh tử điện”, phía bên phải trên vách tường, thì viết cái “Nho” chữ."),t("br"),t("br"),n._v("Không biết mấy cảnh tác phẩm, cho người ta một loại thâm thúy cảm giác, phảng phất nhìn cái chữ này, đang nhìn một cái hoàn chỉnh thế giới."),t("br"),t("br"),n._v("“Những chữ này, là năm đó Thánh tử điện vị thứ nhất điện chủ, Khôi Thánh lưu lại, thường xuyên tìm hiểu, đối thư hoạ nghề nghiệp tiến bộ, có tác dụng cực lớn!”"),t("br"),t("br"),n._v("Trương Cửu Tiêu trong mắt tràn đầy hưng phấn."),t("br"),t("br"),n._v("Hắn là thất tinh đỉnh phong cấp bậc thư họa sư, biết bốn chữ này trân quý."),t("br"),t("br"),n._v("Loại cấp bậc này thư hoạ, bình thường là không dễ dàng có thể nhìn thấy."),t("br"),t("br"),n._v("Mặc dù đứng sừng sững ở nơi này, vài vạn năm, nhưng không có mảy may tổn thương, ngăn nắp chói mắt, như là vừa mới viết đồng dạng."),t("br"),t("br"),n._v("“Những chữ này, có khả năng bản thân hấp thu linh khí, duy trì vết mực không tổn hại...”"),t("br"),t("br"),n._v("Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Không nghĩ tới cái kia tại huyễn cảnh bên trong mài gậy sắt gia hỏa, đối thư hoạ lý giải sâu như vậy, có thể viết ra như vậy có ý cảnh kiểu chữ."),t("br"),t("br"),n._v("Sớm biết liền không ngủ, cùng đối phương học một ít thư hoạ, có lẽ ba ngày thời gian, cũng có thể tiến bộ không ít."),t("br"),t("br"),n._v("“Đây là đại lục học phủ cao nhất, ta... Rốt cục xem như tiến đến!”"),t("br"),t("br"),n._v("Biết những chữ này, liền lơ lửng tại cửa ra vào, không biết chạy trốn, sớm tối đều có cơ hội nhìn, Trương Cửu Tiêu không tại rầu rĩ, mà là nhìn về phía trước mắt rộng lớn cửa, nhịn không được cảm khái, kích động hốc mắt ửng hồng."),t("br"),t("br"),n._v("Thân là gia tộc bàng chi con cháu, hắn trả giá, muốn so hạch tâm con cháu còn nhiều hơn, vì có thể đến nơi đây, chịu đến gia tộc chú trọng, nhiều năm trước liền ly biệt quê hương, đi tới Thanh Nguyên đế quốc, chỉ là vì một cái kia... Ở gia tộc mãi mãi cũng không có được danh ngạch!"),t("br"),t("br"),n._v("Vốn cho rằng, đời này có thể đi vào là một loại hy vọng xa vời, không nghĩ tới, cuối cùng vẫn hoàn thành."),t("br"),t("br"),n._v("“Huyết mạch chỉ có thể nhường ngươi điểm khởi đầu so với người bình thường cao một chút, chân chính muốn đi đến đỉnh phong, còn cần bản thân cố gắng!”"),t("br"),t("br"),n._v("Biết cảm thụ của hắn, Trương Huyền cười cười, an ủi một câu."),t("br"),t("br"),n._v("Tinh thuần huyết mạch, ưu tú thiên phú, quả thực là người người đều khát vọng, có điều, những này đều chỉ để cho người ta cất bước cao điểm mà thôi, chân chính muốn đi đến đỉnh phong, cần vẫn là không ngừng cố gắng cùng ngoan cường phấn đấu."),t("br"),n._v("Tựa như hắn, không có đặc thù thiên phú, cũng không có lão sư tốt, cùng siêu cường huyết mạch, có thể nói một nghèo hai trắng... Đồng dạng theo Thiên Huyền vương quốc từng bước một đi tới, đạt đến hiện tại tình trạng."),t("br"),t("br"),n._v("Không phải hắn thiên phú cao, cũng không phải hắn vận khí tốt, mà là hắn có kiên trì hăm hở tiến lên tâm!"),t("br"),t("br"),n._v("Không có loại này không ngừng rêu rao, không ngừng tự kiểm điểm, khát vọng trưởng thành ý niệm, là tuyệt không có khả năng trở thành cường giả."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Trương Cửu Tiêu gật đầu."),t("br"),t("br"),n._v("Đi vào Thánh tử điện, lập tức cảm thấy linh khí trong đó, so bên ngoài nồng nặc gấp bội, trọng yếu nhất chính là, ẩn chứa trong đó một loại đặc thù khí tức, tựa hồ tại nơi này tu luyện, có thể sảng khoái tinh thần, tư duy càng thêm rõ ràng."),t("br"),t("br"),n._v("“Khó trách có khả năng thu hút nhiều người như vậy tới, quả nhiên là tu luyện Thánh địa!”"),t("br"),t("br"),n._v("Như vậy linh khí nồng nặc, Thiên Đạo công pháp đầy đủ lời nói, coi như không dùng tìm kiếm linh thạch, tu vi cũng tiến bộ cực nhanh!"),t("br"),t("br"),n._v("Không có gì bất ngờ xảy ra, đồng dạng có thể trong vòng mười năm trở thành cửu tinh danh sư, chân chính đứng ở thế giới chi đỉnh."),t("br"),t("br"),n._v("Chỗ ở, không tính quá xa, dựa theo trên ngọc bài bản đồ, rất nhanh liền đi tới trước mặt, là cái độc lập biệt viện, linh khí so bốn phía càng thêm nồng đậm, đi lại trong đó, dù cho không tu luyện, cũng có thể nhường thân thể khoẻ mạnh, bách bệnh không sinh."),t("br"),t("br"),n._v("Trương Cửu Tiêu còn chưa tới đến trước mặt, liền cùng hắn tách ra, hắn là ban phổ thông học viên, chỉ có thể ở tập thể ký túc xá, xa xa không có đạt tới hắn loại đãi ngộ này."),t("br"),t("br"),n._v("Cùng lúc trước Hồng Viễn danh sư học viện đồng dạng, đỉnh phong học sinh, cư trú chính là khu biệt thự, bình thường học sinh, chỉ có thể ở tại ký túc xá."),t("br"),t("br"),n._v("Lớp tinh anh chỗ của người ở, so với đối phương muốn tốt hơn nhiều, có điều, bản thân bài danh cuối cùng, vị trí biệt viện, xem như tít ngoài rìa, kém nhất."),t("br"),t("br"),n._v("Đi tới trước cửa, lấy ra ngọc bài, phía ngoài phong ấn lập tức mở ra, Trương Huyền đẩy cửa, đang muốn đi vào, chỉ thấy một cái thướt tha thân ảnh, đi tới bên cạnh mình sân nhỏ, ngừng lại."),t("br"),t("br"),n._v("Chính là Băng Nguyên cung thiên tài Trần Nhạc Dao!"),t("br"),t("br"),n._v("“Đa tạ Nhạc Dao tiên tử vừa rồi tại trong tỉ thí báo cho!”"),t("br"),t("br"),n._v("Trương Huyền ôm quyền."),t("br"),t("br"),n._v("Trước đó nếu không phải đối phương truyền âm nói, Trương gia huyết mạch thiên phú là nhường thời gian rút ngắn, muốn vượt qua kích hoạt huyết mạch Trương Khiêm, khả năng còn cần tốn thêm một chút công phu."),t("br"),t("br"),n._v("“Trương sư khách khí...”"),t("br"),t("br"),n._v("Trần Nhạc Dao vội vàng đáp lễ: “Đây là ta phải làm!”"),t("br"),t("br"),n._v("Trước mắt vị này chính là thiếu cung chủ lão sư, càng là đối với nàng có nửa sư tình nghĩa, xuất hiện nguy hiểm, không có tiến lên giúp một tay, đã có chút xấu hổ."),t("br"),t("br"),n._v("“Đây là ngươi chỗ ở?”"),t("br"),t("br"),n._v("Thấy nữ hài, mở ra trước cửa phong ấn, có thể tùy thời đi vào, Trương Huyền có chút kỳ quái."),t("br"),t("br"),n._v("Hắn là xông sơn môn khảo hạch một tên sau cùng, ở tại cái cuối cùng biệt viện, không gì đáng trách, nhưng mà vị này Trần Nhạc Dao, hắn nhìn kỹ, có lẽ là ba mươi mấy tên, không nên cùng mình sát bên ở nha!"),t("br"),t("br"),n._v("“Biết Trương sư ở chỗ này, ta cùng người khác đổi một lần!” Trần Nhạc Dao cười cười."),t("br"),t("br"),n._v("Bên cạnh cái nhà này là thứ 49 tên ở, nàng hơn ba mươi tên biệt viện, dùng để trao đổi, đương nhiên vui vẻ bằng lòng."),t("br"),t("br"),n._v("“Đổi?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Đúng, Trương sư là thiếu cung chủ lão sư, hơn nữa Băng Nguyên cung giải quyết trên việc tu luyện nan đề, là Nhạc Dao đại ân nhân, đương nhiên hy vọng có thể ở gần một chút, cái gì cần chiếu cố, cũng có thể cứ việc chạy tới!”"),t("br"),t("br"),n._v("Trần Nhạc Dao mỉm cười."),t("br"),t("br"),n._v("Thật ra thì nàng là có chút lo lắng Trương gia người sẽ lại tới tìm phiền toái, ở tại trước mặt, cũng có thể lẫn nhau chăm sóc."),t("br"),t("br"),n._v("Bất kể nói thế nào, Trương Khiêm sự tình đều bởi vì nàng mà lên."),t("br"),t("br"),n._v("“Khách khí!”"),t("br"),t("br"),n._v("Trương Huyền bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Khó trách trước mắt vị này, thái độ đại biến, nguyên lai đã biết mình là Triệu Nhã lão sư."),t("br"),t("br"),n._v("Trước đó, không dám nói ra Triệu Nhã là học sinh của mình, sợ đối phương chỉ là Băng Nguyên cung một cái đệ tử bình thường, không biết được loại sự tình này, nói ra phiền phức càng lớn, càng nan giải hơn thả... Nếu biết, liền dễ làm."),t("br"),t("br"),n._v("“Nếu biết ta cùng Triệu Nhã quan hệ, nhưng có cự ly xa đưa tin ngọc bài, ta muốn cùng nàng nói lên vài câu, không biết có thể?”"),t("br"),t("br"),n._v("Trong lòng hơi động, nhớ tới sự kiện, Trương Huyền đầy kéo hi vọng nhìn lại."),t("br"),t("br"),n._v("Lúc trước Triệu Nhã rời đi thời điểm, hắn còn không có đưa tin ngọc bài, lại nói, cho dù có, khoảng cách xa như vậy, cũng không cách nào truyền lại tin tức."),t("br"),t("br"),n._v("Trước mắt vị này là Băng Nguyên cung người, khẳng định có liên lạc tông môn phương pháp, vừa vặn có thể cùng Triệu Nhã liên lạc một chút."),t("br"),t("br"),n._v("Hơn nửa năm không thấy, muốn nói không muốn người học sinh này, đó là giả."),t("br"),t("br"),n._v("“Ta có cự ly xa đưa tin ngọc bài... Bất quá, cần sắp đặt trận pháp tiến hành gia trì, mới có thể liên hệ đến thiếu cung chủ! Trận pháp này, bố trí khá là phiền toái, hơn nữa, thực lực của ta cũng hơi có không đủ, khả năng cần khoảng ba tháng thời gian!”"),t("br"),t("br"),n._v("Trần Nhạc Dao nói."),t("br"),t("br"),n._v("“Ba tháng?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày, nhìn lại: “Không biết, ngươi muốn bố trí trận pháp này là cấp mấy? Ta đối với trận pháp cũng sơ lược thông một hai, có lẽ có thể giúp chút gì không!”"),t("br"),t("br"),n._v("Thời gian ba tháng đối với người khác mà nói, một cái bế quan liền có thể làm được, nhưng đối với hắn mà nói, có chút dài, đơn giản trận pháp lời nói, cũng là có thể giúp một tay sắp đặt một lần."),t("br"),t("br"),n._v("“Là cái cấp tám trận pháp...”"),t("br"),t("br"),n._v("Trần Nhạc Dao nói."),t("br"),t("br"),n._v("“Cấp tám?”"),t("br"),t("br"),n._v("Trương Huyền gật đầu: “Loại cấp bậc này trận pháp, chỉ cần không phải thái phức tạp, ta có thể sắp đặt, có thể hay không... Cho ta nhìn một chút trận đồ?”"),t("br"),t("br"),n._v("(Là bạch ngân minh  tăng thêm ba chương, rạng sáng còn có Canh [4]. Cuối cùng bốn giờ nguyệt phiếu không quăng lãng phí ah!)"),t("br"),t("br"),n._v("P/S: Chương 4 thôi để trưa mai nhé"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1316"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1318"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);