(window.webpackJsonp=window.webpackJsonp||[]).push([[369],{724:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1309-ngoan-nhan-tinh-roi-【-canh-4-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1309-ngoan-nhan-tinh-roi-【-canh-4-】"}},[n._v("#")]),n._v(" Chương 1309: Ngoan Nhân tỉnh rồi 【 Canh [4] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1309: Ngoan Nhân tỉnh rồi "),t("br"),t("br"),n._v("Hắn hiện tại đối với trận pháp lý giải cứ việc chỉ là thất tinh cấp bậc, nhưng mà học tập chính là Thiên Đạo trận pháp, cơ sở vững chắc, cho dù là cấp tám sơ kỳ trận đồ, nghiêm túc học tập, cũng có khả năng nhẹ nhõm sắp đặt thành công."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Tăng thêm truyền tống ngọc bài truyền tống hiệu quả đặc thù trận pháp mà thôi, không tính là cái gì cơ mật, Trần Nhạc Dao không có bất kỳ cái gì giữ lại, cổ tay rung lên, đem một cái bản vẽ đưa tới."),t("br"),t("br"),n._v("Tiện tay nhận lấy, Trương Huyền cúi đầu nhìn lại."),t("br"),t("br"),n._v("Nếu như đem đưa tin ngọc phù, xem như kiếp trước điện thoại, trận pháp này, liền là tháp tín hiệu, có khả năng càng xa khoảng cách tiếp nhận tín hiệu, tăng thêm truyền tin hiệu quả."),t("br"),t("br"),n._v("Trên nguyên lý đơn giản, trên thực tế lại cực kỳ phức tạp."),t("br"),t("br"),n._v("“Mặc dù là cấp tám hạ phẩm trận pháp, nhưng... Lấy ta hiện tại đối với trận pháp lý giải, sắp đặt thành công có thể làm được, bất quá... Cần ít nhất ba ngày thời gian! Hơn nữa không thể có bất luận cái gì sai lầm, quá phiền toái...”"),t("br"),t("br"),n._v("Nhíu nhíu mày."),t("br"),t("br"),n._v("Hắn đối với trận pháp lý giải, cứ việc so với một chút bát tinh hạ phẩm trận pháp sư, đều không hề yếu, nhưng sắp đặt trận pháp này, cần thiết phải chú ý quá nhiều, hơi không cẩn thận, không những không thể tăng cường đưa tin hiệu quả, làm không cẩn thận còn có thể phá hư ngọc bài kết cấu, để hắn báo hỏng... Bởi vậy, cần tiêu tốn cực lớn tâm huyết, căn cứ suy tính, ít nhất phải ba ngày công phu."),t("br"),t("br"),n._v("Ba ngày đối với hắn mà nói, chỉ vì sắp đặt một cái trận pháp... Thực sự quá dài dằng dặc."),t("br"),t("br"),n._v("“Nhìn tới... Muốn trước kiểm tra một cái bát tinh trận pháp sư mới được!”"),t("br"),t("br"),n._v("Biết đối với trận pháp lý giải, còn kém một chút, Trương Huyền suy tư phút chốc, trong lòng có kế hoạch."),t("br"),t("br"),n._v("Hiện tại đối với trận pháp lý giải, còn chưa đủ, bố trí, thời gian sẽ rất lâu, nhưng chỉ cần khảo hạch cái bát tinh trận pháp sư, nhìn liên quan thư tịch, bố trí lại nên rất đơn giản."),t("br"),t("br"),n._v("“Trương sư có thể hay không sắp đặt?”"),t("br"),t("br"),n._v("Thấy hắn cau mày, Trần Nhạc Dao nhìn qua."),t("br"),t("br"),n._v("“Còn kém một chút!” Lắc đầu, Trương Huyền mỉm cười: “Đúng rồi, ngươi cũng đã biết, Thánh tử điện nhưng có địa phương khảo hạch bát tinh trận pháp sư? Ta nghĩ trước khảo hạch một lần, sau đó lại nghiên cứu một chút, làm sao sắp đặt! Như vậy, cũng có thể đáng tin cậy một chút.”"),t("br"),t("br"),n._v("Thánh tử điện loại này đại lục đệ nhất học phủ, hẳn là sẽ có các chức nghiệp khảo hạch địa phương, nếu không, nhiều như vậy phụ tu học sinh, làm sao tiến thêm một bước?"),t("br"),t("br"),n._v("Trọng yếu nhất, không còn phụ tu, danh sư cũng không thể khảo hạch."),t("br"),t("br"),n._v("“Khảo hạch đẳng cấp địa phương, tại ‘Trí’ điện, trước đó ta chuyên môn hỏi thăm qua, biết vị trí cụ thể, nếu không, ta mang Trương sư đi qua đi!”"),t("br"),t("br"),n._v("Nghe hắn muốn lúc này, khảo hạch bát tinh trận pháp sư, Trần Nhạc Dao cười cười, nói."),t("br"),t("br"),n._v("“Làm phiền tiên tử!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Vừa rồi thông qua thân phận ngọc bài, đã biết Thánh tử điện một chút phân chia, cứ việc đồng dạng phân chia tất cả điện đường, nhưng cùng Hồng Viễn danh sư học viện, cùng Chiến Sư đường những địa phương này, hoàn toàn khác biệt."),t("br"),t("br"),n._v("Cái trước dựa theo chức nghiệp, hoặc là tu luyện khác biệt, tiến hành phân chia, sinh ra thuần thú học viện, luyện đan học viện, võ kỹ điện loại hình..."),t("br"),t("br"),n._v("Mà ở trong đó, cứ việc cũng chia làm năm điện, lại cùng chức nghiệp loại hình không quan hệ, lấy năm chữ tiến hành mệnh danh."),t("br"),t("br"),n._v("Theo thứ tự là: Nhân, nghĩa, lễ, trí, thư!"),t("br"),t("br"),n._v("Nhân điện, giáo sư giảng bài cùng học viên học tập vị trí, truyền thụ kiến thức, học tập kỹ năng, truyền thừa văn hóa."),t("br"),t("br"),n._v("Nghĩa điện, các loại thí luyện, rèn luyện đối kháng Dị Linh tộc nhân nơi chốn, tăng lên học sinh ở giữa phối hợp, tăng tiến ở giữa tình cảm."),t("br"),t("br"),n._v("Lễ điện, tỷ thí, chiến đấu nơi chốn."),t("br"),t("br"),n._v("Trí điện, chức nghiệp khảo hạch, học tập công pháp, mở ra trí tuệ vị trí."),t("br"),t("br"),n._v("Thư điện, củng cố nội tâm, rèn luyện phẩm chất địa phương."),t("br"),t("br"),n._v("Nhân nghĩa lễ trí tín, ôn lương cung kiệm nhượng, năm điện liên hợp, tạo thành Thánh tử điện."),t("br"),t("br"),n._v("Để đến đây tu hành học sinh, không chỉ thực lực cường đại, càng quan trọng hơn là, có đức, lễ độ."),t("br"),t("br"),n._v("“Trương sư khách khí!”"),t("br"),t("br"),n._v("Trần Nhạc Dao gật gật đầu, không nói thêm lời, đi đầu dẫn đường."),t("br"),t("br"),n._v("Trí điện, nói là một cái điện đường, trên thực tế cũng không phải là một cái cung điện, mà là cùng Hồng Viễn học viện viện hệ, là cái to lớn chi nhánh."),t("br"),t("br"),n._v("Ẩn chứa trong đó trên trăm loại nghề nghiệp khảo hạch tràng."),t("br"),t("br"),n._v("Hai người tốc độ cực nhanh, đi hơn mười phút, đi tới lối vào, vừa mắt chỗ là cái to lớn mặt tường, không biết tài liệu gì rèn đúc mà thành, chính giữa, điêu khắc một cái chữ lớn, cho người ta một loại ôn nhuận an lành cảm giác."),t("br"),t("br"),n._v("Chính là “Trí” chữ."),t("br"),t("br"),n._v("“Chữ này... Cảm giác rất quen thuộc...”"),t("br"),t("br"),n._v("Trương Huyền kìm lòng không được dừng bước."),t("br"),t("br"),n._v("Đồng dạng là kiểu chữ, nhưng mà trước mắt cái này, cùng phía ngoài “Thánh tử điện” ba chữ, hoàn toàn khác biệt, cứ việc không có tản mát ra mãnh liệt khí tức, lại cho người ta một loại trời cao bỏ bao la cảm giác, hình như lưu lại kiểu chữ người, mang theo rộng thu được lòng dạ, chỉ dẫn thế gian."),t("br"),t("br"),n._v("“Đây là... Khổng sư lưu lại chữ!”"),t("br"),t("br"),n._v("Đang tại khó hiểu, liền nghe đến trong óc một giọng nói vang lên."),t("br"),t("br"),n._v("“Ngoan Nhân, ngươi đã tỉnh?”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Không phải người khác, chính là thôn phệ đại não ý thức sau Ngoan Nhân, ngủ mê tiếp cận một tháng, rốt cục tỉnh lại."),t("br"),t("br"),n._v("“Đúng, may mắn không làm nhục mệnh, rốt cục đem tên kia luyện hóa!”"),t("br"),t("br"),n._v("Ngoan Nhân mang theo âm thanh kích động truyền đến."),t("br"),t("br"),n._v("“Cảm giác thế nào? Thực lực có tiến bộ hay không?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("Ngoan Nhân là lá bài tẩy của hắn, nếu như thực lực bạo tăng, đối với hắn mà nói, tuyệt đối là chuyện tốt."),t("br"),t("br"),n._v("“Bẩm báo chủ nhân, dung hợp đại não cùng con mắt, thực lực của ta, có rất tiến nhanh bước, không có gì bất ngờ xảy ra, Thánh vực thất trọng cường giả, cũng không là đối thủ!”"),t("br"),t("br"),n._v("Cười cười, Ngoan Nhân truyền âm tới."),t("br"),t("br"),n._v("“Thánh vực thất trọng?” Thở hốc vì kinh ngạc, Trương Huyền tắc lưỡi."),t("br"),t("br"),n._v("Một tháng trước, cái tên này mới Xuất Khiếu cảnh đỉnh phong, dung hợp về sau, vậy mà trực tiếp chợt tăng hai cái cấp bậc, thật là đáng sợ!"),t("br"),n._v(" Ai, bên cạnh đều là chút người gì ah, một cái so một cái biến thái, để hắn áp lực thật lớn..."),t("br"),t("br"),n._v("Học sinh của mình, Triệu Nhã, nghe Trần Nhạc Dao giọng nói, tu vi khẳng định so với hắn hiện tại cao nhiều..."),t("br"),t("br"),n._v("Thuộc hạ cũng nắm giữ Thánh vực thất trọng khó mà chống lại thực lực, thật vất vả lấy cái thực lực không mạnh phân thân, bản tôn còn ngày ngày chịu đòn..."),t("br"),t("br"),n._v("“Ta luyện hóa đại não, rất nhiều kiến thức truyền thừa xuống, nhất là vu hồn nhất đạo, một mực đến Thánh vực cửu trọng, tất cả đều có...”"),t("br"),t("br"),n._v("Ngoan Nhân nói tiếp."),t("br"),t("br"),n._v("“Quá tốt rồi!”"),t("br"),t("br"),n._v("Trương Huyền mắt sáng rực lên."),t("br"),t("br"),n._v("Trước đó, theo Mặc Hồn Sinh nơi đó, chỉ lấy được Hóa Phàm cảnh công pháp, đến Thánh vực về sau, bởi vì không có cách nào tu luyện dừng lại một đoạn thời gian, về sau theo Thanh Điền Hoàng nơi đó đạt được Thánh vực trước bốn nặng pháp quyết, giờ phút này cũng đã tu luyện đến đỉnh phong."),t("br"),t("br"),n._v("Có cái này, về sau liền có thể tìm kiếm liên quan tới hồn phách tu luyện pháp quyết, dung hợp suốt ngày đạo công pháp, mau chóng tăng lên vu hồn lực lượng."),t("br"),t("br"),n._v("“Không đúng... Vu hồn pháp quyết, không phải vu hồn sư đặc thù sao? Ngoan Nhân vì sao biết?”"),t("br"),t("br"),n._v("Một cái ý nghĩ trong đầu hiện lên."),t("br"),t("br"),n._v("Căn cứ Mặc Hồn Sinh lưu lại kiến thức, vu hồn công pháp, là vu hồn sư nghề nghiệp truyền thừa, Ngoan Nhân là Dị Linh tộc nhân, làm sao lại cái này? Hơn nữa còn là bản hoàn chỉnh?"),t("br"),t("br"),n._v("“Trước không muốn nhiều như vậy...”"),t("br"),t("br"),n._v("Biết nghĩ những thứ này cũng vô dụng, Trương Huyền trong lòng tiếp tục hỏi: “Loại trừ vu hồn công pháp, nhưng có chân khí pháp môn tu luyện?”"),t("br"),t("br"),n._v("Ngoan Nhân là có thể cùng Khổng sư chiến đấu siêu cấp cường giả, bản thân tu luyện công pháp, tất nhiên cũng mười phần tinh diệu, nếu là có thể học tập, đối với hắn thực lực tăng lên, cũng có rất lớn trợ giúp."),t("br"),t("br"),n._v("“Có Dị Linh tộc nhân pháp môn tu luyện, không thích hợp chủ nhân thể chất, ta hiện tại liền truyền cho ngươi...”"),t("br"),t("br"),n._v("Ngoan Nhân nói hết, Trương Huyền lập tức cảm thấy nhất đạo vô số kiến thức chảy vào trong óc."),t("br"),t("br"),n._v("Hắn cũng có thể làm đến ý niệm truyền lại kiến thức, đối phương dung hợp Ngoan Nhân ký ức, đương nhiên càng thêm dễ dàng."),t("br"),t("br"),n._v("“Quả nhiên không thể tu luyện...”"),t("br"),t("br"),n._v("Xem hết truyền đến kiến thức, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Vu hồn công pháp, có thể tiến hành nghiên cứu, nhưng Dị Linh tộc pháp môn tu luyện, nhưng không có tác dụng gì."),t("br"),t("br"),n._v("Nhân loại thể chất cùng Dị Linh tộc, có rất lớn chênh lệch, coi như hắn tu luyện Thiên Đạo công pháp, cũng không cách nào bù đắp, bởi vậy... Đối phương pháp quyết, coi như hắn thiên phú cực cao, cũng là không cách nào luyện thành."),t("br"),t("br"),n._v("“Đáng tiếc...”"),t("br"),t("br"),n._v("Cảm khái một tiếng, trong lòng nhịn không được than thở."),t("br"),t("br"),n._v("Nếu là có thể tu luyện, về sau sưu tập những công pháp khác liền sẽ đơn giản không ít, tu vi đột phá cũng sẽ trở nên càng thêm dễ dàng."),t("br"),t("br"),n._v("Bất quá bây giờ nhìn tới, vẫn là nghĩ tới quá ngây thơ rồi."),t("br"),t("br"),n._v("Lại trò chuyện một hồi."),t("br"),t("br"),n._v("Mặc dù thôn phệ Ngoan Nhân ký ức, nhưng năm đó bị thương nghiêm trọng, lại thêm cách nhau mấy vạn năm, không ít ký ức, cũng là rất mơ hồ, tỷ như liên quan tới Khổng sư sự tích, chỉ có thể nói ra cái đại khái, nói rõ chi tiết lời nói, cũng liền cũng không nói ra được."),t("br"),t("br"),n._v("“Đúng rồi, ngươi mới vừa nói, đây là Khổng sư lưu chữ, có ý tứ gì?”"),t("br"),t("br"),n._v("Biết đối phương tỉnh lại, không ít chuyện về sau có thể kỹ càng hỏi thăm, không cần thiết chậm trễ thời gian, nhớ tới lời nói mới rồi, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("“Cái này ‘Trí’ chữ, là Khổng sư tự tay viết viết, hơn nữa cảnh giới so trước đó Phong Thánh đài thư tay, cao minh nhiều lắm, nếu như có thể để cho ta thôn phệ... Có thể để cho lực lượng bạo tăng, phát huy ra thực lực mạnh hơn!”"),t("br"),t("br"),n._v("Ngoan Nhân tràn đầy hưng phấn mà thanh âm vang lên."),t("br"),t("br"),n._v("“Thôn phệ?” Trương Huyền nhất thời mặt đen lại."),t("br"),t("br"),n._v("Cái tên này thực có can đảm muốn."),t("br"),t("br"),n._v("Đây chính là Thánh tử điện, đại lục đệ nhất học phủ, ngươi chạy đến thôn phệ người ta Khổng sư lưu lại chữ... Đoán chừng còn chưa hoàn thành, liền sẽ bị tại chỗ đánh chết!"),t("br"),t("br"),n._v("Làm không cẩn thận, bản thân cũng chạy không được!"),t("br"),t("br"),n._v("“Chuyện này, nghĩ như thế nào làm sao quên mất...”"),t("br"),t("br"),n._v("Hừ một tiếng, Trương Huyền tiếp tục hướng trước mắt chữ viết nhìn lại."),t("br"),t("br"),n._v("Khó trách có chút quen mắt, Phong Thánh đài bên trên thư tay, hắn nghiên cứu qua rất nhiều lần, đã sớm đối Khổng sư kiểu chữ quen thuộc."),t("br"),t("br"),n._v("“Cái này” Trí “chữ, mượt mà sung mãn, như là trong đầu lóe ra ánh sáng, thường xuyên quan sát, có thể khiến người ta trí tuệ sung mãn, linh trí mở rộng...”"),t("br"),t("br"),n._v("Thấy hắn đứng ở tại chỗ, Trần Nhạc Dao cười nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cái chữ này, quả thực muốn so Phong Thánh đài Khổng sư để thư lại cường đại hơn rất nhiều, bất kể ý cảnh vẫn là thần vận, đều càng cao minh hơn, liền xem như hắn, nhìn thời gian dài, cũng có thể gột rửa vẩn đục, tư duy trở nên càng thêm nhanh chóng."),t("br"),t("br"),n._v("Không hổ là Khổng sư tác phẩm... Khôi Thánh chữ, tới so sánh, vẫn là kém rất lớn một đoạn."),t("br"),t("br"),n._v("“Khó trách cái khác con em của đại gia tộc, đều có lẽ nơi này, trở thành Thánh tử điện học viên... Không nói cái khác, chỉ nói cái chữ này, liền có định nguyên thần, củng cố tu vi tác dụng, ở đây đột phá... Có khả năng áp chế tâm ma, để cho người ta tạp niệm không sinh!”"),t("br"),t("br"),n._v("Trong lòng tràn đầy rung động, không khỏi cảm khái."),t("br"),t("br"),n._v("Thánh tử điện, có Khổng sư chữ lớn trấn áp, liền xem như tâm ma, cũng không dám làm loạn."),t("br"),t("br"),n._v("Chỉ sợ, nguyên nhân chính là như vậy, tất cả đại gia tộc, mới bằng lòng đem ưu tú nhất hậu bối đưa đến nơi này, tiếp nhận truyền thừa cùng chỉ điểm."),t("br"),t("br"),n._v("“Đi vào đi!”"),t("br"),t("br"),n._v("Nhìn một hồi, lĩnh ngộ không ít, Trương Huyền tràn đầy cảm khái."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trần Nhạc Dao nhẹ gật đầu, hai người cùng đi vào."),t("br"),t("br"),n._v("(Là trắng bạc minh Yên Vũ Hồng Trần Vũ tăng thêm Chương 3: Đến, lão nhai nghỉ ngơi. Thuận tiện, đầu tháng cầu nguyệt phiếu!)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1317"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1319"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);