(window.webpackJsonp=window.webpackJsonp||[]).push([[383],{738:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1321-tru-phi-nguoi-la-đien-chu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1321-tru-phi-nguoi-la-đien-chu"}},[n._v("#")]),n._v(" Chương 1321: Trừ phi ngươi là điện chủ!")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1321: Trừ phi ngươi là điện chủ!"),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Giãy dụa lấy từ dưới đất bò dậy, Thủy sư tỷ khóe miệng cuồng rút, lần nữa nhìn về phía thanh niên trước mắt, vẻ mặt triệt để thay đổi: “Ngươi đối kiếm lĩnh ngộ, đã đạt đến... Chân giải tình trạng?”"),i("br"),i("br"),n._v("Thân là kiếm đạo thiên tài, đương nhiên biết, kiếm đạo phân cấp, trường kiếm trong tay, còn không có đâm ra đi, liền dọa đến rơi trên mặt đất, không ngừng run rẩy, loại tình huống này, chỉ ở lão sư trên thân gặp được, nói cách khác... Trước mắt vị này, thoạt nhìn chỉ có hai mươi tuổi thanh niên, đối kiếm đạo nắm giữ, đã cùng nàng lão sư so sánh!"),i("br"),i("br"),n._v("Làm sao làm được?"),i("br"),i("br"),n._v("Kiếm đạo chân giải, không chỉ cần đối với kiếm pháp lĩnh ngộ cực sâu, càng quan trọng hơn là, có đầy đủ trải qua cùng cảm ngộ, lão sư sở dĩ có thể đi vào cảnh giới kia, là bởi vì, đã từng đi bộ đi lại vạn dặm, trải nghiệm người bình thường khó khăn, lúc này mới một lần bước vào."),i("br"),i("br"),n._v("Trước mắt vị này, tính toán đâu ra đấy, theo trong bụng mẹ bắt đầu tu luyện, cũng liền chừng hai mươi năm, làm sao có thể lĩnh ngộ cái này?"),i("br"),i("br"),n._v("Trương Huyền cũng không phủ nhận, ngón tay búng một cái."),i("br"),i("br"),n._v("Trên mặt đất triều bái trường kiếm mất đi áp chế, “Ông!” Bay lên, rơi vào lòng bàn tay."),i("br"),i("br"),n._v("Nhẹ nhàng bắn ra, một tiếng hí dài."),i("br"),i("br"),n._v("“Kiếm này không sai, rất thích hợp ngươi!”"),i("br"),i("br"),n._v("Tiện tay đưa trả lại."),i("br"),i("br"),n._v("  Chuôi kiếm này, nhẹ nhàng thon dài, tương đối thích hợp nữ tử phát huy lực lượng."),i("br"),i("br"),n._v("“Là lão sư vì ta chọn!”"),i("br"),i("br"),n._v("Nhẹ gật đầu, đem trường kiếm thu hồi, Thủy sư tỷ nhìn qua: “Ngươi muốn nhìn liên quan tới kiếm đạo thư tịch?”"),i("br"),i("br"),n._v("Có thể lĩnh ngộ kiếm đạo chân giải, nàng đã mất đi tỷ thí lòng tin."),i("br"),i("br"),n._v("Chênh lệch thực sự quá lớn, coi như ra tay, cũng mất mặt xấu hổ."),i("br"),i("br"),n._v("“Lão sư ta nơi đó, sao chép không ít kiếm đạo thư tịch, có thể mang ngươi tới...” Chần chờ một chút, Thủy sư tỷ nói."),i("br"),i("br"),n._v("“Vậy làm phiền dẫn đường!”"),i("br"),i("br"),n._v("Trương Huyền ánh mắt sáng lên."),i("br"),i("br"),n._v("“Bất quá, trước thời hạn nói cho ngươi một câu, ngươi đã lĩnh ngộ chân giải, có con đường của mình, không nên ôm hy vọng quá lớn! Ta sợ, còn không có nhìn thấy sách, liền sẽ bị đuổi ra ngoài...” Thủy sư tỷ nói."),i("br"),i("br"),n._v("“Vì cái gì?”"),i("br"),i("br"),n._v("Trương Huyền không hiểu."),i("br"),i("br"),n._v("Hắn cái tuổi này có khả năng lĩnh ngộ chân giải, được cho chân chính kiếm đạo thiên tài, loại người này, chỉ cần dùng kiếm danh sư, đều ưa thích, làm sao ngược lại không nên ôm hy vọng quá lớn?"),i("br"),i("br"),n._v("“Cái này... Lão sư ta, là Thánh tử điện, đệ nhất kiếm đạo đại tông sư, Kiếm Tần Sinh, tu vi cao thâm, địa vị cũng cực kỳ kính trọng, chỉ bất quá...”"),i("br"),i("br"),n._v("Dừng lại một chút, Thủy sư tỷ hạ giọng: “Làm việc... Có chút quá chú ý quy tắc...”"),i("br"),i("br"),n._v("“Chú ý quy tắc?”"),i("br"),i("br"),n._v("Trương Huyền sững sờ, lập tức giật mình."),i("br"),i("br"),n._v("Thân là học sinh, không thật nhiều nói lão sư, nói như vậy chỉ sợ là muốn biểu đạt, hắn vị lão sư này, có chút cứng nhắc, là cái lão học cứu."),i("br"),i("br"),n._v("Loại người này, nói chuyện làm việc, mười phần ngay thẳng, coi trọng ngươi, tám ngựa ngựa đều kéo không trở lại, nói không đến cùng một chỗ, dù là địa vị lại cao hơn, bối cảnh lại hùng hồn, cũng không có nửa điểm tác dụng."),i("br"),i("br"),n._v("“Vâng, hắn đã từng là lễ điện trưởng lão, chú trọng lễ tiết cùng quy củ! Muốn nhìn hắn sưu tập thư tịch, đạt được chỉ điểm của hắn, nhất định phải thông qua khảo nghiệm, thu hoạch được công nhận mới được... Trương gia vị kia kiếm đạo thiên tài Trương Húc, là nội viện đều tên tuổi lẫy lừng thiên tài, kiếm đạo mệnh danh thế hệ trẻ tuổi đệ nhất. Đối kiếm thuật lĩnh ngộ, đã đạt đến chân giải tình trạng, muốn tới đây đọc sách, đồng dạng bị đuổi ra ngoài, cho nên, ta mới như vậy nói...”"),i("br"),i("br"),n._v("Thủy sư tỷ nói."),i("br"),i("br"),n._v("“Lĩnh ngộ chân giải đều đuổi ra ngoài, vì cái gì?” Trương Huyền khó hiểu."),i("br"),n._v(" Có thể đạt tới kiếm đạo chân giải, cũng liền nói đối kiếm pháp lĩnh ngộ không kém gì bản thân, loại người này, coi như tại Thánh tử điện, cũng không tổn hại quá yếu!"),i("br"),i("br"),n._v("Trực tiếp đuổi đi, làm sao đều nói không đi qua ah!"),i("br"),i("br"),n._v("“Ta mới vừa nói, lão sư chú trọng nhất nghi thức, hắn sùng bái nhất người, là Thánh tử điện đời thứ ba điện chủ! Vị điện chủ này lấy kiếm pháp nhập đạo, tu vi mạnh mẽ, cầm kiếm đi chân trời, một kiếm bình thiên hạ. Thánh Nhân quý tộc, từng cái nhún nhường ba phần. Thậm chí Danh Sư đường tổng bộ, đều không người dám tiếp xúc thẳng hắn phong! Đem Thánh tử điện uy thế, một lần đẩy lên đỉnh phong... Bất quá, vị điện chủ này thực lực, mặc dù mạnh mẽ, đáng tiếc, truyền thụ học sinh trên phương diện, kém rất nhiều, qua đời về sau, truyền thừa cắt đứt, toàn bộ Thánh tử điện kiếm đạo cũng bắt đầu sa sút!”"),i("br"),i("br"),n._v("“Lão sư, là hắn hậu bối, thủ hộ lấy hắn năm đó lưu lại một đạo kiếm ý! Muốn trở thành lão sư học sinh, hoặc là từ nơi này học đồ vật, nhất định phải đầu tiên đạt được kiếm ý công nhận, ta có thể theo hơn mười vị kiếm đạo thiên tài khảo hạch bên trong trổ hết tài năng, là bởi vì chiếm thủy thuộc tính công pháp, vạn vật đều dung hợp tiện nghi. Mà Trương Húc lĩnh ngộ thuộc về mình kiếm đạo, cùng kiếm ý xung đột lẫn nhau, cho nên... Liền bị đuổi ra ngoài!”"),i("br"),i("br"),n._v("“Ah!”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Đối kiếm lĩnh ngộ đạt tới chân giải tình trạng, thi triển kiếm ý, như là có linh hồn bình thường, quả thực sẽ đối với tu luyện giả tiến hành lựa chọn."),i("br"),i("br"),n._v("Thật giống như Linh Hư kiếm, bản thân bởi vì lĩnh ngộ phía ngoài “Kiếm” chữ, đạt đến chân giải, trực tiếp thần phục."),i("br"),i("br"),n._v("Kiếm ý xung đột lẫn nhau, tương đương với lý niệm trái ngược."),i("br"),i("br"),n._v("Vị này Thủy sư tỷ, tu luyện chính là thủy thuộc tính pháp quyết, tính tình ôn hòa, dễ dàng đạt được kiếm ý công nhận, mà kiếm đạo lĩnh ngộ khắc sâu, có con đường của mình, độ khó tất nhiên sẽ tăng thêm không ít."),i("br"),i("br"),n._v("“Chẳng lẽ... Hắn sưu tập sách, đều là vị này đời thứ ba điện chủ lưu lại? Nội dung cũng truyền thừa nhất mạch?”"),i("br"),i("br"),n._v("Trong lòng hơi động."),i("br"),i("br"),n._v("Đạt được kiếm ý công nhận, thật ra thì, cũng có thể hiểu thành, con đường tu luyện tới giống nhau."),i("br"),i("br"),n._v("Chỉ có như vậy người, mới có thể xem sách của hắn, đạt được chỉ điểm của hắn, nói trắng ra là, vị này Kiếm Tần Sinh là muốn bồi dưỡng được bản thân nhất mạch kiếm đạo cao thủ."),i("br"),i("br"),n._v("“Mỗi người đều kiếm pháp đều có đặc biệt lý giải, không biết hắn lĩnh ngộ là loại nào, bất quá... Có lẽ cùng kiếm lão nhân khác biệt!” Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Hắn đối với kiếm pháp lý giải, thoát thai từ kiếm lão nhân."),i("br"),i("br"),n._v("Vị này kiếm đạo đại tông sư, rong ruổi liên minh nhiều năm như vậy, cũng không nghe nói qua cùng vị này đời thứ ba điện chủ liên quan đến, cũng không thuộc về cùng một cái con đường."),i("br"),i("br"),n._v("Nói cách khác, bản thân thật muốn đi qua, rất có thể sẽ cùng Trương Húc đồng dạng, trực tiếp bị đuổi đi."),i("br"),i("br"),n._v("“Nếu như chỉ là nhìn một chút thư tịch, không chép ghi chép, cũng không dùng thần thức liếc nhìn, thậm chí đều không mở ra... Chẳng lẽ cũng không cho phép?”"),i("br"),i("br"),n._v("Rầu rĩ một chút, Trương Huyền nhịn không được hỏi."),i("br"),i("br"),n._v("Hắn đọc sách cùng những người khác khác biệt, chỉ cần ánh mắt có khả năng nhìn thấy, thời gian nháy mắt liền có thể thu vào đến thư viện."),i("br"),i("br"),n._v("Không cần đối phương chỉ điểm, cũng không cần sao chép, chỉ cần nhìn một lần liền biết có thể hay không đem Linh Hư tam kiếm bổ sung hoàn chỉnh... Như vậy hẳn là rất đơn giản đi!"),i("br"),i("br"),n._v("“Nhìn một chút?”"),i("br"),i("br"),n._v("Thủy sư tỷ cười khổ lắc đầu: “Những sách vở này, lão sư xem như bảo bối, chuyên môn bố trí một cái phòng, không có thủ lệnh của hắn, vào cũng không vào được, trừ phi... Ngươi là điện chủ, nếu không, nằm mơ cũng đừng nghĩ!”"),i("br"),i("br"),n._v("“Điện chủ có thể đi vào?”"),i("br"),i("br"),n._v("Trương Huyền sửng sốt."),i("br"),i("br"),n._v("Dường như... Hắn là điện chủ duy nhất người ứng cử."),i("br"),i("br"),n._v("Mặc dù bây giờ không phải là, hẳn là không bao lâu, sẽ thành công..."),i("br"),i("br"),n._v("“Đó là đương nhiên! Điện chủ là Thánh tử điện cao nhất thủ lĩnh, lão sư coi như cự tuyệt bất luận kẻ nào đi qua, cũng không dám cự tuyệt điện chủ ah!”"),i("br"),i("br"),n._v("Thủy sư tỷ lắc đầu: “Bất quá, Thánh tử điện đã hơn ba trăm năm không có chân chính điện chủ, hiện tại xử lý chuyện, cũng chỉ là quyền điện chủ thôi... Được rồi, nói có chút nhiều, điện chủ không phải chúng ta những tiểu nhân vật này có thể thảo luận. Giải thích nhiều như vậy, ngươi còn muốn hay không đi? Muốn đi lời nói, hiện tại liền đi; Cảm thấy không có hi vọng, cũng đừng có quấy rầy!”"),i("br"),i("br"),n._v("“Đi, tự nhiên muốn đi!”"),i("br"),i("br"),n._v("Trương Huyền mỉm cười."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1329"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1331"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);