(window.webpackJsonp=window.webpackJsonp||[]).push([[384],{739:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1322-thanh-nhan-quy-toc-phan-cap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1322-thanh-nhan-quy-toc-phan-cap"}},[n._v("#")]),n._v(" Chương 1322: Thánh Nhân quý tộc phân cấp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1322: Thánh Nhân quý tộc phân cấp"),t("br"),t("br"),n._v("Coi như biết vị này Kiếm Tần Sinh kiếm đạo lý niệm cùng mình khác biệt, cũng muốn đi qua nhìn một chút, ngộ nhỡ có khả năng gom góp Linh Hư tam kiếm cần có thiếu hụt, liền có thể nhẹ nhõm xung kích chữ Giáp ngõ hẻm, ngưng tụ ra Thiên Đạo cấp bậc Xuất Khiếu cảnh công pháp."),t("br"),t("br"),n._v("Lại nói, mỗi người phương pháp tu luyện, đều không hoàn toàn giống nhau, chỉ có cố tìm cái chung, gác lại cái bất đồng, mới có thể dung hội quán thông, thu hoạch được càng lớn tiến bộ!"),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Thấy hắn muốn đi, Thủy sư tỷ nhẹ gật đầu, cùng Thanh Vũ nói một tiếng, hai người đi ra “Nghĩa điện”."),t("br"),t("br"),n._v("“Một lát nữa, một khi kiếm ý không đồng ý, lão sư bảo ngươi rời khỏi, tốt nhất một câu không nói, xoay người rời đi! Trong lòng còn có may mắn, nhiều lời nói nhảm, ta sợ ngươi sẽ nếm chút khổ sở!” Một bên hướng về phía trước, Thủy sư tỷ một bên truyền âm tới."),t("br"),t("br"),n._v("“Tu luyện kiếm đạo, phần lớn đều tính tình như kiếm, sắc bén cương mãnh, thẳng tới thẳng lui, ta hiểu!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Có thể đem kiếm đạo tu luyện tới chân giải tình trạng, nói rõ đem trọn người sinh mệnh, tinh, khí, thần, toàn bộ hòa vào trong đó, tính cách thường thường cũng sẽ tùy theo thay đổi."),t("br"),t("br"),n._v("Loại người này, ghét nhất người khác vết mực."),t("br"),t("br"),n._v("Thật giống như kiếm lão nhân cái kia đạo tàn hồn, làm việc rất quả quyết, thậm chí biến mất, đều nhanh như vậy, không có chút nào lưu luyến!"),t("br"),t("br"),n._v("“Biết liền tốt!” Thủy sư tỷ thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Có thể bằng chừng ấy tuổi, liền loại thực lực này, càng đem kiếm đạo lý giải đến tình trạng như thế, thân thế tất nhiên không đơn giản, nàng cũng không muốn hảo tâm, cuối cùng xác thực chọc hai bên đều mất hứng!"),t("br"),t("br"),n._v("Nghĩ đến thân phận, nhịn không được có chút hiếu kỳ: “Ngươi gọi Trương Huyền... Trương gia lúc nào có như vậy một vị thiên tài, trước đó chưa từng nghe qua?”"),t("br"),t("br"),n._v("“Ta không phải Trương gia!”"),t("br"),t("br"),n._v("Thấy lại bị người hiểu lầm, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Không phải?” Thủy sư tỷ có chút không tin."),t("br"),t("br"),n._v("Không phải Trương gia, nói cách khác... Không có huyết mạch chi lực, có thể còn trẻ như vậy giống như thực lực này, làm sao làm được?"),t("br"),t("br"),n._v("“Ta theo Thanh Nguyên đế quốc phương hướng mà đến, sở dĩ thực lực cũng không tệ lắm, là bởi vì ta mười phần... Cố gắng!” Trương Huyền giải thích."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghĩ tới tên này, hai phút đồng hồ không đến, liền xông tám cái ngõ nhỏ, còn muốn đi học kiếm... Thủy sư tỷ nhịn không được nhẹ gật đầu: “Có thể nhờ vào tán tu, còn trẻ như vậy liền đạt tới thực lực như thế, quả thực đủ liều!”"),t("br"),t("br"),n._v("“Thủy sư tỷ, ngươi vừa rồi vận dụng huyết mạch chi lực, chẳng lẽ... Cũng là cái nào đó Thánh Nhân quý tộc xuất thân?”"),t("br"),t("br"),n._v("Trương Huyền cũng hỏi ra nghi ngờ."),t("br"),t("br"),n._v("“Ngươi... Không biết ta?”"),t("br"),t("br"),n._v("Thủy sư tỷ tràn đầy im lặng."),t("br"),t("br"),n._v("Nàng mặc dù thực lực chỉ có Xuất Khiếu cảnh đỉnh phong, nhưng bằng mượn thân phận, dung mạo, tại Thánh tử điện ngoại viện, đều tên tuổi lẫy lừng, xem như nhân vật phong vân, cái tên này thế mà ngay cả mình lai lịch cũng không biết... Thật hay giả?"),t("br"),t("br"),n._v("“Ngày hôm nay lần đầu tiên nhìn thấy Thủy sư tỷ, như thế nào nhận biết...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Lần đầu tiên thấy?” Thủy sư tỷ lúc này mới kịp phản ứng, đôi mắt thanh tú trợn tròn: “Ngươi không phải là ngày hôm nay khảo hạch tiến đến học sinh a?”"),t("br"),t("br"),n._v("“Đúng vậy a!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Mới vừa thi được đến, liền chạy tới xông nghĩa điện, một hơi thông quan tám cái ngõ nhỏ?” Nuốt ngụm nước bọt, Thủy sư tỷ da mặt quất loạn."),t("br"),t("br"),n._v("Hiện tại tân sinh cũng quá trâu rồi a?"),t("br"),t("br"),n._v("Nàng cái này nghiên cứu thời gian dài như vậy, liền cái chữ Bính ngõ hẻm đều không qua được, cái tên này, mới đi đến liền xông tám cái?"),t("br"),t("br"),n._v("“A, ta buổi trưa thi xong, đi trước một chuyến trí điện, nghiên cứu cái bát tinh trận pháp sư, thông qua về sau, bố trí cái cấp tám trận pháp, lại cùng người khác trò chuyện một canh giờ, trở lại chỗ ở, thuận tiện tắm rửa một cái, lúc này mới tới nghĩa điện...”"),t("br"),t("br"),n._v("Nhớ lại một chút, Trương Huyền nói."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Thủy sư tỷ thân thể cứng ngắc."),t("br"),t("br"),n._v("Bát tinh trận pháp sư, khó mà thông qua, coi như Lạc gia tiểu công chúa vị kia ca ca, cũng liền tiếp theo nghiên cứu hai lần đều không có thông qua... Làm sao nghe cái tên này khẩu khí, chạy tới khảo hạch, cùng ra ngoài dạo phố đồng dạng đơn giản?"),t("br"),t("br"),n._v("Mấu chốt nhất là... Coi như trở thành loại cấp bậc này trận pháp sư, cấp tám trận pháp muốn sắp đặt, không có ba, năm tháng, cũng khó có thể hoàn thành... Buổi trưa trở thành Thánh tử điện học viên, sắp đặt trận pháp, tán gẫu toàn bộ làm xong, đi tới nghĩa điện, cũng chỉ là buổi chiều, đến bây giờ Thiên Đô không có hắc..."),t("br"),t("br"),n._v("Tốc độ quá nhanh đi!"),t("br"),t("br"),n._v("“Ngươi... Khảo hạch bát tinh trận pháp sư, dùng bao lâu?”"),t("br"),t("br"),n._v("Cũng lại kìm nén không được, hỏi."),t("br"),t("br"),n._v("“Cái này...” Nhíu nhíu mày, Trương Huyền cẩn thận nhớ lại nửa ngày: “Nếu như không tính khảo hạch thực lực... Có cái năm, sáu phút đồng hồ đi! Chủ yếu là, vị kia Tô trưởng lão làm trễ nải một chút, nếu không, hẳn là có thể nhanh chút!”"),t("br"),t("br"),n._v("“Năm sáu phút đồng hồ...”"),t("br"),t("br"),n._v("Lần nữa lắc lư một cái, Thủy sư tỷ cảm thấy bị đả kích thương tích đầy mình."),t("br"),t("br"),n._v("Bát tinh trận pháp sư, nàng cũng muốn kiểm tra, đáng tiếc tu vi quá thấp, không đủ tư cách, bởi vậy, một mực không thành công, vị này, tu vi so với hắn còn thấp, vài phút liền khảo hạch thông qua..."),t("br"),t("br"),n._v("Càng nghĩ càng thấy đến tâm tắc, chỉ cảm thấy ngực đau nhức, không thở nổi."),t("br"),t("br"),n._v("“Đúng rồi, ngươi vẫn chưa trả lời ta, ngươi có phải hay không Thánh Nhân quý tộc xuất thân!”"),t("br"),t("br"),n._v("Thấy nữ hài vẻ mặt một hồi hồng một hồi bạch, một hồi che ngực, thoạt nhìn tựa như tinh thần rối loạn, Trương Huyền nhíu nhíu mày, hỏi."),t("br"),t("br"),n._v("“Ta tên Thủy Thiên Nhu, đến từ Thánh Nhân Thủy gia!”"),t("br"),t("br"),n._v("Đè nén trong lòng rung động, Thủy sư tỷ Thủy Thiên Nhu nói."),t("br"),t("br"),n._v("“Thánh Nhân Thủy gia?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trương Huyền nhớ tới một việc, tràn đầy khó hiểu: “Ta trước đây quen biết một vị Thánh Nhân Mộc gia người, tu luyện chính là mộc thuộc tính công pháp, thủy, mộc... Chẳng lẽ Thánh Nhân quý tộc bên trong, có quan hệ với ngũ hành gia tộc?”"),t("br"),n._v("Hồng Viễn đế quốc nhận biết Mộc Viên, là Thánh Nhân Mộc gia, cái này lại toát ra cái Thủy gia, sẽ không Thánh Nhân quý tộc bên trong, thật có cái gọi là ngũ hành gia tộc a?"),t("br"),t("br"),n._v("Nếu không, nào có trùng hợp như vậy, Thủy gia tu luyện thủy thuộc tính pháp quyết, Mộc gia tu luyện mộc thuộc tính lực lượng?"),t("br"),t("br"),n._v("“Ngũ hành gia tộc, là Thánh Nhân quý tộc bên trong nhất đẳng gia tộc, ngươi... Ngay cả điều này cũng không biết?”"),t("br"),t("br"),n._v("Thủy Thiên Nhu thật sự có chút bó tay rồi."),t("br"),t("br"),n._v("Ngũ hành gia tộc, mặc dù là nhất đẳng gia tộc, nhưng từ trước tới nay như thể chân tay, liên hợp cùng một chỗ, coi như Trương gia, Lạc gia đều muốn kiêng kị ba phần... Trước mắt vị này, đều kiểm tra đến Thánh tử điện, liền cái này đều không có nghe qua?"),t("br"),t("br"),n._v("Quá cô lậu quả văn đi!"),t("br"),t("br"),n._v("Bất quá, suy nghĩ một chút cũng liền giật mình."),t("br"),t("br"),n._v("Thanh Nguyên đế quốc là cái xa xôi khu vực địa phương nhỏ, không có liên quan tới Thánh Nhân quý tộc ghi chép, cũng rất bình thường."),t("br"),t("br"),n._v("Hạ trùng không thể ngữ băng, giếng con ếch không thể ngữ biển, phàm phu không thể ngữ đạo!"),t("br"),t("br"),n._v("Đạo lý giống vậy."),t("br"),t("br"),n._v("Nghĩ đến cái này, lắc đầu, mở miệng nói: “Thánh Nhân quý tộc, chia làm mấy cái đẳng cấp, cái này... Ngươi hẳn nghe nói qua đi!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Tám đại đế quốc phạm vi bên trong, cũng có Thánh Nhân quý tộc, thật giống như vị kia Tiết Ngưng, Tiết tam công tử vị trí gia tộc, có điều, đều là một chút tam đẳng."),t("br"),t("br"),n._v("“Thánh Nhân quý tộc, chia làm bốn đẳng cấp, thứ nhất, là siêu cấp quý tộc, địa vị coi như so ra kém Danh Sư đường tổng bộ, nhưng cũng so luyện đan sư công hội tổng bộ, luyện khí sư công hội tổng bộ, cao hơn mấy phần! Tại đại lục nắm giữ tuyệt đối quyền nói chuyện cùng cao thượng địa vị... Theo thứ tự là Trương gia, Lạc gia cùng Tưởng gia!”"),t("br"),t("br"),n._v("Thủy Thiên Nhu giải thích: “Thứ hai, liền là ngũ hành gia tộc vị trí nhất đẳng gia tộc. Thủy gia, Mộc gia, đều ở trong đó! Một chút thế lực lớn, cũng ở hàng ngũ này, tỷ như, Băng Nguyên cung, luyện đan sư công hội...”"),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Trương Huyền bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Sớm biết Trương gia, Lạc gia rất mạnh, là siêu cấp thế lực bá chủ, không nghĩ tới lại là nấc thang thứ nhất gia tộc, địa vị so luyện đan sư công hội tổng bộ, cũng cao hơn."),t("br"),t("br"),n._v("Khó trách, nghe nói mình thích chính là Lạc gia tiểu công chúa, Ngô sư đám người như vậy lo lắng."),t("br"),t("br"),n._v("“Thứ ba, liền là nhị đẳng gia tộc, địa vị cùng liên minh đế quốc không kém nhiều! Loại này đẳng cấp gia tộc, số lượng rất nhiều, chừng hơn trăm cái. Còn tam đẳng gia tộc, cái này một chút phong hào đế quốc tương đương, phân tán khắp nơi, số lượng thực sự quá nhiều, cụ thể có bao nhiêu, ta cũng không rõ lắm.”"),t("br"),t("br"),n._v("Thủy Thiên Nhu ngắn gọn liền đem Thánh Nhân quý tộc bốn đẳng cấp nói rõ chi tiết một lần."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Vừa nói như vậy, hắn liền hiểu."),t("br"),t("br"),n._v("Nếu như đem Trương gia, Lạc gia loại này siêu cấp quý tộc, so sánh Thanh Nguyên phong hào đế quốc, như vậy ngũ hành gia tộc liền là Hồng Viễn nhất đẳng đế quốc, nhị đẳng gia tộc, liền cùng nhị đẳng Huyễn Vũ đế quốc đồng dạng, còn Tiết gia như vậy tam đẳng gia tộc, liền cùng vương quốc liên minh tương đương."),t("br"),t("br"),n._v("Nhìn tới, Thánh Nhân quý tộc, cũng chờ cấp nghiêm khắc."),t("br"),t("br"),n._v("“Ta một cái học... Bằng hữu, là Thánh Nhân Viên gia, không biết cái này Viên gia, là mấy đẳng gia tộc?”"),t("br"),t("br"),n._v("Nhớ tới Viên Đào, Trương Huyền hỏi lần nữa."),t("br"),t("br"),n._v("Băng Nguyên cung, đã có thể so với nhất đẳng gia tộc, không biết Viên gia xem như mấy các loại, nếu là quá thấp... Cũng nên cân nhắc đem hắn tìm trở về."),t("br"),t("br"),n._v("Thấy hắn quả thật cái gì cũng không biết, Thủy Thiên Nhu mặt mũi bất đắc dĩ: “Ngũ hành gia tộc, Mộc gia, Thủy gia, Kim gia, họ đều cùng ngũ hành đối ứng, Hỏa gia, họ Xích, bởi vậy Thánh Nhân Xích gia, liền là trong ngũ hành Hỏa gia! Mà Thổ gia, họ Viên, nếu như ngươi vị bằng hữu này, là Thánh Nhân Viên gia, chính là ngũ hành Thổ gia!”"),t("br"),t("br"),n._v("“Ngũ hành Thổ gia? Nhất đẳng gia tộc?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Không nghĩ tới Viên gia, lợi hại như vậy."),t("br"),t("br"),n._v("“Đúng vậy a, không chỉ có như vậy, Viên gia vẫn là trong ngũ hành gia tộc mạnh mẽ nhất, lực lượng nặng nề cuồng mãnh, phòng ngự vô địch...” Thủy Thiên Nhu nói."),t("br"),t("br"),n._v("Thủy gia công pháp và huyết mạch lực lượng, mặc dù có thể bao dung vạn vật, lấy nhu thắng cương, nhưng... Lực công kích vẫn là quá yếu!"),t("br"),t("br"),n._v("Trong ngũ hành, lực công kích mạnh nhất là Kim gia, nhưng muốn nói nặng nề cùng phòng ngự, Viên gia thuộc về thứ nhất, không người có thể so sánh."),t("br"),t("br"),n._v("“Vậy là tốt rồi...”"),t("br"),t("br"),n._v("Nghe được Viên gia lợi hại như vậy, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Đã như vậy, cũng không cần lại lo lắng Viên Đào."),t("br"),t("br"),n._v("Lợi hại như thế gia tộc, tất nhiên có thủ đoạn để hắn nhanh chóng tăng lên, làm không cẩn thận, tu vi của hắn, so với Triệu Nhã, đều không hề yếu."),t("br"),t("br"),n._v("“Đây là lão sư ta nơi ở, một lát nữa, tận lực ít nói chuyện...”"),t("br"),t("br"),n._v("Đang tại nói chuyện, Thủy Thiên Nhu dừng lại giải thích, truyền âm tới."),t("br"),t("br"),n._v("Ngẩng đầu nhìn lên, lúc này mới phát hiện đã đi không biết bao xa, đi tới một cái rộng rãi viện lạc, còn không có tiến vào bên trong, liền cảm nhận được từng đạo kiếm ý ngang dọc, tựa như lần nữa trở lại Kiếm Trì."),t("br"),t("br"),n._v("“Đây là Kiếm Tần Sinh trưởng lão chỗ ở? Quả nhiên bất phàm!”"),t("br"),t("br"),n._v("Trương Huyền vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Không hổ là không kém gì kiếm lão nhân kiếm đạo đại tông sư, coi như hắn lĩnh ngộ kiếm đạo chân giải, đột nhiên đi tới, cũng cảm thấy bị khí tức áp chế, có chút vận chuyển không ra."),t("br"),t("br"),n._v("“Lúc mới nhập môn có một đạo kiếm khí, sẽ đem bình thường học viên ngăn tại ngoài cửa, ta đi vào trước giúp ngươi bẩm báo, để lão sư thả ngươi tiến đến, lại nói câu nói kế tiếp...”"),t("br"),t("br"),n._v("Hít sâu một hơi, Thủy Thiên Nhu đi tới viện lạc trước cửa, đang muốn đi vào, chỉ thấy một đạo kiếm khí tại thanh niên đầu ngón tay loạn chuyển, muốn chạy trốn, lại không cách nào làm được."),t("br"),t("br"),n._v("“Kiếm khí? Ngươi nói là đạo này?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1330"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1332"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);