(window.webpackJsonp=window.webpackJsonp||[]).push([[388],{743:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1326-chan-giai-phan-loai-thuong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1326-chan-giai-phan-loai-thuong"}},[n._v("#")]),n._v(" Chương 1326: Chân giải phân loại (thượng)")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1326: Chân giải phân loại (thượng)"),i("br"),i("br"),n._v("“Là ta thất lễ...”"),i("br"),i("br"),n._v("Cười xong, Kiếm Tần Sinh khôi phục trước đó bộ dáng, lần nữa nhìn về phía Trương Huyền, càng xem càng thoả mãn: “Vừa rồi ngươi thi triển chính là  đi!”"),i("br"),i("br"),n._v("Sửng sốt một chút, Trương Huyền lắc đầu: “Đó là cái gì? Chưa từng nghe qua!”"),i("br"),i("br"),n._v("“Chưa từng nghe qua?”"),i("br"),i("br"),n._v("Kiếm Tần Sinh cau mày: “Bộ kiếm pháp kia, chính là ta vừa rồi tu luyện cái kia, từ tiên tổ Thánh tử điện đời thứ ba điện chủ Kiếm Lưu Thủy sáng tạo, truyền thừa đến nơi này của ta, đã không biết bao nhiêu đời, trong điện cũng có lưu truyền... Ngươi vừa rồi thi triển, bất kể kiếm ý, chiêu số đều giống nhau y hệt, làm sao có thể không phải?”"),i("br"),i("br"),n._v("Bộ kiếm pháp kia, mặc dù là hắn tiên tổ sáng tạo, lại không phải hắn mạch này truyền thừa xuống, Thánh tử điện Kiếm đường, cũng không ít học sinh tu luyện."),i("br"),i("br"),n._v("Vốn cho rằng, vị này coi như không phải Trương gia người, khẳng định cũng là cơ duyên khảo hạch, đạt được Lưu Thủy kiếm quyết tu luyện mấu chốt, mới có thể trong khoảng thời gian ngắn, đạt tới thực lực như thế, làm sao đều không có nghĩ đến, người ta liền bộ này kiếm quyết, nghe đều không có nghe qua!"),i("br"),i("br"),n._v("“Vừa rồi thi triển, là ta một mình sáng tạo kiếm pháp, cùng ngươi nói kiếm quyết, là hai việc khác nhau!”"),i("br"),i("br"),n._v("Rõ ràng đối phương nói, có thể là vừa rồi tu luyện cái kia tất cả đều là sai chiêu số, Trương Huyền cười lắc đầu."),i("br"),i("br"),n._v("Cái loại này rác rưởi kiếm pháp, sai như thế không hợp thói thường, thật muốn gặp gỡ, nhìn cũng không nhìn liếc mắt, còn học... Làm sao có thể?"),i("br"),i("br"),n._v("“Một mình sáng tạo?” Kiếm Tần Sinh sửng sốt, con mắt trợn tròn: “Đó là ngươi sáng lập ra kiếm pháp?”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Nơi này là Thánh tử điện, đại lục đỉnh phong nhất cơ quan, nếu như lại nói Dương Huyền, một khi thẩm tra Danh Sư đường tổng bộ, không có người này, phiền phức càng nhiều, còn không bằng nói mình sáng tạo."),i("br"),i("br"),n._v("Lĩnh ngộ kiếm đạo chân giải, đã được cho kiếm đạo đại tông sư, sáng chế mấy bộ kiếm pháp, không tính là gì."),i("br"),i("br"),n._v("“Cái này...” Kiếm Tần Sinh vẫn còn có chút khó tin: “Có thể hay không đem ngươi sáng lập ra bộ kiếm pháp kia, lại thi triển một lần, cho ta xem một chút?”"),i("br"),i("br"),n._v("đọｃ ngantruyen.com/"),i("br"),n._v("“Đương nhiên!”"),i("br"),i("br"),n._v("Nhẹ gật đầu, Trương Huyền ngón tay bóp một cái, một đạo kiếm khí thẳng tắp theo đầu ngón tay bắn đi ra, cổ tay rung lên, từng đạo hàn mang, trên không trung lóe lên, tựa như muốn đem không khí đâm rách."),i("br"),i("br"),n._v("Chiêu kiếm của hắn, rất đơn giản, thoạt nhìn không có bất kỳ hoa tiếu gì, lại mang theo để cho người ta khó mà tránh né sắc bén, dường như chỉ cần gặp gỡ, liền sẽ bị đánh trúng."),i("br"),i("br"),n._v("Kiếm chiêu tốc độ cùng vừa rồi đồng dạng, rất chậm, lại xen lẫn làm cho không người nào có thể kháng cự đại thế."),i("br"),i("br"),n._v("Kháng cự kiếm pháp, tựa như là tại chống cự Thiên Đạo."),i("br"),i("br"),n._v("“Quả nhiên không phải Lưu Thủy kiếm quyết...”"),i("br"),i("br"),n._v("Tự lẩm bẩm, Kiếm Tần Sinh có chút kinh hồn bạt vía."),i("br"),i("br"),n._v("Còn tưởng rằng, đối phương tu luyện tiên tổ kiếm pháp, hiện tại xem ra, hai loại kiếm quyết ý cảnh hoàn toàn khác biệt, căn bản cũng không thuộc về đồng nhất truyền thừa."),i("br"),i("br"),n._v("Hơn nữa kiếm pháp của đối phương, mặc dù chỉ có một chiêu, lại bao gồm vô số biến hóa ở bên trong, so tiên tổ Lưu Thủy kiếm quyết càng thêm tinh diệu."),i("br"),i("br"),n._v("Chỉ là uy lực kém không ít, hẳn là chỉ là đẳng cấp thấp kiếm pháp, không cao hơn Thánh vực trung phẩm."),i("br"),i("br"),n._v("“Trương sư có khả năng sáng chế cường đại như thế kiếm chiêu, bản thân càng là đạt đến chân giải tình trạng, hẳn không phải là tới học tập a!”"),i("br"),i("br"),n._v("Tâm cảnh xoay một cái, đem thất lạc cảm xúc dứt bỏ, Kiếm Tần Sinh lần nữa nhìn qua."),i("br"),i("br"),n._v("Có khả năng lĩnh ngộ kiếm đạo chân giải đã là một phương đại tông sư, càng sáng chế như vậy kiếm pháp, so với chính mình cũng không thua bao nhiêu, muốn nói tới hướng hắn học tập, rất khó để cho người ta tin tưởng."),i("br"),i("br"),n._v("“Ta tới này quả thực là có chuyện xảy ra muốn nhờ, nghe nói tiền bối nơi này, thu vào rất nhiều kiếm pháp bí tịch, có thể hay không cho ta mượn xem một chút?”"),i("br"),i("br"),n._v("Trương Huyền nói ra mục đích đến."),i("br"),i("br"),n._v("“Đọc sách? Cũng không phải không được, chỉ bất quá...”"),i("br"),i("br"),n._v("Kiếm Tần Sinh chần chờ một chút: “Ta chỗ này  là năm đó Lưu Thủy tiên tổ xây, trong đó ẩn hàm tiên tổ kiếm ý, chỉ có đạt được công nhận, mới có thể đi vào. Những năm này ta thu đồ cũng là dùng cái này tiêu chuẩn, mục đích đúng là muốn cho khả năng đủ thuận lợi đi vào Kiếm Các học tập.”"),i("br"),i("br"),n._v("“Đạt được kiếm ý công nhận?”"),i("br"),i("br"),n._v("Trương Huyền nhíu nhíu mày, trước đó Thủy Thiên Nhu cũng đã nói một lần."),i("br"),i("br"),n._v("Vốn cho rằng là vị này Kiếm Tần Sinh quyết định quy củ, nhìn tới cũng không phải là như vậy."),i("br"),i("br"),n._v("“Phải! Tiên tổ thực lực, vượt xa quá ta, đạt đến đại lục đỉnh phong nhất, hắn lưu lại kiếm ý huy hoàng mạnh mẽ, liền xem như ta cũng không cách nào phá hư quy tắc!”"),i("br"),i("br"),n._v("Kiếm Tần Sinh cười khổ."),i("br"),i("br"),n._v("Người người đều nói hắn cứng nhắc, của mình mình quý, có cao minh kiếm pháp cũng không rộng vì truyền chi, mới rơi vào kết quả như vậy, thật ra thì chỉ có chính hắn biết, những quy củ này đều là tiên tổ quyết định, với tư cách hậu bối, căn bản không có cách nào sửa đổi."),i("br"),i("br"),n._v("“Muốn vào  đọc sách, hoặc là có điện chủ lệnh, hoặc là có thể có được kiếm ý công nhận, lại không loại thứ ba phương pháp. Nếu như ngươi thật muốn đi nhìn, không ngại kiểm tra một chút, có lẽ liền có thể đạt được công nhận, nhẹ nhõm tiến vào bên trong.”"),i("br"),i("br"),n._v("“Kiểm tra?” Trầm tư một lát, Trương Huyền nhẹ gật đầu: “Được!”"),i("br"),i("br"),n._v("Hắn tuy có điện chủ lệnh, thực lực chân chính cũng chỉ có Xuất Khiếu cảnh sơ kỳ, danh sư cấp bậc cũng chỉ là Thất Tinh... Cái nào một hạng, đều không phù hợp phá giải phong ấn điều kiện."),i("br"),i("br"),n._v("Lúc này lấy ra lời nói, bị người phát hiện, phiền phức vô cùng."),i("br"),i("br"),n._v("Không phù hợp hắn khiêm tốn tính cách."),i("br"),i("br"),n._v("Còn không bằng trực tiếp kiểm tra một chút, có lẽ nhờ vào thiên đạo công pháp và Thiên Đạo kiếm pháp đặc thù, có khả năng tuỳ tiện thông qua."),i("br"),i("br"),n._v("“Đi theo ta!”"),i("br"),i("br"),n._v("Thấy hắn đồng ý, Kiếm Tần Sinh nhẹ gật đầu."),i("br"),i("br"),n._v("Trước mắt vị này, đã không phải Trương gia người, lại giống như này thiên phú, chỉ là đọc sách lời nói, vẫn là rất tình nguyện giúp một tay, chỉ tiếc quy củ không thể phá, cũng không có phương pháp."),i("br"),i("br"),n._v("Trương Huyền theo sát phía sau, Thủy Thiên Nhu, Tạ sư huynh mấy người cũng theo sau."),i("br"),i("br"),n._v("Bọn họ có khả năng ở đây, nói rõ đều thông qua khảo hạch, lúc này cũng muốn nhìn một chút trước mắt vị này kiếm đạo cao thủ, có thể hay không giống như bọn họ."),i("br"),i("br"),n._v("Thời gian không dài, đi tới một cái rộng lớn gian phòng, chính giữa trên vách tường, một cái to lớn “Kiếm” chữ, ngân câu thiết họa, tựa như muốn đâm thủng bầu trời."),i("br"),i("br"),n._v("Cùng Kiếm Trì cái kia có chút tương tự, nhưng lại hoàn toàn khác biệt, ẩn chứa khác biệt ý cảnh."),i("br"),i("br"),n._v("“Kiếm” chữ phía dưới trên vách tường, cũng điêu đầy đủ loại chú thích, xem xét liền niên đại xa xưa, không biết tồn tại đã bao nhiêu năm, cũng hẳn là lịch đại tiền bối đem tâm đắc cùng kinh nghiệm lưu lại."),i("br"),n._v("“Cái chữ này liền là năm đó Lưu Thủy tiên tổ lưu lại, chỉ cần có thể đi vào năm mét trong vòng, không bị công kích đã nói lên đạt được công nhận.” Kiếm Tần Sinh giới thiệu nói."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("Tu luyện tới cao thâm kiếm ý, có khả năng tự động hộ chủ, trên tường cái chữ này ẩn chứa Kiếm Lưu Thủy, cả đời ý niệm, uy lực đương nhiên bất phàm, nếu như tu luyện lý niệm tại hắn trái ngược, sẽ bị trực tiếp công kích."),i("br"),i("br"),n._v("Cũng không phải là không cho phép cái khác chiêu số tồn tại, mà là kiếm ý bản thân bảo vệ."),i("br"),i("br"),n._v("“Ta thử một chút!”"),i("br"),i("br"),n._v("Biết muốn đi đọc sách, chỉ có thể nghĩ biện pháp tới gần, hít sâu một hơi không nói thêm lời, Trương Huyền nhấc chân chậm rãi đi tới."),i("br"),i("br"),n._v("Trước mắt to lớn kiểu chữ, như là một lượt mặt trời, làm cho người ta cảm thấy nồng đậm cảm giác áp bách, ngay cả nguyên thần đều có loại cảm giác bỏng."),i("br"),i("br"),n._v("“Chẳng lẽ ta tu luyện kiếm ý, thật không phù hợp?”"),i("br"),i("br"),n._v("Trước đó chỉ là suy đoán, bây giờ còn chưa tới gần, liền cảm thấy nồng đậm áp lực, trong lòng đã xác nhận."),i("br"),i("br"),n._v("Vậy thì phiền toái."),i("br"),i("br"),n._v("Hắn cũng không muốn len lén lẻn vào đối phương thư khố trộm sách nhìn."),i("br"),i("br"),n._v("Không nói trước có hay không loại thực lực này, cho dù có, Thánh tử điện cũng không dám tùy ý làm như thế."),i("br"),i("br"),n._v("Dù sao, thật muốn làm như vậy, về sau điện chủ thân phận công khai, liền sẽ trở nên hết sức khó xử."),i("br"),i("br"),n._v("Ai, danh lợi hại chết người!"),i("br"),i("br"),n._v("Làm điện chủ, cũng không tưởng tượng bên trong vui vẻ như vậy."),i("br"),i("br"),n._v("Vẫn là lúc trước nghèo xơ xác thời điểm, tự do tự tại, muốn làm gì làm gì, không nhận gò bó!"),i("br"),i("br"),n._v("Thật hy vọng chỉ là cái bình thường học sinh, có thể muốn làm gì thì làm!"),i("br"),i("br"),n._v("Trong lòng cảm khái, dưới chân không có ngừng lại, tiếp tục hướng phía trước, càng đi, cảm thấy áp lực càng lớn, hình như trên đầu nguy hiểm một thanh trường kiếm, bất cứ lúc nào cũng sẽ bổ xuống."),i("br"),i("br"),n._v("Trong cơ thể mình kiếm ý, cảm thấy loại này uy hiếp, rục rà rục rịch, bất cứ lúc nào cũng sẽ nghênh đón đi lên, chống lại."),i("br"),i("br"),n._v("“Hắn có thể thông qua ư?”"),i("br"),i("br"),n._v("Gặp thanh niên từng bước một tiến về phía trước, Thủy Thiên Nhu tràn đầy lo lắng."),i("br"),i("br"),n._v("“Chỉ sợ rất khó...”"),i("br"),i("br"),n._v("Kiếm Tần Sinh lắc đầu."),i("br"),i("br"),n._v("Thông qua quan sát, hắn đã nhìn ra, đối phương có con đường của mình, cùng tiên tổ Lưu Thủy kiếm quyết hoàn toàn khác biệt, muốn có được công nhận, khả năng cũng không lớn."),i("br"),i("br"),n._v("Ô ô ô!"),i("br"),i("br"),n._v("Đang khi nói chuyện, quả nhiên thấy trên vách tường “Kiếm” chữ, nhẹ nhàng chấn động, một đạo huy hoàng kiếm khí, thẳng tắp đối Trương Huyền bổ xuống."),i("br"),i("br"),n._v("“Ai!”"),i("br"),i("br"),n._v("Biết cũng không đạt được công nhận, lắc đầu, Kiếm Tần Sinh bàn tay đưa tay về phía trước."),i("br"),i("br"),n._v("Lực lượng khổng lồ theo đầu ngón tay bắn ra, đem “Kiếm” chữ đâm ra kiếm khí, ngăn cản trở về."),i("br"),i("br"),n._v("“Lui về tới đi!”"),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Biết ra kết quả, Trương Huyền đành phải lắc đầu, lui về bên người mọi người."),i("br"),i("br"),n._v("“Nhìn tới cũng không được công nhận, Kiếm Đồ thư các, chỉ sợ không thể vì ngươi mở ra...”"),i("br"),i("br"),n._v("Kiếm Tần Sinh tràn đầy áy náy."),i("br"),i("br"),n._v("Trương Huyền bất đắc dĩ."),i("br"),i("br"),n._v("Vốn cho rằng Thiên Đạo kiếm pháp có khả năng đem hắn dung hợp, hiện tại xem ra, vẫn là nghĩ quá đơn giản."),i("br"),i("br"),n._v("“Không biết có biện pháp nào có thể được đến công nhận?” Nhịn không được hỏi."),i("br"),i("br"),n._v("“Kiếm đạo mặc dù chỉ có một cái, nhưng mà chân giải, lại có rất nhiều, tiên tổ lĩnh ngộ là một loại, Trương gia vị gia chủ kia, là một loại khác, năm đó Kiếm lão nhân, cũng là một loại... Chân giải khác biệt, lẫn nhau bài xích! Loại trừ lĩnh ngộ cái khác chân giải, còn có thể lĩnh ngộ, Lưu Thủy tiên tổ loại này, hơn nữa cũng đạt tới chân giải tình trạng, cùng trong cơ thể ngươi kiếm ý ngang hàng, nếu không, tuyệt đối không thể!”"),i("br"),i("br"),n._v("Kiếm Tần Sinh cười khổ."),i("br"),i("br"),n._v("Con đường nào cũng dẫn đến Rome, nếu như đem kiếm đạo, so sánh điểm cuối cùng, chân giải so sánh con đường, phương hướng xác định, con đường tất nhiên có rất nhiều đầu."),i("br"),i("br"),n._v("Thật giống như đề mục, có rất nhiều loại giải pháp, cuối cùng đều có thể thu hoạch được đáp án đồng dạng."),i("br"),i("br"),n._v("Chân giải khác biệt, lực lượng khác biệt, có chút thậm chí hoàn toàn trái lại, làm sao có thể đạt được công nhận?"),i("br"),i("br"),n._v("“Ngươi ý tứ... Chỉ cần tu luyện, cái này ‘Kiếm’ chữ phía trên viết kiếm ý, hơn nữa đạt tới chân giải, liền có thể đạt được công nhận?” Trương Huyền nhìn qua."),i("br"),i("br"),n._v("“Không sai!”"),i("br"),i("br"),n._v("Kiếm Tần Sinh gật đầu: “Bất quá, loại sự tình này, nghĩ cùng đừng nghĩ, một loại kiếm đạo chân giải, cũng làm người ta tiêu hao một đời tinh lực, đều khó mà làm được, loại thứ hai... Tuyệt đối không thể! Thánh tử điện, vô số kiếm thuật thiên tài, chưa hề có người từng thành công, vẫn là đừng nghĩ...”"),i("br"),i("br"),n._v("“Thật khó như vậy?” Trương Huyền hơi nghi hoặc một chút, lập tức nhìn về phía “Kiếm” chữ phía dưới rất nhiều kiểu chữ, chậm rãi nhắm mắt lại."),i("br"),i("br"),n._v("“Đúng vậy a, độ khó bao lớn, không thể tưởng tượng! Ta xem qua một chút liên quan tới tiên tổ thư tịch, nghe nói Lưu Thủy tiên tổ, lĩnh ngộ loại này kiếm ý về sau, từng ý định học tập cái khác, kết quả, suýt chút nữa tẩu hỏa nhập ma...”"),i("br"),i("br"),n._v("Thở dài một tiếng, Kiếm Tần Sinh quay đầu nhìn về phía cách đó không xa thanh niên, đang định nói tiếng xin lỗi, để hắn rời khỏi, liền nghe đến một tiếng dồn dập nổ vang."),i("br"),i("br"),n._v("Một cỗ kiếm ý, thẳng tắp từ đối phương mi tâm phun ra, ngay phía trước “Kiếm” chữ, giống như là cảm nhận được cái gì, hưng phấn mà một hồi kịch liệt rung động, phòng ốc đều xuất hiện “Kẹt kẹt!” Thanh âm."),i("br"),i("br"),n._v("“Tiên tổ lĩnh ngộ kiếm đạo chân giải?” Kiếm Tần Sinh con ngươi co rụt lại."),i("br"),i("br"),n._v("(Hoan nghênh mọi người tích cực tham gia 《 Thiên Đạo thư viện 》 mùa hạ hoạt động, ngày 11 tháng 7 tám giờ tối, có kí tên sách, kí tên áo thun đưa tặng. Cụ thể hoạt động phương án nội dung, xin chú ý lão nhai công chúng số, Wechat lục soát “Hoành Tảo Thiên Nhai” tăng thêm chú ý là đủ.)"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1334"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1336"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);