(window.webpackJsonp=window.webpackJsonp||[]).push([[39],{394:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1011-tho-diep-vuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1011-tho-diep-vuong"}},[n._v("#")]),n._v(" Chương 1011: Thổ Diệp Vương")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Lúc này Nhược Thanh Viễn, không chỉ nhịp tim ngừng, vốn là có chút thương thân thể, càng tràn đầy khô quắt, gầy da bọc xương, con mắt lồi ra, giống như là nhìn thấy cái gì cực kỳ hoảng sợ chuyện, bàn tay xiết chặt, một phần nọc độc, còn chưa kịp thả ra, liền bị trực tiếp đánh chết."),t("br"),t("br"),n._v("“Đúng hay không?”"),t("br"),t("br"),n._v("Sắc mặt tái xanh đứng tại phía trên, Bạch độc sư dường như nhìn ra cái gì, quay đầu nhìn về phía bên người Hải độc sư."),t("br"),t("br"),n._v("“Đúng...”"),t("br"),t("br"),n._v("Người sau vẻ mặt nghiêm túc gật đầu."),t("br"),t("br"),n._v("“Cùng ta đoán đồng dạng!”"),t("br"),t("br"),n._v("Bạch độc sư híp mắt lại, thanh âm như đao, mang theo làm người sợ run sát ý: “Dị Linh tộc nhân...”"),t("br"),t("br"),n._v("Nghe nói như thế, Trương Huyền lông mày nhăn lại: “Các ngươi nói... Nhược Thanh Viễn điện chủ là Dị Linh tộc nhân giết?”"),t("br"),t("br"),n._v("Độc sư danh tiếng mặc dù không tốt, nhưng cũng không giống như vu hồn sư đồng dạng, đầu nhập vào Dị Linh tộc nhân, nếu không, cho dù che giấu lại sâu, Danh Sư đường cũng sẽ đem hắn đào ra."),t("br"),t("br"),n._v("Không cùng xuất hiện, nhưng xuất hiện ở đây, chém giết một vị điện chủ, đây rốt cuộc chuyện gì xảy ra?"),t("br"),t("br"),n._v("Nghe đối phương giọng nói, hiển nhiên đã xác nhận."),t("br"),t("br"),n._v("“Nhược điện chủ thi thể khô quắt, là bị người hút đi tinh huyết, loại công kích này chiêu số, chỉ có Dị Linh tộc nhân mới có...”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Bạch độc sư giải thích."),t("br"),t("br"),n._v("“Hút đi tinh huyết?” Trương Huyền trong mắt hoa văn nhúc nhích, lần nữa hướng Nhược điện chủ thi thể nhìn sang, vẻ mặt cũng biến thành khó coi."),t("br"),t("br"),n._v("Cùng đối phương nói đồng dạng, điện chủ trong thi thể tinh huyết không còn một mảnh, giống như là bị cái gì thôn phệ, mười phần đáng sợ."),t("br"),t("br"),n._v("Tinh huyết là nhân loại sinh tồn căn bản, bị cứ thế mà rút sạch, trải qua thống khổ có thể tưởng tượng được, cái này đã không riêng gì chém giết, mà là tàn sát!"),t("br"),t("br"),n._v("“Đây chính là Dị Linh tộc nhân thủ đoạn công kích?” Trong lòng khiếp sợ."),t("br"),t("br"),n._v("Mặc dù hắn tru diệt không ít Dị Linh tộc nhân, nhưng mà trên cơ bản đều là đối phương còn chưa xuất thủ thời điểm liền bị giết chết, đối hắn tàn bạo thủ đoạn, không có quá nhiều cảm xúc, nhìn thấy trước mắt Nhược điện chủ thi thể mới biết được cái chủng tộc này, tại thời kỳ Thượng Cổ, vì cái gì làm cho nhân loại sợ hãi như thế."),t("br"),t("br"),n._v("Hút sạch tinh huyết, cho dù y thuật thông thiên, cũng không có cách nào cứu chữa."),t("br"),t("br"),n._v("“Dị Linh tộc nhân hẳn là tới cướp đoạt quyển kia thư tịch!”"),t("br"),t("br"),n._v("Bạch độc sư nói tiếp."),t("br"),t("br"),n._v("Đối phương lẻn vào nơi này, sớm không động thủ, muộn không động thủ, nhưng Nhược Thanh Viễn đến điện chủ lăng, tìm kiếm thư tịch thời điểm đem chém giết, mục đích rõ ràng."),t("br"),t("br"),n._v("Hải độc sư gật đầu, ánh mắt như điện: “Hắn vừa mới bị giết, thanh âm cũng là mới truyền ra không lâu, hung thủ hẳn là còn không có trốn xa, hiện tại đuổi lời nói còn kịp...”"),t("br"),t("br"),n._v("Nói xong cẩn thận hướng chung quanh nhìn sang, một lát sau, bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Đối phương có cực mạnh phản truy tung ý thức, một chiêu liền đem Nhược điện chủ đánh giết, không có để lại chút nào dấu vết, hòn đảo như thế lớn, một khi tìm nhầm phương hướng, lấy đối phương thực lực, lại nghĩ theo dõi liền gần như không có khả năng."),t("br"),t("br"),n._v("“Ta đến xem!”"),t("br"),t("br"),n._v("Trương Huyền con mắt lạc lên."),t("br"),t("br"),n._v("Bọn họ vừa nghe đến động tĩnh liền xông lại, dựa theo tình huống bình thường, hẳn là còn không có trốn quá xa."),t("br"),t("br"),n._v("Chỉ cần không có chạy trốn, nhờ vào Bạch độc sư, Hải độc sư thực lực bây giờ, hẳn là có thể nhẹ nhõm đem hắn chế ngự, thay đối phương báo thù."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn, dọc theo cái hố nhìn kỹ một vòng, rất nhanh trên mặt đất thấy được một tia dấu vết."),t("br"),t("br"),n._v("“Hướng cái hướng kia đi!” Ngón tay một ngón tay."),t("br"),t("br"),n._v("“Chỗ này?”"),t("br"),t("br"),n._v("Nhìn thấy hắn chỉ ra phương hướng, Bạch độc sư đám người nghi ngờ."),t("br"),t("br"),n._v("Đúng là bọn họ vừa qua khỏi tới vị trí."),t("br"),t("br"),n._v("Một đường vọt tới, cũng không có trông thấy có người chạy trốn, hơn nữa nơi đó là Độc điện đại bản doanh, giết điện chủ, không nghĩ biện pháp chạy trốn, còn hướng đại bản doanh đi đến, không phải muốn chết sao?"),t("br"),t("br"),n._v("Không phải bọn họ không tin vị này Tôn Cường, mà là không có tổ sư ý niệm, đối phương chẳng qua Tòng Thánh mà thôi, so với bọn họ loại này Thánh vực ngũ trọng cường giả vẫn là kém rất nhiều."),t("br"),t("br"),n._v("“Đi theo ta!”"),t("br"),t("br"),n._v("Biết để hung thủ chạy trốn, thư tịch liền có thể sẽ không còn được gặp lại, Trương Huyền cũng không giải thích, vội vã hướng nhìn ra phương hướng, bay lượn mà đi."),t("br"),t("br"),n._v("Thấy hắn như thế khẳng định, Bạch độc sư hai người không còn xoắn xuýt, bám chặt theo."),t("br"),t("br"),n._v("Thời gian không dài, đi ra điện chủ lăng, Trương Huyền nhìn chung quanh một chút, mày nhăn lại."),t("br"),t("br"),n._v("Đầu này Dị Linh tộc nhân hết sức giảo hoạt, trước đó còn có dấu vết, hiện tại liền xem như Minh Lý Chi Nhãn cũng nhìn không ra."),t("br"),t("br"),n._v("Chẳng qua cũng đủ để chứng minh, tên kia hẳn là liền giấu ở lân cận, còn chưa kịp chạy trốn."),t("br"),t("br"),n._v("“Ở đâu?”"),t("br"),t("br"),n._v("Đây là một mảnh mặt đất bằng phẳng, chung quanh có một ít không cao to lắm kiến trúc, cùng một chút rải rác cây cối. Một cái liền có thể nhìn cái đại khái, dựa theo đạo lý, không có bất kỳ cái gì chỗ có thể ẩn tàng thân hình."),t("br"),t("br"),n._v("“Trừ phi...” Con ngươi co rụt lại, Trương Huyền nhịn không được cúi đầu."),t("br"),t("br"),n._v("Trên trời cùng trên mặt đất đều không có, vậy chỉ có một loại khả năng... Trốn xuống đất."),t("br"),t("br"),n._v("Chỉ là chung quanh không có bất kỳ cái gì cái hố, mặt đất cũng bóng loáng như mới, cho dù muốn đi vào, cũng vào không được ah."),t("br"),t("br"),n._v("“Thế nào? Ngươi ý tứ người kia trốn xuống đất?”"),t("br"),t("br"),n._v("Nhìn ra hắn mê man cùng nghi ngờ, Bạch độc sư đi tới trước mặt."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền nhẹ gật đầu: “Dấu vết đến nơi đây biến mất, loại trừ lòng đất, thực sự nghĩ không ra có thể đi nơi nào!”"),t("br"),n._v(" “Cái này đơn giản, có phải hay không lòng đất, dò xét một chút là tốt rồi!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Bạch độc sư cũng không nhiều lời, nguyên thần huyệt ánh sáng lóe lên, một cái cùng thứ nhất mô hình đồng dạng bóng người, chui ra."),t("br"),t("br"),n._v("Nguyên thần xuất khiếu!"),t("br"),t("br"),n._v("Nhẹ nhàng nhoáng một cái, hướng mặt đất chui đi qua, trong nháy mắt liền tiến vào trong đó, biến mất ở trước mắt."),t("br"),t("br"),n._v("Nguyên thần cùng hồn phách đồng dạng, có thể tùy ý biến hóa, không giống thân thể, chịu đến đủ loại gò bó, mặt đất mặc dù cứng rắn, muốn đi vào vẫn là dễ như trở bàn tay."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Bạch độc sư nguyên thần xuống đất không lâu, mọi người liền cảm thấy dưới chân một hồi lay động, ngay sau đó liền thấy một bóng người “Vù vù!” Một chút, từ dưới đất chui ra, trực tiếp xông về phía trước."),t("br"),t("br"),n._v("“Trốn chỗ nào!”"),t("br"),t("br"),n._v("Nhìn thấy bóng người này, Hải độc sư gào lên một tiếng, bỗng nhiên bắt tới, lực lượng hùng hậu, hình thành một cái cực lớn chưởng ấn, còn không có hạ xuống, mặt đất kiến trúc, thân cây liền nhao nhao nổ tung."),t("br"),t("br"),n._v("Lúc này Hải độc sư, đã đạt đến Thánh vực ngũ trọng, tùy tiện ra tay, liền có dời núi lấp biển lực lượng, để cho người ta khó mà ngăn cản."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Chạy ra bóng người, nhìn thấy công kích của hắn, biết một khi bị đánh trúng hẳn phải chết không nghi ngờ, trên không trung bỗng nhiên một đoàn, trực tiếp rơi xuống dưới."),t("br"),t("br"),n._v("Tốc độ dường như so vừa rồi nhảy ra mặt đất đều muốn nhanh thêm mấy phần."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Tảng đá đồng dạng đập xuống đất, vốn nghĩ khẳng định sẽ bị thương hộc máu, nhưng giống như là giọt nước rơi xuống cát, thời gian nháy mắt biến mất không thấy hình bóng."),t("br"),t("br"),n._v("“Đây là... Địa Hành thuật!”"),t("br"),t("br"),n._v("Tại mặt đất đánh ra một cái cực lớn chưởng ấn, lại không tìm tới bóng người, Hải độc sư con ngươi co rụt lại."),t("br"),t("br"),n._v("“Địa Hành thuật?” Trương Huyền sững sờ, không rõ ràng cho lắm."),t("br"),t("br"),n._v("“Là một loại đặc thù huyết thống mới có năng lực, có thể cùng nguyên thần đồng dạng dưới đất đi xuyên, không chịu đến ràng buộc...”"),t("br"),t("br"),n._v("Nhanh chóng giải thích một câu, Hải độc sư hét lớn một tiếng: “Bạch Đình, ngươi ở phía dưới truy kích, ta ở phía trên... Không thể để cho cái tên này chạy trốn!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Lòng đất truyền đến Bạch độc sư thanh âm."),t("br"),t("br"),n._v("Hắn nguyên thần xuất khiếu, có thể tùy ý dưới đất đi lại, hai người lúc lên lúc xuống, đối phương cho dù nắm giữ đi năng lực, tất nhiên cũng không cách nào chạy trốn!"),t("br"),t("br"),n._v("Rầm rầm rầm!"),t("br"),t("br"),n._v("Một hồi đung đưa kịch liệt, bốn phía kiến trúc như là đống cỏ đồng dạng sụp đổ, Trương Huyền đám người bay đến trên không nhìn xuống dưới, chỉ thấy dưới mặt đất dường như có hai phía cự long đang dây dưa, chỗ đến, xuất hiện từng đạo khe nứt to lớn."),t("br"),t("br"),n._v("Vết nứt trực tiếp hướng về phía trước lan tràn, nham thạch cuồn cuộn, đục ngầu sóng khí, đem chung quanh bao phủ."),t("br"),t("br"),n._v("Bạch độc sư vừa mới đột phá Thánh vực ngũ trọng, đối lực lượng khống chế còn không có đạt tới hoàn mỹ như một, trên không trung chiến đấu ngược lại cũng thôi, dưới đất, chắc chắn cho Độc điện mang đến cực lớn tai nạn."),t("br"),t("br"),n._v("Nếu như nói trước đó Trương Huyền thân thể cùng Nhược điện chủ tranh đấu, để Độc điện những này ngàn năm kiến trúc khổ không thể tả lời nói, giờ phút này quả thực chính là hủy diệt tính tiến công!"),t("br"),t("br"),n._v("Mặt đất cuồn cuộn, hình như là cự thú nhúc nhích, vô số lộng lẫy kiến trúc biến thành phế tích."),t("br"),t("br"),n._v("Sưu!"),t("br"),t("br"),n._v("Không biết qua bao lâu, một thân ảnh lần nữa theo mặt đất chui ra, lần này lại không có lần trước tốc độ, mới xuất hiện, liền một ngụm máu tươi phun ra, ngã rầm trên mặt đất."),t("br"),t("br"),n._v("Một mực canh giữ ở phía ngoài Hải độc sư không có chút gì do dự, năm ngón tay mở ra, nhẹ nhàng vồ một cái, mấy đạo chân khí hình thành lồng giam liền đem nó bao phủ ở bên trong, sít sao khóa lại."),t("br"),t("br"),n._v("“Quả nhiên là Dị Linh tộc nhân!” Thấy rõ ràng bộ dáng của đối phương, Trương Huyền nhướng mày."),t("br"),t("br"),n._v("Cái tên này cùng mình giết chết Kim Diệp Vương đám người có chút tương tự, trên thân tản mát ra lăng lệ khí tức, đã đạt đến Thánh vực tứ trọng."),t("br"),t("br"),n._v("“Thiếu chủ, vị này là Thổ Diệp Vương! Cũng là chúng ta Thanh Điền thập đại vương giả một trong, am hiểu đi, sức chiến đấu khả năng không bằng ta, nhưng mà chạy trốn lời nói, tuyệt đối có thể xếp được đệ nhất!”"),t("br"),t("br"),n._v("Tử Diệp vương thanh âm, theo Thiên Nghĩ Phong sào lặng lẽ truyền tới."),t("br"),t("br"),n._v("Cái tên này là Thánh vực tứ trọng cường giả, chỉ cần Trương Huyền bằng lòng, thần thức có thể nhẹ nhõm lan tràn đi ra, kiểm tra tình huống chung quanh."),t("br"),t("br"),n._v("“Thổ Diệp Vương? Lại là thập đại vương giả? Hắn làm sao lại xuất hiện ở đây?” Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("Cái này cái gọi là Thanh Điền thập đại vương giả, mình bây giờ đã gặp một nửa, hơn nữa chết liền chết thu phục thu phục, cơ bản đều không có quan hệ gì với chính mình."),t("br"),t("br"),n._v("Cho dù cái này, cũng là bởi vì bản thân bang Bạch độc sư, Hải độc sư tăng lên thực lực, nếu không nhờ vào hắn đi năng lực, cùng cấp bậc căn bản không ngăn cản nổi."),t("br"),t("br"),n._v("“Hẳn là bản này địa đồ can hệ trọng đại, Thanh Điền Hoàng không yên lòng ta cùng Thủy Diệp vương, lại lặng lẽ phái hắn tới, chuẩn bị bất cứ tình huống nào! Thủy Diệp vương bị giết, ta thần phục về sau, cái tên này theo dõi chủ nhân cùng Hứa trưởng lão đến nơi này.”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Tử Diệp vương suy đoán nói."),t("br"),t("br"),n._v("Độc điện vị trí, bọn họ mấy đại vương giả tất cả đều không rõ ràng, bằng không thì cũng không có khả năng mệnh lệnh thành chủ, tìm kiếm khắp nơi Địa Tàng hoa, dùng để dụ dỗ Độc điện trưởng lão mắc câu rồi."),t("br"),t("br"),n._v("Sở dĩ xuất hiện ở đây, chỉ sợ là lặng lẽ theo dõi bọn hắn."),t("br"),t("br"),n._v("“Chưa chắc là đi theo chúng ta, ta cảm thấy có khả năng theo dõi Bạch độc sư hai người.” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn rời khỏi Tĩnh Viễn thành thời điểm mười phần cẩn thận, nếu có người theo dõi, tất nhiên có thể phát giác."),t("br"),t("br"),n._v("Ngược lại là Bạch độc sư đám người, không ngờ tới điểm ấy, khả năng mới bị đối phương có thời cơ lợi dụng."),t("br"),t("br"),n._v("Đổi lại những người khác, Độc điện mê trận, mạnh mẽ vô cùng, làm cho không người nào có thể đi vào, nhưng mà đối mặt cái này có thể dưới đất đi xuyên Thổ Diệp Vương, có lẽ liền không có khó khăn như vậy."),t("br"),t("br"),n._v("Cái tên này kiêng kị Độc điện, vẫn giấu kín lấy, không dám xuất một chút tay, thẳng đến Nhược điện chủ tự mình hành động, lúc này mới lặng lẽ đi theo, đợi hắn lấy ra thư tịch, một lần tiến công."),t("br"),t("br"),n._v("Dựa theo trước đó phỏng đoán, nhờ vào đi năng lực, hoàn toàn có thể bỏ trốn, khả năng nằm mơ đều không có nghĩ đến, Bạch, Hải hai đại độc sư trong đêm đột phá, lúc này mới bị tại chỗ vây khốn, lại không có cách nào chạy trốn."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1019"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1021"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);