(window.webpackJsonp=window.webpackJsonp||[]).push([[391],{746:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1329-tam-tac-kiem-tan-sinh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1329-tam-tac-kiem-tan-sinh"}},[n._v("#")]),n._v(" Chương 1329: Tâm tắc Kiếm Tần Sinh")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1329: Tâm tắc Kiếm Tần Sinh"),t("br"),t("br"),n._v("“Thử một chút chân chính Nhất Kiếm Phá Hải đi!”"),t("br"),t("br"),n._v("Con mắt sáng lên, Trương Huyền một tiếng gào thét, bàn tay lớn lăng không một trảo."),t("br"),t("br"),n._v("Vừa rồi thi triển, chỉ là Linh Hư tam kiếm bên trong kiếm ý, cũng không dùng hết toàn lực, giờ phút này thấy đối phương ngăn cản lên mười phần nhẹ nhõm, lại không chần chừ."),t("br"),t("br"),n._v("Đùng đùng!"),t("br"),t("br"),n._v("Một hồi không gian xé rách thanh âm, hơn một trăm chuôi trung phẩm Thánh khí trường kiếm gào thét lên bay ra, yên tĩnh lơ lửng giữa không trung."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Trong mắt hoa văn lóe lên, một bộ rườm rà phức tạp kiếm pháp, thẳng tắp hướng về phía trước đâm ra ngoài."),t("br"),t("br"),n._v("Nhất Kiếm Phá Hải tuy chỉ là một chiêu kiếm pháp, nhưng là mượn nhờ 108 thanh trường kiếm tổ hợp thành to lớn kiếm trận, mỗi một chuôi đều ẩn chứa một bộ kiếm chiêu, tính gộp lại, tựa như thiên quân vạn mã."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Kiếm Đồ hóa thành biển, bao phủ mà xuống, vô số đạo kiếm khí, lôi kéo khắp nơi, trong chốc lát liền đem cả phòng, bao phủ ở bên trong."),t("br"),t("br"),n._v("“Đây là... Kiếm lão nhân tuyệt chiêu? Ngươi...”"),t("br"),t("br"),n._v("Chặn lại kiếm khí của đối phương, đang cảm thấy mười phần nhẹ nhõm Kiếm Tần Sinh, đột nhiên nhìn thấy một trương Kiếm Đồ, bao phủ mà xuống, con ngươi thu hẹp, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Hắn mặc dù chưa bao giờ thấy qua Kiếm lão nhân, nhưng mà với tư cách trước kia dùng kiếm cường giả, đã sớm từng nghe nói họ tên, cũng đã được nghe nói tuyệt kỹ của hắn."),t("br"),t("br"),n._v("Thậm chí lúc trước bốn phía du lịch thời điểm, còn chuyên môn đi qua Kiếm trì học qua một đoạn thời gian, phát hiện lý niệm không hợp, mới như vậy coi như thôi."),t("br"),t("br"),n._v("Vốn cho rằng người này truyền thừa sớm đã cắt đứt, nằm mơ đều không có nghĩ đến, trước mắt vị thanh niên này phát huy ra, hơn nữa trò giỏi hơn thầy thắng vu lam, so trong truyền thuyết càng thêm cường đại, càng thêm phong mang tất lộ."),t("br"),t("br"),n._v("Mấu chốt nhất là... Vừa mới là đối phương không có binh khí thích hợp, đưa một chuôi, nằm mơ đều không có nghĩ đến, một cái chớp mắt lấy ra như thế một đống lớn tới."),t("br"),t("br"),n._v("Ngươi có nhiều như vậy, làm gì cầm cái kia thượng phẩm cấp bậc?"),t("br"),t("br"),n._v("Da mặt lay động, cũng lại không để ý tới cái khác, hai tay mở ra, kiếm khí theo đầu ngón tay bắn ra, trước mắt nước chảy bộ dáng bình chướng, lập tức trở nên càng thêm dày hơn nặng."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Mới vừa làm xong những này, hơn một trăm thanh trường kiếm liền đâm tới, to lớn lực trùng kích, rơi vào bình chướng bên trên, “Răng rắc! Răng rắc!” Liên tiếp giòn vang, người sau xuất hiện vết rách, ngay sau đó thủy tinh vỡ vụn."),t("br"),t("br"),n._v("“Những này kiếm khí làm thế nào biết ta Lưu Thủy kiếm quyết bên trong thiếu hụt?”"),t("br"),t("br"),n._v("Thân thể run lên."),t("br"),t("br"),n._v("Hắn vừa mới thi triển kiếm quyết, tựa như nước chảy, rút đao khó đoạn, đối phương lại có thể một lần đánh nát, nói rõ tìm được sơ hở cùng thiếu hụt, để hắn lại khó duy trì di động trạng thái."),t("br"),t("br"),n._v("Lúc trước cùng Trương gia vị kia cao thủ sử dụng kiếm tỷ thí, hắn thực lực như thế đều không thể làm được điểm ấy, trước mắt vị này đến cùng làm sao làm được?"),t("br"),t("br"),n._v("Tràn đầy khiếp sợ, muốn ngăn cản chuyện này phát sinh, lại phát hiện đã tới đã không kịp, hơn trăm thanh trường kiếm bố chế thành Kiếm Đồ, bốn phương tám hướng, tuôn ra mà tới, như là bão tố, hội tụ thành hồng lưu, xuyên thấu qua chiêu số bên trong sơ hở, mạnh mẽ đâm vào trên thân."),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Yết hầu ngòn ngọt, bay ngược mà ra, “Phanh” một tiếng dán tại trên vách tường."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Sắp đặt tại bốn phía phong ấn, tại to lớn lực trùng kích bên dưới, phát ra từng tiếng giòn vang, tựa như lúc nào cũng sẽ nổ tung."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Người dán tại trên tường, công kích còn chưa kết thúc, kiếm ảnh tầng tầng, lại liên tiếp mấy trăm đạo lực lượng, liên tiếp đánh tới, giống như thủy triều, một đợt so một đợt cường thế."),t("br"),t("br"),n._v("Cả phòng, giống như là sáng lên cầu vồng bảy màu, lắc con mắt thấy không rõ lắm, Kiếm Tần Sinh thân ảnh bị kiếm mang triệt để bao phủ."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Phong ấn cũng lại không chịu nổi, rầm rầm nổ tung, gian phòng không còn trận pháp gia trì, cũng phát ra “Kẹt kẹt” thanh âm, không thể kiên trì được nữa, rầm rầm sụp đổ."),t("br"),t("br"),n._v("Đá vụn bắn tung toé, khói bụi xao động."),t("br"),t("br"),n._v("Lơ lửng giữa không trung Kiếm Tần Sinh muốn chạy trốn, cũng không kịp, kiếm ảnh không ngừng rơi xuống, mỗi một chiêu đều đánh vào hắn chiêu số sơ hở chỗ, phòng bị đều làm không được."),t("br"),t("br"),n._v("Rầm rầm rầm!"),t("br"),t("br"),n._v("Thân ảnh không rất lùi lại, liên tục va sụp, bảy, tám gian phòng ốc, mới dừng lại."),t("br"),t("br"),n._v("“Hô!”"),t("br"),t("br"),n._v("Một hơi phun ra, Trương Huyền khuôn mặt trắng bệch thu về bàn tay, lúc này mới cảm thấy toàn thân đau đớn, liền giơ ngón tay lên lực lượng cũng không có."),t("br"),t("br"),n._v("Vừa rồi chỉ là trong lòng thôi diễn, cũng không thực tế vận dụng, chân chính thi triển đi ra mới biết được, chiêu này uy lực khủng bố đến mức nào, hơn nữa công kích đến cuối cùng, lấy hắn lực lượng bây giờ thế mà không cách nào khống chế, thẳng đến tiêu hao sạch sẽ trong cơ thể một điểm cuối cùng chân khí."),t("br"),t("br"),n._v("“Thật là đáng sợ, thân bất do kỷ, về sau vẫn là ít sử dụng cho thỏa đáng...”"),t("br"),t("br"),n._v("Trên đầu toát ra mồ hôi lạnh, Trương Huyền nhịn không được có chút hoảng sợ."),t("br"),t("br"),n._v("Chiêu này thi triển đến cuối cùng, đã không nhận hắn khống chế."),t("br"),t("br"),n._v("Cũng không phải nói chiêu thức có vấn đề gì, mà là thực lực của hắn vẫn là quá thấp, không đủ để hoàn mỹ phát huy, thật giống như hài đồng cầm lấy chuỳ sắt, uy lực rất lớn, nhưng mà vung vẩy đi xuống, muốn thu tay lại cũng khó xử đến."),t("br"),t("br"),n._v("Hắn hiện tại liền là loại này cầm chuỳ sắt hài đồng, Nhất Kiếm Phá Hải, toàn lực thi triển, không đem chân khí trong cơ thể triệt để tiêu hao sạch sẽ, căn bản là không có cách thu hồi."),t("br"),t("br"),n._v("Tu luyện Phong Thánh giải chân khí, có thể đạt tới Thánh vực, hầu như chưa hề dùng hết qua, bây giờ lại giọt nước không dư thừa, đủ thấy chiêu này đến cùng khủng bố cỡ nào!"),t("br"),t("br"),n._v("Cùng người đối chiến, chân khí tiêu hao sạch sẽ, sẽ mười phần nguy hiểm, làm không cẩn thận chết cũng không biết chết như thế nào!"),t("br"),t("br"),n._v("“Đúng rồi, Kiếm Tần Sinh tiền bối không có sao chứ?”"),t("br"),t("br"),n._v("Vừa rồi chỉ lo bản thân đánh thoải mái a, không có chú ý đối phương tình huống, nhìn trước mắt đầy đất phế tích, Trương Huyền nhịn không được có chút lo lắng."),t("br"),t("br"),n._v("Người này mặc dù ngoài miệng nói, không áp chế tu vi, nhưng cũng đem lực lượng giảm thấp xuống, ngộ nhỡ không chịu nổi, bị đánh chết tươi, bản thân tránh không được hung thủ giết người?"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Loại này lo lắng cảm xúc, còn không có triệt để dâng lên, chỉ thấy một bóng người theo phế tích bên trong bay."),t("br"),t("br"),n._v("Chính là Kiếm Tần Sinh."),t("br"),t("br"),n._v("Hắn lúc này cũng lại không còn cao thủ bộ dáng, quần áo trên người phá thật nhiều chỗ, đầy người lỗ kiếm, không nói ra được chật vật."),t("br"),t("br"),n._v("“Tiền bối...” Khóe miệng giật một cái, Trương Huyền vội vàng hỏi."),t("br"),n._v("“Ta không sao! Phốc!”"),t("br"),t("br"),n._v("Xấu hổ nở nụ cười, nói còn chưa dứt lời, lần nữa một ngụm máu tươi phun ra, ngay sau đó “Phù phù!” Một lần, Kiếm Tần Sinh liền từ không trung rớt xuống, một đầu đâm vào bùn đất bên trong, đầy người bụi bặm."),t("br"),t("br"),n._v("Hắn hiện tại nội tâm là sụp đổ, dựa theo tình huống bình thường, lấy thực lực của hắn, kiếm của đối phương chiêu, cho dù cường đại hơn nữa, cũng không thể tổn thương hắn chút nào."),t("br"),t("br"),n._v("Nhưng hắn chỉ vận dụng một phần trăm lực lượng, hơn nữa Lưu Thủy kiếm quyết bị đánh trúng thiếu hụt trực tiếp phá vỡ, không có chút nào phòng bị phía dưới, không có tại chỗ bỏ đi, đã mệnh rất lớn."),t("br"),t("br"),n._v("Nằm tại trong phế tích, nuốt vào một viên đan dược, qua thật lâu, mới chậm rãi khôi phục lại."),t("br"),t("br"),n._v("Đứng dậy, nhìn bốn phía, trong lòng một hồi buồn bực."),t("br"),t("br"),n._v("Vốn là muốn nhìn một lần đối phương xác thực thực lực, cùng đối với kiếm pháp lĩnh ngộ, kết quả bị một chiêu đánh thành như vậy... May mắn không có người nhìn thấy, nếu không mất mặt đều ném chết rồi..."),t("br"),t("br"),n._v("“Lão sư, ngươi không sao chứ?”"),t("br"),t("br"),n._v("“Lão gia, chuyện gì xảy ra?”"),t("br"),t("br"),n._v("“Đây là người nào làm? Đừng để hung thủ chạy trốn...”"),t("br"),t("br"),n._v("Đang tại âm thầm vui mừng, liền nghe đến bốn phía thanh âm huyên náo vang lên."),t("br"),t("br"),n._v("Xoay người nhìn chỉ thấy Thủy Thiên Nhu, Tạ sư huynh các đệ tử, cùng một đám quản gia, hộ vệ, con mắt chờ mong đứng tại cách đó không xa, đồng loạt nhìn qua, tràn đầy lo lắng."),t("br"),t("br"),n._v("“Các ngươi sao lại tới đây?”"),t("br"),t("br"),n._v("Mắt tối sầm lại, Kiếm Tần Sinh suýt chút nữa ngã sấp xuống."),t("br"),t("br"),n._v("Vừa nghĩ tới, không có bị người thấy được, liền thấy lít nha lít nhít tiếp cận hơn trăm người, đây là toàn bộ phủ đệ người đều tới đi!"),t("br"),t("br"),n._v("Làm cái gì?"),t("br"),t("br"),n._v("“Trương sư nói lão sư ngươi bị thương, để chúng ta tới xem một chút, Tạ sư huynh cho rằng đã xảy ra chuyện gì, không quá yên tâm, liền đem bọn họ đều hô tới...”"),t("br"),t("br"),n._v("Thủy Thiên Nhu giải thích nói."),t("br"),t("br"),n._v("“...” Che ngực, Kiếm Tần Sinh cảm thấy đau lòng không thể thở nổi."),t("br"),t("br"),n._v("Đem hắn đánh thảm như vậy, ngược lại cũng thôi, còn gọi tới học sinh, hạ nhân vây xem..."),t("br"),t("br"),n._v("Quá đáng ah!"),t("br"),t("br"),n._v("“Ta cũng không bị thương, chỉ là vừa mới diễn luyện kiếm pháp thời điểm không cẩn thận đem phòng ốc đánh sập...”"),t("br"),t("br"),n._v("Mặt mo đỏ ửng, tràn đầy xấu hổ khoát tay áo."),t("br"),t("br"),n._v("“Không bị tổn thương?”"),t("br"),t("br"),n._v("Tạ sư huynh cùng Thủy Thiên Nhu đám người nhìn nhau."),t("br"),t("br"),n._v("Toàn thân lỗ kiếm, mặt mũi bầm dập, chật vật như thế... Đây chỉ là diễn luyện kiếm pháp?"),t("br"),t("br"),n._v("“Khụ khụ! Trương sư người đâu?”"),t("br"),t("br"),n._v("Biết các học sinh nghĩ cái gì, Kiếm Tần Sinh ho khan một tiếng, nói sang chuyện khác."),t("br"),t("br"),n._v("Mới vừa đứng lên, liền không thấy được đối phương, người làm sao không thấy?"),t("br"),t("br"),n._v("“Trương sư nói, nhìn lão sư thư tịch, mười phần cảm kích, có chút lĩnh ngộ, liền trở về tu luyện...”"),t("br"),t("br"),n._v("Thủy Thiên Nhu nói."),t("br"),t("br"),n._v("“Trở về tu luyện?”"),t("br"),t("br"),n._v("Kiếm Tần Sinh cảm thấy càng tâm tắc."),t("br"),t("br"),n._v("Đem nơi này làm cho một đoàn loạn tao, đem bản thân đánh thành trọng thương, bản thân lại vụng trộm chạy... Còn cảm kích, có như thế cảm kích ư?"),t("br"),t("br"),n._v("“Đúng rồi, lão sư, Trương sư trước khi đi cho cái này, nói để cho ta tự tay giao cho ngươi...”"),t("br"),t("br"),n._v("Đang tràn đầy buồn bực, hận không thể đem tên kia bắt trở lại giáo huấn một lần, chỉ thấy Thủy Thiên Nhu tiến lên một bước, đưa tới một quyển sách."),t("br"),t("br"),n._v("Nhíu nhíu mày, bàn tay một trảo, đem thư tịch cầm tới lòng bàn tay, tiện tay mở ra, chỉ nhìn liếc mắt, liền chấn động toàn thân, trên mặt hiện ra khó có thể tin màu sắc."),t("br"),t("br"),n._v("“Cái này, cái này...”"),t("br"),t("br"),n._v("Kịch liệt rung rung."),t("br"),t("br"),n._v("Trước mắt quyển sách này, vết mực tài cán, rất hiển nhiên là vừa mới viết xong xuôi, phía trên kỹ càng chỉ ra hắn trong tu luyện sai lầm, cùng đối kiếm quyết lĩnh ngộ bên trên chỗ nhầm lẫn."),t("br"),t("br"),n._v("Thậm chí có thật nhiều, là hắn nghi ngờ rất nhiều năm, lại không cách nào giải quyết nan đề..."),t("br"),t("br"),n._v("Nói cách khác, dựa theo phía trên này viết tu luyện, thực lực của hắn tuyệt đối có khả năng tiến thêm một bước..."),t("br"),t("br"),n._v("“Cùng ta đối chiến một chiêu, ta đều không có làm sao ra tay... Liền nhìn ra nhiều vấn đề như vậy?”"),t("br"),t("br"),n._v("Nắm quyển sách trên tay, Kiếm Tần Sinh khó tin."),t("br"),t("br"),n._v("Thân là kiếm đạo đại tông sư, càng là Thánh tử điện lão sư, hắn thường xuyên cho người khác giảng giải kiếm thuật, chỉ điểm tu vi, nguyên nhân chính là như vậy, nhãn lực cũng hết sức lợi hại, cùng cấp bậc không ít danh sư đều xa xa không bằng."),t("br"),t("br"),n._v("Dù vậy, đối với hắn chính mình vấn đề cũng không thể tránh được, trước mắt vị này chỉ nhìn liếc mắt, liền toàn bộ tìm ra, hơn nữa đưa cho biện pháp giải quyết..."),t("br"),t("br"),n._v("Cái này nhãn lực cùng đối kiếm đạo lý giải cũng thực sự quá kinh khủng đi!"),t("br"),t("br"),n._v("“Khó trách có thể tại bằng chừng ấy tuổi liền lĩnh ngộ hai loại chân giải, đối kiếm đạo lý giải, ta kém xa tít tắp...”"),t("br"),t("br"),n._v("Cho tới giờ khắc này, rốt cuộc minh bạch vì cái gì đối phương có khả năng nhanh như vậy đạt được bản thân tiên tổ kiếm ý công nhận, cũng sáng chế cường đại như vậy kiếm pháp."),t("br"),t("br"),n._v("Chỉ bằng vào loại này đối kiếm lý giải cùng lĩnh ngộ, liền là hắn đều xa xa không thể bằng."),t("br"),t("br"),n._v("Căn bản không tại một cái cấp bậc bên trên."),t("br"),t("br"),n._v("“Có lẽ... Đây mới thật sự là thiên tài đi!”"),t("br"),t("br"),n._v("Cảm khái một tiếng, trong lòng có chút buồn bã, đang muốn đem thư tịch thu hồi, đột nhiên nhớ tới một việc, ánh mắt sáng lên: “Đúng rồi!”"),t("br"),t("br"),n._v("“Đối kiếm đạo lĩnh ngộ thâm ảo như vậy, nếu như mang theo hắn đi Trương gia... Có lẽ có thể báo lúc trước một mũi tên mối thù!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1337"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1339"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);