(window.webpackJsonp=window.webpackJsonp||[]).push([[395],{750:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1332-tu-gioi-thieu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1332-tu-gioi-thieu"}},[n._v("#")]),n._v(" Chương 1332: Tự giới thiệu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1332: Tự giới thiệu"),t("br"),t("br"),n._v("“Sơn môn sập?”"),t("br"),t("br"),n._v("Trương Huyền ngẩn ngơ, cứng ngắc tại chỗ."),t("br"),t("br"),n._v("Hôm qua xông thời điểm, còn rất tốt, hơn vạn học sinh tiến vào bên trong, mỗi người thi triển tài năng và học vấn, cuối cùng quyết ra năm trăm người đứng đầu... Làm sao lại đột nhiên sập?"),t("br"),t("br"),n._v("“Vừa rồi Phí sư nói!”"),t("br"),t("br"),n._v("Trần Nhạc Dao nói nhỏ, trong mắt cũng đầy là khó tin."),t("br"),t("br"),n._v("Rất hiển nhiên, tin tức này, đối với nàng mà nói, cũng cực kỳ rung động, nếu không, cũng không có khả năng nhìn thấy Trương Huyền, liền trực tiếp nói ra."),t("br"),t("br"),n._v("“Phí sư?”"),t("br"),t("br"),n._v("“Liền là phía trước cái kia, hắn là lớp tinh anh chủ nhiệm lớp, về sau có chuyện gì có thể tìm hắn, không hiểu, cũng có thể hướng hắn tư vấn!” Trần Nhạc Dao chỉ về phía trước."),t("br"),t("br"),n._v("Trương Huyền nhìn, một người trung niên đang đứng tại ngay phía trước, mấy cái học sinh vây quanh ở bốn phía, tựa hồ tại hỏi thăm sự tình gì."),t("br"),t("br"),n._v("“Nói bởi vì cái gì sao?”"),t("br"),t("br"),n._v("Xông sơn môn quy định, đã trải qua vài vạn năm, đều sừng sững không ngã, làm sao đột nhiên xuất hiện như vậy biến cố?"),t("br"),t("br"),n._v("“Hình như là nói, trấn áp sơn môn Quý Thánh pho tượng bể nát!” Suy nghĩ một chút, Trần Nhạc Dao nói."),t("br"),t("br"),n._v("Trương Huyền mặt mũi không hiểu."),t("br"),t("br"),n._v("Sơn môn sụp đổ cùng pho tượng vỡ vụn có quan hệ gì?"),t("br"),t("br"),n._v("“Xông sơn môn quy định, là Quý Thánh xây dựng, là một cái từ hắn chế tác không gian gấp, rất nhiều khảo hạch hạng mục cũng là từ ý niệm của hắn tiến hành khống chế, pho tượng vỡ vụn, liền đại biểu hắn lưu lại ý niệm biến mất, không gian lại không còn ủng hộ, một cách tự nhiên liền sụp đổ...”"),t("br"),t("br"),n._v("Trần Nhạc Dao nói."),t("br"),t("br"),n._v("“Cái này...” Trương Huyền cau mày."),t("br"),t("br"),n._v("Từng cùng Quý Thánh ở chung được ba ngày, đối vị này Thánh tử điện đời thứ nhất điện chủ, mười phần cảm kích, vốn cho rằng nắm giữ mở ra chưởng giáo lệnh thực lực về sau, lại đi tìm đối phương, ở trước mặt cảm ơn, không nghĩ tới nhanh như vậy, ý niệm liền tiêu tán."),t("br"),t("br"),n._v("“Tồn tại mấy vạn năm, dù là phần lớn thời giờ, đều đang ngủ say, sinh mệnh cũng đến phần cuối...”"),t("br"),t("br"),n._v("Cảm khái một tiếng."),t("br"),t("br"),n._v("Người người đều có tuổi thọ hạn chế, cho dù là Khổng sư đều không có vượt ra thời gian trường hà, siêu thoát ngoại vật."),t("br"),t("br"),n._v("Vị này Quý Thánh khi còn sống, mặc dù thực lực cực mạnh, nhưng mà sau khi chết ý niệm tồn tại thời gian, cũng là có hạn chế, một mực ngủ say lời nói, còn có thể tồn tại lâu một chút, mỗi một lần khảo hạch đều muốn thức tỉnh một lần, dần dà, tán loạn chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("“Đúng vậy a!” Trần Nhạc Dao gật đầu, nhịn không được cảm khái: “Quý Thánh ý niệm tiêu tán, về sau điện chủ vị trí thì càng khó xác lập...”"),t("br"),t("br"),n._v("“Điện chủ?”"),t("br"),t("br"),n._v("“Ngươi còn không biết đi, nghe nói, Quý Thánh ý niệm không riêng gì tuyển chọn học sinh, trọng yếu nhất chính là tuyển chọn mới điện chủ, Thánh tử điện, kỳ trước điện chủ, đều là từ hắn tự mình tuyển chọn mà ra... Hiện tại ý niệm tiêu tán, mới điện chủ còn không có thiết lập, bất kể ai đi làm, đều sẽ danh bất chính, ngôn bất thuận...”"),t("br"),t("br"),n._v("Xem như một phương thế lực thiên tài, có con đường tin tức của mình, việc này mặc dù cơ mật, đối với nàng mà nói cũng không tính quá khó hỏi thăm."),t("br"),t("br"),n._v("“Cái này...” Nháy nháy mắt, Trương Huyền đang muốn nói chuyện, liền nghe đến trong phòng ở giữa một cái to rõ thanh âm vang lên."),t("br"),t("br"),n._v("Quay đầu nhìn, chính là mới vừa rồi cùng mấy cái học sinh nói chuyện Phí sư."),t("br"),t("br"),n._v("“Xem ra chúng ta lớp tinh anh người đã đến đông đủ, đầu tiên, hoan nghênh chư vị trở thành Thánh tử điện học viên, sau này một đoạn thời gian, ta sẽ cùng các ngươi cùng một chỗ vượt qua. Hiện tại, xin các vị tự giới thiệu mình một chút, tận lực để mọi người mau sớm nhận biết ngươi, hơn nữa quen thuộc, bởi vì, về sau các ngươi muốn cùng bạn học của mình, ở chung tương đối một đoạn thời gian dài. Được rồi, vị thứ nhất, Phùng Tử Dật...”"),t("br"),t("br"),n._v("Trương Huyền nhìn."),t("br"),t("br"),n._v("Vị này Phùng Tử Dật, có khả năng liền Trương gia người đều đè ép một đầu, trở thành khảo hạch thứ nhất, nói rõ năng lực, hắn cũng rất muốn nhìn một chút, đến cùng là thần thánh phương nào."),t("br"),t("br"),n._v("Một thanh niên đi ra, thoạt nhìn, hai mươi bốn, năm bộ dạng, bộ dáng rất là bình thường, chưa từng có người khí tức, cũng không có kinh người dung mạo, đi trong đám người, không cẩn thận quan sát, có khả năng căn bản không phát hiện được."),t("br"),t("br"),n._v("Bất quá, Minh Lý Chi Nhãn chiếu rọi xuống, có thể rõ ràng nhìn ra đối phương trong cơ thể cuồng bạo khí tức, như là sông lớn sôi trào, một khi nhen lửa, chắc chắn bộc phát ra lực lượng kinh người."),t("br"),t("br"),n._v("“Lĩnh Vực cảnh hậu kỳ... Quả thực thật mạnh!”"),t("br"),t("br"),n._v("Vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Đối phương chẳng qua hai bốn hai lăm tuổi, liền có loại thực lực này, thiên phú có thể nói, cường đại đến đáng sợ."),t("br"),t("br"),n._v("“Ta tên Phùng Tử Dật!”"),t("br"),t("br"),n._v("Trong đám người đi ra, thanh niên không có quá nhiều lời nói, chỉ là giới thiệu tên, liền không nói thêm lời."),t("br"),t("br"),n._v("Bất quá, càng như vậy, vượt để cho người ta cảm thấy sâu không lường được."),t("br"),t("br"),n._v("“Vị thứ hai, Trương Hủ!”"),t("br"),t("br"),n._v("Phí sư nói."),t("br"),t("br"),n._v("Chỗ của hắn mặc dù có mỗi cái học viên tài liệu cặn kẽ, nhưng mà đối phương không muốn nói, cũng không dễ chịu giải thích thêm."),t("br"),t("br"),n._v("Nương theo lời nói, lại một thanh niên đi ra."),t("br"),t("br"),n._v("Vị này thoạt nhìn so Phùng Tử Dật còn muốn nhỏ, cùng mình không kém nhiều, chỉ có chừng hai mươi tuổi, có thể một thân thực lực, lại không yếu tại cái trước, cho người ta một loại, lật tay thành mây trở tay thành mưa cường thế cảm giác."),t("br"),t("br"),n._v("“Tại hạ Trương Hủ, đến từ Thánh Nhân Trương gia, hy vọng có thể cùng chư vị trở thành bằng hữu, cùng một chỗ tiến bộ!”"),t("br"),t("br"),n._v("Hắn không giống Phùng Tử Dật cao như vậy lãnh, mang theo nụ cười, nói một câu."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn chiếu rọi xuống, vị này Trương Hủ bất kể thân thể, chân khí vẫn là linh hồn, đều không có rõ ràng sơ hở, cường đại không tưởng nổi."),t("br"),t("br"),n._v("Trương Cửu Tiêu, Trương Khiêm thậm chí Trương Vân Phong ở trước mặt đối phương, cũng không tính cái gì, không đáng giá nhắc tới."),t("br"),t("br"),n._v("Cứ việc đối mới chỉ là Lĩnh Vực cảnh hậu kỳ, nhưng thật muốn chiến đấu, hắn dùng hết toàn lực, cũng chưa chắc có khả năng chiến thắng."),t("br"),n._v("Thân là Trương gia hạch tâm con cháu, huyết mạch lực lượng tất nhiên so Trương Khiêm càng thêm cường đại, lại thêm các loại tinh diệu võ kỹ cùng thủ đoạn, chỉ sợ có thể cùng bản thân đồng dạng, vượt qua mấy cái tiểu cấp bậc tiến hành chiến đấu."),t("br"),t("br"),n._v("Trương Hủ giới thiệu xong, kế tiếp là Viên Hải Thanh, Bạch Thanh Huyền..."),t("br"),t("br"),n._v("Nghe được mỗi người giới thiệu Trương Huyền thầm giật mình."),t("br"),t("br"),n._v("Khó trách các đại thế quý tộc, thế lực có thể một mực duy trì đỉnh phong, đối hậu bối bồi dưỡng quả nhiên có chỗ độc đáo."),t("br"),t("br"),n._v("Lớp tinh anh trước 50 tên thế mà hơn 40 vị đều ở trong đó, chân chính từ nhỏ địa phương tới, tính đi tính lại chỉ có hắn một cái."),t("br"),t("br"),n._v("“Nhìn tới danh sư đại lục cũng giống như vậy, bởi vì bồi dưỡng điều kiện khác biệt, lấy được tài nguyên có khoảng cách, ưu tú, càng thêm ưu tú, yếu càng yếu, hơn chênh lệch càng lúc càng lớn...”"),t("br"),t("br"),n._v("Trong lòng cảm khái."),t("br"),t("br"),n._v("Thế lực cường đại cùng gia tộc nắm giữ cấp bậc cao hơn danh sư cùng tài nguyên, bọn họ hậu bối vừa ra đời liền được tốt nhất bồi dưỡng, quả thực không phải gia đình bình thường có thể sánh ngang."),t("br"),t("br"),n._v("Giống hắn như vậy, có thể theo Thiên Huyền vương quốc cái loại này địa phương nhỏ từng bước một đi tới, đã là kỳ tích bên trong kỳ tích."),t("br"),t("br"),n._v("Cũng khó trách, những người khác căn bản không tin, vẫn cảm thấy là Trương gia vãn bối."),t("br"),t("br"),n._v("Thời gian không dài, năm mươi người tự giới thiệu, liền chuẩn bị kết thúc."),t("br"),t("br"),n._v("“Lớp chúng ta vị cuối cùng, Trương Huyền!”"),t("br"),t("br"),n._v("Tiếng nói kết thúc, đám người tất cả con mắt đồng loạt nhìn lại."),t("br"),t("br"),n._v("Vị này Trương Huyền, không phải Trương gia người, lại đem Trương Khiêm, Trương Vân Phong đánh một trận tơi bời, mọi người ở đây tất cả đều tận mắt nhìn thấy, khắc sâu ấn tượng."),t("br"),t("br"),n._v("“Ta tên Trương Huyền, đến từ Thanh Nguyên đế quốc!”"),t("br"),t("br"),n._v("Cũng không có gì có thể khoe khoang, nhẹ nhàng cười một tiếng, Trương Huyền tự giới thiệu."),t("br"),t("br"),n._v("“Quả nhiên là Thanh Nguyên đế quốc tới!”"),t("br"),t("br"),n._v("“Nhỏ như vậy địa phương tới, liền dám đối Trương gia người ra tay, người này lá gan thật là lớn!”"),t("br"),t("br"),n._v("“Bằng vào người thứ năm mươi thành tích, đem Trương Vân Phong đánh bại, các ngươi không cảm thấy kỳ quái sao?”"),t("br"),t("br"),n._v("“Ngược lại cũng đúng là, chỉ sợ là Danh Sư đường một vị nào đó cửu tinh danh sư lặng lẽ bồi dưỡng vãn bối đi...”"),t("br"),t("br"),n._v("Nghe được hắn giới thiệu, đám người tất cả đều thấp giọng."),t("br"),t("br"),n._v("Bằng vào tán tu chi thân liền đạt tới thực lực như thế, càng là liền đến từ Trương gia uy hiếp, mảy may đều không e ngại, tất cả mọi người cũng đều cho là hắn thân thế cũng không có giới thiệu đơn giản như vậy."),t("br"),t("br"),n._v("“Được rồi, chư vị giới thiệu xong, ta liền nói một chút Thánh tử điện một ít quy tắc, cùng lựa chọn lão sư một chút mấu chốt.”"),t("br"),t("br"),n._v("Phí sư cười cười, nói: “Thánh tử điện rất nhiều quy tắc, các vị theo thân phận lệnh bài bên trong, có thể thẩm tra, không cần ta quá nhiều vô dụng tự. Hiện tại muốn nói, là một cái trọng yếu nhất, cũng là mọi người nhất hẳn là chú ý!”"),t("br"),t("br"),n._v("“Cái kia chính là... Mọi người đều là Thánh tử điện học viên, có thể cạnh tranh với nhau, tiến hành giao đấu, nhưng không thể tự giết lẫn nhau! Nếu quả như thật có cái gì không thể điều hòa mâu thuẫn, nhất định phải phân ra sinh tử, có thể trước thời hạn xin tiến hành sinh tử đấu, chỉ cần có ba vị trưởng lão cấp bậc người đồng ý, đạt được cái gọi là sinh tử văn thư, liền có thể tiến hành!”"),t("br"),t("br"),n._v("“Có thứ này tại, công bằng quyết đấu bị giết, thế lực sau lưng, cũng không thể báo thù!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Thật ra thì cùng Hồng Viễn danh sư học viện quy tắc có chút tương tự."),t("br"),t("br"),n._v("Mặc kệ là danh sư, vẫn là những nghề nghiệp khác, có người, liền có tranh đấu, có tranh đấu, liền có giang hồ, người lợi hại hơn nữa, lại cao hơn tâm cảnh, cũng không thể ngoại lệ."),t("br"),t("br"),n._v("Năm đó, Khổng sư đã từng nổi giận, đã từng nghẹn ngào khóc rống, huống chi những người khác."),t("br"),t("br"),n._v("Có mâu thuẫn, liền muốn xử lý, không thể mặc kệ lên men, nếu không, sẽ chỉ vượt ảnh hưởng càng lớn."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mặc kệ là danh sư học viện, vẫn là Thánh tử điện, đều không có ngột ngạt nhân tính, mà là để bọn hắn tự do phát huy, thật có không thể giải quyết mâu thuẫn, vậy liền giải quyết, không cần thiết bởi vì quy tắc, mà trầm tích lên, cuối cùng ủ thành càng lớn tai hoạ."),t("br"),t("br"),n._v("“Còn lựa chọn lão sư... Ý kiến của ta rất đơn giản, lựa chọn ngươi am hiểu nhất!”"),t("br"),t("br"),n._v("Phí sư tiếp tục nói: “Các vị có thể thông qua khảo hạch, theo trên vạn người bên trong chọn lựa đi ra, tất nhiên có một hạng chỗ hơn người, bởi vậy, hi vọng các ngươi có thể đem cái này, tiếp tục phát dương quang đại, vượt qua những người khác...”"),t("br"),t("br"),n._v("Đón lấy, Phí sư nói rõ chi tiết một chút Thánh tử điện lợi hại danh sư, cùng đặc thù thiên phú người, chức nghiệp giả."),t("br"),t("br"),n._v("Nghe giới thiệu những này, Trương Huyền lắc đầu, hoàn toàn không có hứng thú."),t("br"),t("br"),n._v("Hắn chỉ cần có đầy đủ thư tịch, so cùng bất luận người nào học tập đều mạnh, không cần thiết đi quản ai năng lực mạnh."),t("br"),t("br"),n._v("“Nhạc Dao tiên tử, ngươi lần trước cho ta hàn băng linh dịch, không biết còn có hay không? Ta có thể hay không mua hay không một chút?”"),t("br"),t("br"),n._v("Tuyệt phẩm linh thạch loại hình đồ vật, muốn tìm được khá là phiền toái, nhưng trước mắt vị này, thế nhưng là đã có sẵn hàn băng linh dịch, thứ này trước đó sử dụng qua, hiệu quả rất tốt, hoàn toàn có thể nhanh chóng tăng cao tu vi."),t("br"),t("br"),n._v("“Cái này... Ta có là có, có điều, muốn giữ lại xung kích Lĩnh Vực cảnh...”"),t("br"),t("br"),n._v("Chần chờ một chút, Trần Nhạc Dao nói."),t("br"),t("br"),n._v("Đối phương là thiếu cung chủ lão sư, đối nàng lại có nửa sư tình nghĩa, dựa theo tình huống bình thường, hướng nàng hỏi thăm, không nên cự tuyệt, nhưng... Nàng còn muốn giữ lại, xung kích Lĩnh Vực cảnh, thực sự không tiện tặng người."),t("br"),t("br"),n._v("“Xung kích Lĩnh Vực cảnh?”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Trương Huyền ánh mắt sáng lên, nhìn lại: “Nếu như ta có thể giúp ngươi đột phá Lĩnh Vực cảnh, hơn nữa không cần hàn băng linh dịch, có thể hay không đem hắn bán ra cho ta?”"),t("br"),t("br"),n._v("“Nếu như có thể đột phá, đừng nói bán ra, đưa ngươi cũng được!”"),t("br"),t("br"),n._v("Trần Nhạc Dao nói."),t("br"),t("br"),n._v("P/S: Cầu các đại gia ném phiếu cho lão Thất ta a~"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1340"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1342"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);