(window.webpackJsonp=window.webpackJsonp||[]).push([[404],{760:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-126-ta-la-danh-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-126-ta-la-danh-su"}},[n._v("#")]),n._v(" Chương 126: Ta là danh sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Khặc khặc...” "),t("br"),t("br"),n._v(" Nghe nói như thế, ông chủ biến sắc mặt, vội vàng cười khan một tiếng: “Lão gia nói gì vậy, tòa phủ đệ này là cái năm tiến vào nhà, lại tốt như vậy đất đoạn, mười lăm vạn một tháng, thành thật không đắt...” "),t("br"),t("br"),n._v(" “Không đắt?” "),t("br"),t("br"),n._v(" Trương Huyền khóe miệng vung lên: “Tòa phủ đệ này chủ nhân cũ gọi Đỗ Kiều, là cái thương nhân không giả, bất quá hắn lại không chết, bởi vì gặp phải đạo phỉ, chuyện làm ăn đại gặp khó gãy, vốn là muốn đem chỗ này bán ra, nhưng trong thời gian ngắn không tìm được người mua, không thể làm gì khác hơn là thuê. Hắn cùng ngươi thỏa thuận thỏa thuận, là năm vạn một tháng, ngươi thu mười lăm phần trăm tiền thù lao... Hiện tại lại cùng ta nói mười lăm vạn, cái này khẩu vị chẳng lẽ không đại?” "),t("br"),t("br"),n._v(" “Lão gia, ngươi... Ngươi...” "),t("br"),t("br"),n._v(" Nghe được hắn, ông chủ liên tiếp lui về phía sau hai bước."),t("br"),t("br"),n._v(" Thật hay giả?"),t("br"),t("br"),n._v(" Cái phòng này tự mình xác thực là cùng Đỗ Kiều ký ước chừng, cùng nói với phương giống nhau, chỉ có năm vạn một tháng, chỉ là... Này thuộc về thương mại cơ mật, bọn họ ký kết thời điểm, vẫn chưa nói cho người ngoài, trước mắt cái tên này làm sao biết rõ ràng như thế?"),t("br"),t("br"),n._v(" “Làm sao? Muốn phủ nhận?” Trương Huyền nhàn nhạt nhìn sang: “Các ngươi là tại ba tháng trước Phỉ Thúy lâu giao dịch, tiếp khách còn có đầu bảng Thúy Linh cô nương, các ngươi uống chính là tám năm khoảng chừng Lăng Ba túy, thức ăn là hùng hoàng thỏ thịt, thời gian còn để Minh Tâm Nguyệt gảy ca khúc, nói chuyện thành chuyện làm ăn phía sau, vẫn vui vẻ một lần kêu ba cái cô nương... Những việc này có muốn hay không ta từng kiện mấy cho ngươi nghe?” "),t("br"),t("br"),n._v(" “A...” "),t("br"),t("br"),n._v(" Ông chủ như là quái đản giống nhau, tràn đầy sợ hãi."),t("br"),t("br"),n._v(" Nói với phương không kém chút nào, là tốt rồi giống như tận mắt nhìn thấy."),t("br"),t("br"),n._v(" Đây cũng quá khủng bố!"),t("br"),t("br"),n._v(" “Ngươi... Ngươi rốt cuộc là ai?” "),t("br"),t("br"),n._v(" Trong lòng sợ, ông chủ đầy mặt run rẩy."),t("br"),t("br"),n._v(" Đứng ở trước mặt đối phương, thật giống tất cả dối trá đều bị lột sạch giống nhau, để hắn không kìm lòng được cảm thấy chột dạ."),t("br"),t("br"),n._v(" “Ta chỉ là cái thuê phòng người, không muốn làm coi tiền như rác mà thôi!” "),t("br"),t("br"),n._v(" Trương Huyền vẻ mặt hờ hững."),t("br"),t("br"),n._v(" “Là ta lợi ích huân tâm, cái phòng này cùng lão gia nói giống nhau, năm vạn kim tệ một tháng!” Đối phương một hơi nói ra nhiều như vậy, ông chủ thật sự sợ, không dám ở phí lời, vội vàng nói."),t("br"),t("br"),n._v(" “Ừm!” Thấy đối phương thừa nhận, Trương Huyền lúc này mới thoả mãn gật gù, tiện tay lấy ra một tờ kim phiếu: “Đây là mười vạn kim tệ, ta trước định một tháng trước, còn lại cho ta chơi chút người hầu lại đây, còn có... Ngươi khoảng thời gian này liền lưu lại cho ta đương quản gia đi, biểu hiện tốt, đừng nói năm vạn, mười vạn, ngươi muốn bao nhiêu, lão gia ta liền có thể cho ngươi bao nhiêu, so ngươi mở cái thuê trung tâm kiếm lời hơn nhiều.” "),t("br"),t("br"),n._v(" Làm danh sư, lớn như vậy phủ đệ, đương nhiên không thể tự mình quét tước, cũng mà không thể bất cứ chuyện gì đều tự thân làm, người hầu, nha hoàn, đây là nhất định phải có."),t("br"),t("br"),n._v(" Cho tới vì sao phải để cái tên này đương quản gia, vậy thì càng đơn giản."),t("br"),t("br"),n._v(" Biết lừa gạt tiền a!"),t("br"),t("br"),n._v(" Dám vừa mở miệng liền gấp ba giá cả đi muốn, lá gan tuyệt đối rất lớn."),t("br"),t("br"),n._v(" Tự mình ngụy trang danh sư mục đích chính là vì chơi chút nhanh tiền, đang cần nhân tài như vậy."),t("br"),t("br"),n._v(" “Muốn bao nhiêu liền cho ta bao nhiêu?” "),t("br"),t("br"),n._v(" Ông chủ nhất thời hô hấp dồn dập lên."),t("br"),t("br"),n._v(" Hắn mở cái thuê trung tâm, quanh năm suốt tháng, cũng nhiều nhất liền kiếm lời 10, 20 ngàn kim tệ mà thôi, lại như tòa phủ đệ này, một hai năm không nhất định có người đến thuê, muốn lại hắc đối phương không lên đương cũng vô dụng."),t("br"),t("br"),n._v(" Chính vì như thế, thấy đối phương tiện tay lấy ra mười vạn, còn nói ra lời này, hưng phấn lập tức sắp thiêu đốt."),t("br"),t("br"),n._v(" Mẹ nó, sớm biết là đại khách hàng, không nghĩ tới so trâu chân còn lớn hơn."),t("br"),t("br"),n._v(" “Làm sao? Không muốn?” "),t("br"),t("br"),n._v(" “Không... Không, ta đồng ý.” Ông chủ vội vã tiếp nhận kim phiếu, động tác nhanh chóng, so vừa nãy tránh né công kích, còn phải nhanh."),t("br"),t("br"),n._v(" Chuyện tốt như vậy, không muốn mới là quái đản."),t("br"),n._v(" “Ừm! Ta chỉ ở Thiên Huyền thành đợi một thời gian ngắn, hầu hạ được rồi, ta để ngươi trong vòng một tháng kiếm lời mười năm thu vào, làm sao lấy hay bỏ... Chắc hẳn ngươi rất rõ ràng.” Thấy cái tên này thức thời, Trương Huyền thoả mãn gật gù."),t("br"),t("br"),n._v(" “Yên tâm đi, lão gia, ta nhất định cố gắng hầu hạ.” "),t("br"),t("br"),n._v(" Nghe được đối phương hứa hẹn, ông chủ gật đầu liên tục."),t("br"),t("br"),n._v(" Trương Huyền đáp một tiếng."),t("br"),t("br"),n._v(" Muốn cho người triệt để cống hiến cho, ân uy đều xem trọng, cây gậy lớn thêm mứt táo, tuyệt đối là con đường duy nhất, hắn vừa mở miệng liền vạch trần đối phương tính toán, để cho trong lòng kính sợ, sau đó lại quăng ra to lớn lợi ích, không tín phục mới là lạ."),t("br"),t("br"),n._v(" “Ngươi tên là gì?” "),t("br"),t("br"),n._v(" “Thuộc hạ Tôn Cường!” Ông chủ vội vàng trả lời: “Lão gia sau đó có thể gọi ta tiểu Cường!” "),t("br"),t("br"),n._v(" “Tiểu Cường?” Trương Huyền một mặt quái lạ."),t("br"),t("br"),n._v(" “Rõ!” Tôn Cường liền vội vàng gật đầu."),t("br"),t("br"),n._v(" “Ta hiện tại thì có sự tình muốn bàn giao.” "),t("br"),t("br"),n._v(" Cố nén xưng hô đối phương “Con gián” kích động, Trương Huyền khoát tay áo một cái: “Số một, tối hôm nay, liền đem trong sân nô bộc, hầu gái tìm đủ, ngày mai ta tới được thời điểm, muốn nhìn thấy người. Thứ hai, thông qua con đường của ngươi, đem ta ở tại cái nhà này tin tức, tuyên dương ra ngoài, liền nói... Đi ngang qua nơi đây danh sư ‘Dương Huyền’ trong thời gian ngắn biết ở nơi này, tin tức tản bộ càng rộng càng tốt.” "),t("br"),t("br"),n._v(" Nếu không thể dùng tên thật, đương nhiên muốn dùng cái giả, Dương Huyền là hắn kiếp trước lưới tên, thuận miệng hay dùng."),t("br"),t("br"),n._v(" “Danh sư?” "),t("br"),t("br"),n._v(" Sợ hết hồn, Tôn Cường nghi hoặc đến ngẩng đầu nhìn hướng trước mắt lão gia."),t("br"),t("br"),n._v(" “Ân, ta là một vị danh sư, dự định ở đây tìm một thứ, vì lẽ đó, ngươi muốn đem tin tức lan rộng ra ngoài, càng rộng càng tốt.” "),t("br"),t("br"),n._v(" Trương Huyền hừ một tiếng, tiếp tục nói: “Vừa nãy đưa cho ngươi mười vạn kim phiếu, chiêu thu người ở người hầu, khẳng định còn có thừa ra, dùng số tiền này đem chuyện này làm tốt, chỗ tốt không thể thiếu của ngươi, ta xem tu vi của ngươi, vây ở Chân Khí cảnh đỉnh phong chắc là có mười năm trở lên, khoảng thời gian này ta lưu lại tại Thiên Huyền Vương thành, chỉ điểm ngươi đột phá, cũng không tính là gì.” "),t("br"),t("br"),n._v(" “Đa tạ lão gia.” Tôn Cường vội vàng ngã quỵ ở mặt đất."),t("br"),t("br"),n._v(" Cùng nói với phương giống nhau, hắn vây ở Chân Khí cảnh đỉnh phong đã mười năm trở lên, đáng tiếc trước sau không thể pháp, không cách nào đột phá, nếu như thật có thể đạt được danh sư chỉ điểm, trở thành võ giả tứ trọng, ngay trong tầm tay."),t("br"),t("br"),n._v(" Lặng lẽ ngẩng đầu nhìn lại, chỉ thấy trước mắt mới vừa quen vị này lão gia, hai tay chắp ở sau lưng yên tĩnh đứng ở sắp rơi xuống ánh chiều tà bên trong, nhạt hào quang màu vàng bao phủ ở trên người, khác nào không dính dáng tới phàm tục Thần Tiên."),t("br"),t("br"),n._v(" Thấy cảnh này, trong nháy mắt để hắn từ nội tâm cảm thấy nồng đậm khâm phục."),t("br"),t("br"),n._v(" Đối với đối phương thân phận của danh sư tin tưởng không nghi ngờ."),t("br"),t("br"),n._v(" Không phải danh sư, làm sao có khả năng một miệng liền nói ra bản thân bên trong giao dịch? Không phải danh sư, như thế nào biết có tiền như vậy, rộng lượng như vậy..."),t("br"),t("br"),n._v(" Then chốt là đối phương khí chất cùng cử chỉ, một cách tự nhiên mang theo một loại cao cao tại thượng cảm giác, hiện tại có người muốn nói hắn không phải danh sư, hắn đều cảm thấy là đang nói láo."),t("br"),t("br"),n._v(" “Lão gia yên tâm, ta khẳng định đem chuyện này làm tốt.” "),t("br"),t("br"),n._v(" Đáp một tiếng, Tôn Cường toàn thân tràn ngập lòng hăng hái."),t("br"),t("br"),n._v(" Danh sư, đây chính là toàn bộ thế giới người cao quý nhất đám, coi như Thiên Huyền Vương quốc quốc vương trầm đuổi bệ hạ nhìn thấy, đều muốn cung kính có gia, lấy lễ đến tiếp đón, tự mình một cái bình thường tiểu nhân vật, lại có thể có thể hầu hạ người như thế, quả thực chính là có phúc ba đời."),t("br"),t("br"),n._v(" “Đi thôi!” "),t("br"),t("br"),n._v(" Thấy đối phương triệt để thuyết phục, Trương Huyền lúc này mới khoát tay áo một cái."),t("br"),t("br"),n._v(" “Rõ!” Tôn Cường tràn đầy hưng phấn đi ra ngoài."),t("br"),t("br"),n._v(" Thấy hắn rời đi, Trương Huyền lúc này mới thả xuống tư thái, chậm rãi xoay người, một tiếng cảm khái."),t("br"),t("br"),n._v(" “Không nghĩ tới trang bức cũng rất buồn tẻ...” "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-134"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-136"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);