(window.webpackJsonp=window.webpackJsonp||[]).push([[408],{763:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1344-truong-gia-bi-van-thuong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1344-truong-gia-bi-van-thuong"}},[n._v("#")]),n._v(" Chương 1344: Trương gia bí văn (thượng)")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1344: Trương gia bí văn (thượng)"),i("br"),i("br"),n._v("“Không sai, bọn họ vận dụng huyết mạch lực lượng về sau, sẽ đối với bảo vật có đặc thù cảm ứng, cho dù có trận pháp che giấu, cũng có thể phát giác!”"),i("br"),i("br"),n._v("Lạc Huyền Thanh gật đầu, ánh mắt lộ ra hâm mộ: “Nguyên nhân chính là như vậy, Vân gia tuy chỉ là nhị đẳng gia tộc, địa vị lại không thể so với Thủy gia, Mộc gia thấp!”"),i("br"),i("br"),n._v("Tu sĩ, muốn tiến bộ, bảo bối tuyệt đối không thể thiếu hụt, giữa thiên địa, ẩn chứa không biết bao nhiêu bảo tàng, không ít di tích, đều bị người dùng bàn tay lớn pháp che giấu. Xuyên thấu qua trận pháp, có chỗ phát giác, đơn phần này năng lực, đã làm cho vô số người hâm mộ."),i("br"),i("br"),n._v("“Đây là... Đặc thù chức nghiệp?”"),i("br"),i("br"),n._v("Trương Huyền tò mò."),i("br"),i("br"),n._v("“Tự nhiên không phải! Bọn họ là mượn nhờ huyết mạch lực lượng tìm kiếm, không cách nào phát triển!” Lạc Huyền Thanh cười nói."),i("br"),i("br"),n._v("“Ah!” Trương Huyền giật mình."),i("br"),i("br"),n._v("Năm đó Khổng sư xác định đặc thù chức nghiệp, có khả năng làm được hai điểm, mới có tư cách xác nhận, thứ nhất, có khả năng hình thành hệ thống, hoàn mỹ truyền thừa. Thứ hai có khả năng phạm vi lớn phát triển, người bình thường cũng có thể học tập."),i("br"),i("br"),n._v("Dưỡng dược người, phù hợp điều kiện thứ hai, người bình thường cũng có thể học tập, nhưng truyền thừa không đồng nhất, không cách nào hình thành hệ thống; Vân gia, nắm giữ hệ thống, nhưng mà dựa vào là huyết mạch, người bình thường không có cách nào học tập, cho nên, cũng không thể gọi là đặc thù chức nghiệp."),i("br"),i("br"),n._v("“Nhìn tới không có cách nào học tập...”"),i("br"),i("br"),n._v("Lắc đầu, Trương Huyền không chịu được có chút tiếc hận."),i("br"),i("br"),n._v("Hắn hiện tại thiếu thốn nhất liền là bảo vật, nếu như có thể có loại này bản lĩnh, bốn phía tầm bảo, tuyệt đối sẽ có cực lớn phát hiện... Bất quá, đã không phải đặc thù chức nghiệp, vậy liền không có cách nào học tập."),i("br"),i("br"),n._v("Thiên Đạo thư viện ngưng tụ thư tịch, là bởi vì người bình thường có khả năng học tập, huyết mạch chi lực... Là không học được."),i("br"),i("br"),n._v("“Đúng vậy a, nếu như là đặc thù chức nghiệp, khẳng định đông như trẩy hội, vô số người muốn học tập!”"),i("br"),i("br"),n._v("Lạc Huyền Thanh gật đầu."),i("br"),i("br"),n._v("Lại giải thích một hồi, Trương Huyền đôi đám người năng lực cũng biết một chút."),i("br"),i("br"),n._v("Vị này anh vợ, là Thánh vực thất trọng Nhập Hư cảnh cường giả đỉnh phong, còn lại ba người, Bích Hồng Âm thực lực cao nhất, đạt đến Nhập Hư cảnh hậu kỳ, người béo thoạt nhìn phòng ngự không sai, sức chiến đấu lại là rất yếu, khả năng liền Vân Liên Hải cũng chưa chắc đánh thắng được, chẳng qua cộng thêm thân thể lực lượng, liền xem như Lạc Huyền Thanh muốn thắng được, đều không có dễ dàng như vậy."),i("br"),i("br"),n._v("“Đã quyết định được rồi, vậy liền lên đường đi!”"),i("br"),i("br"),n._v("Giải thích xong, tất cả mọi người hiểu rõ chuyện gì xảy ra, lại không chần chừ, đồng loạt rơi vào thất thải thiên hạc trên lưng, một tiếng sắc nhọn kêu to, tiên hạc hóa thành bảy màu lưu quang, hướng chân trời bay đi."),i("br"),i("br"),n._v("  Tiên hạc tốc độ cực nhanh, trên lưng không có phòng hộ tính cơ cấu, Lạc Huyền Thanh đám người thực lực mạnh mẽ, không nhận cương phong ảnh hưởng, nhưng nhìn thấy Trương Huyền đứng ở phía trên cũng có thể vững như bàn thạch, tất cả đều âm thầm kinh hãi."),i("br"),i("br"),n._v("Cho dù tạm thời đột phá, đạt đến Xuất Khiếu cảnh đỉnh phong, thực lực chân chính cũng cùng tất cả mọi người vẫn là có rất lớn chênh lệch, có thể làm được điểm ấy, thiên phú và thực lực, đều cực kỳ bất phàm."),i("br"),i("br"),n._v("“Trương huynh đối với trận pháp lĩnh ngộ cao minh như thế, thực lực càng là không yếu, xông sơn môn khảo hạch, hẳn là thành tích không sai đi!”"),i("br"),i("br"),n._v("Người lùn Vân Liên Hải cười khanh khách nhìn qua."),i("br"),i("br"),n._v("Những người khác cũng đầy là tò mò."),i("br"),i("br"),n._v("Bọn họ loại thực lực này, sớm đã không chú ý tân sinh nhập môn, trước mắt vị này, ở trước sơn môn huyên náo động tĩnh mặc dù lớn, đối bọn hắn tới nói, cũng không hiểu rõ tình hình."),i("br"),i("br"),n._v("“Còn có thể!” Trương Huyền nhẹ gật đầu: “Xếp hạng thứ năm mươi, vừa vặn đi vào lớp tinh anh!”"),i("br"),i("br"),n._v("“Năm mươi?”"),i("br"),i("br"),n._v("Vân Liên Hải sững sờ: “Khóa này nhập môn đều có ai, lợi hại như vậy?”"),i("br"),i("br"),n._v("Trước mắt vị này, chỉ bằng vào tại trên trận pháp trình độ, dựa theo năm trước khảo hạch, tiến cái ba hạng đầu không có cái gì vấn đề, thế nào mới... Người thứ năm mươi?"),i("br"),i("br"),n._v("Cái hạng này, hơi không cẩn thận, liền có thể bị quét xuống."),i("br"),i("br"),n._v("“Bọn họ khóa này, quả thực có mấy cái thiên tư không kém!”"),i("br"),i("br"),n._v("Trương Huyền vẫn chưa trả lời, Lạc Huyền Thanh mở miệng: “Trương gia Trương Hủ, ngươi hẳn là nghe qua đi, liền là bọn họ khóa này!”"),i("br"),i("br"),n._v("“Trương Hủ? Cái kia Địa cấp huyết mạch? Người này, ta đã từng thấy qua một lần, quả thực rất lợi hại, thời gian đầy đủ, thành tựu thậm chí không kém lấy ngươi!” Vân Liên Hải nhịn không được khen ngợi."),i("br"),i("br"),n._v("“Hừ!”"),i("br"),i("br"),n._v("Hừ một tiếng, Lạc Huyền Thanh tựa hồ có chút không vui."),i("br"),i("br"),n._v("“Ngươi cũng đừng tức giận, ta biết ngươi đôi Trương gia người khó chịu, nhưng mà những năm này, Trương gia huyết mạch một cái so một cái tinh thuần, thiên tài lớp lớp, Lạc gia nếu như không có tiểu công chúa hoành không xuất thế, chỉ sợ thật phải bị vượt lên!”"),i("br"),i("br"),n._v("Vân Liên Hải nói."),i("br"),i("br"),n._v("Nói đến tiểu công chúa, trong mắt của hắn mang theo tôn sùng."),i("br"),i("br"),n._v("“Vân huynh, ngươi nói tiểu công chúa, thế nhưng là... Trong truyền thuyết, luyện hóa Tĩnh Không châu, thiên tài vô song vị kia?” Ống tay áo xuống nắm đấm xiết chặt, Trương Huyền giả bộ như điềm nhiên như không có việc gì mà hỏi."),i("br"),i("br"),n._v("“Không sai, chính là Huyền Thanh thân muội muội!”"),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, Vân Liên Hải không để ý Lạc Huyền Thanh tái mét sắc mặt, tràn đầy bát quái nhìn qua: “Đúng rồi, Huyền Thanh, nghe nói muội muội của ngươi, còn chưa ra đời liền gả cho Trương gia tên tiểu thiên tài kia, không biết vị kia thiên tài, có phải là thật hay không cùng trong truyền thuyết đồng dạng, thiên tư vô song?”"),i("br"),i("br"),n._v("“Im miệng!”"),i("br"),i("br"),n._v("Híp mắt lại, Lạc Huyền Thanh mang theo lửa giận: “Ta nói qua, đừng nhắc việc này, ai nhắc, ta liền giết ai!”"),i("br"),i("br"),n._v("“Nhắc hay không, đây đều là sự thật, gia tộc làm ra quyết định, ngươi không cải biến được!”"),i("br"),i("br"),n._v("Người béo Viên Hiểu mở miệng."),i("br"),i("br"),n._v("Thân là gần với tam đại gia tộc Viên gia hậu nhân, chuyện này biết đến rất kỹ càng, biết trước mắt vị này là cái sủng muội cuồng ma, không nguyện ý muội muội cùng người thông gia, trôi qua không hạnh phúc, nhưng cũng biết, thân là gia tộc hậu bối, không có bất kỳ biện pháp nào."),i("br"),n._v(" Làm ra quyết định này, nhất định là thông qua được trưởng lão chương trình nghị sự, loại tình huống này, liền xem như gia chủ, cũng không cách nào tuỳ tiện sửa đổi."),i("br"),i("br"),n._v("“Ta là không cải biến được, nhưng vãn bối luận bàn vẫn là có thể, nếu như tên kia, bị ta gặp lên, ta liền đánh hắn, chủ động từ hôn!”"),i("br"),i("br"),n._v("Mắt sáng lên, Lạc Huyền Thanh cắn răng."),i("br"),i("br"),n._v("“Đây là tội gì!” Vân Liên Hải cười khổ: “Vị kia tiểu thiên tài, thật muốn cùng trong truyền thuyết đồng dạng, huyết mạch tinh thuần cùng muội muội của ngươi đều có thể so sánh, ngươi cảm thấy có thể là đối thủ?”"),i("br"),i("br"),n._v("“Ta...”"),i("br"),i("br"),n._v("Lạc Huyền Thanh vẻ mặt đỏ lên."),i("br"),i("br"),n._v("Thực lực của hắn cứ việc rất mạnh, nhưng là cùng muội muội so... Kém không phải một chút điểm!"),i("br"),i("br"),n._v("Muội muội cứ việc so với hắn nhỏ hơn mười tuổi, nhưng vô luận tu vi hay là thực lực, đều không phải là hắn có thể so sánh."),i("br"),i("br"),n._v("Vị kia tiểu thiên tài, thật tới đồng dạng... Quả thực không phải là đối thủ."),i("br"),i("br"),n._v("“Đúng rồi, Trương gia tên tiểu thiên tài kia, ta ngược lại thật ra nghe qua một cái bí văn, các ngươi có muốn nghe hay không một cái?”"),i("br"),i("br"),n._v("Một mực không lên tiếng Bích Hồng Âm mở miệng."),i("br"),i("br"),n._v("“Bí văn?”"),i("br"),i("br"),n._v("Đám người cùng một chỗ nhìn qua, Lạc Huyền Thanh cũng vểnh tai."),i("br"),i("br"),n._v("“Hai mươi năm trước, lão sư ta, từng được mời đi Trương gia làm khách, một ngày ban đêm, đột nhiên bị Trương gia gia chủ mời đi qua cứu người!”"),i("br"),i("br"),n._v("Đôi mi thanh tú nhíu lên, Bích Hồng Âm giống như là đang nhớ lại trước đó lão sư cùng nàng nói."),i("br"),i("br"),n._v("“Trương gia gia chủ? Ngươi nói hẳn là vị kia  đi!” Thần thái ngưng trọng, Viên Hiểu nổi lòng tôn kính."),i("br"),i("br"),n._v("“Không sai, đúng là hắn!” Bích Hồng Âm gật đầu: “Lúc ấy, lão sư cảm thấy mình chỉ là ma âm sư, không am hiểu cứu người, vì sao muốn mời nàng ra tay, nhìn thấy bệnh nhân thời điểm, rốt cuộc hiểu rõ!”"),i("br"),i("br"),n._v("“Sinh bệnh chính là cái anh hài, không biết là trúng độc vẫn là chuyện gì xảy ra, linh hồn hết sức yếu ớt, bất cứ lúc nào cũng sẽ biến mất! Hưng Mộng Kiếm thánh, hi vọng nàng có thể thi triển ma âm, giúp một tay củng cố hồn phách, không đến mức tán loạn!”"),i("br"),i("br"),n._v("Đám người gật đầu."),i("br"),i("br"),n._v("Ma âm có khả năng hồn xiêu phách lạc, để cho người ta tinh thần sụp đổ, cũng có thể củng cố hồn phách, để cho người ta đột phá ràng buộc, tu vi đại tăng."),i("br"),i("br"),n._v("Anh hài, không có tu luyện qua, thân thể nhỏ yếu, lại thêm linh hồn khó mà khống chế, dùng dược vật, không quá phù hợp, loại tình huống này, ma âm sư quả thực so y sư càng thêm thích hợp."),i("br"),i("br"),n._v("“Lão sư biết cứu một mạng người hơn xây bảy tháp phù đồ, liền xuất thủ, có điều, hiệu quả không phải rất rõ ràng, dù vậy, Kiếm thánh vẫn là cho một số lớn phí tổn, cảm tạ lão sư trợ giúp, về sau, lão sư bị màn đêm buông xuống đưa tiễn, chuyện về sau, biết đến không nhiều...” Bích Hồng Âm nói tiếp."),i("br"),i("br"),n._v("“Ý của ngươi là nói... Năm đó ngươi lão sư cứu chữa đứa bé kia, có thể là... Trương gia vị kia tiểu thiên tài?”"),i("br"),i("br"),n._v("Hiểu được, Viên Hiểu nhịn không được nói."),i("br"),i("br"),n._v("“Cụ thể có phải hay không, lão sư cũng không biết, chỉ là đoạn thời gian có chút ăn khớp, hơn nữa lại là Hưng Mộng Kiếm thánh tự mình mời. Bất quá, dựa theo tình huống bình thường, Trương gia vị thiên tài này, tại mẫu thai bên trong, liền đo lường chảy máu mạch tinh thuần đến cực điểm, hẳn là bảo vệ mười phần chu toàn, không có người có khả năng làm bị thương mới là!”"),i("br"),i("br"),n._v("Bích Hồng Âm nói: “Lúc ấy lão sư không có suy nghĩ nhiều, về sau, nghe nói Trương gia vị này tiểu thiên tài một mực không có xuất hiện qua, mới có chút nghi ngờ!”"),i("br"),i("br"),n._v("Nếu quả như thật khỏe mạnh vô sự, như vậy một vị siêu cấp thiên tài, làm sao có thể giấu diếm hai mươi năm ai cũng chưa thấy qua?"),i("br"),i("br"),n._v("Thậm chí liền Thánh tử điện đều không đến?"),i("br"),i("br"),n._v("Nói không khiến người ta khó hiểu, đó là giả."),i("br"),i("br"),n._v("“Nếu thật là vị kia tiểu thiên tài lời nói, hai mươi năm trước, hẳn là vừa ra đời không bao lâu, liền bị người trọng thương... Liền linh hồn đều muốn biến mất, thương thế này quả thực rất nặng...” Viên Hiểu cau mày."),i("br"),i("br"),n._v("Bọn họ đều là gia tộc tử đệ, biết gia tộc đôi nắm giữ tinh thuần huyết mạch hậu bối, bảo vệ đến cỡ nào chu toàn, Trương gia mấy vạn năm mới xuất hiện như vậy một vị huyết mạch có thể so với lão tổ thiên tài, làm sao lại vừa ra đời liền bị độc thủ?"),i("br"),i("br"),n._v("“Có phải hay không là bên trong gia tộc tranh đấu?”"),i("br"),i("br"),n._v("Trương Huyền nhịn không được chen vào nói."),i("br"),i("br"),n._v("Nếu như đối phương nói là sự thật, bệnh nặng chính là Trương gia tiểu thiên tài cũng quá được rồi... Đều thành như vậy, tự nhiên cũng không có khả năng cưới Lạc Nhược Hi, hắn cũng liền không có bị động như vậy."),i("br"),i("br"),n._v("“Không có khả năng! Trương gia loại này Thánh Nhân quý tộc, cực kỳ chú trọng mặt mũi, tuyệt không có khả năng có nội chiến chuyện phát sinh!”"),i("br"),i("br"),n._v("Nói chuyện chính là Lạc Huyền Thanh."),i("br"),i("br"),n._v("Thân là Lạc gia thiên tài, biết Thánh Nhân quý tộc không nội dung màn."),i("br"),i("br"),n._v("“Không cho phép, không có nghĩa là không có khả năng có ah, ngộ nhỡ vị nào trưởng lão, ngấp nghé vị trí gia chủ, muốn cướp đoạt, cố ý đánh lén vị kia tiểu thiên tài đâu?” Trương Huyền não đại động mở."),i("br"),i("br"),n._v("Bất kể nói thế nào, hắn kiếp trước đều là thư viện nhân viên quản lý, nhìn qua không ít sách vở cùng chuyện xưa."),i("br"),i("br"),n._v("Rất nhiều trong tộc tranh đấu, đều là viết như vậy."),i("br"),i("br"),n._v("“Ngươi không phải gia tộc tử đệ, không hiểu được Thánh Nhân quý tộc quy củ... Ta có thể rõ ràng nói, ngươi mới vừa nói tình huống, là căn bản không có khả năng phát sinh!”"),i("br"),i("br"),n._v("Lạc Huyền Thanh còn chưa mở miệng, Viên Hiểu liền lắc đầu."),i("br"),i("br"),n._v("“Không có khả năng phát sinh?” Trương Huyền nháy con mắt."),i("br"),i("br"),n._v("Quyền lợi để cho người ta mất phương hướng, cửu tinh danh sư, cũng không thể ngoại lệ, huống chi một vài gia tộc trưởng lão, vì sao không có khả năng phát sinh?"),i("br"),i("br"),n._v("Đây coi như là rất kinh điển kiều đoạn được không!"),i("br"),i("br"),n._v("“Nói gia tộc, ngươi khả năng không biết rõ, vậy ta hỏi ngươi...”"),i("br"),i("br"),n._v("Thấy hắn một mặt ngây thơ, Viên Hiểu nhìn lại: “Thân là Thánh tử điện học viên, ngươi biết Thánh tử điện điện chủ ư?”"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1352"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1354"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);