(window.webpackJsonp=window.webpackJsonp||[]).push([[414],{769:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1350-ta-đi-ha-đoc-chet-han"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1350-ta-đi-ha-đoc-chet-han"}},[n._v("#")]),n._v(" Chương 1350: Ta đi hạ độc chết hắn")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1350: Ta đi hạ độc chết hắn"),t("br"),t("br"),n._v("“Trương Thuần? Hắn sao lại tới đây?” Viên Hiểu biến sắc, Bích Hồng Âm mấy người cũng tất cả đều nhíu mày, hiển nhiên cái tên này, tất cả đều biết được."),t("br"),t("br"),n._v("“Hắn là...” Gãi gãi đầu, Trương Huyền trong mắt mang theo hỏi thăm."),t("br"),t("br"),n._v("Trương gia hạch tâm con cháu, hiện nay chỉ nghe nói qua “Tiểu thiên tài” cùng Trương Hủ, cái này Trương Thuần, còn chưa từng nghe người đề cập qua."),t("br"),t("br"),n._v("“Trương Thuần cùng Lạc huynh là cùng giới đi vào Thánh tử điện thiên tài, lẫn nhau tương đối cạnh tranh với nhau, quan hệ...” Nhìn một cái cách đó không xa Lạc Huyền Thanh, Viên Hiểu hạ giọng: “Không thể nào hòa thuận!”"),t("br"),t("br"),n._v("“Ngươi cũng không cần cho bọn hắn giấu diếm!”"),t("br"),t("br"),n._v("Nghe nói như thế, Bích Hồng Âm cười nhạo: “Nào chỉ là không hòa thuận, quả thực liền là có thù! Bất kể làm chuyện gì, đều muốn so sánh, nhất định để một phương nhận thua không thể! Ngươi chạy tới hủy một tòa điện, hắn liền chạy đi qua hủy hai tòa... Những năm này, hai người kia gây ra động tĩnh còn nhỏ ư?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thanh âm của nàng không có cố ý giấu diếm, cách đó không xa Lạc Huyền Thanh nghe rõ ràng, mặt mo đỏ ửng, muốn phản bác lại nói không ra."),t("br"),t("br"),n._v("“Hủy điện? Lạc huynh... Phá hư vương tên tuổi, là cố ý?” Trương Huyền ngẩn người."),t("br"),t("br"),n._v("“Dĩ nhiên không phải, ta... Chỉ là quá muốn thắng, không có khống chế lại lực lượng thôi...” Lạc Huyền Thanh hừ một tiếng."),t("br"),t("br"),n._v("“Quá muốn thắng?”"),t("br"),t("br"),n._v("“Ừm, tỷ như vượt quan, nếu như Trương Thuần dùng mười phút đồng hồ, hắn coi như liều mạng, cũng muốn đạt tới chín phút... Dùng hết toàn lực, tự nhiên không khống chế nổi...” Bích Hồng Âm giải thích."),t("br"),t("br"),n._v("Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Như vậy liều mạng, lực lượng quả thực không dễ khống chế, không may xuất hiện, cũng liền không thể tránh được."),t("br"),t("br"),n._v("Hiện tại xem ra, mặc dù hắn cũng tương tự làm qua phá hư, nhưng chân chính trên ý nghĩa, vẫn rất có đúng mực, chưa bao giờ vì phá hư mà phá hư, nhiều khi đều là cử chỉ vô tâm, nội tâm cũng không muốn!"),t("br"),t("br"),n._v("Như vậy so ra, lập tức phân cao thấp, đối phương bố cục vẫn là nhỏ không ít."),t("br"),t("br"),n._v("“Đã bọn họ cái gì đều lẫn nhau so sánh, vị này Trương Thuần hiện tại tới, có thể hay không nghe được Động Hư hồ lô tin tức, muốn tới đây thò một chân vào?”"),t("br"),t("br"),n._v("Đột nhiên nhớ tới cái gì, Trương Huyền cau mày."),t("br"),t("br"),n._v("Có thể cùng Lạc Huyền Thanh cạnh tranh, tu vi nhất định không kém nhiều, thật muốn tới làm loạn, dự đoán được Động Hư hồ lô bảo vật như vậy, liền không tưởng tượng đơn giản như vậy."),t("br"),t("br"),n._v("“Động Hư hồ lô sự tình, chỉ có chúng ta mấy người biết được, yên tâm đi, hắn tuyệt không hiểu rõ tình hình!” Lạc Huyền Thanh bình tĩnh mà nói."),t("br"),t("br"),n._v("“Ồ?”"),t("br"),t("br"),n._v("Thấy hắn như thế giọng nói, đám người không giải."),t("br"),t("br"),n._v("Nếu như không phải là vì Động Hư hồ lô, tại sao phải chạy đến nơi đây? Thánh tử điện cùng nơi này chênh lệch ít nhất trăm vạn cây số, luôn không khả năng xa như vậy, chỉ vì ngắm cảnh du lịch đi!"),t("br"),t("br"),n._v("“Hắn mang theo thuần thú đường nội viện học sinh, Triệu Huân, Triệu Nghị hai huynh đệ, am hiểu cơ quan bẫy rập thiên công sư Lý Ngân, cùng y sư Thường Ngọc Văn! Nếu như không nhìn lầm, là vì thuần thú!”"),t("br"),t("br"),n._v("Không có trả lời, Lạc Huyền Thanh nói."),t("br"),t("br"),n._v("“Thuần thú?”"),t("br"),t("br"),n._v("Vân Liên Hải nhớ tới cái gì, bừng tỉnh hiểu ra: “Như ngươi vậy nói chuyện, ta đến nghĩ tới, đoạn thời gian trước, Trương Thuần ban bố cái thuần thú nhiệm vụ, nói muốn mời người thuần phục một đầu ẩn chứa Long tộc huyết mạch Thánh thú, thù lao là ba cái Ngọc Mạch linh tủy... Sẽ không phải, người này muốn thuần phục, chính là đầu này Địa Ngục Thanh Long thú a?”"),t("br"),t("br"),n._v("“Ta cũng nhớ tới tới...”"),t("br"),t("br"),n._v("Nghe được hắn nói như vậy, đám người từng cái sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("Ngay cả Trương Huyền cũng hơi hơi kinh ngạc."),t("br"),t("br"),n._v("Trước mấy ngày, để Tôn Cường ra ngoài hỏi thăm tin tức, trở về thời điểm, nói có cái thuần thú nhiệm vụ, thù lao phong phú, lúc đầu ý định đi đón, bởi vì muốn đi lớp tinh anh báo cáo, liền quên... Không nghĩ tới thế mà liền là vị này Trương Thuần ban bố."),t("br"),t("br"),n._v("Mấu chốt nhất là... Ở đây đụng nhau."),t("br"),t("br"),n._v("“Hắn muốn thuần phục, chính là đầu này!”"),t("br"),t("br"),n._v("Thấy mọi người kịp phản ứng, Lạc Huyền Thanh ánh mắt chợt lóe."),t("br"),t("br"),n._v("“Đây cũng quá đúng dịp đi...”"),t("br"),t("br"),n._v("“Đúng vậy a, chúng ta muốn đối phó đầu này đại gia hỏa, hắn cũng muốn tới thuần phục... Lạc huynh, trong này không có nguyên nhân của ngươi a?”"),t("br"),t("br"),n._v("Đám người tất cả đều đồng loạt nhìn qua."),t("br"),t("br"),n._v("Thiên hạ Thánh thú nhiều như vậy, cả hai đụng phải một cái khả năng rất nhỏ, mọi người không phải tin tưởng, cùng trước mắt vị này, không có quan hệ."),t("br"),t("br"),n._v("“Đương nhiên là có quan hệ!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh khóe miệng nâng lên: “Hai ta từ trước tới nay cạnh tranh, hắn đối ta hành tung biết đến rõ rõ ràng ràng, lần trước tới đây, cứ việc rất bí ẩn, còn là bị phát hiện, vì phòng ngừa tiết lộ Động Hư hồ lô bí mật, ta liền bốn phía mua sắm thuần phục Thánh thú đồ vật, để cho người ta chế tạo nhiếp hồn kim châu... Mục đích đúng là để hắn hiểu lầm, cảm thấy ta ý định thuần phục Thánh thú... Không nghĩ tới người này, thật bị lừa rồi!”"),t("br"),t("br"),n._v("Đám người hai mặt nhìn nhau."),t("br"),t("br"),n._v("Còn có thể như vậy?"),t("br"),t("br"),n._v("Bất quá, cũng có thể suy nghĩ ra."),t("br"),t("br"),n._v("Hai người từ trước tới nay cạnh tranh, vị này Trương Thuần, biết được Lạc Huyền Thanh ý định thuần phục Thánh vực bát trọng Thánh thú, khẳng định cũng sẽ nhẫn nhịn khẩu khí, tranh thủ tại đối phương phía trước thành công... Tốt đối hắn tiến hành đả kích!"),t("br"),t("br"),n._v("“Thông minh!”"),t("br"),t("br"),n._v("Trương Huyền cảm khái."),t("br"),t("br"),n._v("Lạc Huyền Thanh làm như vậy, đã dời đi sức chú ý của đối phương, không đến mức truy xét mục đích của hắn, lại để cho giúp một tay diệt trừ nguy hiểm, nhất cử lưỡng tiện!"),t("br"),t("br"),n._v("Mặc kệ vị này Trương Thuần có thể hay không thuần phục Địa Ngục Thanh Long thú, khẳng định đều sẽ thu hút sự chú ý của đối phương, đám người lại nghĩ đi vào cái gọi là di tích, liền trở nên càng thêm đơn giản."),t("br"),t("br"),n._v("Không hổ là Thánh Nhân Lạc gia thiên tài, bản thân anh vợ, mưu kế chơi thuần thục... Thoạt nhìn cử chỉ thô cuồng, trên thực tế lại tâm tư tỉ mỉ."),t("br"),n._v(" “Hắn muốn thuần phục Thanh Long thú, vậy chúng ta là không phải có thể lặng lẽ nấp đi, chờ Thánh thú cùng hắn kiềm chế lẫn nhau về sau, lại đi ra?”"),t("br"),t("br"),n._v("Hiểu rõ chuyện gì xảy ra, Vân Liên Hải cũng không nhịn được khen ngợi một tiếng, nói."),t("br"),t("br"),n._v("Mới vừa còn sầu lấy, như thế nào giải quyết đầu này đại gia hỏa, đã vị này Trương Thuần xuất hiện, làm chim đầu đàn, bọn họ đúng lúc vui nhàn hạ."),t("br"),t("br"),n._v("“Làm như vậy cũng không phải không được, chỉ là ta cùng Trương Thuần đấu đã nhiều năm như vậy, lẫn nhau hiểu rõ, người này sở dĩ nhanh như vậy tới, hẳn là lần nữa nắm giữ tung tích của ta... Một khi phát hiện ta giấu đi, nhất định có chỗ nghi ngờ, đến lúc đó, Động Hư hồ lô bí mật, sẽ rất khó ẩn giấu đi...”"),t("br"),t("br"),n._v("Lạc Huyền Thanh lắc đầu."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?” Vân Liên Hải nói."),t("br"),t("br"),n._v("“Rất đơn giản!” Lạc Huyền Thanh nhẹ nhàng cười một tiếng, ánh mắt lóe lên: “Bọn họ không phải muốn thuần phục Địa Ngục Thanh Long thú ư? Chúng ta nghĩ biện pháp ngăn cản, hơn nữa đem Thanh Long thú dẫn đi qua, như vậy, đầu này đại gia hỏa, liền sẽ bị đối phương kiềm chế, chúng ta liền có thể thừa cơ tìm cơ hội...”"),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Đám người đồng thời gật đầu."),t("br"),t("br"),n._v("Đã diễn trò, đương nhiên muốn làm nguyên bộ."),t("br"),t("br"),n._v("Cũng biết ngươi đã đến, ngươi không ra tay, chẳng phải thật xin lỗi khán giả?"),t("br"),t("br"),n._v("Cho nên, nhất định phải nghĩ phương pháp, làm cho đối phương thoạt nhìn, vô cùng tức giận, nhưng lại không thể làm gì!"),t("br"),t("br"),n._v("Như vậy, mới có thể để cho đối phương lòng hư vinh thỏa mãn, vô cùng vui vẻ giúp mình đánh quái, thừa cơ nhặt thành quả thắng lợi."),t("br"),t("br"),n._v("“Lạc huynh, nhưng có biện pháp?”"),t("br"),t("br"),n._v("Thấy hắn như thế tin tưởng, hiển nhiên đã có kế hoạch của mình, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Đương nhiên!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh nhẹ gật đầu: “Phương pháp rất đơn giản, phái người đi ngăn cản bọn họ, sau đó lại tìm người thu hút Thanh Long thú, giả bộ như muốn thuần phục bộ dạng, Trương Thuần một khi phát hiện ta vượt lên trước một bước, tất nhiên cuống cuồng, như vậy cũng rất dễ dàng lộ ra sơ hở, có thể thừa dịp... Còn lại cũng liền đơn giản!”"),t("br"),t("br"),n._v("Trương Huyền đám người gật đầu."),t("br"),t("br"),n._v("Đối phương biết hắn là tới thuần phục Thanh Long thú, kết quả tới chỗ làm thinh, tùy ý hắn đi qua, ngu nữa cũng sẽ cảm thấy có bẫy, còn không bằng tới cái đóng kịch tranh đoạt, trên thực tế lại là cố ý khích nộ Thánh thú, làm cho đối phương tiếp bàn."),t("br"),t("br"),n._v("Dù sao thực lực của đối phương cùng thủ đoạn, Thanh Long thú nhiều nhất để hắn bị thương, chém giết còn làm không được, chịu đau khổ cũng không phải là phạm tội giết người, cũng không vi phạm danh sư đạo nghĩa."),t("br"),t("br"),n._v("“Đã mọi người cảm thấy không có vấn đề, vậy ta hiện tại liền an bài một chút!”"),t("br"),t("br"),n._v("Thấy mọi người rõ ràng hắn ý tứ, Lạc Huyền Thanh nhìn về phía Trương Huyền cùng Viên Hiểu, nói: “Hai người các ngươi, đi phía trước thu hút Thanh Long thú, Trương sư phụ trách bày trận, Viên Hiểu thân thể mạnh mẽ, thủ đoạn bảo mệnh nhiều, phụ trách vào sơn động thu hút! Bọn họ không xuất hiện, chúng ta có thể đợi đến tối, mượn nhờ nhiếp hồn kim châu lực lượng, để hắn mê hoặc, mà bây giờ, không cần cố kỵ nhiều như vậy, chỉ cần chọc giận đối phương, nghĩ biện pháp để Trương Thuần bọn họ tiếp nhận là được!”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nói phức tạp, trên thực tế rất đơn giản, hắn cùng Viên Hiểu liền là phụ trách dụ quái, quái đưa ra tới về sau, giao cho Trương Thuần đám người là được..."),t("br"),t("br"),n._v("Dù sao đối phương nghĩa vụ làm phiền, không cần cố kỵ."),t("br"),t("br"),n._v("“Tốt, hồng âm, liền hải, chúng ta đi nghênh đón một cái Trương Thuần, vô luận như thế nào đều muốn ngăn cản bọn họ tiếp tục đi tới!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh nói xong, thân thể nhảy lên, hướng Trương Thuần đám người nghênh đón tiếp lấy."),t("br"),t("br"),n._v("“Chúng ta cũng nhanh lên một chút đi!”"),t("br"),t("br"),n._v("Thấy bọn họ rời khỏi, Viên Hiểu hô một tiếng, vòng qua nham thạch hướng hang núi phương hướng lặng lẽ đi tới."),t("br"),t("br"),n._v("“Trương sư, một lát nữa, ngươi trước bày trận, không cần mạnh cỡ nào, chỉ cần có thể thời gian ngắn vây khốn Thanh Long thú là được, ta vào sơn động đem hắn hấp dẫn ra đến, nghĩ biện pháp đưa đến trong trận pháp! Chỉ cần hoàn thành những này, coi như thành công!”"),t("br"),t("br"),n._v("Một bên tiến lên, Viên Hiểu một bên truyền âm khai báo."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Làm như vậy, quả thực đơn giản nhất, dù sao mục đích của bọn hắn không phải đem đại gia hỏa này thuần phục, cũng không phải chém giết, mà là chọc giận, xem như súng."),t("br"),t("br"),n._v("“Vây khốn Thanh Long thú về sau, chúng ta đi qua giúp một tay, Lạc Huyền Thanh mặc dù rất mạnh, nhưng mà Trương Thuần cũng không yếu, hai người tranh đấu, coi như tại nội viện, đều là rất làm cho người chú mục!” Hai mắt sáng lên, Viên Hiểu tràn đầy hưng phấn."),t("br"),t("br"),n._v("Võ giả, đều thích xem cao thủ ở giữa tỷ thí, như vậy mới có thể tăng lên bản thân, học được nhiều thứ hơn."),t("br"),t("br"),n._v("Lạc Huyền Thanh, Trương Thuần, cho dù tại nội viện, cũng đều là đỉnh phong cấp bậc nhân vật, tranh đấu lên, đối với hắn mà nói, nắm giữ lực hấp dẫn thật lớn."),t("br"),t("br"),n._v("“Cũng tốt!” Trương Huyền lên tiếng."),t("br"),t("br"),n._v("Thật ra thì đối với song phương chiến đấu, hắn không có hứng thú quá lớn, thực lực còn không có đạt tới, coi như nhìn, cũng có chút xem không hiểu, hoàn toàn không hiểu xuất sắc ở nơi nào."),t("br"),t("br"),n._v("Huống chi, hắn xem xét, tất nhiên muốn dẫn động thư viện, đến lúc đó, nhìn thấy rất nhiều thiếu hụt, mười phần khó chịu, tại chỗ nôn cũng không tốt lắm đâu!"),t("br"),t("br"),n._v("“Thế nào? Không hứng thú?” Nhìn ra ý nghĩ của hắn, Viên Hiểu cười một tiếng: “Ngươi đừng tưởng rằng bọn họ chỉ là luận bàn, hai người giao thủ, thật đúng là sát sinh giết chết, chiêu chiêu hung hiểm!”"),t("br"),t("br"),n._v("“Sát sinh giết chết?” Trương Huyền kỳ lạ: “Đều là con em đại gia tộc, có thể có cái gì cừu hận?”"),t("br"),t("br"),n._v("Chỉ là lẫn nhau so sánh, còn không đến mức như thế đi."),t("br"),t("br"),n._v("“Ngươi còn không biết? Cũng đúng, ngươi vừa mới đến, không biết cũng rất bình thường!”"),t("br"),t("br"),n._v("Viên Hiểu sững sờ, ngay sau đó nở nụ cười, nhìn quanh hai bên một vòng, thấp giọng: “Nghe nói... Trương Thuần muốn theo đuổi Lạc gia tiểu công chúa... Ai, ngươi làm gì?”"),t("br"),t("br"),n._v("Còn chưa nói xong, chỉ thấy theo sát ở phía sau thanh niên, xoay người rời đi."),t("br"),t("br"),n._v("“Ta đi hạ độc chết cái này Trương Thuần!”"),t("br"),t("br"),n._v("Trương Huyền xua tay."),t("br"),t("br"),n._v("“...” Viên Hiểu."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1358"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1360"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);