(window.webpackJsonp=window.webpackJsonp||[]).push([[42],{397:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1014-hoa-nguyen-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1014-hoa-nguyen-thanh"}},[n._v("#")]),n._v(" Chương 1014: Hỏa Nguyên thành")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Lần này tìm kiếm di tích, không phải chuyện của một cá nhân, dính dáng quá lớn, thậm chí quan hệ đến toàn bộ nhân tộc, bởi vậy Thanh Nguyên Danh Sư đường mười phần chú trọng, không chỉ phái ra Ngô sư, còn phái ra một vị thất tinh đỉnh phong trận pháp sư, trợ giúp mọi người phá giải phong ấn, tìm kiếm chính xác con đường."),t("br"),t("br"),n._v("Hắn vị sư thúc này mặc dù tuổi trẻ, nhưng cả gan làm loạn, cái gì cũng dám làm, vẫn là trước thời hạn khai báo một câu cho thỏa đáng, miễn cho đến lúc đó xuất hiện phiền toái không cần thiết."),t("br"),t("br"),n._v("“Yên tâm đi!” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Thấy hắn một mặt tự tin, Mộc sư không biết nên trả lời như thế nào, nhìn về phía cách đó không xa Lạc Nhược Hi, lại thấy nàng mỉm cười, tựa hồ đối với cái tên này cam đoan, cũng không quá tin tưởng."),t("br"),t("br"),n._v("Khó trách."),t("br"),t("br"),n._v("Lúc trước dẫn hắn đi Phong Thánh đài, muốn hắn nhiều chút tôn trọng, kết quả vẫn là đem người ta toàn bộ vách đá đều làm cho đổ sụp, nếu không phải Khổng sư lên tiếng, chỉ sợ sớm đã bị người đánh chết tươi."),t("br"),t("br"),n._v("Cái tên này, có đôi khi thiên phú kinh người, thông minh tuyệt đỉnh, có đôi khi, lại có vẻ đần độn có chút đáng yêu."),t("br"),t("br"),n._v("Ba người tùy tiện nói chuyện phiếm, Trương Huyền lại hỏi thăm một chút liên quan tới di tích chuyện, biết được khả năng này cùng Khổng sư liên quan đến, trong lòng tràn đầy kích động."),t("br"),t("br"),n._v("Lần trước Phong Thánh đài, đã chính miệng hỏi qua Khổng sư, biết đối phương trong cơ thể cũng nắm giữ Tiên Thiên thai độc, đã cùng hắn liên quan đến, có lẽ liền có thể tìm tới dấu vết để lại."),t("br"),t("br"),n._v("Một đường không nói chuyện, phi hành Thánh thú tốc độ cực nhanh, sau năm ngày, liền thấy Hỏa Nguyên thành xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Hỏa Nguyên thành vốn là cực nóng, hiện tại lại là mùa hạ, nếu như không phải tu luyện giả, đoán chừng khó mà chịu đựng...”"),t("br"),t("br"),n._v("Nhìn cách đó không xa, tại ánh mặt trời chiếu xuống, hơi hơi ửng hồng thành thị, Mộc sư nói."),t("br"),t("br"),n._v("“Quả thực đủ nóng!”"),t("br"),t("br"),n._v("Cảm thụ thoáng cái, nhiệt độ chung quanh, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trước mấy ngày mới vừa đi Tĩnh Viễn thành, cả ngày tuyết rơi, lạnh lẽo như băng, mà ở trong đó nhưng giống như là bếp lò, nếu như không phải đích thân tới, khẳng định sẽ hoài nghi độ chênh lệch nhiệt độ trong ngày như thế lớn hai cái địa phương, làm sao có thể cùng thuộc một cái đế quốc."),t("br"),t("br"),n._v("“Mặc dù nơi này khoảng cách Phong Thánh đài không xa, nhiệt độ nhưng có rất lớn chênh lệch!”"),t("br"),t("br"),n._v("Lần trước hắn cùng Lạc Nhược Hi tới Phong Thánh đài, cách nơi này không tính là quá xa, cũng liền mấy trăm cây số bộ dạng, khoảng cách gần như thế, nơi đó khí hậu, liền rất bình thường, hoàn toàn không có bên này thiêu đốt “Phong Thánh đài, thiên địa phong Thánh, có Thánh Nhân che chở, tự nhiên mưa thuận gió hoà, mà ở trong đó, có mấy tòa cự đại núi lửa hoạt động, cực nóng không thể tránh được!”"),t("br"),t("br"),n._v("Mộc sư giải thích."),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Hồng Viễn đế quốc bản đồ trong đầu hắn, liền có một phần, biết tình huống nơi này."),t("br"),t("br"),n._v("Hỏa Nguyên thành, mệnh danh “Hỏa nguyên”, hỏa đầu nguồn... Không cần nghĩ, cũng có thể biết nhiệt độ làm người ta khó mà tiếp nhận."),t("br"),t("br"),n._v("“Đã nơi này nóng như vậy, vì cái gì còn muốn thành lập thành thị, nhiều người như vậy đợi ở chỗ này?” Lạc Nhược Hi nhịn không được hỏi."),t("br"),t("br"),n._v("Nhiều như vậy núi lửa hoạt động bất cứ lúc nào cũng sẽ bộc phát, lại nóng bức không tưởng nổi, vì cái gì còn có người đợi không rời đi?"),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Mộc sư vẫn chưa trả lời, Trương Huyền cười cười: “Nơi này mặc dù nóng bức, lại hết sức thích hợp sinh trưởng một chút ưa thích làm khô dược liệu, tỷ như, lá khô cỏ! Loại thuốc này vật, có thể không cần lượng nước mà sinh trưởng, quá trình trưởng thành bên trong, lá cây từ đầu đến cuối tựa như chết héo mà gọi tên! Một gốc lá khô cỏ, một khi bán ra, có thể bán đi một viên linh thạch thượng phẩm giá cả.”"),t("br"),t("br"),n._v("“Đắt như thế?” Lạc Nhược Hi sững sờ."),t("br"),t("br"),n._v("Đối với người bình thường tới nói, một viên linh thạch thượng phẩm, có thể đủ ăn uống cả một đời, đều dùng không hết."),t("br"),t("br"),n._v("Một gốc dược liệu liền giá cả cỡ này, khó trách có thể để cho vô số người đổ xô tới, cam tâm đợi ở loại địa phương này."),t("br"),t("br"),n._v("“Không chỉ có như vậy, nơi này còn có chút đặc thù Thánh thú, linh thú, mỗi một loại đều mười phần hiếm thấy, bán ra lời nói có giá trị không nhỏ... Hơn nữa, còn là cái sản xuất tài nguyên khoáng sản bảo địa!” Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("Đem danh sư học viện tất cả thư tịch nhìn xong, Hồng Viễn đế quốc tình huống, biết đến so rất nhiều sinh trưởng ở địa phương danh sư, đều muốn nhiều."),t("br"),t("br"),n._v("“Thì ra là thế...” Lạc Nhược Hi hiểu được."),t("br"),t("br"),n._v("Có thể khiến người ta ở lại chỗ này, thậm chí thành lập to lớn như vậy thành thị, tất nhiên có hắn nguyên nhân."),t("br"),t("br"),n._v("“Ha ha, sư thúc quả nhiên học rộng biết nhiều!”"),t("br"),t("br"),n._v("Nghe được hắn giới thiệu, Mộc sư khen ngợi nhìn một cái."),t("br"),t("br"),n._v("Bản thân vị sư thúc này, mặc dù làm việc có chút không đáng tin cậy, nhưng trong đầu tri thức lượng, lại là thực sự, bất kể nhân văn địa lý vẫn là đặc thù chức nghiệp, đều tin tay nhặt ra, để cho người ta không thể không phục."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Hồng Viễn học viện cái này truyền thừa vạn năm cực lớn danh sư đất tập trung, đều lựa chọn hắn tại cầm lái."),t("br"),t("br"),n._v("“Tùy tiện nói một chút, không tính là gì!” Trương Huyền cười cười."),t("br"),t("br"),n._v("“Bây giờ cách phong ấn mở ra còn có một số thời gian, ta nhìn không bằng chúng ta tới trước Danh Sư đường cùng mọi người tập hợp!”"),t("br"),t("br"),n._v("Ba người đang khi nói chuyện, Thánh thú đã đi tới thành thị bầu trời, chậm rãi rơi xuống, chỉ chốc lát đi tới một cái nguy nga cao ngất điện đường trước mặt."),t("br"),t("br"),n._v("Danh Sư đường!"),t("br"),t("br"),n._v("t r uy e n c u a t u i . v n"),t("br"),n._v("Tòa thành thị này so Tĩnh Viễn thành còn bao la hơn nhiều lắm, nhân khẩu cũng càng thêm dày đặc, nắm giữ Danh Sư đường phân bộ, có điều, thực lực mạnh nhất đường chủ, cũng chỉ là Thánh vực nhất trọng trung kỳ, cùng Hồng Viễn thành chênh lệch rất xa."),t("br"),t("br"),n._v("“Gặp qua Mộc sư!”"),t("br"),t("br"),n._v("Vừa đi vào, một người trung niên liền tiến lên đón."),t("br"),t("br"),n._v("“Ừm, Hàn Tự trận pháp sư cùng Ngô sư bọn người ở tại đây? Dẫn chúng ta qua đi!” Mộc sư cười nói."),t("br"),t("br"),n._v("“Ngô sư bọn họ đã tại hai ngày trước... Đi Nhạn Lâm sơn!”"),t("br"),t("br"),n._v("Trung niên nhân vội vàng trả lời."),t("br"),t("br"),n._v("“Đi Nhạn Lâm sơn?” Mộc sư sững sờ: “Phong ấn không phải còn có mấy ngày mới có thể mở ra ư? Sớm như vậy đi qua làm gì?”"),t("br"),t("br"),n._v("“Ta không biết, chỉ là để cho ta đóng giữ ở đây, tiếp đãi cái khác chạy tới danh sư, để hắn cần phải mau chóng đi qua!” Trung niên nhân vội nói."),t("br"),n._v("“Biết!”"),t("br"),t("br"),n._v("Gật gật đầu, Mộc sư không hỏi thêm nữa, nhìn lại: “Ngô sư cùng Hàn Tự trận pháp sư như là đã đi qua, chỉ sợ phong ấn xuất hiện biến cố, so ta trước đó biết đến càng thêm nghiêm trọng, chúng ta cũng qua xem một chút đi!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền hai người gật đầu, lần nữa ngồi lên phi hành Thánh thú sống lưng."),t("br"),t("br"),n._v("“Phong ấn vị trí, không tại nội thành, mà là tại cách nơi này hơn ba trăm cây số Nhạn Lâm sơn mạch chỗ sâu, nơi đó có một tòa cự đại núi lửa hoạt động, mười phần cực nóng, từ trước tới nay ít ai lui tới...”"),t("br"),t("br"),n._v("Đứng tại lưng thú trong phòng, Mộc sư kỹ càng đem phong ấn tình huống nói tỉ mỉ một lần."),t("br"),t("br"),n._v("Phong ấn thành lập chỗ, cùng Độc điện vị trí hòn đảo đồng dạng, là một tòa núi lửa hoạt động, cực lớn trận pháp, chính là từ núi lửa bên trong thả ra lực lượng duy trì vận chuyển, không hiểu trận pháp, muốn ngạnh xông lời nói, không những không cách nào đi vào, còn có thể dẫn tới sơn mạch sụp đổ, để di tích hủy hoại chỉ trong chốc lát."),t("br"),t("br"),n._v("Bởi vậy, phong ấn không mở ra phía trước, liền xem như Danh Sư đường vô số cao thủ, cũng không dám dùng sức mạnh, chỉ có thể ở nơi này làm chờ lấy."),t("br"),t("br"),n._v("300 cây số khoảng cách, không tính là quá xa, không đến mười phút đồng hồ, liền thấy một cái cao lớn ngọn núi xuất hiện ở trước mắt, chừng cao mấy ngàn thước, nguy nga khí thế, chỉ là phía trên trụi lủi, một cái cây cối đều không có, hầu như tất cả đều là màu đỏ rực tảng đá, còn chưa tới đến trước mặt, liền cho người ta một loại thiêu đốt làn da cảm giác."),t("br"),t("br"),n._v("“Đây là Thanh Hỏa đan! Ăn vào, có thể triệt tiêu hỏa độc ảnh hưởng, không chịu đến tổn thương!”"),t("br"),t("br"),n._v("Cổ tay lật qua lật lại, Mộc sư lấy ra hai cái đan dược đưa tới."),t("br"),t("br"),n._v("Mặc dù thực lực của bọn hắn, loại trình độ này nhiệt lượng không tính là gì, nhưng đợi đến thời gian lâu dài, vẫn như cũ sẽ có hỏa độc xâm nhập trong cơ thể, lưu lại chứng bệnh."),t("br"),t("br"),n._v("Vì phòng ngừa loại tình huống này, nửa tháng trước, luyện đan sư học viện tân nhiệm Chu viện trưởng, để rất nhiều học sinh luyện chế ra đầy đủ Thanh Hỏa đan."),t("br"),t("br"),n._v("Loại đan dược này tứ tinh cấp bậc luyện đan sư liền có thể luyện chế, không hề phức tạp, lợi dụng một chút thanh nhiệt giải độc dược liệu cô đọng mà thành, bù trừ lẫn nhau trừ hỏa độc có không tệ công hiệu."),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Đối với Trương Huyền tới nói, trong cơ thể nắm giữ Thiên Đạo chân khí, liền kịch độc còn không sợ, tự nhiên không sợ những này hỏa độc, có điều, đối phương có hảo ý, không nỡ cự tuyệt, tiện tay nối qua, há miệng nuốt xuống."),t("br"),t("br"),n._v("Đan dược vào cổ họng, lập tức cảm thấy một cỗ mát mẻ chi ý tràn vào toàn thân, trước đó cảm thấy cực nóng, cũng dường như không tồn tại nữa."),t("br"),t("br"),n._v("“Đây không phải... Bình thường Thanh Hỏa đan?” Ánh mắt sáng lên."),t("br"),t("br"),n._v("Mặc dù ở bề ngoài không có khác nhau, nhưng cái này đan dược tính chất, rõ ràng phải so bình thường cường đại hơn rất nhiều."),t("br"),t("br"),n._v("“Đúng ta thu thập rất nhiều dược liệu, bản thân luyện chế mà thành...” Mộc sư cười cười."),t("br"),t("br"),n._v("“Khó trách!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn là một vị thất tinh danh sư, phụ tu chức nghiệp bên trong có luyện đan sư, từ hắn ra tay luyện chế cấp bốn đan dược, công hiệu tự nhiên không tầm thường."),t("br"),t("br"),n._v("Thánh thú phi hành, thời gian không dài, đi tới Nhạn Lâm sơn đỉnh núi, nhìn xuống dưới, chỉ thấy một cái cực lớn hình tròn cái hố trước mặt, vô số học sinh, khoanh chân ngồi ở chung quanh, không ngừng hướng cái nào đó chỗ truyền vào chân khí, trên đầu mồ hôi chảy ròng."),t("br"),t("br"),n._v("“Bọn họ tại củng cố phong ấn...”"),t("br"),t("br"),n._v("Mộc sư sầm mặt lại."),t("br"),t("br"),n._v("Thoạt nhìn, phong ấn quả nhiên so với bọn hắn tưởng tượng còn nguy hiểm hơn, nếu không, cũng không có khả năng nhiều người như vậy, cùng một chỗ động thủ."),t("br"),t("br"),n._v("“Hẳn là trận pháp học viện viện trưởng, mang tới cái kia một vạn học sinh...”"),t("br"),t("br"),n._v("Nhìn một cái, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đây đều là danh sư học viện học sinh, người mặc đặc thù quần áo, một cái liền có thể nhìn ra."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Chính cảm thấy những học sinh này có chút không kiên trì nổi, chỉ thấy lại có một đám học sinh, đi tới trước mặt, từng người bàn tay bay lượn, từng cây vừa mới luyện chế thành công trận kỳ bị đặt ở cố định vị trí bên trên."),t("br"),t("br"),n._v("Những vị trí này, mặc dù không thể thay đổi phong ấn kết cấu, nhưng có thể để cho trở nên vững chắc."),t("br"),t("br"),n._v("Chính là luyện khí sư học viện rất nhiều học sinh."),t("br"),t("br"),n._v("Hai học viện lớn học sinh phối hợp liên tục, động tác lại nhanh lại trôi chảy, xem ra những ngày này không ít làm như thế."),t("br"),t("br"),n._v("Thấy nhiều như vậy học sinh, cố gắng vững chắc, phía dưới hố tròn đều có loại bất cứ lúc nào cũng sẽ bộc phát cảm giác, Trương Huyền nhíu nhíu mày, trong mắt hoa văn lóe lên, trực tiếp nhìn xuống."),t("br"),t("br"),n._v("“Thật rườm rà trận pháp...”"),t("br"),t("br"),n._v("Nhìn một cái, lông mày nhảy một cái."),t("br"),t("br"),n._v("Đối với trận pháp lý giải, không kém gì Thất Tinh trận pháp sư, thế nhưng là nhìn xem phương cấm chế, vẫn như cũ có chút mê muội, khó mà hiểu."),t("br"),t("br"),n._v("“Lại có chút không giống...”"),t("br"),t("br"),n._v("Càng xem càng nghi ngờ."),t("br"),t("br"),n._v("Thoạt nhìn giống như trận pháp, lại có chút không quá giống."),t("br"),t("br"),n._v("Mặc dù cái này phong ấn có chút không ổn định, nhưng không có kích hoạt, bởi vậy, không cách nào dùng Thiên Đạo thư viện phán xét, chỉ dựa vào Minh Lý Chi Nhãn lời nói, lấy hắn hiện tại kiến thức, muốn hiểu rõ, không có mấy tháng thời gian, không cách nào làm được."),t("br"),t("br"),n._v("Thật sự là trận pháp lời nói, không cần nghĩ, tuyệt đối vượt qua cấp bảy!"),t("br"),t("br"),n._v("Một cái vượt qua cấp bảy đại trận... Khó trách Thanh Nguyên đế quốc Danh Sư đường nhiều như vậy danh sư cũng không dám hành động thiếu suy nghĩ."),t("br"),t("br"),n._v("Đã vượt qua bọn họ có thể xử lý phạm trù."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Hắn bên này quan sát, một bên Mộc sư, khống chế Thánh thú chậm rãi bay xuống."),t("br"),t("br"),n._v("Rơi trên mặt đất, còn chưa tới đến hố tròn trước mặt, chỉ thấy Ngô sư tới đón: “Trương sư, Lạc sư, Mộc sư, các ngươi đã tới!”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1022"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1024"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);