(window.webpackJsonp=window.webpackJsonp||[]).push([[425],{780:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1360-day-leo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1360-day-leo"}},[n._v("#")]),n._v(" Chương 1360: Dây leo")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1360: Dây leo"),t("br"),t("br"),n._v("Khó trách Trương Huyền buồn phiền."),t("br"),t("br"),n._v("Trên đường đi đều tại anh vợ trước mặt biểu hiện rất tốt, thậm chí nhẹ nhõm thuần phục Thanh Long thú, như vậy hoàn mỹ, không có kẽ hở... Nằm mơ đều không có nghĩ đến, thời khắc sống còn, bị một cái hồ lô đánh!"),t("br"),t("br"),n._v("Đánh không lại Thánh thú, vũ khí thì cũng thôi đi, một cái treo ở dây leo lên hồ lô, đem hắn đánh đông nam tây bắc cũng không tìm tới... Càng nghĩ càng thấy đến mất mặt."),t("br"),t("br"),n._v("“Bị hồ lô đánh?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Lạc Huyền Thanh hiểu được, hô hấp kìm lòng không được gấp rút: “Nói như vậy... Trương sư nhìn thấy Động Hư hồ lô? Thứ này chân thực tồn tại, không phải hình ảnh?”"),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trước đó Vân Liên Hải không cảm ứng được bảo vật tồn tại, đám người còn tưởng rằng cũng là hư tượng, nghe được cái này, tất cả đều thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Chân thực tồn tại, liền không uổng công."),t("br"),t("br"),n._v("“Động Hư hồ lô, có khả năng giúp người lĩnh ngộ Động Hư cảnh, sức chiến đấu sợ là không kém gì Thánh vực bát trọng sơ kỳ cường giả, Trương sư cứ việc thiên phú vô song, lại cũng chỉ có Thánh vực ngũ trọng Xuất Khiếu cảnh đỉnh phong, chênh lệch khá xa, không cách nào chiến thắng, mười phần bình thường, liền xem như ta, chỉ sợ cũng không phải là đối thủ...”"),t("br"),t("br"),n._v("Thấy hắn một mặt chán nản, Lạc Huyền Thanh an ủi."),t("br"),t("br"),n._v("Có thể khiến người ta đột phá đến Thánh vực bát trọng, cái này Động Hư hồ lô, làm sao có thể đơn giản?"),t("br"),t("br"),n._v("Thực lực không bằng, đừng nói bắt lấy, coi như bắt được, cũng không cách nào luyện hóa!"),t("br"),t("br"),n._v("Nghe hắn nói như vậy, Trương Huyền lúc này mới cảm thấy thoải mái không ít, ngẩng đầu nhìn về phía đám người: “Ta đã phá giải trước mắt không gian, một lát nữa, mang mọi người đi tìm cái hồ lô này, chỉ là, người này mười phần nóng nảy, nhìn thấy người khả năng liền sẽ ra tay, cho nên, các ngươi muốn trước thời hạn khôi phục thương thế, điều chỉnh trạng thái tốt nhất!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Đám người đồng thời gật đầu, mỗi người khoanh chân ngồi xuống."),t("br"),t("br"),n._v("Trương Huyền cũng không chậm trễ, toàn thân huyệt đạo mở ra, nhanh chóng hấp thu thiên địa linh khí, khôi phục vừa rồi Nhất Kiếm Phá Hải hao tổn chân khí, chữa trị thương thế bên trong cơ thể."),t("br"),t("br"),n._v("Không gian gấp phía trong, linh khí nồng đậm, hao tốn không đến hai canh giờ, liền triệt để khôi phục."),t("br"),t("br"),n._v("“Cái này hồ lô thực lực, quả thực mạnh...”"),t("br"),t("br"),n._v("Một bên củng cố tu vi, một bên suy tư đối sách."),t("br"),t("br"),n._v(" Suy nghĩ kỹ mấy cái phương pháp, nhưng cũng không là đối thủ."),t("br"),t("br"),n._v("Cùng Lạc Huyền Thanh nói đồng dạng, người này chỉ sợ nắm giữ Động Hư cảnh sơ kỳ sức chiến đấu, căn bản không phải hắn hiện tại có thể chống lại."),t("br"),t("br"),n._v("Trừ phi... Phóng ra Ngoan Nhân, hơn nữa không riêng gì xương ngón tay, còn có đầu lâu, đi ra để chiến đấu!"),t("br"),t("br"),n._v("Nhưng làm như vậy, Ngoan Nhân thân phận liền tiết lộ, thân là danh sư lại nuôi dưỡng Dị Linh tộc nhân... Truyền đi, làm không cẩn thận Danh Sư đường tổng bộ đều sẽ tới điều tra, thậm chí thủ tiêu hắn danh sư tư chất."),t("br"),t("br"),n._v("“Lên đường đi!”"),t("br"),t("br"),n._v("Nghĩ một lát, đang không có gì tốt phương pháp, chỉ thấy Lạc Huyền Thanh đám người đứng dậy."),t("br"),t("br"),n._v("Thương thế của bọn hắn mặc dù rất nặng, nhưng nuốt vào Trương Huyền cấp cho linh dược chữa thương, lại thêm thân là thế lực lớn hậu bối, bảo vật rất nhiều, giờ phút này đã gần như hoàn toàn khôi phục."),t("br"),t("br"),n._v("“Đi theo ta đằng sau, một bước cũng không thể đi nhầm!” Trương Huyền dặn dò."),t("br"),t("br"),n._v("Đám người biết nguy hiểm, tất cả đều nhẹ gật đầu."),t("br"),t("br"),n._v("Không nói thêm lời, phong ấn tu vi, Trương Huyền đi vào trước."),t("br"),t("br"),n._v("Lĩnh ngộ Diễn Không thiên thư đệ tam trọng, đối không gian nắm giữ có thể trở nên vô cùng cường đại, lại thêm lĩnh ngộ cái không gian này trận pháp bí mật, lần nữa tiến vào bên trong, không có gặp được bất kỳ nguy hiểm nào."),t("br"),t("br"),n._v("Mặc dù mượn nhờ không gian chiến đấu, khả năng không bằng Lạc Huyền Thanh, nhưng ở nơi này, người sau kém xa tít tắp."),t("br"),t("br"),n._v("Có lần đầu tiên tiến vào kinh nghiệm, lần thứ hai liền đơn giản nhiều, thời gian không dài, lần nữa đi tới linh mạch sông dài trước mặt, một cái hồ lô treo ở cách đó không xa."),t("br"),t("br"),n._v("“Là Động Hư hồ lô...”"),t("br"),t("br"),n._v("Lạc Huyền Thanh kích động run rẩy."),t("br"),t("br"),n._v("Nhìn từ đằng xa không rõ ràng lắm, đi tới trước mặt, biết cái này đích xác là Động Hư hồ lô, luyện hóa liền có thể có rất lớn tỷ lệ đột phá Động Hư cảnh Thiên Địa bảo vật."),t("br"),t("br"),n._v("“Ta ở chỗ này chờ, các ngươi động thủ đi!”"),t("br"),t("br"),n._v("Trương Huyền xua tay."),t("br"),t("br"),n._v("Dù sao không dùng ra toàn lực lời nói, hắn loại này bản lĩnh, tại mọi người trước mặt cũng không đáng chú ý, không phải nhúng tay đều không quan trọng."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh đám người từng cái ý chí chiến đấu sục sôi: “Chúng ta mấy người ra tay, nhất định có thể đem chế ngự!”"),t("br"),t("br"),n._v("Dựng thẳng lên nắm đấm, Trương Huyền mỉm cười, cấp cho cổ vũ: “Cố lên!”"),t("br"),t("br"),n._v("Ba phút."),t("br"),t("br"),n._v("Lần nữa trở lại trận pháp bên ngoài, Lạc Huyền Thanh đám người từng cái mặt mũi bầm dập ngồi dưới đất, mặt mũi chán nản."),t("br"),t("br"),n._v("Tứ đại cao thủ liên hợp, thực lực mạnh mẽ, chiêu chiêu tinh diệu, nhưng... Đồng dạng không có đánh qua cái kia hồ lô, bị cuồng đánh một trận, nhất là Viên Hiểu, trên thân mấy cái hồ lô ấn, thê thảm nhất."),t("br"),t("br"),n._v("“Nếu không... Trương sư ngươi đi đem Thanh Long thú mang tới đi, nó ra tay, tuyệt đối có thể tuỳ tiện nghiền ép người này!”"),t("br"),t("br"),n._v("Chần chờ hồi lâu, Vân Liên Hải nói."),t("br"),t("br"),n._v("Địa Ngục Thanh Long thú nắm giữ có thể so với Thánh vực bát trọng đỉnh phong thực lực, bốn người bọn họ liên thủ đồng dạng không phải là đối thủ, nó xuất thủ, hẳn là mười phần đơn giản."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền có chút khó khăn."),t("br"),t("br"),n._v("Không phải hắn không muốn làm như vậy, mà là nếu như gặp phải siêu cấp cao thủ, đánh không lại, gọi thú sủng ngược lại cũng thôi, đánh không lại một cái hồ lô..."),t("br"),t("br"),n._v("Về sau ở trước mặt đối phương, còn có cái gì uy tín?"),t("br"),t("br"),n._v("Lại nói, nơi này Thánh vực bát trọng, căn bản là không có cách đi vào, đem hắn cất vào không gian gấp, cũng là có thể làm được, chỉ khi nào thả ra, đồng dạng sẽ dẫn tới không gian không ổn định, phiền phức lớn hơn."),t("br"),t("br"),n._v("“Như vậy đi, mọi người thay ta thủ hộ, ta vận dụng huyết mạch chi lực...”"),t("br"),t("br"),n._v("Đang tràn đầy rầu rĩ, một bên Lạc Huyền Thanh mắt sáng lên, cắn răng nói."),t("br"),t("br"),n._v("“Vận dụng huyết mạch chi lực?”"),t("br"),t("br"),n._v("Bích Hồng Âm nhíu nhíu mày: “Ngươi phải suy nghĩ kỹ, một khi vận dụng, ít nhất một tháng lại không có thể sử dụng lực lượng...”"),t("br"),t("br"),n._v("Huyết mạch lực lượng cứ việc có thể khiến người ta trong thời gian ngắn bạo phát càng cường đại hơn sức chiến đấu, có thể một khi sử dụng, đối thân thể tiêu hao rất nhiều, liền xem như gia tộc tử đệ, sử dụng trước đó, cũng sẽ do dự mãi."),t("br"),n._v(" “Ta biết, chỉ cần có thể đem hắn bắt lấy, một tháng sau, thực lực của ta khôi phục, liền có thể nghĩ biện pháp luyện hóa, xung kích Thánh vực bát trọng... Hao tổn lại lớn, cũng đáng giá!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh nói."),t("br"),t("br"),n._v("Bằng vào thực lực của hắn bây giờ cùng tích lũy, dưới tình huống bình thường, muốn đột phá Thánh vực bát trọng, không có thời gian mấy năm, thậm chí mấy chục năm, đều khó mà làm được."),t("br"),t("br"),n._v("Mà có thứ này, trong vòng ba tháng liền có thể thành công, coi như hao tổn lực lượng, tu dưỡng một tháng, cũng tuyệt đối xem như kiếm lời!"),t("br"),t("br"),n._v("“Đã như vậy, vậy liền chuẩn bị đi!”"),t("br"),t("br"),n._v("Bích Hồng Âm gật đầu."),t("br"),t("br"),n._v("Dự đoán được bảo vật, không có khả năng không có hao tổn, thiên hạ không có cơm trưa miễn phí, trước khi đến, mọi người đã có đủ loại chuẩn bị tâm tư."),t("br"),t("br"),n._v("Có quyết định, không tại nhiều nói, mỗi người mau chóng khôi phục thực lực."),t("br"),t("br"),n._v("Sau ba canh giờ, lần nữa đi tới hồ lô trước mặt."),t("br"),t("br"),n._v("“Hồng Âm dùng ma âm tiến hành quấy nhiễu, Viên Hiểu, ngươi nghĩ biện pháp chặn lại hồ lô chủ yếu tiến công, Vân Liên Hải cùng ta chính diện ra tay, tranh thủ lần này nhất định thành công!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Đám người lên tiếng."),t("br"),t("br"),n._v("“Bắt đầu!”"),t("br"),t("br"),n._v("Một tiếng hô khẽ, Lạc Huyền Thanh trong cơ thể kịch liệt nổ vang, khí tức trong chốc lát sôi trào lên, ngay sau đó liền thấy chung quanh thân thể hắn không gian, xuất hiện vặn vẹo."),t("br"),t("br"),n._v("Tiến về phía trước một bước, bàn tay chộp tới, hồ lô không gian chung quanh, giống như là lập tức đóng băng lên."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Cảm nhận được địch ý, hồ lô tràn đầy tức giận, lần nữa xông ra ngoài đến, có điều, bùng cháy huyết mạch Lạc Huyền Thanh, thực lực trở nên vô cùng cường đại, liên tục vọt ra mấy lần, thế mà đều không có rời khỏi giam cầm không gian."),t("br"),t("br"),n._v("Leng keng! Leng keng!"),t("br"),t("br"),n._v("Ngay sau đó tiếng đàn vang lên, Bích Hồng Âm bắt đầu đàn tấu, Viên Hiểu, Vân Liên Hải vừa vặn xông lên."),t("br"),t("br"),n._v("“Lần này hẳn là có khả năng bắt lấy...”"),t("br"),t("br"),n._v("Thấy vận dụng huyết mạch Lạc Huyền Thanh, sức chiến đấu chợt tăng không chỉ một lần, đối không gian nắm giữ cũng càng thêm mạnh mẽ, không thể so với Động Hư cảnh cường giả yếu, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Không hổ là Thánh Nhân Lạc gia thiên tài, vận dụng huyết mạch quả thực cùng biến thành người khác."),t("br"),t("br"),n._v("Trước đó, cùng Trương Khiêm thời điểm chiến đấu, gặp qua đối phương sử dụng Trương gia huyết mạch, cứ việc lực lượng cũng tăng lên, nhưng còn xa không bằng trước mắt."),t("br"),t("br"),n._v("Nhìn tới huyết mạch càng tinh khiết hơn, kích hoạt về sau, sinh ra hiệu quả, cũng liền càng lớn."),t("br"),t("br"),n._v("Cảm khái một hồi, ngẩng đầu nhìn lại, chỉ thấy tứ đại cao thủ, đang đem hồ lô đè xuống đất dùng chân loạn đạp, Bích Hồng Âm càng đem đàn ngọc giơ lên, dùng hết toàn lực đập loạn."),t("br"),t("br"),n._v("Đám người hợp lực tiến công phía dưới, trước đó còn hung hăng vô cùng hồ lô, giờ phút này không ngừng run rẩy, không nói ra được thê thảm."),t("br"),t("br"),n._v("“Để ngươi hung hăng...”"),t("br"),t("br"),n._v("Nhớ tới trước đó bị cuồng ẩu thảm trạng, Trương Huyền thoả mãn nhẹ gật đầu, không nói ra được thoải mái."),t("br"),t("br"),n._v("Ngươi không phải rất chảnh ư?"),t("br"),t("br"),n._v("Lần này không dám đắc ý đi!"),t("br"),t("br"),n._v("Biết bên này chiến đấu, lại không lo lắng, không cần hắn ra tay, đám người cũng có thể đem người này bắt lấy, Trương Huyền không tiếp tục để ý, mà là ngẩng đầu hướng trên không linh khí dòng sông nhìn sang."),t("br"),t("br"),n._v("Cùng trước đó nói đồng dạng, đây là một đầu linh mạch."),t("br"),t("br"),n._v("Bất quá, bị người từ dưới đất rút ra, tẩm bổ cái hồ lô này, lại thêm duy trì rất nhiều không gian vận chuyển, đã tiêu hao không sai biệt lắm."),t("br"),t("br"),n._v("“Đáng tiếc...”"),t("br"),t("br"),n._v("Than thở một tiếng."),t("br"),t("br"),n._v("Nếu là đầu này linh mạch, mới vừa bị rút lấy, liền bị phát hiện lời nói, mặc dù không tính quá mạnh, nhưng tuyệt đối có thể bù đắp được mấy chục viên thậm chí hơn trăm viên tuyệt phẩm linh thạch."),t("br"),t("br"),n._v("Mà bây giờ cứ việc linh khí cũng rất dư dả, độ tinh thuần cũng đã không đủ, cùng linh thạch thượng phẩm không kém bao nhiêu, đối với hắn đã dùng ra không lớn."),t("br"),t("br"),n._v("“Ừm, căn này dây leo cũng là đồ tốt...” Lắc đầu, đang muốn đem ánh mắt thu hồi, đột nhiên vẻ mặt khẽ động, con mắt rơi vào linh mạch bên trong cái kia dây leo bên trên."),t("br"),t("br"),n._v("Chỉ có ngón tay gần như độ lớn, lại linh khí lập loè, tựa như ngọc chất, trước đó không có nhìn kỹ, lại thêm núp ở linh mạch bên trong, không khiến người ta chú ý, giờ phút này Minh Lý Chi Nhãn chiếu tới, phát hiện thứ này không chỉ cứng rắn vô cùng, linh khí trong đó càng là mười phần dồi dào, so tinh nguyên linh thạch thượng phẩm đều nồng nặc, không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, cũng lại kìm nén không được, Trương Huyền bay thẳng tới."),t("br"),t("br"),n._v("Rất mau tới đến trước mặt, dây leo toàn thân lộ ra màu xanh lục, tựa như phỉ thúy, chạm tay băng lãnh, trong đó dường như có vô số không gian gấp, như là tổ ong đồng dạng lít nha lít nhít."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Con mắt nháy mấy cái, Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Vừa rồi tại hồ lô phía trên rõ ràng cảm nhận được loại lực lượng này, như là không gian lan tràn, trên thực tế thì là Động Hư chi lực."),t("br"),t("br"),n._v("Chính là loại lực lượng này, có thể khiến người ta đột phá Thánh vực tầng tám cảnh giới."),t("br"),t("br"),n._v("Vốn cho rằng chỉ có hồ lô phía trên mới có, không nghĩ tới phía trên này thế mà cũng tồn tại."),t("br"),t("br"),n._v("“Nhìn một chút có thể hay không luyện hóa...”"),t("br"),t("br"),n._v("Mắt sáng rực lên, bàn tay vội vàng hướng lên sờ tới, nhất thời một cỗ mát mẻ chi ý truyền vào trong cơ thể, một quyển sách xuất hiện tại trong óc."),t("br"),t("br"),n._v("Cười nhạt một tiếng, duỗi ra ngón tay, hướng lên phía trên nhấn tới."),t("br"),t("br"),n._v("Tạch tạch! Tạch tạch!"),t("br"),t("br"),n._v("Liên tiếp giòn vang, thật dài dây leo, như là gặp chủ nhân, phát ra liên tiếp hưng phấn giòn vang, trực tiếp bay tới."),t("br"),t("br"),n._v("“Thu!”"),t("br"),t("br"),n._v("Không chút do dự, Trương Huyền bàn tay lớn vồ một cái."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Dây leo thời gian nháy mắt liền bị bắt vào trữ vật giới chỉ, biến mất không thấy gì nữa."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1368"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1370"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);