(window.webpackJsonp=window.webpackJsonp||[]).push([[427],{782:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1361-nguoi-ha-đoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1361-nguoi-ha-đoc"}},[n._v("#")]),n._v(" Chương 1361: Ngươi hạ độc?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1361: Ngươi hạ độc?"),t("br"),t("br"),n._v("Dây leo bị lấy đi, trên không linh mạch dòng sông, cũng giống mất đi chống đỡ, “Ầm!” Một tiếng nổ tung, cuồng bạo linh khí bao phủ, thổi đến toàn bộ không gian gấp xuất hiện vô số gợn sóng."),t("br"),t("br"),n._v("Cảm nhận được sóng linh khí, đám người sửng sốt một chút, có điều, vẫn chưa để ý, tiếp tục cuồng ẩu."),t("br"),t("br"),n._v("Thình thịch thình thịch bành!"),t("br"),t("br"),n._v("Bốn đại cao thủ, đủ loại thủ đoạn không ngừng thi triển, trường kiếm, đại đao, trường thương, chuỳ sắt... Rất nhiều vũ khí nhao nhao rơi xuống, thời gian không dài, hồ lô liền bị đánh thoi thóp, bất cứ lúc nào cũng sẽ không chịu nổi."),t("br"),t("br"),n._v("“Tiếp tục đánh xuống, liền biến cái gáo, nghỉ một lát đi!”"),t("br"),t("br"),n._v("Trở lại cách đó không xa, Trương Huyền lông mày nhảy một cái, khuyên can nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Lạc Huyền Thanh khoát tay chặn lại, đám người ngừng lại, cùng thời khắc đó, lăng không một trảo, trước mắt không gian vặn vẹo, hình thành bọt khí bộ dáng vòng tròn, đem hắn bao phủ ở bên trong."),t("br"),t("br"),n._v("“Được rồi...”"),t("br"),t("br"),n._v("Khóa lại đối phương, Lạc Huyền Thanh lập tức giống như là rút sạch thân thể tất cả lực lượng, trở nên khuôn mặt trắng bệch, vô cùng suy yếu."),t("br"),t("br"),n._v("Kích hoạt huyết mạch, có nhất định có tác dụng trong thời gian hạn định tính, lúc trước Trương Khiêm, chỉ vận dụng ba chiêu, liền nhắc lại không nổi nắm đấm, hắn cùng hồ lô đối chiến lâu như vậy, liên tục thi triển đâu chỉ trăm chiêu, đã rất đáng sợ!"),t("br"),t("br"),n._v("“Chúc mừng!”"),t("br"),t("br"),n._v("Trong mắt mang theo hâm mộ, có điều, Viên Hiểu đám người còn là toàn bộ ôm quyền."),t("br"),t("br"),n._v("Bọn họ là tiếp nhận mời, tới giúp một tay bắt Động Hư hồ lô, cứ việc cũng rất muốn muốn món bảo vật này, nhưng cũng sẽ không phá trong lòng nói nghĩa cùng chuẩn tắc."),t("br"),t("br"),n._v("tr u y e n c u a t u i . v n"),t("br"),n._v("Huống chi, Lạc Huyền Thanh bất kể thân phận còn là địa vị, đều không phải là bọn họ dám đắc tội."),t("br"),t("br"),n._v("“Nếu như không có mọi người giúp một tay, cũng không có khả năng thành công...”"),t("br"),t("br"),n._v("Gật gật đầu, Lạc Huyền Thanh trả lời một câu, đang định đem không gian bọt khí, thu nhập trữ vật giới chỉ, đợi thân thể khôi phục, lại triệt để luyện hóa cái hồ lô này, đột nhiên, cái này bất cứ lúc nào cũng sẽ biến cái gáo gia hỏa, chấn động mạnh một cái."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Bọt khí lập tức xuất hiện một cái lỗ hổng, hồ lô trực tiếp vọt ra."),t("br"),t("br"),n._v("“Nguy rồi...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Lạc Huyền Thanh tràn đầy cuống cuồng."),t("br"),t("br"),n._v("Kích hoạt huyết mạch lực lượng tiêu hao sạch sẽ, hắn lúc này, mặc dù không đến mức tu vi giảm lớn, nhưng cũng toàn thân suy yếu, một điểm lực lượng đều không có, lúc này chạy trốn, căn bản bắt không được!"),t("br"),t("br"),n._v("“Dừng lại cho ta!”"),t("br"),t("br"),n._v("Viên Hiểu đám người ngăn cản, lực lượng đồng thời tự nhiên."),t("br"),t("br"),n._v("Mất như thế lớn công phu, trên thân đều bị cái này hồ lô đạp tất cả đều là ấn ký, mắt thấy thành công, lại thất bại trong gang tấc, thật sự khóc."),t("br"),t("br"),n._v("Sưu sưu sưu!"),t("br"),t("br"),n._v("Hồ lô dường như đã biết bọn họ công kích quỹ tích, thân thể nhẹ nhàng uốn éo, tránh thoát như mưa to lực lượng, thời gian nháy mắt, vượt qua thanh âm tốc độ, hướng một cái phương hướng vọt tới."),t("br"),t("br"),n._v("Chính là Trương Huyền!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Da đầu nổ tung, Trương Huyền thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Lần này tới, một lần đều không có ra tay, một mực trốn ở trong góc đánh xì dầu (*đánh đấm giả bộ cho có khí thế)... Ngươi qua đây làm gì?"),t("br"),t("br"),n._v("“Trương sư cẩn thận...”"),t("br"),t("br"),n._v("Tràn đầy cuống cuồng, đám người hét lớn."),t("br"),t("br"),n._v("Đối phương tốc độ nhanh chóng, biết tránh né đã tới đã không kịp, Trương Huyền cắn răng một cái, cùng trước đó đồng dạng, nghênh đón tiếp lấy."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Bàn tay cùng đối phương tiếp xúc, lập tức cảm thấy lòng bàn tay đau rát đau, bị đối phương xé mở một cái dữ tợn lỗ hổng, máu tươi bắn tung toé."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Huyết dịch ở tại hồ lô trên thân, người sau cũng không tránh né, lần nữa nhoáng một cái."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Đi tới bụng dưới trước mặt trước."),t("br"),t("br"),n._v("Hồ lô mạnh như vậy, thật muốn đụng vào, tất nhiên ruột xuyên bụng nát, lập tức trọng thương!"),t("br"),t("br"),n._v("Oan có đầu, nợ có chủ, muốn bắt ngươi là bọn họ, làm gì tìm ta..."),t("br"),t("br"),n._v("Trương Huyền sắp khóc, có điều, cũng biết lúc này không phải xoắn xuýt thời điểm, chân khí trong cơ thể vận chuyển, muốn bắt lấy, lại phát hiện bắt hụt, hồ lô cùng bụng dưới đụng một cái, trong tưởng tượng cảm giác đau đớn, không những không có xuất hiện, ngược lại hồ lô trực tiếp biến mất."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, có chút không rõ ràng cho lắm."),t("br"),t("br"),n._v("Những người khác cũng đồng loạt nhìn qua, mang theo nghi ngờ."),t("br"),t("br"),n._v("“Hồ lô đâu?”"),t("br"),t("br"),n._v("Quay một vòng, cũng không phát hiện, Trương Huyền tinh thần nội thị, đột nhiên bờ môi lắc một cái: “Thế nào, thế nào... Chạy tới đan điền ta bên trong?”"),t("br"),t("br"),n._v("Chỉ thấy đan điền của hắn bên trong, một cái hồ lô, đang lười biếng nằm ở bên trong, thấy hắn thần thức quét tới, còn cố ý vặn vẹo uốn éo cái mông, tràn đầy đắc ý."),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("Vội vàng đi tới trước mặt, thần thức lan tràn, Lạc Huyền Thanh đặt mông ngồi dưới đất: “Ngươi đây là... Đưa nó luyện hóa!”"),t("br"),t("br"),n._v("“Luyện hóa?”"),t("br"),t("br"),n._v("“Động Hư hồ lô có thể giúp chủ nhân, lĩnh ngộ Động Hư cảnh, chỉ cần luyện hóa, liền có thể hòa vào đan điền...”"),t("br"),t("br"),n._v("Lạc Huyền Thanh tràn đầy dám tin tưởng: “Chỉ là, luyện hóa hết sức phức tạp, không chỉ muốn tinh huyết, càng quan trọng hơn là, nhất định phải tu vi đạt tới Thánh vực thất trọng đỉnh phong, mới có thể để cho hắn khuất phục, cam tâm nhận chủ, thực lực của ngươi chỉ có Xuất Khiếu cảnh đỉnh phong, chênh lệch trọn hai cái đại cấp bậc, đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Hao tốn vô số đại giới, tiêu hao vô số tâm huyết, rốt cuộc tìm được Động Hư hồ lô, kết quả còn chưa kịp luyện hóa, liền chạy người khác đan điền, Lạc Huyền Thanh che ngực, nghẹn không thở nổi."),t("br"),t("br"),n._v("“Ta không muốn luyện hóa ah... Hồ lô, ngươi đi ra cho ta...”"),t("br"),t("br"),n._v("Trương Huyền cũng khuôn mặt trắng bệch, vội vàng nói."),t("br"),t("br"),n._v("Hắn lần này thật sự là đến giúp anh vợ thu hoạch bảo vật, đồng thời không có ý định cướp người ta đồ vật, ngươi tìm ta đan điền làm gì?"),t("br"),n._v(" Ta thật vất vả xây dựng tráng lệ quang chính hình ảnh... Đều bị ngươi hủy!"),t("br"),t("br"),n._v("Hô hô!"),t("br"),t("br"),n._v("Hồ lô tiếp tục uốn éo cái mông."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Thấy người này, một bộ tiểu nhân đắc chí bộ dáng, Trương Huyền tức giận run rẩy, lại không thể làm gì, đành phải ngẩng đầu nhìn về phía thanh niên trước mắt: “Lạc huynh, ta thật không muốn luyện hóa...”"),t("br"),t("br"),n._v("“Ta biết!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh lắc đầu: “Bảo vật có linh tính, nhìn tới thứ này không thuộc về ta, cưỡng cầu không được...”"),t("br"),t("br"),n._v("Mặc dù buồn bực sắp hộc máu, hắn cũng nghĩ thông."),t("br"),t("br"),n._v("Bảo vật có linh, tiêu tốn như thế lớn tâm huyết, đều không nhận chủ, ngược lại vọt thẳng đến trước mắt cái này Trương sư trong đan điền, nói rõ người ta căn bản không coi trọng hắn!"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Mặt ức đến thấu hồng, Trương Huyền không biết giải thích như thế nào."),t("br"),t("br"),n._v("Cái này gọi chuyện gì, chỉ là tới giúp một tay, lần này được rồi, biến thành như vậy, về sau ai còn dám gọi hắn ra ngoài tìm kiếm bảo vật?"),t("br"),t("br"),n._v("“Lạc huynh luyện hóa hồ lô mục đích, là vì đột phá Động Hư cảnh, nếu như ta có thể giúp ngươi đột phá, có phải hay không có Động Hư hồ lô hay không, đều không quan trọng?”"),t("br"),t("br"),n._v("Một lát sau, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Nhìn người này vô lại bộ dạng, trong thời gian ngắn, chắc chắn sẽ không đi ra, nhưng mà cướp đi đối phương dự định đồ tốt, trong lòng thực sự băn khoăn."),t("br"),t("br"),n._v("“Giúp ta đột phá?”"),t("br"),t("br"),n._v("Lạc Huyền Thanh sững sờ, ngay sau đó lắc đầu: “Đột phá Động Hư cảnh, sao mà khó khăn... Vô số người kẹt tại cảnh giới này, muốn vượt qua mà không thể được, không có Động Hư hồ lô, nào có dễ dàng như vậy thành công!”"),t("br"),t("br"),n._v("Không phải hắn không tin trước mắt vị này, mà là, Thánh vực bát trọng Động Hư cảnh, dính dáng đại đạo, thực sự quá khó khăn, vô số cửu tinh danh sư chỉ điểm, đều làm không được, chỉ có thể dựa vào lĩnh ngộ của mình, huống chi, một cái chỉ có Xuất Khiếu cảnh đỉnh phong tiểu nhân vật!"),t("br"),t("br"),n._v("“Dù sao đều như vậy, không ngại thử một chút!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Đổi lại trước kia, chưa có xem qua Thánh vực bát trọng thư tịch, dù là thư viện cho ra thiếu hụt, cũng không biết như thế nào sửa lại, không thể cho người khác chỉ điểm."),t("br"),t("br"),n._v("Nhưng bây giờ, đối không gian lĩnh ngộ, đạt đến Diễn Không thiên thư đệ tam trọng, càng là luyện hóa Động Hư hồ lô cùng dây leo, đối loại này Thánh vực bát trọng mới có thể nắm giữ lực lượng, đã có hiểu rõ nhất định."),t("br"),t("br"),n._v("Lại mượn nhờ thư viện cho ra thiếu hụt, có lẽ thật có thể để hắn đột phá, xung kích cấp bậc cao hơn."),t("br"),t("br"),n._v("“Thử? Ta đều như vậy, thế nào thử?”"),t("br"),t("br"),n._v("Lạc Huyền Thanh cười khổ."),t("br"),t("br"),n._v("Mới vừa kích hoạt huyết mạch, thân thể hư nhược người bình thường cũng không bằng, đừng nói xung kích cảnh giới, bình thường đi lại đều rất khó."),t("br"),t("br"),n._v("“Ta nghĩ phương pháp giúp ngươi loại bỏ kích hoạt huyết mạch mang tới ảnh hưởng...”"),t("br"),t("br"),n._v("Tinh thần trở lại thức hải, Trương Huyền cẩn thận quan sát vừa rồi lấy được thư tịch."),t("br"),t("br"),n._v("Mấy người cùng hồ lô thời điểm chiến đấu, liền hình thành thư tịch, cũng không phải cố ý theo dõi đối phương, mà là nghiên cứu một chút Thánh Nhân quý tộc huyết mạch kích hoạt sau tình huống, vì về sau đi Trương gia cướp cô dâu, trước thời hạn làm ra chuẩn bị."),t("br"),t("br"),n._v("“Kích hoạt huyết mạch, tương đương với tại hao tổn tinh khí thần, một khi sử dụng, thân thể sẽ nằm ở một cái vô cùng thiếu thốn cảnh giới, không cách nào vận dụng lực lượng, cưỡng ép sử dụng, không những không thể phát huy mạnh nhất sức chiến đấu, còn có thể để tu vi lùi lại, nhiều năm thuần công, thất bại trong gang tấc...”"),t("br"),t("br"),n._v("Nhìn thấy thư tịch bên trên ghi chép, Trương Huyền lông mày vượt nhăn càng chặt."),t("br"),t("br"),n._v("Tiềm lực của một người, nguyên khí cùng tinh lực, là có hạn độ, kích hoạt huyết mạch, tương đương với trước thời hạn đem hắn tiêu hao, không có thời gian tích tụ, muốn khôi phục, gần như không có khả năng."),t("br"),t("br"),n._v("“Thiên Đạo chân khí có khả năng chữa trị thương thế, phá tan kinh mạch tắc nghẽn, khôi phục bị thương tinh khí thần... Đối kích hoạt huyết mạch, có hiệu quả hay không?”"),t("br"),t("br"),n._v("Trong lòng đột nhiên động một cái."),t("br"),t("br"),n._v("Hắn tu luyện chân khí, tinh thuần đến cực điểm, có thể tuỳ tiện đi vào người kinh mạch, tế bào, chữa trị bị hao tổn địa phương, cái này kích hoạt huyết mạch thoạt nhìn không giống nhau lắm, nhưng trên bản chất, cũng là tiêu hao bản nguyên, có lẽ cũng có thể hữu dụng."),t("br"),t("br"),n._v("Nghĩ đến cái này, nhìn lại: “Lạc huynh, ta nghĩ đến một cái phương pháp có lẽ có thể để cho ngươi bây giờ suy yếu mau chóng khôi phục!”"),t("br"),t("br"),n._v("“Ồ? Phương pháp gì?”"),t("br"),t("br"),n._v("Kẻ trước mắt này, một đường đến nay, mang tới kỳ tích thực sự quá nhiều, mặc dù cảm thấy khôi phục nhanh chóng không có khả năng, Lạc Huyền Thanh còn là nhìn lại."),t("br"),t("br"),n._v("Thật có loại phương pháp này, Thánh Nhân quý tộc đã sớm sử dụng, không đến mức ai cũng bó tay toàn tập."),t("br"),t("br"),n._v("“Rất đơn giản!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Trương Huyền lấy ra một cái cái bình, đưa tới, một mặt bình tĩnh: “Đem rượu này uống...”"),t("br"),t("br"),n._v("“Uống rượu?”"),t("br"),t("br"),n._v("Còn tưởng rằng nghĩ ra biện pháp gì, kết quả lại là cái này, Lạc Huyền Thanh lông mày nhịn không được nhảy một cái."),t("br"),t("br"),n._v("“Lạc Huyền Thanh lúc này lực lượng hao hết, nguyên khí tổn hao nhiều, động đều không thể động, uống rượu thương thân, ta nhìn vẫn là thôi đi!”"),t("br"),t("br"),n._v("Bích Hồng Âm ngăn cản."),t("br"),t("br"),n._v("Đều ngồi dưới đất chiến đấu không đứng lên nổi, còn để người ta uống rượu... Không phải là nghiêm túc a!"),t("br"),t("br"),n._v("“Trương sư nếu quả thật muốn uống, ta cùng ngươi!”"),t("br"),t("br"),n._v("Viên Hiểu tiến về phía trước một bước."),t("br"),t("br"),n._v("“Ta đang giúp hắn nghĩ biện pháp, khôi phục kích hoạt huyết mạch mang tới tổn thương...”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền nhìn chằm chằm trước mắt Lạc Huyền Thanh: “Lạc huynh nếu như tin tưởng ta, có thể thử một chút, một khi thành công, ta có thể nghĩ biện pháp để ngươi ngày hôm nay đã đột phá đến Thánh vực bát trọng, thất bại, nhiều nhất một vò rượu mà thôi, đối thân thể hẳn là cũng không có quá lớn ảnh hưởng!”"),t("br"),t("br"),n._v("“Cái này... Tốt a!”"),t("br"),t("br"),n._v("Dừng lại một chút, thấy trước mắt vị này kiên trì, Lạc Huyền Thanh nhẹ gật đầu, đưa tay nhận lấy vò rượu, ngửa đầu liền đem rượu ngon rót đi vào."),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Rượu vào cổ họng, một đạo chua cay, Lạc Huyền Thanh đang cảm thấy thanh niên trước mắt, có chút hồ đồ, đột nhiên con ngươi co rụt lại, thân thể không tự chủ được run rẩy: “Rượu này... Có vấn đề!”"),t("br"),t("br"),n._v("“Ngươi hạ độc?”"),t("br"),t("br"),n._v("Viên Hiểu khuôn mặt trầm xuống, trên thân khí tức tăng vọt."),t("br"),t("br"),n._v("“...” Lạc Huyền Thanh."),t("br"),t("br"),n._v("“...” Trương Huyền."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1369"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1371"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);