(window.webpackJsonp=window.webpackJsonp||[]).push([[43],{398:function(n,h,g){"use strict";g.r(h);var t=g(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1015-ta-co-the-thu-xem"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1015-ta-co-the-thu-xem"}},[n._v("#")]),n._v(" Chương 1015: Ta có thể thử xem")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("“Ngô sư!”"),g("br"),g("br"),n._v("Ba người làm lễ ra mắt."),g("br"),g("br"),n._v("“Đây là thế nào?” Trương Huyền trực tiếp hỏi."),g("br"),g("br"),n._v("“Phong ấn theo nửa tháng trước cũng có chút chưa vững chắc, có điều, đều tại có khống chế phạm vi bên trong, ai biết từ hôm qua bắt đầu, không ngừng lay động, tựa như lúc nào cũng sẽ sụp đổ, sắp không khống chế nổi!”"),g("br"),g("br"),n._v("Lông mày khóa chặt, Ngô sư nhìn về phía trước."),g("br"),g("br"),n._v("“Không khống chế được?”"),g("br"),g("br"),n._v("“Không sai...” Ngô sư gật đầu, đang muốn nói chuyện, liền cảm thấy một cái như sấm rền thanh âm từ dưới đất chậm rãi vang dội đến, như là cự thú rên rỉ, lại hình như sơn mạch phía dưới ẩn núp một đầu cự long, bất cứ lúc nào cũng sẽ sống lại, đem mọi người chém giết."),g("br"),g("br"),n._v("“Đây là cái gì?” Trương Huyền đám người sắc mặt biến đổi."),g("br"),g("br"),n._v("Nghe được thanh âm này, thật giống như thân ở cực lớn tai nạn đầu nguồn, sức người trở nên vô cùng nhỏ bé, một khi tai nạn phát sinh, sợ rằng sẽ bị trong nháy mắt nuốt chửng, Thần Tiên khó cứu."),g("br"),g("br"),n._v("“Chính là phong ấn nội bộ vang lên thanh âm, đến cùng xảy ra chuyện gì, ta cũng không làm rõ ràng được, có điều, như vậy tiếp tục nữa, không bao lâu, toàn bộ phong ấn, liền sẽ hoàn toàn sụp đổ, đến lúc đó, bất kể là di tích, vẫn là Hỏa Nguyên thành, chỉ sợ đều sẽ trong nháy mắt bị di thành đất bằng!”"),g("br"),g("br"),n._v("Ngô sư khắp khuôn mặt là lo lắng."),g("br"),g("br"),n._v("Một khi phong ấn phá hư, núi lửa liền sẽ lập tức bắn ra, Nhạn Lâm sơn khoảng cách Hỏa Nguyên thành chỉ có hơn 300 cây số, khoảng cách gần như thế, tất nhiên sẽ tai bay vạ gió, cường giả Thánh vực đều khó mà may mắn thoát khỏi."),g("br"),g("br"),n._v("Dưới núi lửa mặt ẩn núp lực lượng thực sự quá lớn, đã vượt qua sức người có thể bằng tình trạng!"),g("br"),g("br"),n._v("Ầm ầm!"),g("br"),g("br"),n._v("Đang khi nói chuyện, tiếng nổ càng ngày càng vang, mặt đất bắt đầu nhảy lên, một cỗ dòng khí nóng rực theo phong ấn nội bộ, hướng lên tuôn ra, rung động dữ dội bên dưới, vừa rồi mọi người buông xuống trận kỳ, lập tức bị đánh bay hơn phân nửa."),g("br"),g("br"),n._v("Ngồi tại cái hố trước bảo vệ phần đông trận pháp học viện học sinh, đồng thời vẻ mặt trắng bệch, trong nháy mắt bay ra ngoài mấy trăm người, nguyên một đám miệng phun máu tươi."),g("br"),g("br"),n._v("Những người này một bị thương, đằng sau lập tức có mới học viên bổ sung tới, toàn lực thi triển tu vi, khống chế bốn phía phong ấn đừng sụp đổ."),g("br"),g("br"),n._v("“Lực lượng thật là cường đại...”"),g("br"),g("br"),n._v("Thấy cảnh này, Trương Huyền khuôn mặt tái đi."),g("br"),g("br"),n._v("Phía dưới tuôn ra lực lượng, vượt ra khỏi tưởng tượng ra, cho dù Thánh vực tứ trọng, ngũ trọng cường giả, liên thủ cũng áp chế không nổi."),g("br"),g("br"),n._v("Nhiều như vậy thực lực mạnh mẽ Danh Sư đường học sinh, cũng chỉ là cố gắng duy trì."),g("br"),g("br"),n._v("Loại lực lượng này bên dưới, phong ấn nếu như không mở ra, đừng nói đi vào, chỉ sợ còn chưa tới trước mặt liền sẽ bị cuồng bạo khí lưu quấy thành bụi phấn!"),g("br"),g("br"),n._v("Trước đó vẫn cho là, mặc kệ cái này di tích nguy hiểm cỡ nào, chỉ cần cẩn thận một chút, nhờ vào thư viện cùng Minh Lý Chi Nhãn, luôn có thể xu cát tị hung, sẽ không xuất hiện quá lớn biến cố, hiện tại xem ra... Nghĩ tới quả thực quá đơn giản!"),g("br"),g("br"),n._v("Loại này cuồng bạo chỗ, hơi không cẩn thận, liền có khả năng tại chỗ bỏ mình, Thần Tiên khó cứu!"),g("br"),g("br"),n._v("“Chữ Giáp đội, canh giữ cấn vị, chữ Bính đội, canh giữ tốn vị, càn vị từ chữ Mậu đội canh giữ, cái khác các đội, canh giữ ở tại chỗ, hướng cái hố ở giữa truyền vào chân khí!”"),g("br"),g("br"),n._v("Đúng lúc này, một cái hét lớn vang lên, lập tức một cái lão giả, bay lên, lơ lửng tại mọi người bầu trời, trước mặt một cái cực lớn la bàn lơ lửng, một bên suy tính, một bên hét lớn."),g("br"),g("br"),n._v("Phía dưới rất nhiều học sinh nghe được phân phó của hắn, toàn bộ dựa theo trước đó tổ tốt đội hình, tiến hành sắp xếp, ngay sau đó vô số đạo chân khí, đồng thời tuôn ra tới, tại cái hố vị trí giữa tụ hợp, tựa như dấy lên một cái cực lớn pháo hoa."),g("br"),g("br"),n._v("Chân khí phun trào bên dưới, bốn phía phong ấn, giống như là gia cố, dần dần hướng tới yên bình, phía dưới tiếng nổ, cũng giống là bị áp chế, lại không có trước đó nóng nảy."),g("br"),g("br"),n._v("“Không đúng... Như vậy gia cố lời nói, phong ấn còn làm sao có thể mở ra?” Nhìn một lát, Trương Huyền nhướng mày."),g("br"),g("br"),n._v("Làm như vậy thật là tránh khỏi phía dưới lực lượng kinh khủng tập kích, để trong này tạm thời an toàn, nhưng... Cũng có rất lớn vấn đề!"),g("br"),g("br"),n._v("Cái kia chính là nương theo phong ấn lực lượng gia cố, lại nghĩ đi vào di tích bên trong, chỉ sợ càng ngày càng khó, thậm chí, đều có thể nói không thể nào!"),g("br"),g("br"),n._v("Dựa theo trước đó thuyết pháp, hai, ba ngày sau phong ấn liền sẽ mở ra, mà tiếp tục như vậy gia cố lời nói, đừng nói ba ngày, ba năm cũng chưa chắc có thể lại mở ra."),g("br"),g("br"),n._v("“Đúng vậy a, thế nhưng là chúng ta cũng không có phương pháp! Phong ấn không mở ra, liền không cách nào đi vào, mà bây giờ bộ dạng, không thêm kiên quyết lời nói, toàn bộ di tích đều sẽ sụp đổ...” Ngô sư lắc đầu."),g("br"),g("br"),n._v("Thật ra thì bọn họ cũng không muốn như vậy gia cố, nhưng mà không thêm kiên quyết, di tích đều sẽ không còn tồn tại, đi vào cũng vô dụng!"),g("br"),g("br"),n._v("Lại nói, phía dưới như vậy cuồng bạo, cho dù có thể đi vào, cũng không dám đi vào ah! Ai có thể cam đoan, sẽ không tùy thời bộc phát, đem người chôn ở bên trong?"),g("br"),g("br"),n._v("“Loại trừ gia cố, chẳng lẽ không có những biện pháp khác?” Trương Huyền nhịn không được nói."),g("br"),g("br"),n._v("“Có là có, nhưng... Làm không được!”"),g("br"),g("br"),n._v("Ngô sư lắc đầu."),g("br"),g("br"),n._v("“Nói nghe một chút?” Trương Huyền nhịn không được nhìn qua."),g("br"),g("br"),n._v("Đã có phương pháp, liền có thể thử một lần, dù là không thành công, cũng hầu như so như bây giờ mạnh hơn nhiều."),g("br"),g("br"),n._v("“Cái này phong ấn, như là một cái cách ngăn, đem phía dưới lực lượng cuồng bạo niêm phong lại, thật giống như phong tại cửa động bóng bay, bên trong lực lượng không ngừng công kích, vừa mới bắt đầu bóng bay dựa vào co dãn, còn có thể duy trì, nương theo lực lượng càng ngày càng nhiều, kiểu gì cũng sẽ đạt tới một cái không thể thừa nhận cực hạn, đến lúc đó liền có khả năng trực tiếp nổ tung! Mọi người gia cố phong ấn mục đích, chính là vì bảo giới hạn này kéo dài, bảo phong ấn lực lượng, càng thêm cường đại!”"),g("br"),g("br"),n._v("Ngô sư giải thích nói: “Đáng tiếc bất kể cường đại cỡ nào, chỉ cần phía dưới lực lượng không giải quyết, chẳng khác nào trị ngọn không trị gốc, mãi mãi cũng có sẽ bị xông phá một ngày!”"),g("br"),n._v("“Ừm!” Nghe được giải thích của hắn, Trương Huyền một chút đầu."),g("br"),g("br"),n._v("Hắn nói mười phần hình ảnh."),g("br"),g("br"),n._v("Phía dưới lực lượng cuồng bạo mới là căn bản, không giải quyết cái này, một mực gia cố phong ấn, là vô dụng, thật giống như bảo bóng bay trở nên co dãn càng lúc càng lớn... Chỉ là trong thời gian ngắn, bảo lực lượng cuồng bạo, không cách nào được như ý thôi, trên thực tế kèm theo đè nén thời gian càng ngày càng dài, lực lượng tích tụ càng ngày càng hung ác, một khi bộc phát uy lực sẽ càng lớn!"),g("br"),g("br"),n._v("Thật giống như hồng thủy, dùng đê đập đi chặn đường đồng dạng."),g("br"),g("br"),n._v("Chắn càng nhiều, mực nước càng cao, áp lực cũng liền tăng lên gấp bội, đến cuối cùng, khẳng định sẽ đạt tới một cái không thể thừa nhận cực hạn."),g("br"),g("br"),n._v("Thật muốn như vậy, liền phiền toái."),g("br"),g("br"),n._v("“Một cái biện pháp khác, chính là trị tận gốc! Đem phía dưới lực lượng cuồng bạo loại bỏ... Nhưng mà tình huống trước mắt, phong ấn không có mở ra trước đó, ai cũng không cách nào đi vào, chớ nói chi là tìm tới chỗ mấu chốt, tiến hành ngăn lại! Mà một khi mở ra, phía dưới lực lượng tất nhiên cuồng bạo mà ra...”"),g("br"),g("br"),n._v("Ngô sư cười khổ lắc đầu."),g("br"),g("br"),n._v("Đây chính là cái nghịch lý, mở ra phong ấn, mới có giải quyết triệt để tai họa ngầm khả năng, chỉ khi nào mở ra, tai hoạ ngầm liền sẽ tại chỗ bộc phát..."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Trương Huyền cùng Mộc sư đám người nhìn nhau, cũng không có cách nào lắc đầu."),g("br"),g("br"),n._v("Đối phương nói không sai, cái này đích xác là cái không cách nào lựa chọn vấn đề khó khăn, cho dù biết rõ, không ngừng gia cố phong ấn, sẽ chỉ càng ngày càng phiền phức, nhưng cũng không có bất kỳ biện pháp nào."),g("br"),g("br"),n._v("“Ta nghe Mộc sư nói, canh giữ ở đây danh sư phát hiện Dị Linh tộc nhân về sau, mới xuất hiện loại biến cố này... Không biết là không biết thật?” Trong lòng hơi động, Trương Huyền nghĩ đến cái gì, nhịn không được hỏi."),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Ngô sư nhẹ gật đầu: “Dị Linh tộc nhân tới thời gian không dài, liền vội vã rời đi, không biết dùng thủ đoạn gì, nơi này liền bắt đầu xuất hiện biến cố... Vừa mới bắt đầu xung kích rất nhẹ, cho dù chúng ta mấy cái đều có thể chống lại ở, nhưng nương theo thời gian trôi qua, càng ngày càng lợi hại, hai vạn học sinh tính gộp lại, cũng có chút cố hết sức!”"),g("br"),g("br"),n._v("“Tới thời gian không dài liền đi?” Trương Huyền chần chờ một chút: “Không biết phát hiện những này Dị Linh tộc nhân danh sư, là vị nào?”"),g("br"),g("br"),n._v("“Là một cái bình thường tứ tinh danh sư, lúc ấy phụ trách bảo vệ ở đây!”"),g("br"),g("br"),n._v("Ngô sư nói."),g("br"),g("br"),n._v("“Bình thường tứ tinh danh sư, tu vi cao nhất chẳng qua Hóa Phàm cửu trọng... Lại phát hiện Dị Linh tộc nhân làm loạn?” Trương Huyền kỳ quái."),g("br"),g("br"),n._v("Lần này vì cái này di tích, mà đến Dị Linh tộc nhân, hắn gặp qua không ít, kém nhất đều đạt đến Thánh vực nhất trọng, mạnh thập đại vương giả, càng là đạt đến tứ trọng!"),g("br"),g("br"),n._v("Một cái bình thường tứ tinh danh sư, tu vi cao nhất cũng chỉ Hóa Phàm cửu trọng... Làm sao có thể phát hiện bọn họ làm loạn, mà không bị chém giết?"),g("br"),g("br"),n._v("“Yên tâm đi, vị danh sư này, giá trị tuyệt đối đến tín nhiệm, sẽ không phản bội nhân tộc...”"),g("br"),g("br"),n._v("Còn tưởng rằng hắn cảm thấy, có người cùng Dị Linh tộc nhân cấu kết, Ngô sư vội vàng giải thích."),g("br"),g("br"),n._v("“Ta cũng không phải cảm thấy hắn sẽ phản bội nhân tộc...” Trương Huyền lắc đầu, đang muốn nói cái gì, liền cảm thấy mặt đất tiếng nổ vang lên lần nữa."),g("br"),g("br"),n._v("“Phía dưới lực lượng, vừa mới bắt đầu, hai ba canh giờ mới tiến hành một lần, mà bây giờ đã không cao hơn mười phút đồng hồ...”"),g("br"),g("br"),n._v("Ngô sư nói."),g("br"),g("br"),n._v("Hai ngày trước, phía dưới lực lượng mặc dù cũng xung kích, bởi vì tần suất rất thấp, mọi người không có như thế phí sức, mà bây giờ... Mười phút đồng hồ loại một lần, đều nhanh không kiên trì nổi!"),g("br"),g("br"),n._v("“Như vậy tiếp tục nữa, ta sợ những học sinh này, đều sẽ không chịu nổi...” Trương Huyền khuôn mặt xanh xám."),g("br"),g("br"),n._v("Đây đều là học viên của hắn, càng có một bộ phận lớn, càng có một bộ phận lớn là Huyền Huyền hội, mấy canh giờ duy trì một lần, ngược lại cũng thôi, như vậy mười phút đồng hồ một lần lời nói, chỉ sợ không kiên trì được bao lâu, liền sẽ không chịu nổi, tu vi tổn hao nhiều!"),g("br"),g("br"),n._v("“Ta cũng biết, nhưng là bây giờ đã không có tốt hơn phương pháp...”"),g("br"),g("br"),n._v("Ngô sư lắc đầu."),g("br"),g("br"),n._v("“Lấp không bằng khai thông, đã sớm muộn gì đều áp chế không nổi, là gì không đem bên trong lực lượng cuồng bạo, dẫn dắt đi ra, lời như vậy, liền không cần phiền toái như vậy...”"),g("br"),g("br"),n._v("Một bên Lạc Nhược Hi đột nhiên mở miệng."),g("br"),g("br"),n._v("“Sơ... Chúng ta cũng không phải không nghĩ tới, nhưng mà cỗ lực lượng này quá mạnh, hơn nữa lộ ra bốn phương tám hướng công kích, tìm không thấy phương vị hợp thích nhất, phong ấn chẳng những sẽ trong nháy mắt sụp đổ, còn rất dễ dàng gây nên nổ tung... Làm không cẩn thận, người nơi này đều sẽ bị nổ chết tại chỗ!”"),g("br"),g("br"),n._v("Ngô sư nói."),g("br"),g("br"),n._v("Đồng dạng lấy khí cầu làm thí dụ, càng thổi càng lớn, nương theo đạt tới cực hạn chịu đựng, tất nhiên sẽ nổ tung... Vì phòng ngừa loại chuyện này phát sinh, ở phía trên chói mắt, đem bên trong khí tức thả ra là tốt nhất... Đáng tiếc, một khi đâm không tốt, chẳng khác nào đốt lên dây dẫn nổ trực tiếp dẫn nổ."),g("br"),g("br"),n._v("Không ánh lửa nguyên thành, ở đây tất cả danh sư, đều có thể toàn quân bị diệt, không còn một mống."),g("br"),g("br"),n._v("Bởi vậy, đâm bao lớn mắt, ở nơi nào đâm, là rất lớn học vấn, tìm không thấy vị trí, chính là chết... Bọn họ liền xem như thất tinh danh sư, Thất Tinh trận pháp sư, cũng không có nắm chắc, không dám mạo hiểm."),g("br"),g("br"),n._v("“Tìm tới thích hợp nhất vị trí... Phóng thích lực lượng?”"),g("br"),g("br"),n._v("Nghe được hai người đối thoại, Trương Huyền con mắt đột nhiên sáng lên, ánh mắt lấp lánh nhìn lại, khắp khuôn mặt là hưng phấn: “Cái này... Có lẽ ta có thể thử xem!”"),g("br"),g("br"),n._v("(Mới vừa nhìn thấy, có cái cho tác giả đánh call, mặc dù không biết là cái gì, nhưng cảm giác rất lợi hại bộ dạng, hôm nay liền kết thúc, mọi người nô nức tấp nập tham gia ah, chúng ta bài danh hình như... Ta không tìm được, dù sao rất thấp. Hình như liền một trăm danh đô không có, đổ mồ hôi.)"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1023"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1025"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);